INSERT INTO `chuagiacquang_ask` (`id`, `id_category`, `question`, `author1`, `time`, `answer`, `author2`) VALUES
(17, 7, '<p>The gian co duc Phat khong?</p>\r\n', 'Kim Long', '2013-01-09 06:36:23', '<p style="text-align: justify;">Th&acirc;n ch&agrave;o bạn Kim Long!<br />\r\nLời đầu l&agrave; c&aacute;m ơn bạn đ&atilde; gh&eacute; thăm trang web của Ch&ugrave;a Gi&aacute;c Quang ch&uacute;ng t&ocirc;i, v&agrave; c&aacute;m ơn bạn đ&atilde; quan t&acirc;m vấn đề Phật học m&agrave; đặt c&acirc;u hỏi.<br />\r\nC&acirc;u hỏi của bạn rất hay.</p>\r\n\r\n<p style="text-align: justify;">Thưa bạn!<br />\r\nBạn hỏi l&agrave; &ldquo;Thế gian c&oacute; Phật kh&ocirc;ng?&rdquo;. Theo &yacute; t&ocirc;i hiểu, chữ Phật bạn hỏi ở đ&acirc;y, l&agrave; một vị Phật &lsquo;đi m&acirc;y về gi&oacute;&rsquo;, &lsquo;ngồi một chỗ m&agrave; biết việc mu&ocirc;n phương&rsquo;, &lsquo;qu&aacute; khứ, hiện tại, vị lai đều th&ocirc;ng tỏ&rsquo;, &lsquo;thầy của trời v&agrave; người&rsquo;, &lsquo;phước b&aacute;o v&agrave; quyền lực v&ocirc; bi&ecirc;n v&ocirc; song&rsquo;.&nbsp; Phải kh&ocirc;ng ạ?</p>\r\n\r\n<p style="text-align: justify;">Thưa bạn!<br />\r\nPhật gi&aacute;o hiện nay l&agrave; một t&ocirc;n gi&aacute;o, nhưng l&agrave; một T&ocirc;n Gi&aacute;o V&ocirc; Thần. N&oacute;i như thế, cũng c&oacute; nghĩa l&agrave; tuy&ecirc;n bố rằng Đạo Phật kh&ocirc;ng phải l&agrave; một t&ocirc;n gi&aacute;o &ndash; như mọi người vẫn định nghĩa. Bản th&acirc;n Đạo Phật, từ xưa, l&agrave; một c&aacute;i Đạo Gi&aacute;c Ngộ. Đạo l&agrave; lối sống, l&agrave; con đường; Phật l&agrave; gi&aacute;c ngộ, tức l&agrave; th&ocirc;ng tuệ, th&ocirc;ng th&aacute;i, tr&iacute; thức. Do vậy, từ xưa, Đạo Phật vốn chỉ l&agrave; một lối sống th&ocirc;ng th&aacute;i, một lối sống tr&iacute; tuệ. Lối sống n&agrave;y tồn tại đến ng&agrave;y nay, ngay trong l&ograve;ng Phật gi&aacute;o.</p>\r\n\r\n<p style="text-align: justify;">Đạo Phật, từ khi th&agrave;nh lập, đến nay đ&atilde; trải qua một khoảng d&agrave;i thời gian &ndash; tr&ecirc;n 2500 năm, v&agrave; một khoảng rộng kh&ocirc;ng gian &ndash; khắp thế giới. Trong qu&aacute; tr&igrave;nh truyền b&aacute;, Đạo Phật đ&atilde; sử dụng những văn h&oacute;a, t&iacute;n ngưỡng của c&aacute;c d&acirc;n tộc, c&aacute;c v&ugrave;ng l&atilde;nh thổ l&agrave;m phương tiện để tiếp cận d&acirc;n ch&uacute;ng m&agrave; truyền đạt cho họ c&aacute;i lối sống th&ocirc;ng tuệ. Do đ&oacute;, ng&agrave;y nay, Phật gi&aacute;o mang rất nhiều h&igrave;nh th&aacute;i văn h&oacute;a, phong tục, sinh hoạt kh&aacute;c nhau, nhưng trong Phật gi&aacute;o vẫn tồn tại Phật Ph&aacute;p_tức l&agrave; những nội dung của Đạo Phật ban sơ (cốt tủy của Phật gi&aacute;o).</p>\r\n\r\n<p style="text-align: justify;">V&igrave; l&yacute; do đ&oacute;, với tầm nh&igrave;n của người b&ecirc;n ngo&agrave;i, hiện nay, Phật gi&aacute;o l&agrave; một t&ocirc;n gi&aacute;o kh&ocirc;ng hơn kh&ocirc;ng k&eacute;m. Nhưng thật ra n&oacute; kh&ocirc;ng phải l&agrave; một t&ocirc;n gi&aacute;o; hay để kh&ocirc;ng phải ph&acirc;n t&iacute;ch mất thời gian hoặc tranh luận xa x&ocirc;i, người ta mĩm cười m&agrave; đồng &yacute; rằng <em><strong>Phật gi&aacute;o l&agrave; một t&ocirc;n gi&aacute;o, nhưng n&oacute; ch&iacute;nh x&aacute;c l&agrave; một T&ocirc;n Gi&aacute;o V&ocirc; Thần</strong></em>.</p>\r\n\r\n<p style="text-align: justify;">Thưa bạn!<br />\r\nT&ocirc;i đ&atilde; n&oacute;i qu&aacute; d&agrave;i d&ograve;ng phải kh&ocirc;ng? Xin chờ th&ecirc;m t&iacute; nữa.</p>\r\n\r\n<p style="text-align: justify;">V&igrave; Phật gi&aacute;o hiện nay đ&atilde; như l&agrave; một t&ocirc;n gi&aacute;o &ndash; n&oacute; đ&atilde; bị kho&aacute;c l&ecirc;n m&igrave;nh c&aacute;i m&agrave;u &aacute;o phương tiện t&ocirc;i vừa n&oacute;i tr&ecirc;n, n&ecirc;n c&oacute; người <em><strong>thấy </strong></em>Phật gi&aacute;o như l&agrave; một T&ocirc;n Gi&aacute;o C&oacute; Thần, v&agrave; cũng c&oacute; người <em><strong>hiểu</strong></em> Phật gi&aacute;o như l&agrave; một T&ocirc;n Gi&aacute;o V&ocirc; Thần. Trong T&ocirc;n Gi&aacute;o C&oacute; Thần th&igrave; đức Phật l&agrave; như bạn vừa hỏi ở tr&ecirc;n, đầy mầu nhiệm, si&ecirc;u việt. C&ograve;n trong T&ocirc;n Gi&aacute;o V&ocirc; Thần &ndash; tức l&agrave; trong cốt tủy &ndash; th&igrave; đức Phật chỉ l&agrave; một con người b&igrave;nh thường, một triết gia c&oacute; tấm l&ograve;ng thương người bao la.</p>\r\n\r\n<p style="text-align: justify;">T&oacute;m lại, t&ocirc;i xin trả lời thẳng c&acirc;u hỏi của bạn: &ldquo;Thế gian c&oacute; Phật kh&ocirc;ng?&rdquo;</p>\r\n\r\n<ul>\r\n	<li style="text-align: justify;">Đức Phật như l&agrave; một vị thần: KH&Ocirc;NG. Đ&oacute; l&agrave; phương tiện truyền b&aacute; Phật Ph&aacute;p của c&aacute;c vị tiền bối ng&agrave;y xưa.</li>\r\n	<li style="text-align: justify;">Đức Phật như l&agrave; một vị triết gia c&oacute; tấm l&ograve;ng thương người bao la: C&Oacute;. Ng&agrave;i l&agrave; một người c&oacute; thật tr&ecirc;n cuộc đời, sinh ra v&agrave; chết đi c&aacute;ch nay tr&ecirc;n 2500 năm, tại miền Đ&ocirc;ng Bắc Ấn Độ, nay thuộc về l&atilde;nh thổ nước Nepal.</li>\r\n</ul>\r\n\r\n<p style="text-align: justify;">K&iacute;nh ch&uacute;c bạn dồi d&agrave;o sức khỏe, an lạc v&agrave; hữu &iacute;ch cho đời.</p>\r\n', 'Ban biên tập'),
(18, 7, '<p>Con k&iacute;nh hỏi Sư C&ocirc;: Con đọc trong kinh Ph&aacute;p Hoa : V&igrave; sao Long Nữ ph&aacute;t t&acirc;m d&acirc;ng ch&acirc;u liền th&agrave;nh ch&aacute;nh gi&aacute;c ? Xin Sư C&ocirc; giải đ&aacute;p.</p>\r\n', 'nguyenthuylinh', '2013-01-12 10:40:54', '<p>Th&ugrave;y Linh th&acirc;n mến!</p>\r\n\r\n<p>C&acirc;u hỏi của Th&ugrave;y Linh l&agrave; &ldquo;V&igrave; sao Long nữ ph&aacute;t t&acirc;m d&acirc;ng ch&acirc;u, liền th&agrave;nh ch&aacute;nh gi&aacute;c?&rdquo;. Theo &yacute; c&ocirc;, c&acirc;u hỏi của Linh sẽ được hiểu l&agrave;: &ldquo;Long nữ vừa ph&aacute;t t&acirc;m d&acirc;ng hạt ch&acirc;u ngọc l&ecirc;n đức Phật, th&igrave; Long nữ đ&atilde; lập tức th&agrave;nh Phật. Tại sao c&oacute; chuyện như vậy?&rdquo; Phải kh&ocirc;ng?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Linh ơi! C&acirc;u chuyện Long nữ d&acirc;ng ch&acirc;u v&agrave; th&agrave;nh Phật, gần đ&acirc;y được b&agrave;n t&aacute;n tam sao thất bổn ở một số trang mạng. C&oacute; lẽ Linh đ&atilde; hỏi v&igrave; bị ảnh hưởng bởi một trong c&aacute;c trang đ&oacute;. V&agrave; Linh ạ, hỏi như em th&igrave; kh&ocirc;ng đ&uacute;ng. Tự th&acirc;n c&acirc;u hỏi đ&atilde; sai rồi. LONG NỮ <em>VỪA PH&Aacute;T KHỞI C&Aacute;I T&Acirc;M</em> D&Acirc;NG HẠT NGỌC L&Ecirc;N PHẬT, <em>Đ&Atilde; LẬP TỨC</em> TH&Agrave;NH PHẬT, l&agrave; sai.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Em đừng n&ocirc;n n&oacute;ng nh&eacute;, để cung cấp cho em một c&aacute;ch hiểu đ&uacute;ng về c&acirc;u chuyện n&agrave;y, c&ocirc; sẽ dắt em đi từ từ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><u>Đầu ti&ecirc;n l&agrave; t&oacute;m tắt v&agrave; xuất xứ của c&acirc;u chuyện:</u></p>\r\n\r\n<p>Long nữ l&agrave; một con rắn c&aacute;i nhỏ (rồng l&agrave; rắn đ&oacute; Linh ạ), 08 tuổi. Long nữ ở dưới biển, theo Bồ T&aacute;t Đại Tr&iacute; Văn Th&ugrave; học <em>Kinh Ph&aacute;p Hoa</em>, v&agrave; l&agrave; đệ tử ưu t&uacute; nhất của Bồ T&aacute;t trong thời gian Bồ T&aacute;t dạy bộ Kinh n&agrave;y dưới biển.</p>\r\n\r\n<p>Bồ T&aacute;t Tr&iacute; T&iacute;ch &ndash; người theo hầu Phật Đa Bảo đến từ phương dưới &ndash; v&agrave; Thinh Văn<sup>(*1)</sup> X&aacute; Lợi Phất &ndash; đệ tử Phật Th&iacute;ch Ca ở phương n&agrave;y l&uacute;c ấy &ndash; kh&ocirc;ng tin rằng một con rắn c&aacute;i nhỏ b&eacute; m&agrave; c&oacute; thể th&agrave;nh Phật. Khi Bồ T&aacute;t Văn Th&ugrave; &ndash; đệ tử Phật Th&iacute;ch Ca l&uacute;c ấy &ndash; n&oacute;i, Bồ T&aacute;t Tr&iacute; T&iacute;ch đ&atilde; kh&ocirc;ng tin. Khi Long nữ hiện ra, thưa với Phật Th&iacute;ch Ca rằng con sẽ th&agrave;nh Phật, Thinh Văn X&aacute; Lợi Phất cũng kh&ocirc;ng tin.</p>\r\n\r\n<p>Để chứng minh rằng m&igrave;nh c&oacute; thể th&agrave;nh Phật, m&agrave; lại th&agrave;nh một c&aacute;ch rất nhanh ch&oacute;ng, Long nữ đ&atilde; v&agrave; thi triển việc th&agrave;nh Phật của m&igrave;nh cho Bồ T&aacute;t Tr&iacute; T&iacute;ch v&agrave; Thinh Văn X&aacute; Lợi Phất thấy &ndash; thời gian th&agrave;nh Phật c&ograve;n diễn ra nhanh hơn thời gian Long nữ [hai tay] d&acirc;ng ch&acirc;u v&agrave; Phật Th&iacute;ch Ca đưa [một] tay ra nhận lấy, việc mới xảy ra khi Long nữ ra mắt đức Phật Th&iacute;ch Ca ban n&atilde;y.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&acirc;u chuyện n&agrave;y xuất ph&aacute;t từ <em>Kinh Ph&aacute;p Hoa, phẩm thứ 12 [t&ecirc;n l&agrave;] Đề B&agrave; Đạt Đa</em>, Tam tạng Ph&aacute;p sư Cưu Ma La Thập dịch từ chữ Ấn Độ (Phạn &ndash; Sanskrit) sang chữ H&aacute;n, v&agrave; H&ograve;a thượng Tr&iacute; Tịnh dịch từ H&aacute;n sang Việt. Bộ Kinh n&agrave;y được đăng tr&ecirc;n nhiều trang web, điển h&igrave;nh l&agrave; trang n&agrave;y <a href="http://www.quangduc.com/kinhdien/23phaphoa12.html">http://www.quangduc.com/kinhdien/23phaphoa12.html</a>.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><u>Kế l&agrave; &yacute; nghĩa c&acirc;u chuyện:</u></p>\r\n\r\n<p><em>Kinh Ph&aacute;p Hoa </em>l&agrave; bộ Kinh đứng h&agrave;ng đầu về văn chương v&agrave; tư tưởng trong c&aacute;c bộ kinh Đại Thừa. Kinh cho biết rằng Tiểu Thừa l&agrave; xe d&ecirc; k&eacute;o (Thinh Văn Thừa) hay xe hươu k&eacute;o (Duy&ecirc;n Gi&aacute;c Thừa), v&agrave; Đại Thừa l&agrave; xe tr&acirc;u k&eacute;o (Bồ T&aacute;t Thừa hay Phật Thừa)<sup> (*2)</sup>; trong đ&oacute;, xe tr&acirc;u k&eacute;o l&agrave; &lsquo;xịn&rsquo; hơn. Kinh cũng cho biết rằng th&agrave;nh tựu của Tiểu Thừa l&agrave; H&oacute;a Th&agrave;nh (tạm thời, kh&ocirc;ng c&oacute; thật), c&ograve;n th&agrave;nh tựu của Đại Thừa l&agrave; Bảo Sở<sup>(*3)</sup> (đ&iacute;ch đến ch&acirc;n thật qu&yacute; b&aacute;u của h&agrave;nh giả). Nội dung bộ Kinh, quan trọng nhất l&agrave; TẤT CẢ CH&Uacute;NG SINH ĐỀU C&Oacute; PHẬT T&Iacute;NH B&Igrave;NH ĐẲNG, ai cũng sẽ th&agrave;nh Phật v&agrave; s&acirc;u sắc hơn_ai cũng đ&atilde; th&agrave;nh Phật.</p>\r\n\r\n<p><em>Phẩm 12 Đề B&agrave; Đạt Đa </em>l&agrave; phẩm Kinh chứng minh rằng ai cũng c&oacute; thể th&agrave;nh Phật. Th&agrave;nh Phật tức l&agrave; hưởng được c&aacute;i th&agrave;nh quả cao nhất của Đạo Phật. Ngay cả người Nhất Xiển Đề &ndash; kh&ocirc;ng tin Phật Ph&aacute;p, ph&aacute; hoại Phật Ph&aacute;p, xa lạ v&agrave; khinh miệt Phật Ph&aacute;p &ndash; đại diện l&agrave; Đề B&agrave; Đạt Đa v&agrave; lo&agrave;i ngu si hạ cấp nhất (con rắn, s&uacute;c sinh, m&agrave; l&agrave; con rắn c&aacute;i nữa__người Ấn Độ l&uacute;c đ&oacute; vốn c&oacute; quan niệm coi thường người nữ, giống c&aacute;i, v&agrave; cũng khinh khi lo&agrave;i rắn l&agrave; lo&agrave;i ngu si nhất) cũng c&oacute; thể th&agrave;nh Phật. Con rắn c&aacute;i con l&agrave; Long nữ 08 tuổi đ&oacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trong <em>phẩm Đề B&agrave; Đạt Đa</em>, chuyện Long nữ đại kh&aacute;i như vầy:<br />\r\n<em>Bồ T&aacute;t Tr&iacute; T&iacute;ch &ndash; vị Bồ T&aacute;t ở phương dưới, đi theo hầu Phật Đa Bảo &ndash; xin Phật Đa Bảo đi về. Phật Th&iacute;ch Ca k&ecirc;u n&aacute;n lại, l&aacute;t nữa sẽ c&oacute; Bồ T&aacute;t Văn Th&ugrave; &ndash; đang l&agrave; đệ tử Phật Th&iacute;ch Ca, đi gi&aacute;o h&oacute;a ở biển &ndash; trở về m&agrave; tr&ograve; chuyện. L&aacute;t sau, Bồ T&aacute;t Văn Th&ugrave; về. Bồ T&aacute;t Tr&iacute; T&iacute;ch hỏi Bồ T&aacute;t Văn Th&ugrave; đi l&agrave;m c&ocirc;ng t&aacute;c gi&aacute;o h&oacute;a ở đ&acirc;u, c&oacute; nhiều kh&ocirc;ng, ai l&agrave; đệ tử t&acirc;m đắc nhất. Bồ T&aacute;t Văn Th&ugrave; n&oacute;i đi giảng Kinh Ph&aacute;p Hoa ở biển, đắc quả v&ocirc; số, nhưng đệ tử t&acirc;m đắc nhất l&agrave; Long nữ, v&agrave; cho biết Long nữ sẽ th&agrave;nh Phật. Bồ T&aacute;t Tr&iacute; T&iacute;ch thấy v&ocirc; số Th&aacute;nh ch&uacute;ng từ biển bay l&ecirc;n, th&igrave; khen ngợi Bồ T&aacute;t Văn Th&ugrave; c&oacute; c&ocirc;ng đức lớn, nhưng kh&ocirc;ng tin c&acirc;u chuyện một con rắn c&aacute;i b&eacute; nhỏ m&agrave; c&oacute; thể gi&aacute;c ngộ tu h&agrave;nh th&agrave;nh Phật. L&aacute;t sau, Long nữ l&ecirc;n, lạy Phật ra mắt, v&agrave; c&uacute;ng dường Phật một hạt ch&acirc;u; đức Phật Th&iacute;ch Ca đưa tay đ&oacute;n nhận. Bồ T&aacute;t Tr&iacute; T&iacute;ch nghi ngờ, nhưng kh&ocirc;ng hỏi; c&ograve;n Thinh Văn X&aacute; Lợi Phất th&igrave; nghi ngờ m&agrave; hỏi, chất vấn Long nữ, l&agrave; một con rắn c&aacute;i b&eacute; tẹo th&igrave; l&agrave;m sao lại c&oacute; thể th&agrave;nh Phật. Long nữ, trước mặt mọi người, n&oacute;i, &yacute; rằng: &lsquo;C&aacute;c &ocirc;ng h&atilde;y coi! T&ocirc;i kh&ocirc;ng những c&oacute; thể th&agrave;nh Phật, m&agrave; c&ograve;n th&agrave;nh Phật rất l&agrave; mau lẹ nữa.&rsquo; V&agrave; Long nữ th&agrave;nh Phật thật, thời gian n&oacute; th&agrave;nh Phật nhanh kinh khủng.</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><u>Thứ ba l&agrave; c&aacute;c c&aacute;ch giải th&iacute;ch c&acirc;u chuyện:</u></p>\r\n\r\n<p>C&ocirc; kh&ocirc;ng dẫn Linh v&agrave;o c&otilde;i học thuật đ&acirc;u. C&ocirc; chỉ giới thiệu cho Linh 02 c&aacute;ch giải th&iacute;ch phổ biến trong giới người Việt m&igrave;nh trong v&agrave; ngo&agrave;i nước th&ocirc;i. Đ&oacute; l&agrave; giải th&iacute;ch theo Tư Tưởng Phật gi&aacute;o Đại Thừa v&agrave; giải th&iacute;ch theo Thiền T&ocirc;ng.</p>\r\n\r\n<ul>\r\n	<li>Theo Tư Tưởng Phật gi&aacute;o Đại Thừa: Như c&ocirc; vừa tr&igrave;nh b&agrave;y ở tr&ecirc;n. Theo đ&oacute;, Long nữ th&agrave;nh Phật, chỉ l&agrave; một sự kiện ngụ ng&ocirc;n để tuy&ecirc;n bố tư tưởng MU&Ocirc;N LO&Agrave;I ĐỀU C&Oacute; THỂ TH&Agrave;NH PHẬT. Cũng theo đ&oacute;, loại giải th&iacute;ch n&agrave;y kh&ocirc;ng quan t&acirc;m tới chuyện Long nữ d&acirc;ng ch&acirc;u như thế n&agrave;o, hạt ch&acirc;u tượng trưng cho c&aacute;i g&igrave;, vừa ph&aacute;t t&acirc;m d&acirc;ng ch&acirc;u l&agrave; đ&atilde; th&agrave;nh Phật, hay vừa d&acirc;ng ch&acirc;u xong l&agrave; vừa th&agrave;nh Phật&hellip;</li>\r\n	<li>Theo Thiền T&ocirc;ng: Giải th&iacute;ch kỹ ch&acirc;u l&agrave; Ch&acirc;n T&acirc;m Phật T&iacute;nh. Long nữ d&acirc;ng ch&acirc;u cho Phật l&agrave; tr&igrave;nh kiến giải, rằng con đ&atilde; đại ngộ Ch&acirc;n T&acirc;m Phật T&iacute;nh Chủ Nh&acirc;n &Ocirc;ng&hellip;, v&agrave; đ&atilde; ngộ Bản T&acirc;m ấy th&igrave; đ&atilde; l&agrave; Phật rồi. Ngộ rồi th&igrave; th&agrave;nh Phật liền, c&ograve;n d&acirc;ng cho Phật coi &ndash; tức l&agrave; tr&igrave;nh kiến giải &ndash; th&igrave; l&agrave; chuyện phụ, c&oacute; tr&igrave;nh trước hay tr&igrave;nh sau g&igrave;, th&igrave; Long nữ cũng đ&atilde; l&agrave; Phật rồi, v&igrave; gi&aacute;c ngộ rồi.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Linh ạ, đ&oacute; l&agrave; 02 c&aacute;ch giải th&iacute;ch CH&Iacute;NH THỐNG. C&ograve;n lại th&igrave; cắt kh&uacute;c n&agrave;y lấp kh&uacute;c kh&aacute;c, &yacute; nọ xọ &yacute; kia, giải th&iacute;ch chồng ch&eacute;o, rồi từ đ&oacute; sinh ra nhiều tranh luận&hellip;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><u>T&Oacute;M LẠI:</u></p>\r\n\r\n<p>Qua sự tr&igrave;nh b&agrave;y tr&ecirc;n, chắc Linh giờ tự hỏi: &ldquo;Kh&uacute;c nọ lấp kh&uacute;c kia, sai l&agrave; phải rồi. Nhưng giờ đ&acirc;y, qua 02 c&aacute;ch hiểu CH&Iacute;NH THỐNG ở tr&ecirc;n, n&ecirc;n tin v&agrave;o thuyết n&agrave;o?&rdquo;</p>\r\n\r\n<p>H&agrave; h&agrave;, Linh muốn tin thuyết n&agrave;o th&igrave; tin. V&igrave; c&ocirc; kh&ocirc;ng d&aacute;m l&agrave;m cho niềm tin của người ban sơ học Phật th&ecirc;m rối rắm. Nhưng c&ocirc; cho Linh biết rằng, Phật gi&aacute;o Đại Thừa được h&igrave;nh th&agrave;nh tại Ấn Độ v&agrave;o khoảng đầu T&acirc;y Lịch, c&ograve;n Thiền T&ocirc;ng &ndash; một nh&aacute;nh của Đại Thừa &ndash; được h&igrave;nh th&agrave;nh tại Trung Quốc v&agrave;o cuối đời nh&agrave; Đường, tức khoảng thế kỷ X T&acirc;y Lịch.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&aacute;o lỗi, v&igrave; l&yacute; do kỹ thuật, c&acirc;u trả lời đến h&ocirc;m nay mới xong.</p>\r\n\r\n<p>Ch&uacute;c Linh an lạc.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><u>*** Ch&uacute; th&iacute;ch:</u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>1. Bồ T&aacute;t l&agrave; h&agrave;ng đệ tử Phật, tu theo Đại Thừa. Thinh Văn l&agrave; h&agrave;ng đệ tử Phật, tu theo Tiểu Thừa. (Đ&acirc;y l&agrave; giải th&iacute;ch theo từ ngữ Phật học, kh&ocirc;ng c&oacute; &yacute; khen ch&ecirc; khinh trọng)</p>\r\n\r\n<p>2. Trong <em>phẩm thứ ba [t&ecirc;n l&agrave;] Th&iacute; Dụ.</em></p>\r\n\r\n<p>3. Trong <em>phẩm thứ bảy [t&ecirc;n l&agrave;] H&oacute;a Th&agrave;nh Dụ.</em></p>\r\n', 'Ban Biên Tập'),
(19, 7, '<p><span style=\\"font-size: 12pt; font-family: Helvetica, sans-serif; color: rgb(69, 69, 69); background-position: initial initial; background-repeat: initial initial;\\">Xin sư c&ocirc; nhận x&eacute;t : Kh&ocirc;ng phải người xuất gia ,l&agrave; 1 cư sĩ (c&ocirc;ng nh&acirc;n ,c&ocirc;ng chức, bu&ocirc;n b&aacute;n) nhưng với t&acirc;m học Phật v&agrave; phương tiện gi&uacute;p người tu Phật c&oacute; mặc tăng b&agrave;o hay kh&ocirc;ng. c&oacute; g&igrave; kh&aacute;c? theo nhận x&eacute;t của co thế n&agrave;o</span></p>\r\n', 'Linh Thuy', '2013-01-26 11:00:21', '<p>Xin ch&agrave;o chị Linh Thuy!</p>\r\n\r\n<p>Cảm ơn chị đ&atilde; viếng thăm trang web v&agrave; đặt c&acirc;u hỏi.</p>\r\n\r\n<p>Chị ạ, v&igrave; kh&ocirc;ng hiểu r&otilde; c&acirc;u hỏi của chị, n&ecirc;n t&ocirc;i tạm nương theo lời hỏi ở tr&ecirc;n, chia ra mấy phần như sau v&agrave; trả lời. Nếu c&oacute; g&igrave; kh&ocirc;ng đ&uacute;ng &yacute; hỏi, xin chị gởi c&acirc;u hỏi sau, n&oacute;i r&otilde; &yacute; hơn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chị hỏi: 1. &ldquo;C&oacute; mặc Tăng b&agrave;o hay kh&ocirc;ng&rdquo;? v&agrave; 2. &ldquo;C&oacute; g&igrave; kh&aacute;c?&rdquo;</p>\r\n\r\n<p>Từ 02 c&acirc;u hỏi của chị, v&igrave; chưa hiểu r&otilde;, t&ocirc;i suy ra tới 03 điều chị thắc mắc:</p>\r\n\r\n<ol>\r\n	<li>Người cư sĩ, kh&ocirc;ng phải người xuất gia (tu sĩ), c&oacute; t&acirc;m học Phật v&agrave; c&oacute; phương tiện gi&uacute;p cho người ta (mọi người, cả tu sĩ lẫn cư sĩ kh&aacute;c) tu Phật. Người cư sĩ ấy c&oacute; <em>N&Ecirc;N </em>(hay c&oacute; <em>Đ&Aacute;NG</em>) mặc Tăng b&agrave;o hay kh&ocirc;ng?</li>\r\n	<li>Giữa việc người cư sĩ mặc Tăng b&agrave;o v&agrave; người cư sĩ kh&ocirc;ng mặc Tăng b&agrave;o c&oacute; g&igrave; kh&aacute;c?</li>\r\n	<li>Người mặc Tăng b&agrave;o (tu sĩ) v&agrave; người kh&ocirc;ng mặc Tăng b&agrave;o (cư sĩ) c&oacute; g&igrave; kh&aacute;c nhau?</li>\r\n</ol>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thưa chị!</p>\r\n\r\n<p>Từ khi Đức Phật đi truyền Đạo tới nay, đệ tử của Ng&agrave;i được ph&acirc;n ra l&agrave;m 02 hạng: 1.hạng từ bỏ gia đ&igrave;nh, sống kh&ocirc;ng gia đ&igrave;nh, l&agrave;m tu sĩ v&agrave; 2.hạng sống c&oacute; gia đ&igrave;nh, l&agrave;m cư sĩ.</p>\r\n\r\n<p>Hạng tu sĩ, v&igrave; gia duy&ecirc;n kh&ocirc;ng c&ograve;n bận buộc, hay nhận thấy rằng vấn đề gia duy&ecirc;n kh&ocirc;ng l&agrave;m hạnh ph&uacute;c được cuộc đời m&igrave;nh, n&ecirc;n theo Phật xuất gia, sống đời v&ocirc; gia cư, d&agrave;nh trọn đời cho học hỏi Phật Ph&aacute;p (gi&aacute;o l&yacute; Phật Đ&agrave;) v&agrave; thay Phật đi hoằng truyền ch&acirc;n l&yacute;, dạy dỗ hướng dẫn người d&acirc;n sống theo ch&aacute;nh Đạo.</p>\r\n\r\n<p>Hạng cư sĩ, v&igrave; gia duy&ecirc;n c&ograve;n bận buộc, kh&ocirc;ng thể dứt &aacute;o ra đi, hay kh&ocirc;ng th&uacute; vị lắm với đời sống th&ecirc;nh thang v&ocirc; gia cư, nhưng vẫn rất th&uacute; vị với lối sống v&agrave; gi&aacute;o Ph&aacute;p của Đức Phật, th&igrave; ở lại gia đ&igrave;nh, x&acirc;y dựng kinh tế v&agrave; hỗ trợ cho tầng lớp tu sĩ về cơm &aacute;o v&agrave; phương tiện hoằng Ph&aacute;p.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><u>Về phương diện h&igrave;nh thức:</u></strong> Khi một đất nước, một d&acirc;n tộc, một th&ocirc;n l&agrave;ng, một gia tộc, một gia đ&igrave;nh&hellip;ổn định về ch&iacute;nh trị v&agrave; kinh tế, th&igrave; văn h&oacute;a ph&aacute;t triển. Văn h&oacute;a ph&aacute;t triển, th&igrave; mọi thứ trong đ&oacute; được ph&acirc;n chia hạng mục thứ tự rạch r&ograve;i, để dễ bề quản l&yacute; v&agrave; sinh hoạt; tức l&agrave; lễ nghi hưng thịnh. Vua, c&oacute; &aacute;o m&atilde;o c&acirc;n đai của vua, nghi lễ của vua, sinh hoạt của vua&hellip;mọi người nh&igrave;n v&agrave;o th&igrave; biết ngay đ&oacute; l&agrave; vua.</p>\r\n\r\n<p>Cũng vậy, từ xưa, tu sĩ v&agrave; cư sĩ, c&oacute; quy định quần &aacute;o, nghi thức, sinh hoạt&hellip;kh&aacute;c nhau. Tăng b&agrave;o l&agrave; quần &aacute;o của tu sĩ. Quần &aacute;o của tu sĩ, tiếng Ấn Độ độ gọi l&agrave; Ca Sa, tiếng T&agrave;u dịch l&agrave; Hoại Sắc Y, tức l&agrave; y phục cũ r&aacute;ch &ndash; đồ bỏ. Tu sĩ ng&agrave;y xưa, v&agrave; nay cũng c&ograve;n, sống đời đơn giản, thiếu thốn, lấy tinh thần v&agrave; t&igrave;nh thương vạn loại l&agrave;m niềm vui. Mặc y phục cũ r&aacute;ch, thường l&agrave; vải cũ, đồ r&aacute;ch người đời quăng bỏ, thậm ch&iacute; l&agrave; vải liệm (vải tang). Ăn đồ xin, ai cho g&igrave; ăn nấy, rau th&uacute;i, củ &uacute;ng, t&ocirc; phở bỏ dở dang&hellip;Chỉ c&oacute; điều, với văn h&oacute;a Ấn Độ ng&agrave;y xưa, người đời rất trọng tu sĩ, n&ecirc;n tuy tu sĩ hạ m&igrave;nh như thế, m&agrave; người cư sĩ thường c&uacute;ng dường họ những thứ ngon qu&yacute; của gia đ&igrave;nh m&igrave;nh, mặc d&ugrave; c&oacute; rất nhiều người đời kh&ocirc;ng tin Phật Ph&aacute;p th&igrave; khinh khi v&agrave; quăng liệng đồ ăn cho tu sĩ, cũng như ng&agrave;y nay vậy th&ocirc;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><u>Về phương diện t&acirc;m hồn:</u></strong></p>\r\n\r\n<p>Tu sĩ v&agrave; cư sĩ c&oacute; thể chứng đắc Niết b&agrave;n tối thượng như nhau. Nghĩa l&agrave; c&ugrave;ng c&oacute; khả năng đạt được c&aacute;i hạnh ph&uacute;c cao nhất trong Phật gi&aacute;o như nhau.</p>\r\n\r\n<p>Nhưng tu sĩ th&igrave; c&oacute; nhiều điều kiện thuận lợi hơn cư sĩ, v&igrave; kh&ocirc;ng bận kiếm sống, v&igrave; được thấm đẫm trong gi&aacute;o Ph&aacute;p hằng ng&agrave;y, v&igrave; m&ocirc;i trường xuất gia được sống gần những bậc Thiện Tri Thức.</p>\r\n\r\n<p>Nếu l&agrave;m tu sĩ m&agrave; kh&ocirc;ng hưởng được c&aacute;i hạnh ph&uacute;c Niết b&agrave;n giải tho&aacute;t, m&agrave; hướng l&ograve;ng về t&igrave;m th&uacute; vui ở đời như cư sĩ, th&igrave; cũng ph&iacute; c&ocirc;ng đi xuất gia th&ocirc;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><u>T&oacute;m lại:</u></strong></p>\r\n\r\n<p>Xin trả lời c&acirc;u hỏi của chị:</p>\r\n\r\n<ol>\r\n	<li>Người cư sĩ, c&oacute; t&acirc;m học Phật v&agrave; c&oacute; phương tiện gi&uacute;p cho người ta tu Phật, người cư sĩ ấy rất xứng danh l&agrave; CƯ SĨ HỘ TR&Igrave; PHẬT PH&Aacute;P, v&agrave; chẳng cần g&igrave; quan t&acirc;m tới chuyện mặc &aacute;o v&aacute;, &aacute;o vải tang liệm cả.</li>\r\n	<li>Giữa việc người cư sĩ mặc Tăng b&agrave;o v&agrave; người cư sĩ kh&ocirc;ng mặc Tăng b&agrave;o c&oacute; g&igrave; kh&aacute;c? _ Cư sĩ th&igrave; đ&acirc;u cần mặc &aacute;o tu sĩ, v&igrave; &aacute;o quần nghi lễ l&agrave; để quy định ph&acirc;n biệt c&aacute;c th&agrave;nh phần trong x&atilde; hội.</li>\r\n	<li>Người mặc Tăng b&agrave;o (tu sĩ) v&agrave; người kh&ocirc;ng mặc Tăng b&agrave;o (cư sĩ) c&oacute; g&igrave; kh&aacute;c nhau?_Tu sĩ th&igrave; l&agrave; CHUY&Ecirc;N HỌC TẬP &amp; HOẰNG TRUYỀN PHẬT PH&Aacute;P, cư sĩ th&igrave; l&agrave; CHUY&Ecirc;N HỌC TẬP &amp; HỘ TR&Igrave; PHẬT PH&Aacute;P.</li>\r\n</ol>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&uacute;c chị an lạc.</p>\r\n', 'Ban Biên Tập'),
(20, 7, '<p><span style=\\"font-size: 12pt; font-family: Helvetica, sans-serif; color: rgb(69, 69, 69); background-position: initial initial; background-repeat: initial initial;\\">K&iacute;nh hỏi sư c&ocirc;: Tam ph&aacute;p ấn của Đức Phật dạy l&agrave; g&igrave;? K&iacute;nh nhờ sư c&ocirc; giải th&iacute;ch. Ch&acirc;n th&agrave;nh cảm ơn sư c&ocirc; Ch&uacute;c sư c&ocirc; ph&aacute;p thể khinh an, th&acirc;n t&acirc;m thường lạc,phật sự vi&ecirc;n th&agrave;nh.</span></p>\r\n', 'phật tử An Nhiên ', '2013-03-03 03:37:54', '<p>Mến ch&agrave;o Phật tử An Nhi&ecirc;n!</p>\r\n\r\n<p>Nghe c&acirc;u hỏi, đo&aacute;n rằng An Nhi&ecirc;n l&agrave; cận sự nữ (cư sĩ nữ, Phật tử nữ), phải kh&ocirc;ng chị hả?</p>\r\n\r\n<p>Chị An Nhi&ecirc;n hỏi về Tam Ph&aacute;p Ấn, c&oacute; lẽ chị đ&atilde; tham khảo &iacute;t nhiều c&aacute;c th&ocirc;ng tin tr&ecirc;n mạng Internet gần đ&acirc;y.</p>\r\n\r\n<p>Nhận được c&acirc;u hỏi của chị, t&ocirc;i đ&atilde; bỏ thời gian l&ecirc;n mạng 03 ng&agrave;y để t&igrave;m hiểu th&ocirc;ng tin, v&agrave; quả thật, với th&ocirc;ng tin thượng v&agrave;ng hạ c&aacute;m của Internet b&acirc;y giờ th&igrave; kh&ocirc;ng l&agrave;m rối người mới học tu mới lạ.</p>\r\n\r\n<p>Chị An Nhi&ecirc;n ạ!</p>\r\n\r\n<p><strong>A. Đ&acirc;y l&agrave; danh s&aacute;ch một số trang web về Tam Ph&aacute;p Ấn m&agrave; t&ocirc;i đ&atilde; cẩn thận xem qua:</strong></p>\r\n\r\n<p><strong>Tham khảo chung:</strong></p>\r\n\r\n<ol>\r\n	<li>Video: <a href="http://www.phatam.com/video/tags/tam-phap-an/">http://www.phatam.com/video/tags/tam-phap-an/</a></li>\r\n</ol>\r\n\r\n<p style="margin-left:.25in;">2.&nbsp;&nbsp;&nbsp; Trang web:</p>\r\n\r\n<p style="margin-left:49.5pt;">Tiếng Việt: <a href="https://www.google.com.vn/search?q=Tam+ph%C3%A1p+%E1%BA%A5n&amp;rlz=1C1_____enVN524&amp;aq=f&amp;oq=Tam+ph%C3%A1p+%E1%BA%A5n&amp;aqs=chrome.0.57&amp;sourceid=chrome&amp;ie=UTF-8">https://www.google.com.vn/search?q=Tam+ph%C3%A1p+%E1%BA%A5n&amp;rlz=1C1_____enVN524&amp;aq=f&amp;oq=Tam+ph%C3%A1p+%E1%BA%A5n&amp;aqs=chrome.0.57&amp;sourceid=chrome&amp;ie=UTF-8</a></p>\r\n\r\n<p style="margin-left:45.0pt;">Tiếng Hoa:</p>\r\n\r\n<p style="margin-left:.5in;"><a href="https://www.google.com/search?q=%E4%B8%89%E6%B3%95%E5%8D%B0">https://www.google.com/search?q=三法印</a><br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;<a href="https://www.google.com/search?q=%E4%B8%89%E6%B3%95%E5%8D%B0#hl=vi&amp;sclient=psy-ab&amp;q=%E4%B8%89%E6%B3%95%E5%8D%B0%EF%BC%8C%E5%8D%B0%E9%A1%BA&amp;oq=%E4%B8%89%E6%B3%95%E5%8D%B0%EF%BC%8C%E5%8D%B0%E9%A1%BA&amp;gs_l=serp.3...45555.54335.0.55545.9.9.0.0.0.0.194.1158.1j8.9.0...0.0...1c.1j4.5.psy-ab.wxQ7YBo-N4M&amp;pbx=1&amp;bav=on.2,or.r_gc.r_pw.r_qf.&amp;bvm=bv.43148975,d.aGc&amp;fp=d718d6c5fd858f02&amp;biw=1280&amp;bih=864">https://www.google.com/search?q=三法印，印顺</a></p>\r\n\r\n<p style="margin-left:.25in;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p><strong>&nbsp;C&aacute;c nội dung:</strong></p>\r\n\r\n<p style="margin-left: 45pt;">1.&nbsp;&nbsp;&nbsp; V&ocirc; thường, khổ, v&ocirc; ng&atilde;<br />\r\nThầy Tuệ Hải_ <a href="http://www.youtube.com/watch?v=h0Fa4q0qnVg">http://www.youtube.com/watch?v=h0Fa4q0qnVg</a>&nbsp;&nbsp;&nbsp;<br />\r\nBan Hoằng Ph&aacute;p, gi&aacute;o tr&igrave;nh_ <a href="http://quangduc.com/giaotrinh/01tamphapan.html">http://quangduc.com/giaotrinh/01tamphapan.html</a>&nbsp;<br />\r\nCh&ugrave;a Long Quang <a href="http://chualongquang.com/index.php/phatphap/bai-vit/168-bat-chanh-do">http://chualongquang.com/index.php/phatphap/bai-vit/168-bat-chanh-do</a><br />\r\nThầy Thiện Xu&acirc;n <a href="http://www.youtube.com/watch?v=5nWMFXCgnoY">http://www.youtube.com/watch?v=5nWMFXCgnoY</a></p>\r\n\r\n<p style="margin-left: 45pt;">&nbsp;</p>\r\n\r\n<p style="margin-left: 45pt;">2.&nbsp;&nbsp;&nbsp; V&ocirc; thường, v&ocirc; ng&atilde;, Niết b&agrave;n<br />\r\n<br />\r\n(Tương đối) Thầy Vi&ecirc;n Duy dịch _ Ch&ugrave;a Khu&ocirc;ng Việt &nbsp;&nbsp;&nbsp;<br />\r\n<a href="http://khuongviettu.com/site/index.php?option=com_content&amp;view=article&amp;id=219:tam-phap-n-&amp;catid=22:-thich-vien-duy&amp;Itemid=40">http://khuongviettu.com/site/index.php?option=com_content&amp;view=article&amp;id=219:tam-phap-n-&amp;catid=22:-thich-vien-duy&amp;Itemid=40</a><br />\r\nBan T&ocirc;n Gi&aacute;o Ch&iacute;nh Phủ<br />\r\n<a href="http://btgcp.gov.vn/Plus.aspx/vi/News/38/0/240/0/2927/Tam_phap_an_dau_hieu_nhan_biet_kinh_Phat">http://btgcp.gov.vn/Plus.aspx/vi/News/38/0/240/0/2927/Tam_phap_an_dau_hieu_nhan_biet_kinh_Phat</a><br />\r\n<br />\r\n(Sơ s&agrave;i)<br />\r\nHỏi đ&aacute;p Yahoo <a href="http://vn.answers.yahoo.com/question/index?qid=20080914051152AAWxfqm">http://vn.answers.yahoo.com/question/index?qid=20080914051152AAWxfqm</a><br />\r\nTrang web <a href="http://vuonhoaphatgiao.com/tu-dien-phat-hoc-ct/tam-phap-an-2685/">http://vuonhoaphatgiao.com/tu-dien-phat-hoc-ct/tam-phap-an-2685/</a><br />\r\nTrang web <a href="http://phathocdoisong.com/news/tam-phap-an.d-2151.aspx">http://phathocdoisong.com/news/tam-phap-an.d-2151.aspx</a><br />\r\n<br />\r\n(Chi ly)<br />\r\nH&ograve;a thượng Nhất Hạnh<br />\r\n<a href="http://www.thuvien-thichnhathanh.org/index.php/tac-ph-m/76-con-ng-gii-thoat/500-cgt-chng-11-tam-phap-n?showall=&amp;start=4">http://www.thuvien-thichnhathanh.org/index.php/tac-ph-m/76-con-ng-gii-thoat/500-cgt-chng-11-tam-phap-n?showall=&amp;start=4</a> <a href="http://cusi.free.fr/coban/pht0065.htm">http://cusi.free.fr/coban/pht0065.htm</a><br />\r\nH&ograve;a thượng Ấn Thuận <a href="http://www.yinshun.org.tw/books/21/yinshun21-14.html">http://www.yinshun.org.tw/books/21/yinshun21-14.html</a><br />\r\nTừ điển <a href="http://zh.wikipedia.org/wiki/%E4%B8%89%E6%B3%95%E5%8D%B0">http://zh.wikipedia.org/wiki/三法印</a></p>\r\n\r\n<p style="margin-left: 45pt;">&nbsp;</p>\r\n\r\n<p style="margin-left: 45pt;">3.&nbsp;&nbsp;&nbsp; Kh&ocirc;ng, v&ocirc; tướng, v&ocirc; t&aacute;c:<br />\r\nH&ograve;a thượng Nhất Hạnh <a href="http://langmai.org/tang-kinh-cac/vien-sach/giang-kinh/kinh-phap-an">http://langmai.org/tang-kinh-cac/vien-sach/giang-kinh/kinh-phap-an</a></p>\r\n\r\n<p style="margin-left: 45pt;">&nbsp;</p>\r\n\r\n<p style="margin-left: 45pt;">&nbsp;</p>\r\n\r\n<p style="margin-left: 45pt;">4.&nbsp;&nbsp;&nbsp; Kh&ocirc;ng, v&ocirc; t&aacute;c, v&ocirc; nguyện:<br />\r\nH&ograve;a thượng Tr&iacute; Quảng <a href="http://www.daotrangphaphoa.net/tam-phap-an.html">http://www.daotrangphaphoa.net/tam-phap-an.html</a></p>\r\n\r\n<p style="margin-left: 45pt;">&nbsp;</p>\r\n\r\n<p style="margin-left: 45pt;">&nbsp;</p>\r\n\r\n<p style="margin-left: 45pt;">Tam sao thất bổn<br />\r\nHỏi đ&aacute;p Yahoo.&nbsp; <a href="http://vn.answers.yahoo.com/question/index?qid=20080503044039AASym3L">http://vn.answers.yahoo.com/question/index?qid=20080503044039AASym3L</a><br />\r\nHỏi đ&aacute;p Yahoo.&nbsp; <a href="http://vn.answers.yahoo.com/question/index?qid=20080302045508AAuOo1p">http://vn.answers.yahoo.com/question/index?qid=20080302045508AAuOo1p</a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>B. V&agrave; đ&acirc;y l&agrave; c&acirc;u trả lời chắc chắn: </strong></p>\r\n\r\n<p>Tam Ph&aacute;p Ấn (<strong>三法印</strong>) l&agrave; 03 c&aacute;i con dấu, đ&oacute;ng dấu chứng nhận rằng c&aacute;i gi&aacute;o l&yacute; n&agrave;o đ&oacute; đ&uacute;ng thuộc về Ch&aacute;nh Ph&aacute;p, Phật Ph&aacute;p. 03 con dấu l&agrave;: 1) Chư h&agrave;nh v&ocirc; thường. 2) Chư ph&aacute;p v&ocirc; ng&atilde;. V&agrave; 3) Niết b&agrave;n tịch tĩnh (tịch tịnh <em>cũng được</em>).</p>\r\n\r\n<ol>\r\n	<li>Chư h&agrave;nh v&ocirc; thường: C&aacute;c (mọi) thứ hữu vi đều kh&ocirc;ng tồn tại vững bền, đều lu&ocirc;n biến đổi.</li>\r\n	<li>Chư ph&aacute;p v&ocirc; ng&atilde;: C&aacute;c (mọi) thứ hữu vi hay v&ocirc; vi đều kh&ocirc;ng tồn tại ri&ecirc;ng lẻ, độc lập, kh&ocirc;ng thể tự l&agrave;m chủ m&igrave;nh, kh&ocirc;ng thể tự sinh ra cũng như kh&ocirc;ng thể tự mất đi.</li>\r\n	<li>Niết b&agrave;n tịch tĩnh (tịnh): Niềm an vui lặng lẽ thật sự c&oacute; mặt tr&ecirc;n cuộc đời n&agrave;y, n&oacute; l&agrave; cảm gi&aacute;c vui sướng tối thượng m&agrave; con người c&oacute; thể c&oacute; được, v&agrave; n&oacute; c&oacute; mặt do con người c&ograve;n sống ở đời m&agrave; tận diệt được l&ograve;ng tư dục (khao kh&aacute;t mong cầu &iacute;ch kỷ).</li>\r\n</ol>\r\n\r\n<p><strong>C. Giải th&iacute;ch: </strong></p>\r\n\r\n<ul>\r\n	<li>H&agrave;nh: C&aacute;c thứ hữu vi, tức l&agrave; c&oacute; mặt, tồn tại v&agrave; ti&ecirc;u diệt do t&aacute;c động của c&aacute;i kh&aacute;c. V&iacute; dụ: C&aacute;i t&ocirc;, con g&agrave;&hellip;</li>\r\n	<li>Ph&aacute;p: Gồm cả hữu vi v&agrave; v&ocirc; vi. C&aacute;i g&igrave; hữu vi th&igrave; v&ocirc; thường. C&aacute;i g&igrave; v&ocirc; vi (c&oacute; mặt, tồn tại v&agrave; mất đi kh&ocirc;ng do t&aacute;c động của c&aacute;i kh&aacute;c) th&igrave; v&ocirc; ng&atilde;. V&iacute; dụ: C&aacute;i t&ocirc; (hữu vi), Niết b&agrave;n (v&ocirc; vi)&hellip;</li>\r\n</ul>\r\n\r\n<p>Kh&aacute;i niệm về h&agrave;nh, ph&aacute;p, v&ocirc; thường, v&ocirc; ng&atilde;, chị An Nhi&ecirc;n n&ecirc;n hiểu căn bản như t&ocirc;i giải th&iacute;ch ở tr&ecirc;n l&agrave; tạm đủ rồi. Sau n&agrave;y c&oacute; dịp t&igrave;m hiểu Phật học, sẽ thấy những &yacute; nghĩa s&acirc;u rộng hơn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>D. Chứng minh:</strong></p>\r\n\r\n<p>Chị An Nhi&ecirc;n sẽ hỏi, Tam Ph&aacute;p Ấn, nhiều định nghĩa qu&aacute; thế, rồi biết lấy c&aacute;i g&igrave; l&agrave; đ&uacute;ng nhất, n&ecirc;n tin ai?</p>\r\n\r\n<p>Chị ạ! C&aacute;i t&ecirc;n gốc l&agrave; c&aacute;i t&ocirc;i đ&aacute;p ở tr&ecirc;n, c&ograve;n những t&ecirc;n kh&aacute;c, do sự ph&acirc;n t&iacute;ch chữ nghĩa đ&ocirc;i ch&uacute;t kh&aacute;c nhau m&agrave; th&ocirc;i. Kh&ocirc;ng cần bận l&ograve;ng. Những ai đ&aacute;p kh&ocirc;ng đ&uacute;ng từng chữ (chư h&agrave;nh v&ocirc; thường, chư ph&aacute;p v&ocirc; ng&atilde;, niết b&agrave;n tịch tĩnh) th&igrave; cũng kh&ocirc;ng phải l&agrave; sai, chỉ l&agrave; vị ấy d&ugrave;ng trong một phạm tr&ugrave; định nghĩa kh&aacute;c. Ph&acirc;n t&iacute;ch chữ nghĩa th&igrave; c&aacute;c c&aacute;ch định nghĩa Tam Ph&aacute;p Ấn kia (Khổ, Kh&ocirc;ng, V&ocirc; Tướng, V&ocirc; T&aacute;c&hellip;) cũng chẳng c&oacute; g&igrave; kh&aacute;c biệt.</p>\r\n\r\n<p>Song nếu trả lời đơn giản, m&agrave; đ&uacute;ng theo trường lớp, th&igrave; n&ecirc;n trả lời như t&ocirc;i đ&aacute;p vậy.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>T&ocirc;i c&oacute; căn cứ g&igrave; kh&ocirc;ng?</strong></p>\r\n\r\n<p>Chị ạ,</p>\r\n\r\n<p><strong>Trong giới học thuật:</strong> Người ta hay nương v&agrave;o Từ Điển chuy&ecirc;n ng&agrave;nh, v&igrave; Từ Điển thường được soạn bởi những người c&oacute; tr&igrave;nh độ học thuật m&agrave; tới mức kh&aacute; cao. Trong c&aacute;c bộ Từ Điển Phật học c&oacute; tiếng xưa nay: Đo&agrave;n Trung C&ograve;n, Đạo Uyển (<a href="http://rongmotamhon.net/mainpage/daouyen_listt.php?tg=8">http://rongmotamhon.net/mainpage/daouyen_listt.php?tg=8</a>) v&hellip;v..&nbsp; đều định nghĩa như vậy.</p>\r\n\r\n<p><strong>Trong giới tu h&agrave;nh:</strong> Người ta hay nương v&agrave;o định nghĩa của c&aacute;c bậc Cao Tăng thạc học. Gần nhất, với Việt Nam, ta n&ecirc;n nghe theo H&ograve;a thượng Nhất Hạnh (<a href="http://www.thuvien-thichnhathanh.org/index.php/tac-ph-m/76-con-ng-gii-thoat/500-cgt-chng-11-tam-phap-n?showall=&amp;start=4">http://www.thuvien-thichnhathanh.org/index.php/tac-ph-m/76-con-ng-gii-thoat/500-cgt-chng-11-tam-phap-n?showall=&amp;start=4</a> <a href="http://cusi.free.fr/coban/pht0065.htm">http://cusi.free.fr/coban/pht0065.htm</a>) &ndash; người từng được mời v&agrave;o lưỡng viện quốc hội ở Nga v&agrave; Hoa Kỳ để hướng dẫn giới thiệu Phật Ph&aacute;p cho c&aacute;c bậc ch&iacute;nh kh&aacute;ch; với Trung Hoa, ta n&ecirc;n nghe theo H&ograve;a thượng Ấn Thuận (<a href="http://www.yinshun.org.tw/books/21/yinshun21-14.html">http://www.yinshun.org.tw/books/21/yinshun21-14.html</a>) &ndash; người được t&ocirc;n xưng l&agrave; c&acirc;y đại b&uacute;t số 01 trong thế kỷ XX của Phật học to&agrave;n thế giới, người đứng h&agrave;ng thứ 03 trong hệ thống Phật gi&aacute;o Bắc truyền kể từ sau Đức Phật Th&iacute;ch Ca M&acirc;u Ni; v&agrave; với bề d&agrave;y lịch sử Phật gi&aacute;o, d&ugrave; Bắc Truyền hay Nam Truyền, ai cũng phải c&uacute;i đầu m&agrave; lắng nghe &yacute; kiến của Bồ T&aacute;t Long Thọ trong <em>Đại Tr&iacute; Độ luận</em>, <em>quyển 22</em> (<a href="http://zh.wikipedia.org/wiki/%E4%B8%89%E6%B3%95%E5%8D%B0">http://zh.wikipedia.org/wiki/三法印</a>) - người m&agrave; tất cả hệ ph&aacute;i Nam T&ocirc;ng hay Bắc T&ocirc;ng Ấn Độ đều c&ocirc;ng nhận ng&agrave;i l&agrave; bậc Tổ Sư, người đứng h&agrave;ng thứ 01, sau Đức Phật Th&iacute;ch Ca M&acirc;u Ni tại Ấn Độ. Cả 03 vị n&agrave;y đều định nghĩa Tam Ph&aacute;p Ấn như vậy.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&uacute;c chị an lạc!</p>\r\n', 'Ban biên tập');
INSERT INTO `chuagiacquang_ask` (`id`, `id_category`, `question`, `author1`, `time`, `answer`, `author2`) VALUES
(21, 7, '<p align=\\"\\\\&quot;left\\\\&quot;\\">Con k&iacute;nh hỏi Thầy: Ng&agrave;y nay, con người sống trong sự ph&aacute;t triển thời đại, nhịp sống lu&ocirc;n bận rộn với những lo toan gia đ&igrave;nh, con c&aacute;i , sự nghiệp con người dần mất đi c&acirc;n bằng giữa đời sống vật chất v&agrave; tinh thần. Theo con để t&igrave;m sự c&acirc;n bằng th&igrave; ch&uacute;ng ta co thể t&igrave;m hiểu v&agrave; thực tập &quot;thiền&quot; như thế n&agrave;o. k&iacute;nh thưa thầy vậy&nbsp;&quot;thiền&quot; trong đạo Phật c&oacute; thể gi&uacute;p ch&uacute;ng ta cải thiện , ổn định n&acirc;ng cao đời sống tinh thần? K&iacute;nh xin thầy từ bi chỉ dạy đ&ocirc;i điều v&agrave; con k&iacute;nh hỏi thầy th&ecirc;m: Tổ sư thiền v&agrave; Như lai thiền phải hiểu như thế n&agrave;o ? k&iacute;nh nhờ Thầy giải th&iacute;ch. K&iacute;nh ch&uacute;c Thầy nhiều sức khỏe</p>\r\n', 'Nguyễn Văn Liệt ', '2013-08-21 09:19:23', '<p>Mến ch&agrave;o Nguyễn Văn Liệt!</p>\r\n\r\n<p>Ch&agrave;o mừng bạn đến với trang web Ch&ugrave;a Gi&aacute;c Quang, v&agrave; cảm ơn bạn đ&atilde; đặt c&acirc;u hỏi.</p>\r\n\r\n<p>V&igrave; bận một số việc, thời gian qua kh&ocirc;ng v&agrave;o yahoo!, n&ecirc;n kh&ocirc;ng kịp nhận c&acirc;u hỏi của bạn, xin th&agrave;nh thật c&aacute;o lỗi!</p>\r\n\r\n<p><strong style="line-height: 1.6em;"><u>I. &Yacute; bạn muốn hỏi l&agrave;:</u></strong></p>\r\n\r\n<ol>\r\n	<li>&ldquo;&hellip;con người dần mất đi c&acirc;n bằng giữa đời sống vật chất v&agrave; tinh thần...để t&igrave;m sự c&acirc;n bằng th&igrave; ch&uacute;ng ta co thể t&igrave;m hiểu v&agrave; thực tập&nbsp;&quot;thiền&quot; như thế n&agrave;o.&rdquo; ＞(t&ocirc;i hiểu)＞ <em>Để c&acirc;n bằng giữa đời sống vật chất v&agrave; tinh thần, cần phải t&igrave;m hiểu v&agrave; thực tập Thiền, c&oacute; đ&uacute;ng kh&ocirc;ng? V&agrave; t&igrave;m hiểu, thực tập Thiền như thế n&agrave;o?</em></li>\r\n</ol>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol>\r\n	<li>... \\&quot;thiền\\&quot; trong đạo Phật c&oacute; thể gi&uacute;p ch&uacute;ng ta cải thiện , ổn định n&acirc;ng cao đời sống tinh thần?</li>\r\n	<li>Tổ sư thiền v&agrave; Như lai thiền phải hiểu như thế n&agrave;o ?</li>\r\n</ol>\r\n\r\n<p><strong style="line-height: 1.6em;"><u>II. Kh&oacute; khăn</u></strong></p>\r\n\r\n<p>Bạn ạ! C&acirc;u hỏi của bạn khiến t&ocirc;i ph&acirc;n v&acirc;n, đắn đo kh&ocirc;ng &iacute;t. V&igrave; thật kh&oacute; m&agrave; trả lời. Kh&oacute;, vấn đề nằm ở người hỏi, v&agrave; cả ở người đ&aacute;p.</p>\r\n\r\n<p>Về ph&iacute;a bạn, kh&oacute; l&agrave; ở chỗ t&ocirc;i kh&ocirc;ng biết bạn c&oacute; phải l&agrave; Phật tử của c&aacute;c Thiền Viện kh&ocirc;ng, bạn c&oacute; từng đọc &iacute;t s&aacute;ch Thiền kh&ocirc;ng, hay chỉ l&agrave; Phật tử đơn thuần v&agrave; kh&ocirc;ng thuộc về t&ocirc;ng ph&aacute;i n&agrave;o. Kh&oacute; ch&iacute;nh l&agrave; ở chỗ nếu bạn c&oacute; một &iacute;t kiến thức rồi, v&agrave; những kiến thức ấy trở th&agrave;nh th&agrave;nh kiến, t&ocirc;i sợ t&ocirc;i n&oacute;i bạn kh&ocirc;ng chịu tin, kh&ocirc;ng chịu nghe, rồi lại đi hỏi chỗ kh&aacute;c, &lsquo;đa sư hư bệnh_nhiều thầy thuốc th&igrave; chết con bệnh&rsquo;, th&agrave;nh ra bạn c&agrave;ng bị rối tung th&ecirc;m, đau khổ nhiều th&ecirc;m.</p>\r\n\r\n<p>Về ph&iacute;a t&ocirc;i, kh&oacute; l&agrave; ở chỗ t&ocirc;i kh&ocirc;ng thể tr&igrave;nh b&agrave;y cho bạn r&otilde; vấn đề n&agrave;y trong một v&agrave;i trang giấy, v&igrave; đề t&agrave;i n&agrave;y thật rộng lớn, nếu muốn tr&igrave;nh b&agrave;y chứng cứ v&agrave; l&yacute; luận để thuyết phục người kh&aacute;c.</p>\r\n\r\n<p><span style="line-height: 1.6em;">Bạn ạ! Với 03 c&acirc;u hỏi của bạn, t&ocirc;i đ&atilde; đo&aacute;n bạn l&agrave; Phật tử của Thiền t&ocirc;ng Việt Nam, c&oacute; lẽ đ&atilde; được dạy &iacute;t nhiều tư tưởng Thiền v&agrave; Phật học, kh&ocirc;ng phải l&agrave; Phật tử chung chung đơn thuần. Ch&iacute;nh v&igrave; vậy m&agrave; t&ocirc;i ngần ngại, suy nghĩ v&agrave;i lần, để thư thả v&agrave;i h&ocirc;m, rồi mới chọn c&aacute;ch trả lời.</span></p>\r\n\r\n<p><strong style="line-height: 1.6em;"><u>III. Đầu ti&ecirc;n l&agrave; xin bạn bỏ qua th&agrave;nh kiến cũ m&agrave; lắng nghe t&ocirc;i</u></strong></p>\r\n\r\n<p>Thiền T&ocirc;ng hay truyền c&acirc;u chuyện một học giả tới hỏi Đạo một Thiền sư. Thiền sư r&oacute;t tr&agrave; mời học giả. Tr&agrave; tr&agrave;n m&agrave; Thiền sư vẫn r&oacute;t. Học giả hỏi, Thiền sư n&oacute;i, (&yacute; rằng) <em>nếu &ocirc;ng trong đầu c&ograve;n đầy th&agrave;nh kiến, cố chấp, cao ngạo, tự cho m&igrave;nh đ&uacute;ng, tự cho m&igrave;nh cao, th&igrave; những lời n&oacute;i Đạo của t&ocirc;i đ&acirc;u c&ograve;n chỗ m&agrave; nằm lại trong đầu &ocirc;ng</em>.</p>\r\n\r\n<p>T&ocirc;i cũng mong bạn như vậy.</p>\r\n\r\n<p><strong style="line-height: 1.6em;"><u>IV. Kế l&agrave; giới thiệu về Lịch sử Tư Tưởng Phật Gi&aacute;o</u></strong></p>\r\n\r\n<p>Từ khi Đức Phật Th&iacute;ch Ca th&agrave;nh Đạo, truyền b&aacute; Phật Ph&aacute;p đến nay, thời gian đ&atilde; trải qua tr&ecirc;n dưới 2500 năm, kh&ocirc;ng gian &ndash; từ lưu vực s&ocirc;ng Hằng tại Ấn Độ &ndash; đ&atilde; tỏa rộng ra to&agrave;n thế giới. Tăng đo&agrave;n &ndash; đo&agrave;n thể đệ tử Phật &ndash; từ một nh&oacute;m thống nhất tư tưởng dưới sự d&igrave;u dắt của Đức Phật khi Ng&agrave;i c&ograve;n sống, <em>đ&atilde; ph&aacute;t triển, đ&atilde; ph&acirc;n chia</em> th&agrave;nh hơn 18 bộ ph&aacute;i khi ở Ấn Độ v&agrave;i thế kỷ sau khi Phật mất; th&agrave;nh 03 hệ thống lớn l&agrave; Phật gi&aacute;o Bộ Ph&aacute;i, Phật gi&aacute;o Đại Thừa v&agrave; Phật gi&aacute;o Mật Thừa tại Ấn Độ; th&agrave;nh 10 t&ocirc;ng ph&aacute;i khi qua Trung Quốc; th&agrave;nh 13 (<em>hoặc hơn</em>) t&ocirc;ng ph&aacute;i khi qua Nhật Bản.</p>\r\n\r\n<p><span style="line-height: 1.6em;">Tư tưởng ch&acirc;n ch&iacute;nh của Đạo Phật &ndash; của Đức Phật Th&iacute;ch Ca &ndash; từ khi Ng&agrave;i mất đến nay, </span><em style="line-height: 1.6em;">đ&atilde; ph&aacute;t triển, đ&atilde; bị pha tạp, đ&atilde; bị hiểu lầm</em><span style="line-height: 1.6em;"> rất nhiều; </span><em style="line-height: 1.6em;">mặc d&ugrave; trong tất cả c&aacute;c hệ thống, c&aacute;c t&ocirc;ng ph&aacute;i kia, tư tưởng ch&acirc;n ch&iacute;nh n&agrave;y vẫn tồn tại</em><span style="line-height: 1.6em;">, tất nhi&ecirc;n l&agrave; n&oacute; giữ vị tr&iacute; khinh trọng kh&aacute;c nhau theo từng hệ thống, từng t&ocirc;ng ph&aacute;i.</span></p>\r\n\r\n<p><span style="line-height: 1.6em;">Trong phạm vi c&acirc;u trả lời n&agrave;y, t&ocirc;i kh&ocirc;ng đủ thời gian để n&oacute;i về </span><em style="line-height: 1.6em;">Tư tưởng ch&acirc;n ch&iacute;nh của Đạo Phật &ndash; của Đức Phật Th&iacute;ch Ca</em><span style="line-height: 1.6em;"> m&agrave; hiện đang tồn tại trong tất cả học thuyết, bộ ph&aacute;i, t&ocirc;ng ph&aacute;i Phật gi&aacute;o đương thời. Cũng như kh&ocirc;ng đủ thời gian để n&oacute;i về </span><em style="line-height: 1.6em;">Tư tưởng Phật gi&aacute;o</em><span style="line-height: 1.6em;"> </span><em style="line-height: 1.6em;">đ&atilde; ph&aacute;t triển, đ&atilde; bị pha tạp, đ&atilde; bị hiểu lầm</em><span style="line-height: 1.6em;"> đang tồn tại trong tất cả học thuyết, bộ ph&aacute;i, t&ocirc;ng ph&aacute;i Phật gi&aacute;o đương thời.</span></p>\r\n\r\n<p><span style="line-height: 1.6em;">T&ocirc;i xin bạn lắng nghe v&agrave; tin lời t&ocirc;i n&oacute;i, rằng </span><em style="line-height: 1.6em;">bản th&acirc;n một hệ ph&aacute;i, một t&ocirc;ng ph&aacute;i Phật gi&aacute;o &ndash; trong qu&aacute; tr&igrave;nh h&igrave;nh th&agrave;nh, ph&aacute;t triển của n&oacute; &ndash; cũng c&oacute; sự thay đổi tư tưởng theo thời gian, chứ kh&ocirc;ng hề giữ nguy&ecirc;n dạng như thuở ban đầu</em><span style="line-height: 1.6em;">. Thiền t&ocirc;ng cũng như vậy.</span></p>\r\n\r\n<p><strong><u>V. Kế l&agrave; trả lời 03 c&acirc;u hỏi</u></strong></p>\r\n\r\n<ol>\r\n	<li value="3">Tổ sư thiền v&agrave; Như lai thiền phải hiểu như thế n&agrave;o ?\r\n	<ul>\r\n		<li><strong>Theo H&ograve;a thượng Thanh Từ, vị Tổ Thiền t&ocirc;ng Việt Nam hiện đại</strong></li>\r\n	</ul>\r\n	</li>\r\n</ol>\r\n\r\n<p>Thiền t&ocirc;ng Việt Nam gần đ&acirc;y được phục hưng bởi H&ograve;a thượng Thanh Từ, cũng từ đ&oacute; 02 c&aacute;i t&ecirc;n kia mới xuất hiện khắp nơi. Vậy, t&igrave;m định nghĩa, tốt nhất l&agrave; xem c&aacute;i định nghĩa của H&ograve;a thượng. H&ograve;a thượng c&oacute; định nghĩa về 02 c&aacute;i t&ecirc;n n&agrave;y ở nhiều bộ s&aacute;ch, ở đ&acirc;y t&ocirc;i lấy 01 c&aacute;i định nghĩa ở quyển <em>Thiền sư Ni</em>, tr.03, <em>Lời đầu s&aacute;ch</em></p>\r\n\r\n<p style="margin-left:.25in;">&ldquo;...Như Lai Thiền l&agrave; Thiền PhổTh&ocirc;ng do Đức Phật dạy chung cho tất cả Tăng, Ni v&agrave; cư sỹ tu.</p>\r\n\r\n<p style="margin-left:.25in;">&nbsp;&nbsp; Tổ Sư Thiền l&agrave; Thiền đặc biệt, chỉ n&oacute;i ri&ecirc;ng cho tổ Ca Diếp truyền m&atilde;i đến Tổ Bồ Đề Đạt Ma v&agrave; truyền sang Trung Quốc đến Việt Nam&hellip;&rdquo;</p>\r\n\r\n<p>(Xem th&ecirc;m tại <a href="https://www.google.com.vn/search?q=T%E1%BB%95+s%C6%B0+thi%E1%BB%81n+v%C3%A0+Nh%C6%B0+lai+thi%E1%BB%81n&amp;oq=T%E1%BB%95+s%C6%B0+thi%E1%BB%81n+v%C3%A0+Nh%C6%B0+lai+thi%E1%BB%81n&amp;aqs=chrome.0.69i57.31745183j0&amp;sourceid=chrome&amp;ie=UTF-8">https://www.google.com.vn/search?q= Tổ sư thiền v&agrave; Như lai thiền</a>,</p>\r\n\r\n<p>[PDF] <a href="https://www.google.com.vn/url?sa=t&amp;rct=j&amp;q=&amp;esrc=s&amp;source=web&amp;cd=4&amp;cad=rja&amp;ved=0CEIQFjAD&amp;url=http%3A%2F%2Fthuvienhoasen.org%2Fimages%2Fupload%2FPDF_Books%2FHT__Thanh_Tu%2Fthiensuni.pdf&amp;ei=SXMMUpT2DuTm2QX0uYHgBA&amp;usg=AFQjCNEvt4TqyvE-7m8p7NhVsgI8w7sI9A&amp;sig2=wDvJCIxcWi1YLBplt49GmA&amp;bvm=bv.50723672,d.b2I">THIỀN SƯ&nbsp;NI - Thư Viện Hoa Sen</a>)</p>\r\n\r\n<p>＞＞ N&oacute;i như vậy c&oacute; nghĩa, <strong>Thiền t&ocirc;ng l&agrave; Tổ sư thiền, c&ograve;n lại tất cả c&aacute;c chủng loại thiền kh&aacute;c trong Phật gi&aacute;o gọi chung l&agrave; Như Lai thiền</strong>.</p>\r\n\r\n<ul>\r\n	<li><strong>Theo c&aacute;c học giả v&agrave; h&agrave;nh giả Phật gi&aacute;o ngo&agrave;i Thiền t&ocirc;ng</strong></li>\r\n</ul>\r\n\r\n<p>Gi&aacute;o sư Minh Chi, em ruột của H&ograve;a thượng Minh Ch&acirc;u, một học giả nổi tiếng, viết:</p>\r\n\r\n<p style="margin-left:.25in;">&ldquo;&hellip;Ph&aacute;p Sư Th&aacute;i Hư cũng như H&ograve;a Thượng Th&iacute;ch Minh Ch&acirc;u đều d&ugrave;ng c&aacute;c từ Như Lai Thiền để chỉ Thiền Nguy&ecirc;n Thủy v&agrave; Tổ Sư Thiền để chỉ c&aacute;c h&igrave;nh thức Thiền Ph&aacute;t Triển sau n&agrave;y do c&aacute;c Tổ Sư c&aacute;c Thiền ph&aacute;i nổi danh đề xướng v&agrave; truyền lại cho c&aacute;c đệ tử của m&igrave;nh.&rdquo; (<a href="http://chualagovap.org.vn/index.php?option=com_content&amp;view=article&amp;id=293:ban-v-thin-nguyen-thy-va-thin-phat-trin&amp;catid=94:thin&amp;Itemid=128">http://chualagovap.org.vn/index.php?option=com_content&amp;view=article&amp;id=293:ban-v-thin-nguyen-thy-va-thin-phat-trin&amp;catid=94:thin&amp;Itemid=128</a>)</p>\r\n\r\n<p>＞＞ N&oacute;i như vậy c&oacute; nghĩa, Ph&aacute;p sư Th&aacute;i Hư, một H&ograve;a thượng học giả lừng danh Trung Quốc, H&ograve;a thượng Minh Ch&acirc;u, một học giả uy t&iacute;n Việt Nam, cũng như em trai của Ng&agrave;i l&agrave; gi&aacute;o sư Minh Chi, d&ugrave;ng <strong>Như Lai thiền để chỉ cho Thiền Nguy&ecirc;n Thủy</strong> (tức l&agrave; Thiền của Phật gi&aacute;o Nam t&ocirc;ng-xưa gọi l&agrave; Tiểu thừa), c&ograve;n <strong>Tổ sư thiền l&agrave; Thiền Ph&aacute;t Triển</strong> (tức l&agrave; bao gồm Thiền t&ocirc;ng v&agrave; tất cả c&aacute;c loại Thiền kh&aacute;c của Phật gi&aacute;o Bắc t&ocirc;ng-xưa gọi l&agrave; Đại thừa).</p>\r\n\r\n<ul>\r\n	<li><strong>Theo v&agrave;i học giả v&agrave; h&agrave;nh giả thuộc Thiền t&ocirc;ng</strong></li>\r\n</ul>\r\n\r\n<p>Theo những vị n&agrave;y, <strong>Như Lai thiền ch&iacute;nh l&agrave; Tổ sư thiền, kh&ocirc;ng c&oacute; kh&aacute;c</strong>. Ở đ&acirc;y đơn cử một vị đại diện th&ocirc;i. Đ&acirc;y l&agrave; một đoạn văn của một vị học giả, đệ tử của Thiền ph&aacute;i Tr&uacute;c L&acirc;m Việt Nam hiện nay, đang l&agrave;m gi&aacute;o thọ dạy tại Học Viện Phật gi&aacute;o Việt Nam tại TP.HCM:</p>\r\n\r\n<table border="1" cellpadding="0" cellspacing="0">\r\n	<tbody>\r\n		<tr>\r\n			<td style="width:568px;">\r\n			<p><strong>B&agrave;i 4&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong>THIỀN TRỰC CHỈ=THIỀN T&Ocirc;NG</p>\r\n\r\n			<p align="center"><strong>SI&Ecirc;U PHẬT TỔ SƯ THIỀN</strong></p>\r\n\r\n			<p align="center"><strong>(NHƯ LAI THANH TỊNH THIỀN)</strong></p>\r\n\r\n			<p align="center">超佛祖師禪</p>\r\n\r\n			<p>Thời kỳ thứ hai giả lập t&ecirc;n l&agrave; Si&ecirc;u Phật Tổ Sư Thiền. Xưa nay thiền của c&aacute;c thời kỳ vốn l&agrave; huyết mạch th&ocirc;ng suốt nhau m&agrave; chẳng thể chia cắt, chẳng qua l&agrave; mỗi loại thiền c&oacute; một đặc điểm ri&ecirc;ng, giả lập t&ecirc;n gọi cốt chỉ d&ugrave;ng l&agrave;m ph&ugrave; hiệu để ph&acirc;n biệt m&agrave; th&ocirc;i. T&ecirc;n gọi n&agrave;y đ&atilde; được người đời sau d&ugrave;ng để chỉ Thiền t&ocirc;ng.</p>\r\n\r\n			<p>Thiền v&agrave;o thời kỳ n&agrave;y, tại sao gọi l&agrave; Si&ecirc;u Phật Tổ Sư Thiền? Bởi v&igrave; như ng&agrave;i Đan H&agrave; từng n&oacute;i: &ldquo;<em>Một chữ Phật ta chẳng th&iacute;ch nghe</em>.&rdquo; Ng&agrave;i Triệu Ch&acirc;u cũng n&oacute;i: &ldquo;<em>Niệm Phật một tiếng, phải s&uacute;c miệng ba ng&agrave;y</em>.&rdquo; Lại như ng&agrave;i Nam Tuyền thường n&oacute;i: &ldquo;<em>Tổ n&oacute;i tức t&acirc;m tức Phật, c&ograve;n t&ocirc;i th&igrave; chẳng phải t&acirc;m, chẳng phải Phật, chẳng phải vật.</em>&rdquo; Đ&acirc;y đều l&agrave; d&ugrave;ng si&ecirc;u Phật (kh&ocirc;ng chịu đặt m&igrave;nh v&agrave;o khu&ocirc;n mẫu của Phật) để n&oacute;i. Thời đ&oacute;, người đề ra c&acirc;u hỏi, đều l&agrave; hỏi: Đại &yacute; của Tổ sư từ Ấn Độ sang (Tổ sư t&acirc;y lai &yacute;), c&oacute; thể thấy đ&atilde; đem Phật đặt qua một b&ecirc;n v&agrave; chỉ d&ugrave;ng &yacute; của Tổ sư l&agrave;m trung t&acirc;m, lại c&oacute; thể thấy từ Lục Tổ trở xuống, T&ocirc;ng phong rất l&agrave; hưng thạnh. Thiền của Tổ sư truyền đ&atilde; được sự t&ocirc;n s&ugrave;ng của một số người tham học thời đ&oacute;. &Yacute; của Tổ sư từ Ấn Độ sang lại c&agrave;ng l&agrave; mục ti&ecirc;u trước nhất người học cần phải minh bạch n&ecirc;n mới trở th&agrave;nh thiền ph&aacute;p Si&ecirc;u Phật (kh&ocirc;ng chịu đặt m&igrave;nh v&agrave;o trong khu&ocirc;n mẫu của Phật) lấy Tổ sư l&agrave;m trung t&acirc;m.</p>\r\n\r\n			<p>Lấy đ&acirc;y so s&aacute;nh với qu&aacute; tr&igrave;nh ph&aacute;t triển của Mật t&ocirc;ng, th&igrave; lấy Mật ph&aacute;p của Kim Cang giới l&agrave;m trung t&acirc;m, cũng ch&iacute;nh l&agrave; cấp Du-d&agrave; Mật thứ ba trong bốn cấp m&agrave; T&acirc;y Tạng đ&atilde; ph&acirc;n chia. Kim Cang Tr&iacute; c&ugrave;ng với Bất Kh&ocirc;ng truyền Mật ph&aacute;p Kim Cang giới ở Trung Quốc, đ&atilde; chẳng đồng với Thai Tạng giới d&ugrave;ng Phật l&agrave;m trung t&acirc;m, Kim Cang giới c&oacute; khi d&ugrave;ng Đ&ocirc;ng Phương Bất Động Phật l&agrave;m trung t&acirc;m. Vả lại, đối với Phật v&agrave; Bồ-t&aacute;t, vị n&agrave;o m&agrave; đổi t&ecirc;n th&agrave;nh Kim Cang th&igrave; l&yacute; đều biến th&agrave;nh tr&iacute;, chỉ lấy Kim Cang l&agrave;m trung t&acirc;m, Phật thừa th&agrave;nh l&agrave; Kim Cang thừa. T&ocirc;ng M&ocirc;n Thiền v&agrave;o thời kỳ n&agrave;y, cũng d&ugrave;ng thiền ph&aacute;p của Tổ sư l&agrave;m trung t&acirc;m, Như Lai Thiền th&agrave;nh l&agrave; Tổ Sư Thiền. Đ&acirc;y l&agrave; đem sự ph&aacute;t triển Mật ph&aacute;p ở Ấn Độ để so s&aacute;nh với sự ph&aacute;t triển thiền ph&aacute;p ở Trung Quốc.</p>\r\n\r\n			<p>V&igrave; sao gọi l&agrave; Như Lai thanh tịnh thiền? Bởi v&igrave; đ&acirc;y l&agrave; ph&aacute;p thiền chỉ thẳng tự t&acirc;m (Như Lai thanh tịnh l&agrave; t&ecirc;n gọi kh&aacute;c của tự t&acirc;m), đốn ngộ tự t&acirc;m, kh&ocirc;ng trải qua thứ lớp tu h&agrave;nh như loại thiền truyền thống&nbsp; nương theo kinh gi&aacute;o.</p>\r\n\r\n			<p>&hellip;&hellip;</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>(Xem th&ecirc;m tại <a href="https://www.google.com.vn/search?q=T%E1%BB%95+s%C6%B0+thi%E1%BB%81n+v%C3%A0+Nh%C6%B0+lai+thi%E1%BB%81n&amp;oq=T%E1%BB%95+s%C6%B0+thi%E1%BB%81n+v%C3%A0+Nh%C6%B0+lai+thi%E1%BB%81n&amp;aqs=chrome.0.69i57.31745183j0&amp;sourceid=chrome&amp;ie=UTF-8">https://www.google.com.vn/search?q= Tổ sư thiền v&agrave; Như lai thiền</a>, [DOC]</p>\r\n\r\n<h3><a href="https://www.google.com.vn/url?sa=t&amp;rct=j&amp;q=&amp;esrc=s&amp;source=web&amp;cd=5&amp;cad=rja&amp;ved=0CEkQFjAE&amp;url=http%3A%2F%2Fwww.vbu.edu.vn%2Fapplication%2Fuploads%2Fsubjects%2Fmediahk1-k1%2Fhk1_thienhoc%2Fthien%2520hoc%2520dai%2520cuong%2520word%2F4_sieuphattosuthien.doc&amp;ei=SXMMUpT2DuTm2QX0uYHgBA&amp;usg=AFQjCNElB-_7jvhY_z8olZ7Pc_wqwllVwQ&amp;sig2=QRa0WLfZvGPFTidIC1bdWA&amp;bvm=bv.50723672,d.b2I">si&ecirc;u phật&nbsp;tổ sư thiền&nbsp;- HỌC VIỆN PHẬT GI&Aacute;O VIỆT NAM TẠI TPH</a>..)</h3>\r\n\r\n<ul>\r\n	<li><strong>Vậy th&igrave;, cuối c&ugrave;ng, hiểu theo ai l&agrave; đ&uacute;ng?</strong></li>\r\n</ul>\r\n\r\n<p>Bạn ạ! T&ocirc;i nghĩ ở trang web n&agrave;y, trong phạm vi trao đổi với bạn đ&acirc;y, kh&ocirc;ng phải l&agrave; diễn đ&agrave;n trao đổi học thuật, n&ecirc;n t&ocirc;i kh&ocirc;ng thể tr&igrave;nh b&agrave;y l&yacute; luận nhiều, e rằng bạn cảm thấy rối rắm, nhưng nếu trả lời qua loa, bạn lại kh&ocirc;ng cảm thấy đ&aacute;ng tin, rồi cũng uổng.</p>\r\n\r\n<p>Đ&agrave;nh tạm v&agrave;i d&ograve;ng học thuật &lsquo;kh&ocirc;ng ch&iacute;nh quy&rsquo; vậy!</p>\r\n\r\n<p>Bạn ạ!</p>\r\n\r\n<p align="left">Số l&agrave;, Thiền t&ocirc;ng của Trung Quốc ban đầu vốn kh&ocirc;ng phải được dựng l&ecirc;n từ Tổ sư Đạt Ma (tham khảo <em>Trung Hoa Thiền T&ocirc;ng sử </em>của H&ograve;a thượng Ấn Thuận <a href="http://www.yinshun.org.tw/books/32/yinshun32-00.html">http://www.yinshun.org.tw/books/32/yinshun32-00.html</a>), m&agrave; ch&iacute;nh thức được dựng l&ecirc;n nhờ Thần Hội, một vị đệ tử của Huệ Năng. Từ b&agrave;n tay gầy dựng của Thần Hội, Huệ Năng th&agrave;nh Tổ thứ 06 của Thiền t&ocirc;ng Trung Hoa t&iacute;nh từ Sơ Tổ Đạt Ma v&agrave; trở th&agrave;nh Tổ thứ 33 Thiền Ấn (Độ Trung) Hoa, v&igrave; &ndash; cũng từ Thần Hội &ndash; Đạt Ma l&agrave; Tổ thứ 28 Thiền T&ocirc;ng tại Ấn Độ.</p>\r\n\r\n<p align="left"><strong style="line-height: 1.6em;">Về c&aacute;i từ Như Lai Thiền</strong></p>\r\n\r\n<p align="left">Như Lai Thiền hay Như Lai Thanh Tịnh Thiền l&agrave; c&aacute;i từ xuất hiện trong <em>kinh Lăng Gi&agrave;</em>. Theo <em>kinh Lăng Gi&agrave;</em> th&igrave; Như Lai Thiền hay Như Lai Thanh Tịnh Thiền ch&iacute;nh l&agrave; cấp thiền cao nhất. H&agrave;nh giả tu tới đ&oacute; l&agrave; đạt tới đỉnh.</p>\r\n\r\n<p align="left">Căn cứ theo to&agrave;n bộ c&aacute;c thư tịch (s&aacute;ch vở) hiện c&ograve;n của Thiền t&ocirc;ng, th&igrave; tr&ecirc;n từ Đạt Ma xuống đến Huệ Khả, Tăng X&aacute;n, Đạo T&iacute;n v&agrave; Huệ Năng, kh&ocirc;ng vị n&agrave;o n&oacute;i tới c&aacute;i từ Như Lai Thiền cả. Người n&oacute;i đến từ Như Lai Thiền đầu ti&ecirc;n trong thư tịch Thiền t&ocirc;ng Trung Hoa l&agrave; Thần Hội &ndash; đệ tử Huệ Năng. Thần Hội cho rằng THIỀN T&Ocirc;NG CỦA HUỆ NĂNG L&Agrave; NHƯ LAI THIỀN (tham khảo b&agrave;i viết về Như Lai Thiền &amp; Tổ Sư Thiền của gi&aacute;o sư Phương Lập Thi&ecirc;n <a href="http://read.goodweb.cn/news/news_view.asp?newsid=51328">http://read.goodweb.cn/news/news_view.asp?newsid=51328</a>). Nhưng trong khi một số đệ tử của c&aacute;c d&ograve;ng Thiền kh&aacute;c &ndash; cũng l&agrave; con ch&aacute;u thuộc c&aacute;c d&ograve;ng truyền thừa từ Bồ Đề Đạt Ma &ndash; truyền thuyết rằng Thiền của Bồ Đề Đạt Ma lấy <em>kinh Lăng Gi&agrave;</em> (Ch&acirc;n Thường luận) l&agrave;m y cứ để tu h&agrave;nh, th&igrave; Thần Hội lấy tư tưởng <em>kinh Kim Cang</em> (T&iacute;nh Kh&ocirc;ng luận) l&agrave;m y cứ, bởi sư phụ của Thần Hội l&agrave; Huệ Năng v&agrave; sư tổ của Thần Hội l&agrave; Hoằng Nhẫn đều gi&aacute;c ngộ từ <em>kinh Kim Cang</em> v&agrave; ca tụng tư tưởng T&iacute;nh Kh&ocirc;ng luận. <u>Ch&uacute; th&iacute;ch th&ecirc;m: T&iacute;nh Kh&ocirc;ng luận, Duy Thức luận v&agrave; Ch&acirc;n Thường luận l&agrave; 03 hệ thống tư tưởng lớn v&agrave; <strong><em>ho&agrave;n to&agrave;n</em></strong> kh&aacute;c nhau của Phật gi&aacute;o Đại thừa.</u></p>\r\n\r\n<p align="left">Bắt đầu từ khi Thần Hội TH&Agrave;NH LẬP THIỀN T&Ocirc;NG, viết s&aacute;ch v&agrave; dựng lập t&ocirc;ng ph&aacute;i, th&igrave; c&aacute;c ph&aacute;i Thiền kia mới bắt đầu b&agrave;n luận tới từ Như Lai Thiền, tất nhi&ecirc;n l&agrave; định nghĩa theo <em>kinh Lăng Gi&agrave;</em> (Ch&acirc;n Thường luận). Một minh chứng cho nội dung t&ocirc;i vừa n&ecirc;u, l&agrave; từ ngay những vị đương thời với Thần Hội, thuộc h&agrave;ng đệ tử Huệ Năng, đ&atilde; xem Như Lai Thiền l&agrave; Thiền T&ocirc;ng, l&agrave; cảnh giới cao nhất của Thiền giả, qua c&acirc;u thơ sau trong <em>Chứng Đạo Ca</em> &ndash; một b&agrave;i trường thi nổi tiếng của Vĩnh Gia Huyền Gi&aacute;c, đệ tử ấn chứng cầu Ph&aacute;p của Huệ Năng:</p>\r\n\r\n<p align="left">&ldquo;Đốn gi&aacute;c liễu Như Lai Thiền</p>\r\n\r\n<p align="left">&nbsp;Lục độ, vạn hạnh, thể trung vi&ecirc;n&hellip;&rdquo;</p>\r\n\r\n<p align="left">(Nhanh ch&oacute;ng gi&aacute;c ngộ được rồi c&aacute;i Như Lai Thiền</p>\r\n\r\n<p align="left">&nbsp;Th&igrave; ngay trong đ&oacute;, lục độ, vạn hạnh đ&atilde; c&oacute; sẵn đầy đủ.)</p>\r\n\r\n<p><strong>＞＞</strong> <strong>Như vậy, từ mốc thời gian Thần Hội, c&aacute;c ph&aacute;i Thiền đều cho rằng THIỀN T&Ocirc;NG L&Agrave; NHƯ LAI THIỀN, song c&aacute;ch định nghĩa về Như Lai Thiền của nh&aacute;nh Thần Hội v&agrave; c&aacute;c nh&aacute;nh kh&aacute;c l&agrave; kh&aacute;c nhau.</strong></p>\r\n\r\n<p><strong style="line-height: 1.6em;">Về c&aacute;i từ Tổ Sư Thiền</strong></p>\r\n\r\n<p>C&aacute;i từ ngữ Tổ Sư Thiền để chỉ cho Thiền T&ocirc;ng, chỉ được xuất hiện đầu ti&ecirc;n trong thư tịch Thiền T&ocirc;ng Trung Hoa, l&agrave; bắt đầu từ Ngưỡng Sơn Huệ Tịch (807-883), đệ tử của Quy Sơn Linh Hựu, thuộc nh&aacute;nh thiền Hồng Ch&acirc;u &ndash; nh&aacute;nh của M&atilde; Tổ Đạo Nhất, vốn cũng thuộc về nh&aacute;nh Nam T&ocirc;ng T&agrave;o Kh&ecirc; (tức l&agrave; thiền của Huệ Năng).</p>\r\n\r\n<p>Trong bộ <em>Cảnh Đức Truyền Đăng lục, quyển 11, Vi&ecirc;n Ch&acirc;u Ngưỡng Sơn Huệ Tịch thiền sư</em> (được ghi trong Đại tạng kinh <em>Đại Ch&iacute;nh T&acirc;n Tu, quyển 51, trang 283</em>), ch&eacute;p:</p>\r\n\r\n<p>「師問香嚴：師弟近日見處如何？嚴曰：某甲卒說不得，乃有偈曰：去年貧未是貧，今年貧始是貧；去年貧無卓錐之地，今年貧錐也無。師曰：汝只得如來禪。未得祖師禪。」</p>\r\n\r\n<p>(&yacute;) Ngưỡng Sơn Huệ Tịch ch&ecirc; sư đệ l&agrave; Hương Nghi&ecirc;m Tr&iacute; Nh&agrave;n l&agrave;</p>\r\n\r\n<p>&ldquo;&hellip;<em>Chỉ đạt tới Như Lai Thiền, chưa đạt tới Tổ Sư Thiền</em><em>.</em>&rdquo;</p>\r\n\r\n<p><strong>＞＞</strong><strong>T</strong><strong>ừ đ&oacute;, trong Thiền giới bắt đầu xuất hiện từ TỔ SƯ THIỀN, chỉ cho Th</strong><strong>iền T&ocirc;ng Đốn Ngộ, c&ograve;n tiệm tu tiệm ngộ l&agrave; NHƯ LAI THIỀN, l&agrave; kh&ocirc;ng phải Thiền T&ocirc;ng.</strong></p>\r\n\r\n<p><span style="line-height: 1.6em;">D&ograve;ng học thuật cứ thế m&agrave; tr&ocirc;i, ai chịu ảnh hưởng d&ograve;ng thiền Quy Ngưỡng của Ngưỡng Sơn Huệ Tịch, th&igrave; cho THIỀN T&Ocirc;NG L&Agrave; TỔ SƯ THIỀN, THIỀN ĐỐN NGỘ. C&ograve;n c&aacute;c thiền ph&aacute;i kh&aacute;c vẫn cho THIỀN T&Ocirc;NG L&Agrave; NHƯ LAI THIỀN, vẫn đốn ngộ, vẫn tiệm tu&hellip;đủ thứ.</span></p>\r\n\r\n<p>H&ograve;a thượng Thanh Từ th&igrave; đi theo Thiền của Lục Tổ Huệ Năng &ndash; ch&iacute;nh thức được dựng l&ecirc;n từ Thần Hội &ndash; v&ocirc; niệm v&ocirc; trụ theo <em>kinh Kim Cang</em>, Ng&agrave;i c&ograve;n bị ảnh hưởng bởi c&aacute;i d&ograve;ng nhỏ t&aacute;ch ra từ d&ograve;ng Nam T&ocirc;ng T&agrave;o Kh&ecirc; của Huệ Năng l&agrave; Quy Ngưỡng t&ocirc;ng nữa, n&ecirc;n mới gọi Thiền T&ocirc;ng l&agrave; THIỀN ĐỐN NGỘ, l&agrave; TỔ SƯ THIỀN.</p>\r\n\r\n<p>C&aacute;c học giả hay Thiền giả kh&aacute;c, v&igrave; kh&ocirc;ng thuộc Thiền ph&aacute;i T&agrave;o Kh&ecirc;, hay Quy Ngưỡng, hay v&igrave; kh&ocirc;ng đủ tư liệu s&aacute;ch vở tham khảo, n&ecirc;n bị lầm lẫn m&agrave; định nghĩa kh&aacute;c đi&hellip;Cũng c&oacute; khi kh&ocirc;ng phải những vị đ&oacute; sai, m&agrave; c&oacute; thể l&agrave; c&aacute;c vị đ&oacute; tự đưa ra c&aacute;c định nghĩa về NHƯ LAI THIỀN hay TỔ SƯ THIỀN vậy.</p>\r\n\r\n<ul>\r\n	<li><strong>Vậy th&igrave;, cuối c&ugrave;ng, n&ecirc;n tin theo ai?</strong></li>\r\n</ul>\r\n\r\n<p>Bạn ạ, chắc bạn đồng &yacute; với t&ocirc;i, trong cuộc sống c&oacute; rất nhiều điều m&agrave; ta kh&ocirc;ng thể n&agrave;o hiểu thấu đ&aacute;o?</p>\r\n\r\n<p>Cũng vậy, trong Phật Ph&aacute;p, từ khi Đức Phật dựng lập Đạo Phật đến nay, rất nhiều triết thuyết, đường lối tu h&agrave;nh kh&aacute;c nhau đ&atilde; được lập ra, t&ugrave;y theo sự hiểu biết của vị Tổ sư m&agrave; lập t&ocirc;ng ph&aacute;i kh&aacute;c nhau, v&agrave; bạn thử nghĩ xem, chẳng c&oacute; vị n&agrave;o chịu rằng c&aacute;i t&ocirc;ng chỉ của ph&aacute;i m&igrave;nh l&agrave; thua k&eacute;m ph&aacute;i kh&aacute;c cả.</p>\r\n\r\n<p>Vậy th&igrave; sao?</p>\r\n\r\n<p>Vậy th&igrave;, ai c&oacute; duy&ecirc;n với ph&aacute;i n&agrave;o th&igrave; tu tập v&agrave; học hỏi theo ph&aacute;i ấy. Đặt niềm tin v&agrave;o vị Thầy n&agrave;o c&oacute; duy&ecirc;n với m&igrave;nh nhất, m&igrave;nh &lsquo;chấm&rsquo; nhất. Cần nhất l&agrave; phải biết t&ocirc;n trọng niềm tin v&agrave; gi&aacute;o l&yacute; của t&ocirc;ng ph&aacute;i kh&aacute;c, cũng như của t&ocirc;n gi&aacute;o kh&aacute;c.</p>\r\n\r\n<p>Tất nhi&ecirc;n, c&oacute; một con đường đi chung, khiến ta tho&aacute;t khỏi được c&aacute;i lỗi &lsquo;bệnh&rsquo; t&ocirc;ng ph&aacute;i &ndash; tự cho m&igrave;nh đ&uacute;ng, kh&ocirc;ng thấy người hay &ndash; đ&oacute; l&agrave; HỌC PHẬT (TẤT CẢ T&Ocirc;NG PH&Aacute;I, KINH S&Aacute;CH). Con đường học Phật chung nhất ấy, ở c&aacute;c trường Phật học đều c&oacute; dạy, thứ tự từ thấp đến cao.</p>\r\n\r\n<ul>\r\n	<li><strong>T&oacute;m lại</strong></li>\r\n</ul>\r\n\r\n<p>Về 02 loại Thiền, t&ocirc;i đ&atilde; giới thiệu sơ về định nghĩa cho bạn rồi đấy nh&eacute;. T&ocirc;i kh&ocirc;ng c&oacute; &yacute; coi nhẹ định nghĩa của từng t&ocirc;ng ph&aacute;i đ&acirc;u, mỗi ph&aacute;i đều c&oacute; c&aacute;i hay c&aacute;i dở ri&ecirc;ng của m&igrave;nh. Bạn cứ việc chọn lựa, theo nh&acirc;n duy&ecirc;n trong đời của m&igrave;nh.</p>\r\n\r\n<ol>\r\n	<li value="2">... \\&quot;thiền\\&quot; trong đạo Phật c&oacute; thể gi&uacute;p ch&uacute;ng ta cải thiện , ổn định n&acirc;ng cao đời sống tinh thần?</li>\r\n</ol>\r\n\r\n<p>T&ocirc;i nghĩ rằng, bạn hỏi c&acirc;u n&agrave;y, tức l&agrave; bạn đ&atilde; nghe ai đ&oacute; tuy&ecirc;n truyền.</p>\r\n\r\n<p>M&agrave; thật ra th&igrave; n&oacute; đ&uacute;ng, kh&agrave; kh&agrave;&hellip;</p>\r\n\r\n<p>Nhưng phải hiểu n&oacute; l&agrave; g&igrave; mới được. Đ&oacute; l&agrave; nội dung c&acirc;u hỏi thứ 01 của bạn.</p>\r\n\r\n<ol>\r\n	<li>&nbsp;&ldquo;&hellip;con người dần mất đi c&acirc;n bằng giữa đời sống vật chất v&agrave; tinh thần...để t&igrave;m sự c&acirc;n bằng th&igrave; ch&uacute;ng ta co thể t&igrave;m hiểu v&agrave; thực tập \\&quot;thiền\\&quot; như thế n&agrave;o.&rdquo; ＞(t&ocirc;i hiểu)＞ <em>Để c&acirc;n bằng giữa đời sống vật chất v&agrave; tinh thần, cần phải t&igrave;m hiểu v&agrave; thực tập Thiền, c&oacute; đ&uacute;ng kh&ocirc;ng? V&agrave; t&igrave;m hiểu, thực tập Thiền như thế n&agrave;o?</em></li>\r\n</ol>\r\n\r\n<p>Bạn hỏi đ&uacute;ng. Để c&acirc;n bằng giữa đời sống vật chất v&agrave; tinh thần, cần phải thực tập Thiền.</p>\r\n\r\n<p>Nhưng phải hiểu đ&uacute;ng về Thiền Phật gi&aacute;o th&igrave; mới thực h&agrave;nh c&oacute; hiệu quả c&acirc;n bằng đời sống được. Kh&ocirc;ng th&igrave; chỉ c&agrave;ng tạo th&ecirc;m &aacute;p lực m&agrave; th&ocirc;i.</p>\r\n\r\n<ul>\r\n	<li><strong>Trước ti&ecirc;n l&agrave; n&oacute;i về chữ Thiền trong Phật gi&aacute;o</strong></li>\r\n</ul>\r\n\r\n<p>Đạo Phật, từ thuở ban sơ, chỉ c&oacute; Ph&aacute;p v&agrave; Luật (hiểu đơn giản l&agrave; L&yacute; thuyết v&agrave; Thực h&agrave;nh).</p>\r\n\r\n<p>Đức Phật giảng dạy cho mọi người c&aacute;ch sống, LỜI DẠY đ&oacute; l&agrave; PH&Aacute;P. Đức Phật sống như vậy, như lời Ng&agrave;i dạy mọi người, mọi người theo lời dạy m&agrave; sống, NẾP SỐNG đ&oacute; gọi l&agrave; LUẬT.</p>\r\n\r\n<p>Trong nếp sống của Phật v&agrave; đệ tử Phật, đậm chất Thiền. Hay n&oacute;i r&otilde; hơn, nếp sống của Phật gi&aacute;o l&agrave; nếp sống Thiền. Đi, đứng, nằm, ngồi, n&oacute;i, n&iacute;n, động, tịnh&hellip;đều l&agrave; Thiền.</p>\r\n\r\n<p>Thiền từ đ&acirc;u đến? Từ gi&aacute;o l&yacute; (Ph&aacute;p) m&agrave; đến.</p>\r\n\r\n<p>THIỀN L&Agrave; G&Igrave;? L&Agrave; BU&Ocirc;NG XẢ. Bu&ocirc;ng xả tham vọng trong t&acirc;m hồn, bu&ocirc;ng xả trong h&agrave;nh vi, cử chỉ. V&igrave; bu&ocirc;ng xả, n&ecirc;n hay LẮNG ĐỌNG. V&igrave; bu&ocirc;ng xả, n&ecirc;n hay TỈNH THỨC (tỉnh t&aacute;o trong đời sống, nội tỉnh).</p>\r\n\r\n<p>Gi&aacute;o Ph&aacute;p dạy như thế n&agrave;o m&agrave; h&igrave;nh th&agrave;nh nếp sống Thiền? Phật dạy: (đại &yacute;) Tham l&agrave; khổ, &iacute;ch kỷ l&agrave; khổ, đấu đ&aacute; hơn thua l&agrave; khổ, cuối đời rồi ai cũng chết &ndash; b&igrave;nh đẳng m&agrave; chết.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thời nay, &aacute;p lực từ đời sống, một phần v&igrave; người ta tham m&agrave; ch&egrave;n &eacute;p m&igrave;nh, một phần v&igrave; m&igrave;nh tham m&agrave; tự ch&egrave;n &eacute;p.</p>\r\n\r\n<p>Cho n&ecirc;n, Thiền của Phật gi&aacute;o l&agrave; BU&Ocirc;NG XẢ. Kh&ocirc;ng phải l&agrave; bạn mang c&aacute;i t&acirc;m tham lam đấu đ&aacute; với người đời m&agrave; đi v&agrave;o Thiền đường, ngồi đ&egrave; n&eacute;n n&oacute;, ch&eacute;m chặt n&oacute;, mong n&oacute; hết m&agrave; n&oacute; hết đ&acirc;u. Cũng kh&ocirc;ng phải Thiền của Phật gi&aacute;o l&agrave; bạn mang cả c&aacute;i bầu t&acirc;m sự tham s&acirc;n tật đố đ&oacute; m&agrave; chồng th&ecirc;m c&aacute;i ảo gi&aacute;c do ngồi l&acirc;u đ&egrave; n&eacute;n tư tưởng m&agrave; c&oacute; được &ndash; rất nhiều người lầm lạc ở điểm n&agrave;y.</p>\r\n\r\n<ul>\r\n	<li><strong>Sau c&ugrave;ng l&agrave; giới thiệu bạn một số c&aacute;ch thực tập Thiền Phật gi&aacute;o</strong></li>\r\n</ul>\r\n\r\n<p>Trước mắt, bạn n&ecirc;n bỏ bớt tham vọng.</p>\r\n\r\n<p>Sau đ&oacute;, tốt nhất l&agrave; bạn đến thực tập ở một trung t&acirc;m tu học n&agrave;o đ&oacute; của Phật gi&aacute;o, Đạo tr&agrave;ng Niệm Phật cũng được &ndash; v&igrave; đ&oacute; cũng l&agrave; nếp sống Thiền &ndash; Đạo tr&agrave;ng tọa thiền cũng được.</p>\r\n\r\n<p>Sau c&ugrave;ng, bạn về nh&agrave; th&agrave;nh lập một lịch sinh hoạt cho n&oacute; c&oacute; &lsquo;Đạo vị&rsquo;. Nghĩa l&agrave; sinh hoạt theo kiểu người Phật tử c&oacute; tu tập, kh&ocirc;ng chạy theo danh lợi quyền t&igrave;nh, t&ocirc;n trọng cuộc sống mọi lo&agrave;i xung quanh, thương y&ecirc;u, vun đắp mọi lo&agrave;i, cầu mong mọi lo&agrave;i đều sống được an l&agrave;nh hạnh ph&uacute;c.</p>\r\n\r\n<p><span style="line-height: 1.6em;">T&ocirc;i đ&atilde; trả lời xong rồi. Cảm ơn bạn đ&atilde; chịu kh&oacute; đọc những lời trả lời n&agrave;y. Xin lỗi bạn nếu n&oacute; l&agrave;m bạn kh&oacute; hiểu hoặc l&agrave;m bạn rối rắm khổ đau th&ecirc;m trong đời sống. Nhưng thật l&ograve;ng, t&ocirc;i ch&uacute;c bạn được an l&agrave;nh.</span></p>\r\n', 'Ban Biên tập - Thích Nữ Huệ Thanh');
