INSERT INTO `chuagiacquang_class_lession` (`id`, `id_class`, `id_monk`, `name`, `date_start`, `date_end`, `description`, `order`) VALUES
(1, 12, 15, 'Buổi 1 - Hạnh phúc của người Phật tử_so sánh với hạnh phúc của người đời', '2012-09-26 07:30:00', '2012-09-26 11:00:00', '<p><img alt="" src="https://lh4.googleusercontent.com/--0-70qL4ATw/UM4QpU44DUI/AAAAAAAABds/_yh0Nwe14yc/s800/LopGiaoLyB1.jpg" style="width: 590px; height: 180px;" /></p>\r\n\r\n<p style="font-size: 13px; text-align: center;"><span style="font-size:20px;">CHƯƠNG TR&Igrave;NH Đ&Agrave;O TẠO LỚP GI&Aacute;O L&Yacute; PHẬT TỬ</span></p>\r\n\r\n<p style="font-size: 13px; text-align: center;"><span style="font-size:20px;">Phần I: L&agrave; Một Cư Sĩ Tốt</span></p>\r\n\r\n<p style="font-size: 13px;"><span style="font-size: 13px;"><strong>Thời gian</strong>: 04 th&aacute;ng/08 tiết</span></p>\r\n\r\n<p style="font-size: 13px;"><strong>Gi&aacute;o Thọ</strong>: <em><strong>Thượng tọa Th&iacute;ch Th&ocirc;ng Ấn</strong></em> (ch&ugrave;a Long H&ograve;a, Vĩnh Long)</p>\r\n\r\n<p style="font-size: 13px;"><span style="font-size: 13px;"><strong>Phương ph&aacute;p</strong>: Giảng, ghi ch&eacute;p_ph&aacute;t dĩa_cho c&acirc;u hỏi chuẩn bị, thi.</span></p>\r\n\r\n<p style="font-size: 13px;"><u style="font-size: 13px;"><strong>Địa điểm</strong></u><span style="font-size: 13px;">: Đạo Tr&agrave;ng ch&ugrave;a Gi&aacute;c Quang.</span></p>\r\n\r\n<p style="font-size: 13px;"><u style="font-size: 13.333333969116211px;"><strong>A. D&Agrave;N B&Agrave;I:</strong></u></p>\r\n\r\n<table align="left" border="1" cellpadding="1" cellspacing="1" style="width: 500px;">\r\n	<thead>\r\n		<tr>\r\n			<th scope="col">STT</th>\r\n			<th scope="col">THỜI GIAN</th>\r\n			<th scope="col">CHỦ ĐỀ</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td style="text-align: center;">1</td>\r\n			<td>11-8al-Nh&acirc;m Th&igrave;n</td>\r\n			<td>(Giới thiệu) Hạnh ph&uacute;c của người Phật tử_so s&aacute;nh với hạnh ph&uacute;c của người đời</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align: center;">2</td>\r\n			<td>11-9al-Nh&acirc;m Th&igrave;n</td>\r\n			<td>(Chỉ ra) Hạnh ph&uacute;c của người Phật tử_trong đời thường</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align: center;">3</td>\r\n			<td>11-10al-Nh&acirc;m Th&igrave;n</td>\r\n			<td>Phật tử những điều cần tr&aacute;nh</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align: center;">4</td>\r\n			<td>11-11al-Nh&acirc;m Th&igrave;n</td>\r\n			<td>Phật tử những điều cần l&agrave;m</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style="font-size: 13px;">&nbsp;</p>\r\n\r\n<p style="font-size: 13px;">&nbsp;</p>\r\n\r\n<p style="font-size: 13px;">&nbsp;</p>\r\n\r\n<p style="font-size: 13px;">&nbsp;</p>\r\n\r\n<p style="font-size: 13px;"><u style="font-size: 13px;"><strong>B, VIDEO B&Agrave;I GIẢNG:</strong></u></p>\r\n\r\n<p style="font-size: 13px;"><u><strong><iframe frameborder="0" height="320" scrolling="no" src="http://www.youtube.com/embed/1MlhGosY6vQ" width="480"></iframe></strong></u></p>\r\n\r\n<p style="font-size: 13px;"><u><strong>C. C&Acirc;U HỎI &Ocirc;N TẬP</strong></u></p>\r\n\r\n<p style="font-size: 13px;"><strong>Thời gian</strong>:&nbsp;TH&Aacute;NG 01&nbsp;(11-8al-Nh&acirc;m Th&igrave;n)<br />\r\n<strong>Chủ đề</strong>:&nbsp;(Giới thiệu) Hạnh ph&uacute;c của người Phật tử<br />\r\n<strong>Mục đ&iacute;ch</strong>:&nbsp;Chỉ cho người cư sĩ tại gia biết c&aacute;i hạnh ph&uacute;c m&igrave;nh đang c&oacute; trong cuộc sống.</p>\r\n\r\n<ul style="font-size: 13px;">\r\n	<li><strong>C&acirc;u 1</strong>:&nbsp;Suốt buổi học, ắt hẳn qu&yacute; vị đ&atilde; nghe được nhiều c&acirc;u Ph&aacute;p, nhiều &yacute; nghĩa hay, v&agrave; qu&yacute; vị th&iacute;ch, t&acirc;m đắc. H&atilde;y n&oacute;i lại một c&acirc;u, một &yacute; nghĩa n&agrave;o đ&oacute;, v&agrave; giải th&iacute;ch tại sao n&oacute; hay, hoặc l&yacute; do m&igrave;nh t&acirc;m đắc.</li>\r\n	<li><strong>C&acirc;u 2</strong>:&nbsp;Hạnh ph&uacute;c l&agrave; cảm gi&aacute;c sung sướng, h&agrave;i l&ograve;ng, ngọt ng&agrave;o thỏa m&atilde;n, vui vẻ b&igrave;nh an trong l&ograve;ng trong một l&uacute;c n&agrave;o đ&oacute;, ở một nơi n&agrave;o đ&oacute;. H&atilde;y kể về một hạnh ph&uacute;c c&ograve;n in đậm trong l&ograve;ng m&igrave;nh, từ khi m&igrave;nh được l&agrave;m quen với ch&ugrave;a chiền, được tu h&agrave;nh v&agrave; được học hỏi Phật Ph&aacute;p đến nay.</li>\r\n	<li><strong>C&acirc;u 3</strong>:&nbsp;H&atilde;y t&igrave;m trong dĩa giảng một c&acirc;u xứng đ&aacute;ng để t&oacute;m tắt to&agrave;n bộ nội dung b&agrave;i giảng th&aacute;ng 01 (b&aacute;m s&aacute;t chủ đề) m&agrave; ch&iacute;nh Thầy Gi&aacute;o Thọ đ&atilde; n&oacute;i trong b&agrave;i học.</li>\r\n	<li><strong>C&acirc;u 4</strong>:&nbsp;H&atilde;y chọn c&acirc;u đ&uacute;ng nhất về c&aacute;i từ m&agrave; Thầy Gi&aacute;o Thọ d&ugrave;ng để chỉ cho c&aacute;i hạnh ph&uacute;c ch&acirc;n thật trong Phật gi&aacute;o:<br />\r\n	a. Ch&acirc;n hạnh ph&uacute;c &nbsp; &nbsp; &nbsp;b. An vui &nbsp; &nbsp; &nbsp; &nbsp;c. Cả hai đều đ&uacute;ng</li>\r\n	<li><strong>C&acirc;u 5</strong>:&nbsp;H&atilde;y kể c&aacute;i t&ecirc;n 04 chủ đề trong 04 th&aacute;ng học v&agrave; cho biết Ban Tổ chức muốn cung cấp cho qu&yacute; vị c&aacute;i g&igrave; trong kh&oacute;a học n&agrave;y ?</li>\r\n	<li><strong>C&acirc;u 6</strong>:&nbsp;Thầy Gi&aacute;o Thọ đ&atilde; chia trọn b&agrave;i học ra l&agrave;m 08 phần:<br />\r\n	03:00_05:30 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 05:30_13:00 &nbsp; &nbsp; &nbsp; &nbsp; 13:00_43:00 &nbsp; &nbsp; &nbsp;43:00_48:15 &nbsp; &nbsp; &nbsp;48:15_54:40 &nbsp; &nbsp; 54:40_1:00:00 &nbsp; &nbsp; &nbsp; &nbsp;1:00:00_1:15:37 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;1:15:37_hết&nbsp;<br />\r\n	H&atilde;y ghi t&ecirc;n ti&ecirc;u đề của từng phần thời gian ấy.</li>\r\n	<li><strong>C&acirc;u 7</strong>: &nbsp;H&atilde;y giải th&iacute;ch 04 chữ Quy y Tam Bảo.</li>\r\n	<li><strong>C&acirc;u 8</strong>:&nbsp;Trong b&agrave;i học, Thầy Gi&aacute;o Thọ cho ta biết, quy y Tam Bảo cho ta c&aacute;i hạnh ph&uacute;c g&igrave;? Cũng d&ugrave;ng lời của Thầy, h&atilde;y giải th&iacute;ch v&agrave;i n&eacute;t về c&aacute;i hạnh ph&uacute;c ấy.</li>\r\n	<li><strong>C&acirc;u 9</strong>: H&atilde;y kể t&ecirc;n đơn giản về 05 giới.</li>\r\n	<li><strong>C&acirc;u 10</strong>: H&atilde;y giải th&iacute;ch đơn giản về nội dung 05 giới.</li>\r\n	<li><strong>C&acirc;u 11</strong>: Giữ giới s&aacute;t sinh, cho ta hạnh ph&uacute;c g&igrave; ?</li>\r\n	<li><strong>C&acirc;u 12</strong>:&nbsp;Giữ giới trộm cắp, cho ta hạnh ph&uacute;c g&igrave; ?</li>\r\n	<li><strong>C&acirc;u 13</strong>:&nbsp;Giữ giới t&agrave; d&acirc;m, cho ta hạnh ph&uacute;c g&igrave; ?</li>\r\n	<li><strong>C&acirc;u 14</strong>:&nbsp;Giữ giới n&oacute;i dối, cho ta hạnh ph&uacute;c g&igrave; ?</li>\r\n	<li><strong>C&acirc;u 15</strong>:&nbsp;Giữ giới uống rượu, cho ta hạnh ph&uacute;c g&igrave; ?</li>\r\n	<li><strong>C&acirc;u 16</strong>:&nbsp;Muốn giữ được tr&ograve;n vẹn 05 giới, theo b&agrave;i học, ta n&ecirc;n hạ thủ c&ocirc;ng phu v&agrave;o nơi n&agrave;o?</li>\r\n	<li><strong>C&acirc;u 17</strong>:&nbsp;Theo b&agrave;i học, hạnh ph&uacute;c ch&acirc;n thật của đạo Phật c&oacute; phải được nhận thấy duy nhất ở tương lai kh&ocirc;ng? H&atilde;y n&ecirc;u một c&acirc;u n&oacute;i của Thầy Gi&aacute;o Thọ để chứng minh lời đ&aacute;p của m&igrave;nh.</li>\r\n	<li><strong>C&acirc;u 18</strong>:&nbsp;Vọng tưởng l&agrave; g&igrave; ? N&oacute; nguy hại thế n&agrave;o? Trong Đạo Tr&agrave;ng Niệm Phật n&agrave;y, tu phương ph&aacute;p g&igrave; để ta kh&ocirc;ng bị chạy theo vọng tưởng ?<br />\r\n	&nbsp;</li>\r\n</ul>\r\n\r\n<p align="center"><span style="font-size:16px;"><span style="font-family:times new roman,times,serif;"><em><strong>--- Hết th&aacute;ng 01&nbsp;---</strong></em></span></span></p>\r\n', 2),
(2, 12, 15, 'Buổi 2 - Hạnh phúc của người Phật tử_trong đời thường', '2012-10-25 07:30:00', '2012-10-25 11:00:00', '<p><img alt="" src="https://lh6.googleusercontent.com/-EtEQgtHilWk/UM4Qphtx6iI/AAAAAAAABdw/Ry43tgaeUfY/s800/LopGiaoLyB2.jpg" style="width: 590px; height: 180px;" /></p>\r\n\r\n<p style="font-size: 13px; text-align: center;"><span style="font-size: 20px;">CHƯƠNG TR&Igrave;NH Đ&Agrave;O TẠO LỚP GI&Aacute;O L&Yacute; PHẬT TỬ</span></p>\r\n\r\n<p style="font-size: 13px; text-align: center;"><span style="font-size: 20px;">Phần I: L&agrave; Một Cư Sĩ Tốt</span></p>\r\n\r\n<p style="font-size: 13px;"><span style="font-size: 13px;"><strong>Thời gian</strong>: 04 th&aacute;ng/08 tiết</span></p>\r\n\r\n<p style="font-size: 13px;"><strong>Gi&aacute;o Thọ</strong>:&nbsp;<em><strong>Thượng tọa Th&iacute;ch Th&ocirc;ng Ấn</strong></em>&nbsp;(ch&ugrave;a Long H&ograve;a, Vĩnh Long)</p>\r\n\r\n<p style="font-size: 13px;"><span style="font-size: 13px;"><strong>Phương ph&aacute;p</strong>: Giảng, ghi ch&eacute;p_ph&aacute;t dĩa_cho c&acirc;u hỏi chuẩn bị, thi.</span></p>\r\n\r\n<p style="font-size: 13px;"><u style="font-size: 13px;"><strong>Địa điểm</strong></u><span style="font-size: 13px;">: Đạo Tr&agrave;ng ch&ugrave;a Gi&aacute;c Quang.</span></p>\r\n\r\n<p style="font-size: 13px;"><u><strong>A. D&Agrave;N B&Agrave;I:</strong></u></p>\r\n\r\n<table align="left" border="1" cellpadding="1" cellspacing="1" style="font-size: 13.333333969116211px; width: 500px;">\r\n	<thead>\r\n		<tr>\r\n			<th scope="col">STT</th>\r\n			<th scope="col">THỜI GIAN</th>\r\n			<th scope="col">CHỦ ĐỀ</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td style="text-align: center;">1</td>\r\n			<td>11-8al-Nh&acirc;m Th&igrave;n</td>\r\n			<td>(Giới thiệu) Hạnh ph&uacute;c của người Phật tử_so s&aacute;nh với hạnh ph&uacute;c của người đời</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align: center;">2</td>\r\n			<td>11-9al-Nh&acirc;m Th&igrave;n</td>\r\n			<td>(Chỉ ra) Hạnh ph&uacute;c của người Phật tử_trong đời thường</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align: center;">3</td>\r\n			<td>11-10al-Nh&acirc;m Th&igrave;n</td>\r\n			<td>Phật tử những điều cần tr&aacute;nh</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align: center;">4</td>\r\n			<td>11-11al-Nh&acirc;m Th&igrave;n</td>\r\n			<td>Phật tử những điều cần l&agrave;m</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style="font-size: 13.333333969116211px;">&nbsp;</p>\r\n\r\n<p style="font-size: 13.333333969116211px;">&nbsp;</p>\r\n\r\n<p style="font-size: 13.333333969116211px;">&nbsp;</p>\r\n\r\n<p style="font-size: 13.333333969116211px;">&nbsp;</p>\r\n\r\n<p style="font-size: 13px;">&nbsp;</p>\r\n\r\n<p style="font-size: 13px;">&nbsp;</p>\r\n\r\n<p style="font-size: 13px;">&nbsp;</p>\r\n\r\n<p style="font-size: 13px;"><u><strong>B. VIDEO B&Agrave;I GIẢNG:</strong></u></p>\r\n\r\n<p style="font-size: 13px;"><u><strong><iframe frameborder="0" height="320" scrolling="no" src="http://www.youtube.com/embed/WVy3kiMhmRk" width="480"></iframe></strong></u></p>\r\n\r\n<p style="font-size: 13px;"><u><strong>C.&nbsp;C&Acirc;U HỎI &Ocirc;N TẬP</strong></u></p>\r\n\r\n<p style="font-size: 13px;"><strong>Thời gian</strong>:&nbsp;TH&Aacute;NG 02 (11-9al-Nh&acirc;m Th&igrave;n)<br />\r\n<strong>Chủ đề</strong>: (Chỉ ra) Hạnh ph&uacute;c của người Phật tử<br />\r\n<strong>Mục đ&iacute;ch</strong>: Chỉ cho người cư sĩ tại gia thấy c&aacute;i hạnh ph&uacute;c m&igrave;nh đang c&oacute; trong cuộc sống.</p>\r\n\r\n<ul style="font-size: 13px;">\r\n	<li><strong>C&acirc;u 1</strong>:&nbsp;Suốt buổi học, ắt hẳn qu&yacute; vị đ&atilde; nghe được nhiều c&acirc;u Ph&aacute;p, nhiều &yacute; nghĩa hay, v&agrave; qu&yacute; vị th&iacute;ch, t&acirc;m đắc. H&atilde;y n&oacute;i lại một c&acirc;u, một &yacute; nghĩa n&agrave;o đ&oacute;, v&agrave; giải th&iacute;ch tại sao n&oacute; hay, hoặc l&yacute; do m&igrave;nh t&acirc;m đắc.</li>\r\n	<li><strong>C&acirc;u 2</strong>: Hạnh ph&uacute;c trong đời thường của Phật tử tại gia, được thầy Gi&aacute;o Thọ chỉ ra trong suốt b&agrave;i giảng, gồm 02 phần. Đ&oacute; l&agrave; 02 phần n&agrave;o ?</li>\r\n	<li><strong>C&acirc;u 3</strong>: Hạnh ph&uacute;c trong đời thường của người Phật tử tại gia, phần 01 được thầy Gi&aacute;o Thọ đọc ra trong 03 b&agrave;i kệ, mỗi b&agrave;i kệ c&oacute; 04 c&acirc;u. H&atilde;y đọc thuộc l&ograve;ng ra 03 b&agrave;i kệ đ&oacute;.</li>\r\n	<li><strong>C&acirc;u 4</strong>: Hạnh ph&uacute;c trong đời thường của người Phật tử tại gia, phần 02 được thầy Gi&aacute;o Thọ đọc ra trong 06 b&agrave;i kệ, mỗi b&agrave;i kệ c&oacute; 04 c&acirc;u. H&atilde;y đọc thuộc l&ograve;ng ra 06 b&agrave;i kệ đ&oacute;.</li>\r\n	<li><strong>C&acirc;u 5</strong>: Trong b&agrave;i giảng, phần 02, v&igrave; l&yacute; do thiếu thời gian, thầy Gi&aacute;o Thọ đ&atilde; chỉ ph&acirc;n t&iacute;ch giải th&iacute;ch c&oacute; 03 b&agrave;i kệ. H&atilde;y đọc ra 03 b&agrave;i kệ m&agrave; thầy chưa ph&acirc;n t&iacute;ch.</li>\r\n	<li><strong>C&acirc;u 6</strong>: Trong 03 b&agrave;i kệ m&agrave; thầy Gi&aacute;o Thọ chưa ph&acirc;n t&iacute;ch, c&oacute; 02 b&agrave;i kệ m&agrave; kh&ocirc;ng cần giảng qu&yacute; Phật tử cũng tự hiểu, c&oacute; 01 b&agrave;i kệ m&agrave; phải giảng qu&yacute; Phật tử mới hiểu. H&atilde;y đọc thuộc l&ograve;ng l&ecirc;n 01 c&aacute;i b&agrave;i kệ m&agrave; bản th&acirc;n qu&yacute; vị cho l&agrave; cả lớp sẽ kh&oacute; hiểu đ&oacute;.</li>\r\n	<li><strong>C&acirc;u 7</strong>: Y&ecirc;u l&agrave; mức độ cao của th&iacute;ch, mừng l&agrave; biểu hiện mạnh của vui; y&ecirc;u mừng tức l&agrave; th&iacute;ch vui, m&agrave; ở cấp độ cao hơn. Tham dục nghĩa l&agrave; tham lam mong muốn. Đ&atilde; y&ecirc;u th&iacute;ch th&igrave; tham lam mong muốn, nếu tham lam mong muốn m&agrave; đạt được th&igrave; c&oacute; mừng vui. Ấy vậy, dẫu đ&atilde; y&ecirc;u th&iacute;ch ban đầu, dẫu đ&atilde; tham lam mong muốn l&uacute;c giữa, dẫu đ&atilde; mừng vui l&uacute;c sau; m&agrave; ban đầu, l&uacute;c giữa hay l&uacute;c sau g&igrave;, vẫn kh&ocirc;ng khiến ta tr&aacute;nh được lo v&agrave; sợ! Thầy Gi&aacute;o Thọ đ&atilde; giải th&iacute;ch 03 b&agrave;i kệ 04 c&acirc;u (y&ecirc;u mừng, th&iacute;ch vui v&agrave; tham dục) n&agrave;y một c&aacute;ch tương đối kỹ.&nbsp;<br />\r\n	H&atilde;y theo nội dung giải th&iacute;ch của Thầy, n&ecirc;u t&ecirc;n 02 nh&oacute;m đối tượng ch&iacute;nh của y&ecirc;u mừng, th&iacute;ch vui v&agrave; tham dục, m&agrave; ch&iacute;nh v&igrave; 02 nh&oacute;m đối tượng đ&oacute;, khiến ta lo v&agrave; sợ.</li>\r\n	<li><strong>C&acirc;u 8</strong>: Trong phần 01 (khoảng 30 ph&uacute;t đầu ti&ecirc;n của buổi giảng), để đạt được c&aacute;i hạnh ph&uacute;c Kh&ocirc;ng Lo Kh&ocirc;ng Sợ, an nhi&ecirc;n tự tại trong đời sống, qu&yacute; Phật tử đ&atilde; được học 03 b&agrave;i kệ 04 c&acirc;u, khuy&ecirc;n rằng n&ecirc;n kh&ocirc;ng Y&ecirc;u thương n&oacute;, kh&ocirc;ng Th&iacute;ch th&uacute; n&oacute;, kh&ocirc;ng Ham hố Mong cầu n&oacute;, thậm ch&iacute; c&ograve;n n&ecirc;n kh&ocirc;ng Mừng rỡ, kh&ocirc;ng Vui vẻ phấn khởi khi c&oacute; n&oacute; trong tay.<br />\r\n	Vậy th&igrave;, c&aacute;i th&aacute;i độ sống giữa đời đ&oacute;, l&agrave; th&aacute;i độ n&agrave;o? Qu&yacute; vị h&atilde;y chọn ra c&aacute;i th&aacute;i độ đ&uacute;ng nhất trong c&aacute;c th&aacute;i độ sau:<br />\r\n	a/ Bất cần đời. &nbsp; &nbsp; b/ Ch&aacute;n đời. &nbsp; &nbsp; &nbsp; c/ T&ugrave;y duy&ecirc;n v&agrave; an tịnh.</li>\r\n	<li><strong>C&acirc;u 9</strong>: H&atilde;y kể t&ecirc;n 05 thứ m&agrave; b&agrave;i kệ thứ 03 trong phần 01 dạy ch&uacute;ng ta kh&ocirc;ng n&ecirc;n ham hố mong cầu. (m&aacute;ch nhỏ: 05 thứ n&agrave;y bao gồm hết 02 nh&oacute;m đối tượng, m&agrave; 02 b&agrave;i kệ kia bảo ta n&ecirc;n kh&ocirc;ng y&ecirc;u th&iacute;ch, kh&ocirc;ng mừng vui).</li>\r\n	<li><strong>C&acirc;u 10</strong>: H&atilde;y kể ra v&agrave; giải th&iacute;ch đơn giản về c&aacute;c nghiệp &aacute;c của th&acirc;n, trong b&agrave;i kệ 01 của phần 02.</li>\r\n	<li><strong>C&acirc;u 11</strong>: H&atilde;y kể ra v&agrave; giải th&iacute;ch đơn giản về 04 lỗi của miệng, trong b&agrave;i kệ 01 của phần 02.</li>\r\n	<li><strong>C&acirc;u 12</strong>: T&acirc;m nghi ngờ tức l&agrave; t&acirc;m kh&ocirc;ng tin tưởng, t&acirc;m ngờ vực. Trong buổi giảng, Thầy Gi&aacute;o Thọ đ&atilde; n&oacute;i tới sự nghi ngờ về đạo đức của bậc tu h&agrave;nh, nghi ngờ về sự thủy chung của vợ chồng; sự nghi ngờ l&agrave;m cho mọi người ch&aacute;n ngấy nhau v&agrave; xa l&aacute;nh nhau. H&atilde;y kể ra 01 kinh nghiệm nghi ngờ của bản th&acirc;n qu&yacute; vị, m&agrave; đ&atilde; l&agrave;m cho ch&iacute;nh bản th&acirc;n qu&yacute; vị &ldquo;mất ăn mất ngủ&rdquo; (tức l&agrave; đau khổ).</li>\r\n	<li><strong>C&acirc;u 13</strong>: H&atilde;y chọn 01 c&acirc;u đ&uacute;ng nhất để t&oacute;m kết to&agrave;n bộ b&agrave;i giảng:<br />\r\n	a/ Để c&oacute; hạnh ph&uacute;c trong đời thường, người Phật tử cần Kh&ocirc;ng Lo Kh&ocirc;ng Sợ bất kỳ c&aacute;i g&igrave;, v&agrave; Lo Sao Cho C&oacute; Giấc Ngủ An Ổn.<br />\r\n	b/ Hạnh ph&uacute;c trong đời thường của người Phật tử, l&agrave; Kh&ocirc;ng Lo Kh&ocirc;ng Sợ bất kỳ c&aacute;i g&igrave;, v&agrave; Tới Giờ Ngủ Th&igrave; Bỏ Qua Mọi Thứ Cho Ngủ Được Ngon.<br />\r\n	c/ Hạnh ph&uacute;c trong đời thường của người Phật tử, l&agrave; T&acirc;m An Nhi&ecirc;n Kh&ocirc;ng Lo Kh&ocirc;ng Sợ, v&agrave; Giấc Ngủ Ngon.<br />\r\n	d/ Để c&oacute; hạnh ph&uacute;c trong đời thường, người Phật tử cần Sống T&ugrave;y Duy&ecirc;n Bu&ocirc;ng Xả, Kh&ocirc;ng Tham Luyến Mọi Thứ Tr&ecirc;n Đời v&agrave; Dấn Th&acirc;n V&agrave;o Bước Đường Tu Tập C&aacute;c Ph&aacute;p Phật.</li>\r\n</ul>\r\n\r\n<p align="center" style="font-size: 13.333333969116211px;"><span style="font-size: 16px;"><span style="font-family: ''times new roman'', times, serif;"><em><strong>--- Hết th&aacute;ng 02 ---</strong></em></span></span></p>\r\n', 21),
(3, 12, 15, 'Buổi 3 - Phật tử những điều cần tránh', '2012-11-24 07:30:00', '2012-11-24 11:00:00', '<p><img alt="" src="https://lh4.googleusercontent.com/-0P-elhdi6Xc/UM4Qp8rO_AI/AAAAAAAABd0/dKfN6FkisMA/s800/LopGiaoLyB3.jpg" style="width: 590px; height: 180px;" /></p>\r\n\r\n<p style="font-size: 13px; text-align: center;"><span style="font-size: 20px;">CHƯƠNG TR&Igrave;NH Đ&Agrave;O TẠO LỚP GI&Aacute;O L&Yacute; PHẬT TỬ</span></p>\r\n\r\n<p style="font-size: 13px; text-align: center;"><span style="font-size: 20px;">Phần I: L&agrave; Một Cư Sĩ Tốt</span></p>\r\n\r\n<p style="font-size: 13px;"><span style="font-size: 13px;"><strong>Thời gian</strong>: 04 th&aacute;ng/08 tiết</span></p>\r\n\r\n<p style="font-size: 13px;"><strong>Gi&aacute;o Thọ</strong>:&nbsp;<em><strong>Thượng tọa Th&iacute;ch Th&ocirc;ng Ấn</strong></em>&nbsp;(ch&ugrave;a Long H&ograve;a, Vĩnh Long)</p>\r\n\r\n<p style="font-size: 13px;"><span style="font-size: 13px;"><strong>Phương ph&aacute;p</strong>: Giảng, ghi ch&eacute;p_ph&aacute;t dĩa_cho c&acirc;u hỏi chuẩn bị, thi.</span></p>\r\n\r\n<p style="font-size: 13px;"><u style="font-size: 13px;"><strong>Địa điểm</strong></u><span style="font-size: 13px;">: Đạo Tr&agrave;ng ch&ugrave;a Gi&aacute;c Quang.</span></p>\r\n\r\n<p style="font-size: 13px;"><u><strong>A. D&Agrave;N B&Agrave;I:</strong></u></p>\r\n\r\n<table align="left" border="1" cellpadding="1" cellspacing="1" style="font-size: 13.333333969116211px; width: 500px;">\r\n	<thead>\r\n		<tr>\r\n			<th scope="col">STT</th>\r\n			<th scope="col">THỜI GIAN</th>\r\n			<th scope="col">CHỦ ĐỀ</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td style="text-align: center;">1</td>\r\n			<td>11-8al-Nh&acirc;m Th&igrave;n</td>\r\n			<td>(Giới thiệu) Hạnh ph&uacute;c của người Phật tử_so s&aacute;nh với hạnh ph&uacute;c của người đời</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align: center;">2</td>\r\n			<td>11-9al-Nh&acirc;m Th&igrave;n</td>\r\n			<td>(Chỉ ra) Hạnh ph&uacute;c của người Phật tử_trong đời thường</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align: center;">3</td>\r\n			<td>11-10al-Nh&acirc;m Th&igrave;n</td>\r\n			<td>Phật tử những điều cần tr&aacute;nh</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align: center;">4</td>\r\n			<td>11-11al-Nh&acirc;m Th&igrave;n</td>\r\n			<td>Phật tử những điều cần l&agrave;m</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style="font-size: 13.333333969116211px;">&nbsp;</p>\r\n\r\n<p style="font-size: 13.333333969116211px;">&nbsp;</p>\r\n\r\n<p style="font-size: 13.333333969116211px;">&nbsp;</p>\r\n\r\n<p style="font-size: 13.333333969116211px;">&nbsp;</p>\r\n\r\n<p style="font-size: 13px;">&nbsp;</p>\r\n\r\n<p style="font-size: 13px;">&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><u><strong>B. VIDEO B&Agrave;I GIẢNG:</strong></u></p>\r\n\r\n<p><u><strong><iframe frameborder="0" height="320" scrolling="no" src="http://www.youtube.com/embed/739rJP7nxMg" width="480"></iframe></strong></u></p>\r\n\r\n<p><u><strong>C. C&Acirc;U HỎI &Ocirc;N TẬP:</strong></u></p>\r\n\r\n<p><strong>Thời gian:</strong>&nbsp;TH&Aacute;NG 03 (11-10al-Nh&acirc;m Th&igrave;n)<br />\r\n<strong>Chủ đề</strong>: Phật tử những điều cần tr&aacute;nh<br />\r\n<strong>Mục đ&iacute;ch</strong>: Chỉ cho người cư sĩ tại gia thấy m&igrave;nh cần tr&aacute;nh l&agrave;m những g&igrave;.</p>\r\n\r\n<ul>\r\n	<li><strong>C&acirc;u 1</strong>: Suốt buổi học, ắt hẳn qu&yacute; vị đ&atilde; nghe được nhiều c&acirc;u Ph&aacute;p, nhiều &yacute; nghĩa hay, v&agrave; qu&yacute; vị th&iacute;ch, t&acirc;m đắc. H&atilde;y n&oacute;i lại một c&acirc;u, một &yacute; nghĩa n&agrave;o đ&oacute;, v&agrave; giải th&iacute;ch tại sao n&oacute; hay, hoặc l&yacute; do m&igrave;nh t&acirc;m đắc.</li>\r\n	<li><strong>C&acirc;u 2</strong><span style="font-size: 13px;">: </span>H&atilde;y n&oacute;i 01 c&acirc;u, t&oacute;m kết to&agrave;n bộ nội dung b&agrave;i giảng h&ocirc;m nay.</li>\r\n	<li><strong>C&acirc;u 3</strong><span style="font-size: 13px;">: </span>Tai hại của giận hờn v&agrave; trả giận hờn (gia đ&igrave;nh kh&ocirc;ng hạnh ph&uacute;c).</li>\r\n	<li><strong>C&acirc;u 4</strong><span style="font-size: 13px;">: </span>L&agrave;m sao để tr&aacute;nh được giận hờn.</li>\r\n	<li><strong><span style="font-size: 13px;">C&acirc;u 5</span></strong><span style="font-size: 13px;">: </span>Nguy&ecirc;n nh&acirc;n n&agrave;o m&igrave;nh bị người kh&aacute;c l&agrave;m cho n&oacute;ng giận ?</li>\r\n	<li><strong><span style="font-size: 13px;">C&acirc;u 6</span></strong><span style="font-size: 13px;">: </span>Nguy&ecirc;n nh&acirc;n n&agrave;o m&igrave;nh hay chọc giận người kh&aacute;c ?</li>\r\n	<li><strong><span style="font-size: 13px;">C&acirc;u 7</span></strong><span style="font-size: 13px;">: </span>H&atilde;y kể lại 02 c&acirc;u chuyện về s&acirc;n giận trong b&agrave;i giảng. V&agrave; n&ecirc;u l&ecirc;n c&aacute;i &yacute; nghĩa ch&iacute;nh m&agrave; mỗi c&acirc;u chuyện muốn n&oacute;i với m&igrave;nh.</li>\r\n	<li><strong><span style="font-size: 13px;">C&acirc;u 8</span></strong><span style="font-size: 13px;">: </span>Giận hờn v&agrave; tội &aacute;c hay đi liền nhau. H&atilde;y kể lại một kinh nghiệm bản th&acirc;n về điều đ&oacute;, m&agrave; tới giờ m&igrave;nh vẫn c&ograve;n ấn tượng nhất.</li>\r\n</ul>\r\n\r\n<p style="text-align: center;"><em><strong><span style="font-size:18px;"><span style="font-family:times new roman,times,serif;">Hết th&aacute;ng 03</span></span></strong></em></p>\r\n', 0),
(26, 12, 15, 'Buổi 4 - Phật tử những điều phật tử cần làm', '2012-12-23 07:30:00', '2012-12-23 11:00:00', '<p style="font-size: 13.333333969116211px;"><img alt="" src="https://lh3.googleusercontent.com/-mf80wiAG3hY/UM4QrShT-4I/AAAAAAAABd4/4rr6_R1xJwU/s800/LopGiaoLyB4.jpg" style="width: 590px; height: 180px;" /></p>\r\n\r\n<p style="font-size: 13px; text-align: center;"><span style="font-size: 20px;">CHƯƠNG TR&Igrave;NH Đ&Agrave;O TẠO LỚP GI&Aacute;O L&Yacute; PHẬT TỬ</span></p>\r\n\r\n<p style="font-size: 13px; text-align: center;"><span style="font-size: 20px;">Phần I: L&agrave; Một Cư Sĩ Tốt</span></p>\r\n\r\n<p style="font-size: 13px;"><span style="font-size: 13px;"><strong>Thời gian</strong>: 04 th&aacute;ng/08 tiết</span></p>\r\n\r\n<p style="font-size: 13px;"><strong>Gi&aacute;o Thọ</strong>:&nbsp;<em><strong>Thượng tọa Th&iacute;ch Th&ocirc;ng Ấn</strong></em>&nbsp;(ch&ugrave;a Long H&ograve;a, Vĩnh Long)</p>\r\n\r\n<p style="font-size: 13px;"><span style="font-size: 13px;"><strong>Phương ph&aacute;p</strong>: Giảng, ghi ch&eacute;p_ph&aacute;t dĩa_cho c&acirc;u hỏi chuẩn bị, thi.</span></p>\r\n\r\n<p style="font-size: 13px;"><u style="font-size: 13px;"><strong>Địa điểm</strong></u><span style="font-size: 13px;">: Đạo Tr&agrave;ng ch&ugrave;a Gi&aacute;c Quang.</span></p>\r\n\r\n<p style="font-size: 13px;"><u><strong>A. D&Agrave;N B&Agrave;I:</strong></u></p>\r\n\r\n<table align="left" border="1" cellpadding="1" cellspacing="1" style="font-size: 13.333333969116211px; width: 500px;">\r\n	<thead>\r\n		<tr>\r\n			<th scope="col">STT</th>\r\n			<th scope="col">THỜI GIAN</th>\r\n			<th scope="col">CHỦ ĐỀ</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td style="text-align: center;">1</td>\r\n			<td>11-8al-Nh&acirc;m Th&igrave;n</td>\r\n			<td>(Giới thiệu) Hạnh ph&uacute;c của người Phật tử_so s&aacute;nh với hạnh ph&uacute;c của người đời</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align: center;">2</td>\r\n			<td>11-9al-Nh&acirc;m Th&igrave;n</td>\r\n			<td>(Chỉ ra) Hạnh ph&uacute;c của người Phật tử_trong đời thường</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align: center;">3</td>\r\n			<td>11-10al-Nh&acirc;m Th&igrave;n</td>\r\n			<td>Phật tử những điều cần tr&aacute;nh</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align: center;">4</td>\r\n			<td>11-11al-Nh&acirc;m Th&igrave;n</td>\r\n			<td>Phật tử những điều cần l&agrave;m</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style="font-size: 13.333333969116211px;">&nbsp;</p>\r\n\r\n<p style="font-size: 13.333333969116211px;">&nbsp;</p>\r\n\r\n<p style="font-size: 13.333333969116211px;">&nbsp;</p>\r\n\r\n<p style="font-size: 13.333333969116211px;">&nbsp;</p>\r\n\r\n<p style="font-size: 13px;">&nbsp;</p>\r\n\r\n<p style="font-size: 13px;">&nbsp;</p>\r\n\r\n<p style="font-size: 13.333333969116211px;">&nbsp;</p>\r\n\r\n<p style="font-size: 13.333333969116211px;">&nbsp;</p>\r\n\r\n<p style="font-size: 13.333333969116211px;"><u><strong>B. VIDEO B&Agrave;I GIẢNG:</strong></u></p>\r\n\r\n<p style="font-size: 13.333333969116211px;"><u><strong><iframe frameborder="0" height="320" scrolling="no" src="http://www.youtube.com/embed/kGO8rnfYuFc" width="480"></iframe></strong></u></p>\r\n\r\n<p style="font-size: 13.333333969116211px;"><u><strong>C. C&Acirc;U HỎI &Ocirc;N TẬP:</strong></u></p>\r\n\r\n<p><span style="font-size: 13px; line-height: 1.6em;">Chủ đề: Phật tử những điều cần l&agrave;m</span></p>\r\n\r\n<p>Mục đ&iacute;ch: Chỉ cho người cư sĩ tại gia thấy m&igrave;nh cần l&agrave;m những g&igrave;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style="margin-left:.5in;">1.&nbsp;&nbsp;&nbsp;&nbsp; Suốt buổi học, ắt hẳn qu&yacute; vị đ&atilde; nghe được nhiều c&acirc;u Ph&aacute;p, nhiều &yacute; nghĩa hay, v&agrave; qu&yacute; vị th&iacute;ch, t&acirc;m đắc. H&atilde;y n&oacute;i lại một c&acirc;u, một &yacute; nghĩa n&agrave;o đ&oacute;, v&agrave; giải th&iacute;ch tại sao n&oacute; hay, hoặc l&yacute; do m&igrave;nh t&acirc;m đắc.</p>\r\n\r\n<p style="margin-left:.5in;">2.&nbsp;&nbsp;&nbsp;&nbsp; To&agrave;n bộ nội dung b&agrave;i giảng h&ocirc;m nay c&oacute; 03 phần. H&atilde;y n&oacute;i t&ecirc;n 03 phần đ&oacute;.</p>\r\n\r\n<p style="margin-left:.5in;">3.&nbsp;&nbsp;&nbsp;&nbsp; C&uacute;ng dường Tam Bảo l&agrave; g&igrave;? Mục đ&iacute;ch của việc c&uacute;ng dường Tam Bảo?</p>\r\n\r\n<p style="margin-left:.5in;">4.&nbsp;&nbsp;&nbsp;&nbsp; Sao gọi l&agrave; c&uacute;ng dường Tam Bảo sai lạc?</p>\r\n\r\n<p style="margin-left:.5in;">5.&nbsp;&nbsp;&nbsp;&nbsp; M&ecirc; t&iacute;n l&agrave; g&igrave;? Kể ra c&aacute;c loại h&igrave;nh m&ecirc; t&iacute;n?</p>\r\n\r\n<p style="margin-left:.5in;">6.&nbsp;&nbsp;&nbsp;&nbsp; Ch&aacute;nh t&iacute;n l&agrave; g&igrave;, l&agrave;m sao để c&oacute; ch&aacute;nh t&iacute;n?</p>\r\n\r\n<p style="margin-left:.5in;">7.&nbsp;&nbsp;&nbsp;&nbsp; Tội l&agrave; g&igrave;? C&oacute; mấy loại tội, kể sơ ra?</p>\r\n\r\n<p style="margin-left:.5in;">8.&nbsp;&nbsp;&nbsp;&nbsp; Cho mỗi v&iacute; dụ về mỗi loại tội.</p>\r\n\r\n<p style="margin-left:.5in;">9.&nbsp;&nbsp;&nbsp;&nbsp; Tội xuất ph&aacute;t từ đ&acirc;u, tr&ecirc;n con người ta?</p>\r\n\r\n<p style="margin-left:.5in;">10.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;Phước l&agrave; g&igrave;? C&oacute; mấy loại, kể ra?</p>\r\n\r\n<p style="margin-left:.5in;">11.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Cho mỗi v&iacute; dụ về mỗi loại phước.</p>\r\n\r\n<p style="margin-left:.5in;">12.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; L&agrave;m g&igrave; để c&oacute; được phước?</p>\r\n\r\n<p style="text-align: center;">Hết th&aacute;ng 04</p>\r\n', 0),
(27, 25, 17, 'Buổi 1', '2013-03-22 07:30:00', '2013-03-22 11:00:00', '<p><img alt="" src="https://lh5.googleusercontent.com/-pzainZrTigM/UM4QsFEKYbI/AAAAAAAABd8/g5iWhJxqqP8/s800/LopGiaoLyNangCaoB1.jpg" style="width: 590px; height: 180px;" /></p>\r\n\r\n<p>Kh&oacute;a học sắp xảy ra chưa c&oacute; b&agrave;i viết xin vui l&ograve;ng quay lại sau</p>\r\n', 0),
