INSERT INTO `chuagiacquang_course` (`id`, `name`, `date_start`, `date_end`, `description`, `order`) VALUES
(4, 'KHÓA TU NIỆM PHẬT', '2012-09-26 07:00:00', '2013-12-23 12:00:00', '<p><img alt="" src="https://lh5.googleusercontent.com/-WpSQ9sv8YSM/UMrFFDr2Q1I/AAAAAAAABUY/WVYOkjJNIwM/s800/Lop%2520Giao%2520Ly.jpg" style="width: 590px; height: 180px;" /></p>\r\n\r\n<p>Cung cấp kiến thức nền tảng về PHẬT HỌC cho PHẬT TỬ. C&aacute;c kh&oacute;a học được tổ chức v&agrave; tiến h&agrave;nh học v&agrave;o c&aacute;c ng&agrave;y 11 &acirc;l h&agrave;ng th&aacute;ng.</p>\r\n\r\n<p>C&aacute;c b&agrave;i học sẽ được dựng th&agrave;nh phim, c&aacute;c c&acirc;u hỏi &ocirc;n tập ở mỗi cuối b&agrave;i học sẽ được đăng tải tr&ecirc;n Website để c&aacute;c qu&iacute; Phật Tử c&oacute; dịp tham khảo lại c&aacute;c b&agrave;i học trước đ&oacute;.</p>\r\n', 1);
