INSERT INTO `chuagiacquang_news` (`id`, `id_category`, `author`, `date`, `content`, `title`, `type`) VALUES
(132, 10, 'Tin, ảnh: Lệ Trí - Huệ Nghiêm', '2013-09-14 02:36:04', '<p align="left"><span style="font-size:16px;"><span class="ctcBody" id="ZoomContentBody" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; text-align: justify; width: auto; line-height: 22px;"><font color="#808080"><b>S&aacute;ng qua, 12-9-2013, lễ tổng kết, ph&aacute;t thưởng năm thứ 2 v&agrave; khai giảng năm thứ 3 kh&oacute;a V Trường Trung cấp Phật học (TCPH) Long An đ&atilde; diễn ra trang nghi&ecirc;m tại Văn ph&ograve;ng Ban Gi&aacute;m hiệu&nbsp; - ch&ugrave;a Thi&ecirc;n Kh&aacute;nh (P.4, TP T&acirc;n An, Long An).</b></font></span></span></p>\r\n\r\n<p align="center"><span style="font-size:16px;"><span class="ctcBody" id="ZoomContentBody" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; text-align: justify; width: auto; line-height: 22px;"><font color="#808080"><img alt="LAn05.jpg" border="0" height="323" src="http://giacngo.vn/UserImages/2013/09/13/11/LAn05.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="570" /></font></span></span></p>\r\n\r\n<p align="center"><span style="font-size:16px;"><span class="ctcBody" id="ZoomContentBody" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; text-align: justify; width: auto; line-height: 22px;"><font color="#808080">Chư t&ocirc;n đức chứng minh</font></span></span></p>\r\n\r\n<p align="left"><span style="font-size:16px;"><span class="ctcBody" id="ZoomContentBody" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; text-align: justify; width: auto; line-height: 22px;">ĐĐ.Th&iacute;ch Quang Hạnh, Ch&aacute;nh Thư k&yacute; nh&agrave; trường b&aacute;o c&aacute;o kết quả đ&agrave;o tạo kh&oacute;a V năm thứ 2 (2012-2013) v&agrave; phương hướng đ&agrave;o tạo năm thứ 3 (2013-2014). Trường hiện c&oacute; 120 Tăng Ni sinh đang theo học. Cơ sở 1 tại ch&ugrave;a Thi&ecirc;n Kh&aacute;nh c&oacute; 46 Tăng sinh v&agrave; cơ sở 2 tại ch&ugrave;a Thi&ecirc;n Phước c&oacute; 74 Ni sinh tu học nội tr&uacute;.</span></span></p>\r\n\r\n<p align="left"><span style="font-size:16px;"><span class="ctcBody" id="ZoomContentBody" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; text-align: justify; width: auto; line-height: 22px;">Nhằm tri &acirc;n chư vị đ&atilde; đ&oacute;ng g&oacute;p c&ocirc;ng sức giảng dạy v&agrave; hỗ trợ Tăng Ni sinh trong năm qua, Ban Gi&aacute;m hiệu đ&atilde; kiến nghị Ban Trị sự GHPGVN tỉnh Long An tặng bằng tuy&ecirc;n dương c&ocirc;ng đức cho chư t&ocirc;n đức trong Ban Gi&aacute;o thọ (9 vị), qu&yacute; vị bảo trợ (2 vị).</span></span></p>\r\n\r\n<p align="center"><span style="font-size:16px;"><span class="ctcBody" id="ZoomContentBody" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; text-align: justify; width: auto; line-height: 22px;"><img alt="LAn09.jpg" border="0" height="331" src="http://giacngo.vn/UserImages/2013/09/13/11/LAn09.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="570" /></span></span></p>\r\n\r\n<p align="center"><span style="font-size:16px;"><span class="ctcBody" id="ZoomContentBody" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; text-align: justify; width: auto; line-height: 22px;"><font color="#808080">Tặng thưởng Tăng Ni sinh</font></span></span></p>\r\n\r\n<p align="center"><span style="font-size:16px;"><span class="ctcBody" id="ZoomContentBody" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; text-align: justify; width: auto; line-height: 22px;"><font color="#808080"><img alt="LAn08.jpg" border="0" height="344" src="http://giacngo.vn/UserImages/2013/09/13/11/LAn08.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="570" /></font></span></span></p>\r\n\r\n<p align="center"><span style="font-size:16px;"><span class="ctcBody" id="ZoomContentBody" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; text-align: justify; width: auto; line-height: 22px;"><font color="#808080">Tuy&ecirc;n dương c&ocirc;ng đức những vị gi&aacute;o thọ, ban bảo trợ</font></span></span></p>\r\n\r\n<p align="center"><span style="font-size:16px;"><span class="ctcBody" id="ZoomContentBody" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; text-align: justify; width: auto; line-height: 22px;"><font color="#808080"><img alt="LAn13.jpg" border="0" height="382" src="http://giacngo.vn/UserImages/2013/09/13/11/LAn13.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="570" /></font></span></span></p>\r\n\r\n<p align="center"><span style="font-size:16px;"><span class="ctcBody" id="ZoomContentBody" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; text-align: justify; width: auto; line-height: 22px;"><font color="#808080">TT.Th&iacute;ch Minh Thiện tặng bằng Tuy&ecirc;n dương c&ocirc;ng đức tới TT.Th&iacute;ch Vi&ecirc;n Gi&aacute;c, gi&aacute;o thọ</font></span></span></p>\r\n\r\n<p align="left"><span style="font-size:16px;"><span class="ctcBody" id="ZoomContentBody" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; text-align: justify; width: auto; line-height: 22px;">Nhằm kh&iacute;ch lệ tinh thần tinh tấn học tập của Tăng Ni sinh, Ban Gi&aacute;m hiệu đ&atilde; x&eacute;t khen thưởng cho 5 Tăng sinh (4 vị xếp hạng cao nhất về học tập v&agrave; 1 vị ti&ecirc;u biểu về đạo hạnh), 7 Ni sinh (6 vị xếp hạng cao nhất về học tập v&agrave; 1 vị ti&ecirc;u biểu về đạo hạnh) đạt kết quả tốt trong năm thứ 2 vừa qua.</span></span></p>\r\n\r\n<p align="left"><span style="font-size:16px;"><span class="ctcBody" id="ZoomContentBody" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; text-align: justify; width: auto; line-height: 22px;">Năm thứ 3 l&agrave; năm đ&agrave;o tạo trọng t&acirc;m của trường. Chương tr&igrave;nh c&oacute; 12 m&ocirc;n học với 14 gi&aacute;o thọ, giảng vi&ecirc;n.</span></span></p>\r\n\r\n<p align="left"><span style="font-size:16px;"><span class="ctcBody" id="ZoomContentBody" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; text-align: justify; width: auto; line-height: 22px;">Ban Gi&aacute;m hiệu đ&atilde; lắng nghe lời ph&aacute;t biểu chia sẻ v&agrave; ch&uacute;c mừng của &ocirc;ng Nguyễn Văn Cheo, UV thường trực, Trưởng ban D&acirc;n tộc - T&ocirc;n gi&aacute;o UBMTTQVN tỉnh&nbsp; Long An, Thượng tọa Th&iacute;ch Vi&ecirc;n Gi&aacute;c - đại diện gi&aacute;o thọ v&agrave; lời đạo từ chứng minh của HT.Th&iacute;ch Thiện Huệ, chứng minh Ban Trị sự GHPGVN tỉnh Long An.</span></span></p>\r\n\r\n<p align="center"><span style="font-size:16px;"><span class="ctcBody" id="ZoomContentBody" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; text-align: justify; width: auto; line-height: 22px;"><img alt="LAn11.jpg" border="0" height="346" src="http://giacngo.vn/UserImages/2013/09/13/11/LAn11.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="570" /></span></span></p>\r\n\r\n<p align="center"><span style="font-size:16px;"><span class="ctcBody" id="ZoomContentBody" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; text-align: justify; width: auto; line-height: 22px;"><font color="#808080"><img alt="LAn12.jpg" border="0" height="281" src="http://giacngo.vn/UserImages/2013/09/13/11/LAn12.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="570" /></font></span></span></p>\r\n\r\n<p align="center"><span style="font-size:16px;"><span class="ctcBody" id="ZoomContentBody" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; text-align: justify; width: auto; line-height: 22px;"><font color="#808080">Quang cảnh buổi lễ</font></span></span></p>\r\n\r\n<p align="center"><span style="font-size:16px;"><span class="ctcBody" id="ZoomContentBody" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; text-align: justify; width: auto; line-height: 22px;"><font color="#808080"><img alt="LAn15.jpg" border="0" height="443" src="http://giacngo.vn/UserImages/2013/09/13/11/LAn15.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="550" /></font></span></span></p>\r\n\r\n<p align="center"><span style="font-size:16px;"><span class="ctcBody" id="ZoomContentBody" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; text-align: justify; width: auto; line-height: 22px;"><font color="#808080">TT.Th&iacute;ch Minh Thiện ph&aacute;t biểu</font></span></span></p>\r\n\r\n<p align="center"><span style="font-size:16px;"><span class="ctcBody" id="ZoomContentBody" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; text-align: justify; width: auto; line-height: 22px;"><font color="#808080"><img alt="LAn17.jpg" border="0" height="347" src="http://giacngo.vn/UserImages/2013/09/13/11/LAn17.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="570" /></font></span></span></p>\r\n\r\n<p align="center"><span style="font-size:16px;"><span class="ctcBody" id="ZoomContentBody" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; text-align: justify; width: auto; line-height: 22px;"><font color="#808080">Đạo từ của HT.Th&iacute;ch Thiện Huệ</font></span></span></p>\r\n\r\n<p align="center"><span style="font-size:16px;"><span class="ctcBody" id="ZoomContentBody" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; text-align: justify; width: auto; line-height: 22px;"><font color="#808080"><img alt="LAn18.jpg" border="0" height="338" src="http://giacngo.vn/UserImages/2013/09/13/11/LAn18.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="570" /></font></span></span></p>\r\n\r\n<p align="center"><span style="font-size:16px;"><span class="ctcBody" id="ZoomContentBody" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; text-align: justify; width: auto; line-height: 22px;"><font color="#808080">Kết th&uacute;c buổi lễ bằng nghi thức hồi hướng</font></span></span></p>\r\n\r\n<div>&nbsp;</div>\r\n', 'Trường TCPH tỉnh Long An khai giảng năm 3 khóa V', 0),
(133, 10, 'Bảo Thiên', '2013-09-14 02:37:13', '<p align="left" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><span style="font-size:16px;"><font color="#808080"><b>Đ&uacute;ng 5 giờ s&aacute;ng nay, 13-9, tại giới trường Huệ Nghi&ecirc;m (Q.B&igrave;nh T&acirc;n), giới tử thọ Tỳ-kheo bắt đầu tấn đ&agrave;n.</b></font></span></p>\r\n\r\n<p align="left" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><span style="font-size:16px;">C&oacute; tất cả 71 đ&agrave;n với 213 giới tử thọ Tỳ-kheo nối tiếp nhau tấn đ&agrave;n th&ocirc;ng qua sự hướng dẫn của chư t&ocirc;n đức dẫn thỉnh v&agrave; Ban Quản giới tử.</span></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><span style="font-size:16px;"><img alt="Tandan (1).JPG" border="0" height="381" src="http://giacngo.vn/UserImages/2013/09/13/5/Tandan%20%281%29.JPG" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="580" /></span></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><span style="font-size:16px;"><font color="#808080">Giới tử chuẩn bị tấn đ&agrave;n - Ảnh: Bảo Thi&ecirc;n</font></span></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><span style="font-size:16px;"><img alt="Tandan (2).JPG" border="0" height="449" src="http://giacngo.vn/UserImages/2013/09/13/5/Tandan%20%282%29.JPG" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="580" /></span></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><span style="font-size:16px;"><font color="#808080">Th&ocirc;ng qua sự hướng dẫn của chư t&ocirc;n đức dẫn thỉnh - Ảnh: Bảo Thi&ecirc;n</font></span></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><span style="font-size:16px;"><img alt="Tandan (3).JPG" border="0" height="440" src="http://giacngo.vn/UserImages/2013/09/13/5/Tandan%20%283%29.JPG" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="580" /></span></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px; text-align: justify;"><span style="font-size:16px;">Theo nghi thức, cứ mỗi đ&agrave;n, chư giới tử lần lượt tiến v&agrave;o khu vực truyền giới v&agrave; đảnh lễ chư t&ocirc;n gi&aacute;o phẩm Hội đồng Thập sư Tăng gồm: Trưởng l&atilde;o HT.Th&iacute;ch Tắc An đương vi H&ograve;a thượng đ&agrave;n đầu, HT.Th&iacute;ch Như Niệm đương vi Yết-ma A-x&agrave;-l&ecirc;, HT.Th&iacute;ch Chơn Lạc đương vi Gi&aacute;o thọ A-x&agrave;-l&ecirc;, HT.Th&iacute;ch Minh Th&ocirc;ng đương vi Tuy&ecirc;n Luật sư v&agrave; chư t&ocirc;n gi&aacute;o phẩm trong h&agrave;ng Thất chứng. Ngay sau đ&oacute; l&agrave; nghi thức Yết-ma v&agrave; truyền giới.</span></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><span style="font-size:16px;"><img alt="Tandan (8).JPG" border="0" height="335" src="http://giacngo.vn/UserImages/2013/09/13/5/Tandan%20%288%29.JPG" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="580" /></span></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><span style="font-size:16px;"><font color="#808080">Chư t&ocirc;n đức Hội đồng thập sư Tăng - Ảnh: Bảo Thi&ecirc;n</font></span></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><span style="font-size:16px;"><img alt="Tandan (9).JPG" border="0" height="386" src="http://giacngo.vn/UserImages/2013/09/13/5/Tandan%20%289%29.JPG" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="580" /></span></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><span style="font-size:16px;"><font color="#808080">HT.Th&iacute;ch Như Niệm thực hiện nghi thức Yết-ma - Ảnh: Bảo Thi&ecirc;n</font></span></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><span style="font-size:16px;"><img alt="Tandan (10).JPG" border="0" height="480" src="http://giacngo.vn/UserImages/2013/09/13/5/Tandan%20%2810%29.JPG" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="407" /></span></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><span style="font-size:16px;"><img alt="Tandan (11).JPG" border="0" height="451" src="http://giacngo.vn/UserImages/2013/09/13/5/Tandan%20%2811%29.JPG" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="580" /></span></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><span style="font-size:16px;"><font color="#808080">H&igrave;nh ảnh tấn đ&agrave;n của giới tử thọ Tỳ-kheo - Ảnh: Bảo Thi&ecirc;n</font></span></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><span style="font-size:16px;"><img alt="Tandan (12).JPG" border="0" height="480" src="http://giacngo.vn/UserImages/2013/09/13/5/Tandan%20%2812%29.JPG" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="529" /></span></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><span style="font-size:16px;"><img alt="Tandan (13).JPG" border="0" height="402" src="http://giacngo.vn/UserImages/2013/09/13/5/Tandan%20%2813%29.JPG" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="580" /></span></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px; text-align: justify;"><span style="font-size:16px;">Theo ghi nhận của PV Gi&aacute;c Ngộ Online, mặc d&ugrave; thời tiết kh&ocirc;ng thuận lợi khi xuất hiện mưa nhỏ nhưng kh&ocirc;ng gian giới trường Huệ Nghi&ecirc;m kh&aacute; thanh tịnh v&agrave; trang nghi&ecirc;m. Nội bất xuất, ngoại bất nhập - chỉ c&oacute; Hội đồng Thập sư truyền giới, Ban Quản giới tử v&agrave; to&agrave;n thể chư giới tử - rất ph&ugrave; hợp cho việc tịnh h&oacute;a th&acirc;n t&acirc;m, tiếp nhận giới ph&aacute;p để bước v&agrave;o h&agrave;ng ch&uacute;ng trung t&ocirc;n.</span></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><span style="font-size:16px;"><img alt="Tandan (6).JPG" border="0" height="409" src="http://giacngo.vn/UserImages/2013/09/13/5/Tandan%20%286%29.JPG" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="580" /></span></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><span style="font-size:16px;"><font color="#808080">Giới tử sau khi tấn đ&agrave;n - Ảnh: Bảo Thi&ecirc;n</font></span></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><span style="font-size:16px;"><img alt="Tandan (7).JPG" border="0" height="401" src="http://giacngo.vn/UserImages/2013/09/13/5/Tandan%20%287%29.JPG" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="580" /></span></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><span style="font-size:16px;"><font color="#808080">Chiếu y, b&igrave;nh b&aacute;t - b&aacute;u vật của người xuất gia - Ảnh: Bảo Thi&ecirc;n</font></span></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><span style="font-size:16px;"><img alt="Tandan (5).JPG" border="0" height="480" src="http://giacngo.vn/UserImages/2013/09/13/5/Tandan%20%285%29.JPG" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="482" /></span></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><span style="font-size:16px;"><font color="#808080">Chư t&ocirc;n đức Ban Quản giới tử trao đổi c&ocirc;ng việc giới đ&agrave;n với&nbsp;chư t&ocirc;n đức dẫn thỉnh - Ảnh: Bảo Thi&ecirc;n</font></span></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><span style="font-size:16px;"><img alt="Tandan (4).JPG" border="0" height="425" src="http://giacngo.vn/UserImages/2013/09/13/5/Tandan%20%284%29.JPG" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="580" /></span></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><span style="font-size:16px;"><font color="#808080">Giới trường Huệ Nghi&ecirc;m tĩnh lặng dưới cơn mưa nhỏ v&agrave;o s&aacute;ng nay - Ảnh: Bảo Thi&ecirc;n</font></span></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px; text-align: justify;"><span style="font-size:16px;">Theo th&ocirc;ng tin từ Ban Dẫn thỉnh, nếu mọi việc diễn ra đ&uacute;ng theo dự t&iacute;nh, đến hết giờ chiều nay c&ocirc;ng t&aacute;c truyền giới Tỳ-kheo sẽ kết th&uacute;c v&agrave; nghi thức được nối tiếp l&agrave; thuyết U minh giới.</span></p>\r\n', 'Giới tử thọ Tỳ-kheo tấn đàn', 0),
(134, 10, 'Vũ Giang', '2013-09-14 02:38:47', '<p style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px; text-align: justify;"><font color="#808080"><b>Đ&uacute;ng 8g s&aacute;ng nay, 13-9, Ban Dẫn thỉnh v&agrave; giới tử thọ giới Tỳ-kheo đ&atilde; t&aacute;c bạch, cung thỉnh Hội đồng Tam sư, T&ocirc;n chứng quang l&acirc;m Giới trường tấn đ&agrave;n truyền giới.</b></font></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><img alt="Tran1.jpg" border="0" height="380" src="http://giacngo.vn/UserImages/2013/09/13/11/Tran1.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="570" /></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><font color="#808080" size="2"><img alt="Tran2.jpg" border="0" height="380" src="http://giacngo.vn/UserImages/2013/09/13/11/Tran2.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="570" /></font></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><font color="#808080" size="2">Ban Dẫn thỉnh v&agrave; giới tử t&aacute;c bạch cung thỉnh Hội đồng Thập sư tấn đ&agrave;n, truyền giới</font></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px; text-align: justify;">Ban Điều h&agrave;nh Giới trường v&agrave; giới tử đ&atilde; cung thỉnh HT.Th&iacute;ch Gi&aacute;c Nhường đương vi Đường đầu; HT.Th&iacute;ch Gi&aacute;c Tường, Yết-ma A-x&agrave;-l&ecirc;; HT.Th&iacute;ch Gi&aacute;c Giới, Gi&aacute;o thọ A-x&agrave;-l&ecirc;. Cung thỉnh chư t&ocirc;n T&ocirc;n chứng gồm: Đệ nhất t&ocirc;n chứng, HT.Th&iacute;ch Gi&aacute;c Ph&uacute;c; đệ nhị t&ocirc;n chứng, HT.Th&iacute;ch Gi&aacute;c Ngộ; đệ tam t&ocirc;n chứng: TT.Th&iacute;ch Gi&aacute;c Cầu; đệ tứ t&ocirc;n chứng, TT.Th&iacute;ch Gi&aacute;c Dũng; đệ ngũ t&ocirc;n chứng, HT.Th&iacute;ch Gi&aacute;c H&agrave;; đệ lục t&ocirc;n chứng, TT.Th&iacute;ch Gi&aacute;c Thanh; đệ thất t&ocirc;n chứng, TT.Th&iacute;ch Gi&aacute;c Tuấn.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px; text-align: justify;">Dưới sự dẫn thỉnh của TT.Th&iacute;ch Minh H&oacute;a, giới tử thọ giới đ&atilde; thủ phục, trang nghi&ecirc;m đối trước chư t&ocirc;n đức cầu giới ph&aacute;p. Sau khi Ban Dẫn thỉnh b&aacute;o c&aacute;o c&ocirc;ng t&aacute;c tổ chức Đại giới đ&agrave;n tại Giới trường l&ecirc;n chư t&ocirc;n đức, Hội đồng Tam sư, T&ocirc;n chứng đ&atilde; đảnh lễ Tam bảo l&agrave;m lễ t&aacute;c ph&aacute;p Yết-ma, tấn đ&agrave;n truyền giới cho 21 giới tử.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px; text-align: justify;">Buổi tấn đ&agrave;n truyền giới th&agrave;nh tựu vi&ecirc;n m&atilde;n, như sở nguyện của giới tử. Theo th&ocirc;ng tin từ Ban Tổ chức, chiều nay, t&acirc;n Sa-di v&agrave; Tỳ-kheo tại giới trường tịnh x&aacute; Trung T&acirc;m sẽ được Ban Tổ chức Đại giới đ&agrave;n Quảng Đức s&aacute;ch tấn, gi&aacute;o giới.</p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><font color="#808080" size="2"><img alt="Tran3.jpg" border="0" height="380" src="http://giacngo.vn/UserImages/2013/09/13/11/Tran3.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="570" /></font></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><font color="#808080" size="2">Hội đồng Tam sư, T&ocirc;n chứng ni&ecirc;m hương bạch Phật</font></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><font color="#808080" size="2"><img alt="Tran4.jpg" border="0" height="380" src="http://giacngo.vn/UserImages/2013/09/13/11/Tran4.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="570" /></font></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><font color="#808080" size="2">Giới tử s&aacute;m hối trước khi thọ giới</font></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><font color="#808080" size="2"><img alt="Tran5.jpg" border="0" height="380" src="http://giacngo.vn/UserImages/2013/09/13/11/Tran5.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="570" /></font></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><font color="#808080" size="2">Sau khi đại Tăng vấn h&ograve;a, lễ t&aacute;c ph&aacute;p Yết-ma,&nbsp;<br />\r\ngiờ ph&uacute;t truyền giới đ&atilde; diễn ra trong trang nghi&ecirc;m, thanh tịnh</font></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><font color="#808080" size="2"><img alt="Tran6.jpg" border="0" height="380" src="http://giacngo.vn/UserImages/2013/09/13/11/Tran6.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="570" /></font></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><font color="#808080" size="2">Phủ phục cầu giới</font></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><font color="#808080" size="2"><img alt="Tran7.jpg" border="0" height="380" src="http://giacngo.vn/UserImages/2013/09/13/11/Tran7.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="570" /></font></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><font color="#808080" size="2">Hội đồng Tam sư vấn nạn, truyền giới cho giới tử</font></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><font color="#808080" size="2"><img alt="Tran8.jpg" border="0" height="380" src="http://giacngo.vn/UserImages/2013/09/13/11/Tran8.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="570" /></font></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><font color="#808080" size="2">Ph&aacute;t nguyện thọ giới</font></p>\r\n\r\n<p align="left" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;">* Tại Giới trường tịnh x&aacute; Ngọc Phương, Ban Điều h&agrave;nh Giới trường v&agrave; giới tử đ&atilde; cung thỉnh Hội đồng Tam sư do Ni trưởng TN.Tr&agrave;ng Li&ecirc;n (tịnh x&aacute; Ngọc Diệp) đương vi Đường đầu; Ni trưởng TN.Ngoạt Li&ecirc;n, Yết-ma A-x&agrave;-l&ecirc;; Ni trưởng TN.T&acirc;n Li&ecirc;n, Gi&aacute;o thọ A-x&agrave;-l&ecirc;.&nbsp;</p>\r\n\r\n<p align="left" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;">Cung thỉnh Hội đồng T&ocirc;n chứng gồm: Ni trưởng TN.Minh Li&ecirc;n, Ni trưởng TN.Chi&ecirc;u Li&ecirc;n, Ni trưởng TN.Nh&atilde; Li&ecirc;n, Ni trưởng TN.Liễu Li&ecirc;n, Ni trưởng TN.Phục Li&ecirc;n, Ni trưởng TN.Mai Li&ecirc;n, Ni trưởng TN.Vĩnh Li&ecirc;n, Ni trưởng TN.Dũng Li&ecirc;n, Ni trưởng TN.Hội Li&ecirc;n, Ni trưởng TN.Hiệp Li&ecirc;n, Ni trưởng TN.&Aacute;nh Li&ecirc;n, Ni sư TN.Ẩn Li&ecirc;n, Ni sư TN.Biểu Li&ecirc;n, Ni sư TN.Thuận Li&ecirc;n, Ni sư TN.Tr&igrave; Li&ecirc;n, Ni sư TN.Th&ocirc;ng Li&ecirc;n, Ni sư TN.Li&ecirc;n Li&ecirc;n.</p>\r\n\r\n<p align="left" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;">Nghi thức tấn đ&agrave;n, truyền giới diễn ra trang nghi&ecirc;m, thanh tịnh. 21 giới tử thọ giới Tỳ-kheo-ni đ&atilde; lần lượt thủ phục, đối trước Hội đồng Tam sư, Hội đồng T&ocirc;n chứng cầu giới v&agrave; thọ giới ph&aacute;p.</p>\r\n\r\n<p align="right" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><b>Hạnh &Yacute;</b></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;">----------------------------</p>\r\n\r\n<p align="left" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;">&nbsp;</p>\r\n\r\n<p align="left" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><font color="#a52a2a"><b>Giới trường tổ đ&igrave;nh Bửu Quang: Truyền giới cho giới tử Sa-di, Tỳ-kheo</b></font></p>\r\n\r\n<p align="left" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;">C&ugrave;ng thời gian, tại Giới trường tổ đ&igrave;nh Bửu Quang (Q.Thủ Đức), BTC Đại giới đ&agrave;n Quảng Đức đ&atilde; tấn đ&agrave;n truyền giới cho giới tử Tỳ-kheo, Sa-di theo truyền thống Phật gi&aacute;o Nam t&ocirc;ng.</p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><font color="#808080" size="2"><img alt="Giang (19).JPG" border="0" height="374" src="http://giacngo.vn/UserImages/2013/09/13/11/Giang%20%2819%29.JPG" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="570" /></font></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><font color="#808080" size="2">Giới tử tại buổi tấn đ&agrave;n truyền giới theo truyền thống PG Nam t&ocirc;ng</font></p>\r\n\r\n<p align="left" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;">BTC cung thỉnh Hội đồng Thập sư gồm c&oacute;: HT.Th&iacute;ch Vi&ecirc;n Minh, th&agrave;nh vi&ecirc;n HĐCM GHPGVN; HT.Th&iacute;ch Hộ Ch&aacute;nh,Ủy vi&ecirc;n HĐTS GHPGVN; HT.Th&iacute;ch Thiện Nh&acirc;n, Ph&oacute; BTS GHPGVN Q.9; HT.Th&iacute;ch Thiện Ph&aacute;p, Ủy vi&ecirc;n BTS GHPGVN tỉnh Đồng Nai; c&ugrave;ng TT.Th&iacute;ch Thiện Ph&uacute;c; TT.Th&iacute;ch Thiện Hạnh; TT.Th&iacute;ch Tăng Định; ĐĐ.Ch&aacute;nh Định, ĐĐ.Th&iacute;ch Thiện Minh; TT.Th&iacute;ch Thiện Đạt; TT.Th&iacute;ch Tuệ Quyền; TT.Th&iacute;ch T&acirc;m Lượng; TT.Th&iacute;ch Thiện Mẫn c&ugrave;ng chư Tăng hệ ph&aacute;i Nam t&ocirc;ng tại Giới trường tổ đ&igrave;nh Bửu Quang.</p>\r\n\r\n<p align="left" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;">Buổi lễ c&oacute; nghi thức cung thỉnh chư t&ocirc;n đức Tam sư, Thất chứng quang l&acirc;m giới trường, niệm hương c&uacute;ng dường Tam bảo, chư t&ocirc;n đức Tam sư. Theo đ&oacute;, s&aacute;ng nay Hội đồng Thập sư đ&atilde; truyền giới cho 24 giới tử&nbsp; Sa-di v&agrave; 13 giới tử Tỳ-kheo được thọ giới theo nghi thức của Phật gi&aacute;o Nam t&ocirc;ng.</p>\r\n\r\n<p align="left" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><font color="#a52a2a"><i>Một số h&igrave;nh ảnh tại buổi lễ:</i></font></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><img alt="Giang (2).JPG" border="0" height="375" src="http://giacngo.vn/UserImages/2013/09/13/11/Giang%20%282%29.JPG" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="570" /></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><img alt="Giang (3).JPG" border="0" height="392" src="http://giacngo.vn/UserImages/2013/09/13/11/Giang%20%283%29.JPG" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="570" /></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><img alt="Giang (5).JPG" border="0" height="404" src="http://giacngo.vn/UserImages/2013/09/13/11/Giang%20%285%29.JPG" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="570" /></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><img alt="Giang (6).JPG" border="0" height="369" src="http://giacngo.vn/UserImages/2013/09/13/11/Giang%20%286%29.JPG" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="570" /></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><img alt="Giang (7).JPG" border="0" height="383" src="http://giacngo.vn/UserImages/2013/09/13/11/Giang%20%287%29.JPG" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="570" /></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><img alt="Giang (8).JPG" border="0" height="385" src="http://giacngo.vn/UserImages/2013/09/13/11/Giang%20%288%29.JPG" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="570" /></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><img alt="Giang (10).JPG" border="0" height="363" src="http://giacngo.vn/UserImages/2013/09/13/11/Giang%20%2810%29.JPG" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="570" /></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; line-height: 22px;"><img alt="Giang (11).JPG" border="0" height="411" src="http://giacngo.vn/UserImages/2013/09/13/11/Giang%20%2811%29.JPG" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="570" /></