INSERT INTO `chuathienquang_ask` (`id`, `id_category`, `question`, `author1`, `time`, `answer`, `author2`) VALUES
(1, 2, 'Những khó khăn bắt đầu ập tới với con từ khi con vừa học xong trung học.', 'Phật tử', '2012-11-21 07:15:23', 'Xin chào người bạn trẻ thân mến!  Cám ơn rất nhiều những chia sẻ từ trái tim của bạn. Tất cả những sự chia sẻ đó cũng đều xuất phát từ sự mong muốn có một hướng đi để có thể sống lành, sống đẹp, sống bình an và hạnh phúc. ', 'Thích Nữ Như Hương'),
(2, 2, 'Sự mong muốn thành công đã mang đến quá nhiều đau khổ và bận rộn. Không điều gì con làm mà cảm thấy đủ cả. Làm thế nào con có thể tạo sự an lạc cho bản thân?', 'Phật tử', '2012-11-21 07:15:35', 'Phẩm chất của hành động của bạn phụ thuộc vào phẩm chất sự có mặt của bạn. Đề nghị bạn hăng hái để hiến tặng hạnh phúc, làm cho người khác hạnh phúc. Đó là việc làm tốt đẹp. Nếu bạn không hạnh phúc, bạn sẽ không thể làm điều đó.', 'Thích Nữ Như Hương'),
(3, 2, 'Bất cứ khi nào tôi làm điều gì, tâm trí tôi cứ hướng về tương lai cho việc suy nghĩ?', 'Phật tử', '2012-11-21 07:16:03', 'Khi bạn nhận cùng lúc nhiều bức thư, bạn phải quyết định xem nên đọc bức nào trước. Tuy bạn muốn đọc hai bức thư cùng một lúc bởi chúng đều quan trọng. ', 'Thích Nữ Như Hương'),
(4, 1, 'Làm thế nào để có sự tự tin nơi chính mình?', 'Phật tử', '2012-11-21 07:16:36', 'Câu trả lời này do Ban biên tập ghi lại tóm tắt lời dạy của Sư Ông.  Khi nhìn sâu vào chính mình ta thấy bản thân mình vốn có sẵn một gia tài rất lớn. ', 'Thích Nữ Như Hương'),
(5, 1, 'Kính thưa quý thầy và quý sư cô! Mỗi khi có tâm hành tiêu cực xuất hiện, con rất muốn chuyển hóa chúng.', 'Phật tử', '2012-11-21 07:16:46', 'Thân chào bạn!  Mỗi khi bạn có những tâm hành tiêu cực xuất hiện, thì việc đầu tiên mà bạn phải nên làm là thực tập hơi thở có chánh niệm, để đưa tâm trở về với thân ngay trong giây phút hiện tại.', 'Thích Nữ Như Hương'),
(6, 1, 'Con là một sinh viên vừa tốt nghiệp đại học. Con phải làm gì bây giờ?', 'Phật tử', '2012-11-21 07:16:57', 'Ban Biên Tập Làng Mai đã nhận được thư bạn và nhiều bức thư khác gởi về với nội dung: muốn tìm một nơi nào mà ở đó mình về lại với chính mình, ở đó mình cảm nhận được cảm giác AN LẠC trong cuộc sống này mà không phải mưu tính cho tương lai mà đâu biết mình đang hiện hữu trong hiện tại.', 'Sư cô Cần Nghiêm & Sư cô Văn Nghiêm chia sẻ cùng b'),
(7, 1, 'Con biết rằng thế giới này có nhiều pháp môn tu và đạo Phật luôn hướng con người đến con đường an lạc.', 'Phật tử', '2012-11-21 07:17:16', 'Bạn Anh Nhi thân mến!   Đọc những dòng tâm sự của bạn, trong tôi có sự cảm thông. Ngày xưa, khi tôi bắt đầu có ước muốn đi xuất gia để tiếp nối con đường rất đẹp, rất sáng của Bụt. ', 'Sư  cô Nội Nghiêm chia sẻ'),
(8, 4, 'Con không biết phải làm gì với cuộc sống của chính mình?', 'Phật tử', '2012-11-21 07:17:31', 'Thỉnh thoảng, chúng ta trồng hoa hồng từ những cành giâm, biết rằng cành đó không có rễ. Chúng ta đặt những cành giâm vào đất ẩm. Nếu chúng ta đặt cành đó với thời gian đủ lâu trong điều kiện đất đủ ẩm và giàu chất dinh dưỡng thì nó sẽ vững chắc và đâm rễ.', 'Thích Nữ Như Hương'),
(9, 2, 'Thử hỏi nè', 'Bùi Thanh Tuấn', '2012-11-29 03:41:42', '<p>\r\n	<strong>Thử nghiệm </strong>trả lời đ&acirc;y b&agrave; kon ơi !</p>\r\n<p>\r\n	<strong>Thử nghiệm </strong>trả lời đ&acirc;y b&agrave; kon ơi !</p>\r\n<p>\r\n	<strong>Thử nghiệm </strong>trả lời đ&acirc;y b&agrave; kon ơi !</p>\r\n<p>\r\n	<strong>Thử nghiệm </strong>trả lời đ&acirc;y b&agrave; kon ơi !</p>\r\n', 'Nguyễn Thanh Phong'),
(13, 5, 'Làm thế nào để sự nghiệp phát triển lâu dài và bền vững ?', 'Bùi Thanh Tuấn', '2012-11-29 03:50:48', '<p>\r\n	Thử l&agrave;m theo lời Phật dạy !</p>\r\n', 'Nguyễn Thanh Phong'),
(14, 2, 'Con muốn hỏi như vầy nè', 'Phật tử 1', '2012-12-07 09:44:03', '', '');
