INSERT INTO `chuathienquang_news` (`id`, `id_category`, `author`, `date`, `content`, `title`, `type`) VALUES
(89, 14, 'Đức Hiếu (từ Nhật Bản)', '2013-02-02 17:15:53', '<div id="ContentOutter1b" style="float: left; margin-top: 5px; width: 654px; height: auto; clear: both; color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; line-height: normal;">\r\n<div id="ContentOutter2b" style="float: left; width: 654px; height: auto;">\r\n<div id="ContentOutter3b" style="float: left; width: 654px; height: auto; overflow: hidden;">\r\n<p><span class="ctcSummary" id="ZoomContentSummary" style="font-weight: bold; color: rgb(51, 51, 51); text-align: justify; width: auto; line-height: 22px; font-size: 14px;">Ch&ugrave;a H&ograve;a Lạc l&agrave; ng&ocirc;i ch&ugrave;a đầu ti&ecirc;n của người Việt Nam sống ở miền T&acirc;y, Kansai, Nhật Bản.</span></p>\r\n<span class="ctcBody" id="ZoomContentBody" style="text-align: justify; width: auto; line-height: 22px; font-size: 14px;">V&agrave;o c&aacute;c ng&agrave;y 5 &amp; 6-5-2012, ch&ugrave;a H&ograve;a Lạc tọa lạc tại miền T&acirc;y Kansai, Nhật Bản, sau 7 th&aacute;ng x&acirc;y dựng tr&ugrave;ng tu, nh&acirc;n m&ugrave;a Phật đản PL.2556, đ&atilde; tổ chức lễ kh&aacute;nh th&agrave;nh trang nghi&ecirc;m v&agrave; đạo vị.&nbsp;</span>\r\n\r\n<p><span class="ctcBody" id="ZoomContentBody" style="text-align: justify; width: auto; line-height: 22px; font-size: 14px;">Buổi lễ diễn ra trong kh&ocirc;ng kh&iacute; h&acirc;n hoan, hạnh ph&uacute;c dưới sự ch&uacute; nguyện của chư t&ocirc;n đức Tăng Ni Việt Nam đang du học tại Nhật Bản: c&aacute;c Đại đức Th&iacute;ch Minh Quang, Th&iacute;ch Phước Điền, Th&iacute;ch Quảng Niệm, Th&iacute;ch Hải Nguyện, Th&iacute;ch&nbsp;&nbsp;Tịnh &Acirc;n, Th&iacute;ch Tường Nghi&ecirc;m, Sư c&ocirc; Th&iacute;ch nữ Như T&acirc;m.&nbsp;</span></p>\r\n\r\n<p><span class="ctcBody" id="ZoomContentBody" style="text-align: justify; width: auto; line-height: 22px; font-size: 14px;">Đến dự v&agrave; hộ niệm c&oacute; HT.Takaoka Shucho, tr&uacute; tr&igrave; ch&ugrave;a Đức L&acirc;m, Nagoya; Gi&aacute;o sư Wakahara shusho c&ugrave;ng chư Tăng Nhật Bản, c&aacute;c gi&aacute;o sư Trường Đại học Ryukoku, Kyoto, đại diện c&aacute;c t&ocirc;n gi&aacute;o bạn v&agrave; đ&ocirc;ng đảo qu&yacute; vị đồng hương, Phật tử từ Tokyo, Kyoto, Osaka, Himeji, Yao, Kobe...&nbsp;</span></p>\r\n\r\n<p><span class="ctcBody" id="ZoomContentBody" style="text-align: justify; width: auto; line-height: 22px; font-size: 14px;">Được biết, ng&ocirc;i ch&ugrave;a Việt Nam đầu ti&ecirc;n được x&acirc;y dựng tại xứ sở Hoa Anh Đ&agrave;o l&agrave; ch&ugrave;a Nam H&ograve;a ở tỉnh Saitama. Ch&ugrave;a H&ograve;a Lạc l&agrave; ng&ocirc;i ch&ugrave;a đầu ti&ecirc;n của người Việt Nam sống ở miền T&acirc;y, Kansai.</span></p>\r\n\r\n<p><span class="ctcBody" id="ZoomContentBody" style="text-align: justify; width: auto; line-height: 22px; font-size: 14px;">Sau 2 năm tu học dưới sự hướng dẫn của ĐĐ.Th&iacute;ch Nhuận Phổ (du học sinh đang học kh&oacute;a thạc sĩ tại Trường Đại học Ryukoku, Kyoto), c&ugrave;ng với chư Tăng Ni đang du học tại Nagoya, Kyoto, qua sự thỉnh cầu của to&agrave;n thể Phật tử sống tha hương, Đại đức đ&atilde; ch&iacute;nh thức vận động quy&ecirc;n g&oacute;p, dựng l&ecirc;n ng&ocirc;i ch&ugrave;a H&ograve;a Lạc, để l&agrave;m m&aacute;i ấm t&acirc;m linh cho người con Phật sống xa qu&ecirc;, v&agrave; ng&otilde; hầu đ&aacute;p đền c&ocirc;ng đức của Tam bảo, kế thừa mạng mạch Phật ph&aacute;p.&nbsp;</span></p>\r\n\r\n<p><span class="ctcBody" id="ZoomContentBody" style="text-align: justify; width: auto; line-height: 22px; font-size: 14px;">Ch&ugrave;a được thiết kế theo kiến tr&uacute;c đặc trưng của ch&ugrave;a Việt Nam. C&aacute;c pho tượng Phật được t&ocirc;n tạo bằng đồng (tượng Phật Th&iacute;ch Ca ngồi, 350kg; tượng Địa Tạng Bồ-t&aacute;t, Qu&aacute;n Thế &Acirc;m Bồ-t&aacute;t, đứng, 150kg), Ti&ecirc;u Diện, Hộ Ph&aacute;p v&agrave; c&aacute;c &Aacute;n Thờ đều đặt v&agrave; đ&uacute;c tại Huế. Ch&ugrave;a được x&acirc;y dựng th&agrave;nh 2 tầng, tầng tr&ecirc;n thờ Tam bảo. Tầng dưới l&agrave;m nơi sinh hoạt v&agrave; tu học B&aacute;t quan trai cho Phật tử.&nbsp;</span></p>\r\n\r\n<p><span class="ctcBody" id="ZoomContentBody" style="text-align: justify; width: auto; line-height: 22px; font-size: 14px;">Ng&ocirc;i ch&ugrave;a hiện tại đ&atilde; được x&acirc;y dựng tr&ecirc;n mảnh đất Thần Hộ (Kobe), nằm ở giữa tuyến đường qua lại của Osaka v&agrave; Himeji, trong th&agrave;nh phố Kobe, n&ecirc;n mọi phương tiện giao th&ocirc;ng đi lại cũng rất thuận lợi, gần ga Karumo-eki v&agrave; gần cả c&aacute;c trạm xe bus, si&ecirc;u thị, tuy rằng ng&ocirc;i ch&ugrave;a tuy nhỏ nhưng l&agrave; nơi ẩn chứa t&acirc;m nguyện lớn của bao nhi&ecirc;u người con Việt sống tha hương.</span></p>\r\n\r\n<div align="center"><span class="ctcBody" id="ZoomContentBody" style="text-align: justify; width: auto; line-height: 22px; font-size: 14px;"><img height="413" src="http://www.giacngo.vn/UserImages/2012/05/13/9/chu_tang_lam_le_phat_dan1_669958978_jpg.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="551" /></span></div>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<p align="center"><span class="ctcBody" id="ZoomContentBody" style="text-align: justify; width: auto; line-height: 22px; font-size: 14px;"><img height="412" src="http://www.giacngo.vn/UserImages/2012/05/13/9/chuan_bi_le_khai_kinh_122173442_jpg.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="550" /></span></p>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<p align="center"><span class="ctcBody" id="ZoomContentBody" style="text-align: justify; width: auto; line-height: 22px; font-size: 14px;"><img height="413" src="http://www.giacngo.vn/UserImages/2012/05/13/9/dai_le_khanh_thanh_1_416270818_jpg.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="551" /></span></p>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<p align="center"><span class="ctcBody" id="ZoomContentBody" style="text-align: justify; width: auto; line-height: 22px; font-size: 14px;"><img height="412" src="http://www.giacngo.vn/UserImages/2012/05/13/9/dang_hoa_cung_duong_6_816615872_jpg.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="550" /></span></p>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<div align="center"><span class="ctcBody" id="ZoomContentBody" style="text-align: justify; width: auto; line-height: 22px; font-size: 14px;"><img height="412" src="http://www.giacngo.vn/UserImages/2012/05/13/9/le_cat_bang_khanh_thanh_241012250_jpg.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="550" /></span></div>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<p align="center"><span class="ctcBody" id="ZoomContentBody" style="text-align: justify; width: auto; line-height: 22px; font-size: 14px;"><img height="412" src="http://www.giacngo.vn/UserImages/2012/05/13/9/le_niem_huong_dai_le_khanh_thanh_1_696452260_jpg.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="550" /></span></p>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<p align="center"><span class="ctcBody" id="ZoomContentBody" style="text-align: justify; width: auto; line-height: 22px; font-size: 14px;"><img height="412" src="http://www.giacngo.vn/UserImages/2012/05/13/9/le_phat_dan_cung_nghinh_chu_ton_duc_959066905_jpg.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="550" /></span></p>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<div align="center"><span class="ctcBody" id="ZoomContentBody" style="text-align: justify; width: auto; line-height: 22px; font-size: 14px;"><img height="412" src="http://www.giacngo.vn/UserImages/2012/05/13/9/le_tam_phat_7_867513594_jpg.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="550" /></span></div>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<p align="center"><span class="ctcBody" id="ZoomContentBody" style="text-align: justify; width: auto; line-height: 22px; font-size: 14px;"><img height="412" src="http://www.giacngo.vn/UserImages/2012/05/13/9/le_tam_phat_123_598720555_jpg.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="550" /></span></p>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<p align="center"><span class="ctcBody" id="ZoomContentBody" style="text-align: justify; width: auto; line-height: 22px; font-size: 14px;"><img height="412" src="http://www.giacngo.vn/UserImages/2012/05/13/9/mua_lan_mung_khanh_thanh1_914991374_jpg.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="550" /></span></p>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<div align="center"><span class="ctcBody" id="ZoomContentBody" style="text-align: justify; width: auto; line-height: 22px; font-size: 14px;"><img height="412" src="http://www.giacngo.vn/UserImages/2012/05/13/9/niem_phat_cau_gia_bi_2_363183282_jpg.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="550" /></span></div>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<p align="center"><span class="ctcBody" id="ZoomContentBody" style="text-align: justify; width: auto; line-height: 22px; font-size: 14px;"><img height="413" src="http://www.giacngo.vn/UserImages/2012/05/13/9/quan_khach_nhat_cac_giao_su_875207035_jpg.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="551" /></span></p>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<div align="center"><span class="ctcBody" id="ZoomContentBody" style="text-align: justify; width: auto; line-height: 22px; font-size: 14px;"><img height="413" src="http://www.giacngo.vn/UserImages/2012/05/13/9/tuyen_so_phat_dan_892046402_jpg.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="550" /></span></div>\r\n\r\n<p class="ctcSource" style="font-size: 14px; text-align: right;">&nbsp;</p>\r\n</div>\r\n</div>\r\n</div>\r\n', 'Thêm một ngôi chùa Việt Nam tại Nhật Bản', 0),
(90, 10, 'Nhuận Chánh', '2013-02-02 17:17:37', '<div id="ContentOutter1b" style="float: left; margin-top: 5px; width: 654px; height: auto; clear: both; color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; line-height: normal;">\r\n<div id="ContentOutter2b" style="float: left; width: 654px; height: auto;">\r\n<div id="ContentOutter3b" style="float: left; width: 654px; height: auto; overflow: hidden;">\r\n<p><span style="font-size: 14px; line-height: 22px; text-align: justify;">Nh&acirc;n Đại lễ Phật đản PL.2556, Thi&ecirc;n Quang ni tự (phường Đ&ocirc;ng H&ograve;a, thị x&atilde; Dĩ An, tỉnh B&igrave;nh Dương) đ&atilde; tổ chức trao học bổng khuyến học cho sinh vi&ecirc;n đang theo học c&aacute;c trường đại học v&agrave; cao đẳng.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p align="center"><span class="ctcBody" id="ZoomContentBody" style="text-align: justify; width: auto; line-height: 22px; font-size: 14px;"><img alt="hocbong5.JPG" border="0" height="367" src="http://www.giacngo.vn/UserImages/2012/05/02/10/hocbong5.JPG" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="550" /></span></p>\r\n\r\n<p align="center"><span class="ctcBody" id="ZoomContentBody" style="text-align: justify; width: auto; line-height: 22px; font-size: 14px;"><font color="#808080" size="2">C&aacute;c bạn sinh vi&ecirc;n c&oacute; dịp đến ch&ugrave;a&nbsp;học hỏi về&nbsp;Phật ph&aacute;p</font></span></p>\r\n\r\n<p align="center"><span class="ctcBody" id="ZoomContentBody" style="text-align: justify; width: auto; line-height: 22px; font-size: 14px;"><img alt="hocbong6.JPG" border="0" height="367" src="http://www.giacngo.vn/UserImages/2012/05/02/10/hocbong6.JPG" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="550" /></span></p>\r\n\r\n<p align="center"><span class="ctcBody" id="ZoomContentBody" style="text-align: justify; width: auto; line-height: 22px; font-size: 14px;"><font color="#808080" size="2">T&acirc;m sự với Ni sư Hương Nhũ về cuộc sống gặp nhiều bất hạnh của ch&iacute;nh m&igrave;nh</font></span></p>\r\n\r\n<p><span class="ctcBody" id="ZoomContentBody" style="text-align: justify; width: auto; line-height: 22px; font-size: 14px;">Một số bạn sinh vi&ecirc;n c&oacute; ho&agrave;n cảnh đặc biệt vượt kh&oacute; trong học tập đ&atilde; được Ni sư Th&iacute;ch nữ Hương Nhũ, giảng vi&ecirc;n Học viện Phật gi&aacute;o Việt Nam tại TP.Hồ Ch&iacute; Minh, trụ tr&igrave; Thi&ecirc;n Quang ni tự v&agrave; nh&oacute;m Diễn đ&agrave;n Vẻ đẹp Phật ph&aacute;p trao tặng.</span></p>\r\n\r\n<p><span class="ctcBody" id="ZoomContentBody" style="text-align: justify; width: auto; line-height: 22px; font-size: 14px;">Mỗi bạn sinh vi&ecirc;n được một phần học bổng gồm ba-l&ocirc;, s&aacute;ch, b&aacute;nh, sữa v&agrave; 1.000.000 đồng do Phật tử ủng hộ.&nbsp; Đ&acirc;y cũng l&agrave; dịp để c&aacute;c bạn sinh vi&ecirc;n c&oacute; cơ duy&ecirc;n hiểu biết th&ecirc;m về Đức Phật, về những gi&aacute; trị đạo đức nh&acirc;n văn m&agrave; Ng&agrave;i đ&atilde; chỉ dạy cho đời.</span></p>\r\n\r\n<p align="center"><span class="ctcBody" id="ZoomContentBody" style="text-align: justify; width: auto; line-height: 22px; font-size: 14px;"><img alt="hocbong7.JPG" border="0" height="367" src="http://www.giacngo.vn/UserImages/2012/05/02/10/hocbong7.JPG" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="550" /></span></p>\r\n\r\n<p align="center"><span class="ctcBody" id="ZoomContentBody" style="text-align: justify; width: auto; line-height: 22px; font-size: 14px;"><font color="#808080" size="2">Tặng qu&agrave; cho sinh vi&ecirc;n</font></span></p>\r\n\r\n<p align="center"><span class="ctcBody" id="ZoomContentBody" style="text-align: justify; width: auto; line-height: 22px; font-size: 14px;"><img alt="hocbong9.JPG" border="0" height="367" src="http://www.giacngo.vn/UserImages/2012/05/02/10/hocbong9.JPG" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="550" /></span></p>\r\n\r\n<p align="center"><span class="ctcBody" id="ZoomContentBody" style="text-align: justify; width: auto; line-height: 22px; font-size: 14px;"><font color="#808080" size="2">Tặng học bổng khuyến học cho sinh vi&ecirc;n</font></span></p>\r\n\r\n<p><span class="ctcBody" id="ZoomContentBody" style="text-align: justify; width: auto; line-height: 22px; font-size: 14px;">Dịp n&agrave;y, Ni sư Hương Nhũ n&oacute;i chuyện về t&igrave;nh y&ecirc;u thương, l&ograve;ng vị tha, sẻ chia với c&aacute;c bạn sinh vi&ecirc;n nhằm gi&uacute;p c&aacute;c bạn nỗ lực học tập, học hỏi về hạnh lắng nghe để l&agrave;m lớn mạnh khả năng hiểu biết v&agrave; y&ecirc;u thương, nu&ocirc;i dưỡng ch&iacute; nguyện v&agrave;o đời theo tinh thần Phật dạy.</span></p>\r\n\r\n<p class="ctcSource" style="font-size: 14px; text-align: right;">&nbsp;</p>\r\n</div>\r\n</div>\r\n</div>\r\n', 'Tặng học bổng cho sinh viên', 0);
INSERT INTO `chuathienquang_news` (`id`, `id_category`, `author`, `date`, `content`, `title`, `type`) VALUES
(91, 11, 'BBT Website', '2013-02-02 17:19:18', '<div style="margin: 0px; padding: 0px; outline: 0px; max-width: 654px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: center;"><img alt="" src="http://vuonhoaphatgiao.com/uploads/noidung/images/htts(1).jpg" style="margin: 0px; padding: 0px; outline: 0px; border: 0px; border-spacing: 0px; vertical-align: middle; max-width: 654px; width: 190px; height: 252px;" /><br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n&nbsp;</div>\r\n\r\n<p><strong style="margin: 0px; padding: 0px; outline: 0px; max-width: 654px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">H&ograve;a Thượng Th&iacute;ch Thiện Si&ecirc;u</strong><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<strong style="margin: 0px; padding: 0px; outline: 0px; max-width: 654px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">I. Th&acirc;n thế.</strong><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">Ho&agrave; thượng họ V&otilde;, hu&yacute; Trọng Tường, Ph&aacute;p danh T&acirc;m Phật, tự Tr&iacute; Đức, hiệu Th&iacute;ch Thiện Si&ecirc;u. Ho&agrave; thượng sinh ng&agrave;y 15 th&aacute;ng 7 năm T&acirc;n Dậu (1921) trong một gia đ&igrave;nh th&acirc;m t&iacute;n Phật gi&aacute;o nhiều đời, ở l&agrave;ng Thần Ph&ugrave;, huyện Hương Thuỷ, tỉnh Thừa Thi&ecirc;n-Huế.</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">Th&acirc;n phụ l&agrave; Cụ &ocirc;ng V&otilde; Trọng Gi&aacute;ng, th&acirc;n mẫu l&agrave; Cụ b&agrave; Dương Thị Viết.&nbsp; Ho&agrave; thượng l&agrave; con trưởng trong một gia đ&igrave;nh gồm c&oacute; 5 anh em: 3 trai v&agrave; 2 g&aacute;i. Người em kế của Ng&agrave;i cũng noi theo ch&iacute; nguyện của anh m&igrave;nh v&agrave; xuất gia, đ&oacute; l&agrave; Cố Thượng tọa Th&iacute;ch Thiện Giải, nguy&ecirc;n l&agrave; Ch&aacute;nh Đại diện GHPGVNTN tỉnh L&acirc;m Đồng, tr&uacute; tr&igrave; ch&ugrave;a Phước Huệ &ndash; Bảo Lộc.</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<strong style="margin: 0px; padding: 0px; outline: 0px; max-width: 654px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">&nbsp;II. Quảng đường tu học v&agrave; hoằng ho&aacute;.</strong><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">Vốn c&oacute; t&uacute;c duy&ecirc;n với Phật ph&aacute;p, năm 14 tuổi (1935), &yacute; thức được rằng chỉ c&oacute; noi theo con đường của đức Phật đ&atilde; đi mới giải đ&aacute;p được trọn vẹn &yacute; nghĩa của cuộc sống. H&ograve;a thượng xin ph&eacute;p song th&acirc;n được xuất gia đầu Phật. Ng&agrave;i đế&acirc;n tham học Phật ph&aacute;p tại Phật học đường ch&ugrave;a Tr&uacute;c L&acirc;m &ndash; Huế. Phật học đường n&agrave;y đặt dưới sự chứng minh chủ tr&igrave; của Ho&agrave; thượng Th&iacute;ch Gi&aacute;c Ti&ecirc;n, Ho&agrave; thượng Th&iacute;ch Tr&iacute; Độ l&agrave;m Đốc gi&aacute;o, Ho&agrave; thượng Th&iacute;ch Tịnh Khiết l&agrave;m Ph&oacute; Đốc gi&aacute;o, Cư sĩ T&acirc;m Minh - L&ecirc; Đ&igrave;nh Th&aacute;m l&agrave;m phụ giảng.&nbsp; Đ&acirc;y l&agrave; Phật học đường đầu ti&ecirc;n của hội An Nam Phật học. Những vị tốt nghiệp taị đ&acirc;y đ&atilde; v&agrave; đang l&agrave; rường cột của PGVN như qu&yacute; Ho&agrave; thượng Th&iacute;ch Tr&iacute; Tịnh, Th&iacute;ch Tr&iacute; Quang, Th&iacute;ch Tr&iacute; Nghiểm tức cố ho&agrave; thượng Th&iacute;ch Thiện Minh, ph&aacute;p đệ của Ng&agrave;i...</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">Sau gần 10 năm theo học c&aacute;c kh&oacute;a Sơ, Trung, v&agrave; Cao đẳng Phật học, Ho&agrave; thượng đ&atilde; Tốt nghiệp hạng ưu Đai Học Phật Gi&aacute;o (1944). Cũng trong năm ấy, Trường lại được chuyển l&ecirc;n Đại t&ograve;ng l&acirc;m Kim Sơn, T&ograve;ng L&acirc;m duy nhất của Phật gi&aacute;o ở Trung phần thời bấy giờ, thuộc x&atilde; Lựu Bảo, ngoại &ocirc; kinh th&agrave;nh Huế. Tại đ&acirc;y, Ho&agrave; thượng vừa l&agrave;m Giảng sư của Hội, vừa phụ tr&aacute;ch giảng dạy c&aacute;c lớp Sơ v&agrave; Trung đẳng của Trường. Trong số những vị tham học với Ng&agrave;i l&uacute;c đ&oacute;, c&oacute; nhiều vị đ&atilde; v&agrave; đang phục vụ Gi&aacute;o hội như qu&yacute; Ho&agrave; thượng Thiện Ch&acirc;u, Thiện Giải, Thiện B&igrave;nh, Đổng Minh, Ch&aacute;nh Trực, Thiện Duy&ecirc;n, Từ Mẫn, Quảng Li&ecirc;n ...</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">Năm 26 tuổi, 1947, H&ograve;a thượng được Sơn m&ocirc;n Tăng gi&agrave; cử l&agrave;m Tr&uacute; tr&igrave; Tổ đ&igrave;nh Từ Đ&agrave;m &ndash; Huế, đồng thời giảng dạy cho Tăng Ni ở Phật học đường B&aacute;o Quốc v&agrave; Ni viện Diệu Đức &ndash; Huế.</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">Năm 28 tuổi (1949), Ng&agrave;i được bổn sư l&agrave; Ho&agrave; thượng thượng Trừng hạ Thuỷ, hiệu Th&iacute;ch Gi&aacute;c Nhi&ecirc;n cho ph&eacute;p thọ Cụ t&uacute;c giới tại Đại Giới Đ&agrave;n B&aacute;o Quốc &ndash; Huế. Giới Đ&agrave;n n&agrave;y, do Ho&agrave; thượng Th&iacute;ch Tịnh Khiết l&agrave;m Đ&agrave;n đầu. Với sở học uy&ecirc;n th&acirc;m v&agrave; đạo hạnh trong s&aacute;ng, Ng&agrave;i đ&atilde; được chọn l&agrave;m vị thủ Sa-di.</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">Năm 1951, Ho&agrave; thượng được mời tham gia ph&aacute;i đo&agrave;n Phật gi&aacute;o miền Trung dự Hội nghị Thống nhất Phật gi&aacute;o ba miền tại ch&ugrave;a Từ Đ&agrave;m - Huế.</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">&nbsp;Với Đạo phong v&agrave; sở học của Ng&agrave;i, năm 1950-1955, Ho&agrave; thượng được bầu l&agrave;m Ch&aacute;nh Hội trưởng Tỉnh hội Phật gi&aacute;o Thừa Thi&ecirc;n. Ch&iacute;nh trong thời gian n&agrave;y, với chức vụ m&agrave; ng&agrave;i đảm nhiệm, H&ograve;a thượng đ&atilde; quyết định hợp thức ho&aacute; nội qui th&agrave;nh lập c&aacute;c Khu&ocirc;n Tịnh độ trong khắp to&agrave;n tỉnh với mục đ&iacute;ch kiện to&agrave;n tổ chức để sự hướng dẫn v&agrave; tu học Phật Ph&aacute;p được thống nhất.</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">Năm 1957, Tổng Trị sự Phật gi&aacute;o Trung phần suy cử Ho&agrave; thượng l&agrave;m Đốc gi&aacute;o Phật học đường Trung Việt tại ch&ugrave;a Hải Đức - Nha Trang.</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">Năm 1962, Ng&agrave;i lại trở về Huế để giảng dạy cho Tăng Ni ở Phật học đường B&aacute;o Quốc v&agrave; Ni viện Diệu Đức, cũng như tham gia c&aacute;c Phật sự tại Tổng trị sự Phật gi&aacute;o Trung phần v&agrave; Tỉnh hội Phật gi&aacute;o Thừa Thi&ecirc;n - Huế.</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">Trong suốt thời gian Ng&ocirc; Đ&igrave;nh Diệm l&ecirc;n nắm quyền tại miền nam đ&atilde; thực hiện ch&iacute;nh s&aacute;ch kỳ thị Phật gi&aacute;o, bắt giam, tra tấn, thủ ti&ecirc;u nhiều Phật gi&aacute;o đồ, dụ dỗ cải đạo, m&agrave; đỉnh cao l&agrave; nh&acirc;n m&ugrave;a Phật đản năm 1963, khi ch&iacute;nh quyền ra lệnh triệt hạ cờ Phật gi&aacute;o v&agrave; thảm s&aacute;t c&aacute;c Phật tử tại Đ&agrave;i ph&aacute;t thanh Huế, Ho&agrave; thượng đ&atilde; đại diện cho Tỉnh hội Phật gi&aacute;o Thừa Thi&ecirc;n-Huế c&ugrave;ng k&yacute; v&agrave;o &ldquo;Bản tuy&ecirc;n ng&ocirc;n của Tăng t&iacute;n đồ PGVN&rdquo; gồm 5 điều để phản đối chế độ độc t&agrave;i Ng&ocirc; Đ&igrave;nh Diệm đ&agrave;n &aacute;p Phật gi&aacute;o v&agrave; n&oacute;i l&ecirc;n th&aacute;i độ của h&agrave;ng Phật tử VN l&agrave; đấu tranh bằng đường lối bất bạo động cho l&yacute; tưỡng c&ocirc;ng bằng x&atilde; hội, b&igrave;nh đẵng t&ocirc;n gi&aacute;o. Do vậy đ&ecirc;m 20-8-1963, H&ograve;a thượng đ&atilde; c&ugrave;ng bị bắt giam tại Ty C&ocirc;ng an Thừa Thi&ecirc;n, cho đến khi ch&iacute;nh quyền Diệm bị lật đổ, Ng&agrave;i mới được trả tự do.</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">Từ năm 1964-1974, Ho&agrave; thượng được cung cữ l&agrave;m Ph&oacute; đại diện Gi&aacute;o Hội Phật gi&aacute;o Việt Nam Thống Nhất miền Vạn Hạnh; trong thời gian n&agrave;y, Ng&agrave;i cũng điều h&agrave;nh v&agrave; giảng dạy lớp Cao đẳng Phật học chuy&ecirc;n khoa Liễu Qu&aacute;n tại ch&ugrave;a Linh Quang &ndash; Huế; đ&oacute; l&agrave; lớp đ&agrave;o tạo Giảng sư Phật học của miền Vạn Hạnh. C&aacute;c vị đ&atilde; tốt nghiệp tại lớp chuy&ecirc;n khoa n&agrave;y đ&atilde; v&agrave; đang h&agrave;nh đạo khắp nơi, m&agrave; gần gũi với ch&uacute;ng ta như H&ograve;a thượng Th&iacute;ch Thiện Tr&igrave;, Th&iacute;ch Tịnh Từ, v&agrave; cố Thượng tọa Th&iacute;ch Thiện Tường .vv.. Đồng thời H&ograve;a thượng c&ograve;n được mời giảng dạy cho c&aacute;c t&ograve;ng l&acirc;m ở nhiều nơi kh&aacute;c như Đ&agrave; Nẵng, Nha Trang, T&acirc;y Nguy&ecirc;n v&agrave; S&agrave;i G&ograve;n...Mặc d&ugrave; phải g&aacute;nh v&aacute;c nhiều trọng tr&aacute;ch, nhưng Ng&agrave;i vẫn chu to&agrave;n c&aacute;c Phật sự được Gi&aacute;o hội giao ph&oacute;.</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">Năm 1965, Ho&agrave; thượng được mời khai đạo giới tử tại Đại giới đ&agrave;n Từ Hiếu &ndash; Huế.</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">Năm 1968, Ng&agrave;i được cung thỉnh l&agrave;m Tuy&ecirc;n luật sư v&agrave; khai đạo giới tử tại Đại Giới đ&agrave;n ở Phật học viện Hải Đức - Nha Trang.</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">Năm 1970, Ng&agrave;i l&agrave; vị khai đạo giới tử Đại Giới Đ&agrave;n Vĩnh Gia - Đ&agrave; Nẵng.</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">Năm 1973-1974, H&ograve;a thượng được cung cử chức vụ Viện trưởng Viện Cao đẳng Phật học Hải Đức - Nha Trang.&nbsp; Ch&iacute;nh trong thời gian n&agrave;y, dưới sự hướng dẫn của Ng&agrave;i, bộ kinh Trung A-h&agrave;m được dịch ra Việt ngữ.</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">Năm 1979, Ho&agrave; thượng Bổn sư l&agrave; đức đệ nhị Tăng Thống GHPGVNTN vi&ecirc;n tịch. Để kế thừa bổn sư, Ng&agrave;i được m&ocirc;n ph&aacute;i cung thỉnh giữ chức vụ Tr&uacute; tr&igrave; Tổ đ&igrave;nh Thiền T&ocirc;n &ndash; Huế.</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">Năm 1981-1984, Ng&agrave;i được mời giảng dạy tại Trường Cao cấp Phật học ở ch&ugrave;a Qu&aacute;n Sứ - H&agrave; Nội.</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">Năm 1982-1988, Ho&agrave; thượng được cung thỉnh l&atilde;nh đạo Phật gi&aacute;o tỉnh Ph&uacute; Kh&aacute;nh li&ecirc;n tiếp trong hai nhiệm k&igrave;.</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">Năm 1984-1988, Ng&agrave;i được mời giữ chức Ph&oacute; hiệu trưởng v&agrave; gi&aacute;o thọ cho Trường Cao cấp Phật học tại Sai G&ograve;n.</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">Năm 1987, Ho&agrave; thượng được thỉnh l&agrave;m khai đạo giới tử tại Đại Giới đ&agrave;n B&aacute;o Quốc - Huế.</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">Năm 1991, Ng&agrave;i được cung cử l&agrave;m Ph&oacute; chủ tịch Hội đồng Phi&ecirc;n dịch Đại tạng kinh Việt Nam, đặc tr&aacute;ch H&aacute;n tạng.</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">Năm 1993, Ng&agrave;i được cung thỉnh l&agrave;m Tuy&ecirc;n Luật sư, Yết-ma cho giới đ&agrave;n Thiện H&ograve;a tại tỉnh B&agrave; Rịa - Vũng T&agrave;u.</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">Năm 1994, Ho&agrave; thượng lại được cử l&agrave;m Yết-ma tại Đại Giới đ&agrave;n B&aacute;o Quốc - Huế.&nbsp; Cũng trong năm n&agrave;y, Ng&agrave;i l&agrave;m trưởng ph&aacute;i đo&agrave;n PGVN sang chi&ecirc;m b&aacute;i Phật t&iacute;ch tại Ấn Độ.</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">Năm 1994-2001, Ng&agrave;i được cung ki&ecirc;m Hiệu trưởng Trường Trung cấp Phật học tỉnh Thừa Thi&ecirc;n &ndash; Huế.</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">Khi Học Viện PGVN tại Huế được th&agrave;nh lập năm 1997, Ng&agrave;i được cung cử l&agrave;m Viện trưởng của Học viện n&agrave;y cho đến nay. Song song với chức vụ Viện trưởng, ng&agrave;i đ&atilde; trực tiếp chỉ đạo x&acirc;y dựng Học viện Phật gi&aacute;o Việt Nam tại Huế để l&agrave;m nơi đ&agrave;o tạo Tăng t&agrave;i cho cả nước.</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">Năm 1999, Ho&agrave; thượng được cung thỉnh l&agrave;m Đ&agrave;n đầu Ho&agrave; thượng Đại Giới đ&agrave;n Tịnh Khiết tại ch&ugrave;a Tường V&acirc;n - Huế.</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">Để tri &acirc;n thầy tổ v&agrave; l&agrave;m rạng rỡ t&ocirc;ng phong, n&ecirc;n năm 2000, với cương vị tr&uacute; tr&igrave;, Ng&agrave;i đ&atilde; đứng ra chủ xướng c&ocirc;ng cuộc đại tr&ugrave;ng tu Tổ đ&igrave;nh Thiền T&ocirc;n-Huế, một trong những ng&ocirc;i tổ đ&igrave;nh lớn của PGVN, trở n&ecirc;n nguy nga, h&ugrave;ng vĩ hơn xưa để xứng đ&aacute;ng với c&ocirc;ng hạnh của Tổ Liễu Qu&aacute;n.&nbsp; Đồng thời với c&ocirc;ng tr&igrave;nh n&agrave;y, H&ograve;a thượng đ&atilde; t&ocirc;n tạo ngoại th&agrave;nh th&aacute;p Tổ với mục đ&iacute;ch l&agrave; bảo vệ nơi di t&iacute;ch lịch sữ v&agrave; văn h&oacute;a kh&ocirc;ng những của PGVN m&agrave; c&ograve;n l&agrave; của dận tộc VN.</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">Năm 2001, Ng&agrave;i chủ tr&igrave; Lễ tốt nghiệp v&agrave; Cấp ph&aacute;t văn bằng Cử nh&acirc;n Phật học cho Tăng Ni sinh Học viện PGVN tại Huế, kho&aacute; I (1997-2001). Cũng trong năm n&agrave;y, Ng&agrave;i đ&atilde; chứng minh Lễ kỷ niệm 50 năm th&agrave;nh lập Gia đ&igrave;nh Phật tử Việt Nam, tổ chức tại Tổ đ&igrave;nh Từ Đ&agrave;m - Huế.</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<strong style="margin: 0px; padding: 0px; outline: 0px; max-width: 654px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">III. C&ocirc;ng tr&igrave;nh dịch thuật v&agrave; bi&ecirc;n soạn:</strong><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">Mặc d&ugrave; bận rộn với nhiều c&ocirc;ng việc của Gi&aacute;o hội, gi&atilde;ng dạy Tăng ni v&agrave; hướng dẫn t&iacute;n đồ tu học, Ng&agrave;i cũng đ&atilde; d&agrave;nh nhiều th&igrave; giờ cho việc phi&ecirc;n dịch, bi&ecirc;n soạn v&agrave; in ấn Kinh, Luật, Luận cho Tăng Ni Phật tử c&oacute; t&agrave;i liệu để tu học. Những c&ocirc;ng tr&igrave;nh dịch thuật v&agrave; bi&ecirc;n soạn gồm:</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<em style="margin: 0px; padding: 0px; outline: 0px; max-width: 654px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">1.&nbsp;&nbsp;&nbsp;&nbsp; Dịch thuật:</em><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">- Kinh Thủ Lăng Nghi&ecirc;m (1940).</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">- Ph&aacute;t Bồ-đề t&acirc;m văn (1952).</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">- Kinh Kiến Ch&aacute;nh (1953).</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">- Kinh 42 chương (1958).</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">- Kinh Trường A-h&agrave;m (lược dịch - 1959).</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">- Kinh Ph&aacute;p C&uacute; (1962).</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">- T&acirc;n Duy thức luận (1962)</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">- Luận Th&agrave;nh duy thức (1995).</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">- Luận Đại tr&iacute; độ (5 tập, 1997-2001).</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">- Trung luận (2001)</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<em style="margin: 0px; padding: 0px; outline: 0px; max-width: 654px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">2. Bi&ecirc;n soạn:</em><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">- Nghi thức tụng niệm (đồng soạn, 1958).</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">- Nghi thức thọ Bồ-t&aacute;t giới tại gia (1958)</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">- Đại cương luận C&acirc;u-x&aacute; (1987).</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">- V&ocirc; ng&atilde; l&agrave; Niết-b&agrave;n (1990).</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">- Toả &aacute;nh Từ quang (1992).</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">- Lối v&agrave;o Nh&acirc;n minh học (1995).</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">- Cương yếu Giới luật (1996).</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">- Ngũ uẩn v&ocirc; ng&atilde; (1997)</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">- Kinh Ph&aacute;p hoa giữa c&aacute;c Kinh điển Đại thừa (1997).</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">- Tr&iacute; đức văn lục (9 tập, 1994-2001)</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">* Nhiều b&agrave;i bi&ecirc;n khảo kh&aacute;c đăng tải ở c&aacute;c Tạp ch&iacute; từ 1940-2001, như:</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">- Tạp ch&iacute; Vi&ecirc;n &acirc;m (1940).</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">- Phật gi&aacute;o Việt Nam (1960).</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">- Li&ecirc;n Hoa (1961).</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">- Gi&aacute;c ngộ, 1982.</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">- Tập văn - Ban Văn ho&aacute; Trung ương (1985).</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<strong style="margin: 0px; padding: 0px; outline: 0px; max-width: 654px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">IV. Nhiếp h&oacute;a đồ ch&uacute;ng.</strong><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">H&ograve;a thượng đ&atilde; truyền thọ Ngũ giới, Thập thiện, v&agrave; Bồ-t&aacute;t giới tại gia cho h&agrave;ng vạn Phật tử khắp 3 miền của đất nước. Ng&agrave;i đ&atilde; độ nhiều đệ tử xuất gia như: T.T. Đạo Dung, Hải Tịnh, Hải Ấn, Phước T&uacute;, Ki&ecirc;n Tuệ,Kiện Định &hellip; Về ph&iacute;a Ni c&oacute; Ni sư Chơn Cần, Hải Li&ecirc;n&hellip;</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">Như dự tri được sự ra đi của m&igrave;nh kh&ocirc;ng c&ograve;n l&acirc;u nữa, Ng&agrave;i đ&atilde; nỗ lực phi&ecirc;n dịch t&aacute;c phẩm m&agrave; Ng&agrave;i đ&atilde; dự định, đ&oacute; l&agrave; bộ Trung luận. Trong v&ograve;ng non một năm, Ng&agrave;i đ&atilde; ho&agrave;n tất sự phi&ecirc;n dịch.&nbsp;&nbsp; Do v&igrave; qu&aacute; cố gắng l&agrave;m việc, n&ecirc;n th&aacute;ng 9 năm 2001, bệnh cũ t&aacute;i ph&aacute;t, mặc d&ugrave; đ&atilde; được h&agrave;ng đệ tử, c&aacute;c gi&aacute;o sư b&aacute;c sĩ v&agrave; y sĩ bệnh viện Trung ương Huế tận t&igrave;nh chăm s&oacute;c chữa trị.&nbsp; Nhưng v&igrave; tuổi cao sức yếu, Ng&agrave;i kh&ocirc;ng vượt qua được căn bệnh hiểm ngh&egrave;o. Ng&agrave;i đ&atilde; an tường xả b&aacute;o th&acirc;n l&uacute;c 16 giờ 30 ng&agrave;y 03 th&aacute;ng 10 năm 2001(nhằm ng&agrave;y 17 th&aacute;ng 8 năm T&acirc;n Tỵ) thọ 81 tuổi đời, 53 tuổi đạo.</span><br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;" />\r\n<span style="font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">Suốt cuộc đời, từ khi xuất gia h&agrave;nh đạo cho đến l&uacute;c vi&ecirc;n tịch, Ng&agrave;i đ&atilde; nỗ lực kh&ocirc;ng ngừng trong c&ocirc;ng việc xiễn dương đạo ph&aacute;p. Đặc biệt, do kinh nghiệm bản th&acirc;n cho thấy: nếu Tăng ni Phật tử kh&ocirc;ng được học tập Phật ph&aacute;p một c&aacute;ch nghi&ecirc;m t&uacute;c, th&igrave; sự tu tập sẽ dể bị lầm lạc.&nbsp; Do vậy, Ng&agrave;i đ&atilde; d&agrave;nh nhiều th&igrave; giờ&nbsp; cho c&ocirc;ng việc gi&aacute;o dục, đ&agrave;o tạo Tăng ni với&nbsp; ước nguyện cung cấp cho đời nhiều vị ch&acirc;n tu, thực học để mang niềm hạnh ph&uacute;c,an lạc đến cho mọi người. V&agrave; ch&iacute;nh việc l&agrave;m đ&oacute; cũng l&agrave; đ&oacute;ng g&oacute;p cho qu&ecirc; hương, d&acirc;n tộc, bởi v&igrave; Phật gi&aacute;o với d&acirc;n tộc Việt l&agrave; bất khả ph&acirc;n. Cuộc đời Ng&agrave;i l&agrave; một tấm gương s&aacute;ng ngời về đạo hạnh v&agrave; sự nghiệp hoằng h&oacute;a cho Tăng ni v&agrave; Phật tử noi theo. Mặc d&ugrave; sắc th&acirc;n của Ng&agrave;i kh&ocirc;ng c&ograve;n nữa nhưng đạo hạnh v&agrave; sự nghiệp hoằng h&oacute;a của Ng&agrave;i sẽ m&atilde;i m&atilde;i l&agrave; ngọn đuốc soi đường cho Tăng Ni Phật tử hậu lai.</span></p>\r\n', 'Hòa Thượng Thích Thiện Siêu', 0);
INSERT INTO `chuathienquang_news` (`id`, `id_category`, `author`, `date`, `content`, `title`, `type`) VALUES
(92, 11, 'BBT Website', '2013-02-02 17:20:23', '<div style="margin: 0px; padding: 0px; outline: 0px; max-width: 654px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: center;"><span style="margin: 0px; padding: 0px; outline: 0px; font-size: 14px;"><span style="margin: 0px; padding: 0px; outline: 0px; font-family: arial, helvetica, sans-serif;"><img alt="" src="http://vuonhoaphatgiao.com/uploads/noidung/images/thich_minh_nguyet-vuonhoaphatgiao_com.JPG" style="margin: 0px; padding: 0px; outline: 0px; border: 0px; border-spacing: 0px; vertical-align: middle; max-width: 654px; width: 380px; height: 613px;" /></span></span></div>\r\n\r\n<div style="margin: 0px; padding: 0px; outline: 0px; max-width: 654px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;"><br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<span style="margin: 0px; padding: 0px; outline: 0px; font-size: 14px;"><span style="margin: 0px; padding: 0px; outline: 0px; font-family: arial, helvetica, sans-serif;">H&ograve;a thượng Th&iacute;ch Minh Nguyệt, thế danh l&agrave; L&yacute; Duy Kim, sinh ng&agrave;y 19.12.1907, trong một gia đ&igrave;nh trung n&ocirc;ng, tại x&atilde; T&acirc;n An, tỉnh Thủ Dầu Một (nay l&agrave; S&ocirc;ng B&eacute;).<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm Kỷ M&ugrave;i (1919), khi vừa đ&uacute;ng 13 tuổi, Ng&agrave;i đến ch&ugrave;a Thi&ecirc;n Thai (tỉnh B&agrave; Rịa) xin xuất gia, tu học với Tổ Huệ Đăng, vốn l&agrave; một Tăng sĩ t&agrave;i ba, một nh&agrave; Nho uy&ecirc;n th&acirc;m c&oacute; tinh thần y&ecirc;u nước.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nTrong thời gian tu học tại đ&acirc;y, ngo&agrave;i những thời kh&oacute;a, Ng&agrave;i được gần gũi, hầu cận b&ecirc;n Tổ Huệ Đăng. Do đ&oacute; Ng&agrave;i đ&atilde; c&oacute; được những cảm nghĩ ban đầu về tinh thần d&acirc;n tộc khi được dự nghe những buổi tọa đ&agrave;m giữa Tổ Huệ Đăng v&agrave; cụ Nguyễn Sinh Sắc (th&acirc;n sinh Chủ tịch Hồ Ch&iacute; Minh). &Yacute; thức c&aacute;ch mạng nảy nở trong l&ograve;ng v&agrave; Ng&agrave;i lu&ocirc;n lu&ocirc;n trăn trở về điều ấy.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm Mậu Dần (1938), &yacute; ch&iacute; n&acirc;ng cao tri kiến bằng c&aacute;ch đem đạo Phật v&agrave;o cuộc đời v&agrave; tinh thần y&ecirc;u nước th&ocirc;i th&uacute;c, Ng&agrave;i xin ph&eacute;p Bổn sư để v&acirc;n du tham học khắp nơi, vừa mở mang tr&iacute; tuệ, vừa th&acirc;m nhập thực tại ho&agrave;n cảnh đất nước ở nhiều kh&iacute;a cạnh. Bước ch&acirc;n Ng&agrave;i đ&atilde; nhiều lần đến ch&ugrave;a Phật Bổn (Cần Thơ), Bửu Long (Mỹ Tho), Long An (S&agrave;i g&ograve;n), Vĩnh Tr&agrave;ng (Tiền Giang), &Ocirc; M&ocirc;i (Vĩnh Long), Thi&ecirc;n An v.v... Đ&oacute; l&agrave; những cuộc thuyết giảng, những bước ch&acirc;n gi&aacute;o h&oacute;a ch&uacute;ng sanh, để lại trong l&ograve;ng Tăng Ni Phật tử mọi nơi h&igrave;nh ảnh tốt đẹp về hạnh nguyện của Ng&agrave;i.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm Canh Th&igrave;n (1940), c&ugrave;ng với cao tr&agrave;o chấn hưng Phật gi&aacute;o, cuộc khởi nghĩa Nam Kỳ đ&atilde; nổ ra. Tr&aacute;ch nhiệm đạo - đời, Ng&agrave;i vẫn h&agrave;ng ng&agrave;y nhắc nhở m&igrave;nh phải chu to&agrave;n. Trong bối cảnh phong tr&agrave;o chấn hưng Phật gi&aacute;o, khi suy l&uacute;c thịnh, do nhiều yếu tố thời cuộc chi phối, đặc biệt l&agrave; b&agrave;n tay ph&aacute; rối từ ph&iacute;a thực d&acirc;n v&agrave; những người theo thực d&acirc;n v&agrave; trước nạn đồng b&agrave;o bị &aacute;p bức b&oacute;c lột, t&agrave;n s&aacute;t, l&ograve;ng y&ecirc;u nước của Ng&agrave;i c&agrave;ng được nung nấu th&ecirc;m. Đến khi hay tin H&ograve;a thượng Tr&iacute; Thiền (ch&ugrave;a Tam Bảo, Rạch Gi&aacute;) bị bắt v&agrave; lưu đ&agrave;y C&ocirc;n Đảo, Ng&agrave;i đ&atilde; chuyển l&ograve;ng y&ecirc;u nước của m&igrave;nh th&agrave;nh h&agrave;nh động t&iacute;ch cực.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm Ất Dậu (1945), C&aacute;ch Mạng Th&aacute;ng T&aacute;m th&agrave;nh c&ocirc;ng, cũng l&agrave; l&uacute;c bề d&agrave;y hoạt động của Ng&agrave;i đ&atilde; đủ chứng minh cho l&ograve;ng y&ecirc;u nước nồng n&agrave;n ấy. Ng&agrave;i hoạt động khi ch&igrave;m khi nổi, ẩn hiện khắp mọi nơi với b&iacute; danh Tam Kh&ocirc;ng. Li&ecirc;n tục sau đ&oacute; Ng&agrave;i được mọi người t&iacute;n nhiệm bầu v&agrave;o c&aacute;c chức vụ như Hội trưởng Hội Phật Gi&aacute;o Cứu Quốc Nam bộ, Hội trưởng Hội Phật Gi&aacute;o Cứu Quốc tỉnh Mỹ Tho, Hội trưởng Hội Phật Gi&aacute;o Cứu Quốc khu S&agrave;i g&ograve;n - Gia Định, Ủy vi&ecirc;n Mặt trận tỉnh Mỹ Tho, Ủy vi&ecirc;n khu S&agrave;i G&ograve;n - Gia Định v&agrave; kỳ bộ Việt Minh Nam bộ.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nL&agrave; một c&ocirc;ng d&acirc;n y&ecirc;u nước nồng n&agrave;n, đồng thời l&agrave; một Tăng sĩ vốn đ&atilde; được trang bị tinh thần bi, tr&iacute;, dũng của chốn thiền m&ocirc;n n&ecirc;n hoạt động chống thực d&acirc;n v&agrave; ph&aacute;t triển Hội Phật Gi&aacute;o Cứu Quốc đều được Ng&agrave;i tiến h&agrave;nh song song ở từng địa b&agrave;n, nhất l&agrave; ch&ugrave;a &Ocirc; M&ocirc;i (Đồng Th&aacute;p Mười) nơi đặt trụ sở của Hội Phật Gi&aacute;o Cứu Quốc Nam Bộ. Bất chấp mọi gian nguy, Ng&agrave;i đ&atilde; đi khắp nơi vận động v&agrave; đấu tranh ngay trong v&ugrave;ng thực d&acirc;n kiểm so&aacute;t. V&agrave; trong nội th&agrave;nh S&agrave;i G&ograve;n cũng kh&ocirc;ng thiếu dấu ch&acirc;n Ng&agrave;i.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm Gi&aacute;p Ngọ (1954) sau Hiệp định Gen&egrave;ve, Ng&agrave;i ở lại miền Nam hoạt động c&ugrave;ng c&aacute;c vị kh&aacute;c. Ng&agrave;i vận động được rất nhiều tự viện l&agrave;m cơ sở gi&uacute;p đỡ che giấu c&aacute;n bộ hoạt động c&aacute;ch mạng ngay trong v&ugrave;ng bị kiểm so&aacute;t. L&uacute;c n&agrave;y Ng&agrave;i l&agrave; vị ti&ecirc;u biểu cho lực lượng Tăng sĩ hoạt động y&ecirc;u nước, v&igrave; thế ch&iacute;nh quyền Ng&ocirc; Đ&igrave;nh Diệm ch&uacute; &yacute; v&agrave; theo d&otilde;i rất gắt gao từng bước đi của Ng&agrave;i. Một mặt, ch&uacute;ng ra sức d&ugrave;ng mọi thủ đoạn h&ograve;ng mua chuộc, l&ocirc;i k&eacute;o Ng&agrave;i, thậm ch&iacute; tạo cơ hội cho Ng&agrave;i l&atilde;nh đạo một tổ chức Phật gi&aacute;o hữu danh v&ocirc; thực, c&oacute; sự bảo hộ, t&agrave;i trợ từ ph&iacute;a ch&iacute;nh quyền... Nhưng Ng&agrave;i đ&atilde; kh&eacute;o l&eacute;o từ khước, vẫn tiếp tục con đường c&aacute;ch mạng đ&atilde; đi. Trong thời gian hoạt động b&iacute; mật, Ng&agrave;i từng chủ trương xuất bản nguyệt san Tinh Tấn (1947) v&agrave; tập san Tổ Quốc (1956).<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm Canh T&yacute; (1960) ng&agrave;y 6-4, một cơ sở bị lộ. Tất cả c&aacute;n bộ cốt c&aacute;n bị bắt, trong đ&oacute; c&oacute; Ng&agrave;i. Nh&acirc;n cơ hội n&agrave;y ch&iacute;nh quyền kh&ocirc;ng ngớt r&ecirc;u rao về tội danh &quot;phản nghịch&quot; của Ng&agrave;i, h&ograve;ng l&agrave;m lung lạc những người y&ecirc;u nước kh&aacute;c. V&agrave; để tăng th&ecirc;m sức mạnh cho mục đ&iacute;ch đ&oacute;, Ng&agrave;i bị đ&agrave;y ra C&ocirc;n Đảo với mức &aacute;n 20 năm t&ugrave; khổ sai.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm Gi&aacute;p Dần (1974), sau khi Hiệp định Paris được k&yacute; kết, Ng&agrave;i được trao trả về v&ugrave;ng giải ph&oacute;ng Lộc Ninh. Như vậy Ng&agrave;i đ&atilde; bị lưu đ&agrave;y khổ sai nơi C&ocirc;n Đảo hơn 15 năm.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nKhi được tự do, Ng&agrave;i li&ecirc;n lạc với c&aacute;c vị gi&aacute;o phẩm l&atilde;nh đạo kh&aacute;c trong c&aacute;c tỉnh ph&iacute;a Nam, để chuẩn bị cho lực lượng Phật gi&aacute;o tham gia nổi dậy c&ugrave;ng c&aacute;c mũi tiến c&ocirc;ng của Chiến dịch Hồ Ch&iacute; Minh.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm Ất M&atilde;o (1975), miền Nam ho&agrave;n to&agrave;n giải ph&oacute;ng, Ng&agrave;i gặp lại c&aacute;c vị l&atilde;nh đạo Phật gi&aacute;o sau bao nhi&ecirc;u năm xa c&aacute;ch. Bằng khả năng v&agrave; uy t&iacute;n của m&igrave;nh trước t&igrave;nh h&igrave;nh mới của đất nước, Ng&agrave;i đảm đương c&aacute;c chức vụ Ph&oacute; chủ tịch Ủy Ban Mặt Trận Th&agrave;nh phố Hồ Ch&iacute; Minh, Ủy vi&ecirc;n đo&agrave;n chủ tịch Ủy ban Trung Ương Mặt trận Tổ Quốc Việt Nam. Ngo&agrave;i ra Ng&agrave;i c&ograve;n l&agrave;m Chủ tịch Ủy ban Li&ecirc;n lạc Phật gi&aacute;o Y&ecirc;u nước Th&agrave;nh phố Hồ Ch&iacute; Minh, với nhiệm vụ li&ecirc;n lạc Tăng Ni Phật tử đo&agrave;n kết, x&acirc;y dựng tổ quốc trong giai đoạn mới.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nĐể hậu thuẫn cho mục đ&iacute;ch n&agrave;y, Ng&agrave;i l&atilde;nh chức Chủ nhiệm B&aacute;o Gi&aacute;c Ngộ, tờ b&aacute;o Phật gi&aacute;o đầu ti&ecirc;n tại c&aacute;c tỉnh ph&iacute;a Nam sau ng&agrave;y đất nước ho&agrave;n to&agrave;n thống nhất. V&agrave; để thắt chặt th&ecirc;m t&igrave;nh hữu nghị, Ng&agrave;i đ&atilde; c&ugrave;ng H&ograve;a thượng Tr&iacute; Thủ, H&ograve;a thượng Minh Ch&acirc;u... tham dự Hội nghị T&ocirc;n gi&aacute;o Thế giới được tổ chức tại M&aacute;tx-Cơ-Va - Li&ecirc;n X&ocirc;, tham quan Phật gi&aacute;o Li&ecirc;n X&ocirc; ở v&ugrave;ng Bu-ri-&aacute;t, Hội nghị Tổ chức Phật tử Ch&acirc;u &Aacute; v&igrave; H&ograve;a B&igrave;nh tại M&ocirc;ng Cổ. Trong dịp n&agrave;y Ng&agrave;i được tặng nhiều hu&acirc;n chương hữu nghị.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nBan tr&ugrave; bị Đại hội Thống Nhất Phật Gi&aacute;o Việt Nam được th&agrave;nh lập, Ng&agrave;i l&agrave; Ph&oacute; Ban vận động ki&ecirc;m Trưởng ban Th&ocirc;ng tin. Năm T&acirc;n Dậu (1981), tại Đại hội Đại biểu Phật gi&aacute;o to&agrave;n quốc tổ chức ở H&agrave; Nội, Gi&aacute;o Hội Phật Gi&aacute;o Việt Nam ra đời với tầm v&oacute;c đại thể xuy&ecirc;n suốt, c&oacute; hiến chương chặt chẽ. Ng&agrave;i được suy cử ng&ocirc;i vị Ph&oacute; Ph&aacute;p Chủ thường trực Hội Đồng Chứng Minh Trung Ương.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nThời gian tiếp theo sau đ&oacute;, Ng&agrave;i về an tr&uacute; v&agrave; l&agrave;m việc tại ch&ugrave;a Long Hoa (quận 10, Th&agrave;nh phố Hồ Ch&iacute; Minh). Tại đ&acirc;y, ng&agrave;y 28 th&aacute;ng chạp năm Gi&aacute;p T&yacute; (18/1/1985) Ng&agrave;i đ&atilde; vi&ecirc;n tịch, thọ 77 tuổi đời, v&agrave; 57 năm h&agrave;nh đạo.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nKim quan Ng&agrave;i được đưa về nhập th&aacute;p nơi ch&ugrave;a Tổ Thi&ecirc;n Thai (tỉnh B&agrave; Rịa) theo ph&oacute; ch&uacute;c &quot;Uống nước nhớ nguồn, l&aacute; rụng về cội&quot;.</span></span></div>\r\n', 'Hòa thượng Thích Minh Nguyệt', 0),
(93, 11, 'BBT Website', '2013-02-02 17:21:14', '<div style="margin: 0px; padding: 0px; outline: 0px; max-width: 654px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: center;"><span style="margin: 0px; padding: 0px; outline: 0px; font-size: 14px;"><span style="margin: 0px; padding: 0px; outline: 0px; font-family: arial, helvetica, sans-serif;"><img alt="" src="http://vuonhoaphatgiao.com/uploads/noidung/images/thich_the_long_vuonhoaphatgiao_com.jpg" style="margin: 0px; padding: 0px; outline: 0px; border: 0px; border-spacing: 0px; vertical-align: middle; max-width: 654px; width: 823px; height: 1080px;" /></span></span></div>\r\n\r\n<div style="margin: 0px; padding: 0px; outline: 0px; max-width: 654px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;"><br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<span style="margin: 0px; padding: 0px; outline: 0px; font-size: 14px;"><span style="margin: 0px; padding: 0px; outline: 0px; font-family: arial, helvetica, sans-serif;">H&ograve;a thượng họ Phạm, ph&aacute;p danh Thế Long, sinh năm Kỷ Dậu (1909) tại x&atilde; Hải Anh, huyện Hải Hậu, tỉnh Nam H&agrave;, trong một gia đ&igrave;nh Nho phong thanh bạch c&oacute; truyền thống th&acirc;m t&iacute;n Phật ph&aacute;p. Ng&agrave;i l&agrave; người con &uacute;t nhưng cũng l&agrave; người con trai duy nhất của cụ Phạm Văn Ngoan tức Ng&ocirc;n v&agrave; cụ b&agrave; Trần Thị Thanh, ph&aacute;p danh Diệu Th&aacute;i. Sau khi sinh ra Ng&agrave;i, cụ b&agrave; đồng &yacute; để cụ &ocirc;ng xuất gia. Cụ Phạm Văn Ngoan xuất gia ở ch&ugrave;a Nội, thị trấn Cổ Lễ với ph&aacute;p danh Th&iacute;ch Thanh C&aacute;t v&agrave; cả hai người chị g&aacute;i của Ng&agrave;i cũng xuất gia.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1915, nh&acirc;n chuyến về thăm nh&agrave;, sư cụ Thanh C&aacute;t đ&atilde; khuy&ecirc;n gia đ&igrave;nh cho Ng&agrave;i xuất gia. Ng&agrave;i được yết kiến Sư tổ Quang Tuy&ecirc;n (1) l&uacute;c đ&oacute; đang trụ tr&igrave; ch&ugrave;a Thủy Nhai, x&atilde; Xu&acirc;n Thủy, huyện Xu&acirc;n Thủy - Nam H&agrave;. Ng&agrave;i xuất gia thế ph&aacute;t tại đ&acirc;y, sau đ&oacute; được sang ch&ugrave;a Thượng Ph&uacute;c tham học.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1929, Ng&agrave;i được Tam sư Thất chứng trao truyền Cụ T&uacute;c giới, nối ph&aacute;p đời thứ 46 d&ograve;ng T&agrave;o Động, sơn m&ocirc;n Ph&uacute; Ninh - Nam Định. Sau đ&oacute;, Ng&agrave;i được đến tham học tại Tổ đ&igrave;nh Li&ecirc;n Ph&aacute;i - H&agrave; Nội.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1934, Tổ Quang Tuy&ecirc;n vi&ecirc;n tịch, Ng&agrave;i trở về Cổ Lễ chịu tang v&agrave; tiếp tục c&ocirc;ng việc kiến tạo ch&ugrave;a Cổ Lễ m&agrave; nghiệp sư đang l&agrave;m. Ng&agrave;i thỉnh Sư tổ Thạch Cầu về ch&ugrave;a Cổ Lễ l&agrave;m thầy y chỉ, ho&agrave;n th&agrave;nh c&acirc;y Cửu Phẩm, đ&uacute;c được Đại Hồng Chung nặng 9 tấn, tạo dựng nh&agrave; hội qu&aacute;n v&agrave; ho&agrave;n th&agrave;nh c&aacute;c c&ocirc;ng tr&igrave;nh kiến tr&uacute;c hai b&ecirc;n của t&ograve;a ch&iacute;nh điện. Ngo&agrave;i ra, Ng&agrave;i cũng t&aacute;i tạo ch&ugrave;a Thủy Nhai, Thượng Ph&uacute;c v.v... H&agrave;ng năm, Ng&agrave;i tổ chức lễ hội mừng đản sinh Thiền sư Kh&ocirc;ng Lộ từ ng&agrave;y 11 đến ng&agrave;y 14 th&aacute;ng 9 &acirc;m lịch, c&oacute; h&agrave;ng vạn lượt người đến tham dự cầu nguyện, chi&ecirc;m b&aacute;i.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nSau khi ch&ugrave;a Cổ Lễ được t&ocirc;n tạo ho&agrave;n chỉnh, Ng&agrave;i thỉnh c&aacute;c bậc t&ocirc;n t&uacute;c H&ograve;a thượng v&agrave; Tăng Ni về kết t&uacute;c an cư trong 3 th&aacute;ng hạ. Cứ duy tr&igrave; đều đặn như vậy trong suốt hơn nửa thế kỷ. Năm n&agrave;o ch&ugrave;a Cổ Lễ cũng l&agrave; trường hạ của Tăng Ni ph&iacute;a Nam tỉnh Nam H&agrave;. Ng&agrave;i tận t&acirc;m tr&ocirc;ng nom c&uacute;ng dường mọi vật dụng cần thiết để chư Tăng Ni an t&acirc;m tu học, với cương vị l&agrave; trụ tr&igrave;, rồi đường chủ, chủ giảng trong c&aacute;c kh&oacute;a hạ. C&oacute; năm chư Tăng an cư gần 150 vị. Ch&ugrave;a Cổ Lễ cũng l&agrave; nơi tổ chức nhiều giới đ&agrave;n của Phật gi&aacute;o tỉnh Nam H&agrave;. Ng&agrave;i được mời l&agrave;m H&ograve;a thượng Đ&agrave;n đầu, H&ograve;a thượng A X&agrave; L&ecirc; ở c&aacute;c giới đ&agrave;n ch&ugrave;a Cổ Lễ v&agrave; giới đ&agrave;n Th&aacute;nh &Acirc;n (ch&ugrave;a Cả - Nam Định)...<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nVề mặt Phật sự, Ng&agrave;i l&agrave; một trong những bậc gi&aacute;o phẩm cao cấp của Phật gi&aacute;o Việt Nam v&agrave; của Phật gi&aacute;o tỉnh Nam H&agrave;.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n- Trong suốt thời kỳ 1958 - 1981, Hội Phật Gi&aacute;o Thống Nhất Việt Nam (miền Bắc) hoạt động, Ng&agrave;i được cử giữ chức vụ Ph&oacute; hội trưởng, Hội trưởng Phật gi&aacute;o tỉnh Nam H&agrave;.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n- Th&aacute;ng 10 năm 1964, Đại hội kỳ III Trung ương Hội Phật Gi&aacute;o Thống Nhất Việt Nam, Ng&agrave;i được suy cử v&agrave;o Ban trị sự Trung ương.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n- Năm 1971, tại Đại hội kỳ IV Trung ương Hội Phật Gi&aacute;o Thống Nhất Việt Nam, Ng&agrave;i được suy cử chức Ph&oacute; Hội trưởng ki&ecirc;m Tổng thư k&yacute; Ban trị sự Trung ương.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1973, Ng&agrave;i c&ugrave;ng Ban trị sự Trung ương th&agrave;nh lập trường &quot;Tu học Phật ph&aacute;p Trung ương&quot; tại ch&ugrave;a Qu&aacute;n Sứ. Trải qua hai kh&oacute;a đ&agrave;o tạo, đến năm 1981 trường trực thuộc hệ thống Gi&aacute;o dục của Trung ương Gi&aacute;o Hội Phật Gi&aacute;o Việt Nam v&agrave; đổi t&ecirc;n &quot;Trường Cao Cấp Phật Học Việt Nam cơ sở I&quot;.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n- Sau năm 1975, Ng&agrave;i c&ugrave;ng qu&yacute; H&ograve;a thượng miền Trung, miền Nam th&agrave;nh lập Ban vận động Thống nhất Phật gi&aacute;o.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n- Th&aacute;ng 11 năm 1981,Đại hội đại biểu thống nhất Phật gi&aacute;o Việt Nam lập n&ecirc;n &quot;Gi&aacute;o Hội Phật Gi&aacute;o Việt Nam&quot;, Ng&agrave;i được thỉnh v&agrave;o &quot;Hội đồng Chứng minh&quot; v&agrave; l&agrave; Ph&oacute; chủ tịch Thường trực Hội Đồng Trị Sự Gi&aacute;o Hội Phật Gi&aacute;o Việt Nam.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nVề mặt tham gia c&ocirc;ng t&aacute;c x&atilde; hội, quan niệm &quot;Phật ph&aacute;p bất ly thế gian ph&aacute;p&quot; n&ecirc;n việc đạo việc đời Ng&agrave;i đều xả th&acirc;n phụng sự.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n- Năm 1945, Ng&agrave;i l&agrave; Ph&oacute; Chủ tịch Tỉnh bộ Việt Minh tỉnh Nam Định.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n- Năm 1947, l&agrave; Ph&oacute; chủ tịch Phật gi&aacute;o cứu quốc Nam Định.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n- Năm 1951, l&agrave; Ph&oacute; chủ tịch Mặt trận Li&ecirc;n Việt tỉnh Nam Định, Ủy vi&ecirc;n mặt trận Li&ecirc;n Việt to&agrave;n quốc.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n- Từ năm 1976 - 1980, l&agrave; Ph&oacute; chủ tịch Ủy Ban Mặt Trận Tổ Quốc tỉnh H&agrave; Nam Ninh v&agrave; l&agrave; đại biểu Hội Đồng Nh&acirc;n D&acirc;n Tỉnh.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n- Ng&agrave;i được nh&acirc;n d&acirc;n t&iacute;n nhiệm bầu l&agrave;m Đại biểu Quốc hội kh&oacute;a VII v&agrave; được Quốc hội cử giữ chức Ph&oacute; chủ tịch Quốc hội nước Cộng H&ograve;a X&atilde; Hội Chủ Nghĩa Việt Nam.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n- Ng&agrave;i c&ograve;n l&agrave; th&agrave;nh vi&ecirc;n t&iacute;ch cực của Hội &quot;Phật Gi&aacute;o Ch&acirc;u &Aacute; V&igrave; H&ograve;a B&igrave;nh&quot; (ABCP) trong nhiều kh&oacute;a, v&agrave; l&agrave; Ph&oacute; chủ tịch của hội. Ng&agrave;i đ&atilde; tham gia nhiều tổ chức Phật gi&aacute;o quốc tế tại hầu hết c&aacute;c nước Ch&acirc;u &Aacute;, Đ&ocirc;ng &Acirc;u, tham dự c&aacute;c hội nghị Phật gi&aacute;o thế giới v&agrave; đ&atilde; được Nh&agrave; nước tặng Hu&acirc;n chương Độc Lập hạng hai, Hu&acirc;n chương kh&aacute;ng chiến hạng ba...<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n- Ng&agrave;y 23-3-1985, Ng&agrave;i vi&ecirc;n tịch tại Tổ đ&igrave;nh Cổ Lễ, hưởng thọ 76 tuổi. Lễ tang được tổ chức trọng thể với nghi thức lễ tang Nh&agrave; nước. H&ograve;a thượng Ph&aacute;p chủ Th&iacute;ch Đức Nhuận đ&atilde; ban Th&aacute;p hiệu của Ng&agrave;i l&agrave; An Lạc th&aacute;p, v&agrave; t&ecirc;n hiệu Tế Mỹ. Cuộc đời h&agrave;nh đạo của Ng&agrave;i trải qua 56 m&ugrave;a hạ.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nCh&uacute; th&iacute;ch :<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n(1) Sư tổ Quang Tuy&ecirc;n l&agrave; b&aacute;c tại gia của Ng&agrave;i.</span></span></div>\r\n', 'Hòa Thượng Thích Thế Long (1909-1985)', 0),
(94, 11, 'BBT Website', '2013-02-02 17:21:55', '<div style="margin: 0px; padding: 0px; outline: 0px; max-width: 654px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: center;"><span style="margin: 0px; padding: 0px; outline: 0px; font-size: 14px;"><span style="margin: 0px; padding: 0px; outline: 0px; font-family: arial, helvetica, sans-serif;"><img alt="" src="http://vuonhoaphatgiao.com/uploads/noidung/images/gioi-nghiem-vuonhoaphatgiao_com.jpg" style="margin: 0px; padding: 0px; outline: 0px; border: 0px; border-spacing: 0px; vertical-align: middle; max-width: 654px; width: 250px; height: 353px;" /></span></span></div>\r\n\r\n<div style="margin: 0px; padding: 0px; outline: 0px; max-width: 654px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;"><br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<span style="margin: 0px; padding: 0px; outline: 0px; font-size: 14px;"><span style="margin: 0px; padding: 0px; outline: 0px; font-family: arial, helvetica, sans-serif;">H&ograve;a Thượng Giới Nghi&ecirc;m thế danh l&agrave; Nguyễn Đ&igrave;nh Trấn, sinh ng&agrave;y 5 th&aacute;ng 5 năm 1921, tại l&agrave;ng Gia L&ecirc; Thượng, x&atilde; Thủy Phương, huyện Hương Ph&uacute;, tỉnh Thừa Thi&ecirc;n - Huế. Ng&agrave;i xuất th&acirc;n trong một gia đ&igrave;nh gồm ba anh em, c&oacute; tinh thần y&ecirc;u nước v&agrave; c&oacute; truyền thống đạo đức l&acirc;u đời. &Ocirc;ng nội l&agrave; bậc xuất gia, cha l&agrave; H&ograve;a thượng Quang Diệu, ch&uacute; cũng xuất gia, b&aacute;c l&agrave; H&ograve;a thượng Phước Duy&ecirc;n.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 9 tuổi (1930), Ng&agrave;i xuất gia tại ch&ugrave;a B&atilde;ng L&atilde;ng - Huế thuộc hệ ph&aacute;i Bắc truyền. Sau đ&oacute; Ng&agrave;i được thọ giới Sa Di.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nĐến năm 19 tuổi (1940 - Canh Th&igrave;n), Ng&agrave;i thọ giới Tỳ Kheo tại Đại giới đ&agrave;n ch&ugrave;a Phổ Đ&agrave; - Đ&agrave; Nẳng.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nTrong thời gian n&agrave;y, Phật gi&aacute;o Nam T&ocirc;ng bắt đầu du nhập v&agrave;o Việt Nam. Nhờ vậy Ng&agrave;i c&oacute; dịp nghi&ecirc;n cứu gi&aacute;o l&yacute; Nguy&ecirc;n Thủy.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1944, Ng&agrave;i được sang du học tại Phn&ocirc;mp&ecirc;nh- Campuchia. Năm 1947 H&ograve;a Thượng Niếp T&iacute;ch truyền giới Tỳ Kheo theo truyền thống Nam T&ocirc;ng - Ther&agrave;vada cho Ng&agrave;i. Ng&agrave;i được học đạo với Đức vua s&atilde;i Campuchia, H&ograve;a Thượng Chuon Nath.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nSau đ&oacute; Ng&agrave;i sang du học ở Th&aacute;i Lan v&agrave; Miến Điện. Tại Miến Điện, Ng&agrave;i được học thiền tứ niệm xứ với Ng&agrave;i thiền sư Mah&agrave;si Say&agrave;daw.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nSau hơn mười năm du học nước ngo&agrave;i, Ng&agrave;i trở về Việt Nam để hoằng khai Phật Gi&aacute;o Nguy&ecirc;n Thủy.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1957, Ng&agrave;i c&ugrave;ng c&aacute;c vị Trưởng l&atilde;o H&ograve;a thượng Hộ T&ocirc;ng, Bửu Chơn, Thiện Luật, Tối Thắng, Gi&aacute;c Quang, Kim Quang, ph&aacute;p sư Th&ocirc;ng Kham vận động th&agrave;nh lập Gi&aacute;o Hội Tăng Gi&agrave; Nguy&ecirc;n Thủy Việt Nam.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nKhi Gi&aacute;o Hội Tăng Gi&agrave; Nguy&ecirc;n Thủy Việt Nam ch&iacute;nh thức th&agrave;nh lập, Ng&agrave;i được đề cử giữ chức vụ Ph&oacute; Tổng thư k&yacute;, nhưng v&igrave; Ng&agrave;i c&oacute; khuynh hướng c&aacute;ch mạng n&ecirc;n ch&iacute;nh quyền Ng&ocirc; Đ&igrave;nh Diệm kh&ocirc;ng duyệt cho Ng&agrave;i giữ chức vụ trong Gi&aacute;o Hội. Khi ch&iacute;nh quyền Ng&ocirc; Đ&igrave;nh Diệm sụp đổ, Ng&agrave;i được bầu l&agrave;m Tăng Thống trong bốn nhiệm kỳ từ năm 1964-1971.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1963 trong phong tr&agrave;o đấu tranh chống kỳ thị t&ocirc;n gi&aacute;o của chế độ Ng&ocirc; Đ&igrave;nh Diệm, Ng&agrave;i bị bắt tại Sơn Tr&agrave;. Năm 1966, Ng&agrave;i tham gia đấu tranh chống chế độ lao t&ugrave; tại c&aacute;c nh&agrave; giam Mỹ-Ngụy - Đ&agrave; Nẵng v&agrave; G&agrave;nh R&aacute;ng - Qui Nhơn.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1975, Ng&agrave;i tham gia Ban Li&ecirc;n lạc Phật gi&aacute;o Y&ecirc;u nước Th&agrave;nh phố HCM v&agrave; giữ chức vụ Ph&oacute; Chủ Tịch.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1979, Ng&agrave;i được t&aacute;i cử chức Tăng Thống Gi&aacute;o Hội Tăng Gi&agrave; Nguy&ecirc;n Thủy Việt Nam. Cũng trong năm 1979, Ng&agrave;i được thỉnh tham gia gi&aacute;o đo&agrave;n chư Tăng Phật Gi&aacute;o Nguy&ecirc;n Thủy Việt Nam đi Campuchia để l&agrave;m thầy tế độ cho c&aacute;c sư s&atilde;i đ&atilde; bị Khmer đỏ bắt ho&agrave;n tục. Từ đ&oacute; Phật Gi&aacute;o Campuchia đ&atilde; được phục hồi vững mạnh v&agrave; thắt chặt t&igrave;nh đo&agrave;n kết Việt Nam-Campuchia.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1980, Ban vận động thống nhất Phật Gi&aacute;o Việt Nam được th&agrave;nh lập, Ng&agrave;i tham gia v&agrave;o Ban vận động với tư c&aacute;ch Ph&oacute; Trưởng ban.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1981, Gi&aacute;o Hội Phật Gi&aacute;o Việt Nam ra đời, Ng&agrave;i được thỉnh v&agrave;o Hội đồng Chứng minh v&agrave; được đề cử l&agrave;m Ph&oacute; Chủ Tịch Hội Đồng Trị Sự Gi&aacute;o Hội Phật Gi&aacute;o Việt Nam.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nTrong cuộc đời hoằng h&oacute;a h&agrave;nh đạo, Ng&agrave;i đ&atilde; tham dự nhiều hội nghị quốc tế về Phật gi&aacute;o, đặc biệt l&agrave; tham dự hội nghị kết tập Tam Tạng lần thứ 6 tại Rangoon - Miến Điện.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n- Về sự nghiệp gi&aacute;o dục đ&agrave;o tạo Tăng lữ, Ng&agrave;i đ&atilde; mở một Phật học viện sơ cấp tại ch&ugrave;a Tam Bảo - Đ&agrave; Nẵng v&agrave; một Phật học viện Trung đẳng tại ch&ugrave;a Phật Bảo tại quận T&acirc;n B&igrave;nh - S&agrave;igon, nơi Ng&agrave;i l&agrave;m viện chủ v&agrave; tiến sĩ Thiện Giới l&agrave;m Gi&aacute;m Đốc. Phật học viện đ&agrave;o tạo nhiều Tăng t&agrave;i cho hệ ph&aacute;i Nam T&ocirc;ng.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nSau khi học thiền tứ niệm xứ ở Miến Điện, trở về nước, Ng&agrave;i mở trung t&acirc;m thiền tứ niệm xứ tại Tam Bảo Tự n&uacute;i Lớn-Vũng T&agrave;u, đ&agrave;o tạo nhiều h&agrave;nh giả thiền sinh th&acirc;u đạt kết quả ph&aacute;p h&agrave;nh tốt đẹp. Sau 1975, Ng&agrave;i tiếp tục mở nhiều kh&oacute;a thiền tại ch&ugrave;a Phật Bảo nơi Ng&agrave;i trụ tr&igrave;.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n- Về sự nghiệp s&aacute;ng t&aacute;c dịch thuật, Ng&agrave;i đ&atilde; dịch v&agrave; cho in c&aacute;c t&aacute;c phẩm sau đ&acirc;y:<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n1/. Thiền Tứ Niệm Xứ.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n2/. Mi Ti&ecirc;n vấn đ&aacute;p 1,2,3.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n3/. Lịch sử Phật Tổ Gotama.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n4/. Giải về kiếp.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n5/. Dạ Xoa hỏi Phật.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n6/. Đế Th&iacute;ch vấn đạo.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n7/. Ph&aacute;p số giảng giải.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n8/. Luật tạng P&agrave;li.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNgo&agrave;i ra, c&ograve;n một số kinh Ng&agrave;i đang dịch dở dang, hoặc c&ograve;n trong bản thảo hiện lưu giữ tại ch&ugrave;a Phật Bảo.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n- Về sự nghiệp x&acirc;y dựng ch&ugrave;a chiền, từ miền Nam ra miền Trung, l&ecirc;n Cao Nguy&ecirc;n, ở đ&acirc;u cũng c&oacute; dấu ch&acirc;n Ng&agrave;i bước đến để hoằng khai đạo mầu.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNg&agrave;i đ&atilde; kiến lập c&aacute;c tự viện như ch&ugrave;a Định Quang (Ở l&agrave;ng Giạ L&ecirc;), Tăng Quang (Huế), Tam Bảo (Đ&agrave; Nẵng), Nam Quang (Hội An), Tăng Bảo (Quảng Ng&atilde;i), Bửu Sơn, Ph&aacute;p Qu&acirc;n (Đ&agrave; Lạt), Ph&aacute;p Bảo (Mỹ Tho), B&igrave;nh Long (Phan Thiết), Phước Sơn (Đồng Nai), Tam Bảo Thiền Viện (B&agrave; Rịa - Vũng T&agrave;u) v&agrave; Phật Bảo Th&agrave;nh phố Hồ Ch&iacute; Minh).<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nH&ograve;a Thượng Giới Nghi&ecirc;m c&oacute; số đồ ch&uacute;ng xuất gia đ&ocirc;ng đảo nhất trong Phật Gi&aacute;o Nam T&ocirc;ng Việt Nam, một số hiện c&ograve;n đang du học nước ngo&agrave;i.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nHiện nay, những đệ tử trưởng th&agrave;nh của Ng&agrave;i l&agrave; H&ograve;a Thượng Hộ Nhẫn ở Huế, H&ograve;a thượng Ph&aacute;p Tri, Thượng Tọa Vi&ecirc;n Minh, T&acirc;m Hỷ, Hộ Chơn v.v...<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1984, Ng&agrave;i l&acirc;m trọng bệnh, sức khỏe k&eacute;m dần v&agrave; kh&ocirc;ng hồi phục lại được. Ng&agrave;i đ&atilde; th&acirc;u thần thị tịch l&uacute;c 10 giờ 10 ph&uacute;t ng&agrave;y 13 th&aacute;ng 7 năm Gi&aacute;p T&yacute; tức 09/8/1984 tại ch&ugrave;a Phật Bảo, hưởng thọ 63 tuổi, với hơn 50 năm dốc l&ograve;ng hoằng đạo.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nHằng năm, v&agrave;o m&ugrave;a Vu Lan B&aacute;o Hiếu (13/7 &Acirc;.L) to&agrave;n thể tăng t&iacute;n đồ Phật Gi&aacute;o Nam T&ocirc;ng v&agrave; Gi&aacute;o Hội Phật Gi&aacute;o Việt Nam l&agrave;m lễ tưởng niệm H&ograve;a thượng, một thiền sư, một bậc Thầy từ h&ograve;a khả k&iacute;nh, đức độ cao th&acirc;m, một vị gi&aacute;o phẩm t&ocirc;n t&uacute;c của Phật Gi&aacute;o Việt Nam.</span></span></div>\r\n', 'Hòa Thượng Giới Nghiêm (1921-1984)', 0);
INSERT INTO `chuathienquang_news` (`id`, `id_category`, `author`, `date`, `content`, `title`, `type`) VALUES
(95, 11, 'BBT Website', '2013-02-02 17:22:30', '<div style="margin: 0px; padding: 0px; outline: 0px; max-width: 654px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: center;"><span style="margin: 0px; padding: 0px; outline: 0px; font-size: 14px;"><span style="margin: 0px; padding: 0px; outline: 0px; font-family: arial, helvetica, sans-serif;"><img alt="" src="http://vuonhoaphatgiao.com/uploads/noidung/images/thich-hanh-tru-vuonhoaphatgiao_com.jpg" style="margin: 0px; padding: 0px; outline: 0px; border: 0px; border-spacing: 0px; vertical-align: middle; max-width: 654px; width: 240px; height: 315px;" /></span></span></div>\r\n\r\n<div style="margin: 0px; padding: 0px; outline: 0px; max-width: 654px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;"><br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<span style="margin: 0px; padding: 0px; outline: 0px; font-size: 14px;"><span style="margin: 0px; padding: 0px; outline: 0px; font-family: arial, helvetica, sans-serif;">H&ograve;a thượng ph&aacute;p danh Thị An, ph&aacute;p tự H&agrave;nh Trụ, ph&aacute;p hiệu Phước B&igrave;nh, thuộc d&ograve;ng L&acirc;m Tế Ch&uacute;c Th&aacute;nh đời thứ 42. Ng&agrave;i thế danh l&agrave; L&ecirc; An, sinh năm 1904 trong một gia đ&igrave;nh trung n&ocirc;ng tại l&agrave;ng Phương Lưu, huyện Đồng Xu&acirc;n, tỉnh Ph&uacute; Y&ecirc;n. Th&acirc;n phụ l&agrave; cụ L&ecirc; Uyển, th&acirc;n mẫu l&agrave; b&agrave; Nguyễn Thị Sử. &Ocirc;ng B&agrave; c&oacute; bốn người con, cả ba người con trai đều xuất gia đầu Phật.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNg&agrave;i xuất gia năm 12 tuổi ở ch&ugrave;a l&agrave;ng, đến năm 19 tuổi, được H&ograve;a thượng Giải Tường ch&ugrave;a Phước Sơn thế độ l&agrave;m đệ tử v&agrave; tu học tại đ&acirc;y. Năm 22 tuổi Ng&agrave;i thọ Cụ T&uacute;c giới v&agrave; giữ chức thư k&yacute; trong ch&ugrave;a.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nVới phong c&aacute;ch đĩnh đạc v&agrave; say m&ecirc; học hỏi, Ng&agrave;i đ&atilde; trau giồi kinh luật nội điển c&ugrave;ng Quốc văn ở hầu hết c&aacute;c trường hạ, kh&oacute;a học được tổ chức bấy giờ ở khắp c&aacute;c đạo tr&agrave;ng ch&ugrave;a Thi&ecirc;n Phước - Thủ Đức năm 1934; đạo tr&agrave;ng Tổ đ&igrave;nh B&aacute;t Nh&atilde; - Ph&uacute; Y&ecirc;n năm 1935...<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nGặp l&uacute;c phong tr&agrave;o chấn hưng Phật gi&aacute;o đang ph&aacute;t triển, Ng&agrave;i v&agrave;o Nam tham học ở Th&iacute;ch Học Đường Lưỡng Xuy&ecirc;n do c&aacute;c H&ograve;a thượng Kh&aacute;nh H&ograve;a, Kh&aacute;nh Anh, Huệ Quang l&atilde;nh đạo.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1936, Ng&agrave;i được tiến cử l&agrave;m Gi&aacute;o Thọ sau kh&oacute;a trường Hương do Hội Lưỡng Xuy&ecirc;n Phật Học tổ chức tại ch&ugrave;a Long Phước-Vĩnh Long. Sau đ&oacute;, Ng&agrave;i được cử ra Huế học tại Phật học đường ch&ugrave;a Tường V&acirc;n, rồi đến ch&ugrave;a T&acirc;y Thi&ecirc;n với học Tăng cả ba miền tham dự, do Quốc Sư Phước Huệ l&agrave;m ph&aacute;p chủ giảng dạy.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1940, v&igrave; bệnh trầm trọng, Ng&agrave;i phải trở v&agrave;o Nam điều trị, v&agrave; ở lại giảng dạy tại Ni trường ch&ugrave;a Kim Sơn - Ph&uacute; Nhuận.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1942, Ng&agrave;i được Tổ Kh&aacute;nh H&ograve;a bổ về S&oacute;c Trăng l&agrave;m Gi&aacute;o Thọ giảng dạy ở ch&ugrave;a Hiệp Ch&acirc;u, chi hội Kế S&aacute;ch của Hội Lưỡng Xuy&ecirc;n Phật Học v&agrave; ch&ugrave;a Vi&ecirc;n Gi&aacute;c tại Vĩnh Long.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1945, Ng&agrave;i được H&ograve;a thượng Vạn An đưa về l&agrave;m Gi&aacute;o thọ giảng dạy tại ch&ugrave;a Hội Phước, Nha M&acirc;n tỉnh Sa Đ&eacute;c. Trong thời gian ấy, Ng&agrave;i l&agrave;m Đệ nhất Yết ma Đại giới đ&agrave;n ch&ugrave;a An Phước, Ch&acirc;u Đốc. Sau đ&oacute;, Ng&agrave;i về ch&ugrave;a Long An ở Sa Đ&eacute;c, tại đ&acirc;y đ&atilde; kết nghĩa ph&aacute;p đạo huynh đệ c&ugrave;ng ba vị Kh&aacute;nh Phước, Thới An, Thiện Tường, v&agrave; mở Phật học đường. Chư Tăng khắp lục tỉnh hội tụ về tu học rất đ&ocirc;ng. Xuất th&acirc;n từ đ&acirc;y c&oacute; c&aacute;c H&ograve;a thượng Từ Nhơn, H&ograve;a thượng Huệ Hưng...<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1946, Ng&agrave;i với ba vị sư đệ kết nghĩa l&ecirc;n S&agrave;i G&ograve;n hợp nhau lập ch&ugrave;a Tăng Gi&agrave;, hiện nay l&agrave; ch&ugrave;a Kim Li&ecirc;n, để tiếp độ ch&uacute;ng Tăng tựu về tu học. Đ&acirc;y l&agrave; Phật học đường đầu ti&ecirc;n ở đất S&agrave;i G&ograve;n trong phong tr&agrave;o chấn hưng Phật gi&aacute;o, mở đường cho c&aacute;c Phật học viện sau n&agrave;y ph&aacute;t triển.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1947, Ng&agrave;i lại c&ugrave;ng ba vị sư đệ dựng n&ecirc;n ng&ocirc;i gi&agrave; lam thứ hai l&agrave; ch&ugrave;a Gi&aacute;c Nguy&ecirc;n để chuyển chư Tăng về đ&acirc;y tu học, ch&ugrave;a Tăng Gi&agrave; biến th&agrave;nh trường Phật học d&agrave;nh cho Ni ch&uacute;ng. Hai đạo tr&agrave;ng n&agrave;y ng&agrave;y th&ecirc;m vang tiếng v&agrave; Tăng Ni khắp nơi về học rất đ&ocirc;ng, g&oacute;p phần lớn trong c&ocirc;ng cuộc chấn hưng Phật gi&aacute;o tại đất S&agrave;i G&ograve;n bấy giờ. Ng&agrave;i đảm nhiệm Gi&aacute;m đốc Phật học đường Gi&aacute;c Nguy&ecirc;n v&agrave; H&oacute;a chủ Phật học Ni trường Tăng Gi&agrave;.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1948, Ng&agrave;i mở Đại giới đ&agrave;n tại Phật học đường Gi&aacute;c Nguy&ecirc;n để truyền trao giới ph&aacute;p cho Tăng Ni thọ tr&igrave; tu học. Sau Ng&agrave;i được đề cử l&agrave;m Trưởng ban Nghi Lễ của Gi&aacute;o Hội Tăng Gi&agrave; Nam Việt v&agrave;o năm 1951, l&agrave;m Chứng minh Đạo sư Hội Phật Học Nam Việt tại ch&ugrave;a X&aacute; Lợi S&agrave;i G&ograve;n cho đến cuối đời (1956 - 1984), v&agrave; l&agrave;m Trưởng đo&agrave;n Phật gi&aacute;o Việt Nam tham dự Hội nghị Phật gi&aacute;o Thế giới kỳ 4 tại Nam Vang năm 1957.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1963, Ng&agrave;i khai mở Phật học đường Ch&aacute;nh Gi&aacute;c tại ch&ugrave;a Ch&aacute;nh Gi&aacute;c-Gia Định do Ng&agrave;i l&agrave;m Gi&aacute;m đốc ki&ecirc;m trụ tr&igrave;. Sau đ&oacute; Ng&agrave;i về trụ tr&igrave; th&ecirc;m ch&ugrave;a Đ&ocirc;ng Hưng ở Thủ Thi&ecirc;m v&agrave; chọn nơi n&agrave;y l&agrave;m chốn tĩnh tu nhập thất v&agrave;o những m&ugrave;a An Cư Kiết Hạ.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1967 - 1969, Ng&agrave;i l&agrave;m Giới Sư c&aacute;c Đại giới đ&agrave;n Hải Đức ở Phật học viện Hải Đức-Nha Trang v&agrave; Quảng Đức ở Phật học viện Huệ Nghi&ecirc;m-S&agrave;i g&ograve;n.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1971, Ng&agrave;i được suy cử l&agrave;m Ph&oacute; Chủ Tịch Hội Đồng Gi&aacute;m Luật Viện Tăng Thống Gi&aacute;o Hội Phật Gi&aacute;o Việt Nam Thống Nhất.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1975 - 1977 - 1980, li&ecirc;n tiếp Ng&agrave;i l&agrave;m Đ&agrave;n đầu H&ograve;a thượng c&aacute;c Đại giới đ&agrave;n tại ch&ugrave;a Ấn Quang do Gi&aacute;o Hội Phật Gi&aacute;o Việt Nam Thống Nhất mở ra để truyền trao giới ph&aacute;p cho giới tử to&agrave;n quốc.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nTừ năm 1977-1981, Ng&agrave;i ki&ecirc;m chức Tổng vụ trưởng Tổng Vụ Tăng Sự của Gi&aacute;o Hội Phật Gi&aacute;o Việt Nam Thống Nhất.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1981, Gi&aacute;o Hội Phật Gi&aacute;o Việt Nam được th&agrave;nh lập, Ng&agrave;i được cung thỉnh v&agrave;o l&agrave;m th&agrave;nh vi&ecirc;n Hội Đồng Chứng Minh Trung Ương.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNhận thấy thời gian đồng h&agrave;nh với l&atilde;o bệnh, ph&aacute;t sinh nơi th&acirc;n tứ đại, từ năm 1976 trở đi, Ng&agrave;i ph&aacute;t nguyện nhập thất an tịnh cho đến khi về c&otilde;i Phật. V&agrave;o ng&agrave;y 29 th&aacute;ng 10 năm Gi&aacute;p T&yacute; (1984), huyễn th&acirc;n Ng&agrave;i nhẹ nh&agrave;ng chuyển h&oacute;a. Ng&agrave;i trụ thế 80 năm, được 59 hạ lạp, để lại trong t&acirc;m tưởng đ&agrave;n hậu tấn niềm tri &acirc;n v&ocirc; hạn bởi một sự nghiệp v&ocirc; c&ugrave;ng.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNg&agrave;i c&oacute; c&ocirc;ng đức lớn trong nhiệm vụ gi&aacute;o dục đ&agrave;o tạo nhiều thế hệ Tăng t&agrave;i, v&agrave; truyền thừa ch&iacute;nh ph&aacute;p qua việc phi&ecirc;n dịch, ấn h&agrave;nh nhiều kinh luật phổ biến với c&aacute;c t&aacute;c phẩm để lại:<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n- Sa Di luật giải.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n- Qui Sơn cảnh s&aacute;ch.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n- Tứ phần giới bổn Như Th&iacute;ch.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n- Phạm V&otilde;ng Bồ T&aacute;t giới.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n- Kinh A Di Đ&agrave; sớ sao.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n- Kinh Vị Tằng Hữu Thuyết Nh&acirc;n Duy&ecirc;n.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n- Kinh Hiền Nh&acirc;n.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n- Kinh trừ khủng tai hoạn.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n- Tỳ Kheo giới kinh.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n- Khuyến ph&aacute;t Bồ Đề t&acirc;m văn.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n- Long Thơ Tịnh Độ.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n- Sơ Đẳng Phật học gi&aacute;o khoa thư.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n- Nghi thức Lễ S&aacute;m.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n- Kinh Thi Ca La Việt.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n- Sự t&iacute;ch Phật gi&aacute;ng thế.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nH&ograve;a thượng l&agrave; vị Sư Biểu của h&agrave;ng Cao Tăng đạo cao đức trọng, uy k&iacute;nh trong Tăng Gi&agrave;. C&ocirc;ng hạnh của Ng&agrave;i m&atilde;i c&ograve;n được sự ngưỡng vọng trong l&ograve;ng Tăng Ni, Phật tử Việt Nam.</span></span></div>\r\n', 'Hòa Thượng Thích Hành Trụ (1904-1984)', 0),
(96, 11, 'BBT Website', '2013-02-02 17:22:54', '<div style="margin: 0px; padding: 0px; outline: 0px; max-width: 654px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: center;"><span style="margin: 0px; padding: 0px; outline: 0px; font-size: 14px;"><span style="margin: 0px; padding: 0px; outline: 0px; font-family: arial, helvetica, sans-serif;"><img alt="" src="http://vuonhoaphatgiao.com/uploads/noidung/images/thich_thanh_tri_vuonhoaphatgiao_com.jpg" style="margin: 0px; padding: 0px; outline: 0px; border: 0px; border-spacing: 0px; vertical-align: middle; max-width: 654px; width: 240px; height: 186px;" /></span></span></div>\r\n\r\n<div style="margin: 0px; padding: 0px; outline: 0px; max-width: 654px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;"><br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<span style="margin: 0px; padding: 0px; outline: 0px; font-size: 14px;"><span style="margin: 0px; padding: 0px; outline: 0px; font-family: arial, helvetica, sans-serif;">H&ograve;a thượng thế danh l&agrave; Hồ Văn Li&ecirc;u, h&uacute;y T&acirc;m Huệ, ph&aacute;p hiệu Th&iacute;ch Thanh Tr&iacute;, thuộc d&ograve;ng L&acirc;m Tế đời thứ 43. Ng&agrave;i sinh ng&agrave;y mồng 1 th&aacute;ng 10 năm Kỷ M&ugrave;i (21-11-1919) tại l&agrave;ng Phước Y&ecirc;n, x&atilde; Quảng Thọ, huyện Quảng Điền (nay l&agrave; Hương Điền), tỉnh Thừa Thi&ecirc;n. L&agrave; con trai độc nhất trong một gia đ&igrave;nh c&oacute; năm anh chị em. Th&acirc;n phụ l&agrave; cụ &ocirc;ng Hồ Văn Sừng. Th&acirc;n mẫu l&agrave; cụ b&agrave; T&ocirc; Thị Thục.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nXuất th&acirc;n trong một gia đ&igrave;nh nề nếp, thấm nhuần đạo Phật, hằng ng&agrave;y thường lui tới ng&ocirc;i Sắc tứ Quảng Phước Tự trong l&agrave;ng, n&ecirc;n Ng&agrave;i sớm c&oacute; &yacute; nguyện xuất gia học đạo. Năm 12 tuổi, Ng&agrave;i đ&atilde; xin ph&eacute;p song th&acirc;n v&agrave;o Huế, t&igrave;m đến ch&ugrave;a Từ H&oacute;a, đảnh lễ Đại l&atilde;o H&ograve;a thượng Trừng Di&ecirc;n, xin xuất gia v&agrave; được H&ograve;a thượng hứa khả. Từ đ&oacute; Ng&agrave;i chăm lo học tập kinh luận, t&igrave;m hiểu Phật ph&aacute;p. Đến năm 1936, Ng&agrave;i đ&uacute;ng 18 tuổi, được thọ giới Sa Di tại giới đ&agrave;n ch&ugrave;a Đại Bi tỉnh Thanh H&oacute;a, do Đại l&atilde;o H&ograve;a thượng Th&iacute;ch Huệ Minh tr&uacute; tr&igrave; ch&ugrave;a Từ Hiếu l&agrave;m Đ&agrave;n đầu H&ograve;a thượng.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nTuy c&ograve;n l&agrave; Sa Di, nhưng với tư chất th&ocirc;ng minh, đạo hạnh gương mẫu, n&ecirc;n năm 1940 (22 tuổi), Ng&agrave;i được Phủ T&ocirc;n Nhơn ho&agrave;ng tộc xin Gi&aacute;o hội cho tr&uacute; tr&igrave; ch&ugrave;a Diệu Hỷ th&agrave;nh phố Huế. Hai năm sau, Ng&agrave;i lại được Gi&aacute;o hội v&agrave; Tổ đ&igrave;nh B&aacute;o Quốc mời l&agrave;m Tri sự của Tổ đ&igrave;nh. Năm 1948, l&uacute;c 30 tuổi, Ng&agrave;i thọ Cụ T&uacute;c giới tại Đại giới đ&agrave;n B&aacute;o Quốc do H&ograve;a thượng Th&iacute;ch Tịnh Khiết l&agrave;m đ&agrave;n đầu. Sau đ&oacute; Ng&agrave;i được Gi&aacute;o hội lần lượt giao nhiều tr&aacute;ch nhiệm quan trọng.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1953, Ng&agrave;i l&agrave; Ủy vi&ecirc;n T&agrave;i ch&aacute;nh của Gi&aacute;o hội Tăng gi&agrave; Trung Việt. Cũng trong năm n&agrave;y, Ng&agrave;i đ&atilde; c&ugrave;ng Gi&aacute;o hội s&aacute;ng lập trường tiểu học H&agrave;m Long với mục đ&iacute;ch mở mang về thế học cho ch&uacute;ng điệu để c&oacute; được tr&igrave;nh độ kiến thức phổ th&ocirc;ng g&oacute;p phần xứng đ&aacute;ng cho sự nghiệp của Gi&aacute;o hội. Đến năm 1957 trường n&agrave;y đ&atilde; mở l&ecirc;n đến bậc trung học.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1959, Ng&agrave;i đứng ra hưng c&ocirc;ng đại tr&ugrave;ng tu Tổ đ&igrave;nh B&aacute;o Quốc bằng vật liệu nặng nhưng vẫn giữ được n&eacute;t cổ k&iacute;nh của lối kiến tr&uacute;c &Aacute; Đ&ocirc;ng.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1963 gặp l&uacute;c ph&aacute;p nạn, Ng&agrave;i đ&atilde; c&ugrave;ng chư t&ocirc;n gi&aacute;o phẩm, Tăng Ni, Phật tử đứng l&ecirc;n tranh đấu đ&ograve;i hỏi tự do t&iacute;n ngưỡng, b&igrave;nh đẳng t&ocirc;n gi&aacute;o, c&ugrave;ng bị bắt đ&ecirc;m 20-8-1963 khi ch&iacute;nh quyền Ng&ocirc; Đ&igrave;nh Diệm tấn c&ocirc;ng chiếm ch&ugrave;a.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1964 Gi&aacute;o Hội Phật Gi&aacute;o Việt Nam Thống Nhất được th&agrave;nh lập, Ng&agrave;i được mời l&agrave;m Đặc ủy T&agrave;i ch&aacute;nh Kiến thiết của Tỉnh Gi&aacute;o Hội Thừa Thi&ecirc;n v&agrave; Th&agrave;nh phố Huế. Hai năm sau, Ng&agrave;i được cử l&agrave;m Ch&aacute;nh Đại Diện Tỉnh Gi&aacute;o Hội ki&ecirc;m Gi&aacute;m tự Phật học viện B&aacute;o Quốc - Huế.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nTết Mậu Th&acirc;n 1968, Tổ đ&igrave;nh B&aacute;o Quốc kh&ocirc;ng may bị bom đạn t&agrave;n ph&aacute; nặng nề, Ng&agrave;i lại một lần nữa đứng ra k&ecirc;u gọi sự t&ugrave;y hỷ đ&oacute;ng g&oacute;p của t&iacute;n đồ để đại tr&ugrave;ng tu Tổ đ&igrave;nh.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nĐầu xu&acirc;n 1970, tại buổi họp của Tổ đ&igrave;nh, theo thư đề nghị của H&ograve;a thượng Tr&iacute; Thủ, Ng&agrave;i được mời đảm nhiệm tr&uacute; tr&igrave; Phật học viện B&aacute;o Quốc thay H&ograve;a thượng đang bận Phật sự của Viện H&oacute;a Đạo tại S&agrave;i G&ograve;n. Nhưng Ng&agrave;i vẫn từ chối, chỉ giữ chức Gi&aacute;m tự như cũ, thay mặt H&ograve;a thượng Tr&iacute; Thủ đảm đương Phật sự của Tổ đ&igrave;nh v&agrave; c&ugrave;ng với Ban quản trị Phật học viện B&aacute;o Quốc chăm lo việc gi&aacute;o dưỡng Tăng sinh.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nTh&aacute;ng 8 năm 1972, Ng&agrave;i được Gi&aacute;o Hội mời giữ trọng tr&aacute;ch Ch&aacute;nh Đại Diện lần nữa của tỉnh Thừa Thi&ecirc;n v&agrave; Th&agrave;nh phố Huế, thay thế H&ograve;a thượng Th&iacute;ch Mật Nguyện mới vi&ecirc;n tịch. Với t&igrave;nh cảm y&ecirc;u qu&ecirc; hương l&agrave;ng x&oacute;m, năm 1974 Ng&agrave;i đ&atilde; c&ugrave;ng d&acirc;n l&agrave;ng Phước Y&ecirc;n, nơi ch&agrave;o đời của Ng&agrave;i, tr&ugrave;ng tu ng&ocirc;i Sắc tứ Quảng Phước Cổ Tự m&agrave; thuở thiếu thời Ng&agrave;i hằng lui tới lễ Phật, nghe kinh.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1977,Ng&agrave;i được mời l&agrave;m T&ocirc;n chứng của Đại giới đ&agrave;n tổ chức tại ch&ugrave;a Ấn Quang,S&agrave;i G&ograve;n, v&agrave; l&agrave;m đ&agrave;n chủ của giới đ&agrave;n tổ chức tại ch&ugrave;a B&aacute;o Quốc-Huế. Cũng năm n&agrave;y, Ng&agrave;i ch&iacute;nh thức l&agrave; Ủy vi&ecirc;n của Hội đồng Gi&aacute;o Phẩm Trung ương Gi&aacute;o Hội Phật Gi&aacute;o Việt Nam Thống Nhất.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNg&agrave;y 25-5-1979,Ng&agrave;i được Ủy ban Mặt trận Tổ quốc tỉnh B&igrave;nh Trị Thi&ecirc;n mời v&agrave;o ph&aacute;i đo&agrave;n đi thăm tỉnh kết nghĩa Savannakhet của nước Cộng H&ograve;a D&acirc;n Chủ Nh&acirc;n D&acirc;n L&agrave;o.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nĐầu xu&acirc;n Canh Th&acirc;n (1980) trước nhu cầu của lịch sử, Ng&agrave;i được mời v&agrave;o Ban vận động Thống nhất Phật gi&aacute;o to&agrave;n quốc. Trong Đại hội Thống nhất Phật Gi&aacute;o 1981, Ng&agrave;i được cử l&agrave;m Ủy vi&ecirc;n Hội đồng Trị sự Gi&aacute;o Hội Phật Gi&aacute;o Việt Nam. Th&aacute;ng tư năm Nh&acirc;m Tuất (1982), trong đại hội th&agrave;nh lập Gi&aacute;o Hội Phật Gi&aacute;o Việt Nam tỉnh B&igrave;nh Trị Thi&ecirc;n, Ng&agrave;i được bầu giữ chức Trưởng Ban Trị Sự tỉnh ki&ecirc;m Ch&aacute;nh Đại Diện Gi&aacute;o Hội th&agrave;nh phố Huế. Năm 1983 Ng&agrave;i lại được bầu v&agrave;o Ủy ban Mặt trận Tổ quốc tỉnh.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNg&agrave;i kh&ocirc;ng những chỉ lo tu tr&igrave;, m&agrave; c&ograve;n chỉ đạo tổ chức tự t&uacute;c về kinh tế cho Tăng Ni như th&agrave;nh lập c&aacute;c Tổ hợp chế biến vị trai L&aacute; Bồ Đề, c&aacute;c Tổ hợp n&ocirc;ng nghiệp, c&aacute;c Tổ hợp đan th&ecirc;u, c&aacute;c Tổ hợp tiểu thủ c&ocirc;ng nghiệp kh&aacute;c. Nhờ đ&oacute; m&agrave; Tăng Ni c&oacute; điều kiện l&agrave;m ra của cải vật chất, đ&oacute;ng g&oacute;p lợi &iacute;ch chung cho x&atilde; hội v&agrave; cải thiện sinh hoạt nơi tự viện.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nSuốt cuộc đời Ng&agrave;i đ&atilde; d&agrave;nh t&acirc;m lực v&agrave; tr&iacute; tuệ cộng sự chặt chẽ với H&ograve;a thượng Th&iacute;ch Tr&iacute; Thủ trong việc phụng sự Phật ph&aacute;p, đ&agrave;o tạo Tăng sinh kh&ocirc;ng mệt mỏi. Khi được tin H&ograve;a thượng Th&iacute;ch Tr&iacute; Thủ vi&ecirc;n tịch, Ng&agrave;i đ&atilde; v&agrave;o S&agrave;i G&ograve;n v&agrave; nhận l&agrave;m chấp lệnh cho Tang lễ.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nĐ&aacute;m tang cố H&ograve;a thượng Th&iacute;ch Tr&iacute; Thủ vừa xong, chỉ mười ng&agrave;y sau Ng&agrave;i cũng vi&ecirc;n tịch tại Tu viện Quảng Hương Gi&agrave; Lam S&agrave;i G&ograve;n v&agrave;o l&uacute;c 13 giờ 30&#39; ng&agrave;y 13 th&aacute;ng 3 năm Gi&aacute;p T&yacute; (13-4-1984), hưởng thọ 66 tuổi đời, 40 tuổi hạ. Kim quan của Ng&agrave;i được đưa về Huế nhập th&aacute;p tại ch&ugrave;a B&aacute;o Quốc.</span></span></div>\r\n', 'Hòa Thượng Thích Thanh Trí (1919-1984)', 0),
(97, 11, 'BBT Website', '2013-02-02 17:23:24', '<div style="margin: 0px; padding: 0px; outline: 0px; max-width: 654px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: center;"><span style="margin: 0px; padding: 0px; outline: 0px; font-size: 14px;"><span style="margin: 0px; padding: 0px; outline: 0px; font-family: arial, helvetica, sans-serif;"><img alt="" src="http://vuonhoaphatgiao.com/uploads/noidung/images/thich-phap-trang-vuonhoaphatgiao_com.JPG" style="margin: 0px; padding: 0px; outline: 0px; border: 0px; border-spacing: 0px; vertical-align: middle; max-width: 654px;" /></span></span></div>\r\n\r\n<div style="margin: 0px; padding: 0px; outline: 0px; max-width: 654px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;"><br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<span style="margin: 0px; padding: 0px; outline: 0px; font-size: 14px;"><span style="margin: 0px; padding: 0px; outline: 0px; font-family: arial, helvetica, sans-serif;">H&ograve;a thượng Th&iacute;ch Ph&aacute;p Tr&agrave;ng, thế danh Đồng Ngọc Tự, sinh năm Mậu Tuất (1898) tại th&ocirc;n T&acirc;n Long (nay l&agrave; x&atilde; T&acirc;n B&igrave;nh, huyện Cai Lậy, tỉnh Tiền Giang). Song th&acirc;n l&agrave; cụ Đồng Văn Tỉnh v&agrave; b&agrave; Ng&ocirc; Thị Nơi. Gia đ&igrave;nh Nho học, k&iacute;nh t&iacute;n Phật đạo. Ng&agrave;i l&agrave; người con thứ t&aacute;m trong gia đ&igrave;nh, người anh thứ hai xuất gia trước Ng&agrave;i.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm l&ecirc;n 7 tuổi, Ng&agrave;i đ&atilde; được cha cho học chữ H&aacute;n. Năm l&ecirc;n 13 tuổi, do sức học tiến nhanh, tiếp thu văn nghĩa thư kinh tốt n&ecirc;n Ng&agrave;i được cha cho ra trường l&agrave;ng học chữ Quốc ngữ.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm Mậu Ngọ (1918), vừa tuổi 20, Ng&agrave;i từ chối việc lập gia đ&igrave;nh, ki&ecirc;n quyết xin cha mẹ cho xuất gia v&agrave; t&igrave;m đến ch&ugrave;a Kh&aacute;nh Quới (c&ugrave;ng th&ocirc;n T&acirc;n Long) xin thế độ. Được H&ograve;a thượng Kh&aacute;nh Quới giới thiệu đến ch&ugrave;a Thi&ecirc;n Thai (B&agrave; Rịa) đảnh lễ cầu ph&aacute;p với H&ograve;a thượng Huệ Đăng, được H&ograve;a thượng đặt Ph&aacute;p danh Trừng Tự, hiệu Ph&aacute;p Tr&agrave;ng, nối đời thứ 42 d&ograve;ng L&acirc;m Tế. Nhờ sự dạy dỗ của H&ograve;a thượng Thi&ecirc;n Thai, kh&ocirc;ng bao l&acirc;u Ng&agrave;i đ&atilde; th&ocirc;ng hiểu c&aacute;c kinh tạng v&agrave; thiền luật.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm Mậu Th&igrave;n (1928), ch&uacute;c thọ giới đ&agrave;n được mở tại ch&ugrave;a Phước Long (nay l&agrave; Hội Xu&acirc;n, Cai Lậy) Ng&agrave;i được mời tham dự với cương vị Gi&aacute;o Thọ. L&uacute;c bấy giờ Phật tử x&atilde; Mỹ Phước T&acirc;y đem c&uacute;ng ch&ugrave;a Kh&aacute;nh Long v&agrave;o Tổ đ&igrave;nh Thi&ecirc;n Thai, H&ograve;a thượng Thi&ecirc;n Thai nhận thấy Ng&agrave;i đ&atilde; đủ cơ duy&ecirc;n n&ecirc;n cử l&agrave;m trụ tr&igrave; ch&ugrave;a n&agrave;y. Nhờ v&agrave;o t&agrave;i đức của Ng&agrave;i, c&aacute;c h&agrave;ng Phật tử đ&atilde; đến quy y v&agrave; lễ b&aacute;i rất đ&ocirc;ng.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm Ất Hợi (1935), Ng&agrave;i tham dự giới đ&agrave;n tại ch&ugrave;a Ti&ecirc;n Long (Bi&ecirc;n H&ograve;a) được cử l&agrave;m Yết Ma A X&agrave; L&ecirc;.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm Đinh Sửu (1937), Ng&agrave;i an cư tại Tổ đ&igrave;nh Long H&ograve;a (B&agrave; Rịa) do H&ograve;a thượng Thi&ecirc;n Thai đứng ra tổ chức, Ng&agrave;i được cử giữ chức Ch&aacute;nh Ch&uacute;ng.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm Mậu Dần (1938), Ng&agrave;i được H&ograve;a thượng Thi&ecirc;n Thai ủy nhiệm đứng ra tổ chức giới đ&agrave;n. C&ocirc;ng việc vi&ecirc;n m&atilde;n tốt đẹp, Ng&agrave;i được tấn phong Thượng Tọa (năm Ng&agrave;i được 41 tuổi).<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nTừ đ&acirc;y, uy t&iacute;n v&agrave; đức độ của Ng&agrave;i đ&atilde; lan tỏa khắp nơi, bắt nhịp với cao tr&agrave;o c&aacute;ch mạng ở miền Nam, n&ecirc;n c&aacute;n bộ c&aacute;ch mạng thường lui tới viếng thăm, đ&agrave;m đạo để tranh thủ sự ủng hộ, v&agrave; Ng&agrave;i đ&atilde; nhiệt t&igrave;nh cộng t&aacute;c. Cuối th&aacute;ng 11 năm 1940 (Canh Th&igrave;n) cuộc khởi nghĩa Nam kỳ b&ugrave;ng nổ, Ng&agrave;i l&agrave; một trong những người đầu ti&ecirc;n c&oacute; c&ocirc;ng lớn. Tuy vậy, khi t&igrave;nh h&igrave;nh b&igrave;nh ổn, c&aacute;c cuộc truy l&ugrave;ng, bắt bớ của thực d&acirc;n th&ecirc;m dữ dội, những người cầm đầu cuộc khởi nghĩa ấy lớp bị bắt, lớp bị lưu đ&agrave;y ra C&ocirc;n Đảo. Ri&ecirc;ng Ng&agrave;i nhờ Phật tử th&acirc;n t&iacute;n gi&uacute;p đỡ, nhanh ch&oacute;ng đưa Ng&agrave;i sang ch&ugrave;a Phước Long, x&atilde; T&acirc;n Hợi, huyện Long Mỹ, tỉnh Cần Thơ để ẩn tu. M&atilde;i đến sau năm 1945 Ng&agrave;i mới trở về qu&ecirc; cũ v&agrave; t&igrave;m đến ch&ugrave;a Li&ecirc;n Từ nơi người anh ruột của Ng&agrave;i l&agrave; Thầy Minh T&acirc;m đang ở tu. Ng&agrave;i về đ&acirc;y l&agrave; c&oacute; mục đ&iacute;ch li&ecirc;n lạc lại với c&aacute;c cơ sở c&aacute;ch mạng.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm Đinh Hợi (1947), H&ograve;a thượng Minh Nguyệt đ&atilde; về ch&ugrave;a Li&ecirc;n Từ, c&ugrave;ng Ng&agrave;i th&agrave;nh lập Hội Phật Gi&aacute;o Cứu Quốc huyện C&aacute;i B&egrave;. Sau đ&oacute;, Ng&agrave;i được cử v&agrave;o Ban Chấp H&agrave;nh huyện, rồi Ban Chấp H&agrave;nh cấp tỉnh, v&agrave; Ủy vi&ecirc;n Kiểm so&aacute;t Ban Chấp H&agrave;nh Phật Gi&aacute;o Cứu quốc Nam bộ. Ng&agrave;i đ&atilde; đi đến hầu hết c&aacute;c nơi trong tỉnh vận động Tăng Ni Phật tử, quy&ecirc;n tiền ủng hộ kh&aacute;ng chiến v&agrave; chế tạo vũ kh&iacute;.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm Mậu T&yacute; (1948), Ng&agrave;i c&ocirc;ng t&aacute;c ở Ban Chấp H&agrave;nh Phật Gi&aacute;o Cứu Quốc; Ủy vi&ecirc;n Mặt Trận Li&ecirc;n Việt tỉnh Mỹ Tho.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNhững năm 1949-1950, khi phong tr&agrave;o Phật Gi&aacute;o Cứu Quốc bị giải t&aacute;n, Ng&agrave;i được cử v&agrave;o l&agrave;m đại biểu Phật gi&aacute;o trong Mặt Trận Li&ecirc;n Việt.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNg&agrave;y 10.10.1952, trong chuyến c&ocirc;ng t&aacute;c từ Mỹ Tho về Cai Lậy, Ng&agrave;i bị bắt c&ugrave;ng với tiền quy&ecirc;n g&oacute;p ủng hộ kh&aacute;ng chiến v&agrave; kh&ocirc;ng &iacute;t t&agrave;i liệu quan trọng. Ng&agrave;i bị giam ở kh&aacute;m số 7 (ở Mỹ Tho) v&agrave; bị tra tấn trước khi đ&agrave;y l&ecirc;n Lộc Ninh. Đến năm Qu&yacute; Tỵ (1953) Ng&agrave;i mới được tự do, trở về tiếp tục c&ocirc;ng t&aacute;c trong Mặt Trận Li&ecirc;n Việt Mỹ Tho.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nSau Hiệp định Gen&egrave;ve 1954, Ng&agrave;i nhận nhiệm vụ ở lại tại chỗ để đấu tranh đ&ograve;i thi h&agrave;nh Hiệp định, v&agrave; đ&ograve;i quyền d&acirc;n chủ d&acirc;n sinh, c&ugrave;ng tham gia Ban Chấp H&agrave;nh Phật Gi&aacute;o Lục H&ograve;a tỉnh Định Tường.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1956, Ng&agrave;i đứng ra vận động tổ chức ng&agrave;y lễ Phật Đản tại th&agrave;nh phố Mỹ Tho rất trọng thể. Ng&agrave;i thuyết phục ch&iacute;nh quyền S&agrave;i G&ograve;n cho v&agrave;o nh&agrave; lao thăm c&aacute;c t&ugrave; ch&iacute;nh trị, cung cấp thuốc men, thức ăn... Nhờ đ&oacute; những c&aacute;n bộ bị giam giữ đều nắm được t&igrave;nh h&igrave;nh b&ecirc;n ngo&agrave;i. H&igrave;nh thức n&agrave;y tồn tại được hai năm th&igrave; bị cấm hẳn. Ng&agrave;i chuyển hướng kh&aacute;c, đầu ti&ecirc;n l&agrave; vận động gi&uacute;p đỡ đồng b&agrave;o bị nạn trong chiến dịch của ch&iacute;nh quyền Ng&ocirc; Đ&igrave;nh Diệm truy qu&eacute;t qu&acirc;n B&igrave;nh Xuy&ecirc;n của tướng Bảy Viển ở v&ugrave;ng cầu chữ Y, X&oacute;m Củi - S&agrave;i G&ograve;n Chợ Lớn.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nCuối năm 1957, Ng&agrave;i đ&atilde; tho&aacute;t được đợt truy bắt của ch&iacute;nh quyền S&agrave;i G&ograve;n v&agrave;o ch&ugrave;a Bửu Long, Trung Lương - Mỹ Tho (l&uacute;c n&agrave;y Ng&agrave;i trụ tr&igrave; nơi đ&acirc;y). Ng&agrave;i liền l&aacute;nh sang Bến Tre, đến ch&ugrave;a Bửu Long ở x&atilde; Ph&uacute; Đức, do một Phật tử d&acirc;ng c&uacute;ng Ng&agrave;i.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNg&agrave;y 15.01.1960, Mặt Trận Giải Ph&oacute;ng Miền Nam Việt Nam ra đời, Ng&agrave;i được bầu l&agrave;m Ủy vi&ecirc;n Mặt trận tỉnh Mỹ Tho.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nCuối năm 1964, Trung ương Mặt trận Giải ph&oacute;ng Miền Nam Việt Nam mời Ng&agrave;i th&aacute;p t&ugrave;ng đo&agrave;n đại biểu dự Đại hội Nh&acirc;n d&acirc;n Đ&ocirc;ng Dương tổ chức tại Campuchia từ ng&agrave;y 1 đến 9 th&aacute;ng 3.1965. Đo&agrave;n gồm 27 th&agrave;nh vi&ecirc;n, do &ocirc;ng Huỳnh Tấn Ph&aacute;t dẫn đầu. Sau hội nghị, Ng&agrave;i c&ograve;n được mời tham quan Angkor v&agrave; c&aacute;c nơi kh&aacute;c ở Campuchia. Sau khi về nước, Ng&agrave;i được dự lớp D&acirc;n Vận Mặt Trận tại Văn ph&ograve;ng Trung ương. Hai th&aacute;ng sau khi m&atilde;n kh&oacute;a, Ng&agrave;i về c&ocirc;ng t&aacute;c ở Mặt trận Li&ecirc;n khu 8. Nhờ đ&oacute;, Ng&agrave;i c&oacute; dịp lui tới c&aacute;c v&ugrave;ng An Giang, Ki&ecirc;n Giang, Bến Tre, Mỹ Tho, G&ograve; C&ocirc;ng. Trong một chuyến c&ocirc;ng t&aacute;c, Ng&agrave;i bị m&aacute;y bay oanh k&iacute;ch bị thương ở vai phải,<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1969, Ng&agrave;i c&ugrave;ng H&ograve;a thượng Thiện H&agrave;o được ủy nhiệm hướng dẫn ph&aacute;i đo&agrave;n Phật gi&aacute;o sang viếng tang Tăng Ho&agrave;ng Campuchia.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1970, Trung ương Mặt trận Giải ph&oacute;ng Miền Nam Việt Nam cử Ng&agrave;i tham gia ph&aacute;i đo&agrave;n dự Hội nghị Cao cấp Nh&acirc;n d&acirc;n Đ&ocirc;ng Dương, được tổ chức ở Campuchia. Sau đ&oacute;, Ng&agrave;i về ngụ tại ch&ugrave;a Long Kh&aacute;nh (x&atilde; Long Th&agrave;nh, tỉnh Vĩnh Long) do H&ograve;a thượng Ph&aacute;p V&acirc;n đang trụ tr&igrave;.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1972, Ng&agrave;i chuyển địa b&agrave;n hoạt động l&ecirc;n S&agrave;i G&ograve;n Chợ Lớn. Tr&ecirc;n đường về S&agrave;i G&ograve;n, ng&agrave;y 27.6 Ng&agrave;i bị bắt tại Ph&uacute; L&acirc;m, nhưng do kh&ocirc;ng t&igrave;m được g&igrave; kh&aacute;c hơn n&ecirc;n sau đ&oacute; Ng&agrave;i được trả tự do.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1973, Ng&agrave;i được cử đến gặp Ni Sư Huỳnh Li&ecirc;n ở Tịnh x&aacute; Ngọc Phương (đường L&ecirc; Quang Định - G&ograve; Vấp) để truyền đạt, b&agrave;n bạc c&aacute;c chỉ thị hoạt động của Tăng Ni tổ chức biểu t&igrave;nh chống chế độ S&agrave;i G&ograve;n.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1975, Ng&agrave;i trở lại Tiền Giang để thay mặt Qu&acirc;n Khu 8 k&ecirc;u gọi Tăng Ni Phật tử đo&agrave;n kết v&agrave; dốc t&acirc;m sức x&acirc;y dựng đất nước trong ho&agrave;n cảnh mới của nước nh&agrave; thống nhất.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1978, l&uacute;c n&agrave;y đ&atilde; 80 tuổi, Ng&agrave;i được mời về trụ tr&igrave; ch&ugrave;a Phật Ấn tại th&agrave;nh phố Mỹ Tho.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1981, Ng&agrave;i tham dự Đại hội Đại biểu Thống nhất Phật gi&aacute;o Việt Nam tại H&agrave; Nội v&agrave; được suy cử l&agrave;m th&agrave;nh vi&ecirc;n Hội Đồng Chứng Minh Trung Ương Gi&aacute;o Hội Phật Gi&aacute;o Việt Nam.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNg&agrave;y 17.3.1984, Ng&agrave;i trở lại thăm qu&ecirc; hương (Cai Lậy - Tiền Giang) lần sau c&ugrave;ng. Tối đ&ecirc;m ấy, đang ngồi tham dự một v&aacute;n cờ tướng, Ng&agrave;i đột ngột qua đời. Hưởng thọ 86 tuổi, 45 năm tham gia c&aacute;ch mạng v&agrave; 66 năm sống trong Đạo ph&aacute;p.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nKim quan Ng&agrave;i được đưa về ch&ugrave;a Vĩnh Tr&agrave;ng v&agrave; Bảo th&aacute;p được x&acirc;y dựng trong khu&ocirc;n vi&ecirc;n ch&ugrave;a n&agrave;y.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNg&agrave;i đ&atilde; được Nh&agrave; nước tặng thưởng c&aacute;c hu&acirc;n huy chương:<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n- Hu&acirc;n chương Kh&aacute;ng chiến hạng nhất.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n- Hu&acirc;n chương chống Mỹ hạng nhất.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n- Bằng khen của Mặt trận Tổ quốc tỉnh Tiền Giang.</span></span></div>\r\n', 'Hòa Thượng Thích Pháp Tràng (1898-1984)', 0);
INSERT INTO `chuathienquang_news` (`id`, `id_category`, `author`, `date`, `content`, `title`, `type`) VALUES
(98, 11, 'BBT Website', '2013-02-02 17:23:56', '<div style="margin: 0px; padding: 0px; outline: 0px; max-width: 654px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: center;"><span style="margin: 0px; padding: 0px; outline: 0px; font-size: 14px;"><span style="margin: 0px; padding: 0px; outline: 0px; font-family: arial, helvetica, sans-serif;"><img alt="" src="http://vuonhoaphatgiao.com/uploads/noidung/images/tinh_su_vuonhoaphatgiao_com.jpg" style="margin: 0px; padding: 0px; outline: 0px; border: 0px; border-spacing: 0px; vertical-align: middle; max-width: 654px; width: 400px; height: 600px;" /></span></span></div>\r\n\r\n<div style="margin: 0px; padding: 0px; outline: 0px; max-width: 654px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;"><br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<span style="margin: 0px; padding: 0px; outline: 0px; font-size: 14px;"><span style="margin: 0px; padding: 0px; outline: 0px; font-family: arial, helvetica, sans-serif;">H&ograve;a thượng Tịnh Sự, thế danh l&agrave; V&otilde; Văn Đang, sinh năm Qu&iacute; Sửu (1913), trong một gia đ&igrave;nh c&oacute; truyền thống Nho gi&aacute;o, tại một v&ugrave;ng qu&ecirc; miền Nam, x&atilde; H&ograve;a Long, huyện Lai Vung, tỉnh Đồng Th&aacute;p. Th&acirc;n phụ Ng&agrave;i l&agrave; cụ V&otilde; Văn Tỏ, th&acirc;n mẫu l&agrave; cụ Trần Thị Th&ocirc;ng.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 7 tuổi Ng&agrave;i đ&atilde; học chữ Nho. Rất th&ocirc;ng minh, Ng&agrave;i được người đương thời gọi l&agrave; &quot;Thần đồng L&ecirc; Qu&iacute; Đ&ocirc;n&quot;. Do t&uacute;c duy&ecirc;n sẵn c&oacute; từ nhiều đời, đến năm 12 tuổi Ng&agrave;i xuất gia v&agrave; thọ giới Sa Di tại ch&ugrave;a Bửu Hưng (Sa Đ&eacute;c). Thấy Ng&agrave;i qu&aacute; th&ocirc;ng minh n&ecirc;n Bổn sư đặt ph&aacute;p danh l&agrave; Th&iacute;ch Huệ Lực.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 20 tuổi Ng&agrave;i thọ giới Tỳ kheo tại ch&ugrave;a Kim Hu&ecirc;-Sa Đ&eacute;c. Đến năm 25 tuổi Ng&agrave;i được bổ nhiệm về trụ tr&igrave; tại ch&ugrave;a Phước Định - Chợ L&aacute;ch. Năm 30 tuổi Ng&agrave;i trụ tr&igrave; ch&ugrave;a Vi&ecirc;n Gi&aacute;c-Vĩnh Long. Năm 35 tuổi duy&ecirc;n l&agrave;nh đối với Phật gi&aacute;o Nam T&ocirc;ng đ&atilde; ch&iacute;n m&ugrave;i, Ng&agrave;i được du học tại Ch&ugrave;a Th&aacute;p Campuchia, thọ giới theo Phật gi&aacute;o Nam T&ocirc;ng tại ch&ugrave;a K&ugrave;m Pung (Treyloko) ở Tr&agrave; P&eacute;t.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nTừ Campuchia Ng&agrave;i c&oacute; thiện duy&ecirc;n du học tại Th&aacute;i Lan, thọ giới Tỳ kheo tại ch&ugrave;a Pakknam Bangkok. Ng&agrave;i được thầy tế độ ban ph&aacute;p danh l&agrave; Tịnh Sự (Santakicco) v&igrave; thấy Ng&agrave;i chuy&ecirc;n t&acirc;m h&agrave;nh đạo. Tại xứ Phật gi&aacute;o Th&aacute;i Lan n&agrave;y, Ng&agrave;i đ&atilde; thực h&agrave;nh hạnh đầu đ&agrave;, tu thiền Minh S&aacute;t v&agrave; học A Tỳ Đ&agrave;m (Abhidhamma) trong bảy năm. Rời Th&aacute;i Lan về Việt Nam, Ng&agrave;i trở lại trụ tr&igrave; ch&ugrave;a Vi&ecirc;n Gi&aacute;c - Vĩnh Long. Ng&agrave;i bắt đầu mở mang truyền b&aacute; gi&aacute;o ph&aacute;p Nguy&ecirc;n Thủy. Ng&agrave;i dạy Ph&aacute;p học A Tỳ Đ&agrave;m P&agrave;li (Abhidhamma) v&agrave; Ph&aacute;p h&agrave;nh Tứ Niệm Xứ.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nMuốn cho m&ocirc;n học Abhidhamma được truyền b&aacute; rộng r&atilde;i n&ecirc;n v&agrave;o năm 59 tuổi Ng&agrave;i về trụ tr&igrave; ch&ugrave;a Si&ecirc;u L&yacute; - S&agrave;i G&ograve;n. Mở trường chuy&ecirc;n dạy về m&ocirc;n Abhidhamma v&agrave; dịch c&aacute;c bộ s&aacute;ch Gi&aacute;o Khoa Phật Học như Vi Diệu Ph&aacute;p Sơ Cấp, Trung Cấp v&agrave; Ch&aacute;nh Tạng Abhidhamma.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNg&agrave;i đ&atilde; đ&agrave;o tạo nhiều thế hệ học tr&ograve; Vi Diệu Ph&aacute;p. C&aacute;c thế hệ đệ tử n&agrave;y đ&atilde; kế thừa sự nghiệp của Ng&agrave;i truyền b&aacute; m&ocirc;n Abhidhamma khắp nơi. Những thế hệ đệ tử Vi Diệu Ph&aacute;p đầu ti&ecirc;n hiện nay c&ograve;n những vị ti&ecirc;u biểu như Đại Đức Gi&aacute;c Ch&aacute;nh, cư sĩ Vĩnh Ph&uacute;c, cư sĩ Trần Quỳnh Hương đ&atilde; c&oacute; c&ocirc;ng đức duy tr&igrave; v&agrave; ph&aacute;t triển m&ocirc;n học Abhidhamma.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nVề phương diện đ&agrave;o tạo Tăng t&agrave;i, Ng&agrave;i đ&atilde; từng l&agrave;m thầy Tế độ truyền giới xuất gia cho h&agrave;ng trăm Sa Di, Tỳ Kheo. Họ đ&atilde; tiếp nối được sự nghiệp truyền b&aacute; gi&aacute;o ph&aacute;p của Ng&agrave;i một c&aacute;ch tốt đẹp.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nKh&ocirc;ng những chỉ đ&agrave;o tạo Tăng t&agrave;i m&agrave; Ng&agrave;i c&ograve;n x&acirc;y dựng rất nhiều cơ sở tự viện: ch&ugrave;a Vi&ecirc;n Gi&aacute;c (Vĩnh Long), ch&ugrave;a Long Linh, ch&ugrave;a Gi&aacute;c Phước, ch&ugrave;a Ph&aacute;p Độ, ch&ugrave;a Tr&uacute;c L&acirc;m, ch&ugrave;a Si&ecirc;u L&yacute;, ch&ugrave;a Thiền Quang 1, ch&ugrave;a Thiền Quang 2.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nĐiểm nổi bật nhất trong sự nghiệp hoằng ph&aacute;p của H&ograve;a thượng l&agrave; Ng&agrave;i đ&atilde; dịch ho&agrave;n th&agrave;nh Tạng luận P&agrave;li tức Tạng Abhidhamma gồm:<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n1) Bộ Ph&aacute;p Tụ (Dhammasangani).<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n2) Bộ Ph&acirc;n T&iacute;ch (Vibhanga).<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n3) Bộ Chất Ngữ (Dh&agrave; k&agrave;th&agrave;).<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n4) Bộ Nh&acirc;n Chế Định (PuggalaPanntti).<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n5) Bộ Ngữ T&ocirc;ng (K&agrave;th&agrave; Vatthu).<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n6) Bộ Song Đối (Yamakam).<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n7) Bộ Ph&aacute;t Tr&iacute; (Patthana).<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nĐ&oacute; l&agrave; phần ch&aacute;nh tạng Abhidhamma. Sau đ&acirc;y l&agrave; những dịch phẩm gi&aacute;o khoa Adhidhamma được giảng dạy tại c&aacute;c trường Phật Học Th&aacute;i Lan.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n1) Vi Diệu Ph&aacute;p Sơ Cấp.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n2) Vi Diệu Ph&aacute;p Trung Cấp.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n3) Vi Diệu Ph&aacute;p Cao Cấp.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n4) Thanh Tịnh Đạo.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n5) Diệu Ph&aacute;p L&yacute; Hợp.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nSự nghiệp đạo ph&aacute;p của Ng&agrave;i v&ocirc; c&ugrave;ng to lớn. Nhất l&agrave; Ng&agrave;i đ&atilde; d&agrave;nh hết đời m&igrave;nh để phi&ecirc;n dịch trọn Tạng Vi Diệu Ph&aacute;p (Luận Tạng P&agrave;Li). Qua c&ocirc;ng hạnh n&agrave;y, Ng&agrave;i l&agrave; một ng&ocirc;i sao m&agrave; c&agrave;ng nh&igrave;n ch&uacute;ng ta c&agrave;ng thấy s&aacute;ng hơn.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nĐ&ecirc;m m&ugrave;ng 6 th&aacute;ng 5 năm Gi&aacute;p T&yacute;, Ng&agrave;i thọ bệnh. Nửa đ&ecirc;m h&ocirc;m ấy Ng&agrave;i cho gọi Chư Tăng để ban di huấn v&agrave; gởi lời s&aacute;m hối đến Tăng ch&uacute;ng gần xa. Rồi Ng&agrave;i an tr&uacute; ch&aacute;nh niệm v&agrave; vi&ecirc;n tịch l&uacute;c 6 giờ 15&#39; s&aacute;ng ng&agrave;y 7 th&aacute;ng 5 năm Gi&aacute;p T&yacute; (tức 05-06-1984), hưởng thọ 72 tuổi, h&agrave;nh đạo 52 năm.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nTrước khi vi&ecirc;n tịch Ng&agrave;i đ&atilde; giảng về sự khổ, rồi mỉm cười tắt hơi thở cuối c&ugrave;ng. Ng&agrave;i ra đi nhưng sự nghiệp đạo ph&aacute;p của Ng&agrave;i vẫn c&ograve;n ở lại với hậu tấn ngưỡng cầu giải tho&aacute;t khổ đau, để hằng an vui tự tại nơi Niết B&agrave;n tịch tịnh.</span></span></div>\r\n', 'Hòa Thượng Tịnh Sự (1913-1984)', 0),
(99, 11, 'BBT Website', '2013-02-02 17:24:23', '<div style="margin: 0px; padding: 0px; outline: 0px; max-width: 654px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: center;"><span style="margin: 0px; padding: 0px; outline: 0px; font-size: 14px;"><span style="margin: 0px; padding: 0px; outline: 0px; font-family: arial, helvetica, sans-serif;"><img alt="" src="http://vuonhoaphatgiao.com/uploads/noidung/images/thich_thien_tuong-vuonhoaphatgiao_com.jpg" style="margin: 0px; padding: 0px; outline: 0px; border: 0px; border-spacing: 0px; vertical-align: middle; max-width: 654px; width: 188px; height: 269px;" /></span></span></div>\r\n\r\n<div style="margin: 0px; padding: 0px; outline: 0px; max-width: 654px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;"><br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<span style="margin: 0px; padding: 0px; outline: 0px; font-size: 14px;"><span style="margin: 0px; padding: 0px; outline: 0px; font-family: arial, helvetica, sans-serif;">H&ograve;a thượng ph&aacute;p danh l&agrave; Thanh Giới, tự Chơn Như, hiệu Thiện Tường, thuộc d&ograve;ng L&acirc;m Tế Ch&aacute;nh T&ocirc;ng đời thứ 41. Ng&agrave;i thế danh Ng&ocirc; Văn Phải, sinh năm 1917 tại l&agrave;ng B&igrave;nh Xu&acirc;n, tổng H&ograve;a Lạc, tỉnh G&ograve; C&ocirc;ng (nay thuộc tỉnh Tiền Giang). Ng&agrave;i xuất th&acirc;n trong một gia đ&igrave;nh k&iacute;nh tin Phật đạo. Th&acirc;n phụ l&agrave; cụ Ng&ocirc; Văn Chồn, th&acirc;n mẫu l&agrave; cụ Đỗ Thị Thơ, Ng&agrave;i l&agrave; con thứ hai trong gia đ&igrave;nh c&oacute; 5 anh em. C&ocirc; em &uacute;t sau n&agrave;y nối ch&iacute; Ng&agrave;i, xuất gia hiệu l&agrave; Tịnh Quang cũng l&agrave; một trong h&agrave;ng Ni ch&uacute;ng lỗi lạc.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm l&ecirc;n 9 tuổi, Ng&agrave;i sớm mồ c&ocirc;i cha, lại l&agrave; lao động ch&iacute;nh của gia đ&igrave;nh. Vốn bản t&iacute;nh chơn chất hiền h&ograve;a v&agrave; ch&iacute; hiếu, Ng&agrave;i chịu kh&oacute; với ruộng đồng lam lũ phụ gi&uacute;p mẹ v&agrave; dạy bảo c&aacute;c em. Những l&uacute;c rảnh việc m&ugrave;a m&agrave;ng, Ng&agrave;i thường c&ugrave;ng mẹ l&ecirc;n ch&ugrave;a lễ Phật nghe kinh, nh&acirc;n duy&ecirc;n lần kết v&agrave; hạnh nguyện xuất gia được nung nấu trong l&ograve;ng.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nĐến năm 19 tuổi (Đinh Sửu - 1937), nh&acirc;n ng&agrave;y Phật Đản, Ng&agrave;i tới ch&ugrave;a Long Quang, l&agrave;ng B&igrave;nh Thạnh l&agrave;m lễ thế ph&aacute;t quy y với H&ograve;a thượng trụ tr&igrave;. Trải qua bốn năm tinh tấn chuy&ecirc;n cần tu học, Ng&agrave;i vẫn chưa thỏa m&atilde;n được việc th&acirc;m nhập kinh tạng của m&igrave;nh, Ng&agrave;i b&egrave;n khăn g&oacute;i y b&aacute;t n&acirc;u s&ograve;ng l&ecirc;n S&agrave;i G&ograve;n t&aacute; t&uacute;c ở ch&ugrave;a Linh Sơn c&ocirc;ng quả v&agrave; tham học. Nơi đ&acirc;y, hạnh duy&ecirc;n được gặp H&ograve;a thượng L&ecirc; Phước Ch&iacute;, Ng&agrave;i cầu ph&aacute;p thọ học sớm khuya chuy&ecirc;n cần v&agrave; được truyền trao nhiều ph&aacute;p yếu.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1941, Ng&agrave;i được thọ tam đ&agrave;n Cụ T&uacute;c giới tại ch&ugrave;a Xu&acirc;n Quang ở thị x&atilde; Phan Thiết.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1943, Ng&agrave;i c&ugrave;ng sư huynh l&agrave; H&ograve;a thượng Th&iacute;ch Thới An c&ugrave;ng đi nhiều nơi tham học Phật ph&aacute;p. Hễ nghe đ&acirc;u c&oacute; bậc cao đức th&igrave; Ng&agrave;i t&igrave;m đến xin thụ gi&aacute;o. Do đ&oacute; Ng&agrave;i đ&atilde; gặp kh&ocirc;ng &iacute;t bậc Thầy lỗi lạc như H&ograve;a thượng H&ograve;a B&igrave;nh ở ch&ugrave;a Kim Hu&ecirc;, H&ograve;a thượng Bửu Đạt ở ch&ugrave;a Linh Sơn - Sa Đ&eacute;c...<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1944, Ng&agrave;i về l&agrave;m tr&uacute; tr&igrave; ch&ugrave;a Long An ở Sa Đ&eacute;c, tại đ&acirc;y diễn ra cuộc hội ngộ đ&aacute;ng nhớ giữa Ng&agrave;i, H&ograve;a thượng Thới An, H&ograve;a thượng Kh&aacute;nh Phước với một Tăng kh&aacute;ch đặc biệt l&agrave; H&ograve;a thượng H&agrave;nh Trụ, nh&acirc;n một h&ocirc;m H&ograve;a thượng dừng bước tr&ecirc;n đường hoằng ph&aacute;p. Cảm mến đạo hạnh v&agrave; kiến thức quảng b&aacute;c của H&ograve;a thượng H&agrave;nh Trụ, ba vị đ&atilde; cung thỉnh H&ograve;a thượng lưu lại ch&ugrave;a Long An để mở Phật Học Đường nu&ocirc;i dạy Tăng ch&uacute;ng tu học, t&ocirc;n H&ograve;a thượng l&agrave;m Huynh trưởng ki&ecirc;m H&oacute;a chủ ng&ocirc;i ch&ugrave;a n&agrave;y. Bốn vị đồng ph&aacute;t nguyện kết nghĩa l&agrave;m Tăng hữu trọn đời kề vai s&aacute;t c&aacute;nh b&ecirc;n nhau tr&ecirc;n con đường hoằng dương ch&aacute;nh ph&aacute;p, tiếp Tăng độ ch&uacute;ng.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1946, tiếp nối phong tr&agrave;o chấn hưng Phật gi&aacute;o sau khi chiến tranh chống Ph&aacute;p tạm lắng, cả bốn vị đều rời miền T&acirc;y l&ecirc;n S&agrave;i G&ograve;n th&agrave;nh lập ng&ocirc;i Tam Bảo ở v&ugrave;ng Kh&aacute;nh Hội đặt t&ecirc;n l&agrave; ch&ugrave;a Tăng Gi&agrave; để qui tụ Tăng ch&uacute;ng v&agrave; mở trường đ&agrave;o tạo. Đ&acirc;y cũng l&agrave; Phật học đường đầu ti&ecirc;n ở S&agrave;i G&ograve;n.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1947, nhận thấy Tăng ch&uacute;ng v&agrave; Ni ch&uacute;ng về học rất đ&ocirc;ng, c&aacute;c vị lại lập n&ecirc;n ng&ocirc;i gi&agrave; lam thứ hai ở gần đấy l&agrave; ch&ugrave;a Gi&aacute;c Nguy&ecirc;n để tiếp độ ch&uacute;ng Tăng, c&ograve;n ch&ugrave;a Tăng Gi&agrave; để cho Ni ch&uacute;ng thọ học. Về sau, ch&ugrave;a Tăng Gi&agrave; được đổi t&ecirc;n l&agrave; ch&ugrave;a Kim Li&ecirc;n.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1950, cảm mến &acirc;n đức v&agrave; đạo hạnh của Ng&agrave;i, Hội Vạn Thọ hiến c&uacute;ng cho Ng&agrave;i ng&ocirc;i ch&ugrave;a Vạn Thọ ở v&ugrave;ng T&acirc;n Định. Ng&agrave;i về đấy an trụ v&agrave; tiếp Tăng độ ch&uacute;ng. Từ đ&oacute; trải hơn 10 năm sau, Ng&agrave;i miệt m&agrave;i với c&ocirc;ng việc hoằng dương ch&aacute;nh ph&aacute;p v&agrave; c&oacute; c&ocirc;ng gầy dựng tr&ugrave;ng tu lại c&aacute;c ng&ocirc;i gi&agrave; lam kh&aacute;c như: Tăng Gi&agrave;, Gi&aacute;c Nguy&ecirc;n, Vạn Thọ, Thiền L&acirc;m, Gi&aacute;c Minh, Quan &Acirc;m, Thi&ecirc;n Phước, Hội T&ocirc;n...<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1960, Ng&agrave;i trở về l&agrave;m H&oacute;a chủ Tổ đ&igrave;nh Gi&aacute;c Nguy&ecirc;n nhiếp độ tứ ch&uacute;ng thay ba vị huynh đệ nhận tr&uacute; xứ kh&aacute;c để hoằng dương Phật ph&aacute;p.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1963, Ng&agrave;i đ&atilde; c&ugrave;ng chư Tăng,Ni v&agrave; quần ch&uacute;ng Phật tử tham gia đấu tranh chống ch&iacute;nh quyền Ng&ocirc; Đ&igrave;nh Diệm kỳ thị t&ocirc;n gi&aacute;o, đ&ograve;i b&igrave;nh đẳng v&agrave; tự do t&iacute;n ngưỡng.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1964, Gi&aacute;o Hội Phật Gi&aacute;o Việt Nam Thống Nhất ra đời, từ năm 1973 li&ecirc;n tiếp ba nhiệm kỳ, Ng&agrave;i được cử giữ chức Tổng Vụ Trưởng Tổng Vụ T&agrave;i ch&aacute;nh Kiến thiết.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1968, Ng&agrave;i được cử tham gia ph&aacute;i đo&agrave;n Phật gi&aacute;o Việt Nam đi thăm c&aacute;c nước Phật gi&aacute;o như Singapore, Đ&agrave;i Loan, Hồng K&ocirc;ng v&agrave; Nhật Bản.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1969, Ng&agrave;i l&agrave;m H&oacute;a chủ ki&ecirc;m Gi&aacute;m đốc Phật học viện Tổ đ&igrave;nh Gi&aacute;c Nguy&ecirc;n.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nDo giới hạnh trang nghi&ecirc;m, Ng&agrave;i được c&aacute;c nơi cung thỉnh l&agrave;m giới sư ở c&aacute;c giới đ&agrave;n để truyền trao giới ph&aacute;p cho Tăng Ni trong những năm sau như: Gi&aacute;o Thọ A X&agrave; L&ecirc; tại Đại giới đ&agrave;n ch&ugrave;a Ấn Quang năm 1973 v&agrave; Đại giới đ&agrave;n Quảng Đức năm 1977, Đệ nhất T&ocirc;n chứng Đại giới đ&agrave;n Thiện H&ograve;a tại Ấn Quang năm 1980.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1981, Gi&aacute;o Hội Phật Gi&aacute;o Việt Nam được th&agrave;nh lập, Ng&agrave;i được cung thỉnh l&agrave;m th&agrave;nh vi&ecirc;n Hội đồng Chứng minh Trung Ương.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNhững năm sau đ&oacute;, v&igrave; tuổi gi&agrave; sức yếu, Ng&agrave;i chỉ an tr&uacute; nơi Tổ đ&igrave;nh Gi&aacute;c Nguy&ecirc;n, chuy&ecirc;n t&acirc;m trực tiếp dạy bảo đồ ch&uacute;ng, khuyến gi&aacute;o t&iacute;n đồ. Ng&agrave;i c&ograve;n d&agrave;nh th&igrave; giờ dịch kinh, v&agrave; lấy việc v&atilde;ng sanh Tịnh độ l&agrave;m yếu chỉ. Cũng như những bậc Cao đức kh&aacute;c, biết trước ng&agrave;y giờ trở về c&otilde;i Phật, Ng&agrave;i khuy&ecirc;n ch&uacute;ng đệ tử cố gắng tu tr&igrave;, xả th&acirc;n v&igrave; đạo ph&aacute;p, noi gương đạo bạn giữa Ng&agrave;i v&agrave; ba vị ph&aacute;p hữu thực hiện lục h&ograve;a đo&agrave;n kết trong Tăng giới. V&agrave;o ng&agrave;y 23 th&aacute;ng 8 năm Gi&aacute;p T&yacute;, tức 18 th&aacute;ng 9 năm 1984, Ng&agrave;i xả nhục th&acirc;n th&acirc;u thần về c&otilde;i Phật, hưởng thọ 68 tuổi đời với 46 năm h&agrave;nh đạo.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nCuộc đời Ng&agrave;i l&agrave; một tấm gương th&acirc;n gi&aacute;o với hạnh nguyện chấp t&aacute;c lao động miệt m&agrave;i, thể hiện phương ch&acirc;m : &quot;Nhứt nhựt bất t&aacute;c, nhứt nhựt bất thực&quot;, Ng&agrave;i kh&ocirc;ng nề h&agrave; gian khổ trong mọi c&ocirc;ng t&aacute;c x&acirc;y dựng, tr&ugrave;ng hưng ch&ugrave;a chiền.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nV&agrave; c&ograve;n hơn thế, với một đức độ khả k&iacute;nh, Ng&agrave;i tự nghi&ecirc;m khắc với bản th&acirc;n bằng hạnh Đầu Đ&agrave; như Tổ Ca Diếp. Khắp nơi đều kh&acirc;m phục v&agrave; k&iacute;nh trọng một bậc cao Tăng thẳng thắn, trung thực đầy nghị lực hiếm c&oacute; như Ng&agrave;i.</span></span></div>\r\n', 'Hòa Thượng Thích Thiện Tường (1917-1984)', 0),
(100, 11, 'BBT Website', '2013-02-02 17:24:59', '<p style="margin: 0px; padding: 5px 0px; outline: 0px; max-width: 654px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;"><img alt="" src="http://vuonhoaphatgiao.com/uploads/noidung/images/tam-minh-vuonhoaphatgiao_com.jpg" style="margin: 0px; padding: 0px; outline: 0px; border: 0px; border-spacing: 0px; vertical-align: middle; max-width: 654px; width: 927px; height: 1200px;" /></p>\r\n\r\n<p style="margin: 0px; padding: 5px 0px; outline: 0px; max-width: 654px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;"><br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nCư sĩ T&acirc;m Minh - L&ecirc; Đ&igrave;nh Th&aacute;m sanh năm Đinh Dậu (1897) tại l&agrave;ng Đồng Mỹ (Ph&uacute; Mỹ), tổng Ph&uacute; Khương, phủ Điện B&agrave;n, tỉnh Quảng Nam, nay l&agrave; tỉnh Quảng Nam - Đ&agrave; Nẵng. &Ocirc;ng xuất th&acirc;n trong một gia đ&igrave;nh quyền qu&iacute; nhiều đời l&agrave;m quan. Th&acirc;n sinh l&agrave; Binh Bộ Thượng Thư L&ecirc; Đỉnh ở triều Tự Đức.</p>\r\n\r\n<p style="margin: 0px; padding: 5px 0px; outline: 0px; max-width: 654px; font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;">C&ugrave;ng với anh l&agrave; Y sĩ L&ecirc; Đ&igrave;nh Dương trực tiếp học chữ Nho với th&acirc;n phụ. Cả hai anh em đều tỏ ra th&ocirc;ng minh xuất ch&uacute;ng ngay từ thuở ni&ecirc;n thiếu. Ri&ecirc;ng &ocirc;ng từ nhỏ, đ&atilde; tỏ ra l&agrave; người c&oacute; năng khiếu văn chương thi ph&uacute;.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nTrong những năm theo học tại c&aacute;c trường Tiểu học, Trung học v&agrave; Đại học, &ocirc;ng đ&atilde; chiếm được cảm t&igrave;nh cả thầy v&agrave; bạn. Lu&ocirc;n lu&ocirc;n gi&agrave;nh thứ vị thủ khoa trong c&aacute;c kỳ thi tốt nghiệp cuối cấp.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n&Ocirc;ng tốt nghiệp Y sĩ Đ&ocirc;ng Dương (đậu Thủ Khoa) tại H&agrave; Nội năm 1916, v&agrave; sau đ&oacute; đậu Y khoa B&aacute;c sĩ năm 1930, ngạch Ph&aacute;p quốc, tại Y khoa Đại học đường H&agrave; Nội.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nKhi ra trường với danh hiệu Y sĩ, đ&uacute;ng l&uacute;c phong tr&agrave;o Duy T&acirc;n khởi nghĩa bị thất bại, b&agrave;o huynh Y sĩ L&ecirc; Đ&igrave;nh Dương bị Ph&aacute;p t&ugrave; đ&agrave;y tại Bu&ocirc;n M&ecirc; Thuột. &Ocirc;ng bị t&igrave;nh nghi v&agrave; lu&ocirc;n bị theo d&otilde;i. Từ năm 1916 đến năm 1923, &ocirc;ng được bổ nhiệm v&agrave; l&agrave;m việc tại c&aacute;c bệnh viện Hội An, B&igrave;nh Thuận, S&ocirc;ng Cầu, Qui Nhơn, Tuy H&ograve;a. Ch&iacute;nh trong thời gian n&agrave;y, &ocirc;ng nghi&ecirc;n cứu th&ecirc;m về triết l&yacute; Đ&ocirc;ng phương như Khổng, L&atilde;o v&agrave; Phật gi&aacute;o... Năm 1926, &ocirc;ng phụ tr&aacute;ch điều trị tại bệnh viện Hội An (Quảng Nam). Nh&acirc;n một buổi viếng cảnh tại ch&ugrave;a Tam Thai (tức Ngũ H&agrave;nh Sơn), &ocirc;ng được đọc b&agrave;i kệ của Tổ Huệ Năng ghi tr&ecirc;n v&aacute;ch ch&ugrave;a :<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nBồ đề bổn v&ocirc; thọ,<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nMinh cảnh diệc phi đ&agrave;i,<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nBổn lai v&ocirc; nhất vật,<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nH&agrave; xứ nh&aacute; trần ai.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nĐ&acirc;y l&agrave; lần đầu ti&ecirc;n &ocirc;ng tiếp x&uacute;c với triết l&yacute; Phật gi&aacute;o, b&agrave;i kệ tr&ecirc;n đ&atilde; gieo v&agrave;o t&acirc;m thức &ocirc;ng một ấn tượng s&acirc;u sắc về đạo Phật, mặc dầu thời điểm l&uacute;c ấy chưa cho ph&eacute;p &ocirc;ng đến gần với cửa thiền.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNh&acirc;n cụ Phan Chu Trinh mất, được tin ấy &ocirc;ng cũng như những người y&ecirc;u nước kh&aacute;c, đ&atilde; l&agrave;m lễ truy điệu tại nơi đang l&agrave;m việc, tỉnh Quảng Nam; mật th&aacute;m Ph&aacute;p biết được n&ecirc;n chuyển &ocirc;ng ra l&agrave;m việc ở H&agrave; Tĩnh.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1928, &ocirc;ng lại được thuy&ecirc;n chuyển về Huế, đảm tr&aacute;ch Y sĩ trưởng Viện b&agrave;o chế v&agrave; vi tr&ugrave;ng học Louis Pasteur, &ocirc;ng ph&aacute;t minh ra S&eacute;rum Normet. Ch&iacute;nh năm n&agrave;y, &ocirc;ng mới l&ecirc;n ch&ugrave;a Tr&uacute;c L&acirc;m, c&aacute;ch kinh đ&ocirc; Huế khoảng 7 c&acirc;y số ở sau đ&agrave;n Nam Giao, để thỉnh t&ocirc;n &yacute; về b&agrave;i kệ tr&ecirc;n với H&ograve;a thượng trụ tr&igrave; l&agrave; Ng&agrave;i Gi&aacute;c Ti&ecirc;n. Sau khi hiểu thấu đ&aacute;o b&agrave;i kệ, &ocirc;ng đ&atilde; thực sự chuyển hướng đời m&igrave;nh: Ph&aacute;t nguyện quy y Tam bảo, ăn trường trai từ đ&oacute; v&agrave; nghi&ecirc;n cứu học hỏi kinh điển Phật gi&aacute;o để hoằng h&oacute;a gi&uacute;p đời. Với ch&iacute; nguyện như tr&ecirc;n, &ocirc;ng được thọ tam quy ngũ giới với H&ograve;a thượng Gi&aacute;c Ti&ecirc;n, ph&aacute;p danh l&agrave; T&acirc;m Minh, ph&aacute;p tự l&agrave; Chi&ecirc;u Hải.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1929, &ocirc;ng thọ học th&ecirc;m với H&ograve;a thượng Phước Huệ ch&ugrave;a Thập Th&aacute;p, B&igrave;nh Định. H&ograve;a thượng chỉ đạo &ocirc;ng nghi&ecirc;n cứu c&aacute;c b&agrave;i giảng của Ng&agrave;i Th&aacute;i Hư đại sư ở Trung Hoa về c&aacute;ch thức tổ chức Phật gi&aacute;o, v&agrave; suy nghĩ cải c&aacute;ch thế n&agrave;o cho ph&ugrave; hợp với t&igrave;nh h&igrave;nh Phật gi&aacute;o nước nh&agrave;. &Ocirc;ng đ&atilde; đề đạt &yacute; kiến của m&igrave;nh l&ecirc;n chư H&ograve;a thượng Phước Huệ, Gi&aacute;c Ti&ecirc;n, Gi&aacute;c Nhi&ecirc;n, Tịnh Hạnh, Tịnh Khiết... v&agrave; đ&atilde; được c&aacute;c Ng&agrave;i chấp thuận.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1930, &ocirc;ng trở ra H&agrave; Nội thi bằng Y khoa B&aacute;c sĩ ngạch Ph&aacute;p. Khi trở về, &ocirc;ng vừa học Phật vừa l&agrave;m ng&agrave;nh y m&agrave; vẫn vượt xa c&aacute;c bạn đồng học trong thời gian ấy.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1932, Hội An Nam Phật Học ra đời, do c&aacute;c vị H&ograve;a thượng v&agrave; &ocirc;ng đứng ra chịu tr&aacute;ch nhiệm g&aacute;nh v&aacute;c c&ocirc;ng cuộc hoằng dương ch&aacute;nh ph&aacute;p. Trụ sở hội đầu ti&ecirc;n đặt ở ch&ugrave;a Tr&uacute;c L&acirc;m, sau đ&oacute; l&agrave; ch&ugrave;a Từ Đ&agrave;m. &Ocirc;ng l&agrave;m Hội trưởng v&agrave; c&aacute;c H&ograve;a thượng trong Ban Chứng minh l&agrave; cố vấn cho Hội. Hội bắt đầu truyền đạo với nhiều h&igrave;nh thức:<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nThuyết ph&aacute;p hằng nửa th&aacute;ng cho t&iacute;n đồ nghe tại ch&ugrave;a Từ Quang.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nMở trường đ&agrave;o tạo Tăng t&agrave;i cho Gi&aacute;o hội sau n&agrave;y.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nMở th&ecirc;m c&aacute;c chi hội để g&aacute;nh v&aacute;c tr&aacute;ch nhiệm hoằng dương ch&aacute;nh ph&aacute;p khắp c&aacute;c tỉnh.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nTh&agrave;nh lập Thanh ni&ecirc;n Đức dục (Phật học).<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nXuất bản tờ b&aacute;o Phật gi&aacute;o (Nguyệt san Vi&ecirc;n &Acirc;m).<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nThiết lập c&aacute;c t&ograve;ng l&acirc;m để chư Tăng tu học, v&agrave; đ&agrave;o tạo Tăng t&agrave;i.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nTừ năm 1930, bắt đầu xuất hiện những b&agrave;i viết của &ocirc;ng tr&ecirc;n tờ nguyệt san Vi&ecirc;n &Acirc;m. &Ocirc;ng tự tay viết cả truyện ngắn (k&yacute; t&ecirc;n T.M), truyện d&agrave;i (k&yacute; t&ecirc;n Ch&acirc;u Hải) v&agrave; truyện h&agrave;i hước (k&yacute; t&ecirc;n Ba Rảm).<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nTừ năm 1934-1945 l&agrave; những năm ho&agrave;n chỉnh c&aacute;c tổ chức của Phật gi&aacute;o v&agrave; hệ thống đ&agrave;o tạo Tăng t&agrave;i, c&aacute;c lớp Phật học cho thanh ni&ecirc;n. Kết quả m&agrave; &ocirc;ng đ&atilde; đ&oacute;ng g&oacute;p được trong những năm th&aacute;ng ấy, vẫn m&atilde;i m&atilde;i được ghi nhớ: Một thế hệ Tăng sĩ t&agrave;i ba đ&atilde; nở rộ, l&agrave;m nền tảng tuy&ecirc;n truyền ph&aacute;t huy ch&aacute;nh ph&aacute;p, đo&agrave;n kết Tăng Ni v&agrave; Phật tử, bảo vệ Phật gi&aacute;o trước những kh&oacute; khăn l&uacute;c bấy giờ. Ch&ugrave;a Từ Quang, nơi l&agrave;m giảng đường bước đầu để tuy&ecirc;n dương ch&aacute;nh ph&aacute;p cũng đ&atilde; thấy b&oacute;ng d&aacute;ng của &ocirc;ng trong chiếc &aacute;o d&agrave;i m&agrave;u đen v&agrave; chiếc khăn đ&oacute;ng tươm tất, th&agrave;nh k&iacute;nh đảnh lễ chư Tăng trước khi bước l&ecirc;n Ph&aacute;p t&ograve;a để giảng kinh cho họ.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1946, cuộc chiến tranh chống Ph&aacute;p b&ugrave;ng nổ, d&acirc;n ch&uacute;ng Huế tản cư, &ocirc;ng c&ugrave;ng gia đ&igrave;nh di tản về Quảng Nam. Từ năm 1947 đến 1949 &ocirc;ng l&agrave;m chủ Tịch Ủy ban H&agrave;nh ch&aacute;nh Kh&aacute;ng chiến miền Nam Trung bộ. Tại Li&ecirc;n khu V của v&ugrave;ng kh&aacute;ng chiến, &ocirc;ng tập họp một số đo&agrave;n vi&ecirc;n của đo&agrave;n Phật học Đức dục c&oacute; mặt trong v&ugrave;ng v&agrave; th&agrave;nh lập tổ chức &quot;Phật gi&aacute;o v&agrave; D&acirc;n chủ mới&quot; tại Bồng Sơn - B&igrave;nh Định, nghi&ecirc;n cứu việc tổng hợp gi&aacute;o l&yacute; Phật gi&aacute;o v&agrave; triết học M&aacute;c-L&ecirc; Nin.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nM&ugrave;a h&egrave; năm 1949, &ocirc;ng tập kết ra Bắc. Sau đ&oacute; được đề cử l&agrave;m Chủ tịch phong tr&agrave;o vận động H&ograve;a B&igrave;nh thế giới. Năm 1956, &ocirc;ng v&agrave; H&ograve;a thượng Tr&iacute; Độ tham dự ph&aacute;i đo&agrave;n sang dự đại hội Phật gi&aacute;o Buddha Jayanti tại Ấn Độ.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nNăm 1961, to&agrave;n bộ kinh Lăng Nghi&ecirc;m m&agrave; &ocirc;ng đ&atilde; d&agrave;y c&ocirc;ng phi&ecirc;n dịch v&agrave; ch&uacute; giải trong nhiều năm, đ&atilde; được đăng tải tr&ecirc;n b&aacute;o Vi&ecirc;n &Acirc;m trước đ&acirc;y được &ocirc;ng ho&agrave;n tất v&agrave; xuất bản tại ch&ugrave;a Qu&aacute;n Sứ, H&agrave; Nội. Sau đ&oacute;, s&aacute;ch được gia đ&igrave;nh &ocirc;ng t&aacute;i bản lưu h&agrave;nh rộng r&atilde;i ở miền Nam.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nTrong những năm ở Bắc, ngo&agrave;i ng&agrave;y giờ l&agrave;m việc cho Nh&agrave; nước, &ocirc;ng đến ch&ugrave;a Qu&aacute;n Sứ để dịch kinh v&agrave; giảng kinh, hướng dẫn việc tu học gi&uacute;p nh&agrave; ch&ugrave;a. Kết quả của những năm th&aacute;ng d&agrave;y c&ocirc;ng quả v&igrave; đạo ph&aacute;p v&agrave; d&acirc;n tộc ấy c&ograve;n được thể hiện qua c&aacute;c kinh s&aacute;ch uy&ecirc;n th&acirc;m như:<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nKinh Thủ Lăng Nghi&ecirc;m.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nLuận Nhơn Minh<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nĐại Thừa Khởi T&iacute;n Luận<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nB&aacute;t Th&uacute;c Qui Củ tụng.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nPhật Học thường thức.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nB&aacute;t Nh&atilde; T&acirc;m Kinh.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nLịch sử Phật gi&aacute;o Việt Nam v&agrave; Phật Tổ Th&iacute;ch Ca.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nT&acirc;m Minh - L&ecirc; Đ&igrave;nh Th&aacute;m tuyển tập (gồm 5 tập).<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nĐời người l&agrave; v&ocirc; thường, &ocirc;ng đ&atilde; ngộ l&yacute; ấy v&agrave; đ&atilde; b&igrave;nh thản ra đi ng&agrave;y 23-4-1969 (nhằm 7-3 &acirc;l, năm Kỷ Dậu), sau khi đ&atilde; đ&agrave;m đạo lần cuối c&ugrave;ng với H&ograve;a thượng Đ&ocirc;n Hậu, tại bệnh viện Việt - X&ocirc;, H&agrave; Nội. Thọ 73 tuổi v&agrave; 42 năm phụng sự Tam Bảo.<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\n<br style="margin: 0px; padding: 0px; outline: 0px;" />\r\nTinh th&agrave;nh trong giai đoạn chấn hưng Phật gi&aacute;o giữa thế kỷ XX, Cư sĩ T&acirc;m Minh L&ecirc; Đ&igrave;nh Th&aacute;m quả l&agrave; bậc tiền bối hữu c&ocirc;ng thật s&aacute;ng ch&oacute;i, &ocirc;ng đ&atilde; vượt qua ranh giới h&igrave;nh thức để tựu th&agrave;nh đạo nghiệp cao qu&iacute; cho h&agrave;ng hậu tấn ngưỡng vọng d&ugrave; xuất gia hay tại gia.</p>\r\n', 'Cư Sĩ Tâm Minh - Lê Đình Thám (1897-1969)', 0);
INSERT INTO `chuathienquang_news` (`id`, `id_category`, `author`, `date`, `content`, `title`, `type`) VALUES
(101, 15, 'BBT Website', '2013-02-02 17:29:01', '<h2 class="pHead" style="margin: 0px; padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; font-family: ''Times New Roman'', Times, serif; line-height: 22px; color: rgb(17, 17, 17); text-align: justify; background-color: rgb(238, 238, 238); font-size: 17px !important;">Tim Cook - CEO của Apple hay Jeff Bezos - CEO của Amazon hứa hẹn sẽ l&agrave; những người quyết định số phận của l&agrave;ng c&ocirc;ng nghệ trong tương lai.</h2>\r\n\r\n<p class="pSubTitle" style="padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; font-family: ''Times New Roman'', Times, serif; font-size: 17px; line-height: 22px; color: rgb(17, 17, 17); text-align: justify; background-color: rgb(238, 238, 238); margin: 5px auto !important;">1. Tim Cook (Apple)</p>\r\n\r\n<p class="pSubTitle" style="padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; font-family: ''Times New Roman'', Times, serif; font-size: 17px; line-height: 22px; color: rgb(17, 17, 17); background-color: rgb(238, 238, 238); text-align: center; margin: 5px auto !important;"><img align="Middle" alt="" border="0" class="oImage" height="317" src="http://media.tinmoi.vn//2012/02/13/17_32_1329120013_31_timcook-420-100.jpg" style="margin: auto; padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; display: block;" width="420" /></p>\r\n\r\n<p class="pBody" style="padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; font-family: ''Times New Roman'', Times, serif; font-size: 17px; line-height: 22px; color: rgb(17, 17, 17); text-align: justify; background-color: rgb(238, 238, 238); margin: 5px auto !important;">Giữ vai tr&ograve; l&agrave; gi&aacute;m đốc điều h&agrave;nh của Apple, Tim Cook được tạp ch&iacute; Fortune đ&aacute;nh gi&aacute; l&agrave; người c&oacute; c&ocirc;ng đầu trong việc gi&uacute;p Apple vượt qua gi&ocirc;ng tố những năm 1998, khi c&ocirc;ng ty n&agrave;y đang tr&ecirc;n bờ vực ph&aacute; sản để trở th&agrave;nh cỗ m&aacute;y kiếm tiền như hiện nay.</p>\r\n\r\n<p class="pBody" style="padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; font-family: ''Times New Roman'', Times, serif; font-size: 17px; line-height: 22px; color: rgb(17, 17, 17); text-align: justify; background-color: rgb(238, 238, 238); margin: 5px auto !important;">Hệ thống chuỗi cửa h&agrave;ng b&aacute;n lẻ Apple Store l&agrave; do &ocirc;ng một tay g&acirc;y dựng. Cook cũng ch&iacute;nh l&agrave; người quyết định đ&oacute;ng cửa c&aacute;c nh&agrave; m&aacute;y sản xuất linh kiện của Apple v&agrave; đặt h&agrave;ng với c&aacute;c đối t&aacute;c nước ngo&agrave;i để tận dụng nguồn lao động gi&aacute; rẻ tại Trung Quốc v&agrave; Brazil. Tuy kh&ocirc;ng c&oacute; tầm nh&igrave;n xa như Steve Jobs nhưng Tim Cook l&agrave; một trong những nh&acirc;n t&agrave;i s&aacute;ng gi&aacute; nhất m&agrave; Apple c&oacute; được.</p>\r\n\r\n<p class="pSubTitle" style="padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; font-family: ''Times New Roman'', Times, serif; font-size: 17px; line-height: 22px; color: rgb(17, 17, 17); text-align: justify; background-color: rgb(238, 238, 238); margin: 5px auto !important;">2. Jeff Bezos (Amazon)</p>\r\n\r\n<p class="pSubTitle" style="padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; font-family: ''Times New Roman'', Times, serif; font-size: 17px; line-height: 22px; color: rgb(17, 17, 17); background-color: rgb(238, 238, 238); text-align: center; margin: 5px auto !important;"><img align="Middle" alt="" border="0" class="oImage" height="315" src="http://media.tinmoi.vn//2012/02/13/17_32_1329120014_07_bezos-420-100.jpg" style="margin: auto; padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; display: block;" width="420" /></p>\r\n\r\n<p class="pBody" style="padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; font-family: ''Times New Roman'', Times, serif; font-size: 17px; line-height: 22px; color: rgb(17, 17, 17); text-align: justify; background-color: rgb(238, 238, 238); margin: 5px auto !important;">Ngay sau khi thi&ecirc;n t&agrave;i Steve Jobs qua đời, giới c&ocirc;ng nghệ đ&atilde; nh&igrave;n nhận người s&aacute;ng lập ki&ecirc;m CEO của Amazon ch&iacute;nh l&agrave; nh&acirc;n vật thừa kế xứng đ&aacute;ng của cựu CEO Apple. T&agrave;i năng của Bezos l&agrave; điều kh&ocirc;ng thể b&agrave;n c&atilde;i nhưng điểm nổi bật nhất của &quot;&ocirc;ng tr&ugrave;m&quot; thương mại điện tử n&agrave;y l&agrave; khả năng chiếm lĩnh thị trường trước khi c&aacute;c đối thủ kịp nhận ra tiềm năng của n&oacute;. &Ocirc;ng l&agrave; người g&oacute;p c&ocirc;ng đầu trong việc g&acirc;y dựng Amazon từ một c&ocirc;ng ty b&aacute;n s&aacute;ch trực tuyến trở th&agrave;nh một trong những trang thương mại điện tử lớn nhất thế giới hiện nay.&nbsp;</p>\r\n\r\n<p class="pSubTitle" style="padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; font-family: ''Times New Roman'', Times, serif; font-size: 17px; line-height: 22px; color: rgb(17, 17, 17); text-align: justify; background-color: rgb(238, 238, 238); margin: 5px auto !important;">3. Jonathan Ive (Apple)</p>\r\n\r\n<p class="pSubTitle" style="padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; font-family: ''Times New Roman'', Times, serif; font-size: 17px; line-height: 22px; color: rgb(17, 17, 17); background-color: rgb(238, 238, 238); text-align: center; margin: 5px auto !important;"><img align="Middle" alt="" border="0" class="oImage" height="309" src="http://media.tinmoi.vn//2012/02/13/17_32_1329120014_83_jonathanive-420-100.jpg" style="margin: auto; padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; display: block;" width="420" /></p>\r\n\r\n<p class="pBody" style="padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; font-family: ''Times New Roman'', Times, serif; font-size: 17px; line-height: 22px; color: rgb(17, 17, 17); text-align: justify; background-color: rgb(238, 238, 238); margin: 5px auto !important;">Th&agrave;nh c&ocirc;ng của Apple vẫn thường được nhắc đến gắn liền với c&aacute;i t&ecirc;n&nbsp;Steve Jobs nhờ bộ &oacute;c điều h&agrave;nh quản l&yacute; v&agrave; khả năng marketing si&ecirc;u đẳng. Chỉ đến thời gian gần đ&acirc;y, người ta mới biết đến một nh&acirc;n vật ngồi trong ph&ograve;ng th&iacute; nghiệm v&agrave; vẽ ra những &yacute; tưởng thi&ecirc;n t&agrave;i như Ive.</p>\r\n\r\n<p class="pBody" style="padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; font-family: ''Times New Roman'', Times, serif; font-size: 17px; line-height: 22px; color: rgb(17, 17, 17); text-align: justify; background-color: rgb(238, 238, 238); margin: 5px auto !important;">Jonathan Ive l&agrave; trưởng ph&ograve;ng thiết kế cho mọi sản phẩm của Apple. Bằng đ&ocirc;i tay t&agrave;i hoa của m&igrave;nh, Ive lần lượt tạo ra những sản phẩm đ&atilde; gi&uacute;p tạo n&ecirc;n thương hiệu cho Apple như iPod, iPhone, iPad hay c&aacute;c d&ograve;ng sản phẩm Macbook. Daily Mail thậm ch&iacute; c&ograve;n vinh danh Ive l&agrave; &quot;thi&ecirc;n t&agrave;i thiết kế&quot;.</p>\r\n\r\n<p class="pSubTitle" style="padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; font-family: ''Times New Roman'', Times, serif; font-size: 17px; line-height: 22px; color: rgb(17, 17, 17); text-align: justify; background-color: rgb(238, 238, 238); margin: 5px auto !important;">4. Marissa Mayer (Google)</p>\r\n\r\n<p class="pSubTitle" style="padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; font-family: ''Times New Roman'', Times, serif; font-size: 17px; line-height: 22px; color: rgb(17, 17, 17); background-color: rgb(238, 238, 238); text-align: center; margin: 5px auto !important;"><img align="Middle" alt="" border="0" class="oImage" height="315" src="http://media.tinmoi.vn//2012/02/13/17_32_1329120015_58_net184interviewmmayer14-420-100.jpg" style="margin: auto; padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; display: block;" width="420" /></p>\r\n\r\n<p class="pBody" style="padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; font-family: ''Times New Roman'', Times, serif; font-size: 17px; line-height: 22px; color: rgb(17, 17, 17); text-align: justify; background-color: rgb(238, 238, 238); margin: 5px auto !important;">Marissa Ann Mayer l&agrave; kỹ sư nữ đầu ti&ecirc;n được nhận v&agrave;o l&agrave;m việc tai Google v&agrave; l&agrave; một trong 20 nh&acirc;n vi&ecirc;n đầu ti&ecirc;n g&acirc;y dựng n&ecirc;n Google từ những ng&agrave;y đầu khởi lập. Mới đ&acirc;y, c&ocirc; cũng được tạp ch&iacute; Fortune vinh danh l&agrave; người trẻ tuổi nhất trong danh s&aacute;ch những người phụ nữ quyền lực nhất của thung lũng Silicon.</p>\r\n\r\n<p class="pBody" style="padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; font-family: ''Times New Roman'', Times, serif; font-size: 17px; line-height: 22px; color: rgb(17, 17, 17); text-align: justify; background-color: rgb(238, 238, 238); margin: 5px auto !important;">C&ocirc; nổi tiếng bởi khả năng ph&aacute;t hiện v&agrave; biến những &yacute; tưởng s&aacute;ng tạo trở th&agrave;nh thực tế. Sau nhiều năm phụ tr&aacute;ch việc quản l&iacute; v&agrave; thiết kế cho c&aacute;c sản phẩm của Google, hiện tại Marissa Ann Mayer đang giữ chức ph&oacute; chủ tịch sản phẩm t&igrave;m kiếm v&agrave; trải nghiệm người d&ugrave;ng của Google.</p>\r\n\r\n<p class="pSubTitle" style="padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; font-family: ''Times New Roman'', Times, serif; font-size: 17px; line-height: 22px; color: rgb(17, 17, 17); text-align: justify; background-color: rgb(238, 238, 238); margin: 5px auto !important;">5. Joichi Ito (MIT)</p>\r\n\r\n<p class="pSubTitle" style="padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; font-family: ''Times New Roman'', Times, serif; font-size: 17px; line-height: 22px; color: rgb(17, 17, 17); background-color: rgb(238, 238, 238); text-align: center; margin: 5px auto !important;"><img align="Middle" alt="" border="0" class="oImage" height="315" src="http://media.tinmoi.vn//2012/02/13/17_32_1329120016_39_net204interviewjoi3-420-100.jpg" style="margin: auto; padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; display: block;" width="420" /></p>\r\n\r\n<p class="pBody" style="padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; font-family: ''Times New Roman'', Times, serif; font-size: 17px; line-height: 22px; color: rgb(17, 17, 17); text-align: justify; background-color: rgb(238, 238, 238); margin: 5px auto !important;">Joichi &ldquo; Joi&rdquo; l&agrave; một doanh nh&acirc;n Nhật Bản, &ocirc;ng đang đảm nhận nhiều chức vụ như chủ tịch của Creative Commons (một tổ chức phi lợi nhuận t&ocirc;n vinh t&iacute;nh s&aacute;ng tạo của cộng đồng), gi&aacute;m đốc của MIT Media Lab, th&agrave;nh vi&ecirc;n hội đồng quản trị Mozilla, nh&agrave; hoạt động v&igrave; nh&acirc;n quyền v&agrave; l&agrave; một trong 100 nh&agrave; tư tưởng h&agrave;ng đầu thế giới do tạp ch&iacute; Foreign Policy b&igrave;nh chọn.</p>\r\n\r\n<p class="pSubTitle" style="padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; font-family: ''Times New Roman'', Times, serif; font-size: 17px; line-height: 22px; color: rgb(17, 17, 17); text-align: justify; background-color: rgb(238, 238, 238); margin: 5px auto !important;">6. Shigeru Miyamoto (Nintendo)</p>\r\n\r\n<p class="pSubTitle" style="padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; font-family: ''Times New Roman'', Times, serif; font-size: 17px; line-height: 22px; color: rgb(17, 17, 17); background-color: rgb(238, 238, 238); text-align: center; margin: 5px auto !important;"><img align="Middle" alt="" border="0" class="oImage" height="315" src="http://media.tinmoi.vn//2012/02/13/17_32_1329120017_22_miyamoto-420-100.jpg" style="margin: auto; padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; display: block;" width="420" /></p>\r\n\r\n<p class="pBody" style="padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; font-family: ''Times New Roman'', Times, serif; font-size: 17px; line-height: 22px; color: rgb(17, 17, 17); text-align: justify; background-color: rgb(238, 238, 238); margin: 5px auto !important;">Chưa c&oacute; ai c&oacute; thể so s&aacute;nh được với những th&agrave;nh tựu m&agrave; Shigeru Miyamoto đạt được trong ng&agrave;nh c&ocirc;ng nghiệp game thế giới. &Ocirc;ng l&agrave; người s&aacute;ng tạo ra c&aacute;c tr&ograve; chơi điện tử thuộc h&agrave;ng &quot;kinh điển&quot; như Mario, Donkey Kong, The Legend of Zelda, Star Fox, Pikmin v&agrave; Nintendogs. Miyamoto l&agrave; một trong những nh&agrave; thiết kế game nổi tiếng nhất thế giới, &ocirc;ng được coi l&agrave; &ldquo;cha đẻ của c&aacute;c tr&ograve; chơi hiện đại&rdquo;, người tạo n&ecirc;n cuộc c&aacute;ch mạng về game điện tử đầu thập ni&ecirc;n 1980.</p>\r\n\r\n<p class="pSubTitle" style="padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; font-family: ''Times New Roman'', Times, serif; font-size: 17px; line-height: 22px; color: rgb(17, 17, 17); text-align: justify; background-color: rgb(238, 238, 238); margin: 5px auto !important;">7. Sheryl Sandberg (Facebook)</p>\r\n\r\n<p class="pSubTitle" style="padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; font-family: ''Times New Roman'', Times, serif; font-size: 17px; line-height: 22px; color: rgb(17, 17, 17); background-color: rgb(238, 238, 238); text-align: center; margin: 5px auto !important;"><img align="Middle" alt="" border="0" class="oImage" height="315" src="http://media.tinmoi.vn//2012/02/13/17_32_1329120018_0_sandberg-420-100.jpg" style="margin: auto; padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; display: block;" width="420" /></p>\r\n\r\n<p class="pBody" style="padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; font-family: ''Times New Roman'', Times, serif; font-size: 17px; line-height: 22px; color: rgb(17, 17, 17); text-align: justify; background-color: rgb(238, 238, 238); margin: 5px auto !important;">C&ugrave;ng với Mark Zuckerberg, Sandberg được v&iacute; như l&agrave; bộ n&atilde;o của Facebook. C&ocirc; giữ vai tr&ograve; quan trọng gi&uacute;p Facebook trở th&agrave;nh một trong những mạng x&atilde; hội được nhiều người biết đến nhất. C&ocirc; điều h&agrave;nh tất cả c&aacute;c hoạt động của c&ocirc;ng ty như b&aacute;n h&agrave;ng, tiếp thị, ph&aacute;t triển kinh doanh v&agrave; truyền th&ocirc;ng. N&oacute;i c&aacute;ch kh&aacute;c, nếu Mark Zuckerberg x&acirc;y dựng Facebook th&igrave; Sandberg l&agrave; người mang lợi nhuận về cho c&ocirc;ng ty n&agrave;y.</p>\r\n\r\n<p class="pSubTitle" style="padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; font-family: ''Times New Roman'', Times, serif; font-size: 17px; line-height: 22px; color: rgb(17, 17, 17); text-align: justify; background-color: rgb(238, 238, 238); margin: 5px auto !important;">8. Gabe Newell (Valve)</p>\r\n\r\n<p class="pSubTitle" style="padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; font-family: ''Times New Roman'', Times, serif; font-size: 17px; line-height: 22px; color: rgb(17, 17, 17); background-color: rgb(238, 238, 238); text-align: center; margin: 5px auto !important;"><img align="Middle" alt="" border="0" class="oImage" height="315" src="http://media.tinmoi.vn//2012/02/13/17_32_1329120018_76_gabe-420-100.jpg" style="margin: auto; padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; display: block;" width="420" /></p>\r\n\r\n<p class="pBody" style="padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; font-family: ''Times New Roman'', Times, serif; font-size: 17px; line-height: 22px; color: rgb(17, 17, 17); text-align: justify; background-color: rgb(238, 238, 238); margin: 5px auto !important;">Gabel Newell l&agrave; nh&agrave; đồng s&aacute;ng lập Valve, c&ocirc;ng ty sản suất c&aacute;c tr&ograve; chơi điện tử nổi tiếng như Half-life, Team Fortres v&agrave; Portal. &Ocirc;ng ấy l&agrave; một trong những người th&ocirc;ng minh nhất m&agrave; t&ocirc;i từng gặp&rdquo;, John Riccitiello, Gi&aacute;m đốc điều h&agrave;nh một trong những nh&agrave; ph&aacute;t h&agrave;nh game lớn nhất thế giới, Electronic Arts n&oacute;i. &ldquo;&Ocirc;ng ấy c&oacute; những hiểu biết nhanh nhạy về c&aacute;ch tạo ra c&aacute;c game hấp dẫn&rdquo;. C&aacute;c tr&ograve; chơi của Valve được c&aacute;c game thủ tr&ecirc;n to&agrave;n thế giới rất y&ecirc;u mến.</p>\r\n\r\n<p class="pSubTitle" style="padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; font-family: ''Times New Roman'', Times, serif; font-size: 17px; line-height: 22px; color: rgb(17, 17, 17); text-align: justify; background-color: rgb(238, 238, 238); margin: 5px auto !important;">9. Sundar Pichai (Google)</p>\r\n\r\n<p class="pSubTitle" style="padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; font-family: ''Times New Roman'', Times, serif; font-size: 17px; line-height: 22px; color: rgb(17, 17, 17); background-color: rgb(238, 238, 238); text-align: center; margin: 5px auto !important;"><img align="Middle" alt="" border="0" class="oImage" height="315" src="http://media.tinmoi.vn//2012/02/13/17_32_1329120019_5_sundar-420-100.jpg" style="margin: auto; padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; display: block;" width="420" /></p>\r\n\r\n<p class="pBody" style="padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; font-family: ''Times New Roman'', Times, serif; font-size: 17px; line-height: 22px; color: rgb(17, 17, 17); text-align: justify; background-color: rgb(238, 238, 238); margin: 5px auto !important;">Theo tiết lộ của TechCrunch, c&aacute;ch đ&acirc;y chưa l&acirc;u, Google đ&atilde; chấp nhận trả h&agrave;ng chục triệu USD để giữ ch&acirc;n Sundar Pichai khi anh c&oacute; &yacute; định chuyển sang l&agrave;m việc cho Twitter. Ngay lập tức, ch&agrave;ng trai n&agrave;y đ&atilde; chứng tỏ được gi&aacute; trị của bản th&acirc;n m&igrave;nh. Dưới b&agrave;n tay của Sundar Pichai, tr&igrave;nh duyệt Chrome đ&atilde; ph&aacute;t triển một c&aacute;ch ch&oacute;ng mặt, &quot;vượt mặt&quot; cả Firefox để chiếm ng&ocirc;i vị &aacute; qu&acirc;n trong thị phần tr&igrave;nh duyệt hồi cuối năm 2011 vừa qua.</p>\r\n\r\n<p class="pSubTitle" style="padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; font-family: ''Times New Roman'', Times, serif; font-size: 17px; line-height: 22px; color: rgb(17, 17, 17); text-align: justify; background-color: rgb(238, 238, 238); margin: 5px auto !important;">10. Marc Benioff (Salesforce)</p>\r\n\r\n<p class="pSubTitle" style="padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; font-family: ''Times New Roman'', Times, serif; font-size: 17px; line-height: 22px; color: rgb(17, 17, 17); background-color: rgb(238, 238, 238); text-align: center; margin: 5px auto !important;"><img align="Middle" alt="" border="0" class="oImage" height="280" src="http://media.tinmoi.vn//2012/02/13/17_32_1329120020_26_benioff-420-100.jpg" style="margin: auto; padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; display: block;" width="420" /></p>\r\n\r\n<p class="pBody" style="padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; font-family: ''Times New Roman'', Times, serif; font-size: 17px; line-height: 22px; color: rgb(17, 17, 17); text-align: justify; background-color: rgb(238, 238, 238); margin: 5px auto !important;">Chủ tịch ki&ecirc;m CEO của Salesfore.com tự nhận m&igrave;nh l&agrave; &ldquo; học tr&ograve; của Steve Jobs&rdquo;. Marc Benioff được coi l&agrave; người đi ti&ecirc;n phong trong lĩnh vực điện to&aacute;n đ&aacute;m m&acirc;y. Trong hơn một thập kỷ, khi c&ocirc;ng nghệ n&agrave;y c&ograve;n chưa thịnh h&agrave;nh, &ocirc;ng đ&atilde; phải lắng nghe kh&ocirc;ng &iacute;t những &yacute; kiến chỉ tr&iacute;ch về c&ocirc;ng việc m&agrave; m&igrave;nh đang theo đuổi.</p>\r\n\r\n<p class="pBody" style="padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; font-family: ''Times New Roman'', Times, serif; font-size: 17px; line-height: 22px; color: rgb(17, 17, 17); text-align: justify; background-color: rgb(238, 238, 238); margin: 5px auto !important;">Tuy nhi&ecirc;n, đến thời điểm hiện tại, Salesforce của &ocirc;ng c&oacute; thể kiếm về khoảng 3 tỉ USD trong năm 2012, điều m&agrave; những đối thủ lớn như Amazon hay Google cũng kh&ocirc;ng thể l&agrave;m được.</p>\r\n\r\n<p class="pAuthor" style="padding: 0px; list-style: none; text-rendering: optimizelegibility; outline: none; font-family: ''Times New Roman'', Times, serif; font-size: 17px; line-height: 22px; color: rgb(17, 17, 17); text-align: justify; background-color: rgb(238, 238, 238); margin: 5px auto !important;">Th&agrave;nh Duy</p>\r\n', '10 nhân tài kiệt xuất trong làng công nghệ thế giới', 0),
(102, 15, 'Song Tử', '2013-02-02 17:30:11', '<p style="padding: 0px; margin: 0px 0px 16px; color: rgb(0, 0, 0); font-family: ''Times New Roman''; font-size: 16px; line-height: normal; background-color: rgb(248, 248, 248);"><strong>(MegaFun) - Tại những cuộc thi hay show truyền h&igrave;nh tr&ecirc;n thế giới, đ&atilde; c&oacute; kh&ocirc;ng &iacute;t những gương mặt gốc Việt g&acirc;y ấn tượng l&agrave;m x&ocirc;n xao cộng đồng mạng.</strong></p>\r\n\r\n<p style="padding: 0px; margin: 0px 0px 16px; color: rgb(0, 0, 0); font-family: ''Times New Roman''; font-size: 16px; line-height: normal; background-color: rgb(248, 248, 248);">H&atilde;y c&ugrave;ng MegaFun điểm qua những c&aacute;i t&ecirc;n ti&ecirc;u biểu từng đạt th&agrave;nh t&iacute;ch tại một số cuộc thi.<br />\r\n<br />\r\n<strong>1. Thanh B&ugrave;i với Australian Idol 2008</strong></p>\r\n\r\n<p style="padding: 0px; margin: 0px 0px 16px; color: rgb(0, 0, 0); font-family: ''Times New Roman''; font-size: 16px; line-height: normal; background-color: rgb(248, 248, 248);"><find><a class="find" href="http://vinhlong.megafun.vn/find?q=Thanh%20B%C3%B9i" style="text-decoration: initial; outline: none medium; color: rgb(0, 57, 100);" target="_blank">Thanh B&ugrave;i</a></find>&nbsp;trước khi trở về&nbsp;<find><a class="find" href="http://vinhlong.megafun.vn/find?q=Vi%E1%BB%87t%20Nam" style="text-decoration: initial; outline: none medium; color: rgb(0, 57, 100);" target="_blank">Việt Nam</a></find>&nbsp;ghi dấu ấn với những s&aacute;ng t&aacute;c được nhiều bạn trẻ y&ecirc;u th&iacute;ch đ&atilde; từng rất nổi tiếng tại cuộc thi&nbsp;<em>Australian Idol 2008</em>. Ch&agrave;ng trai sinh năm 1983 n&agrave;y lọt v&agrave;o top 10 cuộc thi v&agrave; ca kh&uacute;c&nbsp;<em>The winner takes it all</em>&nbsp;anh thể hiện đ&atilde; một thời g&acirc;y sốt tr&ecirc;n youtube.</p>\r\n\r\n<table align="center" class="image center" style="border-collapse: collapse; background-color: rgb(233, 233, 233); width: 410px; color: rgb(0, 68, 136); margin-bottom: 10px; font-family: ''Times New Roman''; font-size: 16px; line-height: normal;" width="400">\r\n	<tbody>\r\n		<tr>\r\n			<td style="text-align: center; padding: 5px;"><img alt="Ca sĩ kiêm nhạc sĩ Thanh Bùi" src="http://vinhlong.megafun.vn/dataimages/201209/original/images784269_images228072_ThanhBui_3.jpg" style="margin: 0px; border: 0px; outline: none 0px; padding: 0px; list-style-type: none; max-width: 455px; height: 684px; width: 400px;" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td class="image_desc" style="text-align: center; padding: 5px;">Ca sĩ ki&ecirc;m nhạc sĩ Thanh B&ugrave;i</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style="padding: 0px; margin: 0px 0px 16px; color: rgb(0, 0, 0); font-family: ''Times New Roman''; font-size: 16px; line-height: normal; background-color: rgb(248, 248, 248);">Sinh ra ở&nbsp;<find><a class="find" href="http://vinhlong.megafun.vn/find?q=Adelaide" style="text-decoration: initial; outline: none medium; color: rgb(0, 57, 100);" target="_blank">Adelaide</a></find>, miền Nam nước&nbsp;<find><a class="find" href="http://vinhlong.megafun.vn/find?q=%C3%9Ac" style="text-decoration: initial; outline: none medium; color: rgb(0, 57, 100);" target="_blank">&Uacute;c</a></find>&nbsp;v&agrave; lớn l&ecirc;n từ&nbsp;<find><a class="find" href="http://vinhlong.megafun.vn/find?q=Abbotsford" style="text-decoration: initial; outline: none medium; color: rgb(0, 57, 100);" target="_blank">Abbotsford</a></find>, tiểu bang<find><a class="find" href="http://vinhlong.megafun.vn/find?q=Victoria" style="text-decoration: initial; outline: none medium; color: rgb(0, 57, 100);" target="_blank">Victoria</a></find>, Thanh B&ugrave;i đều c&oacute; cha mẹ l&agrave; người Việt Nam. Ch&agrave;ng trai c&oacute; v&oacute;c d&aacute;ng cao to v&agrave; gương mặt điển trai n&agrave;y bộc lộ niềm đam m&ecirc; ca h&aacute;t từ rất sớm. Với khả năng s&aacute;ng t&aacute;c lẫn chơi nhạc cụ, tại&nbsp;<em>Australian Idol&nbsp;</em>năm 2008, Thanh B&ugrave;i đ&atilde; dừng bước ở top 8 v&agrave; đ&acirc;y được xem l&agrave; th&agrave;nh t&iacute;ch tốt nhất của một người gốc Việt tại c&aacute;c cuộc thi ca h&aacute;t tr&ecirc;n thế giới.</p>\r\n\r\n<p style="padding: 0px; margin: 0px 0px 16px; color: rgb(0, 0, 0); font-family: ''Times New Roman''; font-size: 16px; line-height: normal; background-color: rgb(248, 248, 248);">Sau th&agrave;nh c&ocirc;ng bất ngờ đ&oacute;, Thanh B&ugrave;i đ&atilde; trở về Việt Nam l&agrave;m hoạt động nghệ thuật khi đ&atilde; c&oacute; đằng sau một sự ủng hộ lớn từ những người y&ecirc;u mến anh tại&nbsp;<em>Australian Idol.</em></p>\r\n\r\n<table align="center" class="image center" style="border-collapse: collapse; background-color: rgb(233, 233, 233); width: 465px; color: rgb(0, 68, 136); margin-bottom: 10px; font-family: ''Times New Roman''; font-size: 16px; line-height: normal;" width="490">\r\n	<tbody>\r\n		<tr>\r\n			<td style="text-align: center; padding: 5px;"><img alt="Thanh Bùi cùng các thí sinh khác tại Australian Idol 2008 (anh đứng thứ 4 từ trái sang)" src="http://vinhlong.megafun.vn/dataimages/201209/original/images784271_131bui1.jpg" style="margin: 0px; border: 0px; outline: none 0px; padding: 0px; list-style-type: none; max-width: 455px; height: 326px; width: 490px;" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td class="image_desc" style="text-align: center; padding: 5px;">Thanh B&ugrave;i c&ugrave;ng c&aacute;c th&iacute; sinh kh&aacute;c tại&nbsp;<em>Australian Idol</em>&nbsp;2008 (anh đứng thứ 4 từ tr&aacute;i sang)</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style="padding: 0px; margin: 0px 0px 16px; color: rgb(0, 0, 0); font-family: ''Times New Roman''; font-size: 16px; line-height: normal; background-color: rgb(248, 248, 248);">T&iacute;nh đến thời điểm n&agrave;y, Thanh B&ugrave;i đ&atilde; c&oacute; rất nhiều c&aacute;c s&aacute;ng t&aacute;c hit trong đ&oacute; phải kể đến&nbsp;<em>T&igrave;nh về nơi đ&acirc;u</em>&nbsp;song ca c&ugrave;ng nữ diva&nbsp;<find><a class="find" href="http://vinhlong.megafun.vn/find?q=Th%C3%A1i%20Lan" style="text-decoration: initial; outline: none medium; color: rgb(0, 57, 100);" target="_blank">Th&aacute;i Lan</a></find>&nbsp;<find><a class="find" href="http://vinhlong.megafun.vn/find?q=Tata%20Young" style="text-decoration: initial; outline: none medium; color: rgb(0, 57, 100);" target="_blank">Tata Young</a></find>, hay<em>Lặng thầm một t&igrave;nh y&ecirc;</em>u h&aacute;t c&ugrave;ng&nbsp;<find><a class="find" href="http://vinhlong.megafun.vn/find?q=H%E1%BB%93%20Ng%E1%BB%8Dc%20H%C3%A0" style="text-decoration: initial; outline: none medium; color: rgb(0, 57, 100);" target="_blank">Hồ Ngọc H&agrave;</a></find>. Ti&ecirc;u biểu, b&agrave;i h&aacute;t&nbsp;<em>Đường về xa x&ocirc;i</em>&nbsp;với đoạn video clip tập hợp những h&igrave;nh ảnh về&nbsp;<find><a class="find" href="http://vinhlong.megafun.vn/find?q=S%C3%A0i%20G%C3%B2n" style="text-decoration: initial; outline: none medium; color: rgb(0, 57, 100);" target="_blank">S&agrave;i G&ograve;n</a></find>&nbsp;th&acirc;n thương đ&atilde; từng được rất nhiều bạn trẻ y&ecirc;u th&iacute;ch v&agrave; bầu chọn l&agrave; một trong c&aacute;c ca kh&uacute;c về qu&ecirc; hương hay nhất của l&agrave;ng nhạc trẻ.<br />\r\n<br />\r\n<strong>2. Marcel Nguyễn với Olympics 2012</strong></p>\r\n\r\n<p style="padding: 0px; margin: 0px 0px 16px; color: rgb(0, 0, 0); font-family: ''Times New Roman''; font-size: 16px; line-height: normal; background-color: rgb(248, 248, 248);">Kh&aacute;n giả Việt Nam chắc chưa qu&ecirc;n gương mặt điển trai như t&agrave;i từ điện ảnh của ch&agrave;ng VĐV người&nbsp;<find><a class="find" href="http://vinhlong.megafun.vn/find?q=%C4%90%E1%BB%A9c" style="text-decoration: initial; outline: none medium; color: rgb(0, 57, 100);" target="_blank">Đức</a></find>&nbsp;gốc Việt&nbsp;<find><a class="find" href="http://vinhlong.megafun.vn/find?q=Marcel%20Nguy%E1%BB%85n" style="text-decoration: initial; outline: none medium; color: rgb(0, 57, 100);" target="_blank">Marcel Nguyễn</a></find>&nbsp;tại<em>&nbsp;Olympics 2012</em>.</p>\r\n\r\n<p style="padding: 0px; margin: 0px 0px 16px; color: rgb(0, 0, 0); font-family: ''Times New Roman''; font-size: 16px; line-height: normal; background-color: rgb(248, 248, 248);">Marcel Nguyễn t&ecirc;n đầy đủ l&agrave; Marcel Văn Minh Long Nguyễn, c&oacute; bố l&agrave; người Việt Nam v&agrave; mẹ l&agrave; người Đức. Ch&agrave;ng trai trẻ đ&atilde; theo đuổi niềm đam m&ecirc; l&agrave; m&ocirc;n Thể dục dụng cụ từ khi c&ograve;n rất nhỏ. Tại&nbsp;<em>Olympics 2012</em>, Marcel Nguyễn đ&atilde; gi&agrave;nh tấm HCB nội dung to&agrave;n năng v&agrave; đ&acirc;y l&agrave; chiến t&iacute;ch chưa từng c&oacute; trong lịch sử bộ m&ocirc;n n&agrave;y của người Đức kể từ năm 1936.</p>\r\n\r\n<table align="center" class="image center" style="border-collapse: collapse; background-color: rgb(233, 233, 233); width: 465px; color: rgb(0, 68, 136); margin-bottom: 10px; font-family: ''Times New Roman''; font-size: 16px; line-height: normal;" width="480">\r\n	<tbody>\r\n		<tr>\r\n			<td style="text-align: center; padding: 5px;"><img alt="Marcel Nguyễn tại Olympics 2012" src="http://vinhlong.megafun.vn/dataimages/201209/original/images784273_marcel_12_1343737281_0x441.jpg" style="margin: 0px; border: 0px; outline: none 0px; padding: 0px; list-style-type: none; max-width: 455px; height: 302px; width: 480px;" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td class="image_desc" style="text-align: center; padding: 5px;">Marcel Nguyễn tại&nbsp;<em>Olympics 2012</em></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style="padding: 0px; margin: 0px 0px 16px; color: rgb(0, 0, 0); font-family: ''Times New Roman''; font-size: 16px; line-height: normal; background-color: rgb(248, 248, 248);">Những bức ảnh chụp của Marcel Nguyễn tại Thế vận hội năm nay đ&atilde; từng l&agrave;m &ldquo;đi&ecirc;n đảo&rdquo; ph&aacute;i nữ bởi vẻ đẹp trai kh&ocirc;ng kh&aacute;c g&igrave; một si&ecirc;u sao&nbsp;<find><a class="find" href="http://vinhlong.megafun.vn/find?q=Hollywood" style="text-decoration: initial; outline: none medium; color: rgb(0, 57, 100);" target="_blank">Hollywood</a></find>c&ugrave;ng body đ&uacute;ng chất một VĐV thể thao. Khi thi đấu, Marcel Nguyễn kh&ocirc;ng chỉ nhận được sự ủng hộ của người Đức m&agrave; c&ograve;n c&oacute; được sự cổ vũ rất lớn từ cộng đồng người Việt đang sinh sống tại Anh. Mang trong m&igrave;nh một nửa d&ograve;ng m&aacute;u Việt, Marcel Nguyễn cũng đ&atilde; từng hai lần về Việt Nam v&agrave; b&agrave;y tỏ mong muốn gi&uacute;p đỡ cho thể thao Việt Nam.</p>\r\n\r\n<table align="center" class="image center" style="border-collapse: collapse; background-color: rgb(233, 233, 233); width: 465px; color: rgb(0, 68, 136); margin-bottom: 10px; font-family: ''Times New Roman''; font-size: 16px; line-height: normal;" width="480">\r\n	<tbody>\r\n		<tr>\r\n			<td style="text-align: center; padding: 5px;"><img alt="Vẻ điển trai như tài tử điện ảnh của Marcel Nguyễn" src="http://vinhlong.megafun.vn/dataimages/201209/original/images784272_2433358F_9D19_4373_A9CE_E1116B8167AF_mw800_s.jpg" style="margin: 0px; border: 0px; outline: none 0px; padding: 0px; list-style-type: none; max-width: 455px; height: 359px; width: 480px;" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td class="image_desc" style="text-align: center; padding: 5px;">Vẻ điển trai như t&agrave;i tử điện ảnh của Marcel Nguyễn</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style="padding: 0px; margin: 0px 0px 16px; color: rgb(0, 0, 0); font-family: ''Times New Roman''; font-size: 16px; line-height: normal; background-color: rgb(248, 248, 248);"><strong>3. Jason Việt Tiến với X Factor Anh Quốc</strong></p>\r\n\r\n<p style="padding: 0px; margin: 0px 0px 16px; color: rgb(0, 0, 0); font-family: ''Times New Roman''; font-size: 16px; line-height: normal; background-color: rgb(248, 248, 248);">Mới đ&acirc;y nhất, cư d&acirc;n mạng lại phải ch&uacute; &yacute; đến một gương mặt Việt đ&atilde; c&oacute; một phần tr&igrave;nh diễn ấn tượng tại cuộc thi &acirc;m nhạc&nbsp;<em>X Factor</em>&nbsp;nước Anh &ndash; đ&oacute; l&agrave;<find><a class="find" href="http://vinhlong.megafun.vn/find?q=Jason%20Vi%E1%BB%87t%20Ti%E1%BA%BFn" style="text-decoration: initial; outline: none medium; color: rgb(0, 57, 100);" target="_blank">Jason Việt Tiến</a></find>. Việt Tiến năm nay mới 18 tuổi qu&ecirc; gốc tại&nbsp;<find><a class="find" href="http://vinhlong.megafun.vn/find?q=H%C3%A0%20N%E1%BB%99i" style="text-decoration: initial; outline: none medium; color: rgb(0, 57, 100);" target="_blank">H&agrave; Nội</a></find>, hiện đang du học tại&nbsp;<find><a class="find" href="http://vinhlong.megafun.vn/find?q=Anh" style="text-decoration: initial; outline: none medium; color: rgb(0, 57, 100);" target="_blank">Anh</a></find>. Ch&agrave;ng trai trẻ từng t&acirc;m sự rằng,&nbsp;<em>X Factor&nbsp;</em>l&agrave; chương tr&igrave;nh truyền h&igrave;nh lớn nhất nước Anh v&agrave; cũng l&agrave; show thực tế cậu đặc biệt y&ecirc;u th&iacute;ch. Niềm đam m&ecirc; ca h&aacute;t v&agrave; t&igrave;nh cảm d&agrave;nh ri&ecirc;ng cho vị nữ gi&aacute;m khảo&nbsp;<find><a class="find" href="http://vinhlong.megafun.vn/find?q=Tulisha" style="text-decoration: initial; outline: none medium; color: rgb(0, 57, 100);" target="_blank">Tulisha</a></find>&nbsp;ở&nbsp;<em>X Factor&nbsp;</em>đ&atilde; th&ocirc;i th&uacute;c cậu đăng k&yacute; tham gia.</p>\r\n\r\n<table align="center" class="image center" style="border-collapse: collapse; background-color: rgb(233, 233, 233); width: 465px; color: rgb(0, 68, 136); margin-bottom: 10px; font-family: ''Times New Roman''; font-size: 16px; line-height: normal;" width="480">\r\n	<tbody>\r\n		<tr>\r\n			<td style="text-align: center; padding: 5px;"><img alt="Anh chàng du học sinh Việt Tiến" src="http://vinhlong.megafun.vn/dataimages/201209/original/images784274_TX04XFACTORJASON03seqnjpg1347207470.jpg" style="margin: 0px; border: 0px; outline: none 0px; padding: 0px; list-style-type: none; max-width: 455px; height: 386px; width: 480px;" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td class="image_desc" style="text-align: center; padding: 5px;">Anh ch&agrave;ng du học sinh Việt Tiến</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style="padding: 0px; margin: 0px 0px 16px; color: rgb(0, 0, 0); font-family: ''Times New Roman''; font-size: 16px; line-height: normal; background-color: rgb(248, 248, 248);">Trong h&ocirc;m dự thi, Việt Tiến tr&igrave;nh b&agrave;y ca kh&uacute;c&nbsp;<em>I have nothing</em>&nbsp;của&nbsp;<find><a class="find" href="http://vinhlong.megafun.vn/find?q=Whitney%20Houston" style="text-decoration: initial; outline: none medium; color: rgb(0, 57, 100);" target="_blank">Whitney Houston</a></find>. D&ugrave; sở hữu giọng h&aacute;t kh&ocirc;ng qu&aacute; nổi trội, nhưng Việt Tiến lại ghi điểm ở phong c&aacute;ch h&oacute;m hỉnh, hiện đại, trẻ trung. Ch&agrave;ng du học sinh đ&atilde; rất bạo dạn thể hiện t&igrave;nh cảm với Tulisha, thậm ch&iacute; c&ograve;n tiến thẳng về ph&iacute;a BGK tặng cho c&ocirc; một b&ocirc;ng hồng. Phần thưởng cho anh ch&agrave;ng đ&aacute;ng y&ecirc;u n&agrave;y l&agrave; một nụ h&ocirc;n l&ecirc;n m&aacute; từ ph&iacute;a c&ocirc; n&agrave;ng xinh đẹp Tulisha.</p>\r\n\r\n<table align="center" class="image center" style="border-collapse: collapse; background-color: rgb(233, 233, 233); width: 465px; color: rgb(0, 68, 136); margin-bottom: 10px; font-family: ''Times New Roman''; font-size: 16px; line-height: normal;" width="490">\r\n	<tbody>\r\n		<tr>\r\n			<td style="text-align: center; padding: 5px;"><img alt="Nữ giám khảo Tulisha dành tặng nụ hôn lên má cho Việt Tiến tại X Factor" src="http://vinhlong.megafun.vn/dataimages/201209/original/images784277_120909musikvietanh352a9fjpg1347206525.jpg" style="margin: 0px; border: 0px; outline: none 0px; padding: 0px; list-style-type: none; max-width: 455px; height: 264px; width: 490px;" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td class="image_desc" style="text-align: center; padding: 5px;">Nữ gi&aacute;m khảo Tulisha d&agrave;nh tặng nụ h&ocirc;n l&ecirc;n m&aacute; cho Việt Tiến tại&nbsp;<em>X Factor</em></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style="padding: 0px; margin: 0px 0px 16px; color: rgb(0, 0, 0); font-family: ''Times New Roman''; font-size: 16px; line-height: normal; background-color: rgb(248, 248, 248);">Những ấn tượng tốt đẹp m&agrave; Việt Tiến để lại trong chương tr&igrave;nh&nbsp;đ&atilde; gi&uacute;p cậu c&oacute; một tấm v&eacute; đi tiếp v&agrave;o v&ograve;ng trong. Như vậy, ch&uacute;ng ta sẽ c&ograve;n tiếp tục được thưởng thức tiếng h&aacute;t của ch&agrave;ng trai n&agrave;y tại cuộc thi &acirc;m nhạc&nbsp;<em>X Factor</em>.<br />\r\n<br />\r\n<strong>4. Christine H&agrave; tại Master Chef</strong></p>\r\n\r\n<p style="padding: 0px; margin: 0px 0px 16px; color: rgb(0, 0, 0); font-family: ''Times New Roman''; font-size: 16px; line-height: normal; background-color: rgb(248, 248, 248);">Tại cuộc thi nấu ăn&nbsp;<em>Master Chef</em>&nbsp;của Mỹ, c&oacute; một th&iacute; sinh v&ocirc; c&ugrave;ng đặc biệt đ&atilde; g&acirc;y x&ocirc;n xao dư luận khi thực hiện phần thi khi hai mắt kh&ocirc;ng thể nh&igrave;n thấy g&igrave;. Đ&oacute; l&agrave;&nbsp;<find><a class="find" href="http://vinhlong.megafun.vn/find?q=Christine%20H%C3%A0" style="text-decoration: initial; outline: none medium; color: rgb(0, 57, 100);" target="_blank">Christine H&agrave;</a></find>, một c&ocirc; g&aacute;i gốc Việt khiếm thị.&nbsp;</p>\r\n\r\n<table align="center" class="image center" style="border-collapse: collapse; background-color: rgb(233, 233, 233); width: 465px; color: rgb(0, 68, 136); margin-bottom: 10px; font-family: ''Times New Roman''; font-size: 16px; line-height: normal;" width="480">\r\n	<tbody>\r\n		<tr>\r\n			<td style="text-align: center; padding: 5px;"><img alt="Cô gái đầu bếp mù với câu chuyện cổ tích tại Master Chef" src="http://vinhlong.megafun.vn/dataimages/201209/original/images784292_tumblr_m9l5nnE3yy1qcqsm1o2_1280.jpg" style="margin: 0px; border: 0px; outline: none 0px; padding: 0px; list-style-type: none; max-width: 455px; height: 334px; width: 480px;" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td class="image_desc" style="text-align: center; padding: 5px;">C&ocirc; g&aacute;i đầu bếp m&ugrave; với c&acirc;u chuyện cổ t&iacute;ch tại Master Chef</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style="padding: 0px; margin: 0px 0px 16px; color: rgb(0, 0, 0); font-family: ''Times New Roman''; font-size: 16px; line-height: normal; background-color: rgb(248, 248, 248);">Ngay từ v&ograve;ng đầu ti&ecirc;n, Christine H&agrave; đ&atilde; khiến cả chương tr&igrave;nh phải bất ngờ khi xuất hiện c&ugrave;ng c&acirc;y gậy v&agrave; sự gi&uacute;p đỡ của chồng. Tuy nhi&ecirc;n, điều khiến tất cả phải ngỡ ng&agrave;ng nhất ch&iacute;nh l&agrave; khả năng d&ugrave;ng dao lẫn t&agrave;i nấu ăn tuyệt vời của Christine H&agrave;. M&oacute;n c&aacute; kho tộ, một m&oacute;n ăn đậm chất Việt Nam được c&ocirc; g&aacute;i m&ugrave; chế biến đ&atilde; chinh phục bộ ba gi&aacute;m khảo&nbsp;<find><a class="find" href="http://vinhlong.megafun.vn/find?q=Gordon%20Ramsay" style="text-decoration: initial; outline: none medium; color: rgb(0, 57, 100);" target="_blank">Gordon Ramsay</a></find>,&nbsp;<find><a class="find" href="http://vinhlong.megafun.vn/find?q=Joe%20Bastianich" style="text-decoration: initial; outline: none medium; color: rgb(0, 57, 100);" target="_blank">Joe Bastianich</a></find>v&agrave;&nbsp;<find><a class="find" href="http://vinhlong.megafun.vn/find?q=Graham%20Elliot" style="text-decoration: initial; outline: none medium; color: rgb(0, 57, 100);" target="_blank">Graham Elliot</a></find>.</p>\r\n\r\n<p style="padding: 0px; margin: 0px 0px 16px; color: rgb(0, 0, 0); font-family: ''Times New Roman''; font-size: 16px; line-height: normal; background-color: rgb(248, 248, 248);">Mất đi thị lực, nhưng c&aacute;c gi&aacute;c quan nhạy b&eacute;n c&ugrave;ng khả năng đặc biệt về nấu nướng đ&atilde; gi&uacute;p Christine H&agrave; tiếp tục theo đuổi niềm đam m&ecirc;. Gi&aacute;m khảo Graham Elliot c&ograve;n khẳng định rằng:&nbsp;<em>&quot;Khẩu vị của c&ocirc; ấy tinh tế hơn bất kỳ người n&agrave;o trong ph&ograve;ng thi.&quot;</em></p>\r\n\r\n<table align="center" class="image center" style="border-collapse: collapse; background-color: rgb(233, 233, 233); width: 465px; color: rgb(0, 68, 136); margin-bottom: 10px; font-family: ''Times New Roman''; font-size: 16px; line-height: normal;" width="480">\r\n	<tbody>\r\n		<tr>\r\n			<td style="text-align: center; padding: 5px;"><img alt="Christine Hà tại Master Chef " src="http://vinhlong.megafun.vn/dataimages/201209/original/images784287_2012_06_19_BlindCook2.jpg" style="margin: 0px; border: 0px; outline: none 0px; padding: 0px; list-style-type: none; max-width: 455px; height: 320px; width: 480px;" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td class="image_desc" style="text-align: center; padding: 5px;">Christine H&agrave; tại&nbsp;<em>Master Chef</em></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style="padding: 0px; margin: 0px 0px 16px; color: rgb(0, 0, 0); font-family: ''Times New Roman''; font-size: 16px; line-height: normal; background-color: rgb(248, 248, 248);">C&acirc;u chuyện của Christine H&agrave; đ&atilde; trở th&agrave;nh điểm nhấn đầy x&uacute;c động trong chương tr&igrave;nh truyền h&igrave;nh thực tế&nbsp;<em>Master Chef</em>. Trong trận chung kết vừa diễn ra, c&ocirc; g&aacute;i m&ugrave; gốc Việt đ&atilde; ch&iacute;nh thức vượt qua đối thủ&nbsp;<find><a class="find" href="http://vinhlong.megafun.vn/find?q=Josh%20Marks" style="text-decoration: initial; outline: none medium; color: rgb(0, 57, 100);" target="_blank">Josh Marks</a></find>&nbsp;để trở th&agrave;nh nh&agrave; v&ocirc; địch của&nbsp;<em>Master Chef</em>, một th&agrave;nh t&iacute;ch kh&ocirc;ng tưởng nhưng rất xứng đ&aacute;ng với nghị lực v&agrave; t&agrave;i năng của c&ocirc;.</p>\r\n\r\n<p style="padding: 0px; margin: 0px 0px 16px; color: rgb(0, 0, 0); font-family: ''Times New Roman''; font-size: 16px; line-height: normal; background-color: rgb(248, 248, 248);">Hiện nay, Christine H&agrave; c&ograve;n mở một trang blog ri&ecirc;ng t&ecirc;n&nbsp;<em>The Blind Cook</em>&nbsp;d&agrave;nh ri&ecirc;ng cho niềm đam m&ecirc; ẩm thực của m&igrave;nh. Cũng ch&iacute;nh nhờ trang blog với rất nhiều m&oacute;n ăn Việt n&agrave;y m&agrave; Christine H&agrave; nhận được sự ch&uacute; &yacute; từ BTC&nbsp;<em>Master Chef&nbsp;</em>để mời c&ocirc; tham dự chương tr&igrave;nh.</p>\r\n\r\n<p style="padding: 0px; margin: 0px 0px 16px; color: rgb(0, 0, 0); font-family: ''Times New Roman''; font-size: 16px; line-height: normal; background-color: rgb(248, 248, 248); text-align: right;">&nbsp;</p>\r\n', '4 nhân tài gốc Việt thành công tại ‘đấu trường’ thế giới', 0);
INSERT INTO `chuathienquang_news` (`id`, `id_category`, `author`, `date`, `content`, `title`, `type`) VALUES
(103, 15, 'BBT Website', '2013-02-02 17:31:10', '<p style="margin: 0px 0px 15px; padding: 0px; border: 0px; outline: 0px; font-family: Arial, ''Lucida Grande'', Helvetica, Tahoma, Verdana; vertical-align: baseline; color: rgb(0, 0, 0); line-height: 18px;"><span style="font-weight: bold; line-height: 19.1875px;">ANTĐ - Đ&ecirc;m qua cả Messi lẫn CR7 đều ra s&acirc;n thi đấu v&agrave; đều ghi b&agrave;n. Hai cầu thủ xuất sắc nhất thế giới kh&ocirc;ng hẹn m&agrave; gặp, rủ nhau ph&aacute; vỡ kỷ lục ghi nhiều b&agrave;n thắng nhất ở một m&ugrave;a giải La Liga.</span></p>\r\n\r\n<p style="margin: 0px 0px 15px; padding: 0px; border: 0px; outline: 0px; font-family: Arial, ''Lucida Grande'', Helvetica, Tahoma, Verdana; vertical-align: baseline; color: rgb(0, 0, 0); line-height: 18px;"><span class="storyInlinePhoto" style="margin: 0px; padding: 0px; border: 0px; outline: 0px; font-style: inherit; font-family: inherit; vertical-align: baseline;"><img src="http://www.anninhthudo.vn/Uploaded/hoangdung/2012_04_15/Messi.jpg" style="margin: 0px auto; padding: 2px; border: 1px solid rgb(204, 204, 204); outline: 0px; font-style: inherit; font-family: inherit; vertical-align: baseline; max-width: 460px; display: block; width: 460px;" /></span></p>\r\n\r\n<p><span style="color: rgb(0, 0, 0); font-family: Arial, ''Lucida Grande'', Helvetica, Tahoma, Verdana; line-height: 18px;">Đ&ecirc;m qua, Messi c&oacute; một c&uacute; đ&uacute;p v&agrave;o lưới Levante, gi&uacute;p Barcelona vượt qua CLB n&agrave;y với c&aacute;ch biệt s&iacute;t sao 2-1. Ở cặp đấu giữa Real Madrid v&agrave; Sporting Gijon, Kền kền trắng cũng gi&agrave;nh chiến thắng 3-1 với 1 pha lập c&ocirc;ng của tiền vệ Cris Ronaldo.&nbsp;</span><br style="display: block; margin: 5px 0px; color: rgb(0, 0, 0); font-family: Arial, ''Lucida Grande'', Helvetica, Tahoma, Verdana; line-height: 18px;" />\r\n<br style="display: block; margin: 5px 0px; color: rgb(0, 0, 0); font-family: Arial, ''Lucida Grande'', Helvetica, Tahoma, Verdana; line-height: 18px;" />\r\n<span style="color: rgb(0, 0, 0); font-family: Arial, ''Lucida Grande'', Helvetica, Tahoma, Verdana; line-height: 18px;">Như vậy, hiện Messi v&agrave; CR7 đều c&oacute; 41 b&agrave;n thắng trong cuộc đua tới danh hiệu vua ph&aacute; lưới La Liga 2011/12, Song quan trọng hơn, cột mốc 2 cầu thủ đại t&agrave;i n&agrave;y vừa thiết lập đ&ecirc;m qua ch&iacute;nh l&agrave; kỷ lục mới về số b&agrave;n thắng 1 cầu thủ ghi được nhiều nhất cho CLB tại 1 m&ugrave;a giải La Liga.</span><br style="display: block; margin: 5px 0px; color: rgb(0, 0, 0); font-family: Arial, ''Lucida Grande'', Helvetica, Tahoma, Verdana; line-height: 18px;" />\r\n<br style="display: block; margin: 5px 0px; color: rgb(0, 0, 0); font-family: Arial, ''Lucida Grande'', Helvetica, Tahoma, Verdana; line-height: 18px;" />\r\n<span style="color: rgb(0, 0, 0); font-family: Arial, ''Lucida Grande'', Helvetica, Tahoma, Verdana; line-height: 18px;">Với ri&ecirc;ng tiền đạo Messi, 2 b&agrave;n thắng anh ghi được đ&ecirc;m qua đ&atilde; n&acirc;ng tổng số b&agrave;n thắng anh mang về cho G&atilde; khồng lồ xứ Catalan ở m&ugrave;a giải năm nay l&ecirc;n con số kh&ocirc;ng tưởng: 63. Cris Ronaldo, khi&ecirc;m tốn hơn, cũng đ&atilde; c&oacute; 53 lần gi&uacute;p Real Madrid s&uacute;t tung lưới đối phương m&ugrave;a giải năm nay.</span><br style="display: block; margin: 5px 0px; color: rgb(0, 0, 0); font-family: Arial, ''Lucida Grande'', Helvetica, Tahoma, Verdana; line-height: 18px;" />\r\n<br style="display: block; margin: 5px 0px; color: rgb(0, 0, 0); font-family: Arial, ''Lucida Grande'', Helvetica, Tahoma, Verdana; line-height: 18px;" />\r\n<span style="color: rgb(0, 0, 0); font-family: Arial, ''Lucida Grande'', Helvetica, Tahoma, Verdana; line-height: 18px;">Đ&ecirc;m qua cũng l&agrave; một đ&ecirc;m đ&aacute;ng nhớ của Real Madrid. Chiến thắng với tỷ số c&aacute;ch biệt 3-1 gi&uacute;p CLB n&agrave;y tiếp tục duy tr&igrave; khoảng c&aacute;ch 4 điểm tr&ecirc;n BXH La Liga với đại k&igrave;nh địch Barcelona. Th&ecirc;m v&agrave;o đ&oacute;, CLB n&agrave;y đ&atilde; c&acirc;n bằng kỷ lục ghi nhiều b&agrave;n thắng nhất trong một m&ugrave;a giải La Liga do ch&iacute;nh m&igrave;nh thiết lập hồi 1989/90 với 107 b&agrave;n thắng. La Liga vẫn c&ograve;n 5 v&ograve;ng đấu nữa mới kết th&uacute;c v&agrave; đ&acirc;y l&agrave; cơ hội để Kền kền trắng x&ocirc; đổ kỷ lục cũ của m&igrave;nh kh&aacute; xa.</span></p>\r\n\r\n<p style="margin: 0px 0px 15px; padding: 0px; border: 0px; outline: 0px; font-family: Arial, ''Lucida Grande'', Helvetica, Tahoma, Verdana; vertical-align: baseline; color: rgb(0, 0, 0); line-height: 18px;">&nbsp;</p>\r\n\r\n<p style="margin: 0px 0px 15px; padding: 0px; border: 0px; outline: 0px; font-family: Arial, ''Lucida Grande'', Helvetica, Tahoma, Verdana; vertical-align: baseline; color: rgb(0, 0, 0); line-height: 18px;"><em style="margin: 0px; padding: 0px; border: 0px; outline: 0px; font-family: inherit; vertical-align: baseline;">Theo Bongdaso</em></p>\r\n', 'Hai nhân tài của bóng đá thế giới liên tục xô đổ các kỷ lục', 0),
(104, 16, 'BBT Website', '2013-02-03 14:54:23', '<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><strong>Đ&ecirc;m ng&agrave;y 16 th&aacute;ng 8,trong khu&ocirc;n khổ Tuần lễ Văn H&oacute;a Phật gi&aacute;o Nghệ An 2012,sau khi c&oacute; một thời ph&aacute;p thoại d&agrave;nh cho c&aacute;c bạn trẻ tại NVH Lao động tỉnh.Với sự tham gia của hơn 1000 bạn trẻ tham dự đ&atilde; để lại một dấu ấn v&ocirc; c&ugrave;ng đậm s&acirc;u v&agrave; thức tỉnh biết bao bạn trẻ.Cũng nhằm truyền tải th&ocirc;ng điệp của buổi n&oacute;i chuyện,ngay sau khi thời ph&aacute;p thoại kết th&uacute;c,PV Phattuvietnam.net đ&atilde; c&oacute; cuộc phỏng vấn nhỏ với Sư C&ocirc;</strong></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><em><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Thưa Sư c&ocirc;, b&acirc;y giờ đ&atilde; rất muộn rồi nhưng ch&uacute;ng con xin tri &acirc;n Sư c&ocirc; vẫn d&agrave;nh thời gian chia sẻ cho ch&uacute;ng con. Sư c&ocirc; c&oacute; ấn tương như&nbsp;&nbsp;thế n&agrave;o với qu&ecirc; hương Nghệ An?</span></em></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><strong>SC TNHN</strong>: Nghệ An l&agrave; v&ugrave;ng đất văn hiến, từng l&agrave; kinh đ&ocirc; của nước Việt Thường, với cuộc khởi nghĩa y&ecirc;u nước v&agrave; lập n&ecirc;n kinh đ&ocirc; Vạn An của Mai Hắc Đế, cũng l&agrave; mảnh đất c&ograve;n ghi dấu của Phượng Ho&agrave;ng Trung Đ&ocirc;. Nghệ An cũng l&agrave; nơi c&oacute; rất nhiều ng&ocirc;i ch&ugrave;a cổ v&agrave; ghi dấu nhiều h&igrave;nh ảnh của dấu ấn Văn h&oacute;a Phật gi&aacute;o cho c&aacute;c bạn trẻ.</span></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Đặc biệt hơn,Nghệ An l&agrave; mảnh đất học,nơi sản sinh nhiều bậc nh&acirc;n t&agrave;i, nhiều danh nh&acirc;n v&agrave; nh&agrave; văn h&oacute;a c&oacute; tầm cỡ quốc gia v&agrave; quốc tế.</span></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><em><span style="font-family: arial, helvetica, sans-serif; font-size: small;">PV: Thưa Sư c&ocirc;, C&ocirc; c&oacute; thể cho con biết những yếu tố n&agrave;o ảnh hưởng đến giới trẻ Nghệ An n&oacute;i ri&ecirc;ng v&agrave; giới&nbsp;&nbsp;trẻ Việt&nbsp;Nam h&ocirc;m nay kh&ocirc;ng ạ?</span></em></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><strong>SC TNHN</strong>: Hiện nay,c&aacute;c em được sống trong điều kiện khoa học kĩ thuật ph&aacute;t triển theo đ&agrave; chung của nh&acirc;n loại,c&aacute;c&nbsp;&nbsp;yếu tố x&atilde; hội với vấn đề to&agrave;n cầu h&oacute;a đ&atilde; đưa tuổi trẻ Việt Nam&nbsp;v&agrave;o một thời kỳ đổi mới kh&aacute; phức tạp. Cũng ch&iacute;nh v&igrave; thế m&agrave; đời sống văn h&oacute;a tinh thần của nh&acirc;n d&acirc;n ta được n&acirc;ng cao, được mở rộng, phong ph&uacute; đa dạng</span></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Từ đ&oacute;, nhiều mặt ti&ecirc;u cực của đời sống x&atilde; hội cũng nảy sinh . Phần lớn bạn trẻ Việt&nbsp;Nam&nbsp;kh&ocirc;ng ngờ m&igrave;nh đang g&oacute;p phần l&agrave;m mất đi bản sắc văn h&oacute;a d&acirc;n tộc của ch&iacute;nh m&igrave;nh.</span></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Kh&ocirc;ng những thế,c&ograve;n một số vấn đề kh&aacute; n&oacute;ng v&agrave; ảnh hưởng tới giỡi trẻ như hiện tượng y&ecirc;u gấp &ndash; sống vội của giới trẻ hiện nay,căn bệnh say m&ecirc; thần tượng,ăn mặc &ndash; cuộc đua h&agrave;ng hiệu &ndash; thị hiếu &acirc;m nhạc</span></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Nguy hiểm hơn,nhiều bạn coi rẻ bản th&acirc;n rồi lao v&agrave;o lối sống th&aacute;c loạn, bất m&atilde;n đến độ kh&ocirc;ng biết qu&yacute; mạng sống của m&igrave;nh.</span></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><em><span style="font-family: arial, helvetica, sans-serif; font-size: small;">PV: K&iacute;nh bạch Sư c&ocirc;, trong buổi ph&aacute;p thoại của m&igrave;nh, Sư c&ocirc; đ&atilde; n&ecirc;u ra kh&aacute;i niệm về L&yacute; tưởng v&agrave; tỉnh y&ecirc;u ch&acirc;n ch&aacute;nh. Sư c&ocirc; c&oacute;&nbsp;thể&nbsp;chia sẻ th&ecirc;m Tại sao giới trẻ phải sống c&oacute; l&yacute; tưởng?</span></em></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><strong>SCTNHN</strong>: C&ocirc; xin nhắc lại một lần nữ l&yacute; tưởng l&agrave; những mục đ&iacute;ch tốt đẹp l&agrave;m cho cuộc sống v&agrave; c&ocirc;ng việc c&oacute;&nbsp;&yacute; nghĩa</span></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><span style="font-family: arial, helvetica, sans-serif; font-size: small;">C&ograve;n t&igrave;nh y&ecirc;u ch&acirc;n ch&aacute;nh ch&iacute;nh l&agrave; tấm l&ograve;ng lu&ocirc;n mong muốn điều tốt nhất cho tha nh&acirc;n,l&agrave; đặt những lợi &iacute;ch của tha nh&acirc;n l&ecirc;n tr&ecirc;n lợi &iacute;ch ri&ecirc;ng của bản th&acirc;n.</span></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><em><span style="font-family: arial, helvetica, sans-serif; font-size: small;">PV: Sư c&ocirc; c&oacute; thể chỉ b&agrave;y c&oacute; c&aacute;c bạn trẻ biết những biểu hiện của t&igrave;nh y&ecirc;u thương trong đời thường hiện nay&nbsp;được kh&ocirc;ng&nbsp;ạ?</span></em></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><strong>SCTNHN</strong>: Ch&uacute;ng ta c&oacute; thể bằng những h&agrave;nh động v&ocirc; c&ugrave;ng đơn giản Quan t&acirc;m, chăm lo đến nhau</span></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><span style="font-family: arial, helvetica, sans-serif; font-size: small;">- Chia sẻ lẫn nhau về c&aacute;ch cảm x&uacute;c (cả vui, cả buồn...), c&aacute;ch nghĩ, c&aacute;ch l&agrave;m hay h&agrave;nh động</span></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><span style="font-family: arial, helvetica, sans-serif; font-size: small;">- T&ocirc;n trọng v&agrave; tin tưởng nhau</span></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><span style="font-family: arial, helvetica, sans-serif; font-size: small;">- Ch&acirc;n th&agrave;nh v&agrave; quan t&acirc;m, chia sẻ niềm vui nỗi buồn nhiều hơn, kể cả những chuyện hết sức thầm k&iacute;n,&nbsp;ước mơ, kế hoạch tương lai....</span></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><span style="font-family: arial, helvetica, sans-serif; font-size: small;">- Lu&ocirc;n lu&ocirc;n kh&iacute;ch lệ động vi&ecirc;n nhau để vui hơn, tốt đẹp hơn;th&ocirc;ng&nbsp;cảm lẫn nhau về thiếu s&oacute;t v&agrave; lỗi lầm nhiều hơn.</span></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><em><span style="font-family: arial, helvetica, sans-serif; font-size: small;">PV: Th&ocirc;ng qua buổi n&oacute;i chuyện n&agrave;y, Sư c&ocirc; c&oacute; những lời khuy&ecirc;n ch&acirc;n th&agrave;nh g&igrave; gửi đến c&aacute;c em trong việc x&aacute;c định l&yacute; tưởng cuộc sống?</span></em></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><strong>SC TNHN</strong>: Th&ocirc;ng qua thời ph&aacute;p thoại của m&igrave;nh,c&ocirc; mong rằng giới trẻ VN phải giữ g&igrave;n bản sắc văn h&oacute;a d&acirc;n tộc m&igrave;nh. Một d&acirc;n tộc m&agrave; kh&ocirc;ng giữ được bản sắc văn h&oacute;a ri&ecirc;ng, th&igrave; dần dần sẽ kh&ocirc;ng c&ograve;n d&acirc;n tộc đ&oacute; nữa. C&ocirc; cũng mong rằng c&aacute;c bạn trẻ hiểu ra rằng:</span></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><span style="font-family: arial, helvetica, sans-serif; font-size: small;">- Vật chất kh&ocirc;ng thể đem lại hạnh ph&uacute;c cho con người. Vật chất chỉ&nbsp;l&agrave; một trong c&aacute;c phương tiện của đời sống</span></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><span style="font-family: arial, helvetica, sans-serif; font-size: small;">- Sống theo lời Phật dạy để thiết lập được cuộc sống c&acirc;n bằng.</span></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><span style="font-family: arial, helvetica, sans-serif; font-size: small;">- Giới trẻ phải tập c&oacute; những khoảng lặng trong đời sống</span></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><span style="font-family: arial, helvetica, sans-serif; font-size: small;">- Giữ vững niềm tin trong ch&aacute;nh ph&aacute;p</span></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><span style="font-family: arial, helvetica, sans-serif; font-size: small;">- C&oacute; th&aacute;i độ sống tich cực v&agrave; ch&acirc;n th&agrave;nh</span></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><span style="font-family: arial, helvetica, sans-serif; font-size: small;">- Kh&ocirc;ng dễ d&agrave;ng nản l&ograve;ng trước việc kh&oacute; khăn</span></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><span style="font-family: arial, helvetica, sans-serif; font-size: small;">- Thực tập kh&ocirc;ng s&acirc;n hận</span></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><span style="font-family: arial, helvetica, sans-serif; font-size: small;">- Thực tập t&acirc;m từ bi hỷ xả - tha thứ v&agrave; bao dung</span></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><span style="font-family: arial, helvetica, sans-serif; font-size: small;">- Tu tập v&agrave; giữ g&igrave;n v&agrave; tăng trưởng T&acirc;m hiếu đạo</span></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><em><span style="font-family: arial, helvetica, sans-serif; font-size: small;">PV: Sư c&ocirc; c&oacute; nhận x&eacute;t g&igrave; về c&aacute;c bạn trẻ Nghệ An ạ?</span></em></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><strong>SC TNHN</strong>:&nbsp;</span><span style="font-family: arial, helvetica, sans-serif; font-size: small;">C&oacute; thể n&oacute;i C&ocirc; rất ấn tượng v&agrave; cũng rất ngưỡng mộ c&aacute;c bạn trẻ Nghệ An, từ th&aacute;ng 1 năm 2012 c&ocirc; về đ&acirc;y, c&ocirc; đ&atilde; c&oacute; cảm t&igrave;nh với c&aacute;c bạn trẻ,rồi c&aacute;c bạn trẻ ai ai cũng dễ thương, cũng l&agrave; những người c&oacute; t&acirc;m đạo v&agrave; kh&aacute;t khao t&igrave;m cầu ch&acirc;n l&yacute;.Ngay trong Tuần lễ Văn H&oacute;a Phật Gi&aacute;o n&agrave;y, ch&iacute;nh mắt C&ocirc; đ&atilde; thấy c&aacute;c bạn trẻ l&agrave;m việc v&agrave; gi&uacute;p đỡ Qu&yacute; Thầy trong BTC.</span></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><span style="font-family: arial, helvetica, sans-serif; font-size: small;">Hay đơn cử trong buổi thuyết giảng n&agrave;y, C&ocirc; rất bất ngờ v&agrave; x&uacute;c động khi c&aacute;c bạn về dự v&agrave; lắng nghe buổi giảng một c&aacute;ch chăm ch&uacute;. C&aacute;c bạn trẻ Nghệ An thật tuyệt với! C&ocirc; ch&uacute;c v&agrave; tin c&aacute;c em sẽ l&agrave; những nh&acirc;n tố đi đầu v&agrave; quan trọng nhất đưa Phật gi&aacute;o tỉnh Nghệ An đi l&ecirc;n, ph&aacute;t triển bền vững.</span></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><em><span style="font-family: arial, helvetica, sans-serif; font-size: small;">PV: V&acirc;ng, con xin c&uacute;i đầu đảnh lễ Sư c&ocirc; v&agrave; ch&uacute;c Sư c&ocirc; Ph&aacute;p thể khinh an,Phật sự vi&ecirc;n th&agrave;nh v&agrave; lu&ocirc;n d&agrave;nh nhiều t&igrave;nh cảm ưu &aacute;i cho qu&ecirc; hương Nghệ An ch&uacute;ng con.Nam m&ocirc; A Di Đ&agrave; Phật</span></em></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><span style="font-family: arial, helvetica, sans-serif; font-size: small;">&nbsp;</span></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><img alt="" border="0" src="http://www.phattuvietnam.net/files/2012/08/DSC_1840_831813528.jpg" title="" /></span></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><span style="font-family: arial, helvetica, sans-serif; font-size: small;">&nbsp;</span></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><img alt="" border="0" src="http://www.phattuvietnam.net/files/2012/08/DSC_1841_980739060.jpg" title="" /></span></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><span style="font-family: arial, helvetica, sans-serif; font-size: small;">&nbsp;</span></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><img alt="" border="0" src="http://www.phattuvietnam.net/files/2012/08/DSC_1842_967456057.jpg" title="" /></span></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><img alt="" border="0" src="http://www.phattuvietnam.net/files/2012/08/DSC_1848_327805985.jpg" title="" /></span></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><span style="font-family: arial, helvetica, sans-serif; font-size: small;">&nbsp;</span></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><img alt="" border="0" src="http://www.phattuvietnam.net/files/2012/08/DSC_1851_826568567.jpg" title="" /></span></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><span style="font-family: arial, helvetica, sans-serif; font-size: small;">&nbsp;</span></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><img alt="" border="0" src="http://www.phattuvietnam.net/files/2012/08/DSC_1852_738966852.jpg" title="" /></span></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><span style="font-family: arial, helvetica, sans-serif; font-size: small;">&nbsp;</span></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 19.1875px;"><span style="font-family: arial, helvetica, sans-serif; font-size: small;"><img alt="" border="0" src="http://www.phattuvietnam.net/files/2012/08/DSC_1856_136088080.jpg" title="" /></span></p>\r\n', 'Phỏng vấn Sư cô Idol tại Nghệ An', 0),
(105, 16, 'BBT Website', '2013-02-03 14:55:54', '<p><span class="ctcSummary" id="ZoomContentSummary" style="font-weight: bold; color: rgb(0, 0, 0); width: auto; text-align: justify; font-size: 13px; font-family: Tahoma, Verdana; line-height: normal; background-color: rgb(243, 243, 243);">Tối 16-8, trong khu&ocirc;n khổ Tuần Văn h&oacute;a Phật gi&aacute;o Nghệ An 2012, sư c&ocirc; TN Hương Nhũ c&oacute; một thời ph&aacute;p thoại d&agrave;nh cho hơn 1000 bạn trẻ tại NVH Lao Động tỉnh. Ph&aacute;p thoại đ&atilde; để lại một dấu ấn v&ocirc; c&ugrave;ng đậm s&acirc;u v&agrave; thức tỉnh biết bao bạn trẻ. Nhằm truyền tải th&ocirc;ng điệp của buổi n&oacute;i chuyện, ngay sau khi thời ph&aacute;p thoại kết th&uacute;c, PV (kh&ocirc;ng thẻ) Ch&ugrave;a Ph&uacute;c L&acirc;m online đ&atilde; c&oacute; cuộc tr&ograve; chuyện th&acirc;n t&igrave;nh v&agrave; cởi mở với người m&agrave; giới trẻ &quot;phong&quot; l&agrave; sư c&ocirc; &quot;Idol&quot;:</span></p>\r\n\r\n<p style="line-height: 17px; color: rgb(0, 0, 0); font-family: Tahoma, Verdana; text-align: justify; background-color: rgb(243, 243, 243);">&nbsp;</p>\r\n\r\n<p style="line-height: 17px; color: rgb(0, 0, 0); font-family: Tahoma, Verdana; text-align: justify; background-color: rgb(243, 243, 243);">&nbsp;</p>\r\n\r\n<p style="line-height: 17px; color: rgb(0, 0, 0); font-family: Tahoma, Verdana; text-align: justify; background-color: rgb(243, 243, 243);"><em>PV: Thưa Sư c&ocirc;, b&acirc;y giờ đ&atilde; rất muộn rồi nhưng ch&uacute;ng con xin tri &acirc;n Sư c&ocirc; vẫn d&agrave;nh thời gian chia sẻ cho ch&uacute;ng con. Sư c&ocirc; c&oacute; ấn tương như&nbsp;&nbsp;thế n&agrave;o với qu&ecirc; hương Nghệ An?</em></p>\r\n\r\n<p style="line-height: 17px; color: rgb(0, 0, 0); font-family: Tahoma, Verdana; text-align: justify; background-color: rgb(243, 243, 243);"><strong>SC TNHN</strong>: Nghệ An l&agrave; v&ugrave;ng đất văn hiến, từng l&agrave; kinh đ&ocirc; của nước Việt Thường với cuộc khởi nghĩa y&ecirc;u nước v&agrave; lập n&ecirc;n kinh đ&ocirc; Vạn An của Mai Hắc Đế, v&agrave; l&agrave; mảnh đất ghi dấu của Phượng Ho&agrave;ng Trung Đ&ocirc;. Nghệ An cũng l&agrave; nơi c&oacute; rất nhiều ng&ocirc;i ch&ugrave;a cổ v&agrave; ghi dấu nhiều h&igrave;nh ảnh của dấu ấn Văn h&oacute;a Phật gi&aacute;o.</p>\r\n\r\n<p style="line-height: 17px; color: rgb(0, 0, 0); font-family: Tahoma, Verdana; text-align: justify; background-color: rgb(243, 243, 243);">Đặc biệt hơn, Nghệ An c&ograve;n l&agrave; mảnh đất gi&agrave;u truyền thống học tập, nơi sản sinh nhiều bậc nh&acirc;n t&agrave;i, nhiều danh nh&acirc;n v&agrave; nh&agrave; văn h&oacute;a c&oacute; tầm cỡ quốc gia v&agrave; quốc tế.</p>\r\n\r\n<p style="line-height: 17px; color: rgb(0, 0, 0); font-family: Tahoma, Verdana; text-align: justify; background-color: rgb(243, 243, 243);"><em>PV: Sư c&ocirc; c&oacute; thể cho biết những yếu tố n&agrave;o ảnh hưởng đến giới trẻ Nghệ An n&oacute;i ri&ecirc;ng v&agrave; giới&nbsp;&nbsp;trẻ Việt&nbsp;Nam n&oacute;i chung h&ocirc;m nay kh&ocirc;ng ạ?</em></p>\r\n\r\n<p style="line-height: 17px; color: rgb(0, 0, 0); font-family: Tahoma, Verdana; text-align: justify; background-color: rgb(243, 243, 243);"><strong>SC TNHN</strong>: Hiện nay, c&aacute;c bạn trẻ được sống trong điều kiện khoa học kĩ thuật ph&aacute;t triển theo đ&agrave; chung của nh&acirc;n loại. C&aacute;c&nbsp;yếu tố x&atilde; hội với vấn đề to&agrave;n cầu h&oacute;a đ&atilde; đưa tuổi trẻ Việt Nam&nbsp;v&agrave;o một thời kỳ đổi mới. Cũng ch&iacute;nh v&igrave; thế m&agrave; đời sống văn h&oacute;a tinh thần của nh&acirc;n d&acirc;n ta được n&acirc;ng cao, được mở rộng, phong ph&uacute; đa dạng</p>\r\n\r\n<p style="line-height: 17px; color: rgb(0, 0, 0); font-family: Tahoma, Verdana; text-align: justify; background-color: rgb(243, 243, 243);">Từ đ&oacute;, nhiều mặt ti&ecirc;u cực của đời sống x&atilde; hội cũng nảy sinh. Phần lớn bạn trẻ Việt&nbsp;Nam&nbsp;kh&ocirc;ng ngờ m&igrave;nh đang g&oacute;p một phần nhỏ l&agrave;m mất đi bản sắc văn h&oacute;a d&acirc;n tộc của ch&iacute;nh m&igrave;nh.</p>\r\n\r\n<p style="line-height: 17px; color: rgb(0, 0, 0); font-family: Tahoma, Verdana; text-align: justify; background-color: rgb(243, 243, 243);">Kh&ocirc;ng những thế, c&ograve;n một số vấn đề kh&aacute; n&oacute;ng v&agrave; ảnh hưởng tới giới trẻ như hiện tượng y&ecirc;u gấp, sống vội của giới trẻ hiện nay, căn bệnh say m&ecirc; thần tượng, ăn mặc, cuộc đua h&agrave;ng hiệu, thị hiếu &acirc;m nhạc v.v.</p>\r\n\r\n<p style="line-height: 17px; color: rgb(0, 0, 0); font-family: Tahoma, Verdana; text-align: justify; background-color: rgb(243, 243, 243);">Nguy hiểm hơn, nhiều bạn coi rẻ bản th&acirc;n rồi lao v&agrave;o lối sống th&aacute;c loạn, bất m&atilde;n đến độ kh&ocirc;ng biết qu&yacute; mạng sống của m&igrave;nh.</p>\r\n\r\n<p style="line-height: 17px; color: rgb(0, 0, 0); font-family: Tahoma, Verdana; background-color: rgb(243, 243, 243); text-align: center;"><img border="0" src="http://chuabuuminh.vn/UserImages/2012/08/19/1/09huongnhu_9_jpg.jpg" /></p>\r\n\r\n<p style="line-height: 17px; color: rgb(0, 0, 0); font-family: Tahoma, Verdana; background-color: rgb(243, 243, 243); text-align: center;"><span style="color: rgb(0, 0, 128);">Sư c&ocirc; &quot;Idol&quot; TN Hương Nhũ thuyết giảng cho hơn 1000 bạn trẻ tại Nghệ An</span></p>\r\n\r\n<p style="line-height: 17px; color: rgb(0, 0, 0); font-family: Tahoma, Verdana; text-align: justify; background-color: rgb(243, 243, 243);"><em>PV: Trong ph&aacute;p thoại của m&igrave;nh, Sư c&ocirc; đ&atilde; n&ecirc;u ra kh&aacute;i niệm về l&yacute; tưởng v&agrave; t&igrave;nh y&ecirc;u ch&acirc;n ch&aacute;nh. Sư c&ocirc; c&oacute;&nbsp;thể&nbsp;chia sẻ th&ecirc;m Tại sao giới trẻ phải sống c&oacute; l&yacute; tưởng?</em></p>\r\n\r\n<p style="line-height: 17px; color: rgb(0, 0, 0); font-family: Tahoma, Verdana; text-align: justify; background-color: rgb(243, 243, 243);"><strong>SCTNHN</strong>: C&ocirc; xin nhắc lại một lần nữa, l&yacute; tưởng l&agrave; những mục đ&iacute;ch tốt đẹp l&agrave;m cho cuộc sống v&agrave; c&ocirc;ng việc c&oacute;&nbsp;&yacute; nghĩa. C&ograve;n t&igrave;nh y&ecirc;u ch&acirc;n ch&aacute;nh ch&iacute;nh l&agrave; tấm l&ograve;ng lu&ocirc;n mong muốn điều tốt nhất cho tha nh&acirc;n, nghĩa l&agrave; đặt những lợi &iacute;ch của tha nh&acirc;n l&ecirc;n tr&ecirc;n lợi &iacute;ch ri&ecirc;ng của bản th&acirc;n.</p>\r\n\r\n<p style="line-height: 17px; color: rgb(0, 0, 0); font-family: Tahoma, Verdana; background-color: rgb(243, 243, 243); text-align: center;"><img border="0" src="http://chuabuuminh.vn/UserImages/2012/08/19/1/09huongnhu_26_jpg.jpg" /></p>\r\n\r\n<p style="line-height: 17px; color: rgb(0, 0, 0); font-family: Tahoma, Verdana; text-align: justify; background-color: rgb(243, 243, 243);"><em>PV: Sư c&ocirc; c&oacute; thể chỉ b&agrave;y c&oacute; c&aacute;c bạn trẻ biết những biểu hiện của t&igrave;nh y&ecirc;u thương trong đời thường hiện nay&nbsp;được kh&ocirc;ng&nbsp;ạ?</em></p>\r\n\r\n<p style="line-height: 17px; color: rgb(0, 0, 0); font-family: Tahoma, Verdana; text-align: justify; background-color: rgb(243, 243, 243);"><strong>SCTNHN</strong>: Ch&uacute;ng ta c&oacute; thể bằng những h&agrave;nh động v&ocirc; c&ugrave;ng đơn giản thể hiện sự quan t&acirc;m, chăm lo đến nhau như:</p>\r\n\r\n<p style="line-height: 17px; color: rgb(0, 0, 0); font-family: Tahoma, Verdana; text-align: justify; background-color: rgb(243, 243, 243);">- Chia sẻ cho nhau về c&aacute;ch cảm x&uacute;c (cả vui, cả buồn...), c&aacute;ch nghĩ, c&aacute;ch l&agrave;m hay h&agrave;nh động;</p>\r\n\r\n<p style="line-height: 17px; color: rgb(0, 0, 0); font-family: Tahoma, Verdana; text-align: justify; background-color: rgb(243, 243, 243);">- T&ocirc;n trọng v&agrave; tin tưởng nhau;</p>\r\n\r\n<p style="line-height: 17px; color: rgb(0, 0, 0); font-family: Tahoma, Verdana; text-align: justify; background-color: rgb(243, 243, 243);">- Ch&acirc;n th&agrave;nh quan t&acirc;m v&agrave; chia sẻ niềm vui nỗi buồn nhiều hơn, kể cả những chuyện hết sức thầm k&iacute;n,&nbsp;ước mơ, kế hoạch tương lai....</p>\r\n\r\n<p style="line-height: 17px; color: rgb(0, 0, 0); font-family: Tahoma, Verdana; text-align: justify; background-color: rgb(243, 243, 243);">- Lu&ocirc;n lu&ocirc;n kh&iacute;ch lệ động vi&ecirc;n nhau để vui hơn, tốt đẹp hơn; th&ocirc;ng&nbsp;cảm cho nhau về thiếu s&oacute;t v&agrave; lỗi lầm nhiều hơn.</p>\r\n\r\n<p style="line-height: 17px; color: rgb(0, 0, 0); font-family: Tahoma, Verdana; background-color: rgb(243, 243, 243); text-align: center;"><img border="0" src="http://chuabuuminh.vn/UserImages/2012/08/19/1/07idol1_jpg.jpg" /></p>\r\n\r\n<p style="line-height: 17px; color: rgb(0, 0, 0); font-family: Tahoma, Verdana; text-align: justify; background-color: rgb(243, 243, 243);"><em>PV: Th&ocirc;ng qua cuộc n&oacute;i chuyện n&agrave;y, Sư c&ocirc; c&oacute; những lời khuy&ecirc;n ch&acirc;n th&agrave;nh g&igrave; gửi đến c&aacute;c bạn trẻ trong việc x&aacute;c định l&yacute; tưởng cuộc sống?</em></p>\r\n\r\n<p style="line-height: 17px; color: rgb(0, 0, 0); font-family: Tahoma, Verdana; text-align: justify; background-color: rgb(243, 243, 243);"><strong>SC TNHN</strong>: Th&ocirc;ng qua thời ph&aacute;p thoại của m&igrave;nh, c&ocirc; mong rằng giới trẻ VN phải giữ g&igrave;n bản sắc văn h&oacute;a d&acirc;n tộc m&igrave;nh. Một d&acirc;n tộc m&agrave; kh&ocirc;ng giữ được bản sắc văn h&oacute;a ri&ecirc;ng th&igrave; dần dần sẽ kh&ocirc;ng c&ograve;n d&acirc;n tộc đ&oacute; nữa. C&ocirc; cũng mong rằng c&aacute;c bạn trẻ hiểu ra rằng:</p>\r\n\r\n<p style="line-height: 17px; color: rgb(0, 0, 0); font-family: Tahoma, Verdana; text-align: justify; background-color: rgb(243, 243, 243);">- Vật chất kh&ocirc;ng thể đem lại hạnh ph&uacute;c cho con người. Vật chất chỉ&nbsp;l&agrave; một trong c&aacute;c phương tiện của đời sống</p>\r\n\r\n<p style="line-height: 17px; color: rgb(0, 0, 0); font-family: Tahoma, Verdana; text-align: justify; background-color: rgb(243, 243, 243);">- Sống theo lời Phật dạy để thiết lập được cuộc sống c&acirc;n bằng.</p>\r\n\r\n<p style="line-height: 17px; color: rgb(0, 0, 0); font-family: Tahoma, Verdana; text-align: justify; background-color: rgb(243, 243, 243);">- Giới trẻ phải tập c&oacute; những khoảng lặng trong đời sống.</p>\r\n\r\n<p style="line-height: 17px; color: rgb(0, 0, 0); font-family: Tahoma, Verdana; text-align: justify; background-color: rgb(243, 243, 243);">- Giữ vững niềm tin trong ch&aacute;nh ph&aacute;p.</p>\r\n\r\n<p style="line-height: 17px; color: rgb(0, 0, 0); font-family: Tahoma, Verdana; text-align: justify; background-color: rgb(243, 243, 243);">- C&oacute; th&aacute;i độ sống tich cực v&agrave; ch&acirc;n th&agrave;nh.</p>\r\n\r\n<p style="line-height: 17px; color: rgb(0, 0, 0); font-family: Tahoma, Verdana; text-align: justify; background-color: rgb(243, 243, 243);">- Kh&ocirc;ng dễ d&agrave;ng nản l&ograve;ng trước việc kh&oacute; khăn.</p>\r\n\r\n<p style="line-height: 17px; color: rgb(0, 0, 0); font-family: Tahoma, Verdana; text-align: justify; background-color: rgb(243, 243, 243);">- Thực tập kh&ocirc;ng s&acirc;n hận.</p>\r\n\r\n<p style="line-height: 17px; color: rgb(0, 0, 0); font-family: Tahoma, Verdana; text-align: justify; background-color: rgb(243, 243, 243);">- Thực tập t&acirc;m từ bi hỷ xả - tha thứ v&agrave; bao dung.</p>\r\n\r\n<p style="line-height: 17px; color: rgb(0, 0, 0); font-family: Tahoma, Verdana; text-align: justify; background-color: rgb(243, 243, 243);">- Tu tập v&agrave; giữ g&igrave;n v&agrave; tăng trưởng t&acirc;m hiếu đạo.</p>\r\n\r\n<p style="line-height: 17px; color: rgb(0, 0, 0); font-family: Tahoma, Verdana; background-color: rgb(243, 243, 243); text-align: center;"><img border="0" height="367" src="http://chuabuuminh.vn/UserImages/2012/08/19/1/07idol_jpg.jpg" width="551" /></p>\r\n\r\n<p style="line-height: 17px; color: rgb(0, 0, 0); font-family: Tahoma, Verdana; text-align: justify; background-color: rgb(243, 243, 243);"><em>PV: Sư c&ocirc; c&oacute; nhận x&eacute;t g&igrave; về c&aacute;c bạn trẻ Nghệ An ạ?</em></p>\r\n\r\n<p style="line-height: 17px; color: rgb(0, 0, 0); font-family: Tahoma, Verdana; text-align: justify; background-color: rgb(243, 243, 243);"><strong>SC TNHN</strong>: C&oacute; thể n&oacute;i c&ocirc; rất ấn tượng v&agrave; cũng rất ngưỡng mộ c&aacute;c bạn trẻ Nghệ An. Từ th&aacute;ng 1 năm 2012 c&ocirc; về đ&acirc;y, c&ocirc; đ&atilde; c&oacute; cảm t&igrave;nh với c&aacute;c bạn trẻ. C&aacute;c bạn trẻ ở đ&acirc;y ai ai cũng dễ thương, cũng l&agrave; những người c&oacute; t&acirc;m đạo v&agrave; kh&aacute;t khao t&igrave;m cầu ch&acirc;n l&yacute;. Ngay trong Tuần Văn h&oacute;a Phật gi&aacute;o n&agrave;y, ch&iacute;nh mắt c&ocirc; đ&atilde; thấy c&aacute;c bạn trẻ l&agrave;m việc v&agrave; gi&uacute;p đỡ qu&yacute; thầy trong BTC rất nhiều.</p>\r\n\r\n<p style="line-height: 17px; color: rgb(0, 0, 0); font-family: Tahoma, Verdana; text-align: justify; background-color: rgb(243, 243, 243);">Hay đơn cử trong buổi thuyết giảng n&agrave;y, c&ocirc; rất bất ngờ v&agrave; x&uacute;c động khi c&aacute;c bạn về dự v&agrave; lắng nghe buổi giảng một c&aacute;ch chăm ch&uacute;. C&aacute;c bạn trẻ Nghệ An thật tuyệt với! C&ocirc; ch&uacute;c v&agrave; tin c&aacute;c bạn sẽ l&agrave; những nh&acirc;n tố đi đầu v&agrave; quan trọng nhất đưa Phật gi&aacute;o tỉnh Nghệ An đi l&ecirc;n, ph&aacute;t triển bền vững.</p>\r\n\r\n<p style="line-height: 17px; color: rgb(0, 0, 0); font-family: Tahoma, Verdana; text-align: justify; background-color: rgb(243, 243, 243);"><em>PV: V&acirc;ng, con xin c&uacute;i đầu đảnh lễ Sư c&ocirc;. Ch&uacute;c Sư c&ocirc; Ph&aacute;p thể khinh an, Phật sự vi&ecirc;n th&agrave;nh v&agrave; lu&ocirc;n d&agrave;nh nhiều t&igrave;nh cảm ưu &aacute;i cho qu&ecirc; hương Nghệ An ch&uacute;ng con. Nam m&ocirc; A Di Đ&agrave; Phật</em></p>\r\n', 'Trò chuyện với sư cô "Idol" về giới trẻ ngày nay', 0),
(106, 16, 'BBT Website', '2013-02-03 14:58:21', '<div id="container1">Thi&ecirc;n Quang n&oacute;i chung v&agrave; sư c&ocirc; Hương Nhũ n&oacute;i ri&ecirc;ng, trở th&agrave;nh mối th&acirc;m giao của anh chị em nh&oacute;m khiếm thị kh&ocirc;ng ri&ecirc;ng B&igrave;nh Dương , TP HCM m&agrave; c&ograve;n một số nơi xa x&ocirc;i kh&aacute;c. Mỗi lần gia đ&igrave;nh T&agrave;i Tr&acirc;m ở nh&oacute;m thiện nguyện khiếm thị Hốc M&ocirc;n nghe về Thi&ecirc;n Quang th&igrave; cứ y như kẻ xa qu&ecirc; sắp về lại cố hương, n&oacute;i kh&ocirc;ng qu&aacute; th&igrave; họ kh&ocirc;ng ăn qu&ecirc;n ngủ để mong đến ng&agrave;y gặp sư c&ocirc; Idol của họ.</div>\r\n\r\n<div id="divCnt">\r\n<p>S&aacute;ng 02/10/2012, tại Thi&ecirc;n Quang Ni Tự Dĩ An, B&igrave;nh Dương, do sư c&ocirc; Hương Nhũ tọa chủ, đ&atilde; tổ chức kh&oacute;a tu an lạc v&agrave;o m&ugrave;a Trung Thu cho hơn 300 anh chị em khiếm thị khắp c&aacute;c quận huyện TP HCM v&agrave; B&igrave;nh Dương.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p align="center"><img border="0" src="http://img.tongiaovadantoc.com/2012/10/04/15/08/Trung-thu-1.JPG" style="border:0px;" /><br />\r\n<br />\r\n&nbsp;</p>\r\n\r\n<p>9 giờ s&aacute;ng buổi lễ bắt đầu, c&aacute;c đo&agrave;n&nbsp; khiếm thị quận 9, quận 8, quận Thủ Đức, Quận 11, B&igrave;nh Dương, Hốc M&ocirc;n&hellip; lần lượt tề tựu v&agrave;o khu&ocirc;n vi&ecirc;n Thi&ecirc;n Quang Ni&nbsp; tự.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hội trường lộ thi&ecirc;n được che bằng d&ugrave; hoa v&agrave; bạt, s&acirc;n khấu thiết kế tr&ecirc;n c&aacute;c th&ugrave;ng phuy; b&agrave;n thờ Phật trang nghi&ecirc;m, ph&iacute;a sau l&agrave; bức ph&ocirc;ng m&agrave;u xanh da trời với h&agrave;ng chữ&nbsp; &ldquo;Một ng&agrave;y an lạc cho người khiếm thị&rdquo;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>H&agrave;ng trăm chiếc ghế nhựa sắp đều tr&ecirc;n s&acirc;n đất dưới những t&aacute;n c&acirc;y cao; với sự chứng minh của sư b&agrave; Huệ Gi&aacute;c, tọa chủ Quan &Acirc;m tu viện, v&agrave; l&agrave; trưởng t&ocirc;ng m&ocirc;n Tịnh Độ Non Bồng, c&ugrave;ng qu&yacute; t&ocirc;n đức ni.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p align="center"><img border="0" src="http://img.tongiaovadantoc.com/2012/10/04/15/08/Trung-thu-2.JPG" style="border:0px;" /></p>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<p align="center"><img border="0" src="http://img.tongiaovadantoc.com/2012/10/04/15/08/Trung-thu-3.JPG" style="border:0px;" /><br />\r\n&nbsp;</p>\r\n\r\n<p align="center">&nbsp;<img border="0" src="http://img.tongiaovadantoc.com/2012/10/04/15/08/Trung-thu-4.JPG" style="border:0px;" /><br />\r\n&nbsp;</p>\r\n\r\n<div align="center"><img border="0" src="http://img.tongiaovadantoc.com/2012/10/04/15/09/Trung-thu-5.JPG" style="border:0px;" /><br />\r\n<br />\r\n<br />\r\n<img border="0" src="http://img.tongiaovadantoc.com/2012/10/04/15/09/Trung-thu-6.JPG" style="border:0px;" /><br />\r\n<br />\r\n&nbsp;</div>\r\n&nbsp;\r\n\r\n<div align="justify">Buổi lễ niệm Phật được chư Ni hướng dẫn, sư b&agrave; v&agrave; c&aacute;c mạnh thường qu&acirc;n ph&aacute;t qu&agrave;, ngo&agrave;i g&oacute;p vui văn nghệ của c&aacute;c ch&aacute;u khiếm thị B&igrave;nh Dương, Diệu Kim v&agrave; Thanh Loan nh&oacute;m khiếm&nbsp;thị thiện nguyện Hốc M&ocirc;n, c&ograve;n c&oacute; ca sĩ H&ugrave;ng Thanh hiện diện. Tr&ecirc;n 320 phần qu&agrave; (mỗi ph&acirc;n trị gi&aacute; 250.000 đồng) d&agrave;nh cho những người khiếm thị nh&acirc;n ng&agrave;y an lạc trong m&ugrave;a Trung Thu.</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p align="center"><img border="0" src="http://img.tongiaovadantoc.com/2012/10/04/15/10/Trung-thu-7.JPG" style="border:0px;" /></p>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<p align="center"><img border="0" src="http://img.tongiaovadantoc.com/2012/10/04/15/10/Trung-thu-8.JPG" style="border:0px;" /></p>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<p align="center"><img border="0" src="http://img.tongiaovadantoc.com/2012/10/04/15/10/Trung-thu-9.JPG" style="border:0px;" /></p>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<p align="center"><img border="0" src="http://img.tongiaovadantoc.com/2012/10/04/15/11/Trung-thu-10.JPG" style="border:0px;" /><br />\r\n<br />\r\n&nbsp;</p>\r\n\r\n<p>Đ&acirc;y l&agrave; lần thứ ba Thi&ecirc;n Quang Ni tự đ&atilde; chia sẻ với khiếm thị v&agrave; c&aacute;c em thiếu nhi tại khu&ocirc;n vi&ecirc;n ch&ugrave;a.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thi&ecirc;n Quang n&oacute;i chung v&agrave; sư c&ocirc; Hương Nhũ n&oacute;i ri&ecirc;ng, trở th&agrave;nh mối&nbsp; th&acirc;m giao của anh chị em nh&oacute;m khiếm thị kh&ocirc;ng ri&ecirc;ng B&igrave;nh Dương , TP HCM m&agrave; c&ograve;n một số nơi xa x&ocirc;i kh&aacute;c.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Mỗi lần gia đ&igrave;nh T&agrave;i Tr&acirc;m ở nh&oacute;m thiện nguyện khiếm thị Hốc M&ocirc;n nghe về Thi&ecirc;n Quang th&igrave; cứ y như kẻ xa qu&ecirc; sắp về lại cố hương, n&oacute;i kh&ocirc;ng qu&aacute; th&igrave; họ kh&ocirc;ng ăn qu&ecirc;n ngủ để mong đến ng&agrave;y gặp sư c&ocirc; Idol của họ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p align="center"><img border="0" src="http://img.tongiaovadantoc.com/2012/10/04/15/11/Trung-thu-11.JPG" style="border:0px;" /></p>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<p align="center"><img border="0" src="http://img.tongiaovadantoc.com/2012/10/04/15/11/Trung-thu-12.JPG" style="border:0px;" /></p>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<p align="center"><img border="0" src="http://img.tongiaovadantoc.com/2012/10/04/15/11/Trung-thu-13.JPG" style="border:0px;" /></p>\r\n\r\n<p align="center"><br />\r\n<img border="0" src="http://img.tongiaovadantoc.com/2012/10/04/15/11/Trung-thu-14.JPG" style="border:0px;" /></p>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<p align="center"><img border="0" src="http://img.tongiaovadantoc.com/2012/10/04/15/12/Trung-thu-15.JPG" style="border:0px;" /></p>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<p align="center"><img border="0" src="http://img.tongiaovadantoc.com/2012/10/04/15/12/Trung-thu-15%20%282%29.JPG" style="border:0px;" /></p>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<div align="justify">Nhưng đến rồi, dễ g&igrave; được gặp để t&acirc;m sự, v&igrave; sư c&ocirc; qu&aacute; bận rộn, thậm ch&iacute; những ng&agrave;y ph&aacute;t qu&agrave; như thế, sư c&ocirc; phải tất bật mấy h&ocirc;m v&agrave; kh&ocirc;ng c&oacute; th&igrave; giờ ăn uống; nhưng nh&igrave;n mọi người vui l&agrave; sư c&ocirc; kh&ocirc;ng lộ vẻ mệt mỏi.</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nh&igrave;n thấy sư c&ocirc; l&agrave; lực h&uacute;t cho mọi ph&iacute;a, kể cả sự đo&agrave;n kết của đồng đạo, đ&acirc;y l&agrave; sự th&agrave;nh c&ocirc;ng tr&ecirc;n con đường h&agrave;nh đạo của những tu sĩ trẻ như sư c&ocirc;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Một t&acirc;m nguyện lu&ocirc;n v&igrave; những m&atilde;nh đời k&eacute;m may mắn như sư c&ocirc;, trời cũng chiều l&ograve;ng ngưng mưa suốt buổi thay v&igrave; l&agrave; m&ugrave;a mưa bảo.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p align="center"><img border="0" src="http://img.tongiaovadantoc.com/2012/10/04/15/13/Trung-thu-17.JPG" style="border:0px;" /></p>\r\n\r\n<p align="center"><br />\r\n<img border="0" src="http://img.tongiaovadantoc.com/2012/10/04/15/13/Trung-thu-18.JPG" style="border:0px;" /></p>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<p align="center"><img border="0" src="http://img.tongiaovadantoc.com/2012/10/04/15/13/Trung-thu-19.JPG" style="border:0px;" /></p>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<p align="center"><img border="0" src="http://img.tongiaovadantoc.com/2012/10/04/15/14/Trung-thu-20.JPG" style="border:0px;" /><br />\r\n&nbsp;</p>\r\n\r\n<p align="center">&nbsp;</p>\r\n\r\n<p>Cuộc vui kết th&uacute;c sau buổi cơm trưa, c&aacute;c xe đưa đo&agrave;n về, họ vịn vai nhau như đ&agrave;n kiến nối đu&ocirc;i khi kh&ecirc;nh được mồi, cũng thế, tay vịn vai, tay x&aacute;ch qu&agrave;, kh&ocirc;ng những kh&ecirc;nh mồi như đo&agrave;n kiến m&agrave; họ c&ograve;n kh&ecirc;nh lu&ocirc;n cả t&igrave;nh cảm tr&acirc;n qu&yacute; từ Thi&ecirc;n Quang về lại nơi sinh hoạt h&agrave;ng ng&agrave;y, để rồi, họ tiếp tục chờ đợi c&oacute; tin vui khi nhận được c&aacute;i hẹn gặp gỡ cho kỳ kế tiếp.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Họ vui th&igrave; sư c&ocirc; Idol của họ mệt, thế th&ocirc;i!</p>\r\n</div>\r\n', 'rung Thu an lạc cho hơn 300 người khiếm thị', 0);
INSERT INTO `chuathienquang_news` (`id`, `id_category`, `author`, `date`, `content`, `title`, `type`) VALUES
(107, 15, 'BBT Website', '2013-02-03 15:19:14', '<p><strong style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);">Từ bất ngờ v&igrave; sự đa dạng</strong><br style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);" />\r\n<br style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);" />\r\n<span style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);">Theo thống k&ecirc; của BTC, trong số hơn 2000 th&iacute; sinh đăng k&yacute; tham gia So you think you can dance - Thử th&aacute;ch c&ugrave;ng bước nhảy m&ugrave;a đầu ti&ecirc;n, c&oacute; tới 45% l&agrave; c&aacute;c vũ c&ocirc;ng Hiphop. Ngo&agrave;i một v&agrave;i dancer solo th&igrave; đa số B-Boy, B-Girl đều đến từ c&aacute;c nhỏm nhảy hoặc vũ đo&agrave;n đ&atilde; hoạt đ&ocirc;ng l&acirc;u năm. Với chỉ ri&ecirc;ng loại h&igrave;nh nhảy m&uacute;a mang nguồn gốc từ đường phố n&agrave;y, cũng đ&atilde; c&oacute; kh&ocirc;ng &iacute;t c&aacute;c phong c&aacute;ch kh&aacute;c nhau được mang đến chương tr&igrave;nh. Từ phổ biến như Popping, Breaking cho đến mới lạ như Krump, Hiphop Jazz...</span><br style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);" />\r\n<br style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);" />\r\n<span style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);">C&ugrave;ng chiếm tỷ lệ 11% l&agrave; Latin/Ballroom v&agrave; M&uacute;a đương đại. Đ&acirc;y đều l&agrave; những loại h&igrave;nh đ&ograve;i hỏi c&aacute;c vũ c&ocirc;ng phải trải qua một qu&aacute; tr&igrave;nh luyện tập d&agrave;i hơi v&agrave; b&agrave;i bản. Họ c&oacute; thể l&agrave; sinh vi&ecirc;n c&aacute;c trường m&uacute;a ch&iacute;nh quy, c&oacute; thể đến từ những đo&agrave;n m&uacute;a t&ecirc;n tuổi v&agrave; cũng c&oacute; thể chỉ luyện tập ở c&aacute;c CLB Khi&ecirc;u vũ. Mặc d&ugrave; vậy, cũng đ&atilde; c&oacute; một v&agrave;i trường hợp c&aacute; biệt tự luyện tập v&igrave; kh&ocirc;ng đủ điều kiện theo học.</span><br style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);" />\r\n<br style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);" />\r\n<span style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);">Con số 33% c&ograve;n lại gồm một loạt c&aacute;c phong c&aacute;ch kh&aacute;c nhau. Đ&oacute; c&oacute; thể l&agrave; những bước nhảy latin n&oacute;ng bỏng nhưng lại v&ocirc; c&ugrave;ng bụi bặm như Salsa, Bachata; l&agrave; những tạo h&igrave;nh khoe đường cong đầy quyến rũ của Belly Dance, Bollywood; l&agrave; cả những thể loại nhảy c&ograve;n tương đối mới mẻ như Tap dance hay KPop cover...&nbsp;</span><br style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);" />\r\n<br style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);" />\r\n&nbsp;</p>\r\n\r\n<div style="border: 0px; font-size: 16px; font-family: ''Times New Roman'', Times, serif; vertical-align: baseline; margin: 0px; padding: 0px; color: rgb(0, 0, 0); line-height: 24px; background-color: rgb(243, 243, 243);">\r\n<table align="center" cellpadding="0" cellspacing="0" class="image center" style="border: 0px none; font-style: inherit; font-family: inherit; vertical-align: middle; margin: 0px auto; padding: 0px; border-collapse: collapse; border-spacing: 0px; background-color: rgb(238, 238, 238); width: 400px; color: rgb(0, 68, 136);" width="400">\r\n	<tbody style="border: 0px none; font-style: inherit; font-family: inherit; vertical-align: baseline; margin: 0px; padding: 0px; border-collapse: collapse;">\r\n		<tr style="border: 0px none; font-style: inherit; font-family: inherit; vertical-align: baseline; margin: 0px; padding: 0px; border-collapse: collapse;">\r\n			<td style="border: 0px none; font-style: inherit; font-family: inherit; vertical-align: middle; margin: 0px; padding: 0px; text-align: center; border-collapse: collapse; float: none !important;"><img alt="" src="http://imgs.vietnamnet.vn/Images/2012/11/16/10/20121116101545_1.jpg" style="border: 0px; font-style: inherit; font-family: inherit; vertical-align: baseline; margin: 0px; padding: 0px; max-width: 482px;" /></td>\r\n		</tr>\r\n		<tr style="border: 0px none; font-style: inherit; font-family: inherit; vertical-align: baseline; margin: 0px; padding: 0px; border-collapse: collapse;">\r\n			<td class="image_desc" style="border: 0px none; font-style: inherit; font-size: 11px; font-family: Arial, Helvetica, sans-serif; vertical-align: middle; margin: 0px; padding: 5px; text-align: center; color: rgb(12, 72, 187); background-color: rgb(227, 227, 227); border-collapse: collapse; float: none !important;">Một nam vũ c&ocirc;ng Belly Dance hiếm hoi</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n</div>\r\n\r\n<p><span style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);">Sự đa dạng n&agrave;y đ&atilde; phần n&agrave;o cho thấy phong tr&agrave;o nhảy m&uacute;a ở Việt Nam đang c&oacute; một sự ph&aacute;t triển d&ugrave; kh&ocirc;ng qu&aacute; ồn &agrave;o nhưng mạnh mẽ v&agrave; s&acirc;u rộng. Ngo&agrave;i những loại h&igrave;nh phổ biến, c&aacute;c bạn trẻ c&ograve;n tự t&igrave;m hiểu v&agrave; luyện tập những bộ m&ocirc;n mới lạ để thử th&aacute;ch m&igrave;nh. Kh&ocirc;ng &iacute;t người trong số họ đ&atilde; bước đầu gặt h&aacute;i được một v&agrave;i th&agrave;nh c&ocirc;ng nhất định khi đ&atilde; lọt v&agrave;o đến v&ograve;ng b&aacute;n kết của chương tr&igrave;nh.</span><br style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);" />\r\n<br style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);" />\r\n<span style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);">Quả thực, khi m&agrave; nhiều người vẫn c&oacute; suy nghĩ mặc định rằng nhảy m&uacute;a ở Việt Nam chỉ g&oacute;i gọn trong một v&agrave;i thể loại quen thuộc th&igrave; việc c&aacute;c th&iacute; sinh của Thử th&aacute;ch c&ugrave;ng bước nhảy thể hiện sự đa dạng trong phong c&aacute;ch đ&atilde; mang đến kh&ocirc;ng &iacute;t những bất ngờ đầy th&uacute; vị.&nbsp;</span><br style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);" />\r\n<br style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);" />\r\n<strong style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);">Đến ngỡ ng&agrave;ng trước những t&agrave;i năng&nbsp;</strong><br style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);" />\r\n<br style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);" />\r\n<span style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);">Trước Thử th&aacute;ch c&ugrave;ng bước nhảy, đ&atilde; c&oacute; kh&ocirc;ng &iacute;t cuộc thi nhảy m&uacute;a được tổ chức. Nhưng nh&igrave;n chung, quy m&ocirc; của c&aacute;c cuộc thi n&agrave;y thường bị b&oacute; gọn trong một nh&oacute;m đối tượng nhất định, chủ yếu l&agrave; giữa những người l&agrave;m nghề với nhau. Bước nhảy ho&agrave;n vũ phần n&agrave;o thu h&uacute;t được sự ch&uacute; &yacute; của c&ocirc;ng ch&uacute;ng. Nhưng thực chất, đ&acirc;y chỉ l&agrave; một chương tr&igrave;nh thuần giải tr&iacute; với sự tham gia của những người nổi tiếng, mượn khi&ecirc;u vũ để l&agrave;m mới m&igrave;nh qua đ&oacute; c&acirc;u k&eacute;o kh&aacute;n giả.&nbsp;</span><br style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);" />\r\n<br style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);" />\r\n<span style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);">B&ecirc;n cạnh đ&oacute;, c&aacute;c vũ c&ocirc;ng Việt thường kh&ocirc;ng c&oacute; nhiều cơ hội được tiếp cận một c&aacute;ch gần gũi nhất tới đ&ocirc;ng đảo c&ocirc;ng ch&uacute;ng. D&ugrave; được xuất hiện tr&ecirc;n s&acirc;n khấu, nhưng nếu kh&ocirc;ng phải với vai tr&ograve; l&agrave; &quot;k&eacute;p phụ&quot;, minh họa cho ca sĩ th&igrave; cũng l&agrave; những chương tr&igrave;nh được thực hiện kh&ocirc;ng để phục vụ số đ&ocirc;ng. Những vở m&uacute;a như Sương sớm hay Chuyện kể của những chiếc giầy, d&ugrave; nhận được phản hồi tốt của kh&aacute;n giả, nhưng dăm th&igrave; mười họa mới tr&igrave;nh diễn một lần.</span><br style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);" />\r\n<br style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);" />\r\n<span style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);">Vậy n&ecirc;n, khi m&agrave; c&aacute;c tập đầu ti&ecirc;n của So you think you can dance phi&ecirc;n bản Việt được l&ecirc;n s&oacute;ng, chắc hẳn nhiều người đ&atilde; phải cảm thấy ngỡ ng&agrave;ng v&igrave; t&agrave;i năng của c&aacute;c vũ c&ocirc;ng Việt. 20 c&aacute;i t&ecirc;n được lọt v&agrave;o v&ograve;ng chung kết đều l&agrave; những gương mặt xứng đ&aacute;ng, tuy nhi&ecirc;n vẫn c&ograve;n đ&oacute; kh&ocirc;ng &iacute;t c&aacute;c th&iacute; sinh phải vắng mặt một c&aacute;ch đ&aacute;ng tiếc v&igrave; những l&yacute; do kh&aacute;c.</span><br style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);" />\r\n<br style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);" />\r\n<span style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);">Nổi bật nhất trong số đ&oacute; l&agrave; Hoa Đức C&ocirc;ng. Anh ch&agrave;ng c&oacute; biệt danh CK Animation n&agrave;y đến với nhảy m&uacute;a năm 14 tuổi v&agrave; nhanh ch&oacute;ng trở th&agrave;nh một popper h&agrave;ng đầu của giới Hiphop H&agrave; Nội. Tuy nhi&ecirc;n, đến năm 2007, C&ocirc;ng bất ngờ mắc căn bệnh suy thận qu&aacute;i &aacute;c v&agrave; phải gắn b&oacute; cuộc sống của m&igrave;nh với chiếc m&aacute;y chạy thận.</span><br style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);" />\r\n&nbsp;</p>\r\n\r\n<div style="border: 0px; font-size: 16px; font-family: ''Times New Roman'', Times, serif; vertical-align: baseline; margin: 0px; padding: 0px; color: rgb(0, 0, 0); line-height: 24px; background-color: rgb(243, 243, 243);">\r\n<table align="center" cellpadding="0" cellspacing="0" class="image center" style="border: 0px none; font-style: inherit; font-family: inherit; vertical-align: middle; margin: 0px auto; padding: 0px; border-collapse: collapse; border-spacing: 0px; background-color: rgb(238, 238, 238); width: 400px; color: rgb(0, 68, 136);" width="400">\r\n	<tbody style="border: 0px none; font-style: inherit; font-family: inherit; vertical-align: baseline; margin: 0px; padding: 0px; border-collapse: collapse;">\r\n		<tr style="border: 0px none; font-style: inherit; font-family: inherit; vertical-align: baseline; margin: 0px; padding: 0px; border-collapse: collapse;">\r\n			<td style="border: 0px none; font-style: inherit; font-family: inherit; vertical-align: middle; margin: 0px; padding: 0px; text-align: center; border-collapse: collapse; float: none !important;"><img alt="" src="http://imgs.vietnamnet.vn/Images/2012/11/16/10/20121116101545_2.jpg" style="border: 0px; font-style: inherit; font-family: inherit; vertical-align: baseline; margin: 0px; padding: 0px; max-width: 482px;" /></td>\r\n		</tr>\r\n		<tr style="border: 0px none; font-style: inherit; font-family: inherit; vertical-align: baseline; margin: 0px; padding: 0px; border-collapse: collapse;">\r\n			<td class="image_desc" style="border: 0px none; font-style: inherit; font-size: 11px; font-family: Arial, Helvetica, sans-serif; vertical-align: middle; margin: 0px; padding: 5px; text-align: center; color: rgb(12, 72, 187); background-color: rgb(227, 227, 227); border-collapse: collapse; float: none !important;">Hoa Đức C&ocirc;ng</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n</div>\r\n\r\n<p><span style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);">Sau một năm ch&aacute;n nản v&agrave; bi quan, C&ocirc;ng đ&atilde; quyết định trở lại với niềm đam m&ecirc; của m&igrave;nh bất chấp cơ thể khi ấy vẫn c&ograve;n đau yếu. V&agrave; đ&oacute; ch&iacute;nh l&agrave; quyết định đ&atilde; gi&uacute;p cho popper n&agrave;y c&oacute; thể sống với một t&acirc;m thế lạc quan, y&ecirc;u đời cho đến tận b&acirc;y giờ. Hiện tại, ngo&agrave;i lịch chạy thận 3 lần/1 tuần cố định, C&ocirc;ng vẫn tiếp tục luyện tập popping đồng thời tham gia đứng lớp, hướng dẫn cho c&aacute;c bạn c&oacute; chung niềm đam m&ecirc;.&nbsp;</span><br style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);" />\r\n<br style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);" />\r\n<span style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);">Tương tự Hoa Đức C&ocirc;ng, đến với nhảy m&uacute;a để t&igrave;m hướng đi đ&uacute;ng cho cuộc đời, Nguyễn Vũ Ho&agrave;ng Minh cũng l&agrave; một trong những th&iacute; sinh được đ&aacute;nh gi&aacute; cao ở v&ograve;ng sơ loại v&agrave; b&aacute;n kết. Vượt qua mặc cảm về một gia đ&igrave;nh kh&ocirc;ng y&ecirc;n ấm, ch&agrave;ng vũ c&ocirc;ng c&oacute; sở trường l&agrave; hiphop đương đại n&agrave;y đ&atilde; chinh phục to&agrave;n bộ BGK bằng những bước nhảy kỹ thuật m&agrave; kh&ocirc;ng k&eacute;m phần cảm x&uacute;c.</span><br style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);" />\r\n<br style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);" />\r\n<span style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);">Tuy nhi&ecirc;n, một chấn thương nặng đ&atilde; khiến cho Ho&agrave;ng Minh phải ngừng cuộc chơi sớm. Gặp Minh tại ph&ograve;ng nh&agrave; thi đấu Maximart khi cậu theo &quot;thầy&quot; John Huy Trần đến hướng dẫn cho c&aacute;c th&iacute; sinh top 20, d&ugrave; nỗi buồn đ&atilde; vơi, nhưng sự tiếc nuối vẫn đong đầy trong đ&ocirc;i mắt của dancer 20 tuổi n&agrave;y.</span><br style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);" />\r\n<br style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);" />\r\n<span style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);">L&agrave; một trong những trường hợp hiếm hoi tự luyện tập bộ m&ocirc;n m&uacute;a đương đại kh&oacute; nhằn, Nguyễn Vũ Kh&aacute;nh cũng l&agrave; một điểm s&aacute;ng của v&ograve;ng loại khi b&agrave;i thi gi&agrave;u &yacute; nghĩa của th&iacute; sinh n&agrave;y đ&atilde; khiến cho gi&aacute;m khảo John Huy Trần phải nổi da g&agrave;, c&ograve;n gi&aacute;m khảo Tuyết Minh bật kh&oacute;c ngay tại chỗ. Tuy nhi&ecirc;n, tiếc cho Kh&aacute;nh. Chỉ v&igrave; kh&ocirc;ng được đ&agrave;o tạo cơ bản, thiếu sự ổn định m&agrave; ch&agrave;ng trai c&oacute; khu&ocirc;n mặt cười đ&atilde; phải dừng bước v&igrave; l&yacute; do thuần chuy&ecirc;n m&ocirc;n.&nbsp;</span><br style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);" />\r\n&nbsp;</p>\r\n\r\n<div style="border: 0px; font-size: 16px; font-family: ''Times New Roman'', Times, serif; vertical-align: baseline; margin: 0px; padding: 0px; color: rgb(0, 0, 0); line-height: 24px; background-color: rgb(243, 243, 243);">\r\n<table align="center" cellpadding="0" cellspacing="0" class="image center" style="border: 0px none; font-style: inherit; font-family: inherit; vertical-align: middle; margin: 0px auto; padding: 0px; border-collapse: collapse; border-spacing: 0px; background-color: rgb(238, 238, 238); width: 400px; color: rgb(0, 68, 136);" width="400">\r\n	<tbody style="border: 0px none; font-style: inherit; font-family: inherit; vertical-align: baseline; margin: 0px; padding: 0px; border-collapse: collapse;">\r\n		<tr style="border: 0px none; font-style: inherit; font-family: inherit; vertical-align: baseline; margin: 0px; padding: 0px; border-collapse: collapse;">\r\n			<td style="border: 0px none; font-style: inherit; font-family: inherit; vertical-align: middle; margin: 0px; padding: 0px; text-align: center; border-collapse: collapse; float: none !important;"><img alt="" src="http://imgs.vietnamnet.vn/Images/2012/11/16/10/20121116101545_3.jpg" style="border: 0px; font-style: inherit; font-family: inherit; vertical-align: baseline; margin: 0px; padding: 0px; max-width: 482px;" /></td>\r\n		</tr>\r\n		<tr style="border: 0px none; font-style: inherit; font-family: inherit; vertical-align: baseline; margin: 0px; padding: 0px; border-collapse: collapse;">\r\n			<td class="image_desc" style="border: 0px none; font-style: inherit; font-size: 11px; font-family: Arial, Helvetica, sans-serif; vertical-align: middle; margin: 0px; padding: 5px; text-align: center; color: rgb(12, 72, 187); background-color: rgb(227, 227, 227); border-collapse: collapse; float: none !important;">Nguyễn Vũ Kh&aacute;nh</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n</div>\r\n\r\n<p><span style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);">Trước sự xuất hiện đồng loạt của nhiều t&agrave;i năng nhảy m&uacute;a trong v&ograve;ng sơ loại, ch&iacute;nh bản th&acirc;n bi&ecirc;n đạo m&uacute;a Tuyết Minh cũng cảm thấy v&ocirc; c&ugrave;ng ngạc nhi&ecirc;n. Theo s&aacute;t cuộc thi từ đầu đến giờ, chị c&ograve;n nhận ra một điều rằng nhiều vũ c&ocirc;ng, d&ugrave; chỉ tự luyện tập, nhưng khả năng th&iacute;ch ứng v&agrave; tiến bộ c&ograve;n cao hơn nhiều người được đ&agrave;o tạo b&agrave;i bản. Bằng chứng l&agrave; kh&ocirc;ng &iacute;t sinh vi&ecirc;n c&aacute;c trường m&uacute;a đ&atilde; bị loại ngay từ những v&ograve;ng ngo&agrave;i.</span><br style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);" />\r\n<br style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);" />\r\n<span style="color: rgb(0, 0, 0); font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 24px; background-color: rgb(243, 243, 243);">Trong khi đ&oacute;, Huỳnh Mến, th&iacute; sinh đ&atilde; bị loại ở đ&ecirc;m c&ocirc;ng bố Top 14, c&ograve;n bật m&iacute; rằng: &quot;Em biết vẫn c&ograve;n nhiều người t&agrave;i năng lắm. Nhưng năm nay họ vẫn chưa c&oacute; &yacute; định lộ diện v&igrave; c&ograve;n nghi ngờ sự th&agrave;nh c&ocirc;ng của chương tr&igrave;nh. Nếu chương tr&igrave;nh vẫn giữ được sức h&uacute;t như hiện tại th&igrave; chắc chắn sang năm, họ sẽ tham gia.&quot;</span></p>\r\n', 'Vũ công Việt - những nhân tài ẩn dật', 0),
(108, 15, 'BBT Website', '2013-02-03 15:22:08', '<p style="margin: 0px; padding: 7px 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 21.328125px; color: rgb(66, 69, 71); background-color: rgb(232, 232, 232);"><strong>C&oacute; lẽ kh&ocirc;ng qu&aacute; lời khi đưa ra v&iacute; von ấy sau khi kỳ thủ nh&iacute; Nguyễn Anh Kh&ocirc;i vừa lập th&ecirc;m một chiến c&ocirc;ng tại giải V&ETH; trẻ thế giới ở lứa tuổi U10. Tiềm năng của thể thao tr&iacute; tuệ n&oacute;i chung, cờ vua n&oacute;i ri&ecirc;ng của ch&uacute;ng ta thực ra đ&atilde; được khẳng định từ l&acirc;u. Tuy nhi&ecirc;n, v&igrave; nhiều l&yacute; do m&agrave; c&aacute;i &ldquo;mỏ v&agrave;ng&rdquo; nh&acirc;n t&agrave;i ấy vẫn chưa được khai th&aacute;c v&agrave; ph&aacute;t huy để Việt Nam vươn hẳn l&ecirc;n h&agrave;ng những cường quốc cờ vua thế giới...</strong></p>\r\n\r\n<p style="margin: 0px; padding: 7px 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 21.328125px; color: rgb(66, 69, 71); background-color: rgb(232, 232, 232);"><strong>Th&ecirc;m một lần tỏa s&aacute;ng</strong></p>\r\n\r\n<div style="margin: 0px; padding: 0px; color: rgb(66, 69, 71); font-family: ''Times New Roman''; font-size: 16px; line-height: 21.328125px; background-color: rgb(232, 232, 232);">Đ&acirc;y kh&ocirc;ng phải lần đầu ti&ecirc;n Việt Nam gi&agrave;nh ng&ocirc;i v&ocirc; địch cờ vua c&aacute;c lứa tuổi thế giới, nhưng trước đ&oacute;, chưa c&oacute; ai từng về đ&iacute;ch với th&agrave;nh t&iacute;ch ấn tượng như vậy: điểm tuyệt đối 11/11. Ngay tại giải cờ vua v&ocirc; địch thế giới năm 2012 vừa qua tại Maribo (Slovenia), Nguyễn Anh Kh&ocirc;i cũng l&agrave; nh&agrave; v&ocirc; địch nam duy nhất trong 6 hạng tuổi đạt được th&ocirc;ng số n&agrave;y.</div>\r\n\r\n<div style="margin: 0px; padding: 0px; color: rgb(66, 69, 71); font-family: ''Times New Roman''; font-size: 16px; line-height: 21.328125px; background-color: rgb(232, 232, 232);">\r\n<div align="center" style="margin: 0px; padding: 0px;">\r\n<table align="center" cellpadding="0" cellspacing="5" style="border-collapse: collapse; border-spacing: 0px; border: 0px solid black; height: 300px; margin: 5px; width: 180px;">\r\n	<tbody>\r\n		<tr>\r\n			<td align="center" style="margin: 0px; padding: 0px 5px; vertical-align: top; text-align: center;">\r\n			<div style="margin: 0px; padding: 0px;">\r\n			<div style="margin: 0px; padding: 0px;"><img alt="“Mỏ” nhân tài... lộ thiên 1" src="http://skds3.vcmedia.vn/9ukLn8yrOFc3MNDifFmRg38p1VxM/Image/2013/01/Ky-thu-Nguyen-Anh-Khoi-voi-mot-tuong-lai-sang-cho-co-vua-VN-34e3b.jpg" style="border: 0px; margin: 5px;" title="“Mỏ” nhân tài... lộ thiên 1" width="400" /></div>\r\n			</div>\r\n\r\n			<div style="margin: 0px; padding: 0px;">&nbsp;<em>Kỳ thủ Nguyễn Anh Kh&ocirc;i (b&ecirc;n phải) với một tương lai tươi s&aacute;ng cho cờ vua Việt Nam</em></div>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n</div>\r\nCần n&oacute;i th&ecirc;m rằng ở bảng U10 nam, Kh&ocirc;i c&oacute; điểm elo ban đầu l&agrave; 1923, chỉ đứng thứ 13 trong 150 VĐV dự tranh, nghĩa l&agrave; cơ hội v&ocirc; địch kh&aacute; nhỏ, chưa n&oacute;i tới việc lập th&agrave;nh t&iacute;ch ấn tượng như thế. Trong khi ấy, theo c&aacute;ch t&iacute;nh điểm của hệ Thụy Sĩ, em sẽ rất kh&oacute; tr&aacute;nh được việc phải đối đầu trực tiếp với nhiều đấu thủ c&oacute; elo cao hơn tr&ecirc;n đường đua tiến đến ng&ocirc;i v&ocirc; địch. Tr&ecirc;n thực tế giải n&agrave;y, Anh Kh&ocirc;i đ&atilde; gặp 3 đối thủ c&oacute; elo hơn m&igrave;nh, trong đ&oacute; c&oacute; Andrey Esipenko (Nga, elo 2120 - hơn Kh&ocirc;i tới gần 200 điểm!) ở 3 v&aacute;n đấu cuối c&ugrave;ng v&agrave; đều chiến thắng.</div>\r\n\r\n<p style="margin: 0px; padding: 7px 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 21.328125px; color: rgb(66, 69, 71); background-color: rgb(232, 232, 232);">Giới chuy&ecirc;n m&ocirc;n đ&aacute;nh gi&aacute; Kh&ocirc;i l&agrave; một thần đồng, bởi kh&aacute;c với nhiều kỳ thủ trong đội tuyển trẻ quốc gia, thời gian tập luyện cờ của em &iacute;t hơn rất nhiều, chỉ xem đ&acirc;y như một th&uacute; vui đơn thuần với 3 buổi tập tại trường Năng khiếu Nghiệp vụ TDTT TP.HCM m&agrave; th&ocirc;i. Tuy nhi&ecirc;n, bằng tr&iacute; nhớ cực tốt v&agrave; tư duy cờ bẩm sinh, cậu b&eacute; đ&atilde; sớm khẳng định t&agrave;i năng của m&igrave;nh với những bước đi vững chắc: v&ocirc; địch to&agrave;n quốc, Đ&ocirc;ng Nam &Aacute; v&agrave; ch&acirc;u &Aacute; c&aacute;c lứa tuổi từ U8 - U10... Một &ldquo;vi&ecirc;n ngọc&rdquo; th&ocirc; thật sự rất đ&aacute;ng được đầu tư, chăm ch&uacute;t bởi nếu được vậy, ch&uacute;ng ta ho&agrave;n to&agrave;n c&oacute; thể chờ đợi sẽ lại c&oacute; một L&ecirc; Quang Li&ecirc;m thứ 2.</p>\r\n\r\n<p style="margin: 0px; padding: 7px 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 21.328125px; color: rgb(66, 69, 71); background-color: rgb(232, 232, 232);">C&oacute; một đặc th&ugrave; trong m&ocirc;n thể thao tr&iacute; tuệ, v&ocirc; địch ở lứa tuổi c&agrave;ng lớn th&igrave; c&agrave;ng kh&oacute;, v&igrave; ngo&agrave;i t&agrave;i năng sẵn c&oacute; c&ograve;n phải k&egrave;m theo sự khổ luyện cũng như sự đầu tư nghi&ecirc;m t&uacute;c. C&aacute;ch nay 4 năm, kỳ thủ Trần Minh Thắng g&acirc;y x&ocirc;n xao dư luận khi gi&agrave;nh HCV lứa tuổi U8 thế giới. Nhưng năm nay, khi đại diện Việt Nam đ&aacute;nh bảng U18, thần đồng ng&agrave;y ấy (elo cao thứ 7 ở lứa tuổi của em) chỉ về đ&iacute;ch ở vị tr&iacute; thứ... 40.</p>\r\n\r\n<p style="margin: 0px; padding: 7px 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 21.328125px; color: rgb(66, 69, 71); background-color: rgb(232, 232, 232);">Trước đ&oacute; nữa, Việt Nam từng nhiều lần g&acirc;y ấn tượng tại giải trẻ thế giới. Đầu ti&ecirc;n l&agrave; ng&ocirc;i v&ocirc; địch lứa tuổi U16 (một th&agrave;nh t&iacute;ch đặc biệt ấn tượng) của Đ&agrave;o Thi&ecirc;n Hải v&agrave;o năm 1993. Kế tiếp l&agrave; ng&ocirc;i nữ ho&agrave;ng U12 nữ của Nguyễn Thị Dung năm 1994. Năm 2000, Nguyễn Ngọc Trường Sơn đoạt ng&ocirc;i v&ocirc; địch U10. Tới năm 2005, đến lượt L&ecirc; Quang Li&ecirc;m xuất sắc gi&agrave;nh c&uacute;p bảng U14.</p>\r\n\r\n<p style="margin: 0px; padding: 7px 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 21.328125px; color: rgb(66, 69, 71); background-color: rgb(232, 232, 232);">Như vậy, ho&agrave;n to&agrave;n c&oacute; thể n&oacute;i rằng chiến c&ocirc;ng của Nguyễn Anh Kh&ocirc;i tại giải cờ vua v&ocirc; địch thế giới 2012 chỉ l&agrave; th&ecirc;m một sự khẳng định cho tiềm năng rất lớn của ch&uacute;ng ta tr&ecirc;n trường quốc tế ở m&ocirc;n thể thao tr&iacute; tuệ n&agrave;y.</p>\r\n\r\n<p style="margin: 0px; padding: 7px 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 21.328125px; color: rgb(66, 69, 71); background-color: rgb(232, 232, 232);">Cũng tại giải VĐTG 2012, ngo&agrave;i chiến thắng của Kh&ocirc;i, cần đề cập th&ecirc;m tới th&agrave;nh t&iacute;ch kh&aacute; ấn tượng của kỳ nữ Nguyễn Thị Mai Hưng với vị tr&iacute; thứ 4 ở bảng U18 nữ (chỉ sau 2 VĐV người Nga - cường quốc số 1 TG - v&agrave; 1 VĐV người H&agrave; Lan). Tr&ecirc;n một chừng mực n&agrave;o đ&oacute;, c&oacute; thể n&oacute;i Mai Hưng cũng l&agrave; một nh&acirc;n t&agrave;i hiếm c&oacute; khi được gọi v&agrave;o ĐTQG ch&iacute;nh thức khi mới 15 tuổi, từng v&ocirc; địch ch&acirc;u &Aacute; lứa tuổi U14 trước khi v&ocirc; địch Quốc gia khi mới 17 tuổi.</p>\r\n\r\n<p style="margin: 0px; padding: 7px 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 21.328125px; color: rgb(66, 69, 71); background-color: rgb(232, 232, 232);"><strong>Nhưng vẫn kh&ocirc;ng dễ bay cao</strong></p>\r\n\r\n<div style="margin: 0px; padding: 0px; color: rgb(66, 69, 71); font-family: ''Times New Roman''; font-size: 16px; line-height: 21.328125px; background-color: rgb(232, 232, 232);">Ch&uacute;ng t&ocirc;i đ&atilde; nhắc đến trường hợp của Trần Minh Thắng, nh&agrave; v&ocirc; địch U8 thế giới c&aacute;ch nay 4 năm (lứa tuổi U12) chỉ đứng thứ 40 tại giải năm nay (lứa tuổi U12). L&yacute; do kh&aacute; đơn giản: Thắng kh&ocirc;ng nhận được sự đầu tư xứng đ&aacute;ng (một c&aacute;ch đặc biệt) để ph&aacute;t triển t&agrave;i năng n&ecirc;n trong khi em tiến được 1 bước th&igrave; ch&iacute;nh những đấu thủ c&ugrave;ng tuổi từng thua Thắng 4 năm trước đ&atilde; tiến th&ecirc;m 2-3 bước để rồi vượt qua em tại giải năm nay.</div>\r\n\r\n<div style="margin: 0px; padding: 0px; color: rgb(66, 69, 71); font-family: ''Times New Roman''; font-size: 16px; line-height: 21.328125px; background-color: rgb(232, 232, 232);">\r\n<div align="center" style="margin: 0px; padding: 0px;">\r\n<table align="center" cellpadding="0" cellspacing="5" style="border-collapse: collapse; border-spacing: 0px; border: 0px solid black; height: 300px; margin: 5px; width: 180px;">\r\n	<tbody>\r\n		<tr>\r\n			<td align="center" style="margin: 0px; padding: 0px 5px; vertical-align: top; text-align: center;">\r\n			<div style="margin: 0px; padding: 0px;">\r\n			<div style="margin: 0px; padding: 0px;"><img alt="“Mỏ” nhân tài... lộ thiên 2" src="http://skds3.vcmedia.vn/9ukLn8yrOFc3MNDifFmRg38p1VxM/Image/2013/01/Sau-Quang-Liem,-Truong-Son-bay-gio-Anh-Khoi-dang-tiep-buoc-cho-co-vua-VN-34e3b.jpg" style="border: 0px; margin: 5px;" title="“Mỏ” nhân tài... lộ thiên 2" width="400" /></div>\r\n			</div>\r\n\r\n			<div style="margin: 0px; padding: 0px;">&nbsp;<em>Kỳ thủ L&ecirc; Quang Li&ecirc;m v&agrave; Nguyễn Ngọc Trường Sơn.</em></div>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n</div>\r\nL&agrave;ng cờ Việt Nam c&oacute; lẽ vẫn chưa th&ocirc;i thắc mắc về sự &ldquo;biến mất&rdquo; của t&agrave;i năng Nguyễn Thị Dung (Thanh H&oacute;a) sau khi em v&ocirc; địch U12 thế giới năm 1994. Một nh&acirc;n t&agrave;i hiếm c&oacute; nữa l&agrave; Đ&agrave;o Thi&ecirc;n Hải - cũng từng được xem l&agrave; &ldquo;thần đồng đất Việt&rdquo; với th&agrave;nh t&iacute;ch v&ocirc; địch ở lứa tuổi 16 với nhiều đối thủ mạnh. Nhưng t&agrave;i năng của Hải kh&ocirc;ng được chắp c&aacute;nh khi chỉ quẩn quanh với m&ocirc;i trường tập luyện v&agrave; thi đấu trong nước, m&atilde;i về sau mới c&oacute; cơ hội được sang Hungary tập huấn một thời gian, nhưng lại c&oacute; qu&aacute; &iacute;t điều kiện va chạm đỉnh cao... Bởi vậy, elo cao nhất của Hải chỉ l&ecirc;n tới 2.609 (năm 2005), k&egrave;m theo đ&oacute; l&agrave; phong độ kh&ocirc;ng ổn định.</div>\r\n\r\n<p style="margin: 0px; padding: 7px 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 21.328125px; color: rgb(66, 69, 71); background-color: rgb(232, 232, 232);">Trong mấy năm qua, giới mộ điệu nhắc nhiều tới c&aacute;i t&ecirc;n L&ecirc; Quang Li&ecirc;m, t&agrave;i năng trẻ từng v&ocirc; địch U14 thế giới năm 2005, sau đ&oacute;, nhờ sự đầu tư mạnh mẽ của Li&ecirc;n đo&agrave;n cờ TP.HCM, bộ m&ocirc;n cờ thuộc Sở TDTT (sau l&agrave; Sở VH-TT&amp;DL) v&agrave; gia đ&igrave;nh, em đ&atilde; c&oacute; những bước ph&aacute;t triển rất tốt. Việc được li&ecirc;n tục tạo cơ hội tham gia thi đấu tại c&aacute;c giải quốc tế, va chạm với những đối thủ mạnh đẳng cấp cao đ&atilde; gi&uacute;p Li&ecirc;m tiến bộ nhanh, trở th&agrave;nh người Việt Nam đầu ti&ecirc;n lọt v&agrave;o tốp 100 rồi tốp 50 kỳ thủ mạnh nhất thế giới trước khi bước v&agrave;o h&agrave;ng ngũ những si&ecirc;u đại kiện tướng (elo hơn 2.700).</p>\r\n\r\n<p style="margin: 0px; padding: 7px 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 21.328125px; color: rgb(66, 69, 71); background-color: rgb(232, 232, 232);">Tuy nhi&ecirc;n, c&oacute; một nh&acirc;n t&agrave;i thực ra kh&ocirc;ng hề k&eacute;m cạnh so với Li&ecirc;m - đại kiện tướng Nguyễn Ngọc Trường Sơn. Chỉ tiếc một nỗi, sau khi v&ocirc; địch U10 thế giới năm 2000, Sơn kh&ocirc;ng c&oacute; được sự đầu tư mạnh mẽ của địa phương (Ki&ecirc;n Giang), phần bởi kh&oacute; khăn của bộ m&ocirc;n cờ tỉnh, phần v&igrave; gia đ&igrave;nh cũng kh&ocirc;ng c&oacute; điều kiện kinh tế tốt... M&atilde;i sau n&agrave;y, phải tới khi dư luận l&ecirc;n tiếng th&igrave; Sơn mới nhận được th&ecirc;m sự hỗ trợ từ Li&ecirc;n đo&agrave;n cờ Việt Nam, được gửi sang tập huấn tại &ldquo;l&ograve;&rdquo; đ&agrave;o tạo của C&ocirc;ng ty Chesscom do th&acirc;n phụ của nữ đại kiện tướng Ho&agrave;ng Thanh Trang l&agrave;m chủ ở Hungary, sau lại được th&ecirc;m một số nguồn t&agrave;i trợ n&ecirc;n mới c&oacute; cơ hội dự nhiều giải quốc tế. Tiếc rằng sự đầu tư ấy tới hơi chậm n&ecirc;n Sơn đ&atilde; chưa thể bắt kịp bước tiến của Li&ecirc;m.</p>\r\n\r\n<p align="left" style="margin: 0px; padding: 7px 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 21.328125px; color: rgb(66, 69, 71); background-color: rgb(232, 232, 232);">&ldquo;Mỏ&rdquo; nh&acirc;n t&agrave;i cờ vua Việt Nam đ&atilde; ph&aacute;t lộ. Cần lắm một hướng đi, một c&aacute;ch l&agrave;m, một chiến lược ph&aacute;t triển để khai th&aacute;c v&agrave; ph&aacute;t huy tiềm năng ấy từ những giới chức hữu tr&aacute;ch!&nbsp;</p>\r\n', '“Mỏ” nhân tài... lộ thiên', 0);
INSERT INTO `chuathienquang_news` (`id`, `id_category`, `author`, `date`, `content`, `title`, `type`) VALUES
(109, 15, 'BBT Website', '2013-02-03 15:22:38', '<p style="margin: 0px 0px 0.5em; padding: 5px 0px 3px; font-size: 14px; line-height: 22.390625px; font-family: Arial; color: rgb(0, 0, 0);"><strong style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">&quot;Lắm t&agrave;i, nhiều tật&quot; - ch&acirc;n dung của &quot;nh&acirc;n t&agrave;i&quot; thường được vẽ như vậy từ h&agrave;ng trăm năm nay, mặc nhi&ecirc;n bao h&agrave;m cả yếu tố &quot;t&agrave;i&quot; v&agrave; &quot;tật&quot;. Tuy nhi&ecirc;n, với &quot;L&yacute; thuyết Nh&acirc;n t&agrave;i 3C&quot;, Dave Ulrich - người được coi l&agrave; &quot;bộ &oacute;c&quot; số 1 thế giới về nh&acirc;n sự - đ&atilde; vẽ lại một c&aacute;ch ho&agrave;n hảo ch&acirc;n dung người t&agrave;i của thời đại mới.</strong></p>\r\n\r\n<div id="VietAd" style="margin: 0px; padding: 0px; color: rgb(54, 54, 54); font-family: Arial, Helvetica, sans-serif; line-height: normal;">\r\n<p style="margin: 0px 0px 0.5em; padding: 5px 0px 3px; font-size: 14px; line-height: 1.6em; font-family: Arial; color: rgb(0, 0, 0);"><span id="CtxText" style="margin: 0px; padding: 0px;">Hai người c&ugrave;ng l&agrave;m trong một c&ocirc;ng ty, người thứ nhất giỏi, l&agrave;m được rất nhiều việc nhưng cũng &quot;ph&aacute;&quot; rất nhiều thứ; người thứ hai năng lực b&igrave;nh thường, tạo ra gi&aacute; trị ở mức b&igrave;nh thường nhưng &quot;ph&aacute;&quot; &iacute;t, thậm ch&iacute; l&agrave;m ra được c&aacute;i g&igrave; l&agrave; chắc chắn c&aacute;i đ&oacute;. Người thứ hai chắc chắn kh&ocirc;ng phải l&agrave; người t&agrave;i, song nếu coi người thứ nhất l&agrave; người t&agrave;i th&igrave; cũng chưa thỏa đ&aacute;ng v&igrave; mặc d&ugrave; anh ta giỏi nhưng gi&aacute; trị tạo ra từ sự giỏi đ&oacute; nhiều khi lại kh&ocirc;ng bằng người b&igrave;nh thường kia, c&oacute; khi lại c&ograve;n &acirc;m.</span></p>\r\n\r\n<p style="margin: 0px 0px 0.5em; padding: 5px 0px 3px; font-size: 14px; line-height: 1.6em; font-family: Arial; color: rgb(0, 0, 0);"><span id="CtxText" style="margin: 0px; padding: 0px;">Tư tưởng v&agrave; học thuyết của Dave Ulrich - Gi&aacute;o sư Đại học Michigan (Hoa Kỳ) - về nh&acirc;n lực, nh&acirc;n t&agrave;i, trong đ&oacute; đặc biệt l&agrave; &quot;L&yacute; thuyết Nh&acirc;n t&agrave;i 3C&quot; (3C Talent Formula) đang được &aacute;p dụng phổ biến tr&ecirc;n khắp thế giới, tỏ ra khả dụng đối với c&aacute;c tổ chức trong việc ph&aacute;t hiện &quot;người giỏi&quot;, từ đ&oacute; bồi dưỡng, vun đắp họ th&agrave;nh &quot;người t&agrave;i&quot;, mang lại gi&aacute; trị cao cho tổ chức, x&atilde; hội.</span></p>\r\n\r\n<p style="margin: 0px 0px 0.5em; padding: 5px 0px 3px; font-size: 14px; line-height: 1.6em; font-family: Arial; color: rgb(0, 0, 0);"><span id="CtxText" style="margin: 0px; padding: 0px;"><b style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">Giỏi chưa hẳn đ&atilde; t&agrave;i!</b></span></p>\r\n\r\n<p style="margin: 0px 0px 0.5em; padding: 5px 0px 3px; font-size: 14px; line-height: 1.6em; font-family: Arial; color: rgb(0, 0, 0);"><span id="CtxText" style="margin: 0px; padding: 0px;">L&acirc;u nay ch&uacute;ng ta thường quan niệm &quot;giỏi&quot; v&agrave; &quot;t&agrave;i&quot; l&agrave; một cặp song h&agrave;nh, gắn kết khăng kh&iacute;t với nhau. Tuy nhi&ecirc;n, dưới g&oacute;c nh&igrave;n của Dave Ulrich, &quot;giỏi&quot; mới chỉ l&agrave; điều kiện cần, một người muốn trở th&agrave;nh &quot;nh&acirc;n t&agrave;i&quot; thực thụ th&igrave; c&ograve;n phải hội đủ nhiều yếu tố kh&aacute;c.</span></p>\r\n\r\n<p style="margin: 0px 0px 0.5em; padding: 5px 0px 3px; font-size: 14px; line-height: 1.6em; font-family: Arial; color: rgb(0, 0, 0);"><span id="CtxText" style="margin: 0px; padding: 0px;">Một &quot;người giỏi&quot; sẽ kh&ocirc;ng thể l&agrave; &quot;người t&agrave;i&quot; khi c&aacute;i &quot;giỏi&quot; đ&oacute; l&agrave; trời ph&uacute; v&agrave; chỉ nằm im ở dạng tiềm năng, kh&ocirc;ng được trưng dụng để tạo ra gi&aacute; trị. Nghĩa l&agrave;, một người cho d&ugrave; th&ocirc;ng minh, c&oacute; tố chất đến mấy đi chăng nữa nhưng kh&ocirc;ng chịu l&agrave;m hoặc kh&ocirc;ng biết c&aacute;ch l&agrave;m th&igrave; vẫn bị coi l&agrave; kh&ocirc;ng c&oacute; gi&aacute; trị.</span></p>\r\n\r\n<table border="0" style="margin: 10px auto; padding: 0px; border-collapse: collapse; border-spacing: 0px; color: rgb(102, 102, 102); font-size: 1.2em;">\r\n	<tbody style="margin: 0px; padding: 0px;">\r\n		<tr style="margin: 0px; padding: 0px; color: rgb(54, 54, 54); font-size: 12px;">\r\n			<td style="margin: 0px; padding: 0px;"><span id="CtxText" style="margin: 0px; padding: 0px;"><img alt="" class="center" height="204" src="http://vef.vn/assets/images/Dave-Ulrich-3_1314094050.jpg" style="margin: 0px auto; padding: 0px; border: medium none; text-align: center; float: none; display: block;" title="" width="275" /></span></td>\r\n		</tr>\r\n		<tr style="margin: 0px; padding: 0px; color: rgb(54, 54, 54); font-size: 12px;">\r\n			<td style="margin: 0px; padding: 0px; text-align: center;"><span id="CtxText" style="margin: 0px; padding: 0px;"><i style="margin: 0px; padding: 0px;"><span style="margin: 0px; padding: 0px; color: rgb(128, 0, 0);">Gi&aacute;o sư Dave Ulrich</span></i></span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style="margin: 0px 0px 0.5em; padding: 5px 0px 3px; font-size: 14px; line-height: 1.6em; font-family: Arial; color: rgb(0, 0, 0);"><span id="CtxText" style="margin: 0px; padding: 0px;">Ngược lại, cũng c&oacute; những người giỏi, như trường hợp đ&atilde; n&ecirc;u ở đầu b&agrave;i, tạo ra nhiều gi&aacute; trị nhưng cũng g&acirc;y ra kh&ocirc;ng &iacute;t hệ lụy, phiền to&aacute;i th&igrave; cũng kh&ocirc;ng thể gọi l&agrave; &quot;nh&acirc;n t&agrave;i&quot;, l&agrave; &quot;nguy&ecirc;n kh&iacute;&quot; của tổ chức được.</span></p>\r\n\r\n<p style="margin: 0px 0px 0.5em; padding: 5px 0px 3px; font-size: 14px; line-height: 1.6em; font-family: Arial; color: rgb(0, 0, 0);"><span id="CtxText" style="margin: 0px; padding: 0px;"><b style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">Đ&aacute;nh thức nh&acirc;n t&agrave;i bằng &quot;L&yacute; thuyết Nh&acirc;n t&agrave;i 3C&quot;</b></span></p>\r\n\r\n<p style="margin: 0px 0px 0.5em; padding: 5px 0px 3px; font-size: 14px; line-height: 1.6em; font-family: Arial; color: rgb(0, 0, 0);"><span id="CtxText" style="margin: 0px; padding: 0px;">&quot;L&yacute; thuyết Nh&acirc;n t&agrave;i 3C&quot; (Talent =&nbsp;<b style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">C</b>ompetence*<b style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">C</b>ommitment*<b style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">C</b>ontribution / Nh&acirc;n t&agrave;i = Năng lực*Cam kết*Cống hiến) được Dave Ulrich x&acirc;y dựng sau khi khảo s&aacute;t, nghi&ecirc;n cứu tr&ecirc;n h&agrave;ng ngh&igrave;n doanh nghiệp, trong đ&oacute; c&oacute; hơn một nửa số c&ocirc;ng ty trong danh s&aacute;ch Fortune 500 (được coi l&agrave; những c&ocirc;ng ty tinh hoa nhất to&agrave;n cầu); phỏng vấn hơn 40.000 nh&acirc;n sự được coi l&agrave; &quot;người t&agrave;i&quot; tr&ecirc;n khắp thế giới.</span></p>\r\n\r\n<p style="margin: 0px 0px 0.5em; padding: 5px 0px 3px; font-size: 14px; line-height: 1.6em; font-family: Arial; color: rgb(0, 0, 0);"><span id="CtxText" style="margin: 0px; padding: 0px;"><i style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;"><b style="margin: 0px; padding: 0px;">N</b><b style="margin: 0px; padding: 0px;">ăng lực (</b></i><b style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;"><i style="margin: 0px; padding: 0px;">Competence)</i></b></span></p>\r\n\r\n<p style="margin: 0px 0px 0.5em; padding: 5px 0px 3px; font-size: 14px; line-height: 1.6em; font-family: Arial; color: rgb(0, 0, 0);"><span id="CtxText" style="margin: 0px; padding: 0px;">Một nh&acirc;n sự được cho l&agrave; c&oacute; năng lực khi anh ta c&oacute; kiến thức, kĩ năng v&agrave; gi&aacute; trị ph&ugrave; hợp với c&ocirc;ng việc của h&ocirc;m nay v&agrave; nhất l&agrave; trong tương lai. Năng lực li&ecirc;n quan tới 3 &quot;đ&uacute;ng&quot;: kỹ năng đ&uacute;ng, vị tr&iacute; đ&uacute;ng v&agrave; c&ocirc;ng việc đ&uacute;ng.</span></p>\r\n\r\n<p style="margin: 0px 0px 0.5em; padding: 5px 0px 3px; font-size: 14px; line-height: 1.6em; font-family: Arial; color: rgb(0, 0, 0);"><span id="CtxText" style="margin: 0px; padding: 0px;">C&oacute; 4 bước để x&aacute;c định v&agrave; ph&aacute;t triển năng lực: 1.&nbsp;<i style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">Thiết lập một l&yacute; thuyết hoặc chuẩn mực</i>&nbsp;(những năng lực hiện tại về x&atilde; hội v&agrave; kỹ thuật m&agrave; c&ocirc;ng ty m&igrave;nh đang c&oacute; l&agrave; g&igrave;? C&ocirc;ng ty đang phải đối mặt với những thay đổi về m&ocirc;i trường kinh doanh như thế n&agrave;o v&agrave; đ&atilde; c&oacute; những chiến lược g&igrave; để đối ph&oacute;? Dựa v&agrave;o m&ocirc;i trường tương lai v&agrave; những lựa chọn về chiến lược, nh&acirc;n vi&ecirc;n cần phải thể hiện m&igrave;nh c&oacute; những năng lực về kỹ thuật, x&atilde; hội n&agrave;o?); 2.&nbsp;<i style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">Đ&aacute;nh gi&aacute; c&aacute; nh&acirc;n v&agrave; tập thể</i>&nbsp;(khi đ&atilde; c&oacute; chuẩn mực, c&aacute;c nh&acirc;n vi&ecirc;n sẽ được đ&aacute;nh gi&aacute; về những g&igrave; họ đạt hoặc kh&ocirc;ng đạt được. Trong những năm gần đ&acirc;y, đ&aacute;nh gi&aacute; nh&acirc;n t&agrave;i đ&atilde; bắt đầu dựa tr&ecirc;n cả kết quả c&ocirc;ng việc v&agrave; c&aacute;ch thức h&agrave;nh xử); 3.&nbsp;<i style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">Ph&aacute;t triển nh&acirc;n t&agrave;i</i>&nbsp;(Bộ c&ocirc;ng cụ &quot;6B&quot; gồm Mua -&nbsp;<b style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">B</b>uying: tuyển dụng, t&igrave;m nguồn cung v&agrave; gắn chặt t&agrave;i năng mới v&agrave;o tổ chức; X&acirc;y dựng -&nbsp;<b style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">B</b>uilding: ph&aacute;t triển nh&acirc;n vi&ecirc;n th&ocirc;ng qua việc huấn luyện, c&ocirc;ng việc thực tế hoặc kinh nghiệm sống; Vay mượn -&nbsp;<b style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">B</b>orrowing: Đưa kiến thức v&agrave;o tổ chức th&ocirc;ng qua qua c&aacute;c chuy&ecirc;n gia tư vấn hoặc đối t&aacute;c; R&agrave;ng buộc -&nbsp;<b style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">B</b>ounding: Bổ nhiệm đ&uacute;ng người v&agrave;o những vị tr&iacute; quan trọng; Cắt giảm -<b style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">B</b>ouncing: Loại bỏ những nh&acirc;n sự thừa, yếu k&eacute;m; Kết tinh -&nbsp;<b style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">B</b>inding: Giữ ch&acirc;n nh&acirc;n t&agrave;i). 4.&nbsp;<i style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">Theo d&otilde;i v&agrave; ph&aacute;t hiện, ph&aacute;t triển năng lực</i>&nbsp;(Người t&agrave;i ngo&agrave;i việc được đ&aacute;nh gi&aacute; th&ocirc;ng qua lượng tiền họ tạo ra cho c&ocirc;ng ty th&igrave; c&ograve;n được đ&aacute;nh gi&aacute; th&ocirc;ng qua khả năng tạo ra những người t&agrave;i).</span></p>\r\n\r\n<p style="margin: 0px 0px 0.5em; padding: 5px 0px 3px; font-size: 14px; line-height: 1.6em; font-family: Arial; color: rgb(0, 0, 0);"><span id="CtxText" style="margin: 0px; padding: 0px;">&quot;Nh&acirc;n t&agrave;i phải l&agrave; những người c&oacute; khả năng l&agrave;m tốt những c&ocirc;ng việc của ng&agrave;y h&ocirc;m nay v&agrave; đặc biệt l&agrave;<b style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">của tương lai</b>. Sẽ hết sức sai lầm nếu chỉ so s&aacute;nh th&agrave;nh t&iacute;ch của qu&aacute; khứ để x&aacute;c định ai l&agrave; nh&acirc;n t&agrave;i, m&agrave; phải nh&igrave;n về ph&iacute;a trước xem ở tương lai tổ chức m&igrave;nh sẽ cần những con người như thế n&agrave;o,&quot; Dave Ulrich đặc biệt nhấn mạnh.</span></p>\r\n\r\n<p style="margin: 0px 0px 0.5em; padding: 5px 0px 3px; font-size: 14px; line-height: 1.6em; font-family: Arial; color: rgb(0, 0, 0);"><span id="CtxText" style="margin: 0px; padding: 0px;"><b style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;"><i style="margin: 0px; padding: 0px;">Cam kết - Commitment</i></b></span></p>\r\n\r\n<table align="right" border="0" style="margin: 10px auto; padding: 0px; border-collapse: collapse; border-spacing: 0px; color: rgb(102, 102, 102); font-size: 1.2em;" width="300">\r\n	<tbody style="margin: 0px; padding: 0px;">\r\n		<tr style="margin: 0px; padding: 0px; color: rgb(54, 54, 54); font-size: 12px;">\r\n			<td style="margin: 0px; padding: 0px;"><span id="CtxText" style="margin: 0px; padding: 0px;"><img class="right" height="350" src="http://vef.vn/assets/images/baiVMC23.8_1314094061.jpg" style="margin: 0px 0px 0px 20px; padding: 0px; border: medium none; text-align: right;" /></span></td>\r\n		</tr>\r\n		<tr style="margin: 0px; padding: 0px; color: rgb(54, 54, 54); font-size: 12px;">\r\n			<td style="margin: 0px; padding: 0px;">\r\n			<p align="center" style="margin: 0px 0px 0.5em; padding: 5px 0px 3px; font-size: 14px; line-height: 1.6em; font-family: Arial; color: rgb(0, 0, 0);"><span id="CtxText" style="margin: 0px; padding: 0px;"><span style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif; color: rgb(128, 0, 0);"><b style="margin: 0px; padding: 0px;">Gi&aacute;o sư Dave Ulrich</b></span></span></p>\r\n\r\n			<p style="margin: 0px 0px 0.5em; padding: 5px 0px 3px; font-size: 14px; line-height: 1.6em; font-family: Arial; color: rgb(0, 0, 0);"><span id="CtxText" style="margin: 0px; padding: 0px;"><span style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif; color: rgb(128, 0, 0);">- Nh&acirc;n vật c&oacute; ảnh hưởng nhất thế giới trong lĩnh vực ph&aacute;t triển nh&acirc;n lực (Tạp ch&iacute; HR 2010)<br style="margin: 0px; padding: 0px;" />\r\n			- Giải Nobel Colloquia d&agrave;nh cho những t&ecirc;n tuổi dẫn dắt nền tư tưởng kinh tế v&agrave; kinh doanh (2010)<br style="margin: 0px; padding: 0px;" />\r\n			- Nh&agrave; gi&aacute;o dục quản trị v&agrave; l&agrave; &quot;bộ &oacute;c&quot; quản trị số 1 thế giới (Business Week, 2001)<br style="margin: 0px; padding: 0px;" />\r\n			- Một trong 05 nh&agrave; cố vấn quản trị h&agrave;ng đầu thế giới (Tạp ch&iacute; Forbes, 2000)<br style="margin: 0px; padding: 0px;" />\r\n			- Một trong 10 nh&agrave; tư tưởng s&aacute;ng tạo nhất thế giới.</span></span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n&nbsp;\r\n\r\n<p style="margin: 0px 0px 0.5em; padding: 5px 0px 3px; font-size: 14px; line-height: 1.6em; font-family: Arial; color: rgb(0, 0, 0);"><span id="CtxText" style="margin: 0px; padding: 0px;">Năng lực l&agrave; kh&ocirc;ng đủ nếu thiếu cam kết! Cam kết c&oacute; nghĩa l&agrave; nh&acirc;n vi&ecirc;n sẵn s&agrave;ng cống hiến hết m&igrave;nh cho sự th&agrave;nh c&ocirc;ng của c&ocirc;ng ty. Tr&ecirc;n thực tế, c&oacute; những người rất giỏi, th&ocirc;ng minh, thạo việc nhưng do kh&ocirc;ng &quot;chịu l&agrave;m&quot; hoặc l&agrave;m kh&ocirc;ng hết m&igrave;nh n&ecirc;n r&uacute;t cục họ kh&ocirc;ng tạo ra gi&aacute; trị, đ&oacute;ng g&oacute;p g&igrave;.</span></p>\r\n\r\n<p style="margin: 0px 0px 0.5em; padding: 5px 0px 3px; font-size: 14px; line-height: 1.6em; font-family: Arial; color: rgb(0, 0, 0);"><span id="CtxText" style="margin: 0px; padding: 0px;">Theo Dave Ulrich, nh&acirc;n vi&ecirc;n thường sẽ &quot;hết m&igrave;nh&quot; khi họ &quot;tạo ra gi&aacute; trị cho tổ chức th&igrave; cũng sẽ nhận được những gi&aacute; trị từ tổ chức&quot;. &quot;Gi&aacute; trị&quot; đ&oacute; c&oacute; thể l&agrave;: 1.&nbsp;<i style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">Tầm nh&igrave;n</i>: Mục ti&ecirc;u, hướng đi; 2.&nbsp;<i style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">Cơ hội</i>: Khả năng trưởng th&agrave;nh, ph&aacute;t triển v&agrave; học hỏi; 3.&nbsp;<i style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">Ưu đ&atilde;i</i>: Lương, thưởng hợp l&yacute;; 4.&nbsp;<i style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">T&aacute;c động</i>: C&oacute; thể thấy được th&agrave;nh quả hoặc hiệu quả c&ocirc;ng việc; 5.&nbsp;<i style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">Cộng đồng</i>: Đồng nghiệp, quản l&yacute; v&agrave; l&atilde;nh đạo - những người x&acirc;y dựng n&ecirc;n cộng đồng; 6.&nbsp;<i style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">Truyền th&ocirc;ng</i>: Lu&ocirc;n biết chuyện g&igrave; đang diễn ra v&agrave; tại sao; 7.&nbsp;<i style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">Sự linh hoạt - M&ocirc;i trường l&agrave;m việc</i>: Được trao quyền lựa chọn v&agrave; linh hoạt trong điều kiện l&agrave;m việc.</span></p>\r\n\r\n<p style="margin: 0px 0px 0.5em; padding: 5px 0px 3px; font-size: 14px; line-height: 1.6em; font-family: Arial; color: rgb(0, 0, 0);"><span id="CtxText" style="margin: 0px; padding: 0px;"><b style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;"><i style="margin: 0px; padding: 0px;">Cống hiến - Contribution</i></b></span></p>\r\n\r\n<p style="margin: 0px 0px 0.5em; padding: 5px 0px 3px; font-size: 14px; line-height: 1.6em; font-family: Arial; color: rgb(0, 0, 0);"><span id="CtxText" style="margin: 0px; padding: 0px;">Trước đ&acirc;y, khi đ&aacute;nh gi&aacute; người t&agrave;i thường ta chỉ dừng lại ở 2 yếu tố: năng lực (c&oacute; khả năng l&agrave;m việc) v&agrave; cam kết (c&oacute; &yacute; ch&iacute; l&agrave;m việc). Tuy nhi&ecirc;n, với thế hệ nh&acirc;n lực hiện nay v&agrave; tương lai, chừng đ&oacute; l&agrave; chưa đủ, người t&agrave;i c&ograve;n phải l&agrave; người biết cống hiến v&agrave; được ghi nhận. Nghĩa l&agrave; họ c&ograve;n phải &yacute; thức được &yacute; nghĩa v&agrave; mục đ&iacute;ch của c&ocirc;ng việc m&igrave;nh l&agrave;m, thậm ch&iacute;, như Dave Ulrich nhấn mạnh, họ c&ograve;n cần c&oacute; được sự &quot;thăng hoa&quot;, sự &quot;vi&ecirc;n m&atilde;n&quot; trong cuộc sống v&agrave; c&ocirc;ng việc.</span></p>\r\n\r\n<p style="margin: 0px 0px 0.5em; padding: 5px 0px 3px; font-size: 14px; line-height: 1.6em; font-family: Arial; color: rgb(0, 0, 0);"><span id="CtxText" style="margin: 0px; padding: 0px;">Điều đ&oacute; sẽ đạt được khi c&ugrave;ng với tổ chức, c&aacute; nh&acirc;n trả lời được 7 c&acirc;u hỏi sau: 1.&nbsp;<i style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">T&ocirc;i l&agrave; ai</i>? (người lao động hiểu được m&igrave;nh v&agrave; vị tr&iacute;, vai tr&ograve;, tr&aacute;ch nhiệm... của m&igrave;nh trong tổ chức); 2. T<i style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">&ocirc;i sẽ đi đến đ&acirc;u v&agrave; tại sao</i>? (tổ chức gi&uacute;p được g&igrave; cho người lao động để họ đạt đến mục ti&ecirc;u của m&igrave;nh); 3.&nbsp;<i style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">T&ocirc;i sẽ đi với ai</i>? (tổ chức x&acirc;y dựng nh&oacute;m hỗ trợ như thế n&agrave;o để người lao động kh&ocirc;ng cảm thấy đơn độc); 4.&nbsp;<i style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">L&agrave;m thế n&agrave;o để t&ocirc;i r&egrave;n luyện về tinh thần</i>? (tổ chức h&agrave;nh xử như thế n&agrave;o đối với c&aacute;c chuẩn mực tinh thần như sự khi&ecirc;m tốn, tinh thần phục vụ, sự tha thứ, l&ograve;ng biết ơn...); 5.&nbsp;<i style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">Những thử th&aacute;ch n&agrave;o m&agrave; t&ocirc;i sẽ vượt qua</i>? (tổ chức gi&uacute;p người lao động l&agrave;m thế n&agrave;o để vượt qua những thử th&aacute;ch một c&aacute;ch dễ d&agrave;ng, đầy h&agrave;o hứng v&agrave; th&uacute; vị); 6.&nbsp;<i style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">L&agrave;m thế n&agrave;o để t&ocirc;i c&oacute; thể tiếp cận với c&aacute;c nguồn lực</i>? (tổ chức gi&uacute;p người lao động giải quyết những vấn đề về sức khỏe, kh&ocirc;ng gian v&agrave; c&aacute;c nhu cầu t&agrave;i ch&iacute;nh); 7.&nbsp;<i style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">Nguồn cảm hứng của t&ocirc;i l&agrave; g&igrave;</i>? (tổ chức sẽ l&agrave;m g&igrave; để gi&uacute;p người lao động lu&ocirc;n cảm thấy thoải m&aacute;i, vui vẻ).</span></p>\r\n\r\n<p style="margin: 0px 0px 0.5em; padding: 5px 0px 3px; font-size: 14px; line-height: 1.6em; font-family: Arial; color: rgb(0, 0, 0);"><span id="CtxText" style="margin: 0px; padding: 0px;">Trong &quot;phương tr&igrave;nh 3C&quot; như đ&atilde; n&ecirc;u tr&ecirc;n, 3 biến được kết nối với nhau theo cấp số nh&acirc;n, chứ kh&ocirc;ng phải cấp số cộng. Nghĩa l&agrave;, nếu một biến n&agrave;o đ&oacute; bị mất đi, 2 biến c&ograve;n lại sẽ kh&ocirc;ng thể thay thế được. Chẳng hạn, người c&oacute; năng lực k&eacute;m sẽ kh&ocirc;ng bao giờ l&agrave; &quot;nh&acirc;n t&agrave;i&quot; kể cả khi anh ta c&oacute; sự cống hiến v&agrave; hết m&igrave;nh, v&agrave; ngược lại. N&oacute;i c&aacute;ch kh&aacute;c, nh&acirc;n t&agrave;i phải c&oacute;&nbsp;<i style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">kỹ năng</i>,&nbsp;<i style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">&yacute; ch&iacute;</i>&nbsp;v&agrave;&nbsp;<i style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">mục đ&iacute;ch</i>&nbsp;v&agrave; phải thể hiện qua&nbsp;<i style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">được việc</i>,&nbsp;<i style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">hết m&igrave;nh</i>&nbsp;v&agrave;&nbsp;<i style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">cống hiến</i>.</span></p>\r\n\r\n<p style="margin: 0px 0px 0.5em; padding: 5px 0px 3px; font-size: 14px; line-height: 1.6em; font-family: Arial; color: rgb(0, 0, 0);"><span id="CtxText" style="margin: 0px; padding: 0px;">Để kh&eacute;p lại c&ocirc;ng tr&igrave;nh rất c&oacute; &yacute; nghĩa nhưng lại được tr&igrave;nh b&agrave;y một c&aacute;ch đơn giản, dễ tiếp cận, Dave Ulrich v&iacute; von khi nghĩ về nh&acirc;n t&agrave;i v&agrave; ph&aacute;t triển nh&acirc;n t&agrave;i th&igrave; phải nghĩ đến cả&nbsp;<i style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">c&aacute;i đầu</i>&nbsp;(năng lực),&nbsp;<i style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">b&agrave;n tay</i>v&agrave;&nbsp;<i style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">đ&ocirc;i ch&acirc;n</i>&nbsp;(chịu l&agrave;m) v&agrave;&nbsp;<i style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif;">tr&aacute;i tim</i>&nbsp;(sự vi&ecirc;n m&atilde;n, thăng hoa, cống hiến). Chứ nếu nghĩ nh&acirc;n t&agrave;i, theo c&aacute;ch truyền thống, chỉ l&agrave; &quot;người giỏi&quot; th&igrave; kh&ocirc;ng đủ v&agrave; đ&atilde; qu&aacute; lạc hậu.</span></p>\r\n\r\n<table align="center" border="1" style="margin: 10px auto; padding: 0px; border-collapse: collapse; border-spacing: 0px; color: rgb(102, 102, 102); font-size: 1.2em;" width="550">\r\n	<tbody style="margin: 0px; padding: 0px;">\r\n		<tr style="margin: 0px; padding: 0px; color: rgb(54, 54, 54); font-size: 12px;">\r\n			<td style="margin: 0px; padding: 0px;">\r\n			<p style="margin: 0px 0px 0.5em; padding: 5px 0px 3px; font-size: 14px; line-height: 1.6em; font-family: Arial; color: rgb(0, 0, 0);"><span id="CtxText" style="margin: 0px; padding: 0px;"><span style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif; color: rgb(128, 0, 0);">Nhận lời mời của Trường Doanh Nh&acirc;n PACE, ng&agrave;y 29/9/2011, Gi&aacute;o sư Dave Ulrich sẽ đến Việt Nam để chủ tr&igrave; hội thảo quốc tế với chủ đề &quot;Tư duy lại nh&acirc;n lực v&agrave; nh&acirc;n t&agrave;i&quot;. Hội thảo trong một ng&agrave;y n&agrave;y sẽ tập trung b&agrave;n về những xu hướng mới nhất, những tư tưởng mới nhất về l&atilde;nh đạo (Leadership), về nh&acirc;n lực (Human Resources - HR), về nh&acirc;n t&agrave;i (Talent), v&agrave; về văn h&oacute;a tổ chức (Culture) ở cả g&oacute;c độ vi m&ocirc; của từng doanh nghiệp (chiến lược nh&acirc;n lực c&ocirc;ng ty) v&agrave; ở cả g&oacute;c độ vĩ m&ocirc; của nền kinh tế (chiến lược nh&acirc;n lực quốc gia).</span></span></p>\r\n\r\n			<p style="margin: 0px 0px 0.5em; padding: 5px 0px 3px; font-size: 14px; line-height: 1.6em; font-family: Arial; color: rgb(0, 0, 0);"><span id="CtxText" style="margin: 0px; padding: 0px;"><span style="margin: 0px; padding: 0px; font-family: Arial, Helvetica, sans-serif; color: rgb(128, 0, 0);">Hội thảo lần n&agrave;y nằm trong chuỗi hội thảo quốc tế về kinh tế - kinh doanh do PACE tổ chức thường ni&ecirc;n: Hội thảo về marketing (năm 2007) do Philip Kotler, &quot;cha đẻ&quot; marketing hiện đại, chủ tr&igrave;; Hội thảo về cạnh tranh (năm 2008) do Michael Porter, &quot;cha đẻ&quot; chiến lược cạnh tranh, chủ tr&igrave;; Hội thảo về kinh tế (năm 2009) do Paul Krugman, chủ nh&acirc;n Nobel kinh tế 2008, chủ tr&igrave;; Hội thảo về chiến lược (năm 2010) do Michael Porter chủ tr&igrave;...</span></span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n</div>\r\n', 'Cùng bậc thầy thế giới định nghĩa lại nhân tài', 0),
(110, 15, 'BBT Website', '2013-02-03 15:23:36', '<h2 class="pHead" style="margin: 10px 0px 0px; padding: 10px 0px; border: 0px; list-style: none; outline: 0px; font-size: 12.2pt; font-family: ''Times New Roman'', Times, serif; line-height: 15pt;">Tối 19/11, 14 c&aacute; nh&acirc;n v&agrave; tập thể xuất sắc đ&atilde; được vinh danh tại lễ trao giải thưởng Nh&acirc;n t&agrave;i đất Việt lần thứ 8, nhờ những c&ocirc;ng tr&igrave;nh, sản phẩm c&oacute; t&iacute;nh ứng dụng cao v&agrave; c&oacute; đ&oacute;ng g&oacute;p lớn trong thực tiễn, thuộc c&aacute;c lĩnh vực c&ocirc;ng nghệ th&ocirc;ng tin - truyền th&ocirc;ng, khoa học ứng dụng v&agrave; y dược.</h2>\r\n\r\n<p class="pBody" style="margin: 0px 0px 10px; padding: 0px; border: 0px; list-style: none; outline: 0px; font-size: 16px; line-height: 20px; font-family: ''Times New Roman'', Times, serif; text-align: justify;">Đ&acirc;y l&agrave; năm thứ 8 li&ecirc;n tiếp&nbsp;<tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">giải thưởng</tag>&nbsp;<tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">Nh&acirc;n t&agrave;i đất Việt</tag>&nbsp;được tổ chức dưới sự bảo trợ của Bộ Th&ocirc;ng tin v&agrave; Truyền th&ocirc;ng, Bộ Khoa học v&agrave; C&ocirc;ng nghệ, Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo, Bộ Y tế v&agrave; Viện Khoa học v&agrave; C&ocirc;ng nghệ Việt Nam. Tập đo&agrave;n Bưu ch&iacute;nh Viễn th&ocirc;ng Việt Nam (VNPT) đ&oacute;ng vai tr&ograve; l&agrave; nh&agrave; tổ chức, đồng thời cũng l&agrave; nh&agrave; t&agrave;i trợ ch&iacute;nh.</p>\r\n\r\n<p class="pBody" style="margin: 0px 0px 10px; padding: 0px; border: 0px; list-style: none; outline: 0px; font-size: 16px; line-height: 20px; font-family: ''Times New Roman'', Times, serif; text-align: justify;">So với c&aacute;c năm,&nbsp;<tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">Nh&acirc;n t&agrave;i Đất Việt</tag>&nbsp;năm thứ 8 c&oacute; một số điểm mới. Lĩnh vực CNTT được chia th&agrave;nh 2 nh&oacute;m sản phẩm: &ldquo;Sản phẩm CNTT th&agrave;nh c&ocirc;ng&rdquo; v&agrave; &ldquo;Sản phẩm CNTT triển vọng&rdquo;. Mỗi nh&oacute;m sản phẩm sẽ c&oacute; 1 giải Nhất, 1 giải Nh&igrave; v&agrave; 1<tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">giải Ba</tag>&nbsp;với trị gi&aacute;&nbsp;<tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">giải thưởng</tag>&nbsp;tương ứng l&agrave; 100 triệu, 50 triệu v&agrave; 30 triệu đồng. Ban tổ chức cũng đề ra những ti&ecirc;u ch&iacute; cụ thể v&agrave; chi tiết hơn d&agrave;nh ri&ecirc;ng cho từng nh&oacute;m sản phẩm.</p>\r\n\r\n<p class="pBody" style="margin: 0px 0px 10px; padding: 0px; border: 0px; list-style: none; outline: 0px; font-size: 16px; line-height: 20px; font-family: ''Times New Roman'', Times, serif; text-align: justify;"><tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">Giải thưởng</tag>&nbsp;thuộc lĩnh vực&nbsp;<tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">Khoa học ứng dụng</tag>&nbsp;được Ban tổ chức lựa chọn, trao tặng c&aacute;c nh&agrave; khoa học, tập thể khoa học đạt được th&agrave;nh t&iacute;ch xuất sắc trong nghi&ecirc;n cứu về c&aacute;c vấn đề thuộc một trong c&aacute;c lĩnh vực: to&aacute;n học, cơ học, vật l&yacute; học, h&oacute;a học, sinh học, c&aacute;c khoa học về tr&aacute;i đất v&agrave; m&ocirc;i trường, đ&atilde; ứng dụng c&aacute;c kết quả nghi&ecirc;n cứu v&agrave;o thực tiễn đạt hiệu quả kinh tế, x&atilde; hội cao. Mỗi lĩnh vực c&oacute; tối đa một&nbsp;<tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">giải thưởng</tag>. T&ugrave;y theo gi&aacute; trị khoa học v&agrave; &yacute; nghĩa thực tiễn của kết quả nghi&ecirc;n cứu v&agrave; ứng dụng,&nbsp;<tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">t&aacute;c giả</tag>&nbsp;hoặc tập thể&nbsp;<tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">t&aacute;c giả</tag>&nbsp;c&oacute; thể được nhận c&aacute;c mức thưởng gồm: giải Nhất trị gi&aacute; 100 triệu đồng, giải Nh&igrave; 50 triệu,&nbsp;<tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">giải Ba</tag>&nbsp;30 triệu v&agrave;&nbsp;<tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">giải Khuyến kh&iacute;ch</tag>&nbsp;trị gi&aacute; 20 triệu đồng.</p>\r\n\r\n<table align="center" border="0" cellpadding="1" cellspacing="1" style="margin: 0px 20px 10px 0px; padding: 0px; list-style: none; max-width: 496px; font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 20px; border: 0px !important; background-color: rgb(241, 241, 241) !important;" width="200">\r\n	<tbody style="margin: 0px; padding: 0px; border: 0px; list-style: none; outline: 0px; background-color: transparent;">\r\n		<tr style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">\r\n			<td style="margin: 0px; padding: 0px; border: 0px none; list-style: none;"><img align="Middle" alt="" border="0" class="oImage" height="300" src="http://img2.news.zing.vn/2012/11/21/19-1.jpg" style="margin: 0px; padding: 0px; border: 0px; list-style: none; outline: 0px; background-color: transparent; max-width: 500px; height: auto;" width="450" /></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p class="pBody" style="margin: 0px 0px 10px; padding: 0px; border: 0px; list-style: none; outline: 0px; font-size: 16px; line-height: 20px; font-family: ''Times New Roman'', Times, serif; text-align: justify;">Với lĩnh vực&nbsp;<tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">Y dược</tag>,&nbsp;<tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">Giải thưởng</tag>&nbsp;<tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">Nh&acirc;n t&agrave;i Đất Việt</tag>&nbsp;2012 sẽ được trao cho&nbsp;<tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">t&aacute;c giả</tag>, nh&oacute;m&nbsp;<tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">t&aacute;c giả</tag>&nbsp;của c&aacute;c c&ocirc;ng tr&igrave;nh khoa học, nghi&ecirc;n cứu ứng dụng v&agrave;o thực tế. C&aacute;c c&ocirc;ng tr&igrave;nh đ&atilde; đạt&nbsp;<tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">giải thưởng</tag>&nbsp;trong c&aacute;c hoạt động kh&aacute;c của ng&agrave;nh y tế ở trong v&agrave; ngo&agrave;i nước cũng được khuyến kh&iacute;ch tham gia.</p>\r\n\r\n<p class="pBody" style="margin: 0px 0px 10px; padding: 0px; border: 0px; list-style: none; outline: 0px; font-size: 16px; line-height: 20px; font-family: ''Times New Roman'', Times, serif; text-align: justify;">Sau khi trải qua v&ograve;ng thi chung khảo cam go, s&ocirc;i nổi trong ng&agrave;y 17/11, Ban tổ chức đ&atilde; chọn ra được 11 sản phẩm nổi trội trong lĩnh vực CNTT để trao giải, trong đ&oacute; c&oacute; hai&nbsp;<tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">giải thưởng</tag>&nbsp;phụ d&agrave;nh cho sản phẩm của học sinh phổ th&ocirc;ng c&oacute; triển vọng v&agrave; sản phẩm c&oacute; &yacute; nghĩa x&atilde; hội - nh&acirc;n văn c&oacute; triển vọng. Trong nh&oacute;m sản phẩm CNTT th&agrave;nh c&ocirc;ng, 4&nbsp;<tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">giải khuyến kh&iacute;ch</tag>&nbsp;đ&atilde; được trao cho Thiết bị cảnh b&aacute;o trộm v&agrave; định vị xe gắn m&aacute;y th&ocirc;ng qua ĐTDĐ S-Bike của nh&oacute;m&nbsp;<tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">t&aacute;c giả</tag>&nbsp;C&ocirc;ng ty Cổ phần Giải ph&aacute;p SETECH Việt; Phần mềm điều h&agrave;nh v&agrave; dựng h&igrave;nh ảnh X-Quang kỹ thuật số (DROC - Digital Radiography Operation Console) của nh&oacute;m&nbsp;<tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">t&aacute;c giả</tag>&nbsp;C&ocirc;ng ty Cổ phần&nbsp;<a class="zinglive_highlight" href="http://news.zing.vn/search.html?q=c%C3%B4ng-ngh%E1%BB%87-th%C3%B4ng-tin#taggify" style="margin: 0px; padding: 0px; border-width: 0px 0px 1px; border-bottom-style: dotted; border-bottom-color: rgb(168, 168, 168); list-style: none; outline: 0px; font-size: 12.2pt; background-color: transparent; color: rgb(51, 51, 51); text-decoration: initial; background-position: initial initial; background-repeat: initial initial;" target="_blank">C&ocirc;ng nghệ th&ocirc;ng tin</a>&nbsp;Việt Ba; Hệ thống kinh doanh quản l&yacute; b&aacute;n tour của nh&oacute;m&nbsp;<tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">t&aacute;c giả</tag>&nbsp;C&ocirc;ng ty Vietravel; v&agrave; Hệ thống phần mềm quản l&yacute; trường học trực tuyến t&iacute;ch hợp sổ li&ecirc;n lạc điện tử của nh&oacute;m&nbsp;<tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">t&aacute;c giả</tag>&nbsp;c&ocirc;ng ty Cổ phần Phần mềm Quảng &Iacute;ch.</p>\r\n\r\n<p class="pBody" style="margin: 0px 0px 10px; padding: 0px; border: 0px; list-style: none; outline: 0px; font-size: 16px; line-height: 20px; font-family: ''Times New Roman'', Times, serif; text-align: justify;"><tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">Giải thưởng</tag>&nbsp;cao nhất của nh&oacute;m sản phẩm CNTT th&agrave;nh c&ocirc;ng l&agrave; một&nbsp;<a class="zinglive_highlight" href="http://news.zing.vn/search.html?q=gi%E1%BA%A3i-ba#taggify" style="margin: 0px; padding: 0px; border-width: 0px 0px 1px; border-bottom-style: dotted; border-bottom-color: rgb(168, 168, 168); list-style: none; outline: 0px; font-size: 12.2pt; background-color: transparent; color: rgb(51, 51, 51); text-decoration: initial; background-position: initial initial; background-repeat: initial initial;" target="_blank">giải ba</a>&nbsp;duy nhất, d&agrave;nh cho nh&oacute;m&nbsp;<tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">t&aacute;c giả</tag>&nbsp;của sản phẩm Cổng thanh to&aacute;n chứng kho&aacute;n trực tuyến BIDV@SECURITIES đến từ Trung t&acirc;m CNTT BIDV. Do năm nay&nbsp;<tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">giải thưởng</tag>&nbsp;d&agrave;nh cho nh&oacute;m sản phẩm CNTT th&agrave;nh c&ocirc;ng được x&acirc;y dựng mới ho&agrave;n to&agrave;n từ t&ecirc;n gọi tới chi&ecirc;u ch&iacute;, thể lệ so với c&aacute;c năm trước, n&ecirc;n đ&atilde; kh&ocirc;ng c&oacute; sản phẩm n&agrave;o đủ điều kiện để được ban tổ chức x&eacute;t trao c&aacute;c giải nhất v&agrave; nh&igrave;.</p>\r\n\r\n<table align="center" border="0" cellpadding="1" cellspacing="1" style="margin: 0px 20px 10px 0px; padding: 0px; list-style: none; max-width: 496px; font-family: ''Times New Roman'', Times, serif; font-size: 16px; line-height: 20px; border: 0px !important; background-color: rgb(241, 241, 241) !important;" width="200">\r\n	<tbody style="margin: 0px; padding: 0px; border: 0px; list-style: none; outline: 0px; background-color: transparent;">\r\n		<tr style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">\r\n			<td style="margin: 0px; padding: 0px; border: 0px none; list-style: none;"><img align="Middle" alt="" border="0" class="oImage" height="300" src="http://img2.news.zing.vn/2012/11/21/20-1.jpg" style="margin: 0px; padding: 0px; border: 0px; list-style: none; outline: 0px; background-color: transparent; max-width: 500px; height: auto;" width="450" /></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p class="pBody" style="margin: 0px 0px 10px; padding: 0px; border: 0px; list-style: none; outline: 0px; font-size: 16px; line-height: 20px; font-family: ''Times New Roman'', Times, serif; text-align: justify;">Điều tương tự cũng xuất hiện ở nh&oacute;m sản phẩm CNTT triển vọng, khi chỉ c&oacute; hai giải nh&igrave; được trao cho Hệ thống biến b&aacute;o điện tử th&agrave;nh b&aacute;o n&oacute;i ViNAS của nh&oacute;m<tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">t&aacute;c giả</tag>&nbsp;SYCO đến từ c&ocirc;ng ty TNHH Giải ph&aacute;p s&aacute;ng tạo v&agrave; nghi&ecirc;n cứu ti&ecirc;n tiến iSolar; v&agrave; Hệ thống trả lời tự động bằng giọng n&oacute;i (VIS: Viet voice Serve) của nh&oacute;m&nbsp;<tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">t&aacute;c giả</tag>&nbsp;Ph&ograve;ng th&iacute; nghiệm AILab Trường Đại học Khoa học tự nhi&ecirc;n&nbsp;TP. Hồ Ch&iacute; Minh. B&ecirc;n cạnh đ&oacute; l&agrave; hai&nbsp;<a class="zinglive_highlight" href="http://news.zing.vn/search.html?q=gi%E1%BA%A3i-khuy%E1%BA%BFn-kh%C3%ADch#taggify" style="margin: 0px; padding: 0px; border-width: 0px 0px 1px; border-bottom-style: dotted; border-bottom-color: rgb(168, 168, 168); list-style: none; outline: 0px; font-size: 12.2pt; background-color: transparent; color: rgb(51, 51, 51); text-decoration: initial; background-position: initial initial; background-repeat: initial initial;" target="_blank">giải khuyến kh&iacute;ch</a>&nbsp;d&agrave;nh cho Giải ph&aacute;p s&aacute;ch gi&aacute;o khoa điện tử Classbook của nh&oacute;m&nbsp;<tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">t&aacute;c giả</tag>&nbsp;C&ocirc;ng ty Cổ phần s&aacute;ch điện tử gi&aacute;o dục EDC; v&agrave; Hệ thống quản l&yacute; hoạt động đ&aacute;nh gi&aacute; kết quả học tập (QMC-E.TEST) của&nbsp;nh&oacute;m&nbsp;<tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">t&aacute;c giả</tag>&nbsp;Trường Đại học C&ocirc;ng nghiệp H&agrave; Nội.</p>\r\n\r\n<p class="pBody" style="margin: 0px 0px 10px; padding: 0px; border: 0px; list-style: none; outline: 0px; font-size: 16px; line-height: 20px; font-family: ''Times New Roman'', Times, serif; text-align: justify;">Trong lĩnh vực&nbsp;<tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">Y dược</tag>, với &quot;<tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">Cụm c&ocirc;ng tr&igrave;nh</tag>&nbsp;nghi&ecirc;n cứu phẫu thuật nội soi trẻ em&rdquo;, Gi&aacute;o sư, tiến sĩ Nguyễn Thanh Li&ecirc;m, Gi&aacute;m đốc BV Nhi Trung ương đ&atilde; được trao<tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">giải thưởng</tag>&nbsp;th&agrave;nh tựu trọn đời. &ldquo;<tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">Cụm c&ocirc;ng tr&igrave;nh</tag>&nbsp;gh&eacute;p thận v&agrave; gan từ người sống cho tạng v&agrave; người chết n&atilde;o hiến tạng tại Bệnh viện Chợ Rẫy&rdquo; của tập thể y b&aacute;c sỹ Bệnh viện Chợ Rẫy cũng được Hội đồng gi&aacute;m khảo quyết định trao&nbsp;<tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">giải thưởng</tag>th&agrave;nh tựu&nbsp;<a class="zinglive_highlight" href="http://news.zing.vn/search.html?q=y-d%C6%B0%E1%BB%A3c#taggify" style="margin: 0px; padding: 0px; border-width: 0px 0px 1px; border-bottom-style: dotted; border-bottom-color: rgb(168, 168, 168); list-style: none; outline: 0px; font-size: 12.2pt; background-color: transparent; color: rgb(51, 51, 51); text-decoration: initial; background-position: initial initial; background-repeat: initial initial;" target="_blank">Y dược</a>. Đ&acirc;y l&agrave; hai&nbsp;<tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">cụm c&ocirc;ng tr&igrave;nh</tag>&nbsp;mang &yacute; nghĩa bước ngoặt, gi&uacute;p n&acirc;ng cao tiếng tăm của nội soi Việt Nam tr&ecirc;n to&agrave;n thế giới, v&agrave; tạo th&ecirc;m cơ hội sống cho nhiều bệnh nh&acirc;n mắc bệnh thận, đặc biệt l&agrave; với những bệnh nh&acirc;n mắc bệnh gan giai đoạn cuối.</p>\r\n\r\n<p class="pBody" style="margin: 0px 0px 10px; padding: 0px; border: 0px; list-style: none; outline: 0px; font-size: 16px; line-height: 20px; font-family: ''Times New Roman'', Times, serif; text-align: justify;">Trong lĩnh vực&nbsp;<a class="zinglive_highlight" href="http://news.zing.vn/search.html?q=khoa-h%E1%BB%8Dc-%E1%BB%A9ng-d%E1%BB%A5ng#taggify" style="margin: 0px; padding: 0px; border-width: 0px 0px 1px; border-bottom-style: dotted; border-bottom-color: rgb(168, 168, 168); list-style: none; outline: 0px; font-size: 12.2pt; background-color: transparent; color: rgb(51, 51, 51); text-decoration: initial; background-position: initial initial; background-repeat: initial initial;" target="_blank">Khoa học ứng dụng</a>,&nbsp;<tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">giải thưởng</tag>&nbsp;duy nhất trị gi&aacute; 100 triệu đồng đ&atilde; được trao tặng cho nh&oacute;m&nbsp;<a class="zinglive_highlight" href="http://news.zing.vn/search.html?q=t%C3%A1c-gi%E1%BA%A3#taggify" style="margin: 0px; padding: 0px; border-width: 0px 0px 1px; border-bottom-style: dotted; border-bottom-color: rgb(168, 168, 168); list-style: none; outline: 0px; font-size: 12.2pt; background-color: transparent; color: rgb(51, 51, 51); text-decoration: initial; background-position: initial initial; background-repeat: initial initial;" target="_blank">t&aacute;c giả</a>&nbsp;bao gồm GS. TS Nguyễn Sinh Huy, PGS.TS Hồ Văn Ch&iacute;n (Viện Địa l&yacute; v&agrave; T&agrave;i nguy&ecirc;n TP. Hồ Ch&iacute; Minh, thuộc Viện Khoa học v&agrave; C&ocirc;ng nghệ Việt Nam), v&agrave; c&aacute;c cộng sự với&nbsp;<a class="zinglive_highlight" href="http://news.zing.vn/search.html?q=c%E1%BB%A5m-c%C3%B4ng-tr%C3%ACnh#taggify" style="margin: 0px; padding: 0px; border-width: 0px 0px 1px; border-bottom-style: dotted; border-bottom-color: rgb(168, 168, 168); list-style: none; outline: 0px; font-size: 12.2pt; background-color: transparent; color: rgb(51, 51, 51); text-decoration: initial; background-position: initial initial; background-repeat: initial initial;" target="_blank">cụm c&ocirc;ng tr&igrave;nh</a>&nbsp;&ldquo;Điều khiển lũ ở Tứ gi&aacute;c Long Xuy&ecirc;n - tho&aacute;t lũ ra biển T&acirc;y&rdquo;. C&ocirc;ng tr&igrave;nh nghi&ecirc;n cứu n&agrave;y đ&atilde; được cố Thủ tướng V&otilde; Văn Kiệt ủng hộ v&agrave; đề ra trong đường lối &ldquo;Chung sống với lũ&ldquo; ở đồng bằng s&ocirc;ng Cửu Long năm 1997. Cho đến nay, c&ocirc;ng tr&igrave;nh tho&aacute;t lũ ra biển T&acirc;y vẫn đứng vững v&agrave; ph&aacute;t huy hiệu quả. Đ&acirc;y được đ&aacute;nh gi&aacute; l&agrave; c&ocirc;ng tr&igrave;nh xuất sắc nhất trong số c&aacute;c&nbsp;<tag style="margin: 0px; padding: 0px; border: 0px none; list-style: none;">giải thưởng</tag>&nbsp;khoa học tự nhi&ecirc;n từng được nhận&nbsp;<a class="zinglive_highlight" href="http://news.zing.vn/search.html?q=gi%E1%BA%A3i-th%C6%B0%E1%BB%9Fng#taggify" style="margin: 0px; padding: 0px; border-width: 0px 0px 1px; border-bottom-style: dotted; border-bottom-color: rgb(168, 168, 168); list-style: none; outline: 0px; font-size: 12.2pt; background-color: transparent; color: rgb(51, 51, 51); text-decoration: initial; background-position: initial initial; background-repeat: initial initial;" target="_blank">giải thưởng</a>&nbsp;<a class="zinglive_highlight" href="http://news.zing.vn/search.html?q=nh%C3%A2n-t%C3%A0i-%C4%91%E1%BA%A5t-vi%E1%BB%87t#taggify" style="margin: 0px; padding: 0px; border-width: 0px 0px 1px; border-bottom-style: dotted; border-bottom-color: rgb(168, 168, 168); list-style: none; outline: 0px; font-size: 12.2pt; background-color: transparent; color: rgb(51, 51, 51); text-decoration: initial; background-position: initial initial; background-repeat: initial initial;" target="_blank">Nh&acirc;n t&agrave;i Đất Việt</a>&nbsp;từ trước đến nay.</p>\r\n', 'Các nhân tài đất Việt được vinh danh', 0);
INSERT INTO `chuathienquang_news` (`id`, `id_category`, `author`, `date`, `content`, `title`, `type`) VALUES
(111, 13, 'BBT Website', '2013-02-03 15:25:46', '<div id="hometext" style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-weight: 700; text-align: justify; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 16px; z-index: 880;">&nbsp;Nhằm chia sẻ kh&oacute; khăn với người khiếm thị nh&acirc;n Tết Dương lịch sắp đến, ng&agrave;y 16-12, Ban từ thiện thiền viện Vạn Hạnh (Q.Ph&uacute; Nhuận) đ&atilde; đến thăm v&agrave; tặng qu&agrave; cho người khiếm thị tỉnh Đồng Nai.\r\n<div style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; z-index: 860;" title="Huongdanphattu.Vn">&nbsp;</div>\r\n</div>\r\n\r\n<div class="bodytext" style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; text-align: justify; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 16px; z-index: 850;">\r\n<p align="center" style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;"><span class="ctcBody" id="ZoomContentBody" style="margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; background-color: transparent; font-family: arial, tahoma, verdana; text-align: justify; width: auto; line-height: 22px;"><img alt="DSC_2146.JPG" border="0" height="367" src="http://giacngo.vn/UserImages/2012/12/17/10/DSC_2146.JPG" style="margin: 0px; padding: 1px; border: 1px solid rgb(229, 229, 229); outline: 0px; vertical-align: baseline; background-color: transparent;" width="550" /></span></p>\r\n\r\n<p align="center" style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;"><span class="ctcBody" id="ZoomContentBody" style="margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; background-color: transparent; font-family: arial, tahoma, verdana; text-align: justify; width: auto; line-height: 22px;"><font color="rgb(128, 128, 128);" size="2" style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;">Tặng qu&agrave; cho người khiếm thị kh&oacute; khăn</font></span></p>\r\n\r\n<p align="center" style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;"><span class="ctcBody" id="ZoomContentBody" style="margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; background-color: transparent; font-family: arial, tahoma, verdana; text-align: justify; width: auto; line-height: 22px;"><img alt="DSC_2155.JPG" border="0" height="367" src="http://giacngo.vn/UserImages/2012/12/17/10/DSC_2155.JPG" style="margin: 0px; padding: 1px; border: 1px solid rgb(229, 229, 229); outline: 0px; vertical-align: baseline; background-color: transparent;" width="550" /></span></p>\r\n\r\n<p align="center" style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;"><span class="ctcBody" id="ZoomContentBody" style="margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; background-color: transparent; font-family: arial, tahoma, verdana; text-align: justify; width: auto; line-height: 22px;"><font color="rgb(128, 128, 128);" size="2" style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;">Nhiều nhu yếu phẩm đem đến gi&uacute;p người khiếm thị</font></span></p>\r\n\r\n<p style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;"><span class="ctcBody" id="ZoomContentBody" style="margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; background-color: transparent; font-family: arial, tahoma, verdana; width: auto; line-height: 22px;">Đo&agrave;n đ&atilde; tặng 200 phần qu&agrave; cho người khiếm thị tại huyện Tr&atilde;ng Bom v&agrave; Vĩnh Cữu. Mỗi phần qu&agrave; gồm nhu yếu phẩm v&agrave; 100.000 đồng tiền mặt. Tổng trị gi&aacute; 70 triệu đồng do Phật tử v&agrave; c&aacute;c nh&agrave; hảo t&acirc;m đ&oacute;ng g&oacute;p</span></p>\r\n</div>\r\n', 'Thiền viện Vạn Hạnh tặng quà người khiếm thị', 0),
(112, 13, 'BBT Website', '2013-02-03 15:26:19', '<div class="short-desc clearfix" id="hometext" style="margin: 0px; padding: 8px 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; font-family: Verdana, Arial, Helvetica, sans-serif; color: rgb(0, 0, 0); line-height: normal;">Ban Đại Diện Phật gi&aacute;o thị x&atilde; Hương Thủy đến thăm v&agrave; tặng qu&agrave; cho Hội người m&ugrave; TX. Hương Thủy.\r\n<div style="margin: 10px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; width: 460px;"><img alt="TT. Huế: Phật giáo TX Hương Thủy tặng quà đến Hội người mù" src="http://phatgiaoaluoi.com/uploads/news/2013_01/dsc00193.jpg" style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;" width="460" />\r\n<p style="margin: 0px 0px 1em; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; text-align: center;"><em style="margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 11px; vertical-align: baseline; background-color: transparent;">TT. Huế: Phật gi&aacute;o TX Hương Thủy tặng qu&agrave; đến Hội người m&ugrave;</em></p>\r\n</div>\r\n</div>\r\n\r\n<div class="details-content clearfix" id="bodytext" style="margin: 0px; padding: 8px 0px; border: 0px; outline: 0px; font-size: 10pt; vertical-align: baseline; background-color: transparent; text-align: justify; color: rgb(0, 0, 0); font-family: Arial; line-height: normal;">Nh&acirc;n kỷ niệm ng&agrave;y đức Từ Phụ th&agrave;nh đạo, chiều 18.01.2013 (07.12 Nh&acirc;m Th&igrave;n) Ban Đại Diện Phật gi&aacute;o thị x&atilde; Hương Thủy đến thăm v&agrave; tặng qu&agrave; cho Hội người m&ugrave; TX. Hương Thủy.<br />\r\n<br />\r\nĐại đức Th&iacute;ch Thiện Bửu, ch&aacute;nh Đại diện Phật gi&aacute;o Thị x&atilde; đ&atilde; c&oacute; lời ph&aacute;t biểu chia sẽ động vi&ecirc;n v&agrave; trao những phần qu&agrave; đến c&aacute;c Cụ. Được biết đợt từ thiện n&agrave;y Ban đại diện đ&atilde; ph&aacute;t 120 suất v&agrave; mỗi phần qu&agrave; trị gi&aacute; 300 đồng. Đ&acirc;y cũng ch&iacute;nh l&agrave; tấm l&ograve;ng của Ban đại diện gởi đến c&aacute;c cụ trong dịp tết Nguy&ecirc;n đ&aacute;n sắp đến.<br />\r\n<br />\r\n<strong style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent;">Ch&ugrave;m ảnh ph&aacute;t qu&agrave; tại Hội người m&ugrave;</strong><br />\r\n&nbsp;\r\n<div style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; text-align: center;"><img alt="" src="http://phatgiaoaluoi.com/uploads/news/2013_01/dsc00185.jpg" style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; width: 600px; height: 450px;" /><br />\r\n<br />\r\n<img alt="" src="http://phatgiaoaluoi.com/uploads/news/2013_01/dsc00186.jpg" style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; width: 600px; height: 450px;" /><br />\r\n<br />\r\n<img alt="" src="http://phatgiaoaluoi.com/uploads/news/2013_01/dsc00188.jpg" style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; width: 600px; height: 450px;" /><br />\r\n<br />\r\nVăn nghệ ch&agrave;o mừng Ban Đại diện Phật gi&aacute;o TX Hương Thủy đến thăm v&agrave; tặng qu&agrave;<br />\r\n<br />\r\n<img alt="" src="http://phatgiaoaluoi.com/uploads/news/2013_01/dsc00190.jpg" style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; width: 600px; height: 450px;" /><br />\r\n<br />\r\nĐ&ocirc;ng đảo b&agrave; con đến tiếp nhận<br />\r\n<br />\r\n<img alt="" src="http://phatgiaoaluoi.com/uploads/news/2013_01/dsc00191.jpg" style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; width: 600px; height: 450px;" /><br />\r\n<br />\r\n<img alt="" src="http://phatgiaoaluoi.com/uploads/news/2013_01/dsc00193_1.jpg" style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; width: 600px; height: 450px;" /><br />\r\n<br />\r\nĐại đức Th&iacute;ch Thiện Bửu chia sẻ đến b&agrave; con<br />\r\n<br />\r\n<img alt="" src="http://phatgiaoaluoi.com/uploads/news/2013_01/dsc00199.jpg" style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; width: 600px; height: 450px;" /><br />\r\n<br />\r\n<img alt="" src="http://phatgiaoaluoi.com/uploads/news/2013_01/dsc00200.jpg" style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; width: 600px; height: 450px;" /><br />\r\n<br />\r\nv&agrave; tặng qu&agrave;<br />\r\n<br />\r\n<img alt="" src="http://phatgiaoaluoi.com/uploads/news/2013_01/dsc00202.jpg" style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; width: 600px; height: 450px;" /><br />\r\n<br />\r\n<img alt="" src="http://phatgiaoaluoi.com/uploads/news/2013_01/dsc00203.jpg" style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-color: transparent; width: 600px; height: 450px;" /></div>\r\n</div>\r\n', 'Phật giáo TX Hương Thủy tặng quà đến Hội người mù', 0),
(113, 13, 'BBT Website', '2013-02-03 15:29:08', '<div class="box_more_articel_same_topic" style="float: right; margin-left: 6px; width: 270px; color: rgb(0, 0, 0); font-family: ''Times New Roman''; font-size: 16px; line-height: normal;">\r\n<div class="image" style="float: right; padding: 4px; border: 1px solid rgb(204, 204, 204); width: 260px;"><br />\r\n<img alt="image" src="http://www.daophatngaynay.com/vn/thumbnail.php?file=images/2012/quy1/b____i_ph__t_qu___607316018.jpg&amp;size=article_medium" width="260px" /></div>\r\n\r\n<div class="clearfix">&nbsp;</div>\r\n</div>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: ''Times New Roman''; font-size: 16px; line-height: normal;"><strong>Nh&oacute;m Khiếm thị Thiện Nguyện ra đời được 6 năm, nhưng đ&acirc;y l&agrave; m&ugrave;a Phật Đản lần thứ 4 do những anh chị em khiếm thị đứng ra tổ chức.</strong></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: ''Times New Roman''; font-size: 16px; line-height: normal;">Tuy l&agrave;&nbsp; khiếm thị, &nbsp;họ đ&atilde; biết đến với Phật Ph&aacute;p bằng tấm l&ograve;ng ch&acirc;n th&agrave;nh v&agrave; thuần t&iacute;n. Sau khi hiểu gi&aacute;o l&yacute;, biết luật nh&acirc;n quả, họ hiểu rằng c&aacute;i quả bất hạnh kiếp nầy do những nh&acirc;n qu&aacute; khứ đ&atilde; gieo, để tạo nh&acirc;n tốt cho tương lai, ngo&agrave;i việc tu tập, họ phải vun bồi phước b&aacute;u bằng c&aacute;ch n&agrave;o đ&oacute;, thế l&agrave; anh chị em&nbsp; đồng thuận lập th&agrave;nh nh&oacute;m tr&ecirc;n dưới 60 người, h&agrave;ng th&aacute;ng tr&iacute;ch một phần đồng tiền kh&oacute; nhọc để lập quỷ hỗ trợ cho những gia cảnh bất hạnh d&ugrave; đ&oacute; l&agrave; người s&aacute;ng mắt. Họ l&agrave;m gia c&ocirc;ng b&agrave;n chải, đi b&aacute;n v&eacute; số, ,nh&acirc;n vi&ecirc;n massage, đi dạy học&hellip;Tiền thu nhập trung b&igrave;nh mỗi ng&agrave;y với sự lao động cật lực, cũng chỉ vừa một k&yacute; gạo, thế m&agrave; họ can đảm t&iacute;ch g&oacute;p để gi&uacute;p đỡ thi&ecirc;n tai h&agrave;ng năm tr&ecirc;n qu&ecirc; hương m&igrave;nh, chẳng những thế, đồng tiền vất vả đ&oacute; cũng đ&atilde; đến với những người bất hạnh trong thi&ecirc;n tai Nhật Bản vừa qua. Tuy l&agrave; muối bỏ biển, nhưng tầm tay nhỏ b&eacute; với tấm l&ograve;ng cao cả đ&oacute;, g&acirc;y x&uacute;c động kh&ocirc;ng &iacute;t khi anh chị em Exryu tại Nhật khi nhận chuyển giao lại cho c&ocirc;ng t&aacute;c từ thiện ở Sendai; anh chị em đắn đo trước đồng tiền tr&acirc;n qu&yacute; đ&oacute; nhưng đ&agrave;nh phải nhận để nh&oacute;m Khiếm thị được vui.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: ''Times New Roman''; font-size: 16px; line-height: normal;">Phật ph&aacute;p đ&atilde; chuyển h&oacute;a những con người bất hạnh đ&oacute;, họ sống vui vẻ y&ecirc;u đời hơn, tr&aacute;nh l&agrave;m những nghề m&agrave; trước kia&nbsp; họ cho l&agrave; li&ecirc;n hệ đến sinh mạng sanh vật. Họ biết trường trai, họ sang băng dĩa kinh s&aacute;ch của qu&yacute; thầy để cho mọi người xem, thu &acirc;m b&agrave;i giảng tr&ecirc;n s&aacute;ch cho những th&agrave;nh vi&ecirc;n khiếm thị nghe, họ an ủi tư vấn cho những người s&aacute;ng mắt đang gặp khủng hoảng tinh thần. Họ cũng gi&uacute;p nhiều người đến với Phật Ph&aacute;p. H&agrave;ng năm, những lễ lớn như Vu Lan, Phật Đản họ đều tổ chức ph&aacute;t qu&agrave; cho trẻ em, l&agrave;m văn nghệ c&uacute;ng dường Tam Bảo. Ch&iacute;nh v&igrave; thế m&agrave; chư Tăng Ni đ&atilde; đến với họ mỗi khi cần. Tuy l&agrave; một tư gia, họ tổ chức lễ lộc kh&ocirc;ng thua&nbsp; một ng&ocirc;i ch&ugrave;a. Năm nay, Phật Đản tổ chức v&agrave;o chiều ng&agrave;y mồng 8 th&aacute;ng tư &acirc;m lịch nhằm ng&agrave;y 28/4 dương lịch, được qu&yacute; thầy trong Ban Đại Diện Phật Gi&aacute;o huyện Hốc M&ocirc;n đến l&agrave;m lễ, được sư c&ocirc; Hương Nhũ đến chia xẻ, t&acirc;m sự v&agrave; ph&aacute;t qu&agrave; cho nh&oacute;m. B&aacute;c sĩ Trần Đo&agrave;n v&agrave; anh Trường trong ban quản trị ch&ugrave;a Hoa Nghi&ecirc;m ở Virginia , TT T. Nhật Từ đ&atilde; hỗ trợ. Thượng Tọa T. Thanh Phong nh&acirc;n danh đ&agrave;i truyền h&igrave;nh An Vi&ecirc;n cũng gi&uacute;p đỡ &nbsp;v&agrave; một v&agrave;i th&acirc;n hữu gần xa để lễ Phật Đản của nh&oacute;m được trang trọng hơn. V&agrave;i anh chị em nghệ sĩ&nbsp; từ nơi xa x&ocirc;i&nbsp; đến gi&uacute;p vui để b&agrave; con trong th&ocirc;n x&oacute;m lao động được hưởng kh&ocirc;ng kh&iacute; vui tươi của m&ugrave;a Đản sanh.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: ''Times New Roman''; font-size: 16px; line-height: normal;">&nbsp;Phật Đản lu&ocirc;n l&agrave; m&ugrave;a an vui hạnh ph&uacute;c của&nbsp; nh&oacute;m Khiếm Thị Thiện Nguyện, cũng l&agrave; dịp để&nbsp; những vị Khiếm thị t&agrave;i năng thể hiện giọng ca của m&igrave;nh. Nh&oacute;m tuy &iacute;t người, nhưng c&oacute; người lắp r&aacute;p v&agrave; c&agrave;i đặt phần mềm vi t&iacute;nh, c&oacute; người xử dụng nhạc cụ chuy&ecirc;n nghiệp, họ l&agrave;m đủ ng&agrave;nh nghề trong x&atilde; hội m&agrave; người s&aacute;ng từng l&agrave;m. C&oacute; lẽ đ&acirc;y l&agrave; nh&oacute;m gồm những người khuyết tật đứng ra gi&uacute;p cho những người kh&ocirc;ng khuyết tật trong cuộc sống kh&oacute; khăn hiện nay.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: ''Times New Roman''; font-size: 16px; line-height: normal;">Lễ Phật Đản năm 2556 thật vui nhộn, trời chiều l&ograve;ng người n&ecirc;n kh&ocirc;ng mưa m&agrave; những ng&agrave;y vừa qua tầm tả&nbsp; khắp phố phường. Kết th&uacute;c buổi lễ bằng&nbsp; b&uacute;n Huế - m&oacute;n ăn&nbsp; chay đặc sản miền Trung. Người s&aacute;ng lẫn khiếm thị từng bừng, hoan hỷ. Quả thật, giữa cuộc sống kh&oacute; khăn hiện nay, Phật Ph&aacute;p đ&atilde; gi&uacute;p họ c&oacute; cuộc sống an lạc v&agrave; th&aacute;nh thiện.</p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: ''Times New Roman''; font-size: 16px; line-height: normal;"><img alt="" src="http://www.daophatngaynay.com/vn/files/images/2012/quy1/font_l_____i_814834909.jpg" title="" with="640px" /></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: ''Times New Roman''; font-size: 16px; line-height: normal;">&nbsp;</p>\r\n\r\n<p>&nbsp;\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: ''Times New Roman''; font-size: 16px; line-height: normal;"><img alt="" src="http://www.daophatngaynay.com/vn/files/images/2012/quy1/ch__o_c__848998692.jpg" title="" with="640px" /></p>\r\n</p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: ''Times New Roman''; font-size: 16px; line-height: normal;"><em>Ch&agrave;o đạo kỳ</em></p>\r\n\r\n<p>&nbsp;\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: ''Times New Roman''; font-size: 16px; line-height: normal;"><img alt="" src="http://www.daophatngaynay.com/vn/files/images/2012/quy1/d__ng_hoa_2_177526671.jpg" title="" with="640px" /></p>\r\n</p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: ''Times New Roman''; font-size: 16px; line-height: normal;"><em>D&acirc;ng hoa c&uacute;ng dường</em></p>\r\n\r\n<p>&nbsp;\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: ''Times New Roman''; font-size: 16px; line-height: normal;"><img alt="" src="http://www.daophatngaynay.com/vn/files/images/2012/quy1/m__a_l__n_957856538.jpg" title="" with="640px" /></p>\r\n</p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: ''Times New Roman''; font-size: 16px; line-height: normal;">&nbsp;</p>\r\n\r\n<p>&nbsp;\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: ''Times New Roman''; font-size: 16px; line-height: normal;"><img alt="" src="http://www.daophatngaynay.com/vn/files/images/2012/quy1/b____i_134910786.jpg" title="" with="640px" /></p>\r\n</p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: ''Times New Roman''; font-size: 16px; line-height: normal;"><em>Bố Đại h&ograve;a thượng</em></p>\r\n\r\n<p>&nbsp;\r\n<p align="center" style="color: rgb(0, 0, 0); font-f