(37, 12, 13, 'Tuổi Trẻ Và Sự Cô Đơn', '2011-08-14 08:00:00', '2011-08-14 11:00:00', '<p style="font-size: 13px; text-align: center;"><span style="font-size:20px;">KH&Oacute;A HỌC M&Ugrave;A H&Egrave; D&Agrave;NH CHO SINH VI&Ecirc;N</span></p>\r\n\r\n<p style="font-size: 13px; text-align: center;"><span style="font-size:20px;">Chủ đề: Tuổi Trẻ V&agrave; Sự C&ocirc; Đơn</span></p>\r\n\r\n<p style="font-size: 13px;"><strong>Người giảng</strong>: <em><strong>SC Th&iacute;ch Nữ Hương Nhũ</strong></em></p>\r\n\r\n<p style="font-size: 13px;"><span style="font-size: 13px;"><strong>Phương ph&aacute;p</strong>: Giảng, ghi ch&eacute;p, ph&aacute;t dĩa.</span></p>\r\n\r\n<p style="font-size: 13px;"><u style="font-size: 13px;"><strong>Địa điểm</strong></u><span style="font-size: 13px;">: Đạo Tr&agrave;ng ch&ugrave;a Hoằng Ph&aacute;p.</span></p>\r\n\r\n<p style="font-size: 13px;"><u style="font-size: 13.333333969116211px;"><strong>A. NỘI DUNG:</strong></u></p>\r\n\r\n<p style="font-size: 13px;">B&agrave;i ph&aacute;p sẽ cung cấp cho sinh vi&ecirc;n ...</p>\r\n\r\n<p style="font-size: 13px;"><u style="font-size: 13px;"><strong>B, VIDEO B&Agrave;I GIẢNG:</strong></u></p>\r\n\r\n<p style="font-size: 13px;"><u><strong><iframe frameborder="0" height="320" scrolling="no" src="http://www.youtube.com/embed/tM7lvd4a8Sw" width="480"></iframe></strong></u></p>\r\n', 0),
(38, 12, 13, 'Tìm Lại Chính Mình', '2011-04-25 08:00:00', '2011-04-25 11:00:00', '<p style="font-size: 13px; text-align: center;"><span style="font-size:20px;">KH&Oacute;A TU SINH VI&Ecirc;N HƯỚNG VỀ PHẬT PH&Aacute;P</span></p>\r\n\r\n<p style="font-size: 13px; text-align: center;"><span style="font-size:20px;">Chủ đề: Tọa Đ&agrave;m T&igrave;m Lại Ch&iacute;nh M&igrave;nh</span></p>\r\n\r\n<p style="font-size: 13px;"><strong>Người giảng</strong>: <em><strong>SC Th&iacute;ch Nữ Hương Nhũ</strong></em></p>\r\n\r\n<p style="font-size: 13px;"><span style="font-size: 13px;"><strong>Phương ph&aacute;p</strong>: Giảng, ghi ch&eacute;p, ph&aacute;t dĩa.</span></p>\r\n\r\n<p style="font-size: 13px;"><u style="font-size: 13px;"><strong>Địa điểm</strong></u><span style="font-size: 13px;">: Đạo Tr&agrave;ng ch&ugrave;a Hoằng Ph&aacute;p.</span></p>\r\n\r\n<p style="font-size: 13px;"><u style="font-size: 13.333333969116211px;"><strong>A. NỘI DUNG:</strong></u></p>\r\n\r\n<p style="font-size: 13px;">B&agrave;i ph&aacute;p sẽ cung cấp cho sinh vi&ecirc;n ...</p>\r\n\r\n<p style="font-size: 13px;"><u style="font-size: 13px;"><strong>B, VIDEO B&Agrave;I GIẢNG:</strong></u></p>\r\n\r\n<p style="font-size: 13px;"><u><strong><iframe frameborder="0" height="320" scrolling="no" src="http://www.youtube.com/embed/hYA148dVljQ" width="480"></iframe></strong></u></p>\r\n\r\n<p style="font-size: 13px;"><u><strong><iframe frameborder="0" height="320" scrolling="no" src="http://www.youtube.com/embed/y_ljbDmBDgM" width="480"></iframe></strong></u></p>\r\n', 0),
(39, 12, 13, 'Tiếng Ru Thần Tiên', '2011-11-01 08:00:00', '2011-11-01 11:00:00', '<p style="font-size: 13px; text-align: center;"><span style="font-size:20px;">KH&Oacute;A HỌC M&Ugrave;A H&Egrave; D&Agrave;NH CHO SINH VI&Ecirc;N</span></p>\r\n\r\n<p style="font-size: 13px; text-align: center;"><span style="font-size:20px;">Chủ đề: Tiếng Ru Thần Ti&ecirc;n</span></p>\r\n\r\n<p style="font-size: 13px;"><strong>Người giảng</strong>: <em><strong>SC Th&iacute;ch Nữ Hương Nhũ</strong></em></p>\r\n\r\n<p style="font-size: 13px;"><span style="font-size: 13px;"><strong>Phương ph&aacute;p</strong>: Giảng, ghi ch&eacute;p, ph&aacute;t dĩa.</span></p>\r\n\r\n<p style="font-size: 13px;"><u style="font-size: 13px;"><strong>Địa điểm</strong></u><span style="font-size: 13px;">: Đạo Tr&agrave;ng ch&ugrave;a Bằng H&agrave; Nội</span></p>\r\n\r\n<p style="font-size: 13px;"><u style="font-size: 13.333333969116211px;"><strong>A. NỘI DUNG:</strong></u></p>\r\n\r\n<p style="font-size: 13px;">B&agrave;i ph&aacute;p sẽ cung cấp cho sinh vi&ecirc;n ...</p>\r\n\r\n<p style="font-size: 13px;"><u style="font-size: 13px;"><strong>B, VIDEO B&Agrave;I GIẢNG:</strong></u></p>\r\n\r\n<p style="font-size: 13px;"><u><strong><iframe frameborder="0" height="320" scrolling="no" src="http://www.youtube.com/embed/zGucbKKinL8" width="480"></iframe></strong></u></p>\r\n', 0);
