INSERT INTO `chuathienquang_course` (`id`, `name`, `date_start`, `date_end`, `description`, `order`) VALUES
(4, 'CHƯƠNG TRÌNH GIẢNG DẠY', '2010-01-01 07:00:00', '2013-12-31 12:00:00', '<p style="text-align: justify;"><img alt="" src="https://lh3.googleusercontent.com/-dOYOETCxwGw/UQrMicV_4bI/AAAAAAAAAEo/kjF3ndqH7As/s800/Chuong%2520Trinh%2520Dao%2520Tao.jpg" style="width: 590px; height: 180px;" /></p>\r\n\r\n<p style="text-align: justify;">Đ&acirc;y l&agrave; lịch giảng của Sư C&ocirc; Th&iacute;ch Nữ Hương Nhũ tại c&aacute;c nơi như: Học Viện Phật Gi&aacute;o Việt Nam TP Hồ Ch&iacute; Minh, c&aacute;c ch&ugrave;a, tịnh thất, tịnh x&aacute; trong v&agrave; ngo&agrave;i tỉnh B&igrave;nh Dương.</p>\r\n\r\n<p style="text-align: justify;">C&aacute;c lịch giảng sẽ được liệt k&ecirc; trước để qu&iacute; Phật Tử tiện theo d&otilde;i. Ở cuối mỗi b&agrave;i Ph&aacute;p Giảng, c&aacute;c Video sẽ được tải l&ecirc;n kịp thời để qu&iacute; Phật Tử kh&ocirc;ng c&oacute; điều kiện vẫn c&oacute; thể xem lại.</p>\r\n\r\n<p style="text-align: justify;">Ch&uacute; &yacute; l&agrave; lịch giảng c&oacute; thể được thay đổi m&agrave; kh&ocirc;ng b&aacute;o trước.</p>\r\n', 1);
