INSERT INTO `chuathienquang_dhammapada_detail` (`id`, `type`, `content_vi`, `content_en`) VALUES
(1, 1, 'Ý dẫn đầu các pháp,\nÝ làm chủ, ý tạo;\nNếu với ý ô nhiễm,\nNói lên hay hành động,\nKhổ não bước theo sau,\nNhư xe, chân vật kéo.', 'Mind precedes all knowables,\nmind''s their chief, mind-made are they.\nIf with a corrupted mind\none should either speak or act\ndukkha follows caused by that,\nas does the wheel the ox''s hoof.'),
(2, 1, 'Ý dẫn đầu các pháp,\nÝ làm chủ, ý tạo;\nNếu với ý thanh tịnh, \nNói lên hay hành động,\nAn lạc bước theo sau, \nNhư bóng, không rời hình.', 'Mind precedes all knowables,\nmind''s their chief, mind-made are they.\nIf with a clear, and confident mind\none should speak and act\nhappiness follows caused by that,\nas one''s shadow ne''er departing.'),
(3, 1, 'Nó mắng tôi, đánh tôi,\nNó thắng tôi, cướp tôi \nAi ôm hiềm hận ấy,\nHận thù không thể nguôi.', 'Who bears within them enmity:\n"He has abused and beaten me,\ndefeated me and plundered me",\nhate is not allayed for them.'),
(4, 1, 'Nó mắng tôi, đánh tôi,\nNó thắng tôi, cướp tôi \nKhông ôm hiềm hận ấy,\nHận thù được tự nguôi.', 'Who bears within no enmity:\n"He has abused and beaten me,\ndefeated me and plundered me",\nhate is quite allayed for them.'),
(5, 1, 'Với hận diệt hận thù,\nÐời này không có được.\nKhông hận diệt hận thù,\nLà định luật ngàn thu.', 'Never here by enmity\nare those with enmity allayed,\nthey are allayed by amity,\nthis is the timeless Truth.	'),
(6, 1, 'Và người khác không biết,\nChúng ta đây bị hại.\nChỗ ấy, ai hiểu được\nTranh luận được lắng êm.', 'Still others do not understand\nthat we must perish in this world,\nthose who understand this,\ntheir quarrels are allayed.'),
(7, 1, 'Ai sống nhìn tịnh tướng,\nKhông hộ trì các căn,\năn uống thiếu tiết độ,\nbiếng nhác, chẳng tinh cần.\nMa uy hiếp kẻ ấy,\nnhư cây yếu trước gió.', 'One who beauty contemplates,\nwhose faculties are unrestrained,\nin food no moderation knows,\nis languid, who is indolent:\nthat one does Mara overthrow\nas wind a tree of little strength.'),
(8, 1, 'Ai sống quán bất tịnh,\nKhéo hộ trì các căn,\năn uống có tiết độ,\nCó lòng tin, tinh cần,\nMa không uy hiếp được,\nNhư núi đá, trước gió.', 'One who foulness contemplates,\nwhose faculties are well-restrained,\nin food does moderation know,\nis full of faith, who''s diligent:\nthat one no Mara overthrows,\nas wind does not a rocky mount.'),
(9, 1, 'Ai mặc áo cà sa.\ntâm chưa rời uế trược,\nkhông tự chế, không thực,\nkhông xứng áo cà sa', 'One who wears the stainless robe\nwho''s yet not free from stain,\nwithout restraint and truthfulness\nfor the stainless robe''s unfit.'),
(10, 1, 'Ai rời bỏ uế trược,\ngiới luật khéo nghiêm trì,\ntự chế, sống chơn thực,\nthật xứng áo cà sa.', 'But one who is self-cleansed of stain,\nin moral conduct firmly set,\nhaving restraint and truthfulness\nis fit for the stainless robe.'),
(11, 1, 'Không chân, tướng chân thật,\nchân thật, thấy không chân:\nchúng không đạt chân thật,\ndo tà tư, tà hạnh.', 'Conceiving the real in unreality\nwhile seeing unreal the truly real,\nroaming fields of thoughts ill-formed:\nnever they at the real arrive.	'),
(12, 1, 'Chân thật, biết chân thật,\nKhông chân, biết không chân:\nchúng đạt được chân thật,\ndo chánh tư, chánh hạnh.', 'That which is real they know as real,\nthat unreal, to be unreal;\nroaming fields of thought well-formed\nthey at the real arrive.'),
(13, 1, 'Như mái nhà vụng lợp,\nMưa liền xâm nhập vào.\nCũng vậy tâm không tu,\nTham dục liền xâm nhập.', 'Even as the rain does penetrate\na house that''s badly thatched,\nlikewise lust does penetrate\nthe mind uncultivated.'),
(14, 1, 'Như ngôi nhà khéo lợp,\nMưa không xâm nhập vào.\nCũng vậy tâm khéo tu,\nTham dục không xâm nhập.', 'As rain does never penetrate\na house that is well-thatched,\nso lust does never penetrate\nthe mind well cultivated.'),
(15, 1, 'Nay sầu, đời sau sầu,\nKẻ ác, hai đời sầu;\nNó sầu, nó ưu não,\nThấy nghiệp uế mình làm.', 'Here one grieves, one grieves hereafter,\nin both ways does the evil-doer grieve;\none grieves and is afflicted,\none''s own base kammas seeing.'),
(16, 1, 'Nay vui,đời sau vui,\nLàm phước, hai đời vui,\nNó vui, nó an vui,\nThấy nghiệp tịnh mình làm.', 'Here one joys, one joys hereafter,\nin both ways does the merit-maker joy;\none joys and one rejoices,\none''s own pure kammas seeing.'),
(17, 1, 'Nay than, đời sau than,\nKẻ ác, hai đời than,\nNó than: ''Ta làm ác''\nÐọa cõi dữ, than hơn.', 'Here one burns, one burns hereafter,\nin both ways does the evil-doer burn;\nevil I''ve done, remorsefully one burns,\nand more one burns passed to realms of woe.'),
(18, 1, 'Nay sướng, đời sau sướng,\nLàm phước, hai đời sướng.\nNó sướng: ''Ta làm thiện'',\nSanh cõi lành, sướng hơn.', 'Here one''s glad, one''s glad hereafter,\nin both ways is the merit-maker glad;\n"Merit I''ve made", serenely one is glad,\nand more one''s glad passed to blissful states.'),
(19, 1, 'Nếu người nói nhiều kinh,\nKhông hành trì, phóng dật;\nNhư kẻ chăn bò người,\nKhông phần Sa môn hạnh.', 'Though many sacred texts he chants\nthe heedless man''s no practicer,\nas cowherd counting other''s kine\nin samanaship he has no share.'),
(20, 1, 'Dầu nói ít kinh điển,\nNhưng hành pháp, tùy pháp,\nTừ bỏ tham, sân, si,\nTĩnh giác, tâm giải thoát,\nKhông chấp thủ hai đời,\nDự phần Sa môn hạnh.', 'Though few of the sacred texts he chants\nin Dhamma does his practice run,\nclear of delusion, lust and hate,\nwisdom perfected, with heart well-freed,\nnot clinging to this or other world,\nin samanaship he has a share'),
(21, 2, 'Không phóng dật, đường sống,\nPhóng dật là đường chết.\nKhông phóng dật, không chết,\nPhóng dật như chết rồi.', 'Heedfulness is the Deathless path,\nheedlessness, the path to death.\nThose who are heedful do not die,\nheedless are like the dead.'),
(22, 2, 'Biết rõ sai biệt ấy,\nNgười trí không phóng dật,\nHoan hỷ, không phóng dật,\nAn vui hạnh bậc Thánh.', 'The wise then, recognizing this\nas the distinction of heedfulness,\npleased with the spheres of Nobles Ones,\nin heedfulness rejoice.'),
(23, 2, 'Người hằng tu thiền định,\nThường kiên trì tinh tấn.\nBậc trí hưởng Niết Bàn,\nÐạt an tịnh vô thượng.', 'They meditate persistently,\nconstantly they firmly strive,\nthe steadfast to Nibbana reach,\nthe Unexcelled Secure from bonds.'),
(24, 2, 'Nỗ lực, giữ chánh niệm,\nTịnh hạnh, hành thận trọng\nTự điều, sống theo pháp,\nAi sống không phóng dật,\nTiếng lành ngày tăng trưởng.', 'Assiduous and mindful,\npure kamma making, considerate,\nrestrained, by Dhamma living,\nand in heedfulness, \nfor one such spreads reknown.'),
(25, 2, 'Nỗ lực, không phóng dật,\nTự điều, khéo chế ngự.\nBậc trí xây hòn đảo,\nNước lụt khó ngập tràn.', 'By energy and heedfulness,\nby taming and by self-control,\nthe one who''s wise should make as isle\nno flood can overwhelm.	'),
(26, 2, 'Chúng ngu si, thiếu trí,\nChuyên sống đời phóng dật.\nNgười trí, không phóng dật,\nNhư giữ tài sản quý.', 'Foolish folk of little wit\nin heedlessness indulge,\nthe one who''s wise guards heedfulness\nkin to the greatest wealth.	'),
(27, 2, 'Chớ sống đời phóng dật,\nChớ mê say dục lạc.\nKhông phóng dật, thiền định,\nÐạt được an lạc lớn.', 'Don''t indulge in heedlessness!\nDon''t come near to sexual joys!\nThe heedful and contemplative\nattains abundant bliss.	'),
(28, 2, 'Người trí dẹp phóng dật,\nVới hạnh không phóng dật,\nLeo lầu cao trí tuệ,\nKhông sầu, nhìn khổ sầu,\nBậc trí đứng núi cao,\nNhìn kẻ ngu, đất bằng.', 'When one who''s wise does drive away\nheedlessness by heedfulness,\nhaving ascended wisdom''s tower\nsteadfast, one surveys the fools,\ngriefless, views the grieving folk,\nas mountaineer does those below.'),
(29, 2, 'Tinh cần giữa phóng dật,\nTỉnh thức giữa quần mê.\nNgười trí như ngựa phi,\nBỏ sau con ngựa hèn.', 'Among the heedless, heedful,\namong the sleepy, wide awake.\nAs the swift horse outruns a hack\nso one of good wisdom wins.'),
(30, 2, 'Ðế Thích không phóng dật,\nÐạt ngôi vị Thiên chủ.\nKhông phóng dật, được khen ;\nPhóng dật, thường bị trách.', 'Heedfulness is always praised,\nheedlessness is ever blamed.\nBy heedfulness did Magha go\nto lordship of the gods.'),
(31, 2, 'Vui thích không phóng dật,\nTỷ kheo sợ phóng dật,\nBước tới như lửa hừng,\nThiêu kiết sử lớn nhỏ.', 'The bhikkhu liking heedfulness,\nseeing fear in heedlessness,\nadvances as a conflagration\nburning fetters great and small.'),
(32, 2, 'Vui thích không phóng dật,\nTỷ kheo sợ phóng dật,\nKhông thể bị thối đọa,\nNhất định gần Niết Bàn.', 'The bhikkhu liking heedfulness,\nseeing fear in heedlessness,\nnever will he fall away,\nnear is he to Nibbana.'),
(33, 3, 'Tâm hoảng hốt giao động,\nKhó hộ trì, khó nhiếp,\nNgười trí làm tâm thẳng,\nNhư thợ tên, làm tên.', 'Mind agitated, wavering,\nhard to guard and hard to check,\none of wisdom renders straight\nas arrow-maker a shaft.'),
(34, 3, 'Như cá quăng lên bờ,\nVất ra ngoài thủy giới;\nTâm này vùng vẫy mạnh,\nHãy đoạn thế lực Ma.', 'As fish from watery home\nis drawn and cast upon the land,\neven so flounders this mind\nwhile Mara''s Realm abandoning.'),
(35, 3, 'Khó nắm giữ, khinh động,\nTheo các dục quay cuồng.\nLành thay, điều phục tâm;\nTâm điều, an lạc đến.', 'The mind is very hard to check\nand swift, it falls on what it wants.\nThe training of the mind is good,\na mind so tamed brings happiness.'),
(36, 3, 'Tâm khó thấy, tế nhị,\nTheo các dục quay cuồng.\nNgười trí phòng hộ tâm,\nTâm hộ, an lạc đến.', 'The mind is very hard to see\nand find, it falls on what it wants.\nOne who''s wise should guard the mind,\na guarded mind brings happiness.'),
(37, 3, 'Chạy xa, sống một mình,\nKhông thân, ẩn hang sâu\nAi điều phục được tâm,\nThoát khỏi Ma trói buộc.', 'Drifting far, straying all alone,\nformless, recumbent in a cave.\nThey will be free from Mara''s bonds\nwho do restrain this mind.'),
(38, 3, 'Ai tâm không an trú,\nKhông biết chân diệu pháp,\nTịnh tín bị rúng động,\nTrí tuệ không viên thành.', 'One of unsteady mind,\nwho doesn''t know True Dhamma,\nwho is of wavering confidence\nwisdom fails to win.'),
(39, 3, 'Tâm không đầy tràn dục,\nTâm không (hận) công phá,\nÐoạn tuyệt mọi thiện ác,\nKẻ tỉnh không sợ hãi.', 'One of unflooded mind,\na mind that is not battered,\nabandoning evil, merit too,\nno fear for One Awake.'),
(40, 3, 'Biết thân như đồ gốm,\nTrú tâm như thành trì,\nChống Ma với gươm trí ;\nGiữ chiến thắng không tham', 'Having known this urn-like body,\nmade firm this mind as fortress town,\nwith wisdom-weapon one fights Mara\nwhile guarding booty, unattached.'),
(41, 3, 'Không bao lâu thân này,\nSẽ nằm dài trên đất,\nBị vất bỏ, vô thức,\nNhư khúc cây vô dụng.', 'Not long alas, and it will lie\nthis body, here upon the earth.\nDiscarded, void of consciousness,\nuseless as a rotten log.'),
(42, 3, 'Kẻ thù hại kẻ thù,\nOan gia hại oan gia,\nKhông bằng tâm hướng tà,\nGây ác cho tự thân.', 'Whatever foe may do to foe,\nor haters those they hate\nthe ill-directed mind indeed\ncan do one greater harm.'),
(43, 3, 'Ðiều mẹ cha bà con,\nKhông có thể làm được,\nTâm hướng chánh làm được\nLàm được tốt đẹp hơn.', 'What one''s mother, what one''s father,\nwhatever other kin may do,\nthe well directed mind indeed\ncan do greater good.'),
(44, 4, 'Ai chinh phục đất này\nDạ ma, Thiên giới này?\nAi khéo giảng Pháp cú,\nNhư người khéo hái hoa ?', 'Who will comprehend this earth,\nthe world of Yama, and the gods?\nWho will discern the well-taught Dhamma\nas one who''s skilled selects a flower?'),
(45, 4, 'Hữu học chinh phục đất,\nDạ ma, Thiên giới này.\nHữu học giảng Pháp cú,\nNhư người khéo hái hoa.', 'One Trained will comprehend this earth,\nthe world of Yama, and the gods,\nOne Trained discerns the well-taught Dhamma\nas one who''s skilled selects a flower.'),
(46, 4, 'Biết thân như bọt nước,\nNgộ thân là như huyễn,\nBẻ tên hoa của Ma,\nVượt tầm mắt Thần chết', 'Having known this froth-like body\nand awakening to its mirage nature,\nsmashing Mara''s flowered shafts\nunseen beyond the Death-king go.'),
(47, 4, 'Người nhặt các loại hoa,\nÝ đắm say, tham nhiễm,\nBị Thần chết mang đi,\nNhư lụt trôi làng ngủ.', 'For one who has a clinging mind\nand gathers only pleasure-flowers,\nDeath does seize and carry away\nas great flood a sleeping village.'),
(48, 4, 'Người nhặt các loại hoa,\nÝ đắm say, tham nhiễm,\nCác dục chưa thỏa mãn,\nÐã bị chết chinh phục.', 'For one of desires insatiate\nwho gathers only pleasure-flowers,\nfor one who has a clinging mind\nDeath the sovereign overpowers.'),
(49, 4, 'Như ong đến với hoa,\nKhông hại sắc và hương,\nChe chở hoa, lấy nhụy.\nBậc Thánh đi vào làng.', 'Just as a bee in a flower\nharming neither hue nor scent\ngathers nectar, flies away,\nso in towns a Wise One fares.'),
(50, 4, 'Không nên nhìn lỗi người,\nNgười làm hay không làm.\nNên nhìn tự chính mình,\nCó làm hay không làm.', 'Not others'' opposition\nnor what they did or failed to do,\nbut in oneself should be sought\nthings done, things left undone.'),
(51, 4, 'Như bông hoa tươi đẹp,\nCó sắc nhưng không hương.\nCũng vậy, lời khéo nói,\nKhông làm, không kết quả.', 'Just as a gorgeous blossom\nbrilliant but unscented,\nso fruitless the well-spoken words\nof one who does not act.'),
(52, 4, 'Như bông hoa tươi đẹp,\nCó sắc lại thêm hương ;\nCũng vậy, lời khéo nói,\nCó làm, có kết quả.', 'Just as a gorgeous blossom,\nbrilliant and sweet-scented,\nso fruitful the well-spoken words\nof one who acts as well.'),
(53, 4, 'Như từ một đống hoa,\nNhiều tràng hoa được kết.\nCũng vậy, thân sanh tử,\nLàm được nhiều thiện sự.', 'As from a mass of flowers\nmany a garland may be made,\nso by one born mortal\nshould many good deeds be done.'),
(54, 4, 'Hương các loại hoa thơm \nKhông ngược bay chiều gió\nNhưng hương người đức hạnh\nNgược gió khắp tung bay\nChỉ có bậc chân nhân\nTõa khắp mọi phương trời.', 'The fragrance of flowers drifts with the wind\nas sandalwood, jasmine of lavender.\nThe fragrance of the virtuous sweeps the wind,\nall pervasive is virtue of the good.'),
(55, 4, 'Hoa chiên đàn, già la,\nHoa sen, hoa vũ quý.\nGiữa những hương hoa ấy,\nGiới hương là vô thượng.', 'Sandalwood or lavender,\nlotus or the jasmine great,\nof these many fragrances\nvirtue''s fragrance is supreme.'),
(56, 4, 'Ít giá trị hương này,\nHương già la, chiên đàn ;\nChỉ hương người đức hạnh,\nTối thượng tỏa Thiên giới.', 'Faint is this fragrance\nof lavender and sandalwood,\nbut fragrance of the virtuous\nsoars sublime amongst the gods.	'),
(57, 4, 'Giữa ai có giới hạnh,\nAn trú không phóng dật.\nChánh trí, chơn giải thoát,\nÁc ma không thấy đường.', 'Of those with perfect virtue\nwho dwell in heedfulness,\nfreed by Final Knowledge:\nMara cannot know their path.'),
(58, 4, 'Như giữa đống rác nhớp,\nQuăng bỏ trên đường lớn,\nChỗ ấy hoa sen nở,\nThơm sạch, đẹp ý người.', 'As beside the highroad\nwhere rubbish in a pit is flung\nthere flourishes the lotus bloom\nfragrant and the mind''s delight.'),
(59, 4, 'Cũng vậy giữa quần sanh,\nUế nhiễm, mù, phàm tục,\nÐệ tử bậc Chánh Giác,\nSáng ngời với Tuệ Tri.', 'So among rubbish-beings,\ncommon humans blind-become,\nthe Perfect Buddha''s pupil\noutshines with wisdom bright.'),
(60, 5, 'Ðêm dài cho kẻ thức,\nÐường dài cho kẻ mệt,\nLuân hồi dài, kẻ ngu,\nKhông biết chơn diệu pháp.', 'Long is the night for the sleepless,\nlong is the league for the weary one,\nsamsara''s way is long for fools\nwho know not the Dhamma True.'),
(61, 5, 'Tìm không được bạn đường,\nHơn mình hay bằng mình,\nThà quyết sống một mình,\nKhông làm bạn kẻ ngu.', 'If a wayfarer fails to find\none better or equal,\nsteadfast he should fare alone\nfor a fool''s no fellowship.'),
(62, 5, '"Con tôi, tài sản tôi",\nNgười ngu sanh ưu não,\nTự ta, ta không có,\nCon đâu, tài sản đâu?.', '"Sons have I, wealth have I",\nthus the fool is fretful.\nHe himself is not his own,\nhow then are sons, how wealth?'),
(63, 5, 'Người ngu nghĩ mình ngu,\nNhờ vậy thành có trí.\nNgười ngu tưởng có trí,\nThật xứng gọi chí ngu.', 'Conceiving so his foolishness\nthe fool is thereby wise,\nwhile "fool" is called that fool\nconceited that he''s wise.'),
(64, 5, 'Người ngu, dầu trọn đời,\nThân cận người có trí,\nKhông biết được Chánh pháp,\nNhư muỗng với vị canh.', 'Though all through life the fool\nmight wait upon the wise,\nno more Dhamma can he sense\nthan spoon the taste of soup.'),
(65, 5, 'Người trí, dù một khắc,\nThân cận người có trí,\nBiết ngay chân diệu pháp,\nNhư lưỡi với vị canh.', 'Though briefly one intelligent\nmight wait upon the wise,\nquickly Dhamma he can sense\nas tongue the taste of soup.'),
(66, 5, 'Người ngu si thiếu trí,\nTự ngã thành kẻ thù.\nLàm các nghiệp không thiện,\nPhải chịu quả đắng cay.', 'Fools of feeble wisdom fare\nenemies to themselves,\nmaking evil kamma\nwhich is of bitter fruit.'),
(67, 5, 'Nghiệp làm không chánh thiện,\nLàm rồi sanh ăn năn,\nMặt nhuốm lệ, khóc than,\nLãnh chịu quả dị thục.', 'That kamma''s not well-made\nfrom which there is remorse,\nof which one senses the result\nwith weeping and a tear-stained face.'),
(68, 5, 'Và nghiệp làm chánh thiện,\nLàm rồi không ăn năn,\nHoan hỷ, ý đẹp lòng,\nHưởng thọ quả dị thục.', 'But well-made is that kamma\nwhich done brings no remorse,\nof which one senses the result\nwith glad mind and with joy.'),
(69, 5, 'Người ngu nghĩ là ngọt,\nKhi ác chưa chín muồi;\nÁc nghiệp chín muồi rồi,\nNgười ngu chịu khổ đau.', 'When evil kamma''s immature\nthe fool thinks it is honeyed,\nbut when the evil has matured\nthen to the fool comes dukkha.'),
(70, 5, 'Tháng tháng với ngọn cỏ,\nNgười ngu có ăn uống\nKhông bằng phần mười sáu,\nNgười hiểu pháp hữu vi.', 'Month after month with blady-grass tip\nthe fool may take his food;\nhe''s not worth the slightest bit\nof one who Dhamma knows.'),
(71, 5, 'Nghiệp ác đã được làm,\nNhư sữa, không đông ngay,\nCháy ngầm theo kẻ ngu,\nNhư lửa tro che đậy.', 'As milk, is evil kamma done,\nso slowly does it sour.\nSmouldering does it follow the fool\nlike fire with ashes covered.'),
(72, 5, 'Tự nó chịu bất hạnh,\nKhi danh đến kẻ ngu.\nVận may bị tổn hại,\nÐầu nó bị nát tan.', 'Truly to his detriment\nskill is born to the fool;\nruined is his better nature\nand scattered are his wits.'),
(73, 5, 'Ưa danh không tương xứng,\nMuốn ngồi trước tỷ kheo,\nưa quyền tại tịnh xá,\nMuốn mọi người lễ kính.', 'For position a fool may wish:\namong the bhikkhus precedence,\nin monasteries authority,\nfrom other families honours.'),
(74, 5, '"Mong cả hai tăng, tục,\nNghĩ rằng (chính ta làm).\nTrong mọi việc lớn nhỏ,\nPhải theo mệnh lệnh ta"\nNgười ngu nghĩ như vậy\nDục và mạn tăng trưởng.', 'Both monks and laymen, let them think\n"This was done by me,\nwhatever the works, both great and small, \nlet them depend on me".\nSuch the intention of a fool, \nswollen his greed and conceit.'),
(75, 5, 'Khác thay duyên thế lợi,\nKhác thay đường Niết Bàn.\nTỷ kheo, đệ tử Phật,\nHãy như vậy thắng tri.\nChớ ưa thích cung kính,\nHãy tu hạnh viễn ly.', 'One is the way to worldly gain,\nanother to Nibbana goes.\nClearly comprehending this\nthe bhikkhu, Buddha''s follower\nshould wallow not in proffered gifts,\nsurrendering instead to solitude.'),
(76, 6, 'Nếu thấy bậc hiền trí,\nChỉ lỗi và khiển trách,\nNhư chỉ chỗ chôn vàng,\nHãy thân cận người trí !\nThân cận người như vậy,\nChỉ tốt hơn, không xấu.', 'Should one a man of wisdom meet\nwho points out faults and gives reproof,\nwho lays a hidden treasure bare,\nwith such a sage should one consort.\nConsorting so is one enriched\nand never in decline.'),
(77, 6, 'Những người hay khuyên dạy,\nNgăn người khác làm ác,\nÐược người hiền kính yêu,\nBị người ác không thích.', 'Let him exhort, let him instruct,\nand check one from abasement.\nDear indeed is he to the true,\nnot dear is he to the false.'),
(78, 6, 'Chớ thân với bạn ác,\nChớ thân kẻ tiểu nhân.\nHãy thân người bạn lành,\nHãy thân bậc thượng nhân.', 'Don''t go around with evil friends,\nwith rogues do not resort.\nSpend your time with noble friends,\nand worthy ones consort.'),
(79, 6, 'Pháp hỷ đem an lạc,\nVới tâm tư thuần tịnh;\nNgười trí thường hoan hỷ,\nVới pháp bậc Thánh thuyết.', 'Happy is he who Dhamma drinks\nwith heart that''s clear and cool.\nOne so wise e''er delights\nin Dhamma declared by the Noble.'),
(80, 6, 'Người trị thủy dẫn nước,\nKẻ làm tên nắn tên,\nNgười thợ mộc uốn gỗ,\nBậc trí nhiếp tự thân.', 'Irrigators govern waters,\nfletchers fashion shafts,\nas joiners shape their timber\nthose who are wise tame themselves.'),
(81, 6, 'Như đá tảng kiên cố,\nKhông gió nào lay động,\nCũng vậy, giữa khen chê,\nNgười trí không giao động.', 'Just as a mighty boulder\nstirs not with the wind,\nso the wise are never moved\neither by praise or blame.'),
(82, 6, 'Như hồ nước sâu thẳm,\nTrong sáng, không khuấy đục,\nCũng vậy, nghe chánh pháp,\nNgười trí hưởng tịnh lạc.', 'Even as a fathomless lake,\na lake so calm and clear,\nso dhammas having heard\nserene the wise become.'),
(83, 6, 'Người hiền bỏ tất cả,\nNgười lành không bàn dục,\nDầu cảm thọ lạc khổ,\nBậc trí không vui buồn.', 'Everything the good renounce,\nthe peaceful chatter not of fond delights,\nand whether touched by pleasure or pain\nnor joy or woe in the wise is seen.'),
(84, 6, 'Không vì mình, vì người.\nKhông cầu được con cái,\nKhông tài sản quốc độ,\nKhông cầu mình thành tựu, \nVới việc làm phi pháp.\nVị ấy thật trì giới,\nCó trí tuệ, đúng pháp.', 'Neither for one''s own, nor for another''s sake\none should wish for children, wealth and estate,\nnor success desire by means unjust,\nthus virtuous, and wise, righteous one would be.'),
(85, 6, 'Ít người giữa nhân loại,\nÐến được bờ bên kia.\nCòn số người còn lại,\nXuôi ngược chạy bờ này.', 'Among folk they are few\nwho go to the Further Shore,\nmost among humanity\nscurry on this hither shore.'),
(86, 6, 'Những ai hành trì pháp,\nTheo chánh pháp khéo dạy,\nSẽ đến bờ bên kia,\nVượt ma lực khó thoát.', 'But they who practise Dhamma\naccording to Dhamma well-told,\nfrom Death''s Domain hard to leave\nthey''ll cross to the Further Shore.'),
(87, 6, 'Kẻ trí bỏ pháp đen,\nTu tập theo pháp trắng.\nBỏ nhà, sống không nhà,\nSống viễn ly khó lạc.', 'Abandoning the Dhammas dark\nthe wise should cultivate the bright,\nhaving from home to homeless gone\nin solitude unsettling.'),
(88, 6, 'Hãy cầu vui Niết Bàn,\nBỏ dục, không sở hữu,\nKẻ trí tự rửa sạch,\nCấu uế từ nội tâm.', 'Let them desire that rare delight\nrenouncing pleasures, owing nought,\nthose wise ones should cleanse themselves\nfrom all defilements of the mind.'),
(89, 6, 'Những ai với chánh tâm,\nKhéo tu tập giác chi,\nTừ bỏ mọi ái nhiễm,\nHoan hỷ không chấp thủ.\nKhông lậu hoặc, sáng chói,\nSống tịch tịnh ở đời.', 'Those who come to Wakening\nwith mind full-cultivated,\ndelight, no longer clinging,\nin relinquishing attachment:\nthey, without pollution, radiant,\nin this world have reached Nibbana.'),
(90, 7, 'Ðích đã đến, không sầu,\nGiải thoát ngoài tất cả,\nÐoạn trừ mọi buộc ràng,\nVị ấy không nhiệt não.', 'With journey finished and sorrowless,\nfrom everything completely free,\nfor one who has loosened all the ties\npassion’s fever is not found.'),
(91, 7, 'Tự sách tấn chánh niệm,\nKhông thích cư xá nào,\nNhư ngỗng trời rời ao,\nBỏ sau mọi trú ẩn.', 'Mindful Ones exert themselves,\nin no abode do they delight,\nas swans abandoning their lake\nhome after home they leave behind.'),
(92, 7, 'Tài sản không chất chứa,\nĂn uống biết liễu trì,\nTự tại trong hành xứ,\nKhông vô tướng, giải thoát,\nNhư chim giữa hư không,\nHướng chúng đi khó tìm.', 'For those who don’t accumulate,\nwho well reflect upon their food,\nthey have as range the nameless and\nthe void of perfect freedom too.\nAs birds that wing through space,\nhard to trace their going.'),
(93, 7, 'Ai lậu hoặc đoạn sạch,\nĂn uống không tham đắm,\nTự tại trong hành xứ,\nKhông, vô tướng, giải thoát.\nNhư chim giữa hư không,\nDấu chân thật khó tìm.', 'For whom pollutions are destroyed,\nnot attached to any food,\nhe has as range the nameless and\nthe void of perfect freedom too.\nAs birds that wing through space,\nhard to trace his going.'),
(94, 7, 'Ai nhiếp phục các căn,\nNhư đánh xe điều ngự,\nMạn trừ, lậu hoặc dứt,\nNgười vậy, Chư Thiên mến.', 'Whose faculties are pacified\nas steeds by charioteers well-tamed,\nwith pride abandoned, unpolluted,\nto even devas this One’s dear.'),
(95, 7, 'Như đất không hiềm hận,\nNhư cột trụ kiên trì,\nNhư hồ, không bùn nhơ,\nKhông luân hồi, vị ấy.', 'Like earth is one who’s well-behaved,\nsecure and not resentful,\nas city-post, as filth-free lake,\nno wanderings-on for One Who’s Thus.'),
(96, 7, 'Người tâm ý an tịnh,\nLời an, nghiệp cũng an,\nChánh trí, chơn giải thoát,\nTịnh lạc là vị ấy.', 'Peaceful his mind and peaceful\nhis speech and actions too,\nperfect in knowledge of freedom,\nOne Thus is of utmost peace.'),
(97, 7, 'Không tin, hiểu Vô vi.\nNgười cắt mọi hệ lụy,\nCơ hội tận, xả ly\nVị ấy thật tối thượng.', 'With no beliefs, the Unmade known,\nwith fetters finally severed,\nwith kammas cut and craving shed,\nattained to humanity’s heights.'),
(98, 7, 'Làng mạc hay rừng núi\nThung lũng hay đồi cao,\nLa Hán trú chỗ nào,\nÐất ấy thật khả ái.', 'Whether in town or woods,\nwhether in vale, on hill,\nwherever dwell the Arahants\nso pleasing there the earth.'),
(99, 7, 'Khả ái thay núi rừng,\nChỗ người phàm không ưa,\nVị ly tham ưa thích,\nVì không tìm dục lạc.', 'Delightful are the forests\nwhere folk do not delight,\nthere the Passionless delight,\nthey’re not pleasure-seekers.'),
(100, 8, 'Dầu nói ngàn ngàn lời,\nNhưng không gì lợi ích,\nTốt hơn một câu nghĩa,\nNghe xong, được tịnh lạc.', 'Though a thousand speeches be\ncomposed of meaningless lines,\nbetter the single meaningful line\none hears, then comes to calm.'),
(101, 8, 'Dầu nói ngàn câu kệ\nNhưng không gì lợi ích,\nTốt hơn nói một câu,\nNghe xong, được tịnh lạc.', 'Though a thousand verses be\ncomposed of meaningless lines,\nbetter the single line of verse\none hears, then comes to calm.'),
(102, 8, 'Dầu nói trăm câu kệ\nNhưng không gì lợi ích\nTốt hơn một câu Pháp,\nNghe xong, được tịnh lạc.', 'Though a hundred verses chant\ncomposed of meaningless lines,\nbetter the single Dhamma line\none hears, then comes to calm.'),
(103, 8, 'Dầu tại bãi chiến trường\nThắng ngàn ngàn quân địch,\nTự thắng mình tốt hơn,\nThật chiến thắng tối thượng.', 'Though thousand times a thousand men\nin battle one may conquer,\nyet should one conquer just oneself\none is the greatest conqueror.'),
(104, 8, 'Tự thắng , tốt đẹp hơn,\nHơn chiến thắng người khác.\nNgười khéo điều phục mình,\nThường sống tự chế ngự.', 'Greater the conquest of oneself\nthan subjugating others,\nthat one who''s always well-restrained,\nthat one who''s tamed of self.'),
(105, 8, 'Dầu Thiên Thần, Thát Bà,\nDầu Ma Vương, Phạm Thiên\nKhông ai chiến thắng nổi,\nNgười tự thắng như vậy.', 'Neither deva nor minstrel divine,\nnor Mara together with Brahma,\ncan overthrow the victory\nof such a one as this.'),
(106, 8, 'Tháng tháng bỏ ngàn vàng,\nTế tự cả trăm năm,\nChẳng bằng trong giây lát,\nCúng dường bậc tự tu.\nCúng dường vậy tốt hơn,\nHơn trăm năm tế tự.', 'Month by month for a hundred years\na thousand one might sacrifice,\nbut if for only a moment one\nmight honour the self-developed,\nsuch honour then were better by far\nthan a century of sacrifice.'),
(107, 8, 'Dầu trải một trăm năm,\nThờ lửa tại rừng sâu,\nChẳng bằng trong giây lát,\nCúng dường bậc tự tu.\nCúng dường vậy tốt hơn,\nHơn trăm năm tế tự.', 'One might tend for a hundred years\nthe forest''s sacred fire,\nbut if for only a moment one\nmight honour the self-developed,\nsuch honour then were better by far\nthan a century of sacrifice.'),
(108, 8, 'Suốt năm cúng tế vật,\nÐể cầu phước ở đời.\nKhông bằng một phần tư\nKính lễ bậc chánh trực.', 'Whatever one who merit seeks\nshould for a year make sacrifice,\nall comes not to a quarter part\nof honouring the Noble.'),
(109, 8, 'Thường tôn trọng, kính lễ\nBậc kỳ lão trưởng thượng,\nBốn pháp được tăng trưởng:\nThọ, sắc, lạc, sức mạnh.', 'For one of respectful nature\nwho ever the elders honours,\nlong life and beauty, joy and strength,\nthese qualities increase.'),
(110, 8, 'Dầu sống một trăm năm,\nÁc giới, không thiền định,\nTốt hơn sống một ngày,\nTrì giới, tu thiền định.', 'Though one should live a hundred years\nfoolish, uncontrolled,\nyet better is life for a single day\nmoral and meditative.'),
(111, 8, 'Ai sống một trăm năm,\nÁc tuệ, không thiền định.\nTốt hơn sống một ngày,\nCó tuệ, tu thiền định.', 'Though one should live a hundred years\nfoolish, uncontrolled,\nyet better is life for a single day\nwise and meditative.'),
(112, 8, 'Ai sống một trăm năm,\nLười nhác không tin tấn,\nTốt hơn sống một ngày\nTinh tấn tận sức mình.', 'Though one should live a hundred years\nlazy, of little effort,\nyet better is life for a single day\nstrongly making effort.'),
(113, 8, 'Ai sống một trăm năm,\nKhông thấy pháp sinh diệt,\nTốt hơn sống một ngày,\nThấy được pháp sinh diệt.', 'Though one should live a hundred years\nnot seeing rise and fall,\nyet better is life for a single day\nseeing rise and fall.'),
(114, 8, 'Ai sống một trăm năm,\nKhông thấy câu bất tử,\nTốt hơn sống một ngày,\nThấy được câu bất tử.', 'Though one should live a hundred years\nnot seeing the Deathless State,\nyet better is life for a single day\nseeing Deathlessness.'),
(115, 8, 'Ai sống một trăm năm,\nKhông thấy Pháp Tối thượng,\nTốt hơn sống một ngày,\nThấy được Pháp Tối thượng.', 'Though one should live a hundred years\nnot seeing Dhamma Supreme,\nyet better is life for a single day\nseeing Dhamma Supreme.'),
(116, 9, 'Hãy gấp làm điều lành,\nNgăn tâm làm điều ác.\nAi chậm làm việc lành,\nÝ ưa thích việc ác.', 'Make haste towards the good\nand check the mind from evil.\nThe one who''s is slow in making merit\ndelights in the mind in evil.'),
(117, 9, 'Nếu người làm điều ác,\nChớ tiếp tục làm thêm.\nChớ ước muốn điều ác,\nChứa ác, tất chịu khổ.', 'If one should some evil do\nthen do it not again and again.\nDo not wish for it anew\nfor evil grows to dukkha.'),
(118, 9, 'Nếu người làm điều thiện,\nNên tiếp tục làm thêm.\nHãy ước muốn điều thiện,\nChứa thiện, được an lạc.', 'If one should some merit make\ndo it again and again.\nOne should wish for it anew\nfor merit grows to joy.'),
(119, 9, 'Người ác thấy là hiền.\nKhi ác chưa chín muồi,\nKhi ác nghiệp chín muồi,\nNgười ác mới thấy ác.', 'As long as evil ripens not\neven the evil one goodness knows,\nbut when the evil ripens\nthen that person evil knows.'),
(120, 9, 'Người hiền thấy là ác,\nKhi thiện chưa chín muồi,\nKhi thiện nghiệp chín muồi,\nNgười hiền thấy là thiện.', 'As long as goodness ripens not\neven the good one evil knows,\nbut when the goodness ripens\nthen that person knows the good.'),
(121, 9, 'Chớ chê khinh điều ác,\nCho rằng "chưa đến mình",\nNhư nước nhỏ từng giọt,\nRồi bình cũng đầy tràn.\nNgười ngu chứa đầy ác\nDo chất chứa dần dần.', 'Think lightly not of evil,\n"It will not come to me",\nfor by the falling of water drops\na water jar is filled.\nThe fool with evil fills himself,\nhe soaks up little by little.'),
(122, 9, 'Chớ chê khinh điều thiện\nCho rằng "chưa đến mình",\nNhư nước nhỏ từng giọt,\nRồi bình cũng đầy tràn.\nNgười trí chứa đầy thiện,\nDo chất chứa dần dần.', 'Think lightly not of goodness,\n"It will not come to me",\nfor by the falling of water drops\na water jar is filled.\nThe sage with goodness fills himself,\nhe soaks up little by little.'),
(123, 9, 'Ít bạn đường, nhiều tiền,\nNgười buôn tránh đường hiểm.\nMuốn sống, tránh thuốc độc,\nHãy tránh ác như vậy.', 'As merchant on a perilous path,\ngreat wealth having little guard,\nas life-loving man with poison\nso with evil heedful be.'),
(124, 9, 'Bàn tay không thương tích,\nCó thể cầm thuốc độc.\nKhông thương tích, tránh độc,\nKhông làm, không có ác.', 'If in the hand''s no wound\npoison one may bear.\nA woundless one is poisoned not,\nnon-doers have no evil.'),
(125, 9, 'Hại người không ác tâm,\nNgười thanh tịnh, không uế,\nTội ác đến kẻ ngu,\nNhư ngược gió tung bụi.', 'Who offends the inoffensive,\nthe innocent and blameless one,\nupon that fool does evil fall\nas fine dust flung against the wind.'),
(126, 9, 'Một số sinh bào thai,\nKẻ ác sinh địa ngục,\nNgười thiện lên cõi trời,\nVô lậu chứng Niết Bàn.', 'Some find birth within a womb,\nevil-doer quicken in hell,\ngood-farers to the heavens go,\nthe Unpolluted wholly cool.'),
(127, 9, 'Không trên trời, giữa biển,\nKhông lánh vào động núi,\nKhông chỗ nào trên đời,\nTrốn được quả ác nghiệp.', 'Neither in sky nor surrounding by sea,\nnor by dwelling in a mountain cave,\nnowhere is found that place in earth\nwhere one''s from evil kamma free.'),
(128, 9, 'Không trên trời, giữa biển,\nKhông lánh vào động núi,\nKhông chỗ nào trên đời,\nTrốn khỏi tay Thần Chết.', 'Neither in sky nor surrounding by sea,\nnor by dwelling in a mountain cave,\nnowhere is found that place in earth\nwhere one''s by Death not overcome.'),
(129, 10, 'Mọi người sợ hình phạt,\nMọi người sợ tử vong.\nLấy mình làm ví dụ\nKhông giết, không bảo giết.', 'All tremble at force,\nof death are all afraid.\nLikening others to oneself\nkill not nor cause to kill.'),
(130, 10, 'Mọi người sợ hình phạt,\nMọi người thương sống còn;\nLấy mình làm ví dụ,\nKhông giết, không bảo giết.', 'All tremble at force,\ndear is life to all.\nLikening others to oneself\nkill not nor cause to kill.'),
(131, 10, 'Chúng sanh cầu an lạc,\nAi dùng trượng hại người,\nÐể tìm lạc cho mình,\nÐời sau không được lạc.', 'Whoever harms with force\nthose desiring happiness,\nas seeker after happiness\none gains no future joy.'),
(132, 10, 'Chúng sanh cầu an lạc,\nKhông dùng trượng hại người,\nÐể tìm lạc cho mình,\nÐời sau hưởng được lạc.', 'Whoever doesn''t harm with force\nthose desiring happiness,\nas seeker after happiness\none then gains future joy.'),
(133, 10, 'Chớ nói lời ác độc,\nNói ác, bị nói lại,\nKhổ thay lời phẩn nộ,\nÐao trượng phản chạm người.', 'Speak not harshly to other folk,\nspeaking so, they may retort.\nDukkha indeed is quarrelsome speech\nand force for force may hurt you.'),
(134, 10, 'Nếu tự mình yên lặng,\nNhư chiếc chuông bị bể.\nNgười đã chứng Niết Bàn\nNgười không còn phẩn nộ.', 'If like a broken gong\nnever you reverberate,\nquarreling''s not part of you,\nthat Nibbana''s reached.'),
(135, 10, 'Với gậy người chăn bò,\nLùa bò ra bãi cỏ;\nCũng vậy, già và chết,\nLùa người đến mạng chung.', 'As with force the cowherds drive\ntheir cattle out to graze,\nlike this decay and death drive out\nthe life from all beings.'),
(136, 10, 'Người ngu làm điều ác,\nKhông ý thức việc làm.\nDo tự nghiệp, người ngu\nBị nung nấu, như lửa.', 'When the fool does evil deeds\ntheir end he does not know,\nsuch kamma burns the one unwise\nas one who''s scorched by fire.'),
(137, 10, 'Dùng trượng phạt không trượng\nLàm ác người không ác.\nTrong mười loại khổ đau,\nChịu gấp một loại khổ:', 'Whoever forces the forceless\nor offends the inoffensive,\nspeedily comes indeed\nto one of these ten states:'),
(138, 10, 'Hoặc khổ thọ khốc liệt,\nThân thể bị thương vong,\nHoặc thọ bệnh kịch liệt,\nHay loạn ý tán tâm.', 'Sharp pain or deprivation,\nor injury to the body,\nor to a serious disease,\nderangement of the mind;'),
(139, 10, 'Hoặc tai vạ từ vua,\nHay bị vu trọng tội;\nBà con phải ly tán,\nTài sản bị nát tan.', 'Troubled by the government,\nor else false accusation,\nor by loss of relatives,\ndestruction of one''s wealth;'),
(140, 10, 'Hoặc phòng ốc nhà cửa\nBị hỏa tai thiêu đốt;\nKhi thân hoại mạng chung,\nÁc tuệ sanh địa ngục.', 'Or one''s houses burn\non raging conflagration,\nat the body''s end, in hell\narises that unwise one.'),
(141, 10, 'Không phải sống lõa thể\nBện tóc, tro trét mình,\nTuyệt thực, lăn trên đất,\nSống nhớp, siêng ngồi xổm,\nLàm con người được sạch,\nNếu không trừ nghi hoặc.', 'Not going naked, nor matted hair, nor filth,\nnor fasting, not sleeping on bare earth,\nno penance on heels, nor sweat nor grime\ncan purify a mortal still overcome by doubt.'),
(142, 10, 'Ai sống tự trang sức,\nNhưng an tịnh, nhiếp phục,\nSống kiên trì, phạm hạnh,\nKhông hại mọi sinh linh.\nVị ấy là phạm chí,\nHay sa môn, khất sĩ.', 'Even though adorned, if living in peace\ncalm, tamed, established in the holy life,\nfor beings all laying force aside:\none pure, one peaceful, a bhikkhu is he.'),
(143, 10, 'Thật khó tìm ở đời,\nNgười biết thẹn, tự chế,\nBiết tránh né chỉ trích\nNhư ngựa hiền tránh roi.', 'Where in the world is found\none restrained by shame,\nawakened out of sleep\nas splendid horse with whip?'),
(144, 10, 'Như ngựa hiền chạm roi,\nHãy nhiệt tâm, hăng hái,\nVới tín, giới, tinh tấn,\nThiền định cùng trạch pháp.\nMinh hạnh đủ, chánh niệm,\nÐoạn khổ này vô lượng.', 'As splendid horse touched with whip,\nbe ardent, deeply moved,\nby faith and virtue, effort too,\nby meditation, Dhamma''s search,\nby knowledge, kindness, mindfulness;\nabandon dukkha limitless!'),
(145, 10, 'Người trị thủy dẫn nước,\nKẻ làm tên nắn tên,\nNgười thợ mộc uốn ván,\nBậc tự điều, điều thân.', 'Irrigators govern water,\nfletchers fashion shafts,\nas joiners shape their timber\nthose of good conduct tame themselves.'),
(146, 11, 'Cười gì, hân hoan gì,\nKhi đời mãi bị thiêu?\nBị tối tăm bao trùm,\nSao không tìm ngọn đèn?', 'Why this laughter, why this joy,\nwhen it''s ever blazing?\nShrouded all about by gloom\nwon''t you look for the light?'),
(147, 11, 'Hãy xem bong bóng đẹp,\nChỗ chất chứa vết thương,\nBệnh hoạn nhiều suy tư,\nThật không gì trường cửu.', 'See this body beautiful\na mass of sores, a congeries,\nmuch considered but miserable\nwhere nothing''s stable, nothing persists.'),
(148, 11, 'Sắc này bị suy già,\nỔ tật bệnh, mỏng manh,\nNhóm bất tịnh đổ vỡ,\nChết chấm dứt mạng sống.', 'All decrepit is this body,\ndiseases'' nest and frail;\nthis foul mass is broken up\nfor life does end in death.'),
(149, 11, 'Như trái bầu mùa thu,\nBị vất bỏ quăng đi,\nNhóm xương trắng bồ câu,\nThấy chúng còn vui gì?', 'These dove-hued bones\nscattered in Fall,\nlike long white gourds,\nwhat joy in seeing them?'),
(150, 11, 'Thành này làm bằng xương,\nQuét tô bằng thịt máu,\nỞ đây già và chết,\nMạn, lừa đảo chất chứa.', 'This city''s made of bones\nplastered with flesh and blood,\nwithin are stored decay and death,\nbesmearing and conceit.'),
(151, 11, 'Xe vua đẹp cũng già,\nThân này rồi sẽ già.\nPháp bậc thiện, không già.\nNhư vậy bậc chí thiện\nNói lên cho bậc thiện.', 'Even rich royal chariots rot,\nthe body too does rot, decay,\nbut undecaying''s Dhamma of the Good;\nwho to the good declare.'),
(152, 11, 'Người ít nghe kém học,\nLớn già như trâu đực.\nThịt nó tuy lớn lên,\nNhưng tuệ không tăng trưởng.', 'Just as the ox grows old\nso this man of little learning:\nhis fleshiness increases,\nhis wisdom doesn''t grow.'),
(153, 11, 'Lang thang bao kiếp sống\nTa tìm nhưng chẳng gặp,\nNgười xây dựng nhà này,\nKhổ thay, phải tái sanh.', 'Through many of samsara''s births\nI hasten seeking, finding not\nthe builder of this house:\npain is birth again, again.'),
(154, 11, 'Ôi! Người làm nhà kia\nNay ta đã thấy ngươi!\nNgười không làm nhà nữa.\nÐòn tay ngươi bị gẫy,\nKèo cột ngươi bị tan\nTâm ta đạt tịch diệt,\nTham ái thảy tiêu vong.', 'O Builder of this house, you''re seen!\nyou shall not build a house again;\nall you beams have given away,\nrafters of the ridge decayed,\nmind to the Unconditioned gone,\nexhaustion of craving has it reached.'),
(155, 11, 'Lúc trẻ, không phạm hạnh,\nKhông tìm kiếm bạc tiền.\nNhư cò già bên ao,\nỦ rũ, không tôm cá.', 'Who have not led the holy life\nnor riches won while young,\nthey linger on as aged cranes\naround a fished-out pond.'),
(156, 11, 'Lúc trẻ, không phạm hạnh,\nKhông tìm kiếm bạc tiền.\nNhư cây cung bị gẫy,\nThở than những ngày qua', 'Who have not led the holy life\nnor riches won while young,\nthey languish on, worn-out bows,\nsighing for the past.'),
(157, 12, 'Nếu biết yêu tự ngã,\nPhải khéo bảo vệ mình,\nNgười trí trong ba canh,\nPhải luôn tỉnh thức.', 'If one holds oneself as dear,\nprotected, one protects oneself.\nOne who''s wise should be aware\nthrough all the watches three.'),
(158, 12, 'Trước hết tự đặt mình,\nVào những gì thích đáng.\nSau mới giáo hóa người,\nNgười trí khỏi bị nhiễm.', 'One should first establish\noneself in what is proper.\nOne may then teach others,\nand wise, one is not blamed.'),
(159, 12, 'Hãy tự làm cho mình,\nNhư điều mình dạy người.\nKhéo tự điều, điều người,\nKhó thay tự điều phục !', 'As one teaches others\nso should one do oneself.\nWell-tamed, on may tame others,\noneself to tame is hard.'),
(160, 12, 'Tự mình y chỉ mình,\nNào có y chỉ khác.\nNhờ khéo điều phục mình,\nÐược y chỉ khó được.', 'Oneself is refuge of oneself,\nwho else indeed could refuge be?\nBy good training of oneself\none gains a refuge hard to gain.'),
(161, 12, 'Ðiều ác mình tự làm,\nTự mình sanh, mình tạo.\nNghiền nát kẻ ngu si,\nnhư kim cương, ngọc báu.', 'By oneself is evil done,\nit''s born of self and self-produced.\nEvil grinds the unwise one\nas diamond does the hardest gem.'),
(162, 12, 'Phá giới quá trầm trọng,\nNhư dây leo bám cây\nGieo hại cho tự thân,\nNhư kẻ thù mong ước.', 'He whose conduct''s very bad\nlike oak-tree choked with ivy,\nso he does towards himself\nwhat enemies would wish.'),
(163, 12, 'Dễ làm các điều ác,\nDễ làm tự hại mình.\nCòn việc lành, việc tốt,\nThật tối thượng khó làm.', 'Easy is what''s bad to do,\nwhat''s harmful to oneself.\nBut what is good, of benefit,\nis very hard to do.'),
(164, 12, 'Kẻ ngu si miệt thị,\nGiáo pháp bậc La Hán,\nBậc Thánh, bậc chánh mạng.\nChính do ác kiến này,\nNhư quả loại cây lau\nMang quả tự hoại diệt.', 'Whatever man unwise relies\non evil views and so condemns\nthe Teaching of the Arahants,\nor Noble Ones who Dhamma live,\nhe, as a bamboo fruiting,\nfruits to self-destruction.'),
(165, 12, 'Tự mình, điều ác làm\nTự mình làm nhiễm ô,\nTự mình, ác không làm,\nTự mình làm thanh tịnh.\nTịnh, không tịnh tự mình,\nKhông ai thanh tịnh ai!', 'By oneself is evil done,\nby oneself defiled,\nby oneself it''s left undone,\nby self alone one purified.\nPurity, impurity on oneself depend,\nno one can purify another.'),
(166, 12, 'Dầu lợi người bao nhiêu,\nChớ quên phần tư lợi,\nNhờ thắng trí tư lợi.\nHãy chuyên tâm lợi mình.', 'Let none neglect their good\nfor others'' good however great.\nKnow well oneself''s own good\nand to that good attend.'),
(167, 13, 'Chớ theo pháp hạ liệt.\nChớ sống mặc, buông lung;\nChớ tin theo tà kiến,\nChớ tăng trưởng tục trần.', 'Do not follow base desires,\nnor live with heedlessness,\ndo not follow wrong beliefs\nto grow in worldly ways.'),
(168, 13, 'Nỗ lực, chớ phóng dật!\nHãy sống theo chánh hạnh;\nNgười chánh hạnh hưởng lạc,\nCả đời này, đời sau.', 'Rouse yourself, be diligent,\nin Dhamma faring well.\nWho dwells in Dhamma''s happy\nin this birth and the next.'),
(169, 13, 'Hãy khéo sống chánh hạnh,\nChớ sống theo tà hạnh!\nNgười chánh hạnh hưởng lạc,\nCả đời này, đời sau.', 'Fare in Dhamma coursing well,\nin evil courses do not fare.\nWho dwells in Dhamma''s happy\nin this birth and the next.'),
(170, 13, 'Hãy nhìn như bọt nước,\nHãy nhìn như cảnh huyển!\nQuán nhìn đời như vậy,\nThần chết không bắt gặp.', 'Just as a bubble may be seen,\njust as a faint mirage,\nso should the world be viewed\nthat the Death-king sees one not.'),
(171, 13, 'Hãy đến nhìn đời này,\nNhư xe vua lộng lẫy,\nNgười ngu mới tham đắm,\nKẻ trí nào đắm say.', 'Come, look upon this world\nlike to a rich, royal chariot\nwherein fools lounge at ease\nbut alert ones linger not.'),
(172, 13, 'Ai sống trước buông lung,\nSau sống không phóng dật,\nChói sáng rực đời này.\nNhư trăng thoát mây che.', 'Whoso was heedless formerly\nbut later lives with heedfulness\nilluminates all this world\nas moon when free of clouds.'),
(173, 13, 'Ai dùng các hạnh lành,\nLàm xóa mờ nghiệp ác,\nChói sáng rực đời này,\nNhư trăng thoát mây che.', 'Who by wholesome kamma\ncovers up the evil done\nillumines all this world\nas moon when free from clouds.'),
(174, 13, 'Ðời này thật mù quáng,\nÍt kẻ thấy rõ ràng.\nNhư chim thoát khỏi lưới,\nRất ít đi thiên giới.', 'This world is blind-become\nfew are here who see within\nas few the birds break free from net\nso those who go to heavens.'),
(175, 13, 'Như chim thiên nga bay,\nThần thông liệng giữa trời;\nChiến thắng ma, ma quân,\nKẻ trí thoát đời này.', 'Swans upon the sun''s path fly,\nthe powerful through space,\nconquering Mara and his host\naway from the world the wise are led.'),
(176, 13, 'Ai vi phạm một pháp,\nAi nói lời vọng ngữ,\nAi bác bỏ đời sau,\nKhông ác nào không làm.', 'For one who falsely speaks,\nwho disregards the Dhamma,\nwho other lives denies:\nno evil this one will not do.');
INSERT INTO `chuathienquang_dhammapada_detail` (`id`, `type`, `content_vi`, `content_en`) VALUES
(177, 13, 'Keo kiết không sanh thiên,\nKẻ ngu ghét bố thí,\nNgười trí thích bố thí,\nÐời sau, được hưởng lạc.', 'To heavenly realms the mean don''t fare,\nfools magnanimity ne''er acclaim,\nbut the one of wisdom rejoices at giving\nand happy will be in future lives.'),
(178, 13, 'Hơn thống lãnh cõi đất,\nHơn được sanh cõi trờì,\nHơn chủ trì vũ trụ,\nQuả Dự Lưu tối thắng.', 'Than o''er the earth sole sovereignty,\nthan going unto heaven,\nthan lordship over all the worlds:\nbetter the Steam-winner''s Fruit.'),
(179, 14, 'Vị chiến thắng không bại,\nVị bước đi trên đời,\nKhông dấu tích chiến thắng,\nPhật giới rộng mênh mông,\nAi dùng chân theo dõi\nBậc không để dấu tích?', 'That Buddha traceless of infinite range\nwhose victory none may e''er undo,\nwhose vanquished follow to no world,\nthen by which track will you trace him?	'),
(180, 14, 'Ai giải tỏa lưới tham,\nÁi phược hết dắt dẫn,\nPhật giới rộng mênh mông,\nAi dùng chân theo dõi\nBậc không để dấu tích?', 'That Buddha traceless of infinite range\nin whom''s no entangling craving\nand no ensnaring not anywhere leading,\nthen by which track will you trace him?	'),
(181, 14, 'Người trí chuyên thiền định,\nThích an tịnh viễn ly,\nChư thiên đều ái kính,\nBậc chánh giác, chánh niệm.', 'E''er intent on concentration,\njoyful in peace of letting go,\nmindful, wise, the perfect Buddhas,\nto even devas they are dear.'),
(182, 14, 'Khó thay, được làm người,\nKhó thay, được sống còn,\nKhó thay, nghe Diệu Pháp,\nKhó thay, Phật ra đời!', 'Human birth is hard to gain,\nhard for mortals is their life,\nto come to Dhamma True is hard,\nrare the Buddhas'' arising.'),
(183, 14, 'Không làm mọi điều ác.\nThành tựu các hạnh lành,\nTâm ý giữ trong sạch,\nChính lời chư Phật dạy.', 'Every evil never doing\nand in wholesomeness increasing\nand one''s heart well-purifying:\nthis is the Buddhas'' Teaching.'),
(184, 14, 'Chư Phật thường giảng dạy;\nNhẫn, khổ hạnh tối thượng;\nNiết bàn quả tối thượng;\nXuất gia không phá người;\nSa môn không hại người.', 'Patience''s the austerity supreme,\n"Nibbana''s supreme" the Buddhas say.\nOne who irks or others harms\nis not ordained or monk become.'),
(185, 14, 'Không phỉ báng, phá hoại,\nHộ trì giới căn bản,\nĂn uống có tiết độ,\nSàng tọa chỗ nhàn tịnh\nChuyên chú tăng thượng tâm,\nChính lời chư Phật dạy.', 'Not reviling, neither harming,\nwell-restrained in Patimokha,\nknowing reason in one''s food,\ndwelling far in solitude,\nand striving in the mind sublime:\nthis is the Buddhas'' Teaching.'),
(186, 14, 'Dầu mưa bằng tiền vàng,\nCác dục khó thỏa mãn.\nDục đắng nhiều ngọt ít,\nBiết vậy là bậc trí.', 'Not by rain of golden coins\nis found desires'' satiety,\ndesires are dukkha, of little joy,\nthus a wise one understands.'),
(187, 14, 'Ðệ tử bậc chánh giác,\nKhông tìm cầu dục lạc,\nDầu là dục chư thiên,\nChỉ ưa thích ái diệt.', 'Even with pleasures heavenly\nthat one finds no delight,\nthe perfect Buddha''s pupil\ndelights in craving''s end.'),
(188, 14, 'Loài người sợ hoảng hốt,\nTìm nhiều chỗ quy y,\nHoặc rừng rậm, núi non,\nHoặc vườn cây, đền tháp.', 'Many a refuge do they seek\non hills, in woods, to sacred trees,\nto monasteries and shrines they go;\nfolk by fear tormented.'),
(189, 14, 'Quy y ấy không ổn,\nKhông quy y tối thượng.\nQuy y các chỗ ấy,\nKhông thoát mọi khổ đau?', 'Such refuge isn''t secure,\nsuch refuge isn''t supreme.\nFrom all dukkha one''s not free\nunto that refuge gone.'),
(190, 14, 'Ai quy y Ðức Phật,\nChánh Pháp và chư Tăng,\nAi dùng chánh tri kiến,\nThấy được bốn Thánh đế.', 'But going for refuge to Buddha,\nto Dhamma and the Sangha too,\none sees with perfect wisdom\nthe tetrad of the Noble Truths:'),
(191, 14, 'Thấy khổ và khổ tập,\nThấy sự khổ vượt qua,\nThấy đường Thánh tám ngành,\nÐưa đến khổ não tận.', 'Dukkha, its causal arising,\nthe overcoming of dukkha,\nand the Eight-fold Path that''s Noble\nleading to dukkha''s allaying.'),
(192, 14, 'Thật quy y an ổn,\nThật quy y tối thượng,\nCó quy y như vậy,\nMới thoát mọi khổ đau.', 'Such refuge is secure,\nsuch refuge is supreme.\nFrom all dukkha one is free\nunto that refuge gone.'),
(193, 14, 'Khó gặp bậc thánh nhơn,\nKhông phải đâu cũng có.\nChỗ nào bậc trí sanh,\nGia đình tất an lạc.', 'Hard to find the pure and noble\nwho isn''t born just anywhere,\nwherever one so wise is born\nthat family thrives happily.'),
(194, 14, 'Vui thay, Phật ra đời!\nVui thay, Pháp được giảng!\nVui thay, Tăng hòa hợp!\nHòa hợp tu, vui thay!', 'Blessed is the birth of Buddhas,\nblest True Dhamma''s Teaching,\nblest the Sangha''s harmony\nand blessed is their striving.'),
(195, 14, 'Cúng dường bậc đáng cúng,\nChư Phật hoặc đệ tử,\nCác bậc vượt hý luận,\nÐoạn diệt mọi sầu bi.', 'Who venerates the venerable\nBuddhas or their disciples,\nhave overcome the manifold,\ngrief and lamentation left.'),
(196, 14, 'Cúng dường bậc như vậy,\nTịch tịnh, không sợ hãi,\nCác công đức như vậy,\nKhông ai ước lường được.', 'They who are "Thus", venerable,\ncool and free from every fear -\nno one is able to calculate\ntheir merit as "just-so-much".'),
(197, 15, 'Vui thay, chúng ta sống,\nKhông hận, giữa hận thù!\nGiữa những người thù hận,\nTa sống, không hận thù!', 'We the unhating live\nhappily midst the haters,\namong the hating humans\nfrom hatred dwell we free.'),
(198, 15, 'Vui thay, chúng ta sống,\nKhông bệnh giữa ốm đau!\nGiữa những người bệnh hoạn,\nTa sống, không ốm đau.', 'We who are healthy live\nhappily midst the unhealthy,\namong unhealthy humans\nfrom ill-health dwell we free.'),
(199, 15, 'Vui thay, chúng ta sống,\nKhông rộn giữa rộn ràng;\nGiữa những người rộn ràng,\nTa sống, không rộn ràng.', 'We the unfrenzied live\nhappily midst the frenzied,\namong the frenzied humans\nfrom frenzy dwell we free.'),
(200, 15, 'Vui thay chúng ta sống,\nKhông gì, gọi của ta.\nTa sẻ hưởng hỷ lạc,\nNhư chư thiên Quang Âm.', 'We for whom there''s nought\nlive indeed so happily,\njoy-sustained we''ll be\nlike resplendent gods.'),
(201, 15, 'Chiến thắng sinh thù oán,\nThất bại chịu khổ đau,\nSống tịch tịnh an lạc.\nBỏ sau mọi thắng bại.', 'Victory gives rise to hate,\nthose defeated lie in pain,\nhappily rest the Peaceful\nsurrendering victory-defeat.'),
(202, 15, 'Lửa nào sánh lửa tham ?\nÁc nào bằng sân hận ?\nKhổ nào sánh khổ uẩn,\nLạc nào bằng tịnh lạc.', 'There''s no fire like lust,\nno evil like aversion,\nno dukkha like the aggregates,\nno higher bliss than Peace.'),
(203, 15, 'Ðói ăn, bệnh tối thượng,\nCác hành, khổ tối thượng,\nHiểu như thực là vậy,\nNiết bàn, lạc tối thượng.', 'Hunger is the greatest ill,\nthe greatest dukkha - conditionedness,\nknowing this reality at it is:\nNibbana bliss supreme.'),
(204, 15, 'Không bệnh, lợi tối thượng,\nBiết đủ, tiền tối thượng.\nThành tín đối với nhau,\nLà bà con tối thượng,\nNiết bàn, lạc tối thượng.', 'Health''s the greatest gain,\ncontentment, best of wealth,\ntrusting''s best of kin,\nNibbana bliss supreme.'),
(205, 15, 'Ðã nếm vị độc cư,\nÐược hưởng vị nhàn tịnh,\nKhông sợ hãi, không ác,\nNếm được vị pháp hỷ.', 'Having drunk of solitude\nand tasted Peace Sublime,\nfree from sorrow, evil-free,\none drinks of Dhamma''s joy.'),
(206, 15, 'Lành thay, thấy thánh nhân,\nSống chung thường hưởng lạc.\nKhông thấy những người ngu,\nThường thường được an lạc.', 'So fair''s the sight of Noble Ones,\never good their company,\nby relating not to fools\never happy one may be.'),
(207, 15, 'Sống chung với người ngu,\nLâu dài bị lo buồn.\nKhổ thay gần người ngu,\nNhư thường sống kẻ thù.\nVui thay, gần người trí,\nNhư chung sống bà con.', 'Who moves among fool''s company\nmust truly grieve for long,\nfor ill the company of fools\nas ever that of foes,\nbut weal''s a wise one''s company\nas meeting of one''s folk.'),
(208, 15, 'Do vậy :\nBậc hiền sĩ, trí tuệ\nBậc nghe nhiều, trì giới,\nBậc tự chế, Thánh nhân;\nHãy gần gũi, thân cận\nThiện nhân, trí giả ấy,\nNhư trăng theo đường sao.', 'Thus go with the steadfast, wise, well-versed,\nfirm of virtue, practice-pure,\nEnnobled "Such", who''s sound, sincere,\nas moon in wake of the Milky Way.'),
(209, 16, 'Tự chuyên, không đáng chuyên\nKhông chuyên, việc đáng chuyên\nBỏ đích, theo hỷ ái,\nGanh tị bậc tự chuyên.', 'One makes an effort where none''s due\nwith nothing done where effort''s due,\none grasps the dear, gives up the Quest\nenvying those who exert themselves.'),
(210, 16, 'Chớ gần gũi người yêu,\nTrọn đời xa kẻ ghét.\nYêu không gặp là khổ,\nOán phải gặp cũng đau.', 'Don''t consort with dear ones\nat any time, nor those not dear,\n''tis dukkha not to see the dear,\n''tis dukkha seeing those not dear.'),
(211, 16, 'Do vậy chớ yêu ai,\nÁi biệt ly là ác;\nNhững ai không yêu ghét,\nKhông thể có buộc ràng.', 'Others then do not make dear\nfor hard''s the parting from them.\nFor whom there is no dear, undear\nin them no bonds are found.'),
(212, 16, 'Do ái sinh sầu ưu,\nDo ái sinh sợ hãi,\nAi thoát khỏi tham ái,\nKhông sầu, đâu sợ hãi?', 'From endearment grief is born,\nfrom endearment fear,\none who is endearment-free\nhas no grief -- how fear?'),
(213, 16, 'Ái luyến sinh sầu ưu,\nÁi luyến sinh sợ hãi.\nAi giải thoát ái luyến\nKhông sầu, đâu sợ hãi?', 'From affection grief is born,\nfrom affection fear,\none who is affection-free\nhas no grief -- how fear?'),
(214, 16, 'Hỷ ái sinh sầu ưu,\nHỷ ái sinh sợ hãi.\nAi giải thoát hỷ ái,\nKhông sầu, đâu sợ hãi?', 'From lustfulness arises grief,\nfrom lustfulness springs fear,\none wholly free of lustfulness\nhas no grief -- how fear?'),
(215, 16, 'Dục ái sinh sầu ưu,\nDục ái sinh sợ hãi,\nAi thoát khỏi dục ái,\nKhông sầu, đâu sợ hãi?', 'From attachment grief is born,\nfrom attachment fear,\none who is attachment-free\nhas no grief -- how fear?'),
(216, 16, 'Tham ái sinh sầu ưu,\nTham ái sinh sợ hãi.\nAi giải thoát tham ái,\nKhông sầu, đâu sợ hãi?', 'Out of craving grief is born,\nout of craving fear,\none fully freed of craving\nhas no grief -- how fear?'),
(217, 16, 'Ðủ giới đức, chánh kiến ;\nTrú pháp, chứng chân lý,\nTự làm công việc mình,\nÐược quần chúng ái kính.', 'Perfect in virtue and insight,\nfirm in Dhamma, knower of Truth,\ndear to the people''s such a one\nwho does what should be done.'),
(218, 16, 'Ước vọng pháp ly ngôn\nÝ cảm xúc thượng quả\nTâm thoát ly các dục,\nXứng gọi bậc Thượng Lưu.', 'One with a wish for the Undeclared,\nwith mind so well-pervaded,\na mind not bound in pleasures of sense,\nan "upstream-goer''s" called.'),
(219, 16, 'Khách lâu ngày ly hương,\nAn toàn từ xa về,\nBà con cùng thân hữu,\nHân hoan đón chào mừng.', 'One who''s long away from home\nreturns in safety from afar,\nthen friends, well-wishers, kinsmen too\nare overjoyed at his return.'),
(220, 16, 'Cũng vậy các phước nghiệp,\nÐón chào người làm lành,\nÐời này đến đời kia.\nNhư thân nhân, đón chào.', 'In the same way, with merit done\nwhen from this world to another gone\nthose merits then receive one there\nas relatives a dear one come.'),
(221, 17, 'Bỏ phẫn nộ, ly mạn,\nVượt qua mọi kiết sử,\nKhông chấp trước danh sắc.\nKhổ không theo vô sản.', 'Anger and pride should one forsake,\nall fetters cast aside,\ndukkha''s none where no desire,\nno binding to body or mind.'),
(222, 17, 'Ai chận được phẫn nộ,\nNhư dừng xe đang lăn,\nTa gọi người đánh xe,\nKẻ khác, cầm cương hờ.', 'Who checks arising anger\nas with chariot away,\nthat one I call a charioteer,\nothers merely grip the reins.'),
(223, 17, 'Lấy không giận thắng giận,\nLấy thiện thắng không thiện,\nLấy thí thắng xan tham,\nLấy chơn thắng hư ngụy.', 'Anger conquer by amity,\nevil conquer with good,\nby giving conquer miserly,\nwith truth the speaker of falsity.'),
(224, 17, 'Nói thật, không phẫn nộ,\nCủa ít, thí người xin,\nNhờ ba việc lành này,\nNgười đến gần thiên giới.', 'Speak truth and be not angry,\nfrom little give to one who asks,\nby these conditions three to go\nunto the presence of the gods.'),
(225, 17, 'Bậc hiền không hại ai,\nThân thường được chế ngự\nÐạt được cảnh bất tử,\nÐến đây, không ưu sầu.', 'Those sages inoffensive\nin body e''er restrained\ngo unto the Deathless State\nwhere gone they grieve no more.'),
(226, 17, 'Những người thường giác tỉnh,\nNgày đêm siêng tu học,\nChuyên tâm hướng Niết bàn,\nMọi lậu hoặc được tiêu.', 'For the ever-vigilant\nwho train by day and night\nupon Nibbana e''er intent\npollutions fade away.'),
(227, 17, '227.A-tu-la, nên biết,\nXưa vậy, nay cũng vậy,\nNgồi im, bị người chê,\nNói nhiều, bị người chê,\nNói vừa phải, bị chê.\nLàm người không bị chê,\nThật khó tìm ở đời.', 'An ancient saying, Atula,\nnot only said today --\n"They are blamed who silent sit,\nwho often speak they too are blamed,\nand blamed are they of measured speech" --\nthere''s none in the world unblamed.'),
(228, 17, 'Xưa, vị lai,và nay,\nÐâu có sự kiện này,\nNgười hoàn toàn bị chê,\nNgười trọn vẹn được khen.', 'There never was, there''ll never be\nnor now is ever found\na person blamed perpetually\nor one who''s wholly praised.'),
(229, 17, 'Sáng sáng, thẩm xét kỹ.\nBậc có trí tán thán.\nBậc trí không tỳ vết,\nÐầy đủ giới định tuệ.', 'But those who are intelligent\npraise one of flawless conduct, sage,\nin wisdom and virtue well-composed,\nhaving observed him day by day.'),
(230, 17, 'Hạnh sáng như vàng ròng,\nAi dám chê vị ấy?\nChư thiên phải khen thưởng,\nPhạm Thiên cũng tán dương.', 'Who''s to blame that one so fine\nas gem from Jambu stream?\nEven the devas that one praise,\nby Brahma too is praised.'),
(231, 17, 'Giữ thân đừng phẩn nộ,\nPhòng thân khéo bảo vệ,\nTừ bỏ thân làm ác,\nVới thân làm hạnh lành.', 'Rough action one should guard against,\nbe with body well-restrained,\nbad bodily conduct having shed\ntrain oneself in good.'),
(232, 17, 'Giữ lời đừng phẩn nộ,\nPhòng lời khéo bảo vệ,\nTừ bỏ lời thô ác,\nVới lời, nói điều lành.', 'Rough speaking one should guard against,\nbe in speaking well-restrained,\nbad verbal conduct having shed\ntrain oneself in good.'),
(233, 17, 'Giữ ý đừng phẩn nộ,\nPhòng ý, khéo bảo vệ,\nTừ bỏ ý nghĩ ác,\nVới ý, nghĩ hạnh lành.', 'Rough thinking one should guard against,\nbe in thinking well-restrained,\nbad mental conduct having shed\ntrain oneself in good.'),
(234, 17, 'Bậc trí bảo vệ thân,\nBảo vệ luôn lời nói\nBảo vệ cả tâm tư,\nBa nghiệp khéo bảo vệ.', 'Restrained in body are the wise,\nin speech as well they are restrained,\nlikewise are they restrained in mind,\nthey''re perfectly restrained.'),
(235, 18, 'Ngươi nay giống lá héo,\nDiêm sứ đang chờ ngươi,\nNgươi đứng trước cửa chết,\nÐường trường thiếu tư lương.', 'Now a withered leaf you are\nand now Death''s men draw near,\nnow you stand at the parting gates\nbut waybread you have none.'),
(236, 18, 'Hãy tự làm hòn đảo,\nTinh cần gấp, sáng suốt.\nTrừ cấu uế, thanh tịnh,\nÐến Thánh địa chư Thiên.', 'Make an island of yourself,\nquickly strive and wise become,\nfreed from stain and passionless\nto go to the pure Abodes.'),
(237, 18, 'Ðời ngươi nay sắp tàn,\nTiến gần đến Diêm Vương.\nGiữa đường không nơi nghỉ,\nÐường trường thiếu tư lương.', 'Even now the end draws near,\nto the presence of death you''ve fared.\nAlong the path''s no place for rest\nand waybread you have none.'),
(238, 18, 'Hãy tự làm hòn đảo,\nTinh cần gấp, sáng suốt.\nTrừ cấu uế, thanh tịnh,\nChẳng trở lại sanh già.', 'Make an island of yourself,\nquickly strive and wise become,\nfreed from stain and passionless\nyou''ll not return, take flesh, decay.'),
(239, 18, 'Bậc trí theo tuần tự.\nTừng sát na trừ dần\nNhư thợ vàng lọc bụi\nTrừ cấu uế nơi mình.', 'Little by little, time after time,\nsuccessively then let the sage\nblow away all blemishes\njust as a smith with silver.'),
(240, 18, 'Như sét từ sắt sinh,\nSắt sanh lại ăn sắt,\nCũng vậy, quá lợi dưỡng\nTự nghiệp dẫn cõi ác.', 'As rust arisen out of iron\nitself that iron eats away,\nso kammas done beyond what''s wise\nlead to a state of woe.	'),
(241, 18, 'Không tụng làm nhớp kinh,\nKhông đứng dậy, bẩn nhà,\nBiếng nhác làm nhơ sắc,\nPhóng dật uế người canh.', 'For oral tradition, non-recitation,\nin household life, non-exertion,\nthe fair of form when slovenly,\na sentry''s sloth: all blemishes.'),
(242, 18, 'Tà hạnh nhơ đàn bà,\nXan tham nhớp kẻ thí,\nÁc pháp là vết nhơ,\nÐời này và đời sau.', 'In woman, conduct culpable,\nwith givers, avariciousness,\nall blemishes these evil things\nin this world or the next.'),
(243, 18, 'Trong hàng cầu uế ấy,\nVô minh, nhơ tối thượng,\nÐoạn nhơ ấy, tỷ kheo,\nThành bậc không uế nhiễm.', 'More basic than these blemishes\nis ignorance, the worst of all.\nAbandoning this blemish then,\nbe free of blemish, monks!'),
(244, 18, 'Dễ thay, sống không hổ\nSống lỗ mãng như quạ,\nSống công kích huyênh hoang,\nSống liều lĩnh, nhiễm ô.', 'Easy the life for a shameless one\nwho bold and forward as a crow,\nis slanderer and braggart too:\nthis one''s completely stained.'),
(245, 18, 'Khó thay, sống xấu hổ,\nThường thường cầu thanh tịnh.\nSống vô tư, khiêm tốn,\nTrong sạch và sáng suốt.', 'But hard the life of a modest one\nwho always seeks for purity,\nwho''s cheerful though no braggart,\nclean-living and discerning.'),
(246, 18, 'Ai ở đời sát sinh,\nNói láo không chân thật,\nỞ đời lấy không cho,\nQua lại với vợ người.', 'In the world who life destroys,\nwho words of falsity speaks,\nwho takes what is not freely given\nor to another''s partner goes.'),
(247, 18, 'Uống rượu men, rượu nấu,\nNgười sống đam mê vậy,\nChính ngay tại đời này,\nTự đào bới gốc mình.', 'Or has distilled, fermented drinks:\nWho with abandon follows these\nextirpates the root of self\neven here in this very world.'),
(248, 18, 'Vậy người, hãy nên biết,\nKhông chế ngự là ác,\nChớ để tham phi pháp,\nLàm người đau khổ dài.', 'Therefore friend remember this;\nHard to restrain are evil acts,\ndon''t let greed and wickedness\ndown drag you long in dukkha.'),
(249, 18, 'Do tín tâm, hỷ tâm\nLoài người mới bố thí.\nỞ đây ai bất mãn\nNgười khác được ăn uống,\nNgười ấy ngày hoặc đêm,\nKhông đạt được tâm tịnh?', 'People give as they have faith,\nas they are bright with joyfulness.\nWho''s troubled over gifts received,\nthe food and drink that others get,\nneither in daytime nor by night\nwill come to a collected mind.'),
(250, 18, 'Ai cắt được, phá được,\nTận gốc nhổ tâm ấy.\nNgười ấy ngày hoặc đêm,\nTất đến được tâm định.', 'But who has severed envy''s mind,\nuprooted it, destroyed entire,\nindeed in daytime and by night\nwill come to a collected mind.'),
(251, 18, 'Lửa nào bằng lửa tham!\nChấp nào bằng sân hận!\nLưới nào bằng lưới si!\nSông nào bằng sông ái!', 'There is no fire like lust,\nnought seizes like aversion,\nunequalled is delusion''s net,\nno river''s like to craving.'),
(252, 18, 'Dễ thay thấy lỗi người,\nLỗi mình thấy mới khó.\nLỗi người ta phanh tìm,\nNhư sàng trấu trong gạo.\nCòn lỗi mình, che đậy,\nNhư kẻ gian dấu bài.', 'Other''s faults are easy to see\nyet hard it is to see one''s own,\nand so one winnows just like chaff\nthe faults of other people, while\nhiding away those of one''s own\nas crafty cheat the losing throw.'),
(253, 18, 'Ai thấy lỗi của người,\nThường sanh lòng chỉ trích,\nNgười ấy lậu hoặc tăng,\nRất xa lậu hoặc diệt.', 'Who''s always seeing other''s faults,\ntaking offence, censorious,\npollutions spread for such a one\nwho''s far from their exhaustion.	'),
(254, 18, 'Hư không, không dấu chân,\nNgoài đây, không sa môn,\nChúng sanh thích hý luận,\nNhư Lai, hý luận trừ.', 'In skies above there is no path,\nno peaceful one''s without,\nin manifoldness do folk delight,\nTathagatas are manifold-free.'),
(255, 18, 'Hư không, không dấu chân,\nNgoài đây, không sa môn.\nCác hành không thường trú,\nChư Phật không dao động.', 'In skies above there is no path,\nno peaceful one''s without,\nnothing conditioned ever lasts,\nno Buddha''s ever shaken.	'),
(256, 19, 'Ngươi đâu phải pháp trụ,\nXử sự quá chuyên chế,\nBậc trí cần phân biệt\nCả hai chánh và tà!', 'Whoever judges hastily\ndoes Dhamma not uphold,\na wise one should investigate\ntruth and untruth both.	'),
(257, 19, 'Không chuyên chế, đúng pháp,\nCông bằng, dắt dẫn người,\nBậc trí sống đúng pháp,\nThật xứng danh Pháp trụ.', 'Who others guides impartially\nwith carefulness, with Dhamma,\nthat wise one Dhamma guards,\na "Dhamma-holder''s" called.	'),
(258, 19, 'Không phải vì nói nhiều,\nMới xứng danh bậc trí.\nAn ổn, không oán sợ.\nThật đáng gọi bậc Trí.', 'Just because articulate\none''s not thereby wise,\nhateless, fearless and secure,\na "wise one" thus is called.'),
(259, 19, 'Không phải vì nói nhiều,\nMới xứng danh trì pháp,\nNhững ai tuy nghe ít,\nNhưng thân hành đúng pháp,\nKhông phóng túng chánh pháp,\nMới xứng danh trì pháp.', 'Just because articulate\none''s not skilled in Dhamma;\nbut one who''s heard even little\nand Dhamma in the body sees,\nthat one is skilled indeed,\nnot heedless of the Dhamma.'),
(260, 19, 'Không phải là trưởng lão,\nNếu cho có bạc đầu.\nNgười chỉ tuổi tác cao,\nÐược gọi là "Lão ngu".', 'A man is not an Elder\nthough his head be grey,\nhe''s just fully ripe in years,\n"aged-in-vain" he''s called.	'),
(261, 19, 'Ai chân thật, đúng pháp,\nKhông hại, biết chế phục,\nBậc trí không cấu uế,\nMới xứng danh "Trưởng Lão".', 'In whom is truth and Dhamma too,\nharmlessness, restraint, control,\nhe''s steadfast, rid of blemishes,\nan "Elder" he is called.	'),
(262, 19, 'Không phải nói lưu loát,\nKhông phải sắc mặt đẹp,\nThành được người lương thiện,\nNếu ganh, tham, dối trá.', 'Not by eloquence alone\nor by lovely countenance\nis a person beautiful\nif jealous, boastful, mean.	'),
(263, 19, 'Ai cắt được, phá được\nTận gốc nhổ tâm ấy\nNgười trí ấy diệt sân,\nÐược gọi người hiền thiện.', 'But "beautiful" is called that one\nin whom these are completely shed,\nuprooted, utterly destroyed,\na wise one purged of hate.	'),
(264, 19, 'Ðầu trọc, không Sa môn\nNếu phóng túng, nói láo.\nAi còn đầy dục tham,\nSao được gọi Sa môn ?', 'By shaven head no Samana\nif with deceit, no discipline.\nEngrossed in greed and selfishness\nhow shall he be a Samana?	'),
(265, 19, 'Ai lắng dịu hoàn toàn,\nCác điều ác lớn nhỏ,\nVì lắng dịu ác pháp,\nÐược gọi là Sa môn.', 'All evils altogether he\nsubdues both fine and gross.\nHaving subdued al evil he\nindeed is called a "Samana".	'),
(266, 19, 'Chỉ khất thực nhờ người,\nÐâu phải là tỷ kheo!\nPhải theo pháp toàn diện,\nKhất sĩ không, không đủ.', 'Though one begs from others\nby this alone''s no bhikkhu.\nNot just by this a bhikkhu\nbut from all Dhamma doing.	'),
(267, 19, 'Ai vượt qua thiện ác,\nChuyên sống đời Phạm Hạnh,\nSống thẩm sát ở đời,\nMới xứng danh tỷ kheo.', 'Who both good and evil deeds\nhas gone beyond with holy life,\nhaving discerned the world he fares\nand "Bhikkhu" he is called.	'),
(268, 19, 'Im lặng nhưng ngu si,\nÐâu được gọi ẩn sĩ?\nNhư người cầm cán cân,\nBậc trí chọn điều lành.', 'By silence one is not a sage\nif confused and foolish,\nbut one who''s wise, as if with scales\nweighs, adopts what''s good.	'),
(269, 19, 'Từ bỏ các ác pháp,\nMới thật là ẩn sĩ.\nAi thật hiểu hai đời\nMới được gọi ẩn sĩ.', 'Shunning evil utterly\none is a sage, by that a sage.\nWhoever both worlds knows\nfor that one''s called a "Sage".	'),
(270, 19, 'Còn sát hại sinh linh,\nÐâu được gọi Hiền thánh.\nKhông hại mọi hữu tình,\nMới được gọi Hiền thánh.', 'By harming living beings\none is not a "Noble" man,\nby lack of harm to all that live\none is called a "Nobel One".	'),
(271, 19, 'Chẳng phải chỉ giới cấm\nCũng không phải học nhiều,\nChẳng phải chứng thiền định,\nSống thanh vắng một mình.', 'Not by vows and rituals\nor again by learning much\nor by meditative calm\nor by life in solitude.	'),
(272, 19, '"Ta hưởng an ổn lạc,\nPhàm phu chưa hưởng được":\nTỷ kheo, chớ tự tin\nKhi lậu hoặc chưa diệt.', 'Should you, O bhikkhu, be content,\n"I''ve touched the bliss of letting go\nnot enjoyed by common folk",\nthough you''ve not gained pollution''s end.	'),
(273, 20, 'Tám chánh, đường thù thắng,\nBốn câu, lý thù thắng,\nLy tham, pháp thù thắng,\nGiữa các loài hai chân,\nPháp nhãn, người thù thắng.', 'Of paths the Eight-fold is the best,\nof truths the statement Four,\nthe passionless of teachings best,\nof humankind the Seer.'),
(274, 20, 'Ðường này, không đường khác\nÐưa đến kiến thanh tịnh.\nNếu ngươi theo đường này,\nMa quân sẽ mê loạn.', 'This is the Path, no other''s there\nfor purity of insight,\nenter then upon this path\nbemusing Mara utterly.'),
(275, 20, 'Nếu ngươi theo đường này,\nÐau khổ được đoạn tận.\nTa dạy ngươi con đường.\nVới trí, gai chướng diệt.', 'Entered then upon this Path\nyou''ll make an end of dukkha.\nFreed in knowledge from suffering''s stings\nthe Path''s proclaimed by me.'),
(276, 20, 'Ngươi hãy nhiệt tình làm,\nNhư Lai chỉ thuyết dạy.\nNgười hành trì thiền định\nThoát trói buộc ác Ma.', 'Buddhas just proclaim the Path\nbut you''re the ones to strive.\nContemplatives who tread the Path\nare freed from Mara''s bonds'),
(277, 20, 'Tất cả hành vô thường\nVới Tuệ, quán thấy vậy\nÐau khổ được nhàm chán:\nChính con đường thanh tịnh.', 'When with wisdom one discerns\ntransience of conditioned things\none wearily from dukkha turns\ntreading the Path to purity.'),
(278, 20, 'Tất cả hành khổ đau\nVới Tuệ, quán thấy vậy,\nÐau khổ được nhàm chán:\nChính con đường thanh tịnh.', 'When with wisdom one discerns\nthe dukkha of conditioned things\none wearily from dukkha turns\ntreading the Path to purity.'),
(279, 20, 'Tất cả pháp vô ngã,\nVới Tuệ, quán thấy vậy,\nÐau khổ được nhàm chán:\nChính con đường thanh tịnh.', 'When with wisdom one discerns\nall knowables are not a self\none wearily from dukkha turns\ntreading the Path to purity.'),
(280, 20, 'Khi cần, không nỗ lực,\nTuy trẻ mạnh, nhưng lười\nChí nhu nhược, biếng nhác.\nVới trí tuệ thụ động,\nSao tìm được chánh đạo?', 'Though time to strive, not striving,\nwhile young and strong yet indeed,\nweak-minded and irresolute:\none finds not wisdom''s way.'),
(281, 20, 'Lời nói được thận trọng,\nTâm tư khéo hộ phòng,\nThân chớ làm điều ác,\nHãy giữ ba nghiệp tịnh,\nChứng đạo thánh nhân dạy.', 'In speech ever watchful with mind well-restrained\nnever with body do unwholesomeness.\nSo should one purify these three kamma-paths\nwinning to the Way made known by the Seers.'),
(282, 20, 'Tu Thiền, trí tuệ sanh,\nBỏ Thiền, trí tuệ diệt.\nBiết con đường hai ngả\nÐưa đến hữu, phi hữu,\nHãy tự mình nỗ lực,\nKhiến trí tuệ tăng trưởng.', 'From endeavour wisdom springs,\nlacking effort wisdom wanes:\nhaving known this two-fold path\neither to progress or decline\nso should one exhort oneself\nthat wisdom may increase.'),
(283, 20, 'Ðốn rừng không đốn cây\nTừ rừng, sinh sợ hãi;\nÐốn rừng và ái dục,\nTỷ kheo, hãy tịch mịch.', 'The wood cut down but not a tree\nsince it''s from wood that fear is born.\nHaving cut wood and woodedness\nO bhikkhus be without a wood.'),
(284, 20, 'Khi nào chưa cắt tiệt,\nÁi dục giữa gái trai,\nTâm ý vẫn buộc ràng,\nNhư bò con vú mẹ.', 'As long indeed as woodedness\nof man to women is not cut\nso long in bondage is one''s mind\nas milch-calf to the mother cow.'),
(285, 20, 'Tự cắt dây ái dục,\nNhư tay bẻ sen thu,\nHãy tu đạo tịch tịnh,\nNiết bàn, Thiện Thệ dạy.', 'Cut off affection for oneself\nas a hand a lily in the Fall.\nCultivate this peaceful Path,\nNibbana by the Buddha taught.'),
(286, 20, 'Mùa mưa ta ở đây\nÐông, hạ cũng ở đây,\nNgười ngu tâm tưởng vậy,\nKhông tự giác hiểm nguy.', 'Here shall I spend the Rains,\nhere the Winter, here the Summer.\nThus speculates the fool,\nthe danger he knows not.'),
(287, 20, 'Người tâm ý đắm say\nCon cái và súc vật,\nTử thần bắt người ấy,\nNhư lụt trôi hàng ngủ.', 'For one who has a clinging mind\nand finds delight in babes and herds\nDeath does seize and carry away\nas great flood a sleeping village.'),
(288, 20, 'Một khi tử thần đến,\nKhông có con che chở,\nKhông cha, không bà con,\nKhông thân thích che chở.', 'No sons are there for shelter\nnor father nor related folk,\none by the Death-king seized upon\nin kin no shelter finds.	'),
(289, 20, 'Biết rõ ý nghĩa này,\nBậc trí lo trì giới,\nMau lẹ làm thanh tịnh,\nCon đường đến Niết bàn.', 'Having understood this fact\nthe wise by virtue well-restrained\nswiftly then should clear the Path\nleading to Nibbana.	'),
(290, 21, 'Nhờ từ bỏ lạc nhỏ,\nThấy được lạc lớn hơn,\nBậc trí bỏ lạc nhỏ,\nThấy được lạc lớn hơn.', 'If one should see great happiness\nin giving up small happiness\none wise the lesser would renounce\nthe greater full-discerning.'),
(291, 21, 'Gieo khổ đau cho người,\nMong cầu lạc cho mình,\nBị hận thù buộc ràng\nKhông sao thoát hận thù.', 'Who so for self wants happiness\nby causing others pain,\nentangled in anger''s tangles\none''s from anger never free.	'),
(292, 21, 'Việc đáng làm, không làm,\nKhông đáng làm, lại làm,\nNgười ngạo mạn, phóng dật,\nLậu hoặc ắt tăng trưởng.', 'What should be done is left undone\nand done is what should not be done,\never the pollutions grow\nof those ones proud and heedless.'),
(293, 21, 'Người siêng năng cần mẫn,\nThường thường quán thân niệm,\nKhông làm việc không đáng,\nGắng làm việc đáng làm,\nNgười tư niệm giác tỉnh,\nLậu hoặc được tiêu trừ.', 'But for who always practice well\nbodily mindfulness,\ndo never what should not be done,\nand ever do what should be done\nfor mindful ones, the full-aware,\npollutions fade away.'),
(294, 21, 'Sau khi giết mẹ cha\nGiết hai vua Sát lỵ\nGiết vương quốc, quần thần\nVô ưu, Phạm chí sống.', 'One''s mother and father having slain\nand then two warrior kings,\na realm as well its treasurer,\none goes immune, a Brahmin True.'),
(295, 21, 'Sau khi giết mẹ cha,\nHai vua Bà la môn\nGiết hổ tướng thứ năm\nVô ưu, Phạm chí sống.', 'One''s mother and father having slain\nand then two learned kings,\nas well the fifth, a tiger fierce,\none goes immune, a Brahmin True.'),
(296, 21, 'Ðệ tử Gotama,\nLuôn luôn tự tỉnh giác\nVô luận ngày hay đêm,\nThường tưởng niệm Phật Ðà.', 'Well awakened, they''re awake\never the Buddha''s pupils\nwho constantly by day, by night\nare mindful of the Buddha.'),
(297, 21, 'Ðệ tử Gotama,\nLuôn luôn tự tỉnh giác,\nVô luận ngày hay đêm,\nThường tưởng niệm Chánh Pháp.', 'Well awakened, they''re awake\never the Buddha''s pupils\nwho constantly by day, by night\nare mindful of the Dhamma.'),
(298, 21, 'Ðệ tử Gotama,\nLuôn luôn tự tỉnh giác,\nVô luận ngày hay đêm,\nThường tưởng niệm Tăng già.', 'Well awakened, they''re awake\never the Buddha''s pupils\nwho constantly by day, by night\nare mindful of the Sangha.'),
(299, 21, 'Ðệ tử Gotama,\nLuôn luôn tự tỉnh giác,\nVô luận ngày hay đêm,\nThường tưởng niệm sắc thân.', 'Well awakened, they''re awake\never the Buddha''s pupils\nwho constantly by day, by night\nare mindful of the body.'),
(300, 21, 'Ðệ tử Gotama,\nLuôn luôn tự tỉnh giác,\nVô luận ngày hay đêm,\nÝ vui niềm bất hại.', 'Well awakened, they''re awake\never the Buddha''s pupils\nwho constantly by day, by night\nin harmlessness delight.'),
(301, 21, 'Ðệ tử Gotama,\nLuôn luôn tự tỉnh giác,\nVô luận ngày hay đêm,\nÝ vui tu thiền quán.', 'Well awakened, they''re awake\never the Buddha''s pupils\nwho constantly by day, by night\nin meditation take delight.'),
(302, 21, 'Vui hạnh xuất gia khó,\nTại gia sinh hoạt khó,\nSống bạn không đồng, khổ,\nTrôi lăn luân hồi, khổ.\nVậy chớ sống luân hồi,\nChớ chạy theo đau khổ.', 'Hard''s the going-forth, hard to delight in it,\nhard the household life and dukkha is it too.\nDukkha''s to dwell with those dissimilar\nand dukkha befalls the wanderer.\nBe therefore not a wanderer,\nnot one whom dukkha befalls.'),
(303, 21, 'Tín tâm, sống giới hạnh\nÐủ danh xưng tài sản,\nChỗ nào người ấy đến,\nChỗ ấy được cung kính.', 'Who''s full of faith and virtue,\nof substance, high repute,\nis honoured everywhere,\nwherever that one goes.'),
(304, 21, 'Người lành dầu ở xa\nSáng tỏ như núi tuyết,\nNgười ác dầu ở gần\nNhư tên bắn đêm đen.', 'Afar the true are manifest\nlike Himalayan range,\nyet even here the false aren''t seen,\nthey''re arrows shot by night.'),
(305, 21, 'Ai ngồi nằm một mình,\nÐộc hành không buồn chán,\nTự điều phục một mình\nSống thoải mái rừng sâu.', 'Alone one sits, alone one lies,\nalone one walks unweariedly,\nin solitude one tames oneself\nso in the woods will one delight.'),
(306, 22, 'Nói láo đọa địa ngục\nCó làm nói không làm,\nCả hai chết đồng đẳng,\nLàm người, nghiệp hạ liệt.', 'With one denying truth there goes to hell\nthat one who having done says "I did not".\nBoth of them are making kammas base\nare equal after death.'),
(307, 22, 'Nhiều người khoác cà sa,\nÁc hạnh không nhiếp phục.\nNgười ác, do ác hạnh,\nPhải sanh cõi Ðịa ngục.', 'Many who wear the yellow robe\nare unrestrained in evil things,\nthese evil ones by evil deeds,\nin hell do they arise.'),
(308, 22, 'Tốt hơn nuốt hòn sắt\nCháy đỏ như than hồng,\nHơn ác giới, buông lung\nĂn đồ ăn quốc độ.', 'Better to eat a ball of iron\nglowing as flame of fire\nthan one should eat country''s alms\nimmoral and unrestrained.'),
(309, 22, 'Bốn nạn chờ đợi người,\nPhóng dật theo vợ người ;\nMắc họa, ngủ không yên,\nBị chê là thứ ba,\nÐọa địa ngục, thứ bốn.', 'Four things befall that heedless one\nsleeping with one who''s wed:\ndemerit gained but not good sleep,\nthird is blame while fourth is hell.'),
(310, 22, 'Mắc họa, đọa ác thú,\nBị hoảng sợ, ít vui.\nQuốc vương phạt trọng hình.\nVậy chớ theo vợ người.', 'Demerit''s gained and evil birth,\nscared man and women -- brief their joy,\nthe king decrees a heavy doom:\nso none should sleep with one who''s wed.'),
(311, 22, 'Như cỏ sa vụng nắm,\nTất bị họa đứt tay.\nHạnh sa môn tà vạy,\nTất bị đọa địa ngục.', 'As blady grass when wrongly grasped\nthe hand does lacerate\nso a mishandled monastic life\ndrags one off to hell.'),
(312, 22, 'Sống phóng đãng buông lung,\nTheo giới cấm ô nhiễm,\nSống phạm hạnh đáng nghi\nSao chứng được quả lớn.', 'Whatever of kammas slack,\nwhatever of vows corrupt,\na faltering in the holy life\nnever brings ample fruit.'),
(313, 22, 'Cần phải làm, nên làm\nLàm cùng tận khả năng\nXuất gia sống phóng đãng,\nChỉ tăng loạn bụi đời.', 'If there''s aught that should be done\nlet it be done then steadily,\nin truth a slack monastic life\nall the more stirs up the dust.'),
(314, 22, 'Ác hạnh không nên làm,\nLàm xong, chịu khổ lụy,\nThiện hạnh, ắt nên làm,\nLàm xong, không ăn năn.', 'Better an evil deed not done\nfor misdeed later on torments.\nBetter done is deed that''s good,\nwhich done, does not torment.'),
(315, 22, 'Như thành ở biên thùy,\nTrong ngoài điều phòng hộ\nCũng vậy, phòng hộ mình,\nSát na chớ buông lung.\nGiây phút qua, sầu muộn.\nKhi rơi vào địa ngục.', 'Even as a border town\nguarded within and without,\nso should you protect yourselves.\nDo not let this moment pass\nfor when this moment''s gone they grieve\nsending themselves to hell.	'),
(316, 22, 'Không đáng hổ, lại hổ.\nViệc đáng hổ, lại không.\nDo chấp nhận tà kiến,\nChúng sanh đi ác thú.', 'They are ashamed where shame is not\nbut where is shame are not ashamed\nso by embracing evil views\nbeings go to an evil birth.	'),
(317, 22, 'Không đáng sợ, lại sợ,\nÐáng sợ, lại thấy không,\nDo chấp nhận tà kiến.\nChúng sanh đi ác thú.', 'They are afraid where fear is not\nbut where is fear are unafraid,\nso by embracing evil views\nbeings go to an evil birth.'),
(318, 22, 'Không lỗi, lại thấy lỗi,\nCó lỗi, lại thấy không,\nDo chấp nhận tà kiến,\nChúng sanh đi ác thú.', 'Faults they see where fault is not\nbut where is fault they see it not,\nso by embracing evil views\nbeings go to an evil birth.'),
(319, 22, 'Có lỗi, biết có lỗi,\nKhông lỗi, biết là không,\nDo chấp nhận chánh kiến,\nChúng sanh đi cõi lành.', 'A fault they understand as such,\nthey know as well where fault is not,\nso by embracing righteous views\nbeings go to a happy rebirth.'),
(320, 23, 'Ta như voi giữa trận\nHứng chịu cung tên rơi,\nChịu đựng mọi phỉ báng.\nÁc giới rất nhiều người.', 'Many folks are ill-behaved\nbut I shall endure abuse\nas elephant in battlefield\narrows shot from a bow.'),
(321, 23, 'Voi luyện, đưa dự hội,\nNgựa luyện, được vua cưỡi,\nNgười luyện, bậc tối thượng\nChịu đựng mọi phỉ báng.', 'The tusker tamed they lead in crowds,\nthe king he mounts the tamed,\nnoblest of humans are the tamed\nwho can endure abuse.'),
(322, 23, 'Tốt thay, con la thuần,\nThuần chủng loài ngựa Xinh.\nÐại tượng, voi có ngà.\nTự điều mới tối thượng.', 'Excellent are mules when tamed\nand thoroughbred from Sindh,\nnoble the elephant of state,\nbetter still one tamed of self.	'),
(323, 23, 'Chẳng phải loài cưỡi ấy,\nÐưa người đến Niết bàn,\nChỉ có người tự điều,\nÐến đích nhờ điều phục.', 'Surely not on mounts like these\none goes the Unfrequented Way\nas one by self well-tamed\nis tamed and by the taming goes.'),
(324, 23, 'Con voi tên Tài Hộ,\nPhát dục, khó điều phục,\nTrói buộc, không ăn uống.\nVoi nhớ đến rừng voi.', 'Hard to check the tusker Dhanapala,\nin rut with temple running pungently,\nbound, e''en a morsel he''ll not eat\nfor he recalls the elephant-forest longingly.'),
(325, 23, 'Người ưa ngủ, ăn lớn\nNằm lăn lóc qua lại,\nChẳng khác heo no bụng,\nKẻ ngu nhập thai mãi.', 'A sluggard stupid, steeped in gluttony,\nwho''s sleep-engrossed, who wallows as he lies,\nlike a great porker stuffed, engorged with swill,\ncomes ever and again into a womb.'),
(326, 23, 'Trước tâm này buông lung,\nChạy theo ái, dục, lạc.\nNay Ta chánh chế ngự,\nNhư cầm móc điều voi.', 'Formerly this wandering mind wandered\nwhere it wished, where whim, where pleasure led.\nWisely this day I will restrain it\nas trainer with hook an elephant in rut.'),
(327, 23, 'Hãy vui không phóng dật,\nKhéo phòng hộ tâm ý.\nKéo mình khỏi ác đạo,\nNhư voi bị sa lầy.', 'Do you delight in heedfulness\nand guard your own mind well!\nDraw yourselves from the evil way\nas would elephant sunk in slough.'),
(328, 23, 'Nếu được bạn hiền trí\nÐáng sống chung, hạnh lành,\nNhiếp phục mọi hiểm nguy\nHoan hỷ sống chánh niệm.', 'If for practice one finds a friend\nprudent, well-behaved and wise,\nmindful, joyful, live with him\nall troubles overcoming.'),
(329, 23, 'Không gặp bạn hiền trí.\nÐáng sống chung, hạnh lành\nNhư vua bỏ nước bại,\nHãy sống riêng cô độc,\nNhư voi sống rừng voi.', 'If for practice one finds no friend\nprudent, well-behaved and wise,\nlike king be leaving conquered land,\nfare as lone elephant in the wilds.'),
(330, 23, 'Tốt hơn sống một mình,\nKhông người ngu kết bạn.\nÐộc thân, không ác hạnh\nSống vô tư vô lự,\nNhư voi sống rừng voi.', 'Better it is to live alone\nfor with a fool''s no fellowship,\nno evil do, be free of care,\nfare as lone elephant in the wilds.	'),
(331, 23, 'Vui thay, bạn lúc cần!\nVui thay, sống biết đủ,\nVui thay, chết có đức!\nVui thay, mọi khổ đoạn.', 'Blest to have friends when one''s in need,\nblest contentment with whatever is,\nblessed is merit when life''s at an end,\nabandoning all dukkha is blessedness.'),
(332, 23, 'Vui thay, hiếu kính mẹ,\nVui thay, hiếu kính cha,\nVui thay, kính Sa môn,\nVui thay, kính Hiền Thánh.', 'Respect for one''s mother brings happiness here\nas well as respect for one''s father.\nHere happiness comes from respecting the monks\nand those of virtue excellent.'),
(333, 23, 'Vui thay, già có giới!\nVui thay, tín an trú!\nVui thay, được trí tuệ,\nVui thay, ác không làm.', 'Blest is virtue till life''s end\nand blest the faith standing firm,\nblest the attainment of wisdom\nand blest the non-doing of evils.'),
(334, 24, 'Người sống đời phóng dật,\nÁi tăng như dây leo.\nNhảy đời này đời khác,\nNhư vượn tham quả rừng.', 'As creeping ivy craving grows\nin one living carelessly.\nLike this, one leaps from life to life\nas ape in the forest seeking fruit.'),
(335, 24, 'Ai sống trong đời này,\nBị ái dục buộc ràng\nSầu khổ sẽ tăng trưởng,\nNhư cỏ Bi gặp mưa.', 'Whomsoever in this world\nthis wretched clinging craving routs\nfor such a one do sorrows grow\nas grass well-soaked with rain.'),
(336, 24, 'Ai sống trong đời này\nÁi dục được hàng phục\nSầu rơi khỏi người ấy\nNhư giọt nước lá sen.', 'But whoever in this world\nrouts wretched craving hard to quell,\nfrom such a one do sorrows fall\nlike water drops from lotus leaf.'),
(337, 24, 'Ðây điều lành Ta dạy,\nCác người tụ họp đây,\nHãy nhổ tận gốc ái\nNhư nhổ gốc cỏ Bi.\nChớ để ma phá hoại,\nNhư giòng nước cỏ lau.', 'Prosperity to you, I say,\nto all assembled here!\nWhen needing grass''s fragrant root\nso craving extirpate.\nDon''t let Mara break you again,\nagain as a torrent a reed!'),
(338, 24, 'Như cây bị chặt đốn,\nGốc chưa hại vẫn bền\nÁi tùy miên chưa nhổ,\nKhổ này vẫn sanh hoài.', 'As tree though felled shoots up again\nit its roots are safe and firm\nso this dukkha grows again\nwhile latent craving''s unremoved.'),
(339, 24, 'Ba mươi sáu dòng ái,\nTrôi người đến khả ái.\nCác tư tưởng tham ái.\nCuốn trôi người tà kiến.', 'For whom the six and thirty streams\nso forceful flow to seemings sweet\nfloods of thought that spring from lust\nsweep off such wrong viewholder.'),
(340, 24, 'Dòng ái dục chảy khắp,\nNhư giây leo mọc tràn,\nThấy giây leo vừa sanh,\nVới tuệ, hãy đoạn gốc.', 'Everywhere these streams are swirling,\nup-bursting creepers rooted firm.\nSeeing the craving-creeper there\nwith wisdom cut its root!'),
(341, 24, 'Người đời nhớ ái dục,\nƯa thích các hỷ lạc.\nTuy mong cầu an lạc,\nChúng vẫn phải sanh già.', 'To beings there are pleasures streaming\nsticky with desire,\nsteeped in comfort, happiness seeking,\nsuch ones do come to birth, decay.'),
(342, 24, 'Người bị ái buộc ràng,\nVùng vẫy và hoảng sợ,\nNhư thỏ bị sa lưới.\nChúng sanh ái trói buộc,\nChịu khổ đau dài dài.', 'Who follow craving are assailed,\nthey tremble as the hare ensnared,\nheld fast by fetter and by bonds\nso long they come to dukkha again.'),
(343, 24, 'Người bị ái buộc ràng,\nVùng vẫy và hoảng sợ,\nNhư thỏ bị sa lưới.\nDo vậy vị tỷ kheo,\nMong cầu mình ly tham\nNên nhiếp phục ái dục.', 'Who follow craving are assailed,\nthey tremble as the hare ensnared,\nso let a bhikkhu craving quell\nwhose aim is passionlessness.'),
(344, 24, 'Lìa rừng lại hướng rừng\nThoát rừng chạy theo rừng.\nNên xem người như vậy,\nÐược thoát khỏi buộc ràng.\nLại chạy theo ràng buộc.', 'Who without woodness inclines to the wood.\nfree in the wood to woodness returns.\nDo now regard that person well\nwho free returns to fetter.'),
(345, 24, 'Sắt, cây, gai trói buộc\nNgười trí xem chưa bền.\nTham châu báu, trang sức\nTham vọng vợ và con.', 'Neither of iron nor wood nor hemp\nis bond so strong, proclaim the wise,\nas passion''s yearn for sons, for wives,\nfor gems and ornaments.'),
(346, 24, 'Người có trí nói rằng:\nTrói buộc này thật bền.\nTrì kéo xuống, lún xuống,\nNhưng thật sự khó thoát.\nNgười trí cắt trừ nó,\nBỏ dục lạc, không màng.', 'That bond is strong, proclaim the wise,\ndown-dragging, pliable, hard to loose.\nThis passion severed, they wander forth\nforsaking sensual pleasures.'),
(347, 24, 'Người đắm say ái dục\nTự lao mình xuống dòng\nNhư nhện sa lưới dệt.\nNgười trí cắt trừ nó,\nBỏ mọi khổ, không màng.', 'Ensnared in passion back they fall\nas spider on a self-spun web.\nThis passion severed, wander the wise\nforsaking dukkha all.'),
(348, 24, 'Bỏ quá, hiện, vị lai,\nÐến bờ kia cuộc đời,\nÝ giải thoát tất cả,\nChớ vướng tại sanh già.', 'Let go before, let go the after,\nlet go the middle, beyond the becoming.\nWith mind released in every way\nyou''ll come no more to birth, decay.'),
(349, 24, 'Người tà ý nhiếp phục,\nTham sắc bén nhìn tịnh,\nNgười ấy ái tăng trưởng,\nLàm giây trói mình chặt.', 'For one who''s crushed by thinking much\nexcessive lust from beauty''s sight,\nfor that one craving grows the more,\nthat one makes strong the bonds.'),
(350, 24, 'Ai vui, an tịnh ý,\nQuán bất tịnh, thường niệm,\nNgười ấy sẽ diệt ái,\nCắt đứt Ma trói buộc.', 'But who delights in subduing evil thoughts,\nmeditates on the impurities with mindfulness,\nthat one indeed will make an End,\nand will sever Mara''s bonds.'),
(351, 24, 'Ai tới đích không sợ,\nLy ái, không nhiễm ô\nNhổ mũi tên sanh tử,\nThân này thân cuối cùng.', 'One who''s fearless, reached the End,\nof craving and of blemish free,\nwho has becoming''s thorn plucked out,\nhas this, a final body.'),
(352, 24, 'Ái lìa, không chấp thủ.\nCú pháp khéo biện tài\nThấu suốt từ vô ngại,\nHiểu thứ lớp trước sau.\nThân này thân cuối cùng\nVị như vậy được gọi,\nBậc Ðại trí, đại nhân.', 'One of clinging-craving free,\nwho''s skilled in way of chanting,\nknowing the wording-sequence,\nof what precedes and follows,\npossessed of final body,\none greatly wise, "Great Person"''s called.');
INSERT INTO `chuathienquang_dhammapada_detail` (`id`, `type`, `content_vi`, `content_en`) VALUES
(353, 24, 'Ta hàng phục tất cả,\nTa rõ biết tất cả,\nKhông bị nhiễm pháp nào.\nTa từ bỏ tất cả\nÁi diệt, tự giải thoát.\nÐã tự mình thắng trí,\nTa gọi ai thầy ta?', 'Beyond all beings, wise to all,\nunsoiled by dhammas all am I,\nleft all and freed by craving''s end,\nby self I''ve known, whom teacher call?	'),
(354, 24, 'Pháp thí, thắng mọi thí!\nPháp vị, thắng mọi vị!\nPháp hỷ, thắng mọi hỷ!\nÁi diệt, thắng mọi khổ!', 'Gift of Dhamma surpasses all gifts,\nthe Dhamma, its taste all other tastes beats,\ndelight in the Dhamma bests other delights,\ndestruction of craving conquers all ill.'),
(355, 24, 'Tài sản hại người ngu.\nKhông người tìm bờ kia\nKẻ ngu vì tham giàu,\nHại mình và hại người.', 'Riches ruin a foolish one\nbut not one seeking the Further Shore,\ncraving for wealth a foolish one\nis ruined as if ruining others.	'),
(356, 24, 'Cỏ làm hại ruộng vườn,\nTham làm hại người đời.\nBố thí người ly tham,\nDo vậy được quả lớn.', 'Weeds are a fault of fields,\nlust''s a human fault,\nthus offerings to the lustless\nbear abundant fruit.'),
(357, 24, 'Cỏ làm hại ruộng vườn,\nSân làm hại người đời.\nBố thí người ly sân,\nDo vậy được quả lớn.', 'Weeds are a fault of fields,\nhate''s a human fault,\nhence offerings to the hateless\nbear abundant fruit.'),
(358, 24, 'Cỏ làm hại ruộng vườn,\nSi làm hại người đời,\nBố thí người ly si,\nDo vậy được quả lớn.', 'Weed are the fault of fields,\ndelusion, human''s faults,\nso gifts to the undeluded\nbear abundant fruit.'),
(359, 24, 'Cỏ làm hại ruộng vườn,\nDục làm hại người đời.\nBố thí người ly dục,\nDo vậy được quả lớn.', 'Weed are the fault of fields,\ndesire, human''s faults,\nso gifts to the desireless\nbear abundant fruit.'),
(360, 25, 'Lành thay, phòng hộ mắt!\nLành thay, phòng hộ tai!\nLành thay, phòng hộ mũi!\nLành thay, phòng hộ lưỡi.', 'Right is restraint in the eye,\nrestraint in the ear is right,\nright is restraint in the nose,\nrestraint in the tongue is right.'),
(361, 25, 'Lành thay phòng hộ thân!\nLành thay, phòng hộ lời,\nLành thay, phòng hộ ý.\nLành thay, phòng tất cả.\nTỷ kheo phòng tất cả,\nThoát được mọi khổ đau.', 'Right is restraint in the body,\nrestraint in speech is right,\nright is restraint in the mind,\neverywhere restraint is right.\nThe bhikkhu everywhere restrained\nis from all dukkha free.'),
(362, 25, 'Người chế ngự tay chân,\nChế ngự lời và đầu,\nVui thích nội thiền định,\nÐộc thân, biết vừa đủ,\nThật xứng gọi tỷ kheo.', 'With hands controlled and feet controlled,\nin speech as well as head controlled,\ndelighting in inward collectedness\nalone, content, a bhikkhu''s called.'),
(363, 25, 'Tỷ kheo chế ngự miệng,\nVừa lời, không cống cao,\nKhi trình bày pháp nghĩa,\nLời lẽ dịu ngọt ngào.', 'Whatever bhikkhu tongue-controlled\nspeaks wisely and who is not proud,\nwho theory and practice can expound,\nsweet as honey is his speech.'),
(364, 25, 'Vị tỷ kheo thích Pháp,\nMến pháp, suy tư Pháp.\nTâm tư niệm chánh Pháp,\nKhông rời bỏ chánh Pháp.', 'The bhikkhu who in Dhamma dwells,\nin Dhamma delighting and pondering,\nremembering the Dhamma -- he\ndoes not decline from Dhamma True.'),
(365, 25, 'Không khinh điều mình được,\nKhông ganh người khác được,\nTỷ kheo ganh tỵ người,\nKhông sao chứng Thiền Ðịnh.', 'He should not disdain his gains\nnor live of others envious,\nthe bhikkhu who is envious\ndoes not attain collectedness.'),
(366, 25, 'Tỷ kheo dầu được ít,\nKhông khinh điều mình được,\nSống thanh tịnh không nhác,\nChư thiên khen vị này.', 'Disdaining not his gains,\nthough little he receives,\npure of life and keen\nthat bhikkhu devas praise.'),
(367, 25, 'Hoàn toàn, đối danh sắc,\nKhông chấp Ta, của Ta.\nKhông chấp, không sầu não.\nThật xứng danh Tỷ kheo.', 'For whom there is no making "mine"\ntowards all name and form,\nwho does not grieve for what is not,\nhe''s truly "bhikkhu" called.'),
(368, 25, 'Tỷ kheo trú Từ Bi,\nTín thành giáo Pháp Phật,\nChứng cảnh giới tịch tỉnh.\nCác hạnh an tịnh lạc.', 'The bhikkhu in kindness abiding,\nbright in the Buddha''s Teaching\ncan come to the Place of Peace,\nthe bliss of conditionedness ceased.'),
(369, 25, 'Tỷ kheo, tát thuyền này,\nThuyền không, nhẹ đi mau.\nTrừ tham, diệt sân hận,\nTất chứng đạt Niết Bàn.', 'O bhikkhu bail this boat,\nwhen emptied it will swiftly go.\nHaving severed lust and hate\nthus to Nibbana you''ll go.'),
(370, 25, 'Ðoạn năm, từ bỏ năm,\nTu tập năm tối thượng,\nTỷ kheo cắt năm trói,\nXứng danh vượt "bộc lưu".', 'Five cut off and five forsake,\na further five then cultivate,\na bhikkhu from five fetter free\nis called a "Forder of the flood".'),
(371, 25, 'Tỷ kheo, hãy tu thiền,\nChớ buông lung phóng dật,\nTâm chớ đắm say dục,\nPhóng dật, nuốt sắt nóng\nBị đốt, chớ than khổ!', 'Meditate bhikkhu! Don''t be heedless!\nDon''t let pleasures whirl the mind!\nHeedless, do not gulp a glob of iron!\nBewail not when burning, "This is dukkha"!'),
(372, 25, 'Không trí tuệ, không thiền,\nKhông thiền, không trí tuệ.\nNgười có thiền có tuệ,\nNhất định gần Niết Bàn.', 'No concentration wisdom lacks,\nno wisdom concentration lacks,\nin whom are both these qualities\nnear to Nibbana is that one.'),
(373, 25, 'Bước vào ngôi nhà trống,\nTỷ kheo tâm an tịnh,\nThọ hưởng vui siêu nhân,\nTịnh quán theo chánh pháp.', 'The bhikkhu gone to a lonely place\nwho is of peaceful heart\nin-sees Dhamma rightly,\nknows all-surpassing joy.'),
(374, 25, 'Người luôn luôn chánh niệm,\nSự sanh diệt các uẩn,\nÐược hoan hỷ, hân hoan,\nChỉ bậc bất tử biết.', 'Whenever one reflects\non aggregates'' arise and fall\none rapture gains and joy.\n''Tis Deathless for Those-who-know.'),
(375, 25, 'Ðây Tỷ kheo có trí,\nTu tập pháp căn bản\nHộ căn, biết vừa đủ,\nGìn giữ căn bản giới,\nThường gần gũi bạn lành,\nSống thanh tịnh tinh cần.', 'Here''s indeed the starting point\nfor the bhikkhu who is wise,\nsense-controlled, contented too,\nrestrained to limit freedom ways,\nin company of noble friends\nwho''re pure of life and keen.'),
(376, 25, 'Giao thiệp khéo thân thiện,\nCử chỉ mực đoan trang.\nDo vậy hưởng vui nhiều,\nSẽ dứt mọi khổ đau.', 'One should be hospitable\nand skilled in good behaviour,\nthereby greatly joyful\ncome to dukkha''s end.'),
(377, 25, 'Như hoa Vassika,\nQuăng bỏ cánh úa tàn,\nCũng vậy vị Tỷ kheo,\nHãy giải thoát tham sân.', 'Just as the jasmine sheds\nits shrivelled flowers all,\nO bhikkhus so should you\nlust, aversion shed.'),
(378, 25, 'Thân tịnh, lời an tịnh,\nAn tịnh, khéo thiền tịnh.\nTỷ kheo bỏ thế vật,\nXứng danh bậc tịch tịnh.', 'That bhikkhu calmed of body, speech,\ncalmed and well-composed of mind,\nwho world-enjoyments has renounced,\n"one calmed" indeed is truly called.'),
(379, 25, 'Tự mình chỉ trích mình,\nTự mình dò xét mình,\nTỷ kheo tự phòng hộ,\nChánh niệm trú an lạc.', 'By yourself exhort yourself!\nBy yourself restrain yourself!\nSo mindful and self-guarded too,\nhappily, bhikkhu, will you live.'),
(380, 25, 'Tự mình y chỉ mình,\nTự mình đi đến mình,\nVậy hãy tự điều phục,\nNhư khách buôn ngựa hiền.', 'Oneself is refuge of oneself\nand one is a haven for oneself,\ntherefore one should check oneself\nas a merchant with a splendid horse.'),
(381, 25, 'Tỷ kheo nhiều hân hoan,\nTịnh tín giáo pháp Phật,\nChứng cảnh giới tịch tịnh,\nCác hạnh an tịnh lạc.', 'The bhikkhu full of joy and faith,\nbright in the Buddha''s Teaching\ncan come to the Place of Peace,\nthe bliss of conditionedness ceased.'),
(382, 25, 'Tỷ kheo tuy tuổi nhỏ\nSiêng tu giáo pháp Phật,\nSoi sáng thế gian này,\nNhư trăng thoát khỏi mây.', 'Surely that youthful bhikkhu who\nstrives in the Buddha''s Teaching\nilluminates all this world\nas moon when free from clouds.'),
(383, 26, 'Hởi này Bà-la-môn,\nHãy tinh tấn đoạn lòng,\nTừ bỏ các dục lạc,\nBiết được hành đoạn diệt,\nNgươi là bậc vô vi.', 'O brahmin, strive and cleave the stream,\ndesires of sense discard,\nknowing conditioned things decay\nbe Knower-of-the-Uncreated.'),
(384, 26, 'Nhờ thường trú hai pháp\nÐến được bờ bên kia.\nBà-la-môn có trí,\nMọi kiết sử dứt sạch.', 'When by the twofold Dhamma\na Brahmin''s gone beyond\nall the bonds of One-who-Knows\nhave wholly disappeared.'),
(385, 26, 'Không bờ này, bờ kia\nCả hai bờ không có,\nLìa khổ, không trói buộc\nTa gọi Bà-la-môn.', 'For whom is found no near or far,\nfor whom''s no near and far,\nfree of fear and fetter-free,\nthat one I call a Brahmin True.'),
(386, 26, 'Tu thiền, trú ly trần\nPhận sự xong, vô lậu,\nÐạt được đích tối thượng,\nTa gọi Bà-la-môn.', 'Seated stainless, concentrated,\nwho''s work is done, who''s free of taints,\nhaving attained the highest aim,\nthat one I call a Brahmin True.	'),
(387, 26, 'Mặt trời sáng ban ngày,\nMặt trăng sáng ban đêm.\nKhí giới sáng ban đêm.\nKhí giới sáng Sát lỵ,\nThiền định sáng Phạm chí.\nCòn hào quang đức Phật,\nChói sáng cả ngày đêm.', 'The sun is bright by day,\nthe moon enlights the night,\narmoured shines the warrior,\ncontemplative the Brahmin True.\nBut all day and night-time too\nresplendent does the Buddha shine.	'),
(388, 26, 'Dứt ác gọi Phạm chí,\nTịnh hạnh gọi Sa môn,\nTự mình xuất cấu uế,\nNên gọi bậc xuất gia.', 'By barring-out badness a "brahmin" one''s called\nand one is a monk by conduct serene,\nbanishing blemishes out of oneself\ntherefore one''s known as "one who has left home".'),
(389, 26, 'Chớ có đập Phạm chí!\nPhạm chí chớ đập lại!\nXấu thay đập Phạm chí\nÐập trả lại xấu hơn!', 'One should not a brahmin beat\nnor for that should He react.\nShame! Who would a Brahmin beat,\nmore shame for any should they react.'),
(390, 26, 'Ðối với Bà-la-môn,\nÐây không lợi ích nhỏ.\nKhi ý không ái luyến,\nTâm hại được chận đứng,\nChỉ khi ấy khổ diệt.', 'For brahmin no small benefit\nwhen mind''s aloof from what is dear.\nAs much he turns away from harm\nso much indeed does dukkha die.	'),
(391, 26, 'Với người thân miệng ý,\nKhông làm các ác hạnh\nBa nghiệp được phòng hộ,\nTa gọi Bà-la-môn.', 'In whom there is no wrong-doing\nby body, speech or mind,\nin these three ways restrained,\nthat one I call a Brahmin True.	'),
(392, 26, 'Từ ai, biết chánh pháp\nBậc Chánh Giác thuyết giảng,\nHãy kính lễ vị ấy,\nNhư Phạm chí thờ lửa.', 'From whom one knows the Dhamma\nby Perfect Buddha taught\ndevoutly one should honour them\nas brahmin sacred fire.	'),
(393, 26, 'Ðược gọi Bà-la-môn,\nKhông vì đầu bện tóc,\nKhông chủng tộc, thọ sanh,\nAi thật chân, chánh, tịnh,\nMới gọi Bà-la-môn.', 'By birth one is no brahmin,\nby family, austerity.\nIn whom are truth and Dhamma too\npure is he, a Brahmin''s he.	'),
(394, 26, 'Kẻ ngu, có ích gì\nBện tóc với da dê,\nNội tâm toàn phiền não,\nNgoài mặt đánh bóng suông.', 'Dimwit! What''s the coiled hair for?\nFor what your cloak of skins?\nWithin you are acquisitive,\nyou decorate without!	'),
(395, 26, 'Người mặc áo đống rác,\nGầy ốm, lộ mạch gân,\nÐộc thân thiền trong rừng.\nTa gọi Bà-la-môn.', 'One enduring rag-robes, lean,\nwith body o''erspread by veins,\nlone in the woods who meditates,\nthat one I call a Brahmin True.	'),
(396, 26, 'Ta không gọi Phạm chí,\nVì chỗ sanh, mẹ sanh.\nChỉ được gọi tên suông\nNếu tâm còn phiền não.\nKhông phiền não, chấp trước\nTa gọi Bà-la-môn.', 'I call him a brahmin though\nby womb-born mother''s lineage,\nhe''s just supercilious\nif with sense of ownership,\nowning nothing and unattached:\nthat one I call a Brahmin True.	'),
(397, 26, 'Ðoạn kết các kiết sử,\nKhông còn gì lo sợ\nKhông đắm trước buộc ràng\nTa gọi Bà-la-môn.', 'Who fetters all has severed\ndoes tremble not at all,\nwho''s gone beyond all bond, unyoked,\nthat one I call a Brahmin True.	'),
(398, 26, 'Bỏ đai da bỏ cương\nBỏ dây, đồ sở thuộc\nBỏ then chốt, sáng suốt,\nTa gọi Bà-la-môn.', 'When cutting strap and reins,\nthe rope and bridle too,\ntipping the shaft, he''s Waked,\nthat one I call a Brahmin True.	'),
(399, 26, 'Không ác ý, nhẫn chịu,\nPhỉ báng, đánh, phạt hình,\nLấy nhẫn làm quân lực,\nTa gọi Bà-la-môn.', 'Who angerless endures abuse,\nbeating and imprisonment,\nwith patience''s power, an armed might:\nthat one I call a Brahmin True.'),
(400, 26, 'Không hận, hết bổn phận,\nTrì giới, không tham ái,\nNhiếp phục, thân cuối cùng,\nTa gọi Bà-la-môn.', 'Who''s angerless and dutiful,\nof virtue full and free of lust,\nwho''s tamed, to final body come,\nthat one I call a Brahmin True.	'),
(401, 26, 'Như nước trên lá sen,\nNhư hột cải đầu kim,\nNgười không nhiễm ái dục,\nTa gọi Bà-la-môn.', 'Like water on a lotus leaf,\nor mustard seed on needle point,\nwhoso clings not to sensual things,\nthat one I call a Brahmin True.'),
(402, 26, 'Ai tự trên đời này,\nGiác khổ, diệt trừ khổ,\nBỏ gánh nặng, giải thoát,\nTa gọi Bà-la-môn.', 'Whoso in this world comes to know\ncessation of all sorrow,\nlaid down the burden, freed from bonds,\nthat one I call a Brahmin True.	'),
(403, 26, 'Người trí tuệ sâu xa,\nKhéo biết đạo, phi đạo\nChứng đạt đích vô thượng,\nTa gọi Bà-la-môn.', 'Whose knowledge is deep, who''s wise,\nwho''s skilled in ways right and wrong,\nhaving attained the highest aim,\nthat one I call a Brahmin True.	'),
(404, 26, 'Không liên hệ cả hai,\nXuất gia và thế tục,\nSống độc thân, ít dục,\nTa gọi Bà-la-môn.', 'Aloof alike from laity\nand those gone forth to homelessness,\nwho wanders with no home or wish,\nthat one I call a Brahmin True.	'),
(405, 26, 'Bỏ trượng, đối chúng sanh,\nYếu kém hay kiên cường,\nKhông giết, không bảo giết,\nTa gọi Bà-la-môn.', 'Who blows to beings has renounced\nto trembling ones, to bold,\nwho causes not to kill nor kills,\nthat one I call a Brahmin True.	'),
(406, 26, 'Thiên thần giữa thù địch\nÔn hòa giữa hung hăng.\nKhông nhiễm giữa, nhiễm trước,\nTa gọi Bà-la-môn.', 'Among the hostile, friendly,\namong the violent, cool\ndetached amidst the passionate,\nthat one I call a Brahmin True.	'),
(407, 26, 'Người bỏ rơi tham sân,\nKhông mạn không ganh tị,\nNhư hột cải đầu kim,\nTa gọi Bà-la-môn.', 'From whomever lust and hate,\nconceit, contempt have dropped away,\nas mustard seed from a point of a needle,\nthat one I call a Brahmin True.	'),
(408, 26, 'Nói lên lời ôn hòa,\nLợi ích và chân thật,\nKhông mất lòng một ai,\nTa gọi Bà-la-môn.', 'Who utters speech instructive,\ntrue and gentle too,\nwho gives offence to none,\nthat one I call a Brahmin True.	'),
(409, 26, 'Ở đời, vật dài, ngắn,\nNhỏ, lớn, đẹp hay xấu\nPhàm không cho không lấy,\nTa gọi Bà-la-môn.', 'Who in the world will never take\nwhat is not given, long or short,\nthe great or small, the fair or foul,\nthat one I call a Brahmin True.	'),
(410, 26, 'Người không có hy cầu,\nÐời này và đời sau,\nKhông hy cầu, giải thoát,\nTa gọi Bà-la-môn.', 'In whom there are no longings found\nin this world or the next,\nlongingless and free from bonds,\nthat one I call Brahmin True.	'),
(411, 26, 'Người không còn tham ái,\nCó trí, không nghi hoặc,\nThể nhập vào bất tử,\nTa gọi Bà-la-môn.', 'In whom there is no dependence found,\nwith Final Knowledge freed from doubt,\nwho''s plunged into the Deathless depths,\nthat one I call a Brahmin True.	'),
(412, 26, 'Người sống ở đời này,\nKhông nhiễm cả thiện, ác,\nKhông sầu, sạch không bụi\nTa gọi Bà-la-môn.', 'Here who''s gone beyond both bonds,\nto goodness and to evil too,\nis sorrowless, unsullied, pure,\nthat one I call a Brahmin True.	'),
(413, 26, 'Như trăng sạch không uế\nSáng trong và tịnh lặng,\nHữu ái được đoạn tận,\nTa gọi Bà-la-môn.', 'Who, like the moon, unblemished, pure,\nis clear and limpid, and in whom\ndelights in being a consumed,\nthat one I call a Brahmin True.	'),
(414, 26, 'Vượt đường nguy hiễm này,\nNhiếp phục luân hồi, si,\nÐến bờ kia thiền định\nKhông dục ái, không nghi,\nKhông chấp trước, tịch tịnh,\nTa gọi Bà-la-môn.', '414.Who''s passed this difficult path,\ndelusion''s bond, the wandering-on,\nwho''s crossed beyond, contemplative,\nuncraving with no questioning doubt,\nno clinging''s fuel so cool become,\nthat one I call a Brahmin true.	'),
(415, 26, 'Ai ở đời đoạn dục,\nBỏ nhà, sống xuất gia,\nDục hữu được đoạn tận,\nTa gọi Bà-la-môn.', 'Who has abandoned lusting here\nas homeless one renouncing all,\nwith lust and being quite consumed,\nthat one I call a Brahmin True.'),
(416, 26, 'Ai ở đời đoạn ái\nBỏ nhà, sống xuất gia,\nÁí hữu được đoạn tận,\nTa gọi Bà-la-môn.', 'Who has abandoned lusting here\nas homeless one renouncing all,\nwith lust and being quite consumed,\nthat one I call a Brahmin True.'),
(417, 26, 'Bỏ trói buộc loài người,\nVượt trói buộc cỏi trời.\nGiải thoát mọi buộc ràng,\nTa gọi Bà-la-môn.', 'Abandoned all human bonds\nand gone beyond the bonds of gods,\nunbound one is from every bond,\nthat one I call a Brahmin True.	'),
(418, 26, 'Bỏ điều ưa, điều ghét,\nMát lạnh, diệt sanh y\nBậc anh hùng chiến thắng,\nNhiếp phục mọi thế giới,\nTa gọi Bà-la-môn.', 'Abandoned boredom and delight,\nbecome quite cool and assetless,\na hero, All-worlds-Conqueror,\nthat one I call a Brahmin True.	'),
(419, 26, 'Ai hiểu rõ hoàn toàn\nSanh tử các chúng sanh,\nKhông nhiễm, khéo vượt qua,\nSáng suốt chân giác ngộ,\nTa gọi Bà-la-môn.', 'Who knows how clutching creatures die\nto reappear in many a mode,\nunclutching then, sublime, Awake,\nthat one I call a Brahmin True.	'),
(420, 26, 'Với ai, loài trời, người\nCùng với Càn thát bà,\nKhông biết chỗ thọ sanh\nLậu tận bậc La-hán.\nTa gọi Bà-la-môn.', 'Whose destination is unknown\nto humans, spirits or to gods,\npollutions stayed, an Arahant,\nthat one I call a Brahmin True	'),
(421, 26, 'Ai quá, hiện, vị lai\nKhông một sở hữu gì,\nKhông sở hữu không nắm,\nTa gọi Bà-la-môn.', 'That one who''s free of everything\nthat''s past, that''s present, yet to be,\nwho nothing owns, who''s unattached,\nthat one I call a Brahmin True.	'),
(422, 26, 'Bậc trâu chúa, thù thắng\nBậc anh hùng, đại sĩ,\nBậc chiến thắng, không nhiễm,\nBậc tẩy sạch, giác ngộ,\nTa gọi Bà-la-môn.', 'One noble, most excellent, heroic too,\ngreat sage and one who conquers all,\nwho''s faultless, washed, one Awake,\nthat one I call a Brahmin True.	'),
(423, 26, 'Ai biết được đời trước,\nThấy thiên giới, đọa xứ,\nÐạt được sanh diệt tận\nThắng trí, tự viên thành\nBậc mâu ni đạo sĩ.\nViên mãn mọi thành tựu\nTa gọi Bà-la-môn.', 'Who so does know of former lives\nand sees the states of bliss and woe\nand then who''s reached the end of births,\na sage supreme with wisdom keen,\ncomplete in all accomplishments,\nthat one I call a Brahmin True.	');
INSERT INTO `chuathienquang_event` (`id`, `date`, `content`, `title`) VALUES
(1, '2012-08-31 17:00:00', 'Thân mời các quí Phật Tử ghé dự Lễ Hội Rằm tháng 7 Nhâm Thìn được tổ chức tại Chùa Giác Quang (Chùa Lá) vào lúc 16 giờ cùng ngày', 'Lễ Hội Rằm tháng 7 Nhâm Thìn'),
(2, '2012-11-29 00:00:00', '<p>Nh&acirc;n ng&agrave;y Lễ Rằm Th&aacute;ng 10 Nh&acirc;m Th&igrave;n v&agrave;o l&uacute;c 16 giờ chiều c&ugrave;ng ng&agrave;y, ch&ugrave;a c&oacute; tổ chức lễ hội cầu an v&agrave; cầu si&ecirc;u cho c&aacute;c gia đ&igrave;nh v&agrave; những người đ&atilde; bị tai nạn giao th&ocirc;ng. Th&acirc;n mời c&aacute;c qu&iacute; phật tử tham dự !</p>\r\n', 'Lễ Rằm Tháng 10 Nhâm Thìn'),
(3, '2012-02-07 00:00:00', '<p>\r\n	Nh&acirc;n ng&agrave;y Lễ Rằm Th&aacute;ng 1 Nh&acirc;m Th&igrave;n v&agrave;o l&uacute;c 16 giờ chiều c&ugrave;ng ng&agrave;y, ch&ugrave;a c&oacute; tổ chức lễ hội rằm đầu năm. K&iacute;nh mời qu&iacute; Phật Tử tham dự !</p>\r\n', 'Lễ Rằm Tháng 1 Nhâm Thìn'),
(4, '2012-08-09 07:00:00', '<p>Th&acirc;n mời qu&iacute; Phật Tử đ&atilde; đăng k&iacute; kh&oacute;a tu h&egrave; 2012 đến dự lễ khai giảng tại ch&ugrave;a v&agrave;o l&uacute;c 7h30 c&ugrave;ng ng&agrave;y, địa điểm tại Hội Trường A.</p>\r\n\r\n<p><img alt="" src="https://lh4.googleusercontent.com/-riVxIEbHb0g/UKw8SlH5SkI/AAAAAAAAA5s/5D4rQF4_xBg/s400/100_4129.JPG" style="width: 400px; height: 300px;" /></p>\r\n\r\n<p><img alt="" src="http://www.buddhistedu.org/viet/images/stories/buddhist_study/impkages.jpg" style="width: 261px; height: 193px;" /></p>\r\n\r\n<p>H&igrave;nh đức phật c&ograve;n trẻ</p>\r\n', 'Khóa tu hè 2012'),
(5, '2013-01-22 00:00:00', '<p style="text-align: center;"><img alt="" src="https://lh3.googleusercontent.com/-b2tbUUBIv9A/UM4VhYe3acI/AAAAAAAABeg/H6xZyPgV5j0/s144/LuHuong.gif" style="width: 143px; height: 142px;" /></p>\r\n\r\n<p><span>V&agrave;o ng&agrave;y ? giờ ? ng&agrave;y 23/12/2012 ( nhằm 11 &acirc;l) </span><span>ch&ugrave;a Gi&aacute;c Quang sẽ tiến h&agrave;nh nghi thức Lễ Đặt Đ&aacute; (khởi c&ocirc;ng x&acirc;y dựng ch&iacute;nh điện)</span>.</p>\r\n\r\n<p>Trong buổi lễ gồm c&oacute;:</p>\r\n\r\n<p>&nbsp;&nbsp; - Kh&aacute;ch mời 1</p>\r\n\r\n<p>&nbsp;&nbsp; - Kh&aacute;ch mời 2</p>\r\n\r\n<p>&nbsp;&nbsp; - Kh&aacute;ch mời 3</p>\r\n', 'Lễ đặt đá (khởi công xây dựng chính điện)'),
(6, '2012-11-24 08:30:00', '<p style="text-align: center;"><img alt="" src="https://lh5.googleusercontent.com/-hwR0QJfPMyo/ULwVkO9496I/AAAAAAAABDo/SSEVwHXL3lA/s800/Untitled-1%2520copy.jpg" style="width: 590px; height: 180px;" /><strong><span style="font-size:16px;">Kh&oacute;a Tu Một Ng&agrave;y An Lạc Lần Thứ 61</span></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><u><strong>Video giảng</strong></u></p>\r\n\r\n<p><iframe frameborder="0" height="320" scrolling="no" src="http://www.youtube.com/embed/1MlhGosY6vQ" width="480"></iframe></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span><span>&nbsp;</span><span>&nbsp;</span><span>&nbsp;</span><span>&nbsp;</span><span>&nbsp;</span><span>&nbsp;</span><span>&nbsp;</span><span>&nbsp;</span><span>&nbsp;</span><span>&nbsp;</span><span>&nbsp;</span><span>&nbsp;</span><span>&nbsp;</span><span>&nbsp;</span><span>&nbsp;</span><span>&nbsp;</span><span>&nbsp;</span><span>&nbsp;</span><span>&nbsp;</span><span>&nbsp;</span><span>&nbsp;</span><span>&nbsp;</span><span>&nbsp;</span><span>&nbsp;</span><span>&nbsp;</span><span>&nbsp;</span><span>&nbsp;</span><span>&nbsp;</span><span>&nbsp;</span><span>&nbsp;</span> </span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span>&nbsp;</span></p>\r\n', 'Khóa Tu Niệm Phật (03) Ngày Lần Thứ 61'),
(7, '2013-02-10 06:00:00', '<p style="text-align: center;"><img alt="" src="https://lh6.googleusercontent.com/-CTbaZ_0vwc4/UQoKiU0GViI/AAAAAAAAAEI/85-bpL9-qvo/s800/Tet.jpg" style="width: 472px; height: 289px;" /></p>\r\n\r\n<p style="text-align: center;"><span style="font-size:22px;"><span style="font-family: times new roman,times,serif;"><strong>Chương tr&igrave;nh mừng Xu&acirc;n Tết Nguy&ecirc;n Đ&aacute;n Qu&iacute; Tị 2013</strong></span></span></p>\r\n\r\n<p style="text-align: center;">(Nội dung mẫu phải sửa chữa lại)</p>\r\n\r\n<p>Nam M&ocirc; Bổn Sư Th&iacute;ch Ca M&acirc;u Ni Phật&nbsp;</p>\r\n\r\n<p><span style="line-height: 1.6em;">K&iacute;nh Bạch Chư T&ocirc;n Thiền &ETH;ức Tăng Ni</span></p>\r\n\r\n<p><span style="line-height: 1.6em;">Thưa Q&uacute;y Phật tử v&agrave; đồng hương&nbsp;</span></p>\r\n\r\n<p style="text-align: justify;"><span style="line-height: 1.6em;">Nh&acirc;n m&ugrave;a xu&acirc;n Di Lặc lại đến với người con phật cũng l&agrave; m&ugrave;a xu&acirc;n Q&uacute;y Tỵ lại về với q&uacute;y b&agrave; con . &nbsp;Ch&uacute;ng con &nbsp;t&igrave;m lại lời dạy của &ETH;ức Phật ngan qua lời chuyển ngữ của Cố Ho&agrave; Thượng Minh Ch&acirc;u &nbsp;xin ch&acirc;n th&agrave;nh v&agrave; tr&acirc;n trọng gửi đến Chư T&ocirc;n Thiền &ETH;ức v&agrave; q&uacute;y Phật tử đồng hưong &nbsp;những lời dạy Của &ETH;ức Phật.</span></p>\r\n\r\n<blockquote>\r\n<p style="text-align: justify; margin-left: 80px;">&quot;Kh&ocirc;ng th&acirc;n cận kẻ ngu,<br />\r\nNhưng gần gũi bật tr&iacute;,<br />\r\n&ETH;ảnh lễ người đ&aacute;ng lễ,<br />\r\nL&agrave; điềm l&agrave;nh tối thượng.</p>\r\n</blockquote>\r\n\r\n<blockquote>\r\n<p style="margin-left: 80px;">Học nhiều, nghề nghiệp giỏi,<br />\r\nKh&eacute;o huấn luyện học tập,<br />\r\nN&oacute;i những lời kh&eacute;o n&oacute;i,<br />\r\nL&agrave; điềm l&agrave;nh tối thượng.</p>\r\n</blockquote>\r\n\r\n<blockquote>\r\n<p style="margin-left: 80px;">Hiếu dưỡng mẹ v&agrave; cha,<br />\r\nNu&ocirc;i dưỡng vợ v&agrave; con,<br />\r\nL&agrave;m nghề kh&ocirc;ng rắc rối<br />\r\nL&agrave; điềm l&agrave;nh tối thượng<br />\r\nBố th&iacute; h&agrave;nh đ&uacute;ng ph&aacute;p,<br />\r\nSăn s&oacute;c c&aacute;c b&agrave; con<br />\r\nL&agrave;m nghiệp kh&ocirc;ng lỗi lầm<br />\r\nL&agrave; điềm l&agrave;nh tối thượng.</p>\r\n</blockquote>\r\n\r\n<blockquote>\r\n<p style="margin-left: 80px;">Chấm dứt, từ bỏ &aacute;c,<br />\r\nChế ngự đam m&ecirc; rượu,<br />\r\nTrong ph&aacute;p kh&ocirc;ng ph&oacute;ng dật<br />\r\nL&agrave; điềm l&agrave;nh tối thượng.</p>\r\n</blockquote>\r\n\r\n<blockquote>\r\n<p style="margin-left: 80px;">K&iacute;nh lễ v&agrave; hạ m&igrave;nh,<br />\r\nBiết đủ v&agrave; biết ơn,<br />\r\n&ETH;&uacute;ng thời nghe ch&aacute;nh ph&aacute;p,<br />\r\nL&agrave; điềm l&agrave;nh tối thượng.<br />\r\nNhẫn nhục, lời h&ograve;a nh&atilde;,<br />\r\nYết kiến c&aacute;c sa m&ocirc;n,<br />\r\n&ETH;&uacute;ng thời đ&agrave;m luận ph&aacute;p,<br />\r\nL&agrave; điềm l&agrave;nh tối thượng.</p>\r\n</blockquote>\r\n\r\n<blockquote>\r\n<p style="margin-left: 80px;">Khắc khổ v&agrave; phạm hạnh,<br />\r\nThấy được l&yacute; Th&aacute;nh đế,<br />\r\nGi&aacute;c ngộ quả Niết b&agrave;n,<br />\r\nL&agrave; điềm l&agrave;nh tối thượng.</p>\r\n</blockquote>\r\n\r\n<blockquote>\r\n<p style="margin-left: 80px;">Khi x&uacute;c chạm việc đời,<br />\r\nT&acirc;m kh&ocirc;ng động kh&ocirc;ng sầu,<br />\r\nKh&ocirc;ng uế nhiễm an ổn<br />\r\nL&agrave; điềm l&agrave;nh tối thượng.</p>\r\n</blockquote>\r\n\r\n<blockquote>\r\n<p style="margin-left: 80px;">L&agrave;m sự việc như vậy,<br />\r\nKh&ocirc;ng chỗ n&agrave;o thất bại,<br />\r\nKhắp nơi được an to&agrave;n,<br />\r\nL&agrave; điềm l&agrave;nh tối thượng&quot;.</p>\r\n</blockquote>\r\n\r\n<p><span style="text-align: justify; line-height: 1.6em;">Ch&uacute;ng con c&ugrave;ng to&agrave;n thể đạo tr&agrave;ng xin th&agrave;nh kinh chấp tay k&iacute;nh lễ v&agrave; gửi đến chư T&ocirc;n Thiền &ETH;ức lời cầu ch&uacute;c Ph&aacute;p thể khinh an, ch&uacute;ng sanh dị độ, k&iacute;nh mến gửi đến phật tử v&agrave; đồng hương lời ch&agrave;o v&agrave; lời ch&uacute;c an vui hạnh ph&uacute;c trong điềm l&agrave;nh ng&agrave;y &nbsp;đầu xu&acirc;n m&agrave; &ETH;ức Phật chỉ dạy.</span></p>\r\n\r\n<p style="text-align: justify;"><span style="line-height: 1.6em;">Năm 2013 n&agrave;y ch&uacute;ng ta nguyện với Phật, Ph&aacute;p ,Tăng rằng ch&uacute;ng ta đ&atilde; c&oacute; nơi an tr&uacute; v&agrave; nguyện với l&ograve;ng rằng &nbsp;đừng chạy lăng xăng đi t&igrave;m ng&agrave;y l&agrave;nh th&aacute;ng tốt năm hạn th&aacute;ng xui... Sao la hầu thổ t&uacute; &hellip;.. hay tuổi n&agrave;y hạp với tuổi kia&hellip;. Bởi v&igrave; Phật dạy trong kinh : &ldquo;&ETH;ối với kẻ thanh tịnh, ng&agrave;y n&agrave;o cũng ng&agrave;y l&agrave;nh th&aacute;ng tốt. Ch&uacute;ng ta an tr&uacute; như vậy th&igrave; điềm an l&agrave;nh lu&ocirc;n hiện hữu b&ecirc;n ta trong sinh hoạt đời sống hằng ng&agrave;y. Nơi đ&acirc;y ch&uacute;ng con thiết tha xin s&aacute;m hối v&agrave; ki&ecirc;u gọi q&uacute;y &Ocirc;n, q&uacute;y thầy, q&uacute;y Sư C&ocirc; cũng như q&uacute;y Ch&ugrave;a đ&atilde; hay đang c&oacute; xin săm b&oacute;i quẻ, xin q&uacute;y ng&agrave;i xem lại v&agrave; n&ecirc;n hay kh&ocirc;ng n&ecirc;n để tục lệ tội nghiệp n&agrave;y k&eacute;o d&agrave;i m&atilde;i m&atilde;i..</span></p>\r\n\r\n<p style="text-align: justify;">Duy nguyện to&agrave;n gia nam nữ an vui v&agrave; hạnh ph&uacute;c trong lời phật dạy.</p>\r\n\r\n<p style="text-align: justify;"><span style="line-height: 1.6em;">Nam M&ocirc; &ETH;ương Lai Hạ Sanh Di Lặc T&ocirc;n Phật.</span></p>\r\n\r\n<p>Chương Tr&igrave;nh:</p>\r\n\r\n<p><span style="line-height: 1.6em;">1.Chủ nhật ng&agrave;y 3/2/2013 (23/12/ nh&acirc;m th&igrave;n) Lễ &ETH;ức Phật Th&iacute;ch Ca Th&agrave;nh &ETH;ạo v&agrave; c&uacute;ng cơm cầu si&ecirc;u cho hương linh thờ tại ch&ugrave;a cũng l&agrave; ng&agrave;y tu học tổng kết lại cuối năm nh&acirc;m th&igrave;n.</span></p>\r\n\r\n<p>2.Tối thứ bảy l&uacute;c 6:30 Lễ S&aacute;m Hối v&agrave; 8 giờ ng&agrave;y 9/2/2013 ( 29 thế 30/12/nh&acirc;m th&igrave;n) văn nghệ vui Xu&acirc;n.</p>\r\n\r\n<p><span style="line-height: 1.6em;">3.Lễ &ETH;&oacute;n Giao Thừa l&uacute;c 11:30 tối 9/2/2013 (29 thế 30/12/nh&acirc;m th&igrave;n)</span></p>\r\n\r\n<p>4.Lễ cầu nguyện đầu năm : S&aacute;ng chủ nhật &nbsp;l&uacute;c 10:30 ng&agrave;y &nbsp;1/1/Q&uacute;y Tỵ (ng&agrave;y 10/2/2013)</p>\r\n\r\n<p><span style="line-height: 1.6em;">5.Lễ h&uacute;y nhật Cố Trưởng L&atilde;o H&ograve;a Thượng Th&iacute;ch Tịnh Khiết &ETH;ệ Nhất Tăng Thống Gi&aacute;o Hội Phật Gi&aacute;o Việt Nam Thống Nhất v&agrave; lễ c&uacute;ng rằm Thượng Nguy&ecirc;n v&agrave;o s&aacute;ng chủ nhật &nbsp;l&uacute;c 10: 30 ng&agrave;y 22/1/ Năm Q&uacute;y Tỵ (Ng&agrave;y 3/3/2013)</span></p>\r\n\r\n<p>6.Chủ Nhật ng&agrave;y 2/6/2013 (ng&agrave;y24/4/Q&uacute;y Tỵ) &ETH;ại Lễ Phật &ETH;ản Pl: 2557 l&uacute;c 10: 30&nbsp;</p>\r\n\r\n<p><span style="line-height: 1.6em;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Tỳ Kheo Th&iacute;ch T&acirc;m Hỷ</span></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'Tết Nguyên Đán 2013');
