INSERT INTO `chuathienquang_news` (`id`, `id_category`, `author`, `date`, `content`, `title`, `type`) VALUES
(44, 12, 'Hữu Huệ', '2012-12-15 17:00:00', '<p style="text-align: justify;"><span style="color: rgb(92, 92, 92); font-family: Arial, Tahoma; font-size: 12px; font-style: normal; font-variant: normal; font-weight: bold; letter-spacing: normal; line-height: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); display: inline !important; float: none;">Gần đ&acirc;y b&aacute;o Gi&aacute;c Ngộ (số 460) lại c&oacute; b&agrave;i Hương cỏ thơm viết về Sư c&ocirc;-tiến sĩ Th&iacute;ch nữ Hương Nhũ trở th&agrave;nh giảng vi&ecirc;n được y&ecirc;u mến tại Học viện Phật gi&aacute;o Việt Nam tại TP.HCM của t&aacute;c giả Như Nghi&ecirc;m.</span></p>\r\n\r\n<div style="color: rgb(102, 102, 102); font-family: Arial, Tahoma; font-size: 12px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">\r\n<p style="text-align: justify;"><font color="#000080" face="Arial" size="2">Trong quyển Phật gi&aacute;o trong thế kỷ mới, Tại sao&nbsp; phải chấn hưng? do Giao Điểm xuất bản th&aacute;ng 10-2006 c&oacute; b&agrave;i viết của Ni sư Th&iacute;ch nữ Huệ Li&ecirc;n về&nbsp; Sự đ&oacute;ng g&oacute;p của Ni giới, một sứ mạng c&oacute; thể thực hiện được. Trong đ&oacute; t&aacute;c giả đ&atilde; đưa ra những l&yacute; thuyết v&agrave; dẫn chứng thuyết phục, rằng nếu c&oacute; điều kiện th&iacute;ch hợp th&igrave; &ldquo;gi&aacute; trị đ&oacute;ng g&oacute;p của Ni giới c&oacute; thể đạt đến mức cao nhất m&agrave; một h&agrave;nh giả Tăng c&oacute; thể đạt được&rdquo;. Gần đ&acirc;y b&aacute;o Gi&aacute;c Ngộ (số 460) lại c&oacute; b&agrave;i Hương cỏ thơm viết về Sư c&ocirc;-tiến sĩ Th&iacute;ch nữ Hương Nhũ trở th&agrave;nh giảng vi&ecirc;n được y&ecirc;u mến tại Học viện Phật gi&aacute;o Việt Nam tại TP.HCM của t&aacute;c giả Như Nghi&ecirc;m.</font></p>\r\n\r\n<p style="text-align: justify;"><font color="#000080" face="Arial" size="2">Thật ra <em>Hương cỏ thơm </em>chỉ l&agrave; một b&agrave;i viết mang t&iacute;nh c&aacute;ch c&aacute; nh&acirc;n về l&ograve;ng k&iacute;nh trọng, mến y&ecirc;u của một Ni sinh d&agrave;nh cho người thầy đứng tr&ecirc;n bục giảng nh&acirc;n ng&agrave;y Nh&agrave; gi&aacute;o Việt Nam 20-11. Nhưng hiệu ứng của b&agrave;i viết lại vượt xa hơn l&agrave; một t&igrave;nh cảm ri&ecirc;ng tư của học tr&ograve; d&agrave;nh cho c&ocirc; gi&aacute;o. M&agrave; n&oacute; mang tầm v&oacute;c thời đại. Ta thấy được điều đ&oacute; qua c&aacute;ch đ&oacute;n nhận của c&aacute;c Ni sinh Phật học viện. Khi b&agrave;i b&aacute;o đ&oacute; vừa ph&aacute;t h&agrave;nh th&igrave; t&ocirc;i đ&atilde; nhận được những c&uacute; điện thoại hỏi c&oacute; đọc <em>Hương cỏ thơm</em> chưa? C&oacute; Ni sinh c&ograve;n tặng hẳn t&ocirc;i một tờ b&aacute;o <em>Gi&aacute;c Ngộ </em>để&hellip; khoe. Rồi gợi &yacute; t&ocirc;i viết b&agrave;i về chư Ni &ldquo;c&agrave;ng nhiều c&agrave;ng tốt&rdquo;&hellip;</font></p>\r\n</div>\r\n\r\n<p style="text-align: justify;"><font color="#000080" face="Arial" size="2" style="font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">T&ocirc;i kh&ocirc;ng ngờ một b&agrave;i b&aacute;o b&igrave;nh thường lại được đ&oacute;n nhận qu&aacute; nồng n&agrave;n như vậy. Vậy th&igrave; b&agrave;i b&aacute;o đ&oacute; phải c&oacute; một c&aacute;i g&igrave; đặc biệt. V&agrave; t&ocirc;i nghĩ rằng c&aacute;i đặc biệt đ&oacute; ch&iacute;nh l&agrave; ở chỗ n&oacute; đ&atilde; ra đời đ&uacute;ng l&uacute;c, điểm tr&uacute;ng huyệt của những Ni sinh đang ngồi tr&ecirc;n ghế giảng đường đại học. Đằng sau sự phấn khởi đ&oacute; ta c&oacute; thể thấy được một kh&aacute;t vọng muốn vươn l&ecirc;n, một nỗi l&ograve;ng muốn tự khẳng định m&igrave;nh v&agrave; một t&acirc;m nguyện muốn cống hiến&hellip; m&agrave; họ ấp ủ từ l&acirc;u. B&agrave;i viết do một Ni sinh viết về một Ni sư&nbsp; đ&atilde; n&oacute;i gi&uacute;p họ những điều đ&oacute;, l&agrave; giọt nước l&agrave;m tr&agrave;n ly nước, m&agrave; Sư c&ocirc; Hương Nhũ chỉ l&agrave; một mẫu h&igrave;nh. Rằng c&oacute; ai biết được những vị Ni sư, Sư c&ocirc;, m&agrave; trước hết l&agrave; người phụ nữ Việt Nam cần c&ugrave; lam lủ, với vẻ ngo&agrave;i thầm lặng như cam chịu kia lại ẩn chứa b&ecirc;n trong bao điều kỳ diệu m&agrave; khi cần họ c&oacute; thể b&ugrave;ng l&ecirc;n một c&aacute;ch mạnh mẽ?</font></p>\r\n\r\n<p style="text-align: justify;"><font color="#000080" face="Arial" size="2" style="font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">Thật ra từ l&acirc;u t&ocirc;i cũng rất muốn viết một c&aacute;i g&igrave; đ&oacute; về chư Ni. V&igrave; t&ocirc;i thấy rằng họ thật sự đ&aacute;ng k&iacute;nh, đ&aacute;ng được c&oacute; nhiều hơn những g&igrave; họ đang l&agrave;. X&atilde; hội đ&atilde; đổi thay, tr&ecirc;n thế giới c&oacute; nhiều phụ nữ đạt được những th&agrave;nh tựu xuất sắc tr&ecirc;n hầu hết c&aacute;c lĩnh vực, từ kinh tế cho tới ch&iacute;nh trị&hellip; Sự th&agrave;nh c&ocirc;ng của họ khẳng định một điều rằng, kh&ocirc;ng phải giới t&iacute;nh m&agrave; l&agrave; năng lực tự th&acirc;n mới l&agrave; quan trọng. Thế gian đ&atilde; như vậy th&igrave; tại sao Phật ph&aacute;p lại kh&ocirc;ng được như vậy? Tại sao một c&ocirc; gi&aacute;o b&igrave;nh thường c&oacute; quyền dạy học cho chư Tăng, nhưng khi c&ocirc; gi&aacute;o đ&oacute; xuống t&oacute;c l&agrave;m Ni th&igrave; quyền đ&oacute; bị mất? Chẳng lẽ sau khi xuất gia kh&ocirc;ng c&oacute; gi&aacute; trị bằng trước khi xuất gia? Nhưng rất mừng l&agrave; trong những năm gần đ&acirc;y ta thấy cũng một sự&nbsp; thay đổi tương tự trong h&agrave;ng ngũ Ni lưu. Chư Ni đi học với số lượng đ&ocirc;ng, họ học cũng giỏi nữa, nhất l&agrave; c&oacute; vị c&ograve;n đi du học nước ngo&agrave;i.</font></p>\r\n\r\n<p style="text-align: justify;"><font color="#000080" face="Arial" size="2" style="font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">Đ&atilde; c&oacute; một thời kỳ t&ocirc;i nghe một số chư t&ocirc;n t&uacute;c n&oacute;i rằng Ni học cho lắm rồi th&igrave; cũng về ch&ugrave;a se nhang, b&aacute;n đồ chay&hellip; Nhưng l&agrave;m sao c&oacute; thể kh&aacute;c được khi m&agrave; thời đại n&oacute; l&agrave; như vậy(như &ocirc;ng b&agrave; xưa n&oacute;i con g&aacute;i đi học chữ chỉ để viết thư cho trai). Điều m&agrave; bấy l&acirc;u nay t&ocirc;i rất lấy l&agrave;m lạ l&agrave; tại sao tại những trường Phật học số lượng Ni sinh theo học bao giờ cũng đ&ocirc;ng hơn Tăng. Năng lực tiếp thu của họ cũng giỏi hơn Tăng. Họ thường được xếp hạng rất cao trong lớp, cao hơn Tăng. V&iacute; dụ như đợt tuyển sinh Học viện kh&oacute;a VI vừa rồi, hai hạng nhất v&agrave; nh&igrave; đều l&agrave; của Ni. Tăng chỉ đứng hạng ba. Vậy m&agrave; sau khi ra trường th&igrave; họ lại biệt tăm biệt t&iacute;ch. Kh&ocirc;ng thấy c&oacute; th&agrave;nh tựu g&igrave; nổi bật&hellip; Nhưng c&aacute;i thời ấy đang dần trở th&agrave;nh&hellip; kỷ niệm. C&ugrave;ng với xu hướng ph&aacute;t triển của thời đại, Ni giới cũng đ&atilde; vươn l&ecirc;n để tự khẳng định m&igrave;nh, v&agrave; họ đ&atilde; được đ&oacute;n nhận. Bằng chứng l&agrave; Phật học viện đ&atilde; cho ph&eacute;p chư Ni được đứng lớp&hellip; dạy cả chư Tăng. Ri&ecirc;ng lớp Triết học Phật gi&aacute;o kh&oacute;a VI của ch&uacute;ng t&ocirc;i c&oacute; ba &ldquo;c&ocirc; gi&aacute;o&rdquo; như thế đảm tr&aacute;ch xuy&ecirc;n suốt nhiều bộ m&ocirc;n kh&aacute;c nhau. Đ&oacute; l&agrave; Sư c&ocirc; Hương Nhũ, Sư c&ocirc; Hằng Li&ecirc;n từ Ấn Độ về, v&agrave; Sư c&ocirc; Huệ Chơn tốt nghiệp ở Trung Quốc.</font></p>\r\n\r\n<p style="text-align: justify;"><font color="#000080" face="Arial" size="2" style="font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">Ngo&agrave;i những đặc điểm cần phải c&oacute; của một người đứng lớp như kiến thức, tự tin, nhiệt t&igrave;nh, vui vẻ&hellip;, điều đ&aacute;ng qu&yacute; ở c&aacute;c vị giảng sư Ni n&agrave;y l&agrave; đức t&iacute;nh khi&ecirc;m cung, lễ độ. Khi nghe một Tăng sinh xưng &ldquo;em&rdquo; với m&igrave;nh th&igrave; Sư c&ocirc; Hằng Li&ecirc;n từ tốn m&agrave; n&oacute;i rằng &ldquo;kh&ocirc;ng d&aacute;m nhận, v&igrave; d&ugrave; sao qu&yacute; thầy cũng l&agrave; Tăng&rdquo;. Nhưng ngược lại qu&yacute; sư c&ocirc; đ&oacute; lại xưng m&igrave;nh l&agrave; &ldquo;con&rdquo; v&agrave; vẫn gọi c&aacute;c Tăng sinh bằng &ldquo;thầy&rsquo; một c&aacute;ch tr&acirc;n trọng. Đ&acirc;y c&oacute; lẽ l&agrave; trường hợp kh&aacute; đặc biệt chỉ c&oacute; trong m&ocirc;i trường gi&aacute;o dục Phật gi&aacute;o Việt Nam. Nhưng điều đ&oacute; kh&ocirc;ng hề l&agrave;m cho gi&aacute; trị của họ giảm xuống&nbsp; m&agrave; ngược lại họ c&agrave;ng được &ldquo;học tr&ograve;&rdquo; m&igrave;nh k&iacute;nh mến th&ecirc;m m&agrave; th&ocirc;i. Th&uacute; thật t&ocirc;i chưa thấy c&oacute; trường hợp &ldquo;tr&ograve;&rdquo; n&agrave;o d&aacute;m hỗn với &ldquo;c&ocirc; gi&aacute;o&rdquo; cả.</font></p>\r\n\r\n<p style="text-align: justify;"><font color="#000080" face="Arial" size="2" style="font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">Trước đ&acirc;y t&ocirc;i cũng c&oacute; gặp&nbsp; trường hợp một số vị Ni, v&igrave; muốn chứng tỏ &ldquo;b&igrave;nh đẳng&rdquo; với Tăng n&ecirc;n đ&atilde; c&oacute; những h&agrave;nh động biểu lộ kh&aacute; l&agrave;&hellip; khiếm nh&atilde;. Ngay cả ph&eacute;p lịch sự tối thiểu khi x&atilde; giao l&agrave; x&aacute; ch&agrave;o nhau m&agrave; họ cũng&hellip; bỏn xẻn đối với chư Tăng. C&ograve;n nếu c&oacute; n&oacute;i năng th&igrave; cố t&igrave;nh kẻ cả, trịch thượng chứ kh&ocirc;ng bao giờ chịu thua. L&uacute;c đ&oacute; t&ocirc;i cũng nghĩ rằng như thế l&agrave; b&igrave;nh đẳng thật. V&igrave; chư Tăng n&oacute;i lỗi của chư Ni được th&igrave; chư Ni cũng c&oacute; quyền đ&oacute; chứ sao. Nhưng khi v&agrave;o Học viện, nh&igrave;n thấy tr&igrave;nh độ học vấn v&agrave; sự khi&ecirc;m nhường của c&aacute;c giảng sư Ni, t&ocirc;i mới thấy những vị Ni trước đ&oacute; rất l&agrave;&hellip; b&igrave;nh d&acirc;n học vụ. Th&igrave; ra c&agrave;ng am hiểu về Phật ph&aacute;p người ta c&agrave;ng biết hạ m&igrave;nh v&agrave; t&ocirc;n trọng người kh&aacute;c, coi trọng thực chất hơn l&agrave; h&igrave;nh thức, họ l&agrave;m nhiều hơn n&oacute;i,&nbsp; v&agrave; &acirc;m thầm đ&oacute;ng g&oacute;p chứ kh&ocirc;ng đả ph&aacute; điều g&igrave;. Người c&agrave;ng khi&ecirc;m nhường th&igrave; c&agrave;ng l&agrave;m được nhiều việc. Bởi v&igrave; &ldquo;th&ugrave;ng rỗng k&ecirc;u to&rdquo; vẫn l&agrave; c&acirc;u chuyện xưa nay. &Ocirc;i, k&iacute;nh l&agrave;m sao phận liễu yếu đ&agrave;o tơ m&agrave; c&oacute; ch&iacute; x&ocirc;ng trời thẳm, v&agrave; thương l&agrave;m sao khi l&agrave;m &ldquo;thầy&rdquo; m&agrave; vẫn tự cho m&igrave;nh l&agrave; &ldquo;con&rdquo;! H&igrave;nh ảnh ấy gợi t&ocirc;i nhớ đến những vần thơ trong s&aacute;ch <em>T&acirc;m l&yacute; đạo đức </em>của thầy Ch&acirc;n Quang:</font></p>\r\n\r\n<p><font color="#000080" face="Arial" size="2" style="font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);"><em>Xin c&uacute;i xuống l&agrave;m người h&egrave;n k&eacute;m</em></font></p>\r\n\r\n<p><font color="#000080" face="Arial" size="2" style="font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);"><em>Miễn đ&ocirc;i tay l&agrave;m đẹp cuộc đời</em></font></p>\r\n\r\n<p><font color="#000080" face="Arial" size="2" style="font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);"><em>T&igrave;nh thương chan khắp mu&ocirc;n nơi</em></font></p>\r\n\r\n<p><font color="#000080" face="Arial" size="2" style="font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);"><em>Con tim tuy nhỏ nhưng trời đất &ocirc;m.</em></font></p>\r\n\r\n<p style="text-align: justify;"><font color="#000080" face="Arial" size="2" style="font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">T&ocirc;i nhớ c&aacute;ch đ&acirc;y chừng mấy năm, Học viện c&oacute; tiếp một vị Ni sư người &Acirc;u Mỹ. Vị n&agrave;y c&oacute; n&oacute;i l&agrave; c&oacute; &ldquo;sứ mệnh&rdquo; đi v&ograve;ng quanh thế giới, tổ chức đại hội hai năm một lần k&ecirc;u gọi b&igrave;nh đẳng trong Phật gi&aacute;o bằng c&aacute;ch&hellip; bỏ B&aacute;t kỉnh ph&aacute;p. T&ocirc;i kh&ocirc;ng muốn đề cập t&iacute;nh đ&uacute;ng sai, n&ecirc;n hay kh&ocirc;ng n&ecirc;n, cần hay kh&ocirc;ng cần của sự việc đ&oacute;, nhưng m&agrave; như c&oacute; một vị thầy đ&atilde; n&oacute;i, điều cần thiết của chư Ni ng&agrave;y nay l&agrave; tăng cường học hỏi, n&acirc;ng cao tr&igrave;nh độ của m&igrave;nh cả về thế học lẫn Phật học chứ kh&ocirc;ng phải đi c&atilde;i nhau l&agrave; B&aacute;t kỉnh ph&aacute;p c&oacute; phải do Phật n&oacute;i hay kh&ocirc;ng, c&oacute; cần x&oacute;a bỏ B&aacute;t kỉnh ph&aacute;p hay kh&ocirc;ng? V&agrave; đ&oacute; ch&iacute;nh l&agrave; con đường m&agrave; chư Ni Việt Nam đang đi cũng như bước đầu thu hoạch được kết quả đ&aacute;ng kh&iacute;ch lệ. Ni giới Việt Nam c&oacute; c&aacute;ch l&agrave;m ri&ecirc;ng để khẳng định vị tr&iacute; của m&igrave;nh. Họ kh&ocirc;ng h&ocirc; h&agrave;o k&ecirc;u gọi n&agrave;y nọ m&agrave; chỉ &acirc;m thầm học tập v&agrave; l&agrave;m việc, &ldquo;chậm m&agrave; chắc&rdquo;. V&agrave; một khi nội lực đ&atilde; đầy đủ th&igrave; d&ugrave; người kh&aacute;c c&oacute; muốn phủ nhận cũng kh&ocirc;ng thể n&agrave;o phủ nhận được.</font></p>\r\n\r\n<p style="text-align: justify;"><font color="#000080" face="Arial" size="2" style="font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">D&ugrave; sao đi nữa sự ph&aacute;t triển của Ni giới, nhất l&agrave; Ni giới PGVN l&agrave; một điều đ&aacute;ng mừng, đ&aacute;ng động vi&ecirc;n, đ&aacute;ng kh&iacute;ch lệ. Trong b&agrave;i viết của vị Ni sư n&oacute;i tr&ecirc;n c&oacute; n&oacute;i rằng: Sở dĩ những tiềm năng tốt đẹp của chư Ni chưa được ph&aacute;t huy l&agrave; v&igrave; c&ograve;n phải &ldquo;chờ nh&acirc;n duy&ecirc;n&rdquo;. Thiết nghĩ nh&acirc;n duy&ecirc;n ấy đ&atilde; đến gần. Với những g&igrave; cho thấy hiện nay t&ocirc;i tin rằng Ni giới nước ta sẽ c&ograve;n lớn mạnh hơn nữa trong tương lai. Sẽ c&oacute; nhiều điều kiện v&agrave; cơ hội để thi thố t&agrave;i năng v&agrave; cống hiến sức m&igrave;nh. C&aacute;c Ni sinh đang ngồi tr&ecirc;n ghế nh&agrave; trường cần n&ecirc;n &yacute; thức được điều n&agrave;y, noi gương &ldquo;<em>Hương cỏ thơm&rdquo;</em>, noi gương c&aacute;c sư chị, phấn đấu kh&ocirc;ng ngừng trau giồi tr&iacute; huệ, để ng&agrave;y mai g&oacute;p sức c&ugrave;ng chư Tăng thắp l&ecirc;n ngọn đuốc PGVN ng&agrave;y c&agrave;ng huy ho&agrave;ng s&aacute;ng lạn.</font></p>\r\n\r\n<p style="text-align: justify;"><font color="#000080" face="Arial" size="2" style="font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">Tiềm năng của Ni giới chắc kh&ocirc;ng ai hiểu r&otilde; hơn ch&iacute;nh bản th&acirc;n họ. B&agrave;i viết n&agrave;y chỉ g&oacute;p th&ecirc;m một tiếng n&oacute;i, như một lời sẻ chia, kh&iacute;ch lệ m&agrave; th&ocirc;i.</font></p>\r\n', 'Đọc "Hương Cỏ Thơm"nghĩ về Ni giới hôm nay', 0),
(46, 10, 'Thái Anh', '2012-06-02 17:00:00', '<p style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">Ng&agrave;y 3-6, tại khu phố T&acirc;n H&ograve;a, phường Đ&ocirc;ng H&ograve;a, thị x&atilde; Dĩ An, tỉnh B&igrave;nh Dương đ&atilde; diễn ra lễ trao quyết định th&agrave;nh lập Thi&ecirc;n Quang ni tự cho Ni sư TN.Hương Nhũ.</p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);"><img alt="thienquang (2).JPG" border="0" height="367" src="http://www.giacngo.vn/UserImages/2012/06/04/10/thienquang%20%282%29.JPG" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="550" /></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);"><font color="#808080" size="2">Chư t&ocirc;n đức chứng minh</font></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);"><img alt="thienquang (3).JPG" border="0" height="316" src="http://www.giacngo.vn/UserImages/2012/06/04/10/thienquang%20%283%29.JPG" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="550" /></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);"><font color="#808080" size="2">Quang cảnh buổi lễ trao quyết định</font></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">Đến dự lễ c&oacute; HT.Th&iacute;ch To&agrave;n Đức, Ủy vi&ecirc;n HĐTS, Ph&oacute; ban Thường trực BTS THPG L&acirc;m Đồng; HT.Th&iacute;ch Gi&aacute;c Quang, Ph&oacute; ban ki&ecirc;m Ch&aacute;nh Thư k&yacute; BTS THPG Đồng Nai; TT.Th&iacute;ch Huệ Th&ocirc;ng, Uỷ vi&ecirc;n HĐTS, Trưởng BTS THPG B&igrave;nh Dương; chư t&ocirc;n đức gi&aacute;o phẩm BTS THPG B&igrave;nh Dương, Ban Đại diện PG thị x&atilde; Dĩ An, Học viện PGVN tại TP.HCM, Ph&acirc;n ban Đặc tr&aacute;ch Ni giới Trung ương, đại diện c&aacute;c tự viện v&agrave; đại diện ch&iacute;nh quyền c&aacute;c cấp của tỉnh B&igrave;nh Dương c&ugrave;ng đ&ocirc;ng đảo Phật tử trong ngo&agrave;i tỉnh.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">Tại buổi lễ, ĐĐ.Th&iacute;ch Minh Nghĩa, Ch&aacute;nh Thư k&yacute; BTS THPG B&igrave;nh Dương đ&atilde; c&ocirc;ng bố Quyết định số 79/QĐ-BTS ng&agrave;y 12-5-2012 của Trưởng BTS THPG B&igrave;nh Dương về việc th&agrave;nh lập Thi&ecirc;n Quang ni tự, tọa lạc tại phường Đ&ocirc;ng H&ograve;a, thị x&atilde; Dĩ An.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">Theo đ&oacute;, Thi&ecirc;n Quang ni tự l&agrave; cơ sở t&iacute;n ngưỡng Phật gi&aacute;o thuộc Gi&aacute;o hội PG Việt Nam (GHPGVN), hoạt động trong khu&ocirc;n khổ Hiến chương, Nội quy Ban Tăng sự Trung ương GHPGVN v&agrave; c&aacute;c quy định hiện h&agrave;nh của Nh&agrave; nước.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">Cũng tại buổi lễ, TT.Th&iacute;ch Huệ Th&ocirc;ng trao Quyết định th&agrave;nh lập Thi&ecirc;n Quang ni tự cho Ni sư TN.Hương Nhũ, người đại diện Gi&aacute;o hội chịu tr&aacute;ch nhiệm quản l&yacute;, điều h&agrave;nh, x&acirc;y dựng ch&ugrave;a, đồng thời ph&aacute;t biểu nhấn mạnh về tầm quan trọng của việc th&agrave;nh lập ng&ocirc;i Tam bảo Thi&ecirc;n Quang đ&atilde; g&oacute;p phần đ&aacute;p ứng nhu cầu sinh hoạt văn h&oacute;a t&acirc;m linh của đ&ocirc;ng đảo Phật tử trong v&agrave; ngo&agrave;i tỉnh cũng như t&acirc;m nguyện hoằng dương Phật ph&aacute;p của Ni sư TN.Hương Nhũ.</p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);"><img alt="thienquang (4).JPG" border="0" height="367" src="http://www.giacngo.vn/UserImages/2012/06/04/10/thienquang%20%284%29.JPG" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="550" /></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);"><font color="#808080" size="2">Chư gi&aacute;o phẩm Ni tham dự lễ</font></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);"><img alt="thienquang (5).JPG" border="0" height="367" src="http://www.giacngo.vn/UserImages/2012/06/04/10/thienquang%20%285%29.JPG" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="550" /></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);"><span style="line-height: 18px; font-family: ''Times New Roman''; font-size: 12pt;"><font color="#808080" size="2">ĐĐ.Th&iacute;ch Minh Nghĩa c&ocirc;ng bố quyết định th&agrave;nh lập Thi&ecirc;n Quang ni tự</font></span></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);"><img alt="1.JPG" border="0" height="363" src="http://www.giacngo.vn/UserImages/2012/06/04/10/1.JPG" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="550" /></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);"><font color="#808080" size="2">TT.Th&iacute;ch Huệ Th&ocirc;ng: &quot;Việc th&agrave;nh lập ng&ocirc;i Tam bảo Thi&ecirc;n Quang đ&atilde; g&oacute;p phần</font><br />\r\n<font color="#808080" size="2">&nbsp;đ&aacute;p ứng nhu cầu sinh hoạt văn h&oacute;a t&acirc;m linh của đ&ocirc;ng đảo Phật tử trong v&agrave; ngo&agrave;i tỉnh&quot;</font></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">Được biết, dưới sự chứng minh của cố HT.Th&iacute;ch Thiện Phước, Sơ tổ s&aacute;ng lập Li&ecirc;n t&ocirc;ng Tịnh độ Non Bồng, Thi&ecirc;n Quang ni tự l&uacute;c đầu c&oacute; t&ecirc;n gọi l&agrave; tịnh thất Thi&ecirc;n Quang do Ni trưởng Th&iacute;ch nữ Huệ Gi&aacute;c, viện chủ Quan &Acirc;m tu viện - TP.Bi&ecirc;n H&ograve;a&nbsp;khai sơn năm 1972 tr&ecirc;n khu đất c&oacute; diện t&iacute;ch hơn 10.000 m&eacute;t vu&ocirc;ng, vừa để l&agrave;m nơi cho chư Ni tu học, vừa để tăng gia sản xuất, tạo nguồn kinh tế phục vụ cho c&ocirc; nhi viện tại Quan &Acirc;m tu viện.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">Ng&agrave;y 4-12-2010, Ni trưởng TN.Huệ Gi&aacute;c giao ph&oacute; cho đệ tử l&agrave; Ni sư TN.Hương Nhũ tr&aacute;ch nhiệm ph&aacute;t triển tịnh thất Thi&ecirc;n Quang trở th&agrave;nh trung t&acirc;m tu học cho chư Ni v&agrave; Phật tử, nhất l&agrave; giới trẻ.</p>\r\n', 'Trao quyết định thành lập Thiên Quang ni tự', 0),
(47, 12, 'Mai Lương Ngọc', '2012-12-03 17:00:00', '<p><b style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);"><font color="#808080">Chủ nhật bạn l&agrave;m g&igrave;? Phải chăng l&agrave; đi chơi đ&acirc;u đ&oacute; như c&agrave; ph&ecirc;, xem phim, ăn uống&hellip; c&ugrave;ng bạn b&egrave;, người y&ecirc;u, hay nghỉ ngơi một m&igrave;nh trong ph&ograve;ng?</font></b><br style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);" />\r\n&nbsp;</p>\r\n\r\n<div style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);"><b>1.</b> Bạn cũng như t&ocirc;i vẫn thường l&agrave;m thế. L&agrave;m thế th&igrave; cũng vui vui, cũng hết thời gian đ&oacute;, rồi lại chuẩn bị tiếp cho một tuần l&agrave;m việc. Đ&aacute;ng lẽ sau một ng&agrave;y nghỉ ngơi ch&uacute;ng ta phải cảm thấy khỏe hơn, sảng kho&aacute;i hơn để tiếp tục l&agrave;m việc, học tập. Nhưng tại sao mọi người vẫn thường n&oacute;i thứ hai l&agrave; ng&agrave;y mệt nhất? Phải chăng sau mỗi cuộc vui chơi đ&acirc;u đ&oacute; l&agrave; muộn phiền v&agrave; niềm vui ấy tồn tại kh&ocirc;ng l&acirc;u.<br />\r\n<br />\r\nMỗi một gi&acirc;y ph&uacute;t tr&ocirc;i qua, mỗi một ng&agrave;y tr&ocirc;i qua, sẽ l&agrave; một th&aacute;ng, một năm... nhiều năm tr&ocirc;i qua như c&acirc;u n&oacute;i: &ldquo;Thời gian như b&oacute;ng c&acirc;u qua cửa sổ&rdquo;. Nh&igrave;n lại qu&aacute; khứ th&igrave; nuối tiếc, nghĩ về tương lai th&igrave; hi vọng v&agrave; xa vời, sống với hiện tại th&igrave; bất an&hellip;<br />\r\n&nbsp;\r\n<div align="center"><img alt="coi nguon.jpg" border="0" height="381" src="http://www.giacngo.vn/UserImages/2012/12/04/11/coi%20nguon.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="570" /><br />\r\n<font color="#808080" size="2">Bạn trẻ, th&agrave;nh vi&ecirc;n Cội Nguồn thiền tọa do sư c&ocirc; Hương Nhũ hướng dẫn - Ảnh: C.Ng</font></div>\r\n<br />\r\nC&aacute;i thời điểm bạn đang đứng hiện tại c&oacute; bao giờ bạn nghĩ ba năm trước bạn từng mơ mộng, chờ đợi v&agrave; suy nghĩ lo lắng, rồi một l&uacute;c kh&aacute;c sau ba năm bạn lại nghĩ tiếp ba năm sau. C&aacute;i v&ograve;ng lẫn quẩn ấy cứ khiến ch&uacute;ng ta phải bơi trong cuộc đời với t&acirc;m trạng bất an v&agrave; mệt mỏi, chẳng bao giờ l&agrave; đủ&hellip;<br />\r\n<br />\r\nNhững nghĩ suy muộn phiền của tuổi trẻ, những ước vọng kh&ocirc;ng th&agrave;nh g&acirc;y đau khổ khiến cho bao người như con c&aacute; bị mắc c&acirc;u, danh vọng tiền t&agrave;i, sắc đẹp như miếng mồi ngon. Con c&aacute; trong v&ocirc; minh tiến lại chỗ nguy hiểm m&agrave; ngỡ sắp chiếm được miếng mồi, rồi th&igrave; người bắt muốn l&agrave;m g&igrave; th&igrave; l&agrave;m: vứt, chi&ecirc;n, luộc, m&oacute;c họng thả ra c&acirc;u tiếp&hellip; Ch&uacute;ng ta cũng thế, nhất l&agrave; khi mắc v&agrave;o lưới t&igrave;nh, v&ugrave;ng vẫy trong miếng mồi của người c&acirc;u, rồi than v&atilde;n o&aacute;n tr&aacute;ch.<br />\r\n<br />\r\nĐau khổ hạnh ph&uacute;c l&agrave; chủ đề mu&ocirc;n thuở của lo&agrave;i người. Từ thế hệ n&agrave;y sang thế hệ kh&aacute;c, biết bao b&agrave;i ph&aacute;p, bao nh&agrave; t&acirc;m l&yacute;, bao nghi&ecirc;n cứu để trị t&acirc;m bệnh cho con người, nhưng con người vẫn đầy rẫy đau khổ v&agrave; bi thương. V&igrave; thế người c&oacute; tri thức chưa hẳn đ&atilde; an lạc, người th&ocirc;ng minh chưa hẳn đ&atilde; sung sướng, người gi&agrave;u chưa hẳn đ&atilde; hạnh ph&uacute;c&hellip; Do đ&oacute;, người Phật tử l&agrave; con của V&ocirc; Thượng Y Vương tức bậc thầy trị &ldquo;t&acirc;m bệnh&rdquo; khổ cho thế gian th&igrave; cũng phải học hạnh của thầy m&igrave;nh: từ, bi, hỷ, xả. Nhưng muốn c&oacute; &ldquo;từ&rdquo; th&igrave; phải c&oacute; tr&iacute; huệ để hiểu v&agrave; thương ch&uacute;ng sanh. Vậy mọi thứ phải bắt đầu từ việc học đạo, h&agrave;nh tr&igrave; ch&aacute;nh ph&aacute;p, hiểu kinh điển Phật dạy, nghe thuyết ph&aacute;p&hellip; c&oacute; ch&aacute;nh kiến, ch&aacute;nh tư duy để diệt ba thứ khổ: tham, s&acirc;n, si trong ch&iacute;nh t&acirc;m m&igrave;nh trước sau đ&oacute; mới hằng gi&uacute;p được ch&uacute;ng sanh.<br />\r\n<br />\r\n<b>2.</b> Cũng nương theo tất cả c&aacute;c nh&acirc;n duy&ecirc;n l&agrave;nh, ng&agrave;y Chủ nhật, 2-12 vừa qua, Hội Khuyến Học Cội Nguồn dưới sự hướng dẫn của thầy Ph&aacute;p Như đ&atilde; đến Thi&ecirc;n Quang Ni tự (huyện Dĩ An, tỉnh B&igrave;nh Dương) do sư c&ocirc; Th&iacute;ch nữ Hương Nhũ l&agrave;m trụ tr&igrave;.<br />\r\n<br />\r\nTại đ&acirc;y đo&agrave;n đ&atilde; nghe sư c&ocirc; Hương Nhũ thuyết ph&aacute;p v&agrave; tập thiền, chủ đề &ldquo;H&atilde;y mỉm cười với cuộc sống&rdquo;. Nghe ph&aacute;p v&agrave; thực tập, c&aacute;c bạn phần n&agrave;o đ&oacute; ngộ ra về những vấn đề tuổi trẻ gặp phải: c&aacute;c mối quan hệ, t&igrave;nh y&ecirc;u, sự nghiệp, sự bất hạnh hay hạnh ph&uacute;c, t&acirc;m an th&igrave; thế giới an. Dưới kh&ocirc;ng gian tĩnh lặng trong m&aacute;t của ng&ocirc;i bảo điện, sự lắng đọng trong b&agrave;i ph&aacute;p của sư c&ocirc; đ&atilde; đưa c&aacute;c bạn trẻ trở lại với th&acirc;n t&acirc;m thanh tịnh, nh&igrave;n lại m&igrave;nh, nh&igrave;n lại chuỗi c&aacute;c mối quan hệ, c&aacute;ch h&agrave;nh xử trước sự vật hiện tượng đến v&agrave; biết rằng m&igrave;nh c&ograve;n may mắn hơn bao nhi&ecirc;u người tr&ecirc;n thế giới n&agrave;y.<br />\r\n<br />\r\nM&igrave;nh c&oacute; thời gian để ngồi thiền, nghe ph&aacute;p, c&oacute; đ&ocirc;i tai th&iacute;nh để lắng nghe v&agrave; hiểu, c&oacute; đ&ocirc;i ch&acirc;n khỏe mạnh để đi đến nơi n&agrave;o m&igrave;nh muốn, c&oacute; đ&ocirc;i mắt s&aacute;ng để ngắm nh&igrave;n tinh hoa của đất trời l&agrave; thi&ecirc;n nhi&ecirc;n, l&agrave; bạn hiền, l&agrave; th&acirc;n bằng quyến thuộc, l&agrave; những người y&ecirc;u thương&hellip;<br />\r\n<br />\r\nĐồng thời m&igrave;nh cũng thấm hiểu được sự v&ocirc; thường của cuộc sống v&agrave; th&acirc;n ta để xả bỏ dần dần những muộn phiền, căm giận như tr&uacute;t hơi thở đi ra: &ldquo;Ta h&iacute;t v&agrave;o l&agrave; tinh hoa của đất trời, ta thở ra l&agrave; muộn phiền ti&ecirc;u cực&rdquo; như c&acirc;u qu&aacute;n chiếu l&uacute;c ngồi thiền sư c&ocirc; hướng dẫn.<br />\r\n<br />\r\nBằng những ng&agrave;y Chủ nhật như thế n&agrave;y ch&uacute;ng t&ocirc;i như được kho&aacute;c l&ecirc;n m&igrave;nh chiếc &aacute;o mới, chiếc &aacute;o của m&ugrave;a xu&acirc;n, xu&acirc;n trong cửa thiền, xu&acirc;n nơi th&acirc;n t&acirc;m nơi đất trời thanh tịnh&hellip; c&oacute; bạn b&egrave; đồng tu, c&oacute; m&aacute;i ch&ugrave;a thi&ecirc;ng li&ecirc;ng l&ograve;ng người như b&igrave;nh thản hơn.</div>\r\n', 'Có một ngày Chủ nhật khác', 0);
INSERT INTO `chuathienquang_news` (`id`, `id_category`, `author`, `date`, `content`, `title`, `type`) VALUES
(57, 14, 'Trí Tánh', '2012-12-11 17:00:00', '<blockquote style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);" type="cite">\r\n<div>\r\n<p><strong><font color="#808080">Đ&acirc;y c&oacute; thể coi l&agrave; một trong những di sản lớn nhất về đạo Phật c&ograve;n lưu giữ được đến ng&agrave;y nay.</font></strong></p>\r\n\r\n<p>Nằm ẩn m&igrave;nh trong khu rừng rậm hoang vu của cao nguy&ecirc;n Deccan (Ấn Độ), hang động Ajanta đ&atilde; bị l&atilde;ng qu&ecirc;n trong một thời gian d&agrave;i.</p>\r\n\r\n<p>V&agrave;o năm 1819, một nh&oacute;m người Anh đi săn cọp trong khu vực n&agrave;y đ&atilde; t&igrave;nh cờ ph&aacute;t hiện hệ thống hang động Ajanta. Quần thể hang động Ajanta l&agrave; di t&iacute;ch qu&yacute; gi&aacute; đại diện cho thời kỳ Hưng thịnh của Phật gi&aacute;o tại Ấn Độ.</p>\r\n\r\n<p align="center"><img alt="ajanta-hang-dong-ando-teeniscover-kenh14-01-08c3c_jpg_1.jpg" border="0" height="335" src="http://www.giacngo.vn/UserImages/2012/12/12/10/ajanta-hang-dong-ando-teeniscover-kenh14-01-08c3c_jpg_1.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="550" /></p>\r\n\r\n<p>Ajanta tọa lạc ở lưng chừng n&uacute;i, b&ecirc;n dưới hang động l&agrave; l&ograve;ng vực c&ugrave;ng d&ograve;ng s&ocirc;ng Waghora uốn kh&uacute;c. Hang động được bố tr&iacute; theo dạng h&igrave;nh m&oacute;ng ngựa, kho&eacute;t s&acirc;u v&agrave;o b&ecirc;n trong v&aacute;ch n&uacute;i đ&aacute; thẳng đứng cao 76m.</p>\r\n\r\n<p>Di t&iacute;ch hang động Ajanta c&oacute; tất cả 30 hang, bao gồm Th&aacute;nh đường Phật gi&aacute;o v&agrave; c&aacute;c khu phụ cận. C&aacute;c nh&agrave; khảo cổ đ&atilde; đ&aacute;nh dấu c&aacute;c hang theo số thứ tự.</p>\r\n\r\n<p align="center"><img alt="ajanta-hang-dong-ando-teeniscover-kenh14-02-08c3c_jpg.jpg" border="0" height="336" src="http://www.giacngo.vn/UserImages/2012/12/12/10/ajanta-hang-dong-ando-teeniscover-kenh14-02-08c3c_jpg.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="550" /></p>\r\n\r\n<p>Quần thể hang động Ajanta kh&ocirc;ng được tạo dựng trong một thời kỳ li&ecirc;n tục n&ecirc;n c&aacute;c nh&agrave; nghi&ecirc;n cứu ph&acirc;n chia c&aacute;c hang động theo từng thời kỳ x&acirc;y .</p>\r\n\r\n<p>N&oacute; gồm hai phần: cụm hang được tạo dựng v&agrave;o giai đoạn đầu v&agrave;o thế kỷ II TCN gồm hang số 8, 9, 10, 12, 13, 15A mang m&agrave;u sắc của Phật gi&aacute;o nguy&ecirc;n thủy.</p>\r\n\r\n<p>Cụm hang sau được x&acirc;y dựng v&agrave;o thế kỷ V, mang m&agrave;u sắc Phật gi&aacute;o mới. Tuy nhi&ecirc;n, một số hang vẫn c&ograve;n chưa được ho&agrave;n tất.</p>\r\n\r\n<p align="center"><img alt="ajanta-hang-dong-ando-teeniscover-kenh14-03-08c3c_jpg.jpg" border="0" height="371" src="http://www.giacngo.vn/UserImages/2012/12/12/10/ajanta-hang-dong-ando-teeniscover-kenh14-03-08c3c_jpg.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="550" /></p>\r\n\r\n<p>D&ugrave; vẫn c&ograve;n một số hang vẫn chưa được ho&agrave;n th&agrave;nh, Ajanta vẫn l&agrave; quần thể ch&ugrave;a hang v&ocirc; c&ugrave;ng lớn tr&ecirc;n thế giới do b&agrave;n tay con người tạo ra.</p>\r\n\r\n<p align="center"><img alt="ajanta-hang-dong-ando-teeniscover-kenh14-04-08c3c_jpg.jpg" border="0" height="336" src="http://www.giacngo.vn/UserImages/2012/12/12/10/ajanta-hang-dong-ando-teeniscover-kenh14-04-08c3c_jpg.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="550" /></p>\r\n\r\n<p>Một trong những điểm đặc biệt của quần thể hang động n&agrave;y ch&iacute;nh l&agrave; một số lượng khổng lồ c&aacute;c bức tượng đi&ecirc;u khắc về đạo Phật. Đ&acirc;y được coi l&agrave; những t&aacute;c phẩm hội họa đẹp nhất của nghệ thuật Phật gi&aacute;o.</p>\r\n\r\n<p align="center"><img alt="ajanta-hang-dong-ando-teeniscover-kenh14-05-08c3c_jpg.jpg" border="0" height="369" src="http://www.giacngo.vn/UserImages/2012/12/12/10/ajanta-hang-dong-ando-teeniscover-kenh14-05-08c3c_jpg.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="550" /></p>\r\n\r\n<p>Ngay những bước ch&acirc;n đầu ti&ecirc;n tham quan quần thể kiến tr&uacute;c n&agrave;y, du kh&aacute;ch c&oacute; thể bắt gặp những khung cảnh hết sức vĩ đại.</p>\r\n\r\n<p>&ldquo;Mặt tiền&rdquo; của hang động, h&agrave;ng d&agrave;i tượng Phật được đục đẽo bằng tay v&ocirc; c&ugrave;ng kh&eacute;o l&eacute;o ngay tr&ecirc;n bề mặt của v&aacute;ch đ&aacute;, hay cả một điện thờ với những cột trụ v&ocirc; c&ugrave;ng chắc chắn được tạo n&ecirc;n bởi c&aacute;c nghệ nh&acirc;n ngay trong l&ograve;ng n&uacute;i đ&aacute;.</p>\r\n\r\n<p align="center"><img alt="ajanta-hang-dong-ando-teeniscover-kenh14-16-9dd38_jpg.jpg" border="0" height="797" src="http://www.giacngo.vn/UserImages/2012/12/12/10/ajanta-hang-dong-ando-teeniscover-kenh14-16-9dd38_jpg.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="550" /></p>\r\n\r\n<p>Hang số 12 được đẽo trong thời kỳ đầu v&agrave; được gọi l&agrave; Tăng viện; đ&acirc;y l&agrave; nơi sinh sống của những thầy tu ng&agrave;y xưa.</p>\r\n\r\n<p>Họ sinh hoạt v&agrave; tu h&agrave;nh trong kh&ocirc;ng gian chật hẹp. C&aacute;c thầy tu d&ugrave;ng bệ đ&aacute; l&agrave;m giường ngủ, họ lu&ocirc;n tu&acirc;n thủ trong qu&aacute; tr&igrave;nh tu h&agrave;nh khổ hạnh theo c&aacute;ch như thế.</p>\r\n\r\n<p align="center"><img alt="ajanta-hang-dong-ando-teeniscover-kenh14-13-08c3c_jpg.jpg" border="0" height="412" src="http://www.giacngo.vn/UserImages/2012/12/12/10/ajanta-hang-dong-ando-teeniscover-kenh14-13-08c3c_jpg.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="550" /></p>\r\n\r\n<p>Hang số 9 c&oacute; t&ecirc;n gọi l&agrave; Th&aacute;p viện, l&agrave; một căn ph&ograve;ng d&ugrave;ng l&agrave;m nơi cầu nguyện của c&aacute;c nh&agrave; tu h&agrave;nh. Th&aacute;p viện mang &yacute; nghĩa rất linh thi&ecirc;ng, l&agrave; nơi t&ocirc;n thờ th&aacute;nh t&iacute;ch của Đức Phật.</p>\r\n\r\n<p>Thời kỳ n&agrave;y ho&agrave;n to&agrave;n kh&ocirc;ng c&oacute; sự hiện diện của c&aacute;c tượng Phật. V&igrave; vậy, Th&aacute;p Phật được xem l&agrave; một biểu tượng linh thi&ecirc;ng để t&ocirc;n thờ v&agrave; phụng sự.</p>\r\n\r\n<p align="center"><img alt="ajanta-hang-dong-ando-teeniscover-kenh14-15-9dd38_jpg.jpg" border="0" height="374" src="http://www.giacngo.vn/UserImages/2012/12/12/10/ajanta-hang-dong-ando-teeniscover-kenh14-15-9dd38_jpg.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="550" /></p>\r\n\r\n<p>Nh&oacute;m hang động thứ 2, tượng Phật hiện diện khắp nơi. C&oacute; tượng Phật ngồi thể hiện rằng, đ&atilde; đạt được sự gi&aacute;c ngộ dưới cội bồ đề, xung quanh l&agrave; sự c&aacute;m dỗ của quỷ ma.</p>\r\n\r\n<p>Trước khi gi&aacute;c ngộ, Đức Phật đ&atilde; trải qua nhiều giai đoạn tu luyện, tất cả đều được thể hiện qua h&agrave;ng loạt t&aacute;c phẩm đi&ecirc;u khắc trong hang.</p>\r\n\r\n<p align="center"><img alt="ajanta-hang-dong-ando-teeniscover-kenh14-10-08c3c_jpg.jpg" border="0" height="334" src="http://www.giacngo.vn/UserImages/2012/12/12/10/ajanta-hang-dong-ando-teeniscover-kenh14-10-08c3c_jpg.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="550" /></p>\r\n\r\n<p>C&oacute; được c&aacute;c t&aacute;c phẩm tượng Phật đạt đến vẻ đẹp đỉnh cao như thế l&agrave; nhờ v&agrave;o c&aacute;c họa sĩ v&agrave; nh&agrave; đi&ecirc;u khắc đ&atilde; thấm nhuần Phật ph&aacute;p. Ch&iacute;nh họ đ&atilde; g&oacute;p phần rất lớn tạo n&ecirc;n gi&aacute; trị cho quần thể hang động Ajanta.</p>\r\n\r\n<p align="center"><img alt="ajanta-hang-dong-ando-teeniscover-kenh14-07-08c3c_jpg.jpg" border="0" height="412" src="http://www.giacngo.vn/UserImages/2012/12/12/10/ajanta-hang-dong-ando-teeniscover-kenh14-07-08c3c_jpg.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="550" /></p>\r\n\r\n<p>Từ thế kỷ thứ VI đến thế kỷ VII l&agrave; giai đoạn ph&aacute;t triển của nghệ thuật Phật gi&aacute;o tại hang động Ajanta. Đ&oacute; l&agrave; những bức b&iacute;ch họa được vẽ tr&ecirc;n tường trong hang động.</p>\r\n\r\n<p align="center"><img alt="ajanta-hang-dong-ando-teeniscover-kenh14-09-08c3c_jpg.jpg" border="0" height="336" src="http://www.giacngo.vn/UserImages/2012/12/12/10/ajanta-hang-dong-ando-teeniscover-kenh14-09-08c3c_jpg.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="550" /></p>\r\n\r\n<p>C&aacute;ch m&agrave; những người thợ d&ugrave;ng để tạo ra những t&aacute;c phẩm của m&igrave;nh cũng v&ocirc; c&ugrave;ng độc đ&aacute;o. Theo một số t&agrave;i liệu, nguy&ecirc;n liệu ch&iacute;nh để l&agrave;m n&ecirc;n những bức vẽ ch&iacute;nh l&agrave; đ&aacute; cuội v&agrave; c&aacute;c loại rau quả.</p>\r\n\r\n<p align="center"><img alt="ajanta-hang-dong-ando-teeniscover-kenh14-17-608e9_jpg.jpg" border="0" height="330" src="http://www.giacngo.vn/UserImages/2012/12/12/10/ajanta-hang-dong-ando-teeniscover-kenh14-17-608e9_jpg.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="550" /></p>\r\n\r\n<p>Rau quả được nghiền n&aacute;t ra để tạo th&agrave;nh 1 chất keo, sau đ&oacute; được nghiền tiếp với c&aacute;c vi&ecirc;n sỏi đầy m&agrave;u sắc để tạo ra một loại &ldquo;sơn&rdquo; cho những t&aacute;c phẩm n&agrave;y.</p>\r\n\r\n<p>Minh chứng r&otilde; nhất ch&iacute;nh l&agrave; những h&igrave;nh tượng Phật tr&ecirc;n những bức tường ở hang số 10 d&ugrave; đ&atilde; trải qua nhiều thế kỷ nhưng vẫn kh&ocirc;ng hề bị phai m&agrave;u.</p>\r\n\r\n<p align="center"><img alt="ajanta-hang-dong-ando-teeniscover-kenh14-08-08c3c_jpg.jpg" border="0" height="334" src="http://www.giacngo.vn/UserImages/2012/12/12/10/ajanta-hang-dong-ando-teeniscover-kenh14-08-08c3c_jpg.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="550" /></p>\r\n\r\n<p>C&oacute; thể n&oacute;i, nghệ thuật hội họa của người Ấn Độ cổ đại đ&atilde; đạt đến chất lượng ho&agrave;n hảo về m&agrave;u sắc cũng như bố cục. C&aacute;c t&aacute;c phẩm n&agrave;y đ&atilde; qua cuộc thử nghiệm th&agrave;nh c&ocirc;ng của thời gian.</p>\r\n\r\n<p align="center"><img alt="ajanta-hang-dong-ando-teeniscover-kenh14-06-08c3c_jpg.jpg" border="0" height="333" src="http://www.giacngo.vn/UserImages/2012/12/12/10/ajanta-hang-dong-ando-teeniscover-kenh14-06-08c3c_jpg.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="550" /></p>\r\n\r\n<p>Quần thể ch&ugrave;a hang Ajanta l&agrave; di t&iacute;ch lịch sử lớn nhất thế giới đ&atilde; được UNESCO c&ocirc;ng nhận v&agrave;o năm 1983. Đ&acirc;y c&oacute; thể coi l&agrave; một trong những di sản lớn nhất về đạo Phật c&ograve;n lưu giữ được đến ng&agrave;y nay.</p>\r\n</div>\r\n</blockquote>\r\n', 'Choáng ngợp hàng nghìn tượng Phật thiêng xuyên núi', 0),
(62, 12, 'Liên Hoa', '2013-01-16 17:00:00', '<p><b style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);"><font color="#808080">Người giả danh l&agrave; Phạm Hữu Đức (41 tuổi, tr&uacute; P.1, Q.5, TP.HCM) đ&atilde; lừa c&aacute;c ch&ugrave;a: Chiếu Minh, Phước Thiện, Minh Sư (H.B&igrave;nh Ch&aacute;nh, TP.HCM).</font></b><br style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);" />\r\n<br style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);" />\r\n<span style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); display: inline !important; float: none;">Theo đ&oacute;, với thủ đoạn giả danh nh&acirc;n vi&ecirc;n điện lực, ng&agrave;y 29-12-2012, Phạm Hữu Đức đ&atilde; tới ch&ugrave;a Chiếu Minh (x&atilde; B&igrave;nh Ch&aacute;nh), y&ecirc;u cầu qu&yacute; Ni ở đ&acirc;y l&agrave;m hợp đồng thay t&ecirc;n người sử dụng điện kế với chi ph&iacute; gần 500.000 đồng.</span><br style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);" />\r\n&nbsp;</p>\r\n\r\n<div align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);"><font color="#A9A9A9" size="2"><img alt="doi tuong lua dao.jpg" border="0" height="433" src="http://www.giacngo.vn/UserImages/2013/01/17/11/doi%20tuong%20lua%20dao.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="570" /><br />\r\n<font color="#808080">Bị can Phạm Hữu Đức <font size="2">- <font size="2">Ảnh: H.Long</font></font></font></font></div>\r\n\r\n<p><br style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);" />\r\n<span style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); display: inline !important; float: none;">Tương tự, Đức đến ch&ugrave;a Phước Thiện (x&atilde; B&igrave;nh Ch&aacute;nh) y&ecirc;u cầu l&agrave;m hợp đồng mua b&aacute;n điện thay t&ecirc;n sử dụng v&agrave; gi&aacute; điện thấp hơn quy định với ph&iacute; hơn 3 triệu đồng.</span></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">Tiếp tục thủ đoạn tr&ecirc;n, s&aacute;ng 3-1-2013, Phạm Hữu Đức đến ch&ugrave;a Minh Sư (x&atilde; T&acirc;n Qu&yacute; T&acirc;y) v&agrave; lừa 500.000 đồng. Ngo&agrave;i ra, Đức y&ecirc;u cầu nh&agrave; ch&ugrave;a nộp 1.780.000 đồng để l&agrave;m hồ sơ giảm tiền điện nhưng nh&agrave; ch&ugrave;a đ&atilde; khất hẹn do chưa c&oacute; tiền.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">Vụ việc bị ph&aacute;t hiện khi trụ tr&igrave; ch&ugrave;a Minh Sư đem sự việc kể với mọi người. Chiều 12-1, khi Phạm Hữu Đức đến nhận tiền tại ch&ugrave;a Minh Sư th&igrave; bị c&ocirc;ng an bắt quả tang.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">H&ocirc;m qua, 16-1, Cơ quan cảnh s&aacute;t điều tra C&ocirc;ng an huyện B&igrave;nh Ch&aacute;nh cho biết đ&atilde; khởi tố vụ &aacute;n, khởi tố bị can v&agrave; bắt tạm giam Phạm Hữu Đức về tội &ldquo;lừa đảo chiếm đoạt t&agrave;i sản&rdquo;.</p>\r\n', 'Giả nhân viên điện lực lừa các chùa', 0),
(63, 12, 'Cao Phong (SGGP)', '2013-01-14 17:00:00', '<p><strong style="color: rgb(128, 128, 128); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">Trong những ng&agrave;y qua, c&acirc;u chuyện ph&ograve;ng karaoke, nh&agrave; h&agrave;ng tổ chức tiệc cưới kh&ocirc;ng chỉ g&acirc;y ồn &agrave;o cho ch&ugrave;a Dơi m&agrave; c&ograve;n g&acirc;y ồn &agrave;o cho cả dư luận.</strong></p>\r\n\r\n<p align="justify" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">Sự việc bắt đầu từ năm 2011, nhưng đến khi Bộ VH-TT-DL lập đo&agrave;n kiểm tra để b&aacute;o c&aacute;o t&igrave;nh h&igrave;nh, đề xuất hướng xử l&yacute; th&igrave; dư luận lại n&oacute;ng l&ecirc;n. Dư luận &ldquo;ồn &agrave;o&rdquo; kh&ocirc;ng chỉ l&agrave; chuyện c&aacute;c ph&ograve;ng karaoke hay nh&agrave; h&agrave;ng n&agrave;y c&oacute; t&aacute;c động đến m&ocirc;i trường sống của dơi hay kh&ocirc;ng, m&agrave; c&ograve;n ở chỗ c&oacute; n&ecirc;n đặt một dự &aacute;n du lịch - nh&agrave; h&agrave;ng - kh&aacute;ch sạn (với quy m&ocirc; kh&aacute;ch sạn 100 ph&ograve;ng, nh&agrave; h&agrave;ng c&oacute; khả năng phục vụ 1.200 kh&aacute;ch)&hellip; kế một ng&ocirc;i ch&ugrave;a m&agrave; nhiều người chọn để l&agrave;m điểm du lịch t&acirc;m linh!</p>\r\n\r\n<p align="justify" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);"><font color="#808080" size="2"><img alt="truoc chua Doi.jpg" border="0" height="383" src="http://www.giacngo.vn/UserImages/2013/01/15/11/truoc%20chua%20Doi.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="570" /></font></p>\r\n\r\n<div align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);"><font color="#808080" size="2">Cảnh ồn &agrave;o, b&aacute;t nh&aacute;o xung quanh ch&ugrave;a Dơi - Ảnh: VnExpress</font></div>\r\n\r\n<p align="justify" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">Từ l&acirc;u, ch&ugrave;a Dơi l&agrave; một n&eacute;t văn h&oacute;a, một thương hiệu du lịch t&acirc;m linh gắn với địa danh S&oacute;c Trăng. T&ecirc;n của ng&ocirc;i ch&ugrave;a l&agrave; Mahatup hay c&ograve;n l&agrave; ch&ugrave;a M&atilde; Tộc. Ng&ocirc;i ch&ugrave;a n&agrave;y được x&acirc;y dựng c&aacute;ch đ&acirc;y gần 450 năm (1569). N&eacute;t độc đ&aacute;o của n&oacute; ch&iacute;nh l&agrave; ng&ocirc;i ch&ugrave;a duy nhất trong số gần 100 ng&ocirc;i ch&ugrave;a tại S&oacute;c Trăng c&oacute; sự lưu tr&uacute; của h&agrave;ng ng&agrave;n con dơi b&aacute;m c&aacute;c c&acirc;y sao, dầu&hellip; trồng xung quanh ch&ugrave;a.</p>\r\n\r\n<p align="justify" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">Theo c&aacute;c nh&agrave; khoa học, dơi lưu tr&uacute; tại ng&ocirc;i ch&ugrave;a l&agrave; lo&agrave;i dơi ngựa lớn v&agrave; nhiều lo&agrave;i dơi lớn kh&aacute;c. Do lo&agrave;i dơi n&agrave;y hay ăn hoa quả n&ecirc;n người d&acirc;n địa phương gọi l&agrave; dơi quạ. Theo đ&oacute;, người d&acirc;n quen gọi t&ecirc;n ch&ugrave;a l&agrave; &ldquo;ch&ugrave;a Dơi&rdquo;. Thời cao điểm ng&ocirc;i ch&ugrave;a thu h&uacute;t hơn 1 triệu con dơi v&agrave; c&aacute;c loại chim, c&ograve;&hellip; sinh sống. Thế nhưng, sau sự ra đi của c&aacute;c lo&agrave;i chim, c&ograve; nay đ&agrave;n dơi đang thưa thớt.</p>\r\n\r\n<p align="justify" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">C&aacute;ch đ&acirc;y v&agrave;i th&aacute;ng người viết đ&atilde; c&oacute; dịp t&igrave;m hiểu nguy&ecirc;n nh&acirc;n v&igrave; sao ch&ugrave;a Dơi lại thưa vắng dần dơi! Một vị sư ở ch&ugrave;a cho biết: &ldquo;Trước năm 2000 số lượng dơi rất nhiều, l&uacute;c đ&oacute; 10 phần hiện nay chỉ c&ograve;n 1-2 phần. Nguy&ecirc;n nh&acirc;n chủ yếu do nhiều người săn bắt b&aacute;n cho c&aacute;c qu&aacute;n x&aacute; chế biến l&agrave;m m&oacute;n ăn. Ch&uacute;ng t&ocirc;i mong ch&iacute;nh quyền c&oacute; giải ph&aacute;p ngăn chặn c&aacute;c nh&agrave; h&agrave;ng b&aacute;n m&oacute;n ăn chế biến từ dơi quạ&rdquo;. Chuyện đ&agrave;n dơi giảm nhanh số lượng ai cũng biết nguy&ecirc;n nh&acirc;n ch&iacute;nh do đặc t&iacute;nh di chuyển xa để t&igrave;m thức ăn (di chuyển từ 60 - 100km), người d&acirc;n địa phương ở c&aacute;c tỉnh l&acirc;n cận như Tr&agrave; Vinh, Bạc Li&ecirc;u&hellip; cũng săn dơi quạ b&aacute;n cho c&aacute;c qu&aacute;n nhậu, nh&agrave; h&agrave;ng&hellip;<br />\r\n<br />\r\nCh&ugrave;a Dơi l&agrave; một trong những c&ocirc;ng tr&igrave;nh nghệ thuật kiến tr&uacute;c ti&ecirc;u biểu của Phật gi&aacute;o Nam t&ocirc;ng Khmer Nam bộ v&agrave; thuộc v&agrave;o h&agrave;ng c&aacute;c ng&ocirc;i ch&ugrave;a cổ nhất của tỉnh S&oacute;c Trăng. Mới đ&acirc;y, ch&ugrave;a Dơi được Hiệp hội Du lịch ĐBSCL chọn l&agrave; một trong 7 điểm dừng ch&acirc;n ti&ecirc;u biểu của v&ugrave;ng ĐBSCL. Đường v&agrave;o ch&ugrave;a Dơi được x&acirc;y dựng mở rộng, khang trang, tho&aacute;ng m&aacute;t l&agrave; một nỗ lực đ&aacute;ng ghi nhận của tỉnh S&oacute;c Trăng gần đ&acirc;y.</p>\r\n\r\n<p align="justify" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">Song, dư luận cho rằng, m&ocirc;i trường ồn &agrave;o đ&atilde; t&aacute;c động đến đ&agrave;n dơi! C&aacute;ch đ&acirc;y khoảng 5 th&aacute;ng, ch&uacute;ng t&ocirc;i đ&atilde; đặt vấn đề n&agrave;y v&agrave; &ocirc;ng L&ecirc; Th&agrave;nh Tr&iacute;, Ph&oacute; Chủ tịch UBND tỉnh S&oacute;c Trăng, cho biết: Tỉnh đ&atilde; chỉ đạo ng&agrave;nh chức năng nghi&ecirc;n cứu đ&aacute;nh gi&aacute; t&aacute;c động m&ocirc;i trường xem c&oacute; ảnh hưởng đến đ&agrave;n dơi sinh sống tại ch&ugrave;a Dơi hay kh&ocirc;ng!<br />\r\n<br />\r\nHơn 10 năm trước, dơi quạ treo đầy tr&ecirc;n c&aacute;c c&acirc;y từ cổng v&agrave;o ng&ocirc;i ch&ugrave;a. Nhưng giờ đ&acirc;y, một chi tiết cần lưu &yacute;, nếu quan s&aacute;t kỹ c&aacute;c c&acirc;y gần cổng ch&ugrave;a thưa vắng dơi, bởi đ&agrave;n dơi đ&atilde; thu gọn lại đeo b&aacute;m ở v&agrave;i c&acirc;y gần nơi c&aacute;c sư trong ch&ugrave;a nghỉ. Điều n&agrave;y &ldquo;tr&ugrave;ng khớp&rdquo; với l&yacute; giải của một nh&agrave; khoa học ở Trường ĐH Cần Thơ l&agrave; &ldquo;Dơi l&agrave; động vật hoang d&atilde;, cần sự y&ecirc;n tĩnh, nếu bị con người t&aacute;c động qu&aacute; nhiều th&igrave; ch&uacute;ng sẽ kh&ocirc;ng c&ograve;n gần gũi với con người&rdquo;!<br />\r\n<br />\r\nDư luận b&aacute;o ch&iacute; vừa qua &ldquo;ồn &agrave;o&rdquo;, phải chăng c&oacute; g&igrave; đ&oacute; thiếu thuyết phục của một dự &aacute;n nh&agrave; h&agrave;ng - kh&aacute;ch sạn gần ch&ugrave;a. V&igrave; khi trả lời b&aacute;o ch&iacute;, &ocirc;ng Mai Khương, Chủ tịch HĐND tỉnh S&oacute;c Trăng, cho biết bản th&acirc;n &ocirc;ng l&agrave; ủy vi&ecirc;n Ban Thường vụ Tỉnh ủy cũng chưa được UBND tỉnh đưa vấn đề n&agrave;y ra b&agrave;n ở Ban Thường vụ. Kh&ocirc;ng qu&aacute; muộn để S&oacute;c Trăng đ&aacute;nh gi&aacute;, kiểm tra lại c&aacute;c c&ocirc;ng đoạn cần thiết của dự &aacute;n n&agrave;y đ&uacute;ng hay kh&ocirc;ng, c&oacute; t&aacute;c động đến đ&agrave;n dơi hay kh&ocirc;ng?</p>\r\n\r\n<p align="justify" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">Trong khi chờ kết luận của Bộ VH-TT-DL, ch&uacute;ng ta nghĩ liệu c&oacute; ph&ugrave; hợp kh&ocirc;ng khi một c&ocirc;ng tr&igrave;nh kh&aacute; ho&agrave;nh tr&aacute;ng, mở karaoke kh&ocirc;ng ph&eacute;p, tiệc cưới&hellip; trước một ng&ocirc;i ch&ugrave;a m&agrave; du kh&aacute;ch d&agrave;nh nhiều t&igrave;nh cảm để gởi gắm t&acirc;m linh?</p>\r\n', '“Ngã mặn” trước cổng chùa Dơi', 0),
(70, 13, 'Thái Anh', '2012-07-21 17:00:00', '<p style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);"><strong>GNO</strong> - Ng&agrave;y 21-7, tại Thi&ecirc;n Quang Ni tự (phường Đ&ocirc;ng H&ograve;a, thị x&atilde; Dĩ An, tỉnh B&igrave;nh Dương), Sư c&ocirc; TN.Hương Nhũ, trụ tr&igrave; ch&ugrave;a đ&atilde; tổ chức trao tặng 100 phần qu&agrave; cho người m&ugrave; v&agrave; người c&oacute; ho&agrave;n cảnh kh&oacute; khăn tại địa phương.</p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);"><img alt="tangqua1.JPG" border="0" height="354" src="http://www.giacngo.vn/UserImages/2012/07/22/10/tangqua1.JPG" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="550" /></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);"><font color="#808080" size="2">Ni sư TN.Hương Nhũ tặng qu&agrave; cho người khiếm thị - Ảnh: Th&aacute;i Anh</font></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">Mỗi phần qu&agrave; trị gi&aacute; 300.000đ gồm thực phẩm v&agrave; tiền mặt do nh&oacute;m Phật tử Ch&iacute; T&acirc;m, hiện đang định cư tại Canada t&agrave;i trợ.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">Đ&acirc;y l&agrave; kh&ocirc;ng chỉ l&agrave; hoạt động nhằm chia sẻ những kh&oacute; khăn đối với c&aacute;c đối tượng c&oacute; ho&agrave;n cảnh k&eacute;m may mắn m&agrave; c&ograve;n l&agrave; cơ duy&ecirc;n để Ni sư Hương Nhũ hướng họ về với &aacute;nh s&aacute;ng Phật ph&aacute;p nhiệm mầu.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">Thi&ecirc;n Quang Ni tự được THPG B&igrave;nh Dương quyết định th&agrave;nh lập ng&agrave;y 12-5 vừa qua, nhưng đ&atilde; c&oacute; c&aacute;c hoạt động từ thiện x&atilde; hội đầy &yacute; nghĩa, g&oacute;p một phần nhỏ c&ugrave;ng địa phương chăm lo đời sống cho những mảnh đời kh&oacute; khăn, bất hạnh.</p>\r\n', 'Thiên Quang Ni tự tặng 100 phần quà cho người mù', 0),
(71, 10, 'L.Đ.L', '2012-10-05 17:00:00', '<p style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);"><strong>GNO - <font color="#808080">Đ&oacute; l&agrave; chủ đề album nhạc Phật gi&aacute;o vol3 của ca sĩ - MC H&ugrave;ng Thanh. Anh cho biết, trong album sẽ c&oacute; ca kh&uacute;c &quot;Một ng&agrave;y vui&quot; được H&ugrave;ng Thanh thực hiện phần ghi h&igrave;nh c&ugrave;ng 300 người m&ugrave; trong chương tr&igrave;nh từ thiện - kh&oacute;a tu d&agrave;nh cho người khiếm thị do SC.Th&iacute;ch nữ Hương Nhũ tổ chức tại Thi&ecirc;n Quang Ni Tự.</font></strong></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);">H&ugrave;ng Thanh cho biết th&ecirc;m, tương lai anh sẽ tạo đi&ecirc;̀u ki&ecirc;̣n đ&ecirc;̉ những người khi&ecirc;́m thị tr&igrave;nh di&ecirc;̃n với m&igrave;nh trong c&aacute;c chương tr&igrave;nh ca nhạc Ph&acirc;̣t gi&aacute;o...</p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);"><font color="#808080" size="2"><img alt="hUNG tHANH 2.jpg" border="0" height="378" src="http://www.giacngo.vn/UserImages/2012/10/06/11/hUNG%20tHANH%202.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="570" /></font></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);"><font color="#808080" size="2">Album sẽ c&oacute; sự tham gia của những người khiếm thị </font></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);"><font color="#808080" size="2"><img alt="hUNG tHANH 1.jpg" border="0" height="378" src="http://www.giacngo.vn/UserImages/2012/10/06/11/hUNG%20tHANH%201.jpg" style="padding: 1px; border: 1px solid rgb(229, 229, 229);" width="570" /></font></p>\r\n\r\n<p align="center" style="color: rgb(0, 0, 0); font-family: arial, tahoma, verdana; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);"><font color="#808080" size="2">Ca sĩ H&ugrave;ng Thanh v&agrave; một em khiếm thị - Ảnh: H.Thanh cung cấp</font></p>\r\n', 'Đóa hoa kỳ diệu', 0);
INSERT INTO `chuathienquang_news` (`id`, `id_category`, `author`, `date`, `content`, `title`, `type`) VALUES
(72, 16, 'Hiền Huy Hòa Hiệp', '2011-01-09 17:00:00', '<p style="color: rgb(0, 0, 0); font-family: Arial,Helvetica,sans-serif; font-size: 16px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 25.5938px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; background-color: rgb(255, 255, 255); text-align: justify;"><strong>Những sinh vi&ecirc;n học sinh c&ograve;n rất trẻ m&agrave; đ&atilde; hướng về Phật ph&aacute;p v&agrave; hơn thế nữa l&agrave; muốn xuất gia th&igrave; phải n&oacute;i rằng đ&oacute; l&agrave; một c&aacute;i duy&ecirc;n tốt đẹp được gieo trồng nhiều đời. Nhiều em nhận thức được những sự giả dối, tham &aacute;i, bon chen, ganh tỵ, sự bất to&agrave;n của đời sống, sự v&ocirc; thường&hellip;l&agrave; nguy&ecirc;n nh&acirc;n đưa đến khổ đau n&ecirc;n muốn tho&aacute;t ly để t&igrave;m một lối đi mới, một hướng sống mới cao đẹp hơn.</strong></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial,Helvetica,sans-serif; font-size: 16px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 25.5938px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; background-color: rgb(255, 255, 255); text-align: justify;"><strong><em>Hiền Huy H&ograve;a Hiệp</em></strong><strong><em>:</em></strong><strong><em> Con ch&agrave;o C&ocirc;. Con được biết về C&ocirc; qua nhiều trang Website v&agrave; một số VCD đặc biệt d&agrave;nh cho thanh thiếu ni&ecirc;n. Xin C&ocirc; cho con biết v&igrave; sao C&ocirc; lại đi xuất gia?</em></strong></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial,Helvetica,sans-serif; font-size: 16px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 25.5938px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; background-color: rgb(255, 255, 255); text-align: justify;"><strong><em>Th&iacute;ch Nữ Hương Nhũ</em></strong>: Ch&agrave;o Hiền Huy H&ograve;a Hiệp <em>(ch&agrave;, c&aacute;i t&ecirc;n đặc biệt qu&aacute; ha!)</em>. C&acirc;u hỏi n&agrave;y C&ocirc; được nghe rất nhiều người hỏi, kể từ khi mới bước ch&acirc;n v&agrave;o chốn thiền m&ocirc;n. Hồi đ&oacute; C&ocirc; c&ograve;n trẻ qu&aacute; n&ecirc;n kh&ocirc;ng thể n&oacute;i g&igrave; nhiều về l&yacute; tưởng giải tho&aacute;t v&agrave; gi&aacute;c ngộ được. Đơn giản chỉ l&agrave; v&igrave; khi chứng kiến c&aacute;i chết của người th&acirc;n &ndash; người chị d&acirc;u của C&ocirc; đột ngột ra đi để lại đứa con trai vừa tr&ograve;n 1 tuổi. L&uacute;c đ&oacute; C&ocirc; cũng chỉ mới tốt nghiệp Đại Học Sư Phạm khoa tiếng Nga, cuộc đời cũng nhiều ước mơ với m&agrave;u hồng tươi đẹp nhưng kh&ocirc;ng hiểu sao c&aacute;i chết của người chị l&agrave;m cho m&igrave;nh thức tỉnh một điều, đ&oacute; l&agrave; sự v&ocirc; thường của cuộc sống. Sau n&agrave;y, qua nhiều năm được tu học với <em>Sư Phụ</em> l&agrave; <em>Ni trưởng thượng Huệ hạ Gi&aacute;c</em> v&agrave; được r&egrave;n luyện b&ecirc;n huynh đệ, C&ocirc; cảm nhận được vị ngọt của Ch&aacute;nh Ph&aacute;p v&agrave; nhận thức một c&aacute;ch s&acirc;u sắc l&agrave; con đường xuất gia l&agrave; con đường duy nhất đem lại hạnh ph&uacute;c trọn vẹn cho m&igrave;nh v&agrave; người <em>(n&oacute;i &ldquo; duy nhất &rdquo; c&oacute; lẽ nhiều bạn kh&ocirc;ng h&agrave;i l&ograve;ng, nhưng đ&oacute; l&agrave; sự thật).</em></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial,Helvetica,sans-serif; font-size: 16px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 25.5938px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; background-color: rgb(255, 255, 255); text-align: justify;"><strong><em>Hiền Huy H&ograve;a Hiệp: Ng&agrave;y nay giới trẻ c&oacute; nhiều thuận duy&ecirc;n để tiếp x&uacute;c với Phật gi&aacute;o - nhưng b&ecirc;n cạnh đ&oacute; kh&ocirc;ng &iacute;t phụ huynh phản đối (v&iacute; dụ: Nhiều sinh vi&ecirc;n học sinh muốn đi xuất gia nhưng gia đ&igrave;nh kh&ocirc;ng đồng &yacute;&hellip;). C&ocirc; c&oacute; &yacute; kiến g&igrave; về vấn đề n&agrave;y?</em></strong></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial,Helvetica,sans-serif; font-size: 16px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 25.5938px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; background-color: rgb(255, 255, 255); text-align: justify;"><strong><em>Th&iacute;ch Nữ Hương Nhũ: </em></strong>Xuất gia l&agrave; một nh&acirc;n duy&ecirc;n. C&oacute; lẽ con cũng hiểu triết l&yacute; Phật gi&aacute;o dựa tr&ecirc;n nền tảng nh&acirc;n qủa v&agrave; nh&acirc;n duy&ecirc;n. Sự xuất gia của th&aacute;i tử Tất Đạt Đa: Từ bỏ cung v&agrave;ng điện ngọc, vợ đẹp con xinh để t&igrave;m cầu sự giải tho&aacute;t v&agrave; gi&aacute;c ngộ cho m&igrave;nh v&agrave; cho ch&uacute;ng sinh l&agrave; một sự từ bỏ vĩ đại m&agrave; ch&uacute;ng ta được biết đến như &nbsp;một <em>&ldquo; Đại Sự Xuất Thế &rdquo;.</em> Ng&agrave;i đ&atilde; từ bỏ tất cả để rồi được tất cả đ&oacute; l&agrave; an lạc hạnh ph&uacute;c vĩnh hằng cho tự th&acirc;n v&agrave; tha nh&acirc;n. Mọi thứ tr&ecirc;n thế gian đều v&ocirc; thường nhưng nếu ch&uacute;ng ta biết sống v&agrave; l&agrave;m theo lời Phật dạy th&igrave; niềm an lạc sẽ lu&ocirc;n hiện hữu.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial,Helvetica,sans-serif; font-size: 16px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 25.5938px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; background-color: rgb(255, 255, 255); text-align: justify;">Những sinh vi&ecirc;n học sinh c&ograve;n rất trẻ m&agrave; đ&atilde; hướng về Phật ph&aacute;p v&agrave; hơn thế&nbsp; nữa l&agrave; muốn xuất gia th&igrave; phải n&oacute;i rằng đ&oacute; l&agrave; một c&aacute;i duy&ecirc;n tốt đẹp được gieo trồng nhiều đời. Nhiều em nhận thức được những&nbsp; sự giả dối, tham &aacute;i, bon chen, ganh tỵ, sự bất to&agrave;n của đời sống, sự v&ocirc; thường&hellip;l&agrave; nguy&ecirc;n nh&acirc;n đưa đến khổ đau n&ecirc;n muốn tho&aacute;t ly để t&igrave;m một lối đi mới, một hướng sống mới cao đẹp hơn. Từ bỏ tất cả để đi v&agrave;o đời sống thiền m&ocirc;n tu tập kh&ocirc;ng phải l&agrave; điều dễ l&agrave;m, v&igrave; cuộc sống n&agrave;y ai cũng c&oacute; những ham muốn ri&ecirc;ng tư. Những bậc cha mẹ hiểu Phật ph&aacute;p sẽ rất vui mừng khi biết con m&igrave;nh nu&ocirc;i dưỡng những &yacute; hướng xuất trần. Dẫu rằng <em>&ldquo; Con Xuất Gia Mẹ Kh&oacute;c, V&igrave; Thương Con , Thương Con &rdquo;&hellip;</em>nhưng cần hiểu rằng dẫu cuộc sống <em>&ldquo; Xuất Thế Gian &rdquo; </em>sẽ c&oacute; nhiều thử th&aacute;ch v&agrave; gian kh&oacute; nhưng&nbsp; sau đ&oacute; c&aacute;c em sẽ c&oacute; đầy đủ năng lực thể chất v&agrave; tinh thần để l&agrave;m tốt đẹp cho cuộc đời. Nếu C&ocirc; kh&ocirc;ng xuất gia m&agrave; lập gia đ&igrave;nh th&igrave; b&acirc;y giờ C&ocirc; cũng đang bận rộn với một gia đ&igrave;nh nho nhỏ, l&agrave;m sao c&oacute; thời gian v&agrave; cơ hội để được mở rộng tấm l&ograve;ng quan t&acirc;m v&agrave; gi&uacute;p đỡ mọi người, nhất l&agrave; c&aacute;c em th&acirc;n mến của C&ocirc;.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial,Helvetica,sans-serif; font-size: 16px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 25.5938px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; background-color: rgb(255, 255, 255); text-align: justify;">Sự cấm cản của cha mẹ thường xuất ph&aacute;t từ t&igrave;nh thương y&ecirc;u, chấp thủ, <em>&ldquo; Duy nhất c&oacute; một đứa con để nối d&otilde;i t&ocirc;ng đường &rdquo;</em> hay l&agrave; chưa hiểu hết &iacute;ch lợi của đời sống xuất gia.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 16px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 25.59375px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-align: center;"><em>&ldquo; Hiếu trần thế bưng cơm tr&agrave; nước</em></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 16px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 25.59375px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-align: center;"><em>Hiếu xuất gia giải tho&aacute;t lu&acirc;n hồi &rdquo;.</em></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial,Helvetica,sans-serif; font-size: 16px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 25.5938px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; background-color: rgb(255, 255, 255); text-align: justify;">Cho đến khi n&agrave;o cha mẹ hiểu được hạnh ph&uacute;c thế gian cũng v&ocirc; thường như &aacute;ng m&acirc;y bay&hellip; Hạnh ph&uacute;c vĩnh hằng chỉ c&oacute; được ở sự gi&aacute;c ngộ tr&ecirc;n nền tảng Từ Bi v&agrave; Tr&iacute; Tuệ th&igrave; sẽ kh&ocirc;ng c&ograve;n ngăn cản con m&igrave;nh dấn bước v&agrave;o lộ tr&igrave;nh tu tập để giải tho&aacute;t.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial,Helvetica,sans-serif; font-size: 16px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 25.5938px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; background-color: rgb(255, 255, 255); text-align: justify;"><strong><em>Hiền Huy H&ograve;a Hiệp: Con được biết C&ocirc; thường tiếp x&uacute;c gần gũi với giới trẻ, rất nhiều bạn trẻ đ&atilde; mến mộ gọi C&ocirc; Hương Nhũ l&agrave; &ldquo; Sư C&ocirc; Idol &rdquo;. Vậy C&ocirc; c&oacute; thể cho con biết nh&acirc;n duy&ecirc;n n&agrave;o C&ocirc; thường gặp gỡ tiếp x&uacute;c với c&aacute;c bạn trẻ như vậy&nbsp; ạ?</em></strong></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial,Helvetica,sans-serif; font-size: 16px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 25.5938px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; background-color: rgb(255, 255, 255); text-align: justify;"><strong><em>&nbsp;Th&iacute;ch Nữ Hương Nhũ:</em></strong> Hồi c&ograve;n nhỏ C&ocirc; đ&atilde; th&iacute;ch dạy học hay chơi tr&ograve; dạy học với c&aacute;c em b&eacute; hơn m&igrave;nh với lại ai m&agrave; chẳng <strong><em>&ldquo;</em></strong><em> y&ecirc;u từng đ&ocirc;i mắt s&aacute;ng, long lanh như những giọt sương &rdquo;</em><strong><em>&hellip;</em></strong>, lớn l&ecirc;n thi v&agrave;o đại học sư phạm, đang thực tập sư phạm th&igrave; C&ocirc; đi xuất gia, nh&acirc;n duy&ecirc;n của C&ocirc; lại tiếp tục dạy học sau gần 10 năm tu học tại Ấn Độ. Cuộc sống thiền m&ocirc;n của C&ocirc; kh&ocirc;ng xa rời giới trẻ, v&igrave; C&ocirc; nghĩ rằng c&aacute;c em lu&ocirc;n cần sự th&ocirc;ng cảm, hiểu biết v&agrave; quan t&acirc;m của những tấm l&ograve;ng bao dung quảng đại, kh&ocirc;ng &iacute;ch kỷ, để gi&uacute;p c&aacute;c em c&oacute; một cuộc đời tươi s&aacute;ng. Nhiều em t&igrave;m gặp C&ocirc; sau những thất bại v&agrave; đau khổ trong t&igrave;nh cảm v&agrave; những vấp ng&atilde; của cuộc đời. Như trưa nay Hiền Huy H&ograve;a Hiệp mới tiếp x&uacute;c với một số bạn trẻ tại Quan &Acirc;m Tu Viện. C&aacute;c bạn đ&oacute; đến đ&acirc;y sau khi đ&atilde; được biết C&ocirc; qua những b&agrave;i ph&aacute;p d&agrave;nh cho giới trẻ tại ch&ugrave;a Hoằng Ph&aacute;p như ph&aacute;p thoại <strong><em>&ldquo;</em></strong> T&igrave;m Lại Ch&iacute;nh M&igrave;nh &rdquo;. Nhiều bạn đ&atilde; đi học lại sau nhiều năm bỏ trường đại học như bạn Vũ tiến Luận (ph&aacute;p danh Tịnh Lu&acirc;n - TPHCM), sau khi nhận thức được cần phải bước ra khỏi b&oacute;ng tối của game, của bạn xấu&hellip; Luận đi học lại v&agrave; bắt đầu c&oacute; những định hướng mới cho cuộc đời. Hiền Huy H&ograve;a Hiệp cũng gặp c&aacute;c sinh vi&ecirc;n Thi&ecirc;n Tr&uacute;c, Hoa Hương, Phương Nghi&ecirc;m v&agrave; Việt H&ugrave;ng (ph&aacute;p danh Phước Tr&iacute;)&hellip; c&aacute;c bạn l&ecirc;n ch&ugrave;a rất đều đặn v&agrave;o thứ bảy, chủ nhật để lễ Phật, sau đ&oacute; dạy k&egrave;m văn h&oacute;a cho q&uacute;y Thầy C&ocirc; trẻ (c&oacute; cả tiểu Tăng v&agrave; tiểu Ni)<strong><em>.</em></strong> C&aacute;c bạn trẻ đ&atilde; đi l&agrave;m như Th&agrave;nh (kỹ sư), C&oacute;, Vũ&hellip;, đang l&agrave;m việc b&ecirc;n c&ocirc;ng ty bất động sản&hellip; hay Long chuẩn bị xuất gia&hellip; N&oacute;i chung, nếu C&ocirc; kh&ocirc;ng đi giảng th&igrave; những ng&agrave;y nghỉ của C&ocirc; cũng d&agrave;nh phần lớn thời gian cho c&aacute;c em. Do sự th&acirc;n thiết v&agrave; gần gũi giữa hai thế hệ n&ecirc;n c&aacute;c em mạnh dạn tham vấn với C&ocirc; v&agrave; mọi vấn đề li&ecirc;n quan đến c&ocirc;ng việc, cuộc sống, đạo Phật&hellip;</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 16px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 25.59375px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-align: center;"><strong><em>&nbsp;<img border="0" src="http://www.daophatngaynay.com/vn/files.php?file=images/2010/quy4/IMG_1081__1__132341839.jpg" /></em></strong></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial,Helvetica,sans-serif; font-size: 16px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 25.5938px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; background-color: rgb(255, 255, 255); text-align: justify;"><strong><em>Hiền Huy H&ograve;a Hiệp: Ph&aacute;p thoại Thắp S&aacute;ng Niềm Tin, Nắng ấm M&ugrave;a Xu&acirc;n, Hương Hoa Đất Việt, v&agrave; đặc biệt ph&aacute;p thoại &ldquo; T&igrave;m Lại Ch&iacute;nh M&igrave;nh &rdquo; đ&atilde; c&oacute; một t&aacute;c động mạnh mẽ v&agrave; ảnh hưởng lớn đối với giới trẻ, ph&aacute;p &acirc;m ch&ugrave;a Hoằng Ph&aacute;p c&oacute; ph&aacute;p thoại n&agrave;y c&oacute; đến gần 20 ng&agrave;n lượt người v&agrave;o nghe.Sau ph&aacute;p thoại n&agrave;y C&ocirc; c&agrave;ng bận rộn hơn với c&aacute;c bạn trẻ c&oacute; phải vậy kh&ocirc;ng ạ?</em></strong></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial,Helvetica,sans-serif; font-size: 16px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 25.5938px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; background-color: rgb(255, 255, 255); text-align: justify;"><strong><em>Th&iacute;ch Nữ Hương Nhũ:</em></strong> Thay cho c&acirc;u trả lời, C&ocirc; Hương Nhũ đ&atilde; đưa cho ch&uacute;ng t&ocirc;i xem một loạt c&aacute;c tin nhắn của c&aacute;c bạn sinh vi&ecirc;n học sinh đ&atilde; gửi tin cho C&ocirc; c&ugrave;ng với rất nhiều c&acirc;u hỏi về mọi kh&iacute;a cạnh cuộc sống, v&agrave; rất nhiều tin nhắn thể hiện t&igrave;nh cảm rất đặc biệt d&agrave;nh cho sư C&ocirc;. Kh&ocirc;ng &iacute;t bạn trẻ hứa hẹn sẽ xuất gia sau khi hoc xong phổ th&ocirc;ng hay sau khi tốt nghiệp đại học. Ch&uacute;ng t&ocirc;i c&ograve;n nhớ một tin nhắn: &ldquo; <em>Xin Sư C&ocirc; h&atilde;y gi&uacute;p con đi theo con đường của Sư C&ocirc; sau khi con học xong đại học </em>&rdquo;.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 16px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 25.59375px; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);"><strong><em>&nbsp;</em></strong></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial,Helvetica,sans-serif; font-size: 16px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 25.5938px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; background-color: rgb(255, 255, 255); text-align: justify;"><strong><em>Hiền Huy H&ograve;a Hiệp: Thưa C&ocirc;, c&oacute; c&acirc;u chuyện n&agrave;o l&agrave;m C&ocirc; cảm động nhất về c&aacute;c bạn trẻ m&agrave; C&ocirc; đ&atilde; từng tiếp x&uacute;c.</em></strong></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial,Helvetica,sans-serif; font-size: 16px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 25.5938px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; background-color: rgb(255, 255, 255); text-align: justify;">&nbsp; <strong><em>Th&iacute;ch Nữ Hương Nhũ:</em></strong> Tất nhi&ecirc;n l&agrave; c&oacute;, dường như cứ bạn trẻ n&agrave;o từ bỏ tội lỗi để bước v&agrave;o &aacute;nh s&aacute;ng đều l&agrave;m C&ocirc; cảm động, v&agrave; c&oacute; những em bất chấp sự cấm cản của gia đ&igrave;nh đ&atilde; trốn nh&agrave; đi xuất gia m&agrave; b&acirc;y giờ đ&atilde; trở th&agrave;nh Tỳ Kheo c&oacute; khả năng gi&uacute;p đỡ tha nh&acirc;n tr&ecirc;n lộ tr&igrave;nh trở về Bến Gi&aacute;c. Nhưng cảm động nhất c&oacute; lẽ l&agrave; trường hợp của một nam sinh vi&ecirc;n (xin tạm giấu t&ecirc;n) b&ecirc; tha, bỏ học, mất hết phương hướng cho cuộc đời, c&oacute; lẽ do nh&acirc;n duy&ecirc;n n&ecirc;n sau khi t&igrave;nh cờ được gặp C&ocirc;, bạn ấy lại từ bỏ những th&oacute;i hư tật xấu ng&agrave;y trước, đi học lại v&agrave; học rất tốt. Hiện nay bạn chuẩn bị đi l&agrave;m với một t&acirc;m nguyện: <em>&ldquo; Con được như ng&agrave;y h&ocirc;m nay l&agrave; nhờ Phật ph&aacute;p n&ecirc;n d&ugrave; sức Con c&oacute; hạn nhưng con vẫn xin được đ&oacute;ng g&oacute;p tất cả c&ocirc;ng sức của m&igrave;nh cho những người bất hạnh hơn m&igrave;nh &rdquo;</em>. V&agrave; cảm động hơn l&agrave; mẹ của bạn ấy khi gặp C&ocirc; lu&ocirc;n n&oacute;i rằng: <em>&ldquo; Sư C&ocirc; ơi! L&agrave;m sao Sư C&ocirc; gi&uacute;p ch&aacute;u xuất gia lu&ocirc;n C&ocirc; nh&eacute;. Nếu được như thế gia đ&igrave;nh con mang ơn Sư C&ocirc; m&atilde;i m&atilde;i.&rdquo;</em></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 16px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 25.59375px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-align: center;"><strong><em>&nbsp;<img border="0" src="http://www.daophatngaynay.com/vn/files.php?file=images/2010/quy4/IMG_1102__1__413379486.jpg" /></em></strong></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial,Helvetica,sans-serif; font-size: 16px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 25.5938px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; background-color: rgb(255, 255, 255); text-align: justify;"><strong><em>Hiền Huy H&ograve;a Hiệp: B&acirc;y giờ nhiều bạn trẻ lao v&agrave;o nếp sống bu&ocirc;ng thả, &ldquo; sống thử &rdquo; - với t&igrave;nh y&ecirc;u thương d&agrave;nh cho giới trẻ C&ocirc; c&oacute; lời nhắn nhủ&nbsp; g&igrave;&nbsp; với giới trẻ l&agrave; những người chủ tương lai của đất nước?</em></strong></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial,Helvetica,sans-serif; font-size: 16px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 25.5938px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; background-color: rgb(255, 255, 255); text-align: justify;"><strong><em>Th&iacute;ch Nữ Hương Nhũ:</em></strong> Lối sống gấp, <em>&ldquo;sống thử &rdquo;</em> lu&ocirc;n đem lại những hậu quả xấu kh&oacute; lường m&agrave; cụ thể l&agrave; c&aacute;c căn bệnh x&atilde; hội hiểm ngh&egrave;o, đưa đến sự b&ecirc; tha, bỏ học h&agrave;ng loạt, tự giết chết tương lai v&agrave; nhất l&agrave; nhiều trường hợp đ&aacute;ng tiếc dẫn đến sự ph&aacute; thai.</p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial,Helvetica,sans-serif; font-size: 16px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 25.5938px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; background-color: rgb(255, 255, 255); text-align: justify;">Mới đ&acirc;y trong một ph&aacute;p thoại tại ch&ugrave;a Quan &Acirc;m Tu Viện &ndash; Ph&uacute; Nhuận, c&ocirc; c&oacute; đặt ra một vấn đề trong b&agrave;i giảng: Tội &aacute;c của sự ph&aacute; thai qua bản Kinh Diệt Tội Trường thọ, <em>&ldquo; Tại sao khi một v&agrave;i người chết v&igrave; sự khủng bố d&ugrave; ở bất cứ nơi n&agrave;o, cũng đều bị l&ecirc;n &aacute;n tội &aacute;c giết người, nhưng hiện nay h&agrave;ng năm, những b&agrave;o thai v&ocirc; tội bị giết từ khi c&ograve;n trứng nước c&oacute; khoảng 60 cho đến 100 triệu b&agrave;o thai tr&ecirc;n to&agrave;n thế giới -&nbsp; th&igrave; sự giết ch&oacute;c đ&oacute; lại &acirc;m thầm được bỏ qua, c&oacute; chăng l&agrave; tiếng n&oacute;i phản đối đi v&agrave;o khoảng kh&ocirc;ng v&ocirc; vọng của một số người c&oacute; lương t&acirc;m. Tuy c&ograve;n trẻ m&agrave; c&aacute;c em đ&atilde; mang lấy tội &aacute;c giết người v&igrave; mỗi b&agrave;o thai l&agrave; một mạng sống. Nếu c&aacute;c bạn trẻ đi v&agrave;o lối sống vội v&atilde; tội lỗi sẽ dễ d&agrave;ng chuốc lấy quả b&aacute;o nặng nề do việc l&agrave;m n&agrave;y, những căn bệnh về th&acirc;n v&agrave; mặc cảm tội lỗi trong t&acirc;m kh&ocirc;ng dễ g&igrave; phai nhạt. Hơn nữa theo lời Phật dạy trong Kinh Trường thọ Diệt tội th&igrave; thật ra tội &aacute;c của sự ph&aacute; thai thật kh&oacute; lường: N&oacute; đưa đến quả b&aacute;o địa ngục A Tỳ, ngang với tội giết Cha, giết Mẹ, l&agrave;m th&acirc;n Phật chảy m&aacute;u cũng như tội &aacute;c ph&aacute; h&ograve;a hợp Tăng &rdquo;.</em></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial,Helvetica,sans-serif; font-size: 16px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 25.5938px; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; background-color: rgb(255, 255, 255); text-align: justify;"><strong><em>&nbsp;</em></strong><em>* Ch&uacute;ng con xin cảm ơn C&ocirc; rất nhiều đ&atilde; d&agrave;nh ch&uacute;t &iacute;t thời gian cho cuộc phỏng vấn nhanh. K&iacute;nh ch&uacute;c C&ocirc; An Lạc, Phật Sự Vi&ecirc;n Th&agrave;nh, v&agrave; lu&ocirc;n l&agrave; &ldquo; SƯ C&Ocirc; IDOL &rdquo; của giới Trẻ.</em></p>\r\n\r\n<p style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; font-size: 16px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 25.59375px; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);"><strong><em>Nam</em></strong><strong><em> M&ocirc; Hoan Hỷ Tạng Bồ T&aacute;t Ma Ha T&aacute;t.</em></strong></p>\r\n', 'Phỏng vấn nhanh: SC. Thích Nữ Hương Nhũ - Tuổi trẻ đồng hành cùng Phật giáo', 0),
(73, 16, 'Hữu Huệ', '2013-01-29 11:10:18', '<table border="0" cellpadding="0" cellspacing="0" id="shortdsc" style="font-family: Arial, Tahoma; font-size: 12px; color: rgb(102, 102, 102); font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px;">\r\n	<tbody>\r\n		<tr>\r\n			<td class="newsTitleDetail" style="font-weight: bold; color: rgb(153, 51, 0); font-size: 18px; padding-bottom: 12px;" valign="top">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td valign="top">\r\n			<table border="0" cellpadding="3" cellspacing="0" id="tbl_store_imgThumb" style="font-family: Arial, Tahoma; font-size: 12px; color: rgb(0, 0, 0); float: left; margin-right: 10px; margin-bottom: 5px;" width="170">\r\n				<tbody>\r\n					<tr>\r\n						<td><img border="0" src="http://www.hoalinhthoai.com/img-news/hoalinhthoai_1229152750_IQTh.jpg" style="border: 1px solid rgb(0, 0, 0);" /></td>\r\n					</tr>\r\n					<tr>\r\n						<td class="img_dsc" style="color: gray; font-size: 11px; font-style: italic;" valign="top">&nbsp;</td>\r\n					</tr>\r\n				</tbody>\r\n			</table>\r\n\r\n			<div id="dsc" style="padding-bottom: 10px; font-size: 12px; color: rgb(92, 92, 92); font-weight: bold; text-align: justify;">Gần đ&acirc;y b&aacute;o Gi&aacute;c Ngộ (số 460) lại c&oacute; b&agrave;i Hương cỏ thơm viết về Sư c&ocirc;-tiến sĩ Th&iacute;ch nữ Hương Nhũ trở th&agrave;nh giảng vi&ecirc;n được y&ecirc;u mến tại Học viện Phật gi&aacute;o Việt Nam tại TP.HCM của t&aacute;c giả Như Nghi&ecirc;m.</div>\r\n\r\n			<div class="newsContent">\r\n			<p align="justify">&nbsp;</p>\r\n\r\n			<div>\r\n			<p style="text-align: justify;"><font color="#000080" face="Arial" size="2">Trong quyển Phật gi&aacute;o trong thế kỷ mới, Tại sao&nbsp; phải chấn hưng? do Giao Điểm xuất bản th&aacute;ng 10-2006 c&oacute; b&agrave;i viết của Ni sư Th&iacute;ch nữ Huệ Li&ecirc;n về&nbsp; Sự đ&oacute;ng g&oacute;p của Ni giới, một sứ mạng c&oacute; thể thực hiện được. Trong đ&oacute; t&aacute;c giả đ&atilde; đưa ra những l&yacute; thuyết v&agrave; dẫn chứng thuyết phục, rằng nếu c&oacute; điều kiện th&iacute;ch hợp th&igrave; &ldquo;gi&aacute; trị đ&oacute;ng g&oacute;p của Ni giới c&oacute; thể đạt đến mức cao nhất m&agrave; một h&agrave;nh giả Tăng c&oacute; thể đạt được&rdquo;. Gần đ&acirc;y b&aacute;o Gi&aacute;c Ngộ (số 460) lại c&oacute; b&agrave;i Hương cỏ thơm viết về Sư c&ocirc;-tiến sĩ Th&iacute;ch nữ Hương Nhũ trở th&agrave;nh giảng vi&ecirc;n được y&ecirc;u mến tại Học viện Phật gi&aacute;o Việt Nam tại TP.HCM của t&aacute;c giả Như Nghi&ecirc;m.</font></p>\r\n\r\n			<p style="text-align: justify;"><font color="#000080" face="Arial" size="2">Thật ra <em>Hương cỏ thơm </em>chỉ l&agrave; một b&agrave;i viết mang t&iacute;nh c&aacute;ch c&aacute; nh&acirc;n về l&ograve;ng k&iacute;nh trọng, mến y&ecirc;u của một Ni sinh d&agrave;nh cho người thầy đứng tr&ecirc;n bục giảng nh&acirc;n ng&agrave;y Nh&agrave; gi&aacute;o Việt Nam 20-11. Nhưng hiệu ứng của b&agrave;i viết lại vượt xa hơn l&agrave; một t&igrave;nh cảm ri&ecirc;ng tư của học tr&ograve; d&agrave;nh cho c&ocirc; gi&aacute;o. M&agrave; n&oacute; mang tầm v&oacute;c thời đại. Ta thấy được điều đ&oacute; qua c&aacute;ch đ&oacute;n nhận của c&aacute;c Ni sinh Phật học viện. Khi b&agrave;i b&aacute;o đ&oacute; vừa ph&aacute;t h&agrave;nh th&igrave; t&ocirc;i đ&atilde; nhận được những c&uacute; điện thoại hỏi c&oacute; đọc <em>Hương cỏ thơm</em> chưa? C&oacute; Ni sinh c&ograve;n tặng hẳn t&ocirc;i một tờ b&aacute;o <em>Gi&aacute;c Ngộ </em>để&hellip; khoe. Rồi gợi &yacute; t&ocirc;i viết b&agrave;i về chư Ni &ldquo;c&agrave;ng nhiều c&agrave;ng tốt&rdquo;&hellip;</font></p>\r\n			</div>\r\n\r\n			<p style="text-align: justify;"><font color="#000080" face="Arial" size="2">T&ocirc;i kh&ocirc;ng ngờ một b&agrave;i b&aacute;o b&igrave;nh thường lại được đ&oacute;n nhận qu&aacute; nồng n&agrave;n như vậy. Vậy th&igrave; b&agrave;i b&aacute;o đ&oacute; phải c&oacute; một c&aacute;i g&igrave; đặc biệt. V&agrave; t&ocirc;i nghĩ rằng c&aacute;i đặc biệt đ&oacute; ch&iacute;nh l&agrave; ở chỗ n&oacute; đ&atilde; ra đời đ&uacute;ng l&uacute;c, điểm tr&uacute;ng huyệt của những Ni sinh đang ngồi tr&ecirc;n ghế giảng đường đại học. Đằng sau sự phấn khởi đ&oacute; ta c&oacute; thể thấy được một kh&aacute;t vọng muốn vươn l&ecirc;n, một nỗi l&ograve;ng muốn tự khẳng định m&igrave;nh v&agrave; một t&acirc;m nguyện muốn cống hiến&hellip; m&agrave; họ ấp ủ từ l&acirc;u. B&agrave;i viết do một Ni sinh viết về một Ni sư&nbsp; đ&atilde; n&oacute;i gi&uacute;p họ những điều đ&oacute;, l&agrave; giọt nước l&agrave;m tr&agrave;n ly nước, m&agrave; Sư c&ocirc; Hương Nhũ chỉ l&agrave; một mẫu h&igrave;nh. Rằng c&oacute; ai biết được những vị Ni sư, Sư c&ocirc;, m&agrave; trước hết l&agrave; người phụ nữ Việt Nam cần c&ugrave; lam lủ, với vẻ ngo&agrave;i thầm lặng như cam chịu kia lại ẩn chứa b&ecirc;n trong bao điều kỳ diệu m&agrave; khi cần họ c&oacute; thể b&ugrave;ng l&ecirc;n một c&aacute;ch mạnh mẽ?</font></p>\r\n\r\n			<p style="text-align: justify;"><font color="#000080" face="Arial" size="2">Thật ra từ l&acirc;u t&ocirc;i cũng rất muốn viết một c&aacute;i g&igrave; đ&oacute; về chư Ni. V&igrave; t&ocirc;i thấy rằng họ thật sự đ&aacute;ng k&iacute;nh, đ&aacute;ng được c&oacute; nhiều hơn những g&igrave; họ đang l&agrave;. X&atilde; hội đ&atilde; đổi thay, tr&ecirc;n thế giới c&oacute; nhiều phụ nữ đạt được những th&agrave;nh tựu xuất sắc tr&ecirc;n hầu hết c&aacute;c lĩnh vực, từ kinh tế cho tới ch&iacute;nh trị&hellip; Sự th&agrave;nh c&ocirc;ng của họ khẳng định một điều rằng, kh&ocirc;ng phải giới t&iacute;nh m&agrave; l&agrave; năng lực tự th&acirc;n mới l&agrave; quan trọng. Thế gian đ&atilde; như vậy th&igrave; tại sao Phật ph&aacute;p lại kh&ocirc;ng được như vậy? Tại sao một c&ocirc; gi&aacute;o b&igrave;nh thường c&oacute; quyền dạy học cho chư Tăng, nhưng khi c&ocirc; gi&aacute;o đ&oacute; xuống t&oacute;c l&agrave;m Ni th&igrave; quyền đ&oacute; bị mất? Chẳng lẽ sau khi xuất gia kh&ocirc;ng c&oacute; gi&aacute; trị bằng trước khi xuất gia? Nhưng rất mừng l&agrave; trong những năm gần đ&acirc;y ta thấy cũng một sự&nbsp; thay đổi tương tự trong h&agrave;ng ngũ Ni lưu. Chư Ni đi học với số lượng đ&ocirc;ng, họ học cũng giỏi nữa, nhất l&agrave; c&oacute; vị c&ograve;n đi du học nước ngo&agrave;i.</font></p>\r\n\r\n			<p style="text-align: justify;"><font color="#000080" face="Arial" size="2">Đ&atilde; c&oacute; một thời kỳ t&ocirc;i nghe một số chư t&ocirc;n t&uacute;c n&oacute;i rằng Ni học cho lắm rồi th&igrave; cũng về ch&ugrave;a se nhang, b&aacute;n đồ chay&hellip; Nhưng l&agrave;m sao c&oacute; thể kh&aacute;c được khi m&agrave; thời đại n&oacute; l&agrave; như vậy(như &ocirc;ng b&agrave; xưa n&oacute;i con g&aacute;i đi học chữ chỉ để viết thư cho trai). Điều m&agrave; bấy l&acirc;u nay t&ocirc;i rất lấy l&agrave;m lạ l&agrave; tại sao tại những trường Phật học số lượng Ni sinh theo học bao giờ cũng đ&ocirc;ng hơn Tăng. Năng lực tiếp thu của họ cũng giỏi hơn Tăng. Họ thường được xếp hạng rất cao trong lớp, cao hơn Tăng. V&iacute; dụ như đợt tuyển sinh Học viện kh&oacute;a VI vừa rồi, hai hạng nhất v&agrave; nh&igrave; đều l&agrave; của Ni. Tăng chỉ đứng hạng ba. Vậy m&agrave; sau khi ra trường th&igrave; họ lại biệt tăm biệt t&iacute;ch. Kh&ocirc;ng thấy c&oacute; th&agrave;nh tựu g&igrave; nổi bật&hellip; Nhưng c&aacute;i thời ấy đang dần trở th&agrave;nh&hellip; kỷ niệm. C&ugrave;ng với xu hướng ph&aacute;t triển của thời đại, Ni giới cũng đ&atilde; vươn l&ecirc;n để tự khẳng định m&igrave;nh, v&agrave; họ đ&atilde; được đ&oacute;n nhận. Bằng chứng l&agrave; Phật học viện đ&atilde; cho ph&eacute;p chư Ni được đứng lớp&hellip; dạy cả chư Tăng. Ri&ecirc;ng lớp Triết học Phật gi&aacute;o kh&oacute;a VI của ch&uacute;ng t&ocirc;i c&oacute; ba &ldquo;c&ocirc; gi&aacute;o&rdquo; như thế đảm tr&aacute;ch xuy&ecirc;n suốt nhiều bộ m&ocirc;n kh&aacute;c nhau. Đ&oacute; l&agrave; Sư c&ocirc; Hương Nhũ, Sư c&ocirc; Hằng Li&ecirc;n từ Ấn Độ về, v&agrave; Sư c&ocirc; Huệ Chơn tốt nghiệp ở Trung Quốc.</font></p>\r\n\r\n			<p style="text-align: justify;"><font color="#000080" face="Arial" size="2">Ngo&agrave;i những đặc điểm cần phải c&oacute; của một người đứng lớp như kiến thức, tự tin, nhiệt t&igrave;nh, vui vẻ&hellip;, điều đ&aacute;ng qu&yacute; ở c&aacute;c vị giảng sư Ni n&agrave;y l&agrave; đức t&iacute;nh khi&ecirc;m cung, lễ độ. Khi nghe một Tăng sinh xưng &ldquo;em&rdquo; với m&igrave;nh th&igrave; Sư c&ocirc; Hằng Li&ecirc;n từ tốn m&agrave; n&oacute;i rằng &ldquo;kh&ocirc;ng d&aacute;m nhận, v&igrave; d&ugrave; sao qu&yacute; thầy cũng l&agrave; Tăng&rdquo;. Nhưng ngược lại qu&yacute; sư c&ocirc; đ&oacute; lại xưng m&igrave;nh l&agrave; &ldquo;con&rdquo; v&agrave; vẫn gọi c&aacute;c Tăng sinh bằng &ldquo;thầy&rsquo; một c&aacute;ch tr&acirc;n trọng. Đ&acirc;y c&oacute; lẽ l&agrave; trường hợp kh&aacute; đặc biệt chỉ c&oacute; trong m&ocirc;i trường gi&aacute;o dục Phật gi&aacute;o Việt Nam. Nhưng điều đ&oacute; kh&ocirc;ng hề l&agrave;m cho gi&aacute; trị của họ giảm xuống&nbsp; m&agrave; ngược lại họ c&agrave;ng được &ldquo;học tr&ograve;&rdquo; m&igrave;nh k&iacute;nh mến th&ecirc;m m&agrave; th&ocirc;i. Th&uacute; thật t&ocirc;i chưa thấy c&oacute; trường hợp &ldquo;tr&ograve;&rdquo; n&agrave;o d&aacute;m hỗn với &ldquo;c&ocirc; gi&aacute;o&rdquo; cả.</font></p>\r\n\r\n			<p style="text-align: justify;"><font color="#000080" face="Arial" size="2">Trước đ&acirc;y t&ocirc;i cũng c&oacute; gặp&nbsp; trường hợp một số vị Ni, v&igrave; muốn chứng tỏ &ldquo;b&igrave;nh đẳng&rdquo; với Tăng n&ecirc;n đ&atilde; c&oacute; những h&agrave;nh động biểu lộ kh&aacute; l&agrave;&hellip; khiếm nh&atilde;. Ngay cả ph&eacute;p lịch sự tối thiểu khi x&atilde; giao l&agrave; x&aacute; ch&agrave;o nhau m&agrave; họ cũng&hellip; bỏn xẻn đối với chư Tăng. C&ograve;n nếu c&oacute; n&oacute;i năng th&igrave; cố t&igrave;nh kẻ cả, trịch thượng chứ kh&ocirc;ng bao giờ chịu thua. L&uacute;c đ&oacute; t&ocirc;i cũng nghĩ rằng như thế l&agrave; b&igrave;nh đẳng thật. V&igrave; chư Tăng n&oacute;i lỗi của chư Ni được th&igrave; chư Ni cũng c&oacute; quyền đ&oacute; chứ sao. Nhưng khi v&agrave;o Học viện, nh&igrave;n thấy tr&igrave;nh độ học vấn v&agrave; sự khi&ecirc;m nhường của c&aacute;c giảng sư Ni, t&ocirc;i mới thấy những vị Ni trước đ&oacute; rất l&agrave;&hellip; b&igrave;nh d&acirc;n học vụ. Th&igrave; ra c&agrave;ng am hiểu về Phật ph&aacute;p người ta c&agrave;ng biết hạ m&igrave;nh v&agrave; t&ocirc;n trọng người kh&aacute;c, coi trọng thực chất hơn l&agrave; h&igrave;nh thức, họ l&agrave;m nhiều hơn n&oacute;i,&nbsp; v&agrave; &acirc;m thầm đ&oacute;ng g&oacute;p chứ kh&ocirc;ng đả ph&aacute; điều g&igrave;. Người c&agrave;ng khi&ecirc;m nhường th&igrave; c&agrave;ng l&agrave;m được nhiều việc. Bởi v&igrave; &ldquo;th&ugrave;ng rỗng k&ecirc;u to&rdquo; vẫn l&agrave; c&acirc;u chuyện xưa nay. &Ocirc;i, k&iacute;nh l&agrave;m sao phận liễu yếu đ&agrave;o tơ m&agrave; c&oacute; ch&iacute; x&ocirc;ng trời thẳm, v&agrave; thương l&agrave;m sao khi l&agrave;m &ldquo;thầy&rdquo; m&agrave; vẫn tự cho m&igrave;nh l&agrave; &ldquo;con&rdquo;! H&igrave;nh ảnh ấy gợi t&ocirc;i nhớ đến những vần thơ trong s&aacute;ch <em>T&acirc;m l&yacute; đạo đức </em>của thầy Ch&acirc;n Quang:</font></p>\r\n\r\n			<p><font color="#000080" face="Arial" size="2"><em>Xin c&uacute;i xuống l&agrave;m người h&egrave;n k&eacute;m</em></font></p>\r\n\r\n			<p><font color="#000080" face="Arial" size="2"><em>Miễn đ&ocirc;i tay l&agrave;m đẹp cuộc đời</em></font></p>\r\n\r\n			<p><font color="#000080" face="Arial" size="2"><em>T&igrave;nh thương chan khắp mu&ocirc;n nơi</em></font></p>\r\n\r\n			<p><font color="#000080" face="Arial" size="2"><em>Con tim tuy nhỏ nhưng trời đất &ocirc;m.</em></font></p>\r\n\r\n			<p style="text-align: justify;"><font color="#000080" face="Arial" size="2">T&ocirc;i nhớ c&aacute;ch đ&acirc;y chừng mấy năm, Học viện c&oacute; tiếp một vị Ni sư người &Acirc;u Mỹ. Vị n&agrave;y c&oacute; n&oacute;i l&agrave; c&oacute; &ldquo;sứ mệnh&rdquo; đi v&ograve;ng quanh thế giới, tổ chức đại hội hai năm một lần k&ecirc;u gọi b&igrave;nh đẳng trong Phật gi&aacute;o bằng c&aacute;ch&hellip; bỏ B&aacute;t kỉnh ph&aacute;p. T&ocirc;i kh&ocirc;ng muốn đề cập t&iacute;nh đ&uacute;ng sai, n&ecirc;n hay kh&ocirc;ng n&ecirc;n, cần hay kh&ocirc;ng cần của sự việc đ&oacute;, nhưng m&agrave; như c&oacute; một vị thầy đ&atilde; n&oacute;i, điều cần thiết của chư Ni ng&agrave;y nay l&agrave; tăng cường học hỏi, n&acirc;ng cao tr&igrave;nh độ của m&igrave;nh cả về thế học lẫn Phật học chứ kh&ocirc;ng phải đi c&atilde;i nhau l&agrave; B&aacute;t kỉnh ph&aacute;p c&oacute; phải do Phật n&oacute;i hay kh&ocirc;ng, c&oacute; cần x&oacute;a bỏ B&aacute;t kỉnh ph&aacute;p hay kh&ocirc;ng? V&agrave; đ&oacute; ch&iacute;nh l&agrave; con đường m&agrave; chư Ni Việt Nam đang đi cũng như bước đầu thu hoạch được kết quả đ&aacute;ng kh&iacute;ch lệ. Ni giới Việt Nam c&oacute; c&aacute;ch l&agrave;m ri&ecirc;ng để khẳng định vị tr&iacute; của m&igrave;nh. Họ kh&ocirc;ng h&ocirc; h&agrave;o k&ecirc;u gọi n&agrave;y nọ m&agrave; chỉ &acirc;m thầm học tập v&agrave; l&agrave;m việc, &ldquo;chậm m&agrave; chắc&rdquo;. V&agrave; một khi nội lực đ&atilde; đầy đủ th&igrave; d&ugrave; người kh&aacute;c c&oacute; muốn phủ nhận cũng kh&ocirc;ng thể n&agrave;o phủ nhận được.</font></p>\r\n\r\n			<p style="text-align: justify;"><font color="#000080" face="Arial" size="2">D&ugrave; sao đi nữa sự ph&aacute;t triển của Ni giới, nhất l&agrave; Ni giới PGVN l&agrave; một điều đ&aacute;ng mừng, đ&aacute;ng động vi&ecirc;n, đ&aacute;ng kh&iacute;ch lệ. Trong b&agrave;i viết của vị Ni sư n&oacute;i tr&ecirc;n c&oacute; n&oacute;i rằng: Sở dĩ những tiềm năng tốt đẹp của chư Ni chưa được ph&aacute;t huy l&agrave; v&igrave; c&ograve;n phải &ldquo;chờ nh&acirc;n duy&ecirc;n&rdquo;. Thiết nghĩ nh&acirc;n duy&ecirc;n ấy đ&atilde; đến gần. Với những g&igrave; cho thấy hiện nay t&ocirc;i tin rằng Ni giới nước ta sẽ c&ograve;n lớn mạnh hơn nữa trong tương lai. Sẽ c&oacute; nhiều điều kiện v&agrave; cơ hội để thi thố t&agrave;i năng v&agrave; cống hiến sức m&igrave;nh. C&aacute;c Ni sinh đang ngồi tr&ecirc;n ghế nh&agrave; trường cần n&ecirc;n &yacute; thức được điều n&agrave;y, noi gương &ldquo;<em>Hương cỏ thơm&rdquo;</em>, noi gương c&aacute;c sư chị, phấn đấu kh&ocirc;ng ngừng trau giồi tr&iacute; huệ, để ng&agrave;y mai g&oacute;p sức c&ugrave;ng chư Tăng thắp l&ecirc;n ngọn đuốc PGVN ng&agrave;y c&agrave;ng huy ho&agrave;ng s&aacute;ng lạn.</font></p>\r\n\r\n			<p style="text-align: justify;"><font color="#000080" face="Arial" size="2">Tiềm năng của Ni giới chắc kh&ocirc;ng ai hiểu r&otilde; hơn ch&iacute;nh bản th&acirc;n họ. B&agrave;i viết n&agrave;y chỉ g&oacute;p th&ecirc;m một tiếng n&oacute;i, như một lời sẻ chia, kh&iacute;ch lệ m&agrave; th&ocirc;i. </font></p>\r\n			</div>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', 'Đọc "Hương Cỏ Thơm"nghĩ về Ni giới hôm nay', 0);
INSERT INTO `chuathienquang_news` (`id`, `id_category`, `author`, `date`, `content`, `title`, `type`) VALUES
(74, 13, 'Thái Anh - Minh Mẫn', '2013-01-11 11:13:35', '<p><span class="tin_tomtat" style="line-height: 20px; font-size: 14px; font-weight: bold; padding-top: 4px; text-align: justify; color: rgb(96, 96, 96); font-family: ''Times New Roman'', Arial; font-style: normal; font-variant: normal; letter-spacing: normal; orphans: 2; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); width: 620px;"><a class="bold" href="http://nguoiphattu.com/index.php" style="font-family: Arial, Helvetica, Tahoma, sans-serif; font-size: 12px; text-decoration: initial; color: rgb(0, 0, 4); font-weight: bold;"><span style="color: rgb(0, 0, 0);">NPT</span></a> - Ng&agrave;y 09/01/2013, tại Thi&ecirc;n Quang ni tự (phường Đ&ocirc;ng H&ograve;a, thị x&atilde; Dĩ An, tỉnh B&igrave;nh Dương), sư c&ocirc; Hương Nhũ tổ chức &ldquo;Ng&agrave;y tu an lạc cho người khiếm thị lần thứ 4&rdquo; cho 300 người khiếm thị trong c&aacute;c địa phương như B&igrave;nh Dương, Đồng Nai v&agrave; c&aacute;c quận huyện thuộc TP HCM.</span></p>\r\n\r\n<p style="font-family: Arial, Tahoma, Helvetica, sans-serif; font-size: 12px; color: rgb(60, 60, 60); line-height: 18px; text-decoration: initial; margin: 1em 0px; padding: 0px; background-color: rgb(255, 255, 255);"><span class="content" style="font-family: verdana, arial, sans-serif; font-size: 13px; color: rgb(0, 0, 0); line-height: 18pt; text-decoration: initial; overflow: hidden; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); width: 620px;">Ng&agrave;y tu c&oacute; sự chứng minh của TT T.Nhất Ch&iacute;, ủy vi&ecirc;n Ban Trị sự GHPGVN tỉnh B&igrave;nh Dương, Ch&aacute;nh đại diện Phật gi&aacute;o thị x&atilde; Dĩ An, Ni sư TN. Ph&aacute;p Như, Trưởng ban Từ thiện PG B&igrave;nh Dương, sự hiện diện của đại diện ch&iacute;nh quyền địa phương v&agrave; đặc biệt l&agrave; được sự yểm trợ của sư c&ocirc; Ch&acirc;n Như trụ tr&igrave; ch&ugrave;a Vi Phước, thị trấn Thốt Nốt ở Cần Thơ,&nbsp;qu&yacute; Phật tử Cần Thơ, v&agrave; một số mạnh thường qu&acirc;n. Nh&oacute;m từ thiện Từ T&acirc;m B&igrave;nh dương&hellip;</span></p>\r\n\r\n<p style="font-family: Arial, Tahoma, Helvetica, sans-serif; font-size: 12px; color: rgb(60, 60, 60); line-height: 18px; text-decoration: initial; margin: 1em 0px; padding: 0px; background-color: rgb(255, 255, 255);"><span class="content" style="font-family: verdana, arial, sans-serif; font-size: 13px; color: rgb(0, 0, 0); line-height: 18pt; text-decoration: initial; overflow: hidden; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); width: 620px;">Thi&ecirc;n Quang ni tự, tuy chưa được kiến thiết, nhưng mỗi năm đều c&oacute; c&aacute;c đợt chẩn th&iacute; cho c&aacute;c anh chị em khiếm thị, v&igrave; thế, ngo&agrave;i những thanh ni&ecirc;n xem c&ocirc; l&agrave; thần tượng, anh chị em xem Thi&ecirc;n Quang l&agrave; chốn Tổ t&acirc;m linh của người khiếm thị.</span></p>\r\n\r\n<div align="center" style="font-size: 10pt; color: rgb(0, 0, 0); font-family: verdana, arial, sans-serif; line-height: 18pt; text-decoration: initial;">\r\n<div align="center" style="font-size: 10pt; color: rgb(0, 0, 0); font-family: verdana, arial, sans-serif; line-height: 18pt; text-decoration: initial;"><span class="content" style="font-family: verdana, arial, sans-serif; font-size: 13px; color: rgb(0, 0, 0); line-height: 18pt; text-decoration: initial; overflow: hidden; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); width: 620px;">&nbsp;<img src="http://nguoiphattu.com/hinh/hinh2013/thang1/thienquangnitu/image001.jpg" style="border: none;" /></span></div>\r\n\r\n<div align="center" style="font-size: 10pt; color: rgb(0, 0, 0); font-family: verdana, arial, sans-serif; line-height: 18pt; text-decoration: initial;"><span class="content" style="font-family: verdana, arial, sans-serif; font-size: 13px; color: rgb(0, 0, 0); line-height: 18pt; text-decoration: initial; overflow: hidden; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); width: 620px;">&nbsp;<img src="http://nguoiphattu.com/hinh/hinh2013/thang1/thienquangnitu/image002.jpg" style="border: none;" /></span></div>\r\n\r\n<div align="center" style="font-size: 10pt; color: rgb(0, 0, 0); font-family: verdana, arial, sans-serif; line-height: 18pt; text-decoration: initial;"><span class="content" style="font-family: verdana, arial, sans-serif; font-size: 13px; color: rgb(0, 0, 0); line-height: 18pt; text-decoration: initial; overflow: hidden; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: justify; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); width: 620px;">&nbsp;<img src="http://nguoiphattu.com/hinh/hinh2013/thang1/thienquangnitu/image003.jpg" style